#!/usr/bin/env node

/**
 * Build Script per Schema Modulare
 * Combina tutti i moduli in un unico schema Prisma
 */

const fs = require('fs');
const path = require('path');

class SchemaBuild {
  constructor() {
    this.modulesDir = path.join(__dirname, '../prisma/modules');
    this.outputPath = path.join(__dirname, '../prisma/schema.prisma');
  }

  async build() {
    console.log(' Building unified schema...');

    let unifiedSchema = '';

    // Aggiungi header
    unifiedSchema += this.getSchemaHeader();

    // Aggiungi enum da modulo enum
    const enumPath = path.join(this.modulesDir, 'enums/schema.prisma');
    if (fs.existsSync(enumPath)) {
      const enumContent = fs.readFileSync(enumPath, 'utf8');
      unifiedSchema += this.extractContent(enumContent);
    }

    // Aggiungi modelli da tutti i moduli
    const modules = fs.readdirSync(this.modulesDir)
      .filter(dir => dir !== 'enums' && fs.statSync(path.join(this.modulesDir, dir)).isDirectory());

    for (const module of modules) {
      const modulePath = path.join(this.modulesDir, module, 'schema.prisma');
      if (fs.existsSync(modulePath)) {
        const moduleContent = fs.readFileSync(modulePath, 'utf8');
        unifiedSchema += `\n// === MODULE: ${module.toUpperCase()} ===\n`;
        unifiedSchema += this.extractContent(moduleContent);
      }
    }

    // Salva schema unificato
    fs.writeFileSync(this.outputPath, unifiedSchema);

    console.log(' Schema unificato generato');
    console.log(` Output: ${this.outputPath}`);
  }

  getSchemaHeader() {
    return `// === PRISMA SCHEMA UNIFICATO ===
// Generato automaticamente dal build script
// NON MODIFICARE DIRETTAMENTE - Editare i moduli in prisma/modules/

generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

`;
  }

  extractContent(content) {
    // Rimuovi commenti header e mantieni solo definizioni
    return content
      .replace(//**[sS]*?*//g, '') // Rimuovi commenti block
      .replace(///.*$/gm, '') // Rimuovi commenti line
      .replace(/^s*$/gm, '') // Rimuovi righe vuote
      .trim() + '

';
  }
}

if (require.main === module) {
  const builder = new SchemaBuild();
  builder.build().catch(console.error);
}

module.exports = SchemaBuild;

/**
 * Advanced Validations - Fase 7
 * Validazioni Zod per enum e tipi standardizzati
 */

import { z } from 'zod';

// === ENUM VALIDATIONS ===

export const CourseStatusSchema = z.enum(['DRAFT', 'PUBLISHED', 'ACTIVE', 'COMPLETED', 'CANCELLED', 'SUSPENDED']);

export const EnrollmentStatusSchema = z.enum(['PENDING', 'CONFIRMED', 'ACTIVE', 'COMPLETED', 'CANCELLED', 'SUSPENDED']);

// === NUMERIC VALIDATIONS ===

// Validazioni monetarie
export const MoneySchema = z.number()
  .min(0, 'Amount must be positive')
  .max(99999999.99, 'Amount too large')
  .multipleOf(0.01, 'Amount must have max 2 decimal places');

// Validazioni percentuali
export const PercentageSchema = z.number()
  .min(0, 'Percentage must be positive')
  .max(100, 'Percentage cannot exceed 100')
  .multipleOf(0.01, 'Percentage must have max 2 decimal places');

// Validazioni ore
export const HoursSchema = z.number()
  .min(0, 'Hours must be positive')
  .max(999999.99, 'Hours value too large')
  .multipleOf(0.01, 'Hours must have max 2 decimal places');

// === MODEL VALIDATIONS ===

// Person validation
export const PersonValidationSchema = z.object({
  email: z.string().email('Invalid email format'),
  firstName: z.string().min(1, 'First name required').max(100, 'First name too long'),
  lastName: z.string().min(1, 'Last name required').max(100, 'Last name too long'),
  taxCode: z.string().regex(/^[A-Z]{6}[0-9]{2}[A-Z][0-9]{2}[A-Z][0-9]{3}[A-Z]$/, 'Invalid tax code').optional(),
  status: PersonStatusSchema.optional(),
  gender: GenderSchema.optional()
});

// Company validation
export const CompanyValidationSchema = z.object({
  ragioneSociale: z.string().min(1, 'Company name required').max(255, 'Company name too long'),
  piva: z.string().regex(/^[0-9]{11}$/, 'Invalid VAT number').optional(),
  codiceFiscale: z.string().regex(/^[0-9]{11}$/, 'Invalid fiscal code').optional(),
  status: CompanyStatusSchema.optional(),
  type: CompanyTypeSchema.optional()
});

// Course validation
export const CourseValidationSchema = z.object({
  title: z.string().min(1, 'Course title required').max(255, 'Title too long'),
  description: z.string().max(2000, 'Description too long').optional(),
  status: CourseStatusSchema.optional(),
  level: CourseLevelSchema.optional(),
  type: CourseTypeSchema.optional(),
  price: MoneySchema.optional(),
  duration: HoursSchema.optional()
});

// === UTILITY FUNCTIONS ===

/**
 * Valida un oggetto con schema Zod
 */
export function validateWithSchema<T>(schema: z.ZodSchema<T>, data: unknown): {
  success: boolean;
  data?: T;
  errors?: string[];
} {
  try {
    const result = schema.parse(data);
    return { success: true, data: result };
  } catch (error) {
    if (error instanceof z.ZodError) {
      return {
        success: false,
        errors: error.errors.map(e => e.path.join('.') + ': ' + e.message)
      };
    }
    return {
      success: false,
      errors: ['Validation failed']
    };
  }
}

/**
 * Middleware per validazione automatica
 */
export function createValidationMiddleware<T>(schema: z.ZodSchema<T>) {
  return (req: any, res: any, next: any) => {
    const validation = validateWithSchema(schema, req.body);

    if (!validation.success) {
      return res.status(400).json({
        error: 'Validation failed',
        details: validation.errors
      });
    }

    req.validatedData = validation.data;
    next();
  };
}

export default {
  PersonValidationSchema,
  CompanyValidationSchema,
  CourseValidationSchema,
  MoneySchema,
  PercentageSchema,
  HoursSchema,
  validateWithSchema,
  createValidationMiddleware
};

'use client';

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { Course } from '@prisma/client';
import { createCourse, getCourse, updateCourse } from '@/api/courses';
import { Button } from '@/design-system/atoms/Button';
import { Input } from '@/design-system/atoms/Input';
import { Label } from '@/design-system/atoms/Label';
import { Select } from '@/design-system/atoms/Select';
import { toast } from 'sonner';

type CourseFormData = Omit<Course, 'id' | 'createdAt' | 'updatedAt'>;

const initialFormData: CourseFormData = {
  title: '',
  category: '',
  description: '',
  duration: '',
  status: 'DRAFT',
};

export default function CourseForm({ params }: { params: { id: string } }) {
  const router = useRouter();
  const [formData, setFormData] = useState<CourseFormData>(initialFormData);
  const isEditing = params.id !== 'new';

  useEffect(() => {
    if (isEditing) {
      loadCourse();
    }
  }, [isEditing]);

  const loadCourse = async () => {
    try {
      const course = await getCourse(params.id);
      if (course) {
        setFormData({
          title: course.title,
          category: course.category || '',
          description: course.description || '',
          duration: course.duration || '',
          status: course.status || 'DRAFT',
        });
      }
    } catch {
      toast.error('Failed to load course');
      router.push('/courses');
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      if (isEditing) {
        await updateCourse(params.id, formData);
        toast.success('Course updated successfully');
      } else {
        await createCourse(formData);
        toast.success('Course created successfully');
      }
      router.push('/courses');
    } catch {
      toast.error(isEditing ? 'Failed to update course' : 'Failed to create course');
    }
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div className="container mx-auto py-10">
      <h1 className="text-3xl font-bold mb-6">
        {isEditing ? 'Edit Course' : 'New Course'}
      </h1>
      <form onSubmit={handleSubmit} className="space-y-6 max-w-2xl">
        <div>
          <Label htmlFor="title">Title</Label>
          <Input
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <Label htmlFor="category">Category</Label>
          <Input
            id="category"
            name="category"
            value={formData.category || ''}
            onChange={handleChange}
          />
        </div>

        <div>
          <Label htmlFor="description">Description</Label>
          <textarea
            id="description"
            name="description"
            value={formData.description || ''}
            onChange={handleChange}
            className="w-full min-h-[100px] p-2 border rounded-md"
          />
        </div>

        <div>
          <Label htmlFor="duration">Duration</Label>
          <Input
            id="duration"
            name="duration"
            value={formData.duration || ''}
            onChange={handleChange}
          />
        </div>

        <div>
          <Label htmlFor="status">Status</Label>
          <Select
            id="status"
            name="status"
            value={formData.status}
            onChange={handleChange}
          >
            <option value="DRAFT">Draft</option>
            <option value="PUBLISHED">Published</option>
            <option value="ARCHIVED">Archived</option>
          </Select>
        </div>

        <div className="flex gap-4">
          <Button type="submit">
            {isEditing ? 'Update Course' : 'Create Course'}
          </Button>
          <Button
            type="button"
            variant="outline"
            onClick={() => router.push('/courses')}
          >
            Cancel
          </Button>
        </div>
      </form>
    </div>
  );
}

import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import { vi, describe, it, beforeEach, expect } from 'vitest';
import RoleHierarchy from '../roles/RoleHierarchy';
import { getRoleHierarchy, getCurrentUserRoleHierarchy } from '../../services/roles';

// Mock del servizio roles
vi.mock('../../services/roles', () => ({
  RolesService: {
    getRoles: vi.fn(),
    createRole: vi.fn(),
    updateRole: vi.fn(),
    deleteRole: vi.fn(),
    moveRole: vi.fn(),
    getPermissions: vi.fn(),
  },
  getRoleHierarchy: vi.fn(),
  getCurrentUserRoleHierarchy: vi.fn(),
}));

vi.mock('@/services/auth', () => ({
  isAuthenticated: vi.fn().mockReturnValue(true),
}));

// Mock dei componenti UI
vi.mock('@/components/ui/use-toast', () => ({
  toast: vi.fn(),
}));

const mockHierarchy = {
  ADMIN: {
    name: 'Amministratore',
    description: 'Amministratore del sistema',
    level: 1,
    permissions: ['USER_MANAGEMENT', 'ROLE_MANAGEMENT'],
    assignableRoles: ['MANAGER', 'EMPLOYEE'],
  },
  MANAGER: {
    name: 'Manager',
    description: 'Manager aziendale',
    level: 2,
    permissions: ['EMPLOYEE_MANAGEMENT'],
    assignableRoles: ['EMPLOYEE'],
  },
  EMPLOYEE: {
    name: 'Dipendente',
    description: 'Dipendente standard',
    level: 3,
    permissions: ['BASIC_ACCESS'],
    assignableRoles: [],
  },
};

const mockUserHierarchy = {
  highestRole: 'ADMIN',
  userLevel: 1,
  userRoles: ['ADMIN'],
  assignableRoles: ['MANAGER', 'EMPLOYEE'],
};

// Mock dei dati di test rimossi perch non utilizzati

describe('RoleHierarchy CRUD', () => {
  beforeEach(() => {
    vi.clearAllMocks();

    // Mock delle funzioni specifiche per RoleHierarchy
    (getRoleHierarchy as any).mockResolvedValue(mockHierarchy);
    (getCurrentUserRoleHierarchy as any).mockResolvedValue(mockUserHierarchy);
  });

  it('should switch between list and tree view modes', async () => {
    render(<RoleHierarchy />);

    await waitFor(() => {
      expect(screen.getByText('Albero')).toBeInTheDocument();
      expect(screen.getByText('Lista')).toBeInTheDocument();
    });

    // Clicca sulla vista lista
    fireEvent.click(screen.getByText('Lista'));

    // Verifica che il componente risponda al click
    await waitFor(() => {
      expect(screen.getByText('Lista')).toBeInTheDocument();
    });
  });

  it('should filter roles when searching', async () => {
    render(<RoleHierarchy />);

    await waitFor(() => {
      expect(screen.getByText('Lista')).toBeInTheDocument();
    });

    // Passa alla vista lista per vedere la barra di ricerca
    fireEvent.click(screen.getByText('Lista'));

    await waitFor(() => {
      expect(screen.getByPlaceholderText('Cerca ruoli...')).toBeInTheDocument();
    });

    // Cerca un ruolo specifico
    const searchInput = screen.getByPlaceholderText('Cerca ruoli...');
    fireEvent.change(searchInput, { target: { value: 'Admin' } });

    // Verifica che solo i ruoli corrispondenti siano visibili
    expect(screen.getByText('Amministratore')).toBeInTheDocument();
  });
});

import React, { useState, useEffect } from 'react';
import { 
  Building2,
  Calendar,
  Edit,
  Eye,
  FileText,
  Mail,
  MapPin,
  Phone,
  Plus,
  Shield,
  Stethoscope,
  Trash2,
  User,
  Users
} from 'lucide-react';
import { apiGet, apiDelete } from '../../services/api';
import { useToast } from '../../hooks/useToast';
import { DVRManager } from '../managers/DVRManager';
import SopralluogoManager from '../managers/SopralluogoManager';
import RepartoManager from '../managers/RepartoManager';
import CompanySiteForm from './CompanySiteForm';

interface CompanySite {
  id: string;
  siteName: string;
  citta: string;
  indirizzo: string;
  cap: string;
  provincia: string;
  personaRiferimento?: string;
  telefono?: string;
  mail?: string;
  dvr?: string;
  rsppId?: string;
  medicoCompetenteId?: string;
  // Campi sopralluogo corretti secondo lo schema
  ultimoSopralluogo?: string;
  prossimoSopralluogo?: string;
  valutazioneSopralluogo?: string;
  sopralluogoEseguitoDa?: string;
  rspp?: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
  };
  medicoCompetente?: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
  };
  companyId: string;
  createdAt: string;
  updatedAt: string;
}

interface CompanySitesProps {
  companyId: string;
  selectedSiteId?: string | null;
  onSiteFilterChange?: (siteId: string | null) => void;
}

const CompanySites: React.FC<CompanySitesProps> = ({ companyId, selectedSiteId, onSiteFilterChange }) => {
  const [sites, setSites] = useState<CompanySite[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingSite, setEditingSite] = useState<CompanySite | null>(null);
  const [activeManager, setActiveManager] = useState<{
    type: 'dvr' | 'sopralluogo' | 'reparto' | null;
    siteId: string;
    siteName: string;
  }>({ type: null, siteId: '', siteName: '' });
  const { showToast } = useToast();

  // Filtra le sedi in base alla selezione
  const filteredSites = selectedSiteId 
    ? sites.filter(site => site.id === selectedSiteId)
    : sites;

  useEffect(() => {
    fetchSites();
  }, [companyId]);

  const fetchSites = async () => {
    try {
      setLoading(true);
      const response = await apiGet(`/api/v1/company-sites/company/${companyId}`) as { sites: CompanySite[] };
      setSites(response.sites || []);
    } catch (error) {
      console.error('Error fetching company sites:', error);
      showToast({ message: 'Errore nel caricamento delle sedi', type: 'error' });
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (site: CompanySite) => {
    setEditingSite(site);
    setShowForm(true);
  };

  const handleDelete = async (siteId: string) => {
    if (!confirm('Sei sicuro di voler eliminare questa sede?')) return;

    try {
      await apiDelete(`/api/v1/company-sites/${siteId}`);
      await fetchSites();
      showToast({ message: 'Sede eliminata con successo', type: 'success' });
    } catch (error) {
      console.error('Error deleting site:', error);
      showToast({ message: 'Errore nell\'eliminazione della sede', type: 'error' });
    }
  };

  const resetForm = () => {
    setEditingSite(null);
  };

  const handleFormClose = () => {
    setShowForm(false);
    resetForm();
  };

  const handleFormSuccess = () => {
    fetchSites();
    handleFormClose();
    showToast({ 
      message: editingSite ? 'Sede aggiornata con successo' : 'Sede creata con successo', 
      type: 'success' 
    });
  };

  if (loading) {
    return (
      <div className="bg-white rounded-lg shadow p-6">
        <div className="animate-pulse">
          <div className="h-6 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="space-y-3">
            <div className="h-4 bg-gray-200 rounded"></div>
            <div className="h-4 bg-gray-200 rounded w-3/4"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow overflow-hidden">
      <div className="px-6 py-4 border-b border-gray-200">
        <div className="flex flex-wrap items-center justify-between gap-3 mb-3">
          <h2 className="text-lg font-semibold text-gray-800 flex items-center flex-shrink-0">
            <Building2 className="h-5 w-5 mr-2" />
            Sedi Aziendali
          </h2>

          <button
            onClick={() => setShowForm(true)}
            className="px-4 py-2 text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 rounded-full transition-colors flex items-center shadow-md hover:shadow-lg flex-shrink-0"
          >
            <Plus className="h-4 w-4 mr-1" />
            Aggiungi Sede
          </button>
        </div>

        {/* Filtro sedi centrato - Solo se ci sono pi sedi */}
        {sites.length > 1 && onSiteFilterChange && (
          <div className="flex justify-center">
            <div className="flex bg-gray-100 rounded-full shadow-sm border border-gray-200 overflow-x-auto px-1.5 py-0.5 gap-0.5">
              <button
                type="button"
                onClick={() => onSiteFilterChange(null)}
                className={`px-2.5 py-1 text-xs font-medium rounded-full transition-colors duration-200 focus:outline-none focus:ring-1 focus:ring-blue-300 whitespace-nowrap ${
                  selectedSiteId === null
                    ? 'bg-blue-600 text-white shadow-sm'
                    : 'bg-transparent text-gray-700 hover:bg-blue-100'
                }`}
              >
                Tutte ({sites.length})
              </button>
              {sites.map(site => (
                <button
                  key={site.id}
                  type="button"
                  onClick={() => onSiteFilterChange(site.id)}
                  className={`px-2.5 py-1 text-xs font-medium rounded-full transition-colors duration-200 focus:outline-none focus:ring-1 focus:ring-blue-300 whitespace-nowrap ${
                    selectedSiteId === site.id
                      ? 'bg-blue-600 text-white shadow-sm'
                      : 'bg-transparent text-gray-700 hover:bg-blue-100'
                  }`}
                >
                  {site.siteName}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>

      {sites.length === 0 ? (
        <div className="px-6 py-8 text-center">
          <Building2 className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">Nessuna sede trovata</h3>
          <p className="text-gray-500 mb-4">Aggiungi la prima sede per questa azienda.</p>
          <button
            onClick={() => setShowForm(true)}
            className="px-4 py-2 text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 rounded-full transition-colors flex items-center shadow-md hover:shadow-lg mx-auto"
          >
            <Plus className="h-4 w-4 mr-1" />
            Aggiungi Prima Sede
          </button>
        </div>
      ) : filteredSites.length === 0 ? (
        <div className="px-6 py-8 text-center">
          <Building2 className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">Nessuna sede corrispondente</h3>
          <p className="text-gray-500 mb-4">La sede selezionata non  stata trovata.</p>
        </div>
      ) : (
        <div className="p-6">
          {filteredSites.length === 1 ? (
            // Visualizzazione diretta per sede singola
            <div className="space-y-6">
              {filteredSites.map((site) => (
                <div key={site.id}>
                  {/* Header con nome sede e azioni */}
                  <div className="flex justify-between items-center mb-6">
                    <div className="flex items-center">
                      <Building2 className="h-5 w-5 text-blue-600 mr-2" />
                      <h3 className="text-lg font-semibold text-gray-900">{site.siteName}</h3>
                    </div>
                    <div className="flex flex-wrap gap-2">
                      <button
                        onClick={() => setActiveManager({ type: 'dvr', siteId: site.id, siteName: site.siteName })}
                        className="px-3 py-1.5 text-xs font-medium text-blue-700 bg-blue-100 hover:bg-blue-200 rounded-full transition-colors flex items-center"
                        title="Gestisci DVR"
                      >
                        <FileText className="h-3 w-3 mr-1" />
                        DVR
                      </button>
                      <button
                        onClick={() => setActiveManager({ type: 'sopralluogo', siteId: site.id, siteName: site.siteName })}
                        className="px-3 py-1.5 text-xs font-medium text-green-700 bg-green-100 hover:bg-green-200 rounded-full transition-colors flex items-center"
                        title="Gestisci Sopralluoghi"
                      >
                        <Eye className="h-3 w-3 mr-1" />
                        Sopralluoghi
                      </button>
                      <button
                        onClick={() => setActiveManager({ type: 'reparto', siteId: site.id, siteName: site.siteName })}
                        className="px-3 py-1.5 text-xs font-medium text-purple-700 bg-purple-100 hover:bg-purple-200 rounded-full transition-colors flex items-center"
                        title="Gestisci Reparti"
                      >
                        <Users className="h-3 w-3 mr-1" />
                        Reparti
                      </button>
                      <button
                        onClick={() => handleEdit(site)}
                        className="px-3 py-1.5 text-xs font-medium text-blue-700 bg-blue-100 hover:bg-blue-200 rounded-full transition-colors flex items-center"
                        title="Modifica sede"
                      >
                        <Edit className="h-3 w-3 mr-1" />
                        Modifica
                      </button>
                    </div>
                  </div>

                  {/* Griglia informazioni dirette */}
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    {/* Indirizzo */}
                    <div className="space-y-3">
                      <h4 className="text-sm font-semibold text-gray-800 uppercase tracking-wide">Indirizzo</h4>
                      <div className="space-y-2">
                        <div className="flex items-center text-sm text-gray-600">
                          <MapPin className="h-4 w-4 mr-2 text-blue-500" />
                          {site.indirizzo}
                        </div>
                        <div className="text-sm text-gray-600 ml-6">
                          {site.citta}, {site.cap} ({site.provincia})
                        </div>
                      </div>
                    </div>

                    {/* Contatti */}
                    <div className="space-y-3">
                      <h4 className="text-sm font-semibold text-gray-800 uppercase tracking-wide">Contatti</h4>
                      <div className="space-y-2">
                        {site.personaRiferimento && (
                          <div className="flex items-start text-sm text-gray-600">
                            <User className="h-4 w-4 mr-2 mt-0.5 flex-shrink-0 text-green-500" />
                            <span className="break-words">{site.personaRiferimento}</span>
                          </div>
                        )}
                        {site.telefono && (
                          <div className="flex items-start text-sm text-gray-600">
                            <Phone className="h-4 w-4 mr-2 mt-0.5 flex-shrink-0 text-green-500" />
