                        <Calendar className="h-4 w-4 mr-2 text-blue-600" />
                        Sopralluoghi
                      </h4>
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm text-gray-600">
                        {site.ultimoSopralluogo && (
