                     <div className="flex flex-wrap gap-2 ml-4">
                       {/* Pulsanti a forma di pillola per gestire DVR, Sopralluoghi e Reparti */}
                       <button
                         onClick={() => setActiveManager({ type: 'dvr', siteId: site.id, siteName: site.siteName })}
