import React, { useState, useEffect } from 'react';
import { Users } from 'lucide-react';
import { apiGet } from '../../services/api';
import { useToast } from '../../hooks/useToast';

interface Person {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  title?: string;
  photo_url?: string;
  siteId?: string;
}

interface EmployeesSectionProps {
  companyId: string;
}

const EmployeesSection: React.FC<EmployeesSectionProps> = ({ companyId }) => {
  const [employees, setEmployees] = useState<Person[]>([]);
  const [loading, setLoading] = useState(true);
  const { showToast } = useToast();

  useEffect(() => {
    fetchEmployees();
  }, [companyId]);

  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const response = await apiGet(`/api/v1/persons?companyId=${companyId}&roleType=EMPLOYEE`) as { persons?: Person[] };
      setEmployees(response.persons || []);
    } catch (error) {
      console.error('Error fetching employees:', error);
      showToast({ message: 'Errore nel caricamento delle persone', type: 'error' });
    } finally {
      setLoading(false);
    }
  };

  const getInitials = (firstName: string, lastName: string) => {
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase();
  };

  if (loading) {
    return (
      <div className="bg-white rounded-lg shadow p-6">
        <div className="animate-pulse">
          <div className="h-6 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="flex flex-col items-center space-y-2">
                <div className="w-16 h-16 bg-gray-200 rounded-full"></div>
                <div className="h-4 bg-gray-200 rounded w-20"></div>
                <div className="h-3 bg-gray-200 rounded w-16"></div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow overflow-hidden">
      <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
        <h2 className="text-lg font-semibold text-gray-800 flex items-center">
          <Users className="h-5 w-5 mr-2" />
          Persone
        </h2>
        <span className="text-sm text-gray-500">
          {employees.length} persone
        </span>
      </div>

      <div className="p-6">
        {employees.length === 0 ? (
          <div className="text-center py-8">
            <Users className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Nessuna persona trovata</h3>
            <p className="text-gray-500">Non ci sono persone associate a questa azienda.</p>
          </div>
        ) : (
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-6">
            {employees.map((employee) => (
              <div key={employee.id} className="flex flex-col items-center space-y-3 group">
                {/* Avatar con foto o iniziali */}
                <div className="relative">
                  {employee.photo_url ? (
                    <img
                      src={employee.photo_url}
                      alt={`${employee.firstName} ${employee.lastName}`}
                      className="w-16 h-16 rounded-full object-cover border-2 border-gray-200 group-hover:border-blue-300 transition-colors"
                    />
                  ) : (
                    <div className="w-16 h-16 rounded-full bg-gradient-to-br from-blue-400 to-blue-600 flex items-center justify-center border-2 border-gray-200 group-hover:border-blue-300 transition-colors">
                      <span className="text-white font-semibold text-lg">
                        {getInitials(employee.firstName, employee.lastName)}
                      </span>
                    </div>
                  )}

                  {/* Indicatore online/status */}
                  <div className="absolute -bottom-1 -right-1 w-5 h-5 bg-green-400 border-2 border-white rounded-full"></div>
                </div>

                {/* Informazioni persona */}
                <div className="text-center space-y-1">
                  <h3 className="text-sm font-medium text-gray-900 group-hover:text-blue-600 transition-colors">
                    {employee.firstName} {employee.lastName}
                  </h3>
                  <p className="text-xs text-gray-500 leading-tight">
                    {employee.title || 'Persona'}
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default EmployeesSection;

import React, { useState, useEffect } from 'react';
import { 
  Building2,
  Plus,
  Users
} from 'lucide-react';
import { apiGet } from '../../services/api';
import { useToast } from '../../hooks/useToast';
import TabPills from '../ui/TabPills';
import SiteCard from './SiteCard';
import CompanySiteForm from './CompanySiteForm';
import { DVRManager, RepartoManager, SopralluogoManager } from '../managers';

interface CompanySite {
  id: string;
  siteName: string;
  citta: string;
  indirizzo: string;
  cap: string;
  provincia: string;
  personaRiferimento?: string;
  telefono?: string;
  mail?: string;
  dvr?: string;
  rsppId?: string;
  medicoCompetenteId?: string;
  ultimoSopralluogo?: string;
  prossimoSopralluogo?: string;
  valutazioneSopralluogo?: string;
  sopralluogoEseguitoDa?: string;
  rspp?: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
  };
  medicoCompetente?: {
    id: string;
    firstName: string;
    lastName: string;
    email: string;
  };
  companyId: string;
  createdAt: string;
  updatedAt: string;
}

interface Employee {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  title?: string;
  siteId?: string;
}

interface MultiSiteManagerProps {
  companyId: string;
  companyName: string;
}

const MultiSiteManager: React.FC<MultiSiteManagerProps> = ({ companyId }) => {
  const [sites, setSites] = useState<CompanySite[]>([]);
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('company');
  const [showForm, setShowForm] = useState(false);
  const [editingSite, setEditingSite] = useState<CompanySite | null>(null);
  const [activeManager, setActiveManager] = useState<{
    type: 'dvr' | 'sopralluogo' | 'reparto' | null;
    siteId: string;
    siteName: string;
  }>({ type: null, siteId: '', siteName: '' });
  const { showToast } = useToast();

  useEffect(() => {
    fetchData();
  }, [companyId]);

  const fetchData = async () => {
    try {
      setLoading(true);

      // Fetch sites
      const sitesResponse = await apiGet(`/api/v1/company-sites/company/${companyId}`) as { sites: CompanySite[] };
      const sitesData = sitesResponse.sites || [];
      setSites(sitesData);

      // Fetch employees
      const employeesResponse = await apiGet(`/api/v1/persons?companyId=${companyId}&roleType=EMPLOYEE`) as { persons?: Employee[] };
      setEmployees(employeesResponse.persons || []);

      // Set default tab (company if no sites, first site if sites exist)
      if (sitesData.length > 0 && activeTab === 'company') {
        setActiveTab('company');
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      showToast({ message: 'Errore nel caricamento dei dati', type: 'error' });
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (site: CompanySite) => {
    setEditingSite(site);
    setShowForm(true);
  };

  const handleDelete = async (siteId: string) => {
    if (!confirm('Sei sicuro di voler eliminare questa sede?')) return;

    try {
      await apiGet(`/api/company-sites/${siteId}`, { method: 'DELETE' });
      await fetchData();
      showToast({ message: 'Sede eliminata con successo', type: 'success' });

      // Reset active tab if deleted site was selected
      if (activeTab === siteId) {
        setActiveTab('company');
      }
    } catch (error) {
      console.error('Error deleting site:', error);
      showToast({ message: 'Errore nell\'eliminazione della sede', type: 'error' });
    }
  };

  const handleFormClose = () => {
    setShowForm(false);
    setEditingSite(null);
  };

  const handleFormSuccess = () => {
    fetchData();
    handleFormClose();
    showToast({ 
      message: editingSite ? 'Sede aggiornata con successo' : 'Sede creata con successo', 
      type: 'success' 
    });
  };

  // Prepare tabs
  const tabs = [
    {
      id: 'company',
      label: 'Azienda',
      count: employees.length
    },
    ...sites.map((site, index) => ({
      id: site.id,
      label: `Sede ${index + 1}`,
      count: employees.filter(emp => emp.siteId === site.id).length
    }))
  ];

  // Filter data based on active tab
  const getFilteredEmployees = () => {
    if (activeTab === 'company') {
      return employees;
    }
    return employees.filter(emp => emp.siteId === activeTab);
  };

  const getActiveSite = () => {
    if (activeTab === 'company') return null;
    return sites.find(site => site.id === activeTab);
  };

  if (loading) {
    return (
      <div className="bg-white rounded-lg shadow p-6">
        <div className="animate-pulse">
          <div className="h-6 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="space-y-3">
            <div className="h-4 bg-gray-200 rounded"></div>
            <div className="h-4 bg-gray-200 rounded w-3/4"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold text-gray-800 flex items-center">
            <Building2 className="h-5 w-5 mr-2" />
            Gestione Sedi Aziendali
          </h2>
          <button
            onClick={() => setShowForm(true)}
            className="px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-full hover:bg-blue-700 transition-colors flex items-center"
          >
            <Plus className="h-4 w-4 mr-1" />
            Aggiungi Sede
          </button>
        </div>

        {/* Tab Pills - Show only if there are multiple sites */}
        {sites.length > 0 && (
          <div className="mb-6">
            <TabPills
              tabs={tabs}
              activeTab={activeTab}
              onTabChange={setActiveTab}
              className="mb-4"
            />
          </div>
        )}
      </div>

      {/* Sites Grid */}
      {sites.length === 0 ? (
        <div className="bg-white rounded-lg shadow overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
            <h2 className="text-lg font-semibold text-gray-800 flex items-center">
