import React, { useState } from 'react';
import GenericImport, { defaultProcessFile } from '../../shared/GenericImport';
import { useToast } from '../../../hooks/useToast';
import CompanyImportConflictModal from './CompanyImportConflictModal';
import { CompanyImportProps } from './types';
import { csvHeaderMap, columnOrder } from './constants';
import { validateCompany, formatCompanyData, detectConflicts, convertToApiFormat } from './utils';

/**
 * Componente per l'importazione di aziende da file CSV - Versione Refactorizzata
 */
const CompanyImportRefactored: React.FC<CompanyImportProps> = ({
  onImport,
  onClose,
  existingCompanies = []
}) => {
  const [importData, setImportData] = useState<any[]>([]);
  const { showToast } = useToast();
  const [conflicts] = useState<any[]>([]);
  const [showConflictModal, setShowConflictModal] = useState(false);

  // Funzione personalizzata per processare il file CSV
  const customProcessFile = async (file: File): Promise<any[]> => {
    try {
      console.log('Aziende esistenti ricevute:', existingCompanies?.length || 0, existingCompanies?.[0]);

      // Processa il file e ottieni i dati grezzi
      const processedData = await defaultProcessFile(file, csvHeaderMap);

      // Applica formattazione e validazione
      const formattedData = processedData.map(company => {
        // Prima mappa i campi alias ai campi originali
        const mappedCompany = { ...company };

        // Mappatura campi alias inglesi ai campi originali
        const aliasMapping: Record<string, string> = {
          companyName: 'ragioneSociale',
          atecoCode: 'codiceAteco',
          vatNumber: 'piva',
          taxCode: 'codiceFiscale',
          email: 'mail',
          phone: 'telefono',
          city: 'citta',
          province: 'provincia',
          zip: 'cap',
          address: 'sedeAzienda',
          contactPerson: 'personaRiferimento',
          notes: 'note',
          active: 'isActive',
          siteName2: 'siteName',
          siteAddress: 'siteIndirizzo',
          siteCity2: 'siteCitta',
          siteProvince2: 'siteProvincia',
          siteZip: 'siteCap',
          siteContact: 'sitePersonaRiferimento',
          sitePhone2: 'siteTelefono',
          siteEmail2: 'siteMail'
        };

        // Applica la mappatura degli alias
        Object.entries(aliasMapping).forEach(([alias, original]) => {
          if (mappedCompany[alias] !== undefined && mappedCompany[original] === undefined) {
            mappedCompany[original] = mappedCompany[alias];
            delete mappedCompany[alias];
          }
        });

        // Se manca "Nome Sede", usa "Citt Sede"
        if (!mappedCompany.siteName && mappedCompany.siteCitta) {
          mappedCompany.siteName = mappedCompany.siteCitta;
        }

        // Rileva se ci sono campi sede specifici
        const hasSiteSpecificFields = !!(
          mappedCompany.siteName || 
          mappedCompany.siteIndirizzo || 
          mappedCompany.siteCitta || 
          mappedCompany.siteProvincia || 
          mappedCompany.siteCap || 
          mappedCompany.sitePersonaRiferimento || 
          mappedCompany.siteTelefono || 
          mappedCompany.siteMail
        );

        if (hasSiteSpecificFields) {
          mappedCompany._hasSiteData = true;
        }

        return formatCompanyData(mappedCompany);
      });

      // Verifica la presenza di dati basilari
      if (formattedData.length === 0) {
        throw new Error('Il file non contiene dati validi');
      }

      // Conta quante righe hanno la ragione sociale
      const validRowsCount = formattedData.filter(c => c.ragioneSociale).length;
      if (validRowsCount === 0) {
        throw new Error('Nessuna riga contiene la Ragione Sociale, che  un campo obbligatorio');
      }

      // Verifica che almeno il 50% delle righe contenga la ragione sociale
      if (validRowsCount < formattedData.length / 2) {
        showToast({
          message: `Attenzione: solo ${validRowsCount} su ${formattedData.length} righe contengono la Ragione Sociale`,
          type: 'warning'
        });
      }

      // Rileva conflitti e duplicati
      const dataWithConflicts = detectConflicts(formattedData, existingCompanies);

      // Salva i dati processati
      setImportData(dataWithConflicts);

      return dataWithConflicts;
    } catch (error) {
      console.error('Errore durante il processamento del file:', error);
      throw error;
    }
  };

  // Gestione dell'importazione
  const handleImport = async (selectedData: any[], overwriteIds?: string[]) => {
    try {
      // Filtra solo i dati selezionati che non sono duplicati
      const dataToImport = selectedData.filter(company => !company._isDuplicateSite);

      if (dataToImport.length === 0) {
        showToast({
          message: 'Nessuna azienda valida da importare',
          type: 'warning'
        });
        return;
      }

      // Valida tutti i dati prima dell'importazione
      const validationErrors: string[] = [];
      dataToImport.forEach((company, index) => {
        const errors = validateCompany(company);
        if (errors.length > 0) {
          validationErrors.push(`Riga ${index + 1}: ${errors.join(', ')}`);
        }
      });

      if (validationErrors.length > 0) {
        showToast({
          message: `Errori di validazione:\n${validationErrors.join('\n')}`,
          type: 'error'
        });
        return;
      }

      // Converti i dati nel formato API
      const apiData = dataToImport.map(convertToApiFormat);

      // Chiama la funzione di importazione
      await onImport(apiData, overwriteIds);

      showToast({
        message: `${dataToImport.length} aziende importate con successo`,
        type: 'success'
      });

      onClose();
    } catch (error) {
      console.error('Errore durante l\'importazione:', error);
      showToast({
        message: `Errore durante l'importazione: ${error instanceof Error ? error.message : 'Errore sconosciuto'}`,
        type: 'error'
      });
    }
  };

  // Gestione dei conflitti
  const handleConflictResolution = (resolutions: any[]) => {
    const overwriteIds = resolutions
      .filter(r => r.action === 'overwrite')
      .map(r => r.companyId);

    setShowConflictModal(false);
    handleImport(importData, overwriteIds);
  };

  return (
    <>
      <GenericImport
        entityType="aziende"
        uniqueField="piva"
        title="Importa Aziende"
        subtitle="Carica un file CSV con i dati delle aziende da importare"
        columnOrder={columnOrder}
        customProcessFile={customProcessFile}
        onImport={handleImport}
        onClose={onClose}
        customValidation={validateCompany}
        initialPreviewData={importData}
        existingEntities={existingCompanies}
      />

      <CompanyImportConflictModal
        isOpen={showConflictModal}
        onClose={() => setShowConflictModal(false)}
        conflicts={conflicts}
        onResolve={handleConflictResolution}
      />
    </>
  );
};

export default CompanyImportRefactored;

import { titleCaseFields } from './constants';
import { applyTitleCaseToFields } from '../../../utils/textFormatters';

// Validazione personalizzata per le aziende
export const validateCompany = (company: any): string[] => {
  const errors: string[] = [];

  if (!company.ragioneSociale) {
    errors.push('Ragione Sociale obbligatoria');
  } else if (company.ragioneSociale.length > 250) {
    errors.push('Ragione Sociale troppo lunga (max 250 caratteri)');
  }

  // Verifica che ci sia almeno uno tra P.IVA e Codice Fiscale
  if (!company.piva && !company.codiceFiscale) {
    errors.push('P.IVA o Codice Fiscale obbligatori');
  }

  // Verifica della P.IVA (se presente)
  if (company.piva) {
    if (company.piva.length < 8 || company.piva.length > 13) {
      errors.push('P.IVA non valida (deve essere tra 8 e 13 caratteri)');
    }

    // Verifica che contenga solo numeri
    if (!/^\d+$/.test(company.piva)) {
      errors.push('P.IVA deve contenere solo numeri');
    }
  }

  // Verifica del Codice Fiscale SOLO se non c' una P.IVA valida
  if (!company.piva && company.codiceFiscale) {
    // Se il codice fiscale  per un'azienda (11 caratteri) o una persona (16 caratteri)
    if (company.codiceFiscale.length !== 16 && company.codiceFiscale.length !== 11) {
      errors.push('Codice Fiscale non valido (deve essere 16 caratteri per persone fisiche o 11 per aziende)');
    }
  }

  // Verifica campi che potrebbero causare errori 500
  if (company.sdi && company.sdi.length > 7) {
    errors.push('Codice SDI troppo lungo (max 7 caratteri)');
  }

  if (company.pec && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(company.pec)) {
    errors.push('Formato PEC non valido');
  }

  if (company.mail && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(company.mail)) {
    errors.push('Formato Mail non valido');
  }

  if (company.telefono && !/^[\d\s+\-().]+$/.test(company.telefono)) {
    errors.push('Formato Telefono non valido (sono consentiti solo numeri, spazi e caratteri +-(). )');
  }

  // Validazione domini
  if (company.domain && !/^[a-zA-Z0-9][a-zA-Z0-9-]{1,61}[a-zA-Z0-9]\.[a-zA-Z]{2,}$/.test(company.domain)) {
    errors.push('Formato Dominio non valido');
  }

  // Validazione slug
  if (company.slug && !/^[a-z0-9-]+$/.test(company.slug)) {
    errors.push('Slug deve contenere solo lettere minuscole, numeri e trattini');
  }

  // Validazione date
  const dateFields = ['ultimoSopralluogo', 'prossimoSopralluogo', 'ultimoSopralluogoRSPP', 'prossimoSopralluogoRSPP', 'ultimoSopralluogoMedico', 'prossimoSopralluogoMedico'];
  dateFields.forEach(field => {
    if (company[field] && isNaN(Date.parse(company[field]))) {
      errors.push(`Formato data non valido per ${field}`);
    }
  });

  // Validazione boolean
  if (company.isActive && !['true', 'false', '1', '0', 's', 'no', 'si', 'yes', 'no'].includes(String(company.isActive).toLowerCase())) {
    errors.push('Campo Attivo deve essere true/false, 1/0, s/no, yes/no');
  }

  // Verifica lunghezza eccessiva per campi comuni - ALLINEATI AL TEMPLATE
  const maxLengthFields: [string, number][] = [
    ['sedeAzienda', 250],
    ['citta', 100],
    ['provincia', 50],
    ['cap', 10],
    ['personaRiferimento', 100],
    ['note', 1000],
    ['siteName', 100],
    ['indirizzo', 250],
    ['dvr', 100],
    ['valutazioneSopralluogo', 500],
    ['sopralluogoEseguitoDa', 100],
    ['noteSopralluogoRSPP', 500],
    ['noteSopralluogoMedico', 500],
    ['slug', 100],
    ['domain', 100],
    ['subscriptionPlan', 50],
    ['codiceAteco', 20],
    ['iban', 34],
    ['pec', 100],
    ['sdi', 7]
  ];

  maxLengthFields.forEach(([field, maxLength]) => {
    if (company[field] && company[field].length > maxLength) {
      errors.push(`Il campo ${field}  troppo lungo (max ${maxLength} caratteri)`);
    }
  });

  return errors;
};

// Funzione per formattare i dati dell'azienda
export const formatCompanyData = (company: any): any => {
  // Applica il Title Case ai campi specificati
  const formattedCompany = applyTitleCaseToFields(company, titleCaseFields);

  // Conversioni specifiche per i campi booleani
  if (formattedCompany.isActive) {
    const activeValue = String(formattedCompany.isActive).toLowerCase();
    formattedCompany.isActive = ['true', '1', 's', 'si', 'yes'].includes(activeValue);
  }

  // Normalizza i campi numerici
  if (formattedCompany.piva) {
    formattedCompany.piva = formattedCompany.piva.replace(/\D/g, ''); // Rimuovi caratteri non numerici
  }

  if (formattedCompany.codiceFiscale) {
    formattedCompany.codiceFiscale = formattedCompany.codiceFiscale.toUpperCase().replace(/\s/g, '');
  }

  // Normalizza le email
  if (formattedCompany.mail) {
    formattedCompany.mail = formattedCompany.mail.toLowerCase().trim();
  }

  if (formattedCompany.pec) {
    formattedCompany.pec = formattedCompany.pec.toLowerCase().trim();
  }

  return formattedCompany;
};

// Funzione per rilevare conflitti e duplicati
export const detectConflicts = (companies: any[], existingCompanies: any[] = []): any[] => {
  return companies.map((company) => {
    const conflictInfo = { ...company };

    // Cerca aziende esistenti con stesso P.IVA o Codice Fiscale
    const existingCompany = existingCompanies.find(existing => 
      (company.piva && existing.vatNumber === company.piva) ||
      (company.codiceFiscale && existing.taxCode === company.codiceFiscale)
    );

    if (existingCompany) {
      conflictInfo._isExisting = true;
      conflictInfo._existingId = existingCompany.id;

      // Verifica se  una nuova sede per un'azienda esistente
      if (company.siteName || company.siteIndirizzo) {
        const existingSite = existingCompany.sites?.find((site: any) => 
          site.name === company.siteName || 
          site.address === company.siteIndirizzo
        );

        if (existingSite) {
          conflictInfo._isDuplicateSite = true;
        } else {
          conflictInfo._isNewSite = true;
        }
      }
    } else if (company.siteName || company.siteIndirizzo) {
      conflictInfo._isNewCompanyWithSite = true;
    }

    return conflictInfo;
  });
};

// Funzione per convertire i dati per l'API
export const convertToApiFormat = (company: any): any => {
  return {
    name: company.ragioneSociale,
    atecoCode: company.codiceAteco,
    vatNumber: company.piva,
    taxCode: company.codiceFiscale,
    sdi: company.sdi,
    pec: company.pec,
    iban: company.iban,
    address: company.sedeAzienda,
    city: company.citta,
    province: company.provincia,
    postalCode: company.cap,
    email: company.mail,
    phone: company.telefono,
    contactPerson: company.personaRiferimento,
    notes: company.note,
    slug: company.slug,
    domain: company.domain,
    settings: company.settings,
    subscriptionPlan: company.subscriptionPlan,
    isActive: company.isActive,
    // Dati della sede se presenti
    ...(company.siteName && {
      sites: [{
        name: company.siteName,
        address: company.siteIndirizzo,
        city: company.siteCitta,
        province: company.siteProvincia,
        postalCode: company.siteCap,
        contactPerson: company.sitePersonaRiferimento,
        phone: company.siteTelefono,
        email: company.siteMail,
        dvr: company.dvr,
        rsppId: company.rsppId,
        medicoCompetenteId: company.medicoCompetenteId,
        ultimoSopralluogo: company.ultimoSopralluogo,
        prossimoSopralluogo: company.prossimoSopralluogo,
        valutazioneSopralluogo: company.valutazioneSopralluogo,
        sopralluogoEseguitoDa: company.sopralluogoEseguitoDa,
        ultimoSopralluogoRSPP: company.ultimoSopralluogoRSPP,
        prossimoSopralluogoRSPP: company.prossimoSopralluogoRSPP,
        noteSopralluogoRSPP: company.noteSopralluogoRSPP,
        ultimoSopralluogoMedico: company.ultimoSopralluogoMedico,
        prossimoSopralluogoMedico: company.prossimoSopralluogoMedico,
        noteSopralluogoMedico: company.noteSopralluogoMedico
      }]
    })
  };
};

