            <Grid item xs={6}>
              <TextField
                fullWidth
                type="date"
                label="End Date"
                value={tempFilters.endDate || ''}
                onChange={(e) => handleFilterChange('endDate', e.target.value)}
                InputLabelProps={{ shrink: true }}
