/**
 * Consent Management Tab Component
 * Handles GDPR consent management interface
 */

import React, { useState } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Switch,
  FormControlLabel,
  Alert,
  Chip,
  Stack,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Divider,
  LinearProgress,
  Tooltip,
  IconButton
} from '@mui/material';
import {
  Info as InfoIcon,
  CheckCircle as CheckCircleIcon,
  Cancel as CancelIcon,
  Refresh as RefreshIcon
} from '@mui/icons-material';
import { UseGDPRConsentReturn, ConsentType, CONSENT_TYPES } from '../../types/gdpr';
import { format } from 'date-fns';

interface ConsentManagementTabProps {
  hook: UseGDPRConsentReturn;
}

export const ConsentManagementTab: React.FC<ConsentManagementTabProps> = ({ hook }) => {
  const {
    consents,
    loading,
    error,
    grantConsent,
    withdrawConsent,
    refreshConsents,
    getConsentStats,
    hasConsent,
    getConsentByType
  } = hook;

  const [confirmDialog, setConfirmDialog] = useState<{
    open: boolean;
    type: ConsentType | null;
    action: 'grant' | 'withdraw';
  }>({ open: false, type: null, action: 'grant' });

  const [processingConsent, setProcessingConsent] = useState<string | null>(null);

  const stats = getConsentStats();

  const handleConsentToggle = (type: ConsentType, currentStatus: boolean) => {
    setConfirmDialog({
      open: true,
      type,
      action: currentStatus ? 'withdraw' : 'grant'
    });
  };

  const handleConfirmAction = async () => {
    if (!confirmDialog.type) return;

    try {
      setProcessingConsent(confirmDialog.type);

      if (confirmDialog.action === 'grant') {
        await grantConsent(confirmDialog.type);
      } else {
        await withdrawConsent(confirmDialog.type);
      }
    } catch (error) {
      console.error('Error processing consent:', error);
    } finally {
      setProcessingConsent(null);
      setConfirmDialog({ open: false, type: null, action: 'grant' });
    }
  };

  const getConsentStatusColor = (type: ConsentType) => {
    return hasConsent(type) ? 'success' : 'default';
  };

  const getConsentStatusIcon = (type: ConsentType) => {
    return hasConsent(type) ? <CheckCircleIcon /> : <CancelIcon />;
  };

  const formatConsentDate = (consent: any) => {
    if (!consent) return 'Never';
    return format(new Date(consent.grantedAt || consent.withdrawnAt), 'PPp');
  };

  if (loading && consents.length === 0) {
    return (
      <Box sx={{ p: 3 }}>
        <LinearProgress />
        <Typography variant="body2" sx={{ mt: 2, textAlign: 'center' }}>
          Loading consent information...
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Stack direction="row" justifyContent="space-between" alignItems="center" sx={{ mb: 3 }}>
        <Typography variant="h6" component="h2">
          Consent Management
        </Typography>

        <Stack direction="row" spacing={1} alignItems="center">
          <Chip
            label={`${stats.granted}/${stats.total} Granted`}
            color={stats.granted === stats.total ? 'success' : 'warning'}
            size="small"
          />

          <Tooltip title="Refresh consent data">
            <IconButton onClick={refreshConsents} disabled={loading} size="small">
              <RefreshIcon />
            </IconButton>
          </Tooltip>
        </Stack>
      </Stack>

      {/* Error Alert */}
      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {/* Overview Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="primary" gutterBottom>
                {stats.granted}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Active Consents
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="warning.main" gutterBottom>
                {stats.withdrawn}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Withdrawn Consents
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={4}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="info.main" gutterBottom>
                {Math.round((stats.granted / stats.total) * 100)}%
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Compliance Rate
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Consent Types List */}
      <Card>
        <CardContent>
          <Typography variant="h6" gutterBottom>
            Consent Preferences
          </Typography>

          <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
            Manage your consent for different types of data processing. You can withdraw consent at any time.
          </Typography>

          <List>
            {Object.entries(CONSENT_TYPES).map(([type, config], index) => {
              const consentType = type as ConsentType;
              const consent = getConsentByType(consentType);
              const isGranted = hasConsent(consentType);
              const isProcessing = processingConsent === consentType;

              return (
                <React.Fragment key={consentType}>
                  <ListItem>
                    <ListItemText
                      primary={
                        <Stack direction="row" spacing={1} alignItems="center">
                          <Typography variant="subtitle1">
                            {config.label}
                          </Typography>
                          <Chip
                            icon={getConsentStatusIcon(consentType)}
                            label={isGranted ? 'Granted' : 'Withdrawn'}
                            color={getConsentStatusColor(consentType)}
                            size="small"
                          />
                        </Stack>
                      }
                      secondary={
                        <Box sx={{ mt: 1 }}>
                          <Typography variant="body2" color="text.secondary">
                            {config.description}
                          </Typography>
                          {consent && (
                            <Typography variant="caption" color="text.secondary" sx={{ mt: 0.5, display: 'block' }}>
                              Last updated: {formatConsentDate(consent)}
                            </Typography>
                          )}
                        </Box>
                      }
                    />

                    <ListItemSecondaryAction>
                      <FormControlLabel
                        control={
                          <Switch
                            checked={isGranted}
                            onChange={() => handleConsentToggle(consentType, isGranted)}
                            disabled={isProcessing || loading}
                            color="primary"
                          />
                        }
                        label=""
                      />
                    </ListItemSecondaryAction>
                  </ListItem>

                  {index < Object.keys(CONSENT_TYPES).length - 1 && <Divider />}
                </React.Fragment>
              );
            })}
          </List>
        </CardContent>
      </Card>

      {/* Information Alert */}
      <Alert severity="info" sx={{ mt: 3 }}>
        <Stack direction="row" spacing={1} alignItems="flex-start">
          <InfoIcon fontSize="small" />
          <Box>
            <Typography variant="subtitle2" gutterBottom>
              Your Rights Under GDPR
            </Typography>
            <Typography variant="body2">
              You have the right to withdraw consent at any time. Withdrawing consent will not affect 
              the lawfulness of processing based on consent before its withdrawal. Some features may 
              become unavailable if you withdraw certain consents.
            </Typography>
          </Box>
        </Stack>
      </Alert>

      {/* Confirmation Dialog */}
      <Dialog
        open={confirmDialog.open}
        onClose={() => setConfirmDialog({ open: false, type: null, action: 'grant' })}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          {confirmDialog.action === 'grant' ? 'Grant Consent' : 'Withdraw Consent'}
        </DialogTitle>

        <DialogContent>
          {confirmDialog.type && (
            <Box>
              <Typography variant="body1" gutterBottom>
                Are you sure you want to {confirmDialog.action} consent for:
              </Typography>

              <Typography variant="subtitle1" color="primary" gutterBottom>
                {CONSENT_TYPES[confirmDialog.type]?.label}
              </Typography>

              <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                {CONSENT_TYPES[confirmDialog.type]?.description}
              </Typography>

              {confirmDialog.action === 'withdraw' && (
                <Alert severity="warning">
                  Withdrawing this consent may limit certain features or functionality.
                </Alert>
              )}
            </Box>
          )}
        </DialogContent>

        <DialogActions>
          <Button 
            onClick={() => setConfirmDialog({ open: false, type: null, action: 'grant' })}
            disabled={processingConsent !== null}
          >
            Cancel
          </Button>

          <Button 
            onClick={handleConfirmAction}
            variant="contained"
            disabled={processingConsent !== null}
            color={confirmDialog.action === 'withdraw' ? 'warning' : 'primary'}
          >
            {processingConsent ? 'Processing...' : `${confirmDialog.action === 'grant' ? 'Grant' : 'Withdraw'} Consent`}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default ConsentManagementTab;

/**
 * Data Export Tab Component
 * Handles GDPR data export requests and downloads
 */

import React, { useState } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Alert,
  Chip,
  Stack,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  LinearProgress,
  Tooltip,
  IconButton,
  FormControl,
  FormLabel,
  RadioGroup,
  FormControlLabel,
  Radio,
  Checkbox
} from '@mui/material';
import {
  Download as DownloadIcon,
  Add as AddIcon,
  Refresh as RefreshIcon,
  Cancel as CancelIcon,
  CheckCircle as CheckCircleIcon,
  Error as ErrorIcon,
  Schedule as ScheduleIcon,
  Info as InfoIcon
} from '@mui/icons-material';
import { UseDataExportReturn, DataExportFormData, DataExportRequest } from '../../types/gdpr';
import { format, formatDistanceToNow } from 'date-fns';

interface DataExportTabProps {
  hook: UseDataExportReturn;
}

export const DataExportTab: React.FC<DataExportTabProps> = ({ hook }) => {
  const {
    exportRequests,
    loading,
    error,
    requestExport,
    downloadExport,
    cancelExport,
    refreshRequests,
    getExportStats,
    getLatestExport,
    canRequestNewExport
  } = hook;

  const [newExportDialog, setNewExportDialog] = useState(false);
  const [exportForm, setExportForm] = useState<DataExportFormData>({
    format: 'json',
    includeAuditTrail: true,
    includeConsents: true
  });
  const [processingRequest, setProcessingRequest] = useState<string | null>(null);

  const stats = getExportStats();
  const latestExport = getLatestExport();

  const handleRequestExport = async () => {
    try {
      setProcessingRequest('new');
      await requestExport(exportForm);
      setNewExportDialog(false);
      // Reset form
      setExportForm({
        format: 'json',
        includeAuditTrail: true,
        includeConsents: true
      });
    } catch (error) {
      console.error('Error requesting export:', error);
    } finally {
      setProcessingRequest(null);
    }
  };

  const handleDownload = async (requestId: string) => {
    try {
      setProcessingRequest(requestId);
      await downloadExport(requestId);
    } catch (error) {
      console.error('Error downloading export:', error);
    } finally {
      setProcessingRequest(null);
    }
  };

  const handleCancel = async (requestId: string) => {
    try {
      setProcessingRequest(requestId);
      await cancelExport(requestId);
    } catch (error) {
      console.error('Error cancelling export:', error);
    } finally {
      setProcessingRequest(null);
    }
  };

  const getStatusIcon = (status: DataExportRequest['status']) => {
    switch (status) {
      case 'completed':
        return <CheckCircleIcon color="success" />;
      case 'failed':
        return <ErrorIcon color="error" />;
      case 'processing':
        return <ScheduleIcon color="info" />;
      case 'pending':
        return <ScheduleIcon color="warning" />;
      default:
        return <InfoIcon />;
    }
  };

  const getStatusColor = (status: DataExportRequest['status']) => {
    switch (status) {
      case 'completed':
        return 'success';
      case 'failed':
        return 'error';
      case 'processing':
        return 'info';
      case 'pending':
        return 'warning';
      default:
        return 'default';
    }
  };

  const isExpired = (request: DataExportRequest) => {
    return request.expiryDate && new Date() > new Date(request.expiryDate);
  };

  const canDownload = (request: DataExportRequest) => {
    return request.status === 'completed' && request.downloadUrl && !isExpired(request);
  };

  const canCancel = (request: DataExportRequest) => {
    return request.status === 'pending' || request.status === 'processing';
  };

  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Stack direction="row" justifyContent="space-between" alignItems="center" sx={{ mb: 3 }}>
        <Typography variant="h6" component="h2">
          Data Export
        </Typography>

        <Stack direction="row" spacing={1} alignItems="center">
          <Chip
            label={`${stats.available} Available`}
            color={stats.available > 0 ? 'success' : 'default'}
            size="small"
          />

          <Tooltip title="Refresh export requests">
            <IconButton onClick={refreshRequests} disabled={loading} size="small">
              <RefreshIcon />
            </IconButton>
          </Tooltip>

          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={() => setNewExportDialog(true)}
            disabled={!canRequestNewExport() || loading}
          >
            New Export
          </Button>
        </Stack>
      </Stack>

      {/* Error Alert */}
      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {/* Cannot Request Alert */}
      {!canRequestNewExport() && (
        <Alert severity="warning" sx={{ mb: 3 }}>
          You have reached the maximum number of concurrent export requests. 
          Please wait for existing requests to complete or cancel them.
        </Alert>
      )}

      {/* Stats Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="primary" gutterBottom>
                {stats.total}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Total Requests
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="success.main" gutterBottom>
                {stats.completed}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Completed
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="info.main" gutterBottom>
                {stats.processing + stats.pending}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                In Progress
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="warning.main" gutterBottom>
                {stats.expired}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Expired
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Latest Export Quick Access */}
      {latestExport && canDownload(latestExport) && (
        <Card sx={{ mb: 3, bgcolor: 'success.light' }}>
          <CardContent>
            <Stack direction="row" justifyContent="space-between" alignItems="center">
              <Box>
                <Typography variant="subtitle1" gutterBottom>
                  Latest Export Ready
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Requested {formatDistanceToNow(new Date(latestExport.requestDate))} ago  
                  Format: {latestExport.format.toUpperCase()}  
                  Expires {formatDistanceToNow(new Date(latestExport.expiryDate!))}
                </Typography>
              </Box>

              <Button
                variant="contained"
                startIcon={<DownloadIcon />}
                onClick={() => handleDownload(latestExport.id)}
                disabled={processingRequest === latestExport.id}
              >
                Download
              </Button>
            </Stack>
          </CardContent>
        </Card>
      )}

      {/* Export Requests Table */}
      <Card>
        <CardContent>
          <Typography variant="h6" gutterBottom>
            Export History
          </Typography>

          {loading && exportRequests.length === 0 ? (
            <Box sx={{ py: 3 }}>
              <LinearProgress />
              <Typography variant="body2" sx={{ mt: 2, textAlign: 'center' }}>
                Loading export requests...
              </Typography>
            </Box>
          ) : exportRequests.length === 0 ? (
            <Box sx={{ py: 6, textAlign: 'center' }}>
              <Typography variant="body1" color="text.secondary" gutterBottom>
                No export requests found
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Create your first data export to download your personal information.
              </Typography>
            </Box>
          ) : (
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Status</TableCell>
                    <TableCell>Format</TableCell>
                    <TableCell>Requested</TableCell>
                    <TableCell>Expires</TableCell>
                    <TableCell>Size</TableCell>
                    <TableCell align="right">Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {exportRequests.map((request) => (
                    <TableRow key={request.id}>
                      <TableCell>
                        <Stack direction="row" spacing={1} alignItems="center">
                          {getStatusIcon(request.status)}
                          <Chip
                            label={request.status}
                            color={getStatusColor(request.status)}
                            size="small"
                            variant="outlined"
                          />
                          {isExpired(request) && (
                            <Chip
                              label="Expired"
                              color="error"
                              size="small"
                              variant="outlined"
                            />
                          )}
                        </Stack>
                      </TableCell>

                      <TableCell>
                        <Typography variant="body2" sx={{ textTransform: 'uppercase' }}>
                          {request.format}
                        </Typography>
                      </TableCell>

                      <TableCell>
                        <Typography variant="body2">
                          {format(new Date(request.requestDate), 'PPp')}
                        </Typography>
                      </TableCell>

                      <TableCell>
                        <Typography variant="body2">
                          {request.expiryDate 
                            ? format(new Date(request.expiryDate), 'PPp')
                            : 'N/A'
                          }
                        </Typography>
                      </TableCell>

                      <TableCell>
                        <Typography variant="body2">
                          {request.fileSize ? `${(request.fileSize / 1024 / 1024).toFixed(2)} MB` : 'N/A'}
                        </Typography>
                      </TableCell>

                      <TableCell align="right">
                        <Stack direction="row" spacing={1} justifyContent="flex-end">
                          {canDownload(request) && (
                            <Tooltip title="Download export">
                              <IconButton
                                size="small"
                                onClick={() => handleDownload(request.id)}
                                disabled={processingRequest === request.id}
                                color="primary"
                              >
                                <DownloadIcon />
                              </IconButton>
                            </Tooltip>
                          )}

                          {canCancel(request) && (
                            <Tooltip title="Cancel export">
                              <IconButton
                                size="small"
                                onClick={() => handleCancel(request.id)}
                                disabled={processingRequest === request.id}
                                color="error"
                              >
                                <CancelIcon />
                              </IconButton>
                            </Tooltip>
                          )}
                        </Stack>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          )}
        </CardContent>
      </Card>

      {/* Information */}
      <Alert severity="info" sx={{ mt: 3 }}>
        <Typography variant="subtitle2" gutterBottom>
          Data Export Information
        </Typography>
        <Typography variant="body2">
           Export files are available for download for 7 days after completion<br/>
           You can have up to 3 concurrent export requests<br/>
           Exports include your personal data, consent history, and audit trail (if selected)<br/>
           Large exports may take several minutes to process
        </Typography>
      </Alert>

      {/* New Export Dialog */}
      <Dialog
        open={newExportDialog}
        onClose={() => setNewExportDialog(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          Request Data Export
        </DialogTitle>

        <DialogContent>
          <Box sx={{ mt: 2 }}>
            <Typography variant="body1" gutterBottom>
              Request an export of your personal data. You will receive a downloadable file 
              containing all your information stored in our system.
            </Typography>

            <Grid container spacing={3} sx={{ mt: 2 }}>
              <Grid item xs={12} md={6}>
                <FormControl component="fieldset">
                  <FormLabel component="legend">Export Format</FormLabel>
                  <RadioGroup
                    value={exportForm.format}
                    onChange={(e) => setExportForm(prev => ({ ...prev, format: e.target.value as any }))}
                  >
                    <FormControlLabel value="json" control={<Radio />} label="JSON (Structured data)" />
                    <FormControlLabel value="csv" control={<Radio />} label="CSV (Spreadsheet format)" />
                    <FormControlLabel value="pdf" control={<Radio />} label="PDF (Human readable)" />
                  </RadioGroup>
                </FormControl>
              </Grid>

              <Grid item xs={12} md={6}>
                <FormControl component="fieldset">
                  <FormLabel component="legend">Include Additional Data</FormLabel>
                  <Box sx={{ mt: 1 }}>
                    <FormControlLabel
                      control={
                        <Checkbox
                          checked={exportForm.includeAuditTrail}
                          onChange={(e) => setExportForm(prev => ({ 
                            ...prev, 
                            includeAuditTrail: e.target.checked 
                          }))}
                        />
                      }
                      label="Audit Trail (Activity logs)"
                    />

                    <FormControlLabel
                      control={
                        <Checkbox
                          checked={exportForm.includeConsents}
                          onChange={(e) => setExportForm(prev => ({ 
                            ...prev, 
                            includeConsents: e.target.checked 
                          }))}
                        />
                      }
                      label="Consent History"
                    />
                  </Box>
                </FormControl>
              </Grid>
            </Grid>

            <Alert severity="info" sx={{ mt: 3 }}>
              <Typography variant="body2">
                Processing time depends on the amount of data and selected format. 
                You will be notified when your export is ready for download.
              </Typography>
            </Alert>
          </Box>
        </DialogContent>

        <DialogActions>
          <Button 
            onClick={() => setNewExportDialog(false)}
            disabled={processingRequest === 'new'}
          >
            Cancel
          </Button>

          <Button 
            onClick={handleRequestExport}
            variant="contained"
            disabled={processingRequest === 'new'}
            startIcon={processingRequest === 'new' ? <LinearProgress /> : <DownloadIcon />}
          >
            {processingRequest === 'new' ? 'Requesting...' : 'Request Export'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default DataExportTab;

/**
 * Deletion Request Tab Component
 * Handles "Right to be Forgotten" requests
 */

import React, { useState } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Alert,
  Chip,
  Stack,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  LinearProgress,
  Tooltip,
  IconButton,
  TextField,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  Accordion,
  AccordionSummary,
  AccordionDetails
} from '@mui/material';
import {
  Delete as DeleteIcon,
  Add as AddIcon,
  Refresh as RefreshIcon,
  Cancel as CancelIcon,
  CheckCircle as CheckCircleIcon,
  Error as ErrorIcon,
  Schedule as ScheduleIcon,
  Warning as WarningIcon,
  Info as InfoIcon,
  ExpandMore as ExpandMoreIcon
} from '@mui/icons-material';
import { UseDeletionRequestReturn, DeletionRequestFormData, DeletionRequest } from '../../types/gdpr';
import { format, formatDistanceToNow } from 'date-fns';

interface DeletionRequestTabProps {
  hook: UseDeletionRequestReturn;
}

export const DeletionRequestTab: React.FC<DeletionRequestTabProps> = ({ hook }) => {
  const {
    deletionRequests,
    loading,
    error,
    submitDeletionRequest,
    cancelDeletionRequest,
    refreshRequests,
    getDeletionStats,
    getLatestRequest,
    canSubmitNewRequest,
    getStatusColor,
    getStatusDescription,
    formatRequestForDisplay,
    validateFormData
  } = hook;

  const [newRequestDialog, setNewRequestDialog] = useState(false);
  const [requestForm, setRequestForm] = useState<DeletionRequestFormData>({
    reason: '',
    confirmEmail: '',
    additionalInfo: ''
  });
  const [formErrors, setFormErrors] = useState<Record<string, string>>({});
  const [processingRequest, setProcessingRequest] = useState<string | null>(null);
  const [activeStep, setActiveStep] = useState(0);

  const stats = getDeletionStats();
  const latestRequest = getLatestRequest();

  const handleFormChange = (field: keyof DeletionRequestFormData, value: string) => {
    setRequestForm(prev => ({ ...prev, [field]: value }));

    // Clear error for this field
    if (formErrors[field]) {
      setFormErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleSubmitRequest = async () => {
    const validation = validateFormData(requestForm);

    if (!validation.isValid) {
      setFormErrors(validation.errors);
      return;
    }

    try {
      setProcessingRequest('new');
      await submitDeletionRequest(requestForm);
      setNewRequestDialog(false);
      setActiveStep(0);
      // Reset form
      setRequestForm({
        reason: '',
        confirmEmail: '',
        additionalInfo: ''
      });
      setFormErrors({});
    } catch (error) {
      console.error('Error submitting deletion request:', error);
    } finally {
      setProcessingRequest(null);
    }
  };

  const handleCancelRequest = async (requestId: string) => {
    try {
      setProcessingRequest(requestId);
      await cancelDeletionRequest(requestId);
    } catch (error) {
      console.error('Error cancelling deletion request:', error);
    } finally {
      setProcessingRequest(null);
    }
  };

  const getStatusIcon = (status: DeletionRequest['status']) => {
    switch (status) {
      case 'completed':
        return <CheckCircleIcon color="success" />;
      case 'approved':
        return <CheckCircleIcon color="info" />;
      case 'rejected':
        return <ErrorIcon color="error" />;
      case 'cancelled':
        return <CancelIcon color="disabled" />;
      case 'pending':
        return <ScheduleIcon color="warning" />;
      default:
        return <InfoIcon />;
    }
  };

  const canCancel = (request: DeletionRequest) => {
    return request.status === 'pending';
  };

  const steps = [
    {
      label: 'Provide Reason',
      description: 'Explain why you want to delete your data'
    },
    {
      label: 'Confirm Identity',
      description: 'Verify your email address'
    },
    {
      label: 'Review & Submit',
      description: 'Review your request before submission'
    }
  ];

  const handleNext = () => {
    if (activeStep === 0) {
      if (!requestForm.reason || requestForm.reason.trim().length < 10) {
        setFormErrors({ reason: 'Please provide a detailed reason (minimum 10 characters)' });
        return;
      }
    } else if (activeStep === 1) {
      if (!requestForm.confirmEmail) {
        setFormErrors({ confirmEmail: 'Email confirmation is required' });
        return;
      }
    }

    setFormErrors({});
    setActiveStep(prev => prev + 1);
  };

  const handleBack = () => {
    setActiveStep(prev => prev - 1);
  };

  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Stack direction="row" justifyContent="space-between" alignItems="center" sx={{ mb: 3 }}>
        <Typography variant="h6" component="h2">
          Data Deletion Requests
        </Typography>

        <Stack direction="row" spacing={1} alignItems="center">
          <Chip
            label={`${stats.active} Active`}
            color={stats.active > 0 ? 'warning' : 'default'}
            size="small"
          />

          <Tooltip title="Refresh deletion requests">
            <IconButton onClick={refreshRequests} disabled={loading} size="small">
              <RefreshIcon />
            </IconButton>
          </Tooltip>

          <Button
            variant="contained"
            startIcon={<DeleteIcon />}
            onClick={() => setNewRequestDialog(true)}
            disabled={!canSubmitNewRequest() || loading}
            color="error"
          >
            Request Deletion
          </Button>
        </Stack>
      </Stack>

      {/* Error Alert */}
      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {/* Cannot Submit Alert */}
      {!canSubmitNewRequest() && (
        <Alert severity="warning" sx={{ mb: 3 }}>
          You already have an active deletion request. Only one deletion request can be active at a time.
        </Alert>
      )}

      {/* Warning Alert */}
      <Alert severity="warning" sx={{ mb: 3 }}>
        <Typography variant="subtitle2" gutterBottom>
          Important: Data Deletion is Permanent
        </Typography>
        <Typography variant="body2">
          Once your data deletion request is approved and processed, all your personal information 
          will be permanently removed from our systems. This action cannot be undone.
        </Typography>
      </Alert>

      {/* Stats Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="primary" gutterBottom>
                {stats.total}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Total Requests
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="warning.main" gutterBottom>
                {stats.pending}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Pending Review
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="success.main" gutterBottom>
                {stats.completed}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Completed
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography variant="h6" color="error.main" gutterBottom>
                {stats.rejected}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Rejected
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Latest Request Status */}
      {latestRequest && (
        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Latest Request Status
            </Typography>

            <Stack direction="row" spacing={2} alignItems="center" sx={{ mb: 2 }}>
              {getStatusIcon(latestRequest.status)}
              <Chip
                label={latestRequest.status}
                color={getStatusColor(latestRequest.status)}
                variant="outlined"
              />
              <Typography variant="body2" color="text.secondary">
                Submitted {formatDistanceToNow(new Date(latestRequest.requestDate))} ago
              </Typography>
            </Stack>

            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
              {getStatusDescription(latestRequest.status)}
            </Typography>

            {latestRequest.adminNotes && (
              <Alert severity="info">
                <Typography variant="subtitle2" gutterBottom>
                  Admin Notes:
                </Typography>
                <Typography variant="body2">
                  {latestRequest.adminNotes}
                </Typography>
              </Alert>
            )}

            {canCancel(latestRequest) && (
              <Box sx={{ mt: 2 }}>
                <Button
                  variant="outlined"
                  color="error"
                  startIcon={<CancelIcon />}
                  onClick={() => handleCancelRequest(latestRequest.id)}
                  disabled={processingRequest === latestRequest.id}
                >
                  Cancel Request
                </Button>
              </Box>
            )}
          </CardContent>
        </Card>
      )}

      {/* Deletion Requests History */}
      <Card>
        <CardContent>
          <Typography variant="h6" gutterBottom>
            Request History
          </Typography>

          {loading && deletionRequests.length === 0 ? (
            <Box sx={{ py: 3 }}>
              <LinearProgress />
              <Typography variant="body2" sx={{ mt: 2, textAlign: 'center' }}>
                Loading deletion requests...
              </Typography>
            </Box>
          ) : deletionRequests.length === 0 ? (
            <Box sx={{ py: 6, textAlign: 'center' }}>
              <Typography variant="body1" color="text.secondary" gutterBottom>
                No deletion requests found
              </Typography>
              <Typography variant="body2" color="text.secondary">
                You have not submitted any data deletion requests yet.
              </Typography>
            </Box>
          ) : (
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Status</TableCell>
                    <TableCell>Reason</TableCell>
                    <TableCell>Submitted</TableCell>
                    <TableCell>Processed</TableCell>
                    <TableCell align="right">Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {deletionRequests.map((request) => {
                    const formattedRequest = formatRequestForDisplay(request);

                    return (
                      <TableRow key={request.id}>
                        <TableCell>
                          <Stack direction="row" spacing={1} alignItems="center">
                            {getStatusIcon(request.status)}
                            <Chip
                              label={request.status}
                              color={getStatusColor(request.status)}
                              size="small"
                              variant="outlined"
                            />
                          </Stack>
                        </TableCell>

                        <TableCell>
                          <Typography variant="body2" sx={{ maxWidth: 200 }}>
                            {request.reason.length > 50 
                              ? `${request.reason.substring(0, 50)}...`
                              : request.reason
                            }
                          </Typography>
                        </TableCell>

                        <TableCell>
                          <Typography variant="body2">
                            {formattedRequest.formattedRequestDate}
                          </Typography>
                          <Typography variant="caption" color="text.secondary">
                            {formattedRequest.daysSinceRequest} days ago
                          </Typography>
                        </TableCell>

                        <TableCell>
                          <Typography variant="body2">
                            {formattedRequest.formattedProcessedDate || 'Not processed'}
                          </Typography>
                        </TableCell>

                        <TableCell align="right">
                          {canCancel(request) && (
                            <Tooltip title="Cancel request">
                              <IconButton
                                size="small"
                                onClick={() => handleCancelRequest(request.id)}
                                disabled={processingRequest === request.id}
                                color="error"
                              >
                                <CancelIcon />
                              </IconButton>
                            </Tooltip>
                          )}
                        </TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </TableContainer>
          )}
        </CardContent>
      </Card>

      {/* Information Accordion */}
      <Accordion sx={{ mt: 3 }}>
        <AccordionSummary expandIcon={<ExpandMoreIcon />}>
          <Typography variant="subtitle1">
            Right to be Forgotten - Information
          </Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography variant="body2" paragraph>
            Under GDPR Article 17, you have the right to request the deletion of your personal data 
            in certain circumstances:
          </Typography>

          <ul>
            <li>The personal data is no longer necessary for the original purpose</li>
            <li>You withdraw consent and there's no other legal basis for processing</li>
            <li>Your personal data has been unlawfully processed</li>
            <li>Deletion is required for compliance with a legal obligation</li>
          </ul>

          <Typography variant="body2" paragraph>
            Please note that we may not be able to delete your data if:
          </Typography>

          <ul>
            <li>We need to keep it for legal compliance</li>
            <li>It's necessary for the establishment, exercise, or defense of legal claims</li>
            <li>There are legitimate interests that override your right to deletion</li>
          </ul>
        </AccordionDetails>
      </Accordion>

      {/* New Deletion Request Dialog */}
      <Dialog
        open={newRequestDialog}
        onClose={() => setNewRequestDialog(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          <Stack direction="row" spacing={1} alignItems="center">
            <WarningIcon color="error" />
            Request Data Deletion
          </Stack>
        </DialogTitle>

        <DialogContent>
          <Alert severity="error" sx={{ mb: 3 }}>
            <Typography variant="subtitle2" gutterBottom>
              Warning: This action is irreversible
            </Typography>
            <Typography variant="body2">
              Once approved, all your personal data will be permanently deleted from our systems.
            </Typography>
          </Alert>

          <Stepper activeStep={activeStep} orientation="vertical">
            {steps.map((step, index) => (
              <Step key={step.label}>
                <StepLabel>
                  {step.label}
                </StepLabel>
                <StepContent>
                  <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                    {step.description}
                  </Typography>

                  {index === 0 && (
                    <TextField
                      fullWidth
                      multiline
                      rows={4}
                      label="Reason for deletion *"
                      value={requestForm.reason}
                      onChange={(e) => handleFormChange('reason', e.target.value)}
                      error={!!formErrors.reason}
                      helperText={formErrors.reason || 'Please provide a detailed explanation (minimum 10 characters)'}
                      placeholder="Please explain why you want to delete your data..."
                    />
                  )}

                  {index === 1 && (
                    <Box>
                      <TextField
                        fullWidth
                        label="Confirm your email address *"
                        type="email"
                        value={requestForm.confirmEmail}
                        onChange={(e) => handleFormChange('confirmEmail', e.target.value)}
                        error={!!formErrors.confirmEmail}
                        helperText={formErrors.confirmEmail || 'Enter your account email to confirm your identity'}
                        sx={{ mb: 2 }}
                      />

                      <TextField
                        fullWidth
                        multiline
                        rows={3}
                        label="Additional information (optional)"
                        value={requestForm.additionalInfo}
                        onChange={(e) => handleFormChange('additionalInfo', e.target.value)}
                        helperText="Any additional context or specific requests"
                        placeholder="Optional: Any additional information..."
                      />
                    </Box>
                  )}

                  {index === 2 && (
                    <Box>
                      <Typography variant="subtitle2" gutterBottom>
                        Review Your Request:
                      </Typography>

                      <Paper sx={{ p: 2, bgcolor: 'grey.50' }}>
                        <Typography variant="body2" gutterBottom>
                          <strong>Reason:</strong> {requestForm.reason}
                        </Typography>
                        <Typography variant="body2" gutterBottom>
                          <strong>Email:</strong> {requestForm.confirmEmail}
                        </Typography>
                        {requestForm.additionalInfo && (
                          <Typography variant="body2">
                            <strong>Additional Info:</strong> {requestForm.additionalInfo}
                          </Typography>
                        )}
                      </Paper>
                    </Box>
                  )}

                  <Box sx={{ mt: 2 }}>
                    <Button
                      variant="contained"
                      onClick={index === steps.length - 1 ? handleSubmitRequest : handleNext}
                      disabled={processingRequest === 'new'}
                    >
                      {index === steps.length - 1 ? 'Submit Request' : 'Next'}
                    </Button>

                    {index > 0 && (
                      <Button
                        onClick={handleBack}
                        sx={{ ml: 1 }}
                        disabled={processingRequest === 'new'}
                      >
                        Back
                      </Button>
                    )}
                  </Box>
                </StepContent>
              </Step>
            ))}
          </Stepper>
        </DialogContent>

        <DialogActions>
          <Button 
            onClick={() => {
              setNewRequestDialog(false);
              setActiveStep(0);
              setFormErrors({});
            }}
            disabled={processingRequest === 'new'}
          >
            Cancel
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default DeletionRequestTab;

/**
 * Privacy Settings Tab Component
 * Handles privacy preferences and GDPR settings
 */

import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  Switch,
  FormControlLabel,
  Alert,
  Chip,
  Stack,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Divider,
  LinearProgress,
  Tooltip,
  IconButton,
  CircularProgress,
  Paper
} from '@mui/material';
import {
  Settings as SettingsIcon,
  Security as SecurityIcon,
  Info as InfoIcon,
  Save as SaveIcon,
  Refresh as RefreshIcon,
  Download as DownloadIcon,
  RestoreFromTrash as RestoreIcon,
  Warning as WarningIcon
} from '@mui/icons-material';
import { UsePrivacySettingsReturn, PrivacySettingsFormData } from '../../types/gdpr';

interface PrivacySettingsTabProps {
  hook: UsePrivacySettingsReturn;
}

export const PrivacySettingsTab: React.FC<PrivacySettingsTabProps> = ({ hook }) => {
  const {
    settings,
    loading,
    error,
    hasUnsavedChanges,
    updatePrivacySettings,
    updateSingleSetting,
    resetToDefaults,
    getComplianceScore,
    getComplianceRecommendations,
    checkForChanges,
    getSettingDescription,
    getSettingImpact,
    exportSettings,
    refreshSettings
  } = hook;

  const [localSettings, setLocalSettings] = useState<PrivacySettingsFormData | null>(null);
  const [resetDialog, setResetDialog] = useState(false);
  const [saving, setSaving] = useState(false);

  // Initialize local settings when settings are loaded
  useEffect(() => {
    if (settings && !localSettings) {
      setLocalSettings({
        dataProcessingConsent: settings.dataProcessingConsent,
        marketingConsent: settings.marketingConsent,
        analyticsConsent: settings.analyticsConsent,
        profileVisibility: settings.profileVisibility,
        dataRetentionOptOut: settings.dataRetentionOptOut,
        thirdPartySharing: settings.thirdPartySharing
      });
    }
  }, [settings, localSettings]);

  // Check for changes when local settings change
  useEffect(() => {
    if (localSettings) {
      checkForChanges(localSettings);
    }
  }, [localSettings, checkForChanges]);

  const handleSettingChange = (key: keyof PrivacySettingsFormData, value: boolean) => {
    setLocalSettings(prev => prev ? { ...prev, [key]: value } : null);
  };

  const handleSave = async () => {
    if (!localSettings) return;

    try {
      setSaving(true);
      await updatePrivacySettings(localSettings);
    } catch (error) {
      console.error('Error saving privacy settings:', error);
    } finally {
      setSaving(false);
    }
  };

  const handleReset = async () => {
    try {
      await resetToDefaults();
      setResetDialog(false);
      // Settings will be updated via the hook
    } catch (error) {
      console.error('Error resetting privacy settings:', error);
    }
  };

  const handleQuickToggle = async (key: keyof PrivacySettingsFormData, value: boolean) => {
    try {
      await updateSingleSetting(key, value);
    } catch (error) {
      console.error('Error updating setting:', error);
    }
  };

  const complianceScore = getComplianceScore();
  const recommendations = getComplianceRecommendations();

  const getScoreColor = (score: number) => {
    if (score >= 90) return 'success';
    if (score >= 70) return 'warning';
    return 'error';
  };

  const getImpactColor = (impact: string) => {
    switch (impact) {
      case 'high': return 'error';
      case 'medium': return 'warning';
      case 'low': return 'success';
      default: return 'default';
    }
  };

  if (loading && !settings) {
    return (
      <Box sx={{ p: 3 }}>
        <LinearProgress />
        <Typography variant="body2" sx={{ mt: 2, textAlign: 'center' }}>
          Loading privacy settings...
        </Typography>
      </Box>
    );
  }

  if (!settings || !localSettings) {
    return (
      <Box sx={{ p: 3, textAlign: 'center' }}>
        <Typography variant="body1" color="text.secondary">
          Unable to load privacy settings. Please try refreshing the page.
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Stack direction="row" justifyContent="space-between" alignItems="center" sx={{ mb: 3 }}>
        <Typography variant="h6" component="h2">
          Privacy Settings
        </Typography>

        <Stack direction="row" spacing={1} alignItems="center">
          <Chip
            icon={<SecurityIcon />}
            label={`Compliance: ${complianceScore}%`}
            color={getScoreColor(complianceScore)}
            size="small"
          />

          <Tooltip title="Export settings">
            <IconButton onClick={exportSettings} size="small">
              <DownloadIcon />
            </IconButton>
          </Tooltip>

          <Tooltip title="Refresh settings">
            <IconButton onClick={refreshSettings} disabled={loading} size="small">
              <RefreshIcon />
            </IconButton>
          </Tooltip>
        </Stack>
      </Stack>

      {/* Error Alert */}
      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {/* Unsaved Changes Alert */}
      {hasUnsavedChanges && (
        <Alert severity="warning" sx={{ mb: 3 }}>
          <Stack direction="row" justifyContent="space-between" alignItems="center">
            <Typography variant="body2">
              You have unsaved changes to your privacy settings.
            </Typography>
            <Button
              variant="contained"
              size="small"
              startIcon={saving ? <CircularProgress size={16} /> : <SaveIcon />}
              onClick={handleSave}
              disabled={saving}
            >
              {saving ? 'Saving...' : 'Save Changes'}
            </Button>
          </Stack>
        </Alert>
      )}

      <Grid container spacing={3}>
        {/* Settings Panel */}
        <Grid item xs={12} lg={8}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Privacy Preferences
              </Typography>

              <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                Configure your privacy settings to control how your data is processed and shared.
              </Typography>

              <List>
                {Object.entries(localSettings).map(([key, value], index) => {
                  const settingKey = key as keyof PrivacySettingsFormData;
                  const description = getSettingDescription(settingKey);
                  const impact = getSettingImpact(settingKey);
                  const isBoolean = typeof value === 'boolean';

                  if (!isBoolean) return null;

                  return (
                    <React.Fragment key={settingKey}>
                      <ListItem>
                        <ListItemText
                          primary={
                            <Stack direction="row" spacing={1} alignItems="center">
                              <Typography variant="subtitle1">
                                {settingKey.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
                              </Typography>
                              <Chip
                                label={impact}
                                color={getImpactColor(impact)}
                                size="small"
                                variant="outlined"
                              />
                            </Stack>
                          }
                          secondary={
                            <Typography variant="body2" color="text.secondary" sx={{ mt: 0.5 }}>
                              {description}
                            </Typography>
                          }
                        />

                        <ListItemSecondaryAction>
                          <Stack direction="row" spacing={1} alignItems="center">
                            {/* Quick toggle for immediate save */}
                            <Tooltip title="Apply immediately">
                              <IconButton
                                size="small"
                                onClick={() => handleQuickToggle(settingKey, !value)}
                                disabled={loading}
                              >
                                <SaveIcon fontSize="small" />
                              </IconButton>
                            </Tooltip>

                            <FormControlLabel
                              control={
                                <Switch
                                  checked={value}
                                  onChange={(e) => handleSettingChange(settingKey, e.target.checked)}
                                  disabled={loading}
                                  color="primary"
                                />
                              }
                              label=""
                            />
                          </Stack>
                        </ListItemSecondaryAction>
                      </ListItem>

                      {index < Object.keys(localSettings).length - 1 && <Divider />}
                    </React.Fragment>
                  );
                })}
              </List>
            </CardContent>

            <CardActions sx={{ justifyContent: 'space-between', px: 2, pb: 2 }}>
              <Button
                startIcon={<RestoreIcon />}
                onClick={() => setResetDialog(true)}
                disabled={loading}
                color="warning"
              >
                Reset to Defaults
              </Button>

              <Stack direction="row" spacing={1}>
                <Button
                  variant="outlined"
                  onClick={refreshSettings}
                  disabled={loading}
                >
                  Refresh
                </Button>

                <Button
                  variant="contained"
                  startIcon={saving ? <CircularProgress size={16} /> : <SaveIcon />}
                  onClick={handleSave}
                  disabled={!hasUnsavedChanges || saving}
                >
                  {saving ? 'Saving...' : 'Save Changes'}
                </Button>
              </Stack>
            </CardActions>
          </Card>
        </Grid>

        {/* Compliance Panel */}
        <Grid item xs={12} lg={4}>
          <Stack spacing={3}>
            {/* Compliance Score */}
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Compliance Score
                </Typography>

                <Box sx={{ textAlign: 'center', mb: 2 }}>
                  <Typography variant="h3" color={getScoreColor(complianceScore)}>
                    {complianceScore}%
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    GDPR Compliance
                  </Typography>
                </Box>

                <LinearProgress
                  variant="determinate"
                  value={complianceScore}
                  color={getScoreColor(complianceScore)}
                  sx={{ height: 8, borderRadius: 4 }}
                />
              </CardContent>
            </Card>

            {/* Recommendations */}
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Recommendations
                </Typography>

                {recommendations.length > 0 ? (
                  <List dense>
                    {recommendations.map((recommendation, index) => (
                      <ListItem key={index} sx={{ px: 0 }}>
                        <ListItemText
                          primary={
                            <Typography variant="body2">
                              {recommendation}
                            </Typography>
                          }
                        />
                      </ListItem>
                    ))}
                  </List>
                ) : (
                  <Typography variant="body2" color="text.secondary">
                    No recommendations at this time. Your settings look good!
                  </Typography>
                )}
              </CardContent>
            </Card>

            {/* Privacy Info */}
            <Paper sx={{ p: 2, bgcolor: 'info.light', color: 'info.contrastText' }}>
              <Stack direction="row" spacing={1} alignItems="flex-start">
                <InfoIcon fontSize="small" />
                <Box>
                  <Typography variant="subtitle2" gutterBottom>
                    Privacy Notice
                  </Typography>
                  <Typography variant="body2">
                    These settings control how we process your personal data. 
                    Changes take effect immediately and are logged for compliance.
                  </Typography>
                </Box>
              </Stack>
            </Paper>
          </Stack>
        </Grid>
      </Grid>

      {/* Reset Confirmation Dialog */}
      <Dialog
        open={resetDialog}
        onClose={() => setResetDialog(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          <Stack direction="row" spacing={1} alignItems="center">
            <WarningIcon color="warning" />
            Reset Privacy Settings
          </Stack>
        </DialogTitle>

        <DialogContent>
          <Typography variant="body1" gutterBottom>
            Are you sure you want to reset all privacy settings to their default values?
          </Typography>

          <Alert severity="warning" sx={{ mt: 2 }}>
            This action cannot be undone. All your current privacy preferences will be lost.
          </Alert>
        </DialogContent>

        <DialogActions>
          <Button onClick={() => setResetDialog(false)}>
            Cancel
          </Button>

          <Button 
            onClick={handleReset}
            variant="contained"
            color="warning"
            disabled={loading}
          >
            Reset Settings
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default PrivacySettingsTab;

import React, { useState, useEffect, useRef } from 'react';
import { 
  ArrowUpDown,
  CheckSquare,
  Filter,
  LayoutGrid,
  List,
  Plus,
  Search
} from 'lucide-react';

export interface PageHeaderProps {
  /** Main page title */
  title: string;
  /** Subtitle/description text */
  subtitle: string;
  /** View mode: 'table' or 'grid' */
  viewMode: 'table' | 'grid';
  /** Function to toggle between view modes */
  onViewModeChange: (mode: 'table' | 'grid') => void;
  /** Whether the page is in selection mode */
  selectionMode: boolean;
  /** Function to toggle selection mode */
  onToggleSelectionMode: () => void;
  /** Current search value */
  searchValue: string;
  /** Function to handle search value changes */
  onSearchChange: (value: string) => void;
  /** Whether any filters are active */
  filtersActive?: boolean;
  /** Primary action button text (Add, Create, etc.) */
  primaryActionText?: string;
  /** Primary action function */
  onPrimaryAction?: () => void;
  /** Primary action button icon */
  primaryActionIcon?: React.ReactNode;
  /** Primary action dropdown options */
  primaryActionDropdownOptions?: Array<{
    label: string;
    icon?: React.ReactNode;
    onClick: () => void;
  }>;
  /** Content for the selection mode toolbar */
  selectionToolbar?: React.ReactNode;
  /** Additional filter components */
  filterComponents?: React.ReactNode;
  /** Function to handle sorting */
  onSort?: () => void;
  /** Sort options for dropdown menu */
  sortOptions?: Array<{
    label: string;
    onClick: () => void;
  }>;
  /** Filter options for dropdown menu */
  filterOptions?: Array<{
    label: string;
    onClick: () => void;
  }>;
  /** Function to toggle filters */
  onToggleFilters?: () => void;
  description?: string;
  children?: React.ReactNode;
}

/**
 * Standardized page header component with consistent styling
 * Includes title, description, view controls, search, and primary action
 */
const PageHeader: React.FC<PageHeaderProps> = ({
  title,
  subtitle,
  viewMode,
  onViewModeChange,
  selectionMode,
  onToggleSelectionMode,
  searchValue,
  onSearchChange,
  filtersActive = false,
  primaryActionText = 'Aggiungi',
  onPrimaryAction,
  primaryActionIcon = <Plus className="h-5 w-5" />,
  primaryActionDropdownOptions,
  selectionToolbar,
  filterComponents,
  onSort,
  sortOptions,
  filterOptions,
  onToggleFilters,
  description,
  children,
}) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [sortDropdownOpen, setSortDropdownOpen] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const sortDropdownRef = useRef<HTMLDivElement>(null);

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setDropdownOpen(false);
      }
      if (sortDropdownRef.current && !sortDropdownRef.current.contains(event.target as Node)) {
        setSortDropdownOpen(false);
      }
    };

    if (dropdownOpen || sortDropdownOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [dropdownOpen, sortDropdownOpen]);

  return (
    <div className="space-y-4 mb-6">
      {/* Title row with actions */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-2xl font-bold text-gray-800">{title}</h1>
          {description && (
            <p className="text-gray-500">{description}</p>
          )}
        </div>

        <div className="flex items-center gap-3">
          {/* Selection Mode Toggle (Modify) */}
          <button
            onClick={onToggleSelectionMode}
            className={`px-4 h-10 rounded-full border flex items-center gap-2 transition ${
              selectionMode ? 'bg-blue-100 text-blue-700 border-blue-400' : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
            }`}
          >
            <CheckSquare className="h-4 w-4" />
            Modifica
          </button>

          {/* View Mode Toggle */}
          <div className="flex items-center bg-gray-100 rounded-full p-1 relative h-10 w-40">
            <button
              className={`flex-1 h-8 z-10 relative transition-colors duration-300 flex items-center justify-center gap-1.5 ${viewMode === 'grid' ? 'text-blue-700' : 'text-gray-600'}`}
              onClick={() => onViewModeChange('grid')}
            >
              <LayoutGrid className="h-4 w-4" />
              <span className="text-xs font-medium">Card</span>
            </button>
            <button
              className={`flex-1 h-8 z-10 relative transition-colors duration-300 flex items-center justify-center gap-1.5 ${viewMode === 'table' ? 'text-blue-700' : 'text-gray-600'}`}
              onClick={() => onViewModeChange('table')}
            >
              <List className="h-4 w-4" />
              <span className="text-xs font-medium">Table</span>
            </button>
            <span
              className={`absolute top-1 bottom-1 h-8 bg-white rounded-full shadow transition-transform duration-300 ${
                viewMode === 'table' ? 'right-1 left-[50%]' : 'left-1 right-[50%]'
              }`}
              style={{ zIndex: 0 }}
            />
          </div>

          {/* Primary Action Button/Dropdown */}
          {(onPrimaryAction || primaryActionDropdownOptions) && (
            <div className="relative" ref={dropdownRef}>
              <button
                onClick={primaryActionDropdownOptions ? () => setDropdownOpen(!dropdownOpen) : onPrimaryAction}
                className="px-4 h-10 rounded-full bg-blue-600 text-white flex items-center gap-2 shadow-sm hover:bg-blue-700 focus:outline-none"
              >
                {primaryActionIcon}
                {primaryActionText}
                {primaryActionDropdownOptions && (
                  <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" />
                  </svg>
                )}
              </button>

              {dropdownOpen && primaryActionDropdownOptions && (
                <div className="absolute right-0 mt-2 py-2 w-56 bg-white rounded-lg shadow-lg z-50 border border-gray-200">
                  {primaryActionDropdownOptions.map((option, idx) => (
                    <button
                      key={idx}
                      className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 flex items-center gap-2"
                      onClick={() => { setDropdownOpen(false); option.onClick(); }}
                    >
                      {option.icon}
                      <span>{option.label}</span>
                    </button>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Search and filters row */}
      <div className="flex flex-col sm:flex-row sm:items-center gap-3">
        <div className="relative flex-grow">
          <input
            type="text"
            value={searchValue}
            onChange={(e) => onSearchChange(e.target.value)}
            placeholder="Cerca..."
            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-full focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
          <Search className="h-4 w-4 absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
          {searchValue && (
            <button
              className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400 hover:text-gray-600"
              onClick={() => onSearchChange('')}
            >

            </button>
          )}
        </div>

        {onSort && (
          <div className="relative" ref={sortDropdownRef}>
            <button 
              onClick={sortOptions ? () => setSortDropdownOpen(!sortDropdownOpen) : onSort} 
              className="px-3 py-1.5 rounded-full border border-gray-300 text-gray-700 hover:bg-gray-50 flex items-center gap-1.5"
            >
              <ArrowUpDown className="h-3.5 w-3.5" />
              Sort By
              {sortOptions && (
                <svg className="w-3.5 h-3.5 ml-1" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" />
                </svg>
              )}
            </button>

            {sortDropdownOpen && sortOptions && (
              <div className="absolute right-0 mt-2 py-2 w-40 bg-white rounded-lg shadow-lg z-50 border border-gray-200">
                {sortOptions.map((option, idx) => (
                  <button
                    key={idx}
                    className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
                    onClick={() => { 
                      setSortDropdownOpen(false); 
                      option.onClick(); 
                    }}
                  >
                    {option.label}
                  </button>
                ))}
              </div>
            )}
          </div>
        )}

        <button 
          className={`px-3 py-1.5 rounded-full ${filtersActive 
            ? 'bg-blue-100 text-blue-700' 
            : 'border border-gray-300 text-gray-700 hover:bg-gray-50'} flex items-center gap-1.5`}
        >
          <Filter className="h-3.5 w-3.5" />
          {filtersActive ? 'Filtri attivi' : 'Filters'}
        </button>

        {filterComponents}
      </div>

      {/* Selection mode toolbar - shown right below the search bar */}
      {selectionMode && selectionToolbar}

      {children && <div className="mt-3 sm:mt-0">{children}</div>}
    </div>
  );
};

export default PageHeader;

import React from 'react';
import { CheckSquare, Square, Trash2 } from 'lucide-react';

interface SelectionToolbarProps {
  /** Number of selected items */
  selectedCount: number;
  /** Total items available */
  totalCount: number;
  /** Select all handler */
  onSelectAll: () => void;
  /** Deselect all handler */
  onDeselectAll: () => void;
  /** Delete selected items handler */
  onDelete?: () => void;
  /** Delete selected items handler (legacy name) */
  onDeleteSelected?: () => void;
  /** Custom actions to add to the toolbar */
  extraActions?: React.ReactNode;
}

/**
 * Standardized toolbar for selection mode actions
 * Displays consistent selection actions across all pages
 */
const SelectionToolbar: React.FC<SelectionToolbarProps> = ({
  selectedCount,
  totalCount,
  onSelectAll,
  onDeselectAll,
  onDeleteSelected,
  onDelete,
  extraActions,
}) => {
  // Usa onDelete se disponibile, altrimenti onDeleteSelected (per compatibilit)
  const handleDelete = onDelete || onDeleteSelected;

  return (
    <div className="flex flex-wrap items-center gap-2 py-2">
      <button
        onClick={onSelectAll}
        className="px-3 py-1 rounded-full bg-blue-100 text-blue-700 text-xs font-medium flex items-center gap-1"
        aria-label="Seleziona tutti"
      >
        <CheckSquare className="h-3 w-3" />
        Seleziona tutti
      </button>

      <button
        onClick={onDeselectAll}
        className="px-3 py-1 rounded-full bg-gray-100 text-gray-700 text-xs font-medium flex items-center gap-1"
        aria-label="Deseleziona tutti"
      >
        <Square className="h-3 w-3" />
        Deseleziona tutti
      </button>

      {handleDelete && (
        <button
          onClick={handleDelete}
          disabled={selectedCount === 0}
          className={`px-3 py-1 rounded-full text-xs font-medium flex items-center gap-1 ${
            selectedCount > 0 
              ? 'bg-red-100 text-red-700 hover:bg-red-200' 
              : 'bg-gray-100 text-gray-400 cursor-not-allowed'
          }`}
          aria-label="Elimina selezionati"
        >
          <Trash2 className="h-3 w-3" />
          Elimina selezionati {selectedCount > 0 && `(${selectedCount})`}
        </button>
      )}

      {extraActions}
    </div>
  );
};

export default SelectionToolbar;

