import React, { Suspense } from 'react';
import { ErrorBoundary } from '../ui/ErrorBoundary';
import { LoadingFallback } from '../ui/LoadingFallback';

/**
 * Lazy-loaded Chart components
 * Week 11 Implementation - Component Lazy Loading
 */

// Lazy load Chart.js components
const Line = React.lazy(() => import('react-chartjs-2').then(module => ({ default: module.Line })));
const Bar = React.lazy(() => import('react-chartjs-2').then(module => ({ default: module.Bar })));
const Pie = React.lazy(() => import('react-chartjs-2').then(module => ({ default: module.Pie })));
const Doughnut = React.lazy(() => import('react-chartjs-2').then(module => ({ default: module.Doughnut })));

interface LazyChartProps {
  type: 'line' | 'bar' | 'pie' | 'doughnut';
  [key: string]: any;
}

const ChartFallback = () => (
  <div className="h-64 flex items-center justify-center border rounded-lg bg-gray-50">
    <LoadingFallback message="Loading Chart..." size="lg" />
  </div>
);

export const LazyChart: React.FC<LazyChartProps> = ({ type, ...props }) => {
  const renderChart = () => {
    switch (type) {
      case 'line':
        return <Line {...props} />;
      case 'bar':
        return <Bar {...props} />;
      case 'pie':
        return <Pie {...props} />;
      case 'doughnut':
        return <Doughnut {...props} />;
      default:
        return <div>Unsupported chart type</div>;
    }
  };

  return (
    <ErrorBoundary>
      <Suspense fallback={<ChartFallback />}>
        {renderChart()}
      </Suspense>
    </ErrorBoundary>
  );
};

// Individual lazy chart components
export const LazyLineChart: React.FC<any> = (props) => (
  <ErrorBoundary>
    <Suspense fallback={<ChartFallback />}>
      <Line {...props} />
    </Suspense>
  </ErrorBoundary>
);

export const LazyBarChart: React.FC<any> = (props) => (
  <ErrorBoundary>
    <Suspense fallback={<ChartFallback />}>
      <Bar {...props} />
    </Suspense>
  </ErrorBoundary>
);

export const LazyPieChart: React.FC<any> = (props) => (
  <ErrorBoundary>
    <Suspense fallback={<ChartFallback />}>
      <Pie {...props} />
    </Suspense>
  </ErrorBoundary>
);

export const LazyDoughnutChart: React.FC<any> = (props) => (
  <ErrorBoundary>
    <Suspense fallback={<ChartFallback />}>
      <Doughnut {...props} />
    </Suspense>
  </ErrorBoundary>
);

export default LazyChart;

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../design-system/molecules/Card';
import { Button } from '../../design-system/atoms/Button';
import { Input } from '../../design-system/atoms/Input';
import { Label } from '../../design-system/atoms/Label';
import { Badge } from '../../design-system/atoms/Badge';
import { 
  AlertTriangle,
  Calendar,
  CheckCircle,
  Edit,
  FileText,
  Plus,
  Trash2
} from 'lucide-react';
import { apiGet, apiDelete } from '../../services/api';
import { useToast } from '../../hooks/useToast';

interface DVR {
  id: string;
  titolo: string;
  descrizione: string;
  dataCreazione: string;
  dataScadenza: string;
  stato: 'attivo' | 'scaduto' | 'in_revisione';
  responsabile: string;
  versione: string;
  note?: string;
}

interface DVRManagerProps {
  siteId: string;
  siteName: string;
}

export const DVRManager: React.FC<DVRManagerProps> = ({ siteId, siteName }) => {
  const [dvrs, setDvrs] = useState<DVR[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingDvr, setEditingDvr] = useState<DVR | null>(null);
  const [formData, setFormData] = useState({
    titolo: '',
    descrizione: '',
    dataScadenza: '',
    responsabile: '',
    versione: '',
    note: ''
  });
  const { showToast } = useToast();

  useEffect(() => {
    fetchDvrs();
  }, [siteId]);

  const fetchDvrs = async () => {
    try {
      setLoading(true);
      const response = await apiGet(`/api/v1/dvr/site/${siteId}`) as { dvrs?: DVR[] };
      setDvrs(response.dvrs || []);
    } catch (error) {
      console.error('Error fetching DVRs:', error);
      showToast({ message: 'Errore nel caricamento dei DVR', type: 'error' });
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const payload = {
        ...formData,
        siteId,
        dataCreazione: editingDvr ? editingDvr.dataCreazione : new Date().toISOString(),
        stato: editingDvr ? editingDvr.stato : 'attivo'
      };

      if (editingDvr) {
        await apiPut(`/api/v1/dvr/${editingDvr.id}`, payload);
        showToast({ message: 'DVR aggiornato con successo', type: 'success' });
      } else {
        await apiPost('/api/v1/dvr', payload);
        showToast({ message: 'DVR creato con successo', type: 'success' });
      }

      await fetchDvrs();
      handleCloseForm();
    } catch (error) {
      console.error('Error saving DVR:', error);
      showToast({ message: 'Errore nel salvataggio del DVR', type: 'error' });
    }
  };

  const handleEdit = (dvr: DVR) => {
    setEditingDvr(dvr);
    setFormData({
      titolo: dvr.titolo,
      descrizione: dvr.descrizione,
      dataScadenza: dvr.dataScadenza.split('T')[0],
      responsabile: dvr.responsabile,
      versione: dvr.versione,
      note: dvr.note || ''
    });
    setShowForm(true);
  };

  const handleDelete = async (id: string) => {
    if (!confirm('Sei sicuro di voler eliminare questo DVR?')) return;

    try {
      await apiDelete(`/api/v1/dvr/${id}`);
      showToast({ message: 'DVR eliminato con successo', type: 'success' });
      await fetchDvrs();
    } catch (error) {
      console.error('Error deleting DVR:', error);
      showToast({ message: 'Errore nell\'eliminazione del DVR', type: 'error' });
    }
  };

  const handleCloseForm = () => {
    setShowForm(false);
    setEditingDvr(null);
    setFormData({
      titolo: '',
      descrizione: '',
      dataScadenza: '',
      responsabile: '',
      versione: '',
      note: ''
    });
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const getStatoBadgeVariant = (stato: string) => {
    switch (stato) {
      case 'attivo': return 'default';
      case 'scaduto': return 'destructive';
      case 'in_revisione': return 'secondary';
      default: return 'default';
    }
  };

  const getStatoIcon = (stato: string) => {
    switch (stato) {
      case 'attivo': return <CheckCircle className="h-4 w-4" />;
      case 'scaduto': return <AlertTriangle className="h-4 w-4" />;
      case 'in_revisione': return <AlertTriangle className="h-4 w-4" />;
      default: return <FileText className="h-4 w-4" />;
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="text-gray-500">Caricamento DVR...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">DVR - {siteName}</h2>
          <p className="text-gray-600">Gestione Documenti di Valutazione dei Rischi</p>
        </div>
        <Button onClick={() => setShowForm(true)} variant="primary">
          <Plus className="h-4 w-4 mr-2" />
          Nuovo DVR
        </Button>
      </div>

      {dvrs.length === 0 ? (
        <Card>
          <CardContent className="text-center py-8">
            <FileText className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Nessun DVR presente</h3>
            <p className="text-gray-500 mb-4">Inizia creando il primo documento di valutazione dei rischi per questa sede.</p>
            <Button onClick={() => setShowForm(true)} variant="primary">
              <Plus className="h-4 w-4 mr-2" />
              Crea primo DVR
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div className="grid gap-4">
          {dvrs.map((dvr) => (
            <Card key={dvr.id}>
              <CardHeader>
                <div className="flex justify-between items-start">
                  <div>
                    <CardTitle className="flex items-center gap-2">
                      {getStatoIcon(dvr.stato)}
                      {dvr.titolo}
                      <Badge variant={getStatoBadgeVariant(dvr.stato)}>
                        {dvr.stato.replace('_', ' ')}
                      </Badge>
                    </CardTitle>
                    <p className="text-sm text-gray-600 mt-1">Versione {dvr.versione}</p>
                  </div>
                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleEdit(dvr)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      variant="destructive"
                      size="sm"
                      onClick={() => handleDelete(dvr.id)}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  <p className="text-gray-700">{dvr.descrizione}</p>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                    <div className="flex items-center text-gray-600">
                      <Calendar className="h-4 w-4 mr-2" />
                      <span>Creato: {new Date(dvr.dataCreazione).toLocaleDateString()}</span>
                    </div>
                    <div className="flex items-center text-gray-600">
                      <Calendar className="h-4 w-4 mr-2" />
                      <span>Scadenza: {new Date(dvr.dataScadenza).toLocaleDateString()}</span>
                    </div>
                    <div className="flex items-center text-gray-600">
                      <User className="h-4 w-4 mr-2" />
                      <span>Responsabile: {dvr.responsabile}</span>
                    </div>
                  </div>

                  {dvr.note && (
                    <div className="mt-3 p-3 bg-gray-50 rounded-lg">
                      <p className="text-sm text-gray-700">{dvr.note}</p>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Form Modal */}
      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
              <h3 className="text-lg font-semibold text-gray-900">
                {editingDvr ? 'Modifica DVR' : 'Nuovo DVR'}
              </h3>
              <button
                onClick={handleCloseForm}
                className="text-gray-400 hover:text-gray-600"
              >
                <span className="sr-only">Chiudi</span>

              </button>
            </div>

            <form onSubmit={handleSubmit} className="p-6 space-y-4">
              <div>
                <Label htmlFor="titolo">Titolo *</Label>
                <Input
                  id="titolo"
                  name="titolo"
                  value={formData.titolo}
                  onChange={handleChange}
                  required
                />
              </div>

              <div>
                <Label htmlFor="descrizione">Descrizione *</Label>
                <textarea
                  id="descrizione"
                  name="descrizione"
                  value={formData.descrizione}
                  onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => handleChange(e)}
                  required
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <Label htmlFor="versione">Versione *</Label>
                <Input
                  id="versione"
                  name="versione"
                  value={formData.versione}
                  onChange={handleChange}
                  required
                  placeholder="es. 1.0"
                />
              </div>

              <div>
                <Label htmlFor="responsabile">Responsabile *</Label>
                <Input
                  id="responsabile"
                  name="responsabile"
                  value={formData.responsabile}
                  onChange={handleChange}
                  required
                />
              </div>

              <div>
                <Label htmlFor="dataScadenza">Data Scadenza *</Label>
                <Input
                  id="dataScadenza"
                  name="dataScadenza"
                  type="date"
                  value={formData.dataScadenza}
                  onChange={handleChange}
                  required
                />
              </div>

              <div>
                <Label htmlFor="note">Note</Label>
                <textarea
                  id="note"
                  name="note"
                  value={formData.note}
                  onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => handleChange(e)}
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div className="flex justify-end space-x-3 pt-4">
                <Button type="button" variant="outline" onClick={handleCloseForm}>
                  Annulla
                </Button>
                <Button type="submit" variant="primary">
                  {editingDvr ? 'Aggiorna' : 'Crea'} DVR
                </Button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../design-system/molecules/Card';
import { Button } from '../../design-system/atoms/Button';
import { Input } from '../../design-system/atoms/Input';
import { Label } from '../../design-system/atoms/Label';
import { Badge } from '../../design-system/atoms/Badge';
import { apiGet, apiDelete } from '../../services/api';
import { toast } from 'react-hot-toast';

interface Reparto {
  id: string;
  nome: string;
  responsabileId?: string;
  createdAt: string;
  updatedAt: string;
  site: {
    id: string;
    siteName: string;
    company: {
      id: string;
      name: string;
    };
  };
  responsabile?: {
    id: string;
    nome: string;
    cognome: string;
  };
}

interface RepartoFormData {
  nome: string;
  responsabileId?: string;
}

interface RepartoManagerProps {
  siteId: string;
  siteName: string;
}

const RepartoManager: React.FC<RepartoManagerProps> = ({ siteId, siteName }) => {
  const [reparti, setReparti] = useState<Reparto[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingReparto, setEditingReparto] = useState<Reparto | null>(null);
  const [formData, setFormData] = useState<RepartoFormData>({
    nome: '',
    responsabileId: ''
  });

  useEffect(() => {
    loadReparti();
  }, [siteId]);

  const loadReparti = async () => {
    try {
      setLoading(true);
      const response = await apiGet(`/api/v1/reparto/site/${siteId}`) as { reparti?: Reparto[] };
      setReparti(response.reparti || []);
    } catch (error) {
      console.error('Error loading reparti:', error);
      toast.error('Errore nel caricamento dei reparti');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      if (editingReparto) {
        // Update existing reparto
        await apiPut(`/api/v1/reparto/${editingReparto.id}`, formData);
        toast.success('Reparto aggiornato con successo');
      } else {
        // Create new reparto
        await apiPost(`/api/v1/reparto/site/${siteId}`, formData);
        toast.success('Reparto creato con successo');
      }

      resetForm();
      loadReparti();
    } catch (error) {
      console.error('Error saving reparto:', error);
      toast.error('Errore nel salvataggio del reparto');
    }
  };

  const handleEdit = (reparto: Reparto) => {
    setEditingReparto(reparto);
    setFormData({
      nome: reparto.nome,
      responsabileId: reparto.responsabileId || ''
    });
    setShowForm(true);
  };

  const handleDelete = async (repartoId: string) => {
    if (!confirm('Sei sicuro di voler eliminare questo reparto?')) {
      return;
    }

    try {
      await apiDelete(`/api/v1/reparto/${repartoId}`);
      toast.success('Reparto eliminato con successo');
      loadReparti();
    } catch (error) {
      console.error('Error deleting reparto:', error);
      toast.error('Errore nell\'eliminazione del reparto');
    }
  };

  const resetForm = () => {
    setFormData({
      nome: '',
      responsabileId: ''
    });
    setEditingReparto(null);
    setShowForm(false);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('it-IT');
  };

  if (loading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Reparti - {siteName}</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-4">Caricamento reparti...</div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle>Reparti - {siteName}</CardTitle>
          <Button 
            onClick={() => setShowForm(!showForm)}
            variant={showForm ? "outline" : "primary"}
          >
            {showForm ? 'Annulla' : 'Nuovo Reparto'}
          </Button>
        </CardHeader>
        <CardContent>
          {showForm && (
            <form onSubmit={handleSubmit} className="space-y-4 mb-6 p-4 border rounded-lg bg-gray-50">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="nome">Nome Reparto *</Label>
                  <Input
                    id="nome"
                    type="text"
                    value={formData.nome}
                    onChange={(e) => setFormData({ ...formData, nome: e.target.value })}
                    required
                    placeholder="Nome del reparto"
                  />
                </div>
                <div>
                  <Label htmlFor="responsabileId">ID Responsabile</Label>
                  <Input
                    id="responsabileId"
                    type="text"
                    value={formData.responsabileId}
                    onChange={(e) => setFormData({ ...formData, responsabileId: e.target.value })}
                    placeholder="ID della persona responsabile (opzionale)"
                  />
                </div>
              </div>
              <div className="flex gap-2">
                <Button type="submit">
                  {editingReparto ? 'Aggiorna Reparto' : 'Crea Reparto'}
                </Button>
                <Button type="button" variant="outline" onClick={resetForm}>
                  Annulla
                </Button>
              </div>
            </form>
          )}

          {reparti.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <p>Nessun reparto registrato per questa sede.</p>
              <p className="text-sm mt-2">Clicca su "Nuovo Reparto" per aggiungerne uno.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {reparti.map((reparto) => (
                <div key={reparto.id} className="border rounded-lg p-4 hover:bg-gray-50">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <h3 className="font-semibold text-lg">
                          {reparto.nome}
                        </h3>
                        <Badge variant="secondary">Reparto</Badge>
                      </div>
                      <div className="text-sm text-gray-600 space-y-1">
                        {reparto.responsabile ? (
                          <p>
                            <strong>Responsabile:</strong> {reparto.responsabile.nome} {reparto.responsabile.cognome}
                          </p>
                        ) : (
                          <p className="text-gray-400">
                            <strong>Responsabile:</strong> Non assegnato
                          </p>
                        )}
                        <p>
                          <strong>Creato il:</strong> {formatDate(reparto.createdAt)}
                        </p>
                        {reparto.updatedAt !== reparto.createdAt && (
                          <p>
                            <strong>Ultimo aggiornamento:</strong> {formatDate(reparto.updatedAt)}
                          </p>
                        )}
                      </div>
                    </div>
                    <div className="flex gap-2 ml-4">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleEdit(reparto)}
                      >
                        Modifica
                      </Button>
                      <Button
                        size="sm"
                        variant="destructive"
                        onClick={() => handleDelete(reparto.id)}
                      >
                        Elimina
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default RepartoManager;

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../design-system/molecules/Card';
import { Button } from '../../design-system/atoms/Button';
import { Input } from '../../design-system/atoms/Input';
import { Label } from '../../design-system/atoms/Label';
import { Badge } from '../../design-system/atoms/Badge';
import { apiGet, apiDelete } from '../../services/api';
import { toast } from 'react-hot-toast';

interface Sopralluogo {
  id: string;
  esecutoreId: string;
  dataEsecuzione: string;
  note?: string;
  createdAt: string;
  updatedAt: string;
  site: {
    id: string;
    siteName: string;
    company: {
      id: string;
      name: string;
    };
  };
  esecutore: {
    id: string;
    nome: string;
    cognome: string;
  };
}

interface SopralluogoFormData {
  esecutoreId: string;
  dataEsecuzione: string;
  note?: string;
}

interface SopralluogoManagerProps {
  siteId: string;
  siteName: string;
}

const SopralluogoManager: React.FC<SopralluogoManagerProps> = ({ siteId, siteName }) => {
  const [sopralluoghi, setSopralluoghi] = useState<Sopralluogo[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingSopralluogo, setEditingSopralluogo] = useState<Sopralluogo | null>(null);
  const [formData, setFormData] = useState<SopralluogoFormData>({
    esecutoreId: '',
    dataEsecuzione: '',
    note: ''
  });

  useEffect(() => {
    loadSopralluoghi();
  }, [siteId]);

  const loadSopralluoghi = async () => {
    try {
      setLoading(true);
      const response = await apiGet(`/api/v1/sopralluogo/site/${siteId}`) as { sopralluoghi?: Sopralluogo[] };
      setSopralluoghi(response.sopralluoghi || []);
    } catch (error) {
      console.error('Error loading sopralluoghi:', error);
      toast.error('Errore nel caricamento dei sopralluoghi');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      if (editingSopralluogo) {
        // Update existing sopralluogo
        await apiPut(`/api/v1/sopralluogo/${editingSopralluogo.id}`, formData);
        toast.success('Sopralluogo aggiornato con successo');
      } else {
        // Create new sopralluogo
        await apiPost(`/api/v1/sopralluogo/site/${siteId}`, formData);
        toast.success('Sopralluogo creato con successo');
      }

      resetForm();
      loadSopralluoghi();
    } catch (error) {
      console.error('Error saving sopralluogo:', error);
      toast.error('Errore nel salvataggio del sopralluogo');
    }
  };

  const handleEdit = (sopralluogo: Sopralluogo) => {
    setEditingSopralluogo(sopralluogo);
    setFormData({
      esecutoreId: sopralluogo.esecutoreId,
      dataEsecuzione: sopralluogo.dataEsecuzione.split('T')[0], // Format for date input
      note: sopralluogo.note || ''
    });
    setShowForm(true);
  };

  const handleDelete = async (sopralluogoId: string) => {
    if (!confirm('Sei sicuro di voler eliminare questo sopralluogo?')) {
      return;
    }

    try {
      await apiDelete(`/api/v1/sopralluogo/${sopralluogoId}`);
      toast.success('Sopralluogo eliminato con successo');
      loadSopralluoghi();
    } catch (error) {
      console.error('Error deleting sopralluogo:', error);
      toast.error('Errore nell\'eliminazione del sopralluogo');
    }
  };

  const resetForm = () => {
    setFormData({
      esecutoreId: '',
      dataEsecuzione: '',
      note: ''
    });
    setEditingSopralluogo(null);
    setShowForm(false);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('it-IT');
  };

  const isRecentSopralluogo = (dateString: string) => {
    const sopralluogoDate = new Date(dateString);
    const now = new Date();
    const diffInDays = Math.floor((now.getTime() - sopralluogoDate.getTime()) / (1000 * 60 * 60 * 24));
    return diffInDays <= 30; // Recent if within 30 days
  };

  if (loading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Sopralluoghi - {siteName}</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-4">Caricamento sopralluoghi...</div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle>Sopralluoghi - {siteName}</CardTitle>
          <Button 
            onClick={() => setShowForm(!showForm)}
            variant={showForm ? "outline" : "primary"}
          >
            {showForm ? 'Annulla' : 'Nuovo Sopralluogo'}
          </Button>
        </CardHeader>
        <CardContent>
          {showForm && (
            <form onSubmit={handleSubmit} className="space-y-4 mb-6 p-4 border rounded-lg bg-gray-50">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="esecutoreId">ID Esecutore *</Label>
                  <Input
                    id="esecutoreId"
                    type="text"
                    value={formData.esecutoreId}
                    onChange={(e) => setFormData({ ...formData, esecutoreId: e.target.value })}
                    required
                    placeholder="ID della persona che esegue il sopralluogo"
                  />
                </div>
                <div>
                  <Label htmlFor="dataEsecuzione">Data Esecuzione *</Label>
                  <Input
                    id="dataEsecuzione"
                    type="date"
                    value={formData.dataEsecuzione}
                    onChange={(e) => setFormData({ ...formData, dataEsecuzione: e.target.value })}
                    required
                  />
                </div>
              </div>
              <div>
                <Label htmlFor="note">Note</Label>
                <textarea
                  id="note"
                  value={formData.note}
                  onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => setFormData({ ...formData, note: e.target.value })}
                  placeholder="Note aggiuntive sul sopralluogo..."
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
              <div className="flex gap-2">
                <Button type="submit">
                  {editingSopralluogo ? 'Aggiorna Sopralluogo' : 'Crea Sopralluogo'}
                </Button>
                <Button type="button" variant="outline" onClick={resetForm}>
                  Annulla
                </Button>
              </div>
            </form>
          )}

          {sopralluoghi.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <p>Nessun sopralluogo registrato per questa sede.</p>
              <p className="text-sm mt-2">Clicca su "Nuovo Sopralluogo" per aggiungerne uno.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {sopralluoghi.map((sopralluogo) => (
                <div key={sopralluogo.id} className="border rounded-lg p-4 hover:bg-gray-50">
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <h3 className="font-semibold">
                          Sopralluogo del {formatDate(sopralluogo.dataEsecuzione)}
                        </h3>
                        {isRecentSopralluogo(sopralluogo.dataEsecuzione) && (
                          <Badge variant="default">Recente</Badge>
                        )}
                      </div>
                      <div className="text-sm text-gray-600 space-y-1">
                        <p>
                          <strong>Esecutore:</strong> {sopralluogo.esecutore.nome} {sopralluogo.esecutore.cognome}
                        </p>
                        {sopralluogo.note && (
                          <p>
                            <strong>Note:</strong> {sopralluogo.note}
                          </p>
                        )}
                        <p>
                          <strong>Creato il:</strong> {formatDate(sopralluogo.createdAt)}
                        </p>
                      </div>
                    </div>
                    <div className="flex gap-2 ml-4">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleEdit(sopralluogo)}
                      >
                        Modifica
                      </Button>
                      <Button
                        size="sm"
                        variant="destructive"
                        onClick={() => handleDelete(sopralluogo.id)}
                      >
                        Elimina
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

