/**
 * Componente PersonImport refactorizzato - Versione modulare e ottimizzata
 * 
 * Questo componente  stato refactorizzato per migliorare:
 * - Manutenibilit: Codice diviso in moduli specializzati
 * - Leggibilit: Logica separata per responsabilit
 * - Testabilit: Funzioni pure e componenti isolati
 * - Performance: Ottimizzazione delle operazioni sui dati
 */

import React, { useState, useCallback, useEffect } from 'react';
import ImportModal from '../../shared/modals/ImportModal';
import PersonImportConflictModal from '../PersonImportConflictModal';

// Import dei moduli specializzati
import { PREVIEW_COLUMNS } from './constants';
import { validatePersons } from './validationUtils';
import { ConflictInfo } from './conflictUtils';
import { 
  processPersonImportFile, 
  prepareDataForImport, 
  formatPersonsForAPI 
} from './dataProcessing';

interface PersonImportProps {
  onImport: (persons: any[], overwriteIds?: string[]) => Promise<void>;
  onClose: () => void;
  existingPersons?: any[];
  existingCompanies?: any[];
  onRefreshData?: () => Promise<void>;
}

const PersonImportRefactored: React.FC<PersonImportProps> = ({
  onImport,
  onClose,
  existingPersons = [],
  existingCompanies = [],
  onRefreshData
}) => {
  // State management
  const [previewData, setPreviewData] = useState<any[]>([]);
  const [conflicts, setConflicts] = useState<{ [index: number]: ConflictInfo }>({});
  const [selectedRows, setSelectedRows] = useState<Set<number>>(new Set());

  // Inizializza tutte le righe come selezionate quando cambiano i dati di preview
  useEffect(() => {
    if (previewData.length > 0) {
      setSelectedRows(new Set(Array.from({ length: previewData.length }, (_, i) => i)));
    }
  }, [previewData]);

  // Gestore per il cambio di selezione delle righe
  const handleRowSelectionChange = (newSelectedRows: Set<number>) => {
    setSelectedRows(newSelectedRows);
  };

  // Funzione personalizzata per processare il file
  const customProcessFile = useCallback(async (file: File) => {
    const result = await processPersonImportFile(file, existingCompanies, existingPersons);

    setConflicts(result.conflicts);
    setPreviewData(result.data);
    setSelectedRows(result.selectedRows);

    return result.data;
  }, [existingCompanies, existingPersons]);

  // Aggiorna la risoluzione di un conflitto
  const updateConflictResolution = (index: number, resolution: Partial<ConflictInfo>) => {
    setConflicts(prev => ({
      ...prev,
      [index]: { ...prev[index], ...resolution }
    }));
  };

  // Gestione dell'import
  const handleImport = useCallback(async (persons: any[], overwriteIds?: string[], importSelectedRows?: Set<number>) => {
    try {
      // Usa le righe selezionate passate dal modal, con fallback allo state locale
      const activeSelectedRows = importSelectedRows || selectedRows;

      // Prepara i dati applicando le risoluzioni dei conflitti
      const { resolvedPersons, finalOverwriteIds } = prepareDataForImport(
        persons,
        conflicts,
        activeSelectedRows,
        overwriteIds
      );

      // Formatta i dati per l'API
      const formattedPersons = formatPersonsForAPI(resolvedPersons);

      await onImport(formattedPersons, finalOverwriteIds);

      // Aggiorna i dati esistenti dopo l'importazione per rilevare nuovi duplicati
      if (onRefreshData) {
        await onRefreshData();
      }

      onClose();
    } catch (error) {
      throw error;
    }
  }, [conflicts, selectedRows, onImport, onClose, onRefreshData]);

  // Opzioni per le aziende
  const companyOptions = existingCompanies.map(company => ({
    value: company.id,
    label: company.ragioneSociale || company.name || 'Azienda senza nome'
  }));

  // Gestione dell'assegnazione delle aziende tramite dropdown
  const handleCompanyChange = useCallback((selectedRowIds: string[], companyId: string) => {
    // Trova l'azienda selezionata
    const selectedCompany = existingCompanies.find(c => c.id === companyId);
    if (!selectedCompany) {
      return;
    }

    const companyName = selectedCompany.ragioneSociale || selectedCompany.name;

    // Aggiorna i dati di preview per le righe selezionate
    setPreviewData(prevData => {
      const newData = [...prevData];

      selectedRowIds.forEach(rowIdStr => {
        const rowIndex = parseInt(rowIdStr);

        if (rowIndex >= 0 && rowIndex < newData.length) {
          newData[rowIndex] = {
            ...newData[rowIndex],
            companyId: companyId,
            companyName: companyName
          };

          // Rimuovi eventuali conflitti di azienda non valida per questa riga
          if (conflicts[rowIndex] && conflicts[rowIndex].type === 'invalid_company') {
            setConflicts(prevConflicts => {
              const newConflicts = { ...prevConflicts };
              delete newConflicts[rowIndex];
              return newConflicts;
            });
          }
        }
      });

      return newData;
    });
  }, [existingCompanies, conflicts]);

  // Controlli extra per il modal (vuoti come richiesto)
  const extraControls = (
    <div className="mb-4">
      {/* Sezione rimossa come richiesto dall'utente */}
    </div>
  );

  return (
    <>
      <ImportModal
        title="Importa Persone"
        subtitle="Carica un file CSV per importare le persone nel sistema"
        onImport={handleImport}
        onClose={onClose}
        processFile={customProcessFile}
        uniqueKey="taxCode"
        existingData={existingPersons}
        previewColumns={PREVIEW_COLUMNS}
        validateRows={validatePersons}
        supportedFormats={['.csv']}
        formatsMessage="Formato supportato: CSV (separatore punto e virgola)"
        showBulkSelectButtons={true}
        extraControls={extraControls}
        hidePreviewTable={false}
        useSingleCheckboxColumn={false}
        initialPreviewData={previewData}
        conflicts={conflicts}
        onConflictResolutionChange={updateConflictResolution}
        selectedRows={selectedRows}
        onRowSelectionChange={handleRowSelectionChange}
        availableCompanies={existingCompanies}
        onCompanyChange={handleCompanyChange}
      />

      {/* Modal per gestire i conflitti di duplicati */}
      {Object.keys(conflicts).length > 0 && (
        <PersonImportConflictModal
          isOpen={Object.keys(conflicts).length > 0}
          conflicts={Object.entries(conflicts).map(([index, conflictInfo]) => ({
            person: previewData[parseInt(index)],
            index: parseInt(index),
            type: conflictInfo.type,
            existingPerson: conflictInfo.existingPerson,
            suggestedCompanies: conflictInfo.suggestedCompanies
          }))}
          onResolve={(resolutions) => {
            // Converte le risoluzioni dal formato del modal al formato interno
            resolutions.forEach(resolution => {
              updateConflictResolution(resolution.index, {
                resolution: resolution.action,
                selectedCompanyId: resolution.companyId,
                selectedCompanyName: resolution.companyName
              });
            });
          }}
          onClose={() => {
            // Chiudi il modal dei conflitti ma mantieni aperto l'ImportModal
          }}
          existingCompanies={existingCompanies}
        />
      )}
    </>
  );
};

export default PersonImportRefactored;

/**
 * Utility per la gestione dei conflitti nell'importazione persone
 */

export interface ConflictInfo {
  type: 'duplicate' | 'invalid_company';
  existingPerson?: any;
  suggestedCompanies?: any[];
  resolution?: 'skip' | 'overwrite' | 'assign_company';
  selectedCompanyId?: string;
  selectedCompanyName?: string;
}

/**
 * Rileva conflitti nei dati delle persone
 */
export const detectConflicts = (
  persons: any[], 
  existingPersons: any[], 
  existingCompanies: any[]
): { [index: number]: ConflictInfo } => {
  const detectedConflicts: { [index: number]: ConflictInfo } = {};

  persons.forEach((person, index) => {
    // Controlla duplicati di codice fiscale
    if (person.taxCode) {
      const existingPerson = existingPersons.find(ep => 
        ep.taxCode && ep.taxCode.toLowerCase().trim() === person.taxCode.toLowerCase().trim()
      );

      if (existingPerson) {
        detectedConflicts[index] = {
          type: 'duplicate',
          existingPerson: {
            id: existingPerson.id,
            firstName: existingPerson.firstName,
            lastName: existingPerson.lastName,
            email: existingPerson.email,
            phone: existingPerson.phone,
            taxCode: existingPerson.taxCode,
            birthDate: existingPerson.birthDate,
            address: existingPerson.address,
            city: existingPerson.city,
            province: existingPerson.province,
            postalCode: existingPerson.postalCode,
            roleType: existingPerson.personRoles?.[0]?.roleType || 'N/A',
            companyName: existingPerson.company?.ragioneSociale || existingPerson.company?.name || 'N/A'
          }
          // Rimuovo la risoluzione automatica 'skip' per permettere all'utente di scegliere
        };
      }
    }

    // Controlla aziende non valide
    if (person.companyName && !person.companyId) {
      // Trova aziende simili
      const suggestedCompanies = existingCompanies.filter(c => {
        const companyName = c.ragioneSociale || c.name || '';
        return companyName.toLowerCase().includes(person.companyName.toLowerCase()) ||
               person.companyName.toLowerCase().includes(companyName.toLowerCase());
      });

      detectedConflicts[index] = {
        type: 'invalid_company',
        suggestedCompanies,
        resolution: suggestedCompanies.length > 0 ? 'assign_company' : 'skip',
        selectedCompanyId: suggestedCompanies.length > 0 ? suggestedCompanies[0].id : undefined,
        selectedCompanyName: suggestedCompanies.length > 0 ? (suggestedCompanies[0].ragioneSociale || suggestedCompanies[0].name) : undefined
      };
    }
  });

  return detectedConflicts;
};

/**
 * Risolve automaticamente tutti i duplicati con l'azione specificata
 */
export const resolveAllDuplicates = (
  conflicts: { [index: number]: ConflictInfo },
  action: 'skip' | 'overwrite'
): { [index: number]: ConflictInfo } => {
  const updated = { ...conflicts };
  Object.keys(updated).forEach(indexStr => {
    const index = parseInt(indexStr);
    if (updated[index] && updated[index].type === 'duplicate') {
      updated[index] = { ...updated[index], resolution: action };
    }
  });
  return updated;
};

/**
 * Risolve automaticamente tutte le aziende non valide con l'azione specificata
 */
export const resolveAllInvalidCompanies = (
  conflicts: { [index: number]: ConflictInfo },
  action: 'skip' | 'assign_first'
): { [index: number]: ConflictInfo } => {
  const updated = { ...conflicts };
  Object.keys(updated).forEach(indexStr => {
    const index = parseInt(indexStr);
    if (updated[index] && updated[index].type === 'invalid_company') {
      if (action === 'skip') {
        updated[index] = { ...updated[index], resolution: 'skip' };
      } else if (action === 'assign_first' && updated[index].suggestedCompanies && updated[index].suggestedCompanies!.length > 0) {
        const firstCompany = updated[index].suggestedCompanies![0];
        updated[index] = { 
          ...updated[index], 
          resolution: 'assign_company',
          selectedCompanyId: firstCompany.id,
          selectedCompanyName: firstCompany.ragioneSociale || firstCompany.name
        };
      }
    }
  });
  return updated;
};

/**
 * Converte i conflitti dal formato interno al formato del modal
 */
export const convertConflictsForModal = (
  conflicts: { [index: number]: ConflictInfo },
  previewData: any[]
) => {
  return Object.entries(conflicts).map(([index, conflict]) => ({
    person: previewData[parseInt(index)],
    index: parseInt(index),
    type: conflict.type,
    existingPerson: conflict.existingPerson,
    suggestedCompanies: conflict.suggestedCompanies || []
  }));
};

/**
 * Utility per il processamento dei dati nell'importazione persone
 */

import { defaultProcessFile } from '../../shared/GenericImport';
import { applyTitleCaseToFields } from '../../../utils/textFormatters';
import { extractBirthDateFromTaxCode } from './dateUtils';
import { detectConflicts, ConflictInfo } from './conflictUtils';
import { normalizePersonStatus } from './validationUtils';
import { formatDateForAPI } from './dateUtils';
import { CSV_HEADER_MAP, TITLE_CASE_FIELDS } from './constants';

/**
 * Processa il file CSV e prepara i dati per l'importazione
 */
export const processPersonImportFile = async (
  file: File,
  existingCompanies: any[],
  existingPersons: any[]
): Promise<{
  data: any[];
  conflicts: { [index: number]: ConflictInfo };
  selectedRows: Set<number>;
}> => {
  // Prima processa il file con la funzione standard
  const data = await defaultProcessFile(file, CSV_HEADER_MAP, ';');

  if (data.length === 0) {
    return { data: [], conflicts: {}, selectedRows: new Set() };
  }

  // Applica Title Case ai campi specificati per ogni persona
  const processedData = data.map((person: any) => 
    applyTitleCaseToFields(person, TITLE_CASE_FIELDS)
  );

  // Risolvi i nomi delle aziende in ID
  const finalData = processedData.map((person: any) => {
    // Normalizza il codice fiscale
    if (person.taxCode) {
      person.taxCode = person.taxCode.toUpperCase().trim();
    }

    // Se c' un codice fiscale ma non una data di nascita, prova a estrarla
    if (person.taxCode && !person.birthDate) {
      const extractedDate = extractBirthDateFromTaxCode(person.taxCode);
      if (extractedDate) {
        person.birthDate = extractedDate;
      }
    }

    if (person.companyName && typeof person.companyName === 'string') {
      const company = existingCompanies.find(c => 
        (c.ragioneSociale && c.ragioneSociale.toLowerCase().trim() === person.companyName.toLowerCase().trim()) ||
        (c.name && c.name.toLowerCase().trim() === person.companyName.toLowerCase().trim())
      );

      if (company) {
        person.companyId = company.id;
        person.companyName = company.ragioneSociale || company.name;
      } else {
        // NON assegnare companyId se non trovato - questo dovrebbe attivare il conflict detection
        person.companyId = undefined;
      }
    }

    return person;
  });

  // Rileva conflitti
  const detectedConflicts = detectConflicts(finalData, existingPersons, existingCompanies);

  // Deseleziona automaticamente le righe con conflitti di duplicato
  let selectedRows: Set<number>;
  if (Object.keys(detectedConflicts).length > 0) {
    selectedRows = new Set<number>();
    finalData.forEach((_, index) => {
      const conflict = detectedConflicts[index];
      // Seleziona solo le righe senza conflitti di duplicato
      if (!conflict || conflict.type !== 'duplicate') {
        selectedRows.add(index);
      }
    });
  } else {
    // Se non ci sono conflitti, seleziona tutte le righe
    selectedRows = new Set(Array.from({ length: finalData.length }, (_, i) => i));
  }

  return {
    data: finalData,
    conflicts: detectedConflicts,
    selectedRows
  };
};

/**
 * Prepara i dati per l'importazione applicando le risoluzioni dei conflitti
 */
export const prepareDataForImport = (
  persons: any[],
  conflicts: { [index: number]: ConflictInfo },
  selectedRows: Set<number>,
  overwriteIds: string[] = []
): {
  resolvedPersons: any[];
  finalOverwriteIds: string[];
} => {
  const resolvedPersons: any[] = [];
  const finalOverwriteIds = [...overwriteIds];

  // Itera attraverso tutte le persone per applicare le risoluzioni dei conflitti
  persons.forEach((person, index) => {
    // Se la riga non  selezionata, saltala
    if (!selectedRows.has(index)) {
      return;
    }

    const conflict = conflicts[index];

    // Se non c' conflitto, aggiungi la persona
    if (!conflict) {
      resolvedPersons.push(person);
      return;
    }

    // Gestione dei duplicati
    if (conflict.type === 'duplicate') {
      if (!conflict.resolution) {
        return; // Salta questo duplicato
      }

      if (conflict.resolution === 'skip') {
        return; // Salta questo duplicato
      }

      if (conflict.resolution === 'overwrite' && conflict.existingPerson) {
        finalOverwriteIds.push(conflict.existingPerson.id);
        resolvedPersons.push(person);
        return;
      }
    }

    // Gestione delle aziende non valide
    if (conflict.type === 'invalid_company') {
      if (conflict.resolution === 'skip') {
        return; // Salta questa persona
      }

      if (conflict.resolution === 'assign_company' && conflict.selectedCompanyId) {
        resolvedPersons.push({
          ...person,
          companyId: conflict.selectedCompanyId,
          companyName: conflict.selectedCompanyName
        });
        return;
      }
    }

    // Se arriviamo qui, c' un conflitto non risolto - salta la persona
  });

  return { resolvedPersons, finalOverwriteIds };
};

/**
 * Formatta i dati delle persone per l'API
 */
export const formatPersonsForAPI = (persons: any[]): any[] => {
  return persons.map(person => {
    const formatted: any = {};

    // Copia tutti i campi necessari
    ['firstName', 'lastName', 'email', 'phone', 'taxCode', 'birthDate',
      'residenceAddress', 'city', 'province', 'postalCode', 'title', 'companyId', 'username', 'notes', 'status'].forEach(field => {
      if (person[field] !== undefined && person[field] !== null && person[field] !== '') {
        formatted[field] = person[field];
      }
    });

    // Gestione speciale per il campo status
    formatted.status = normalizePersonStatus(person.status);

    // Formatta la data di nascita se presente
    if (formatted.birthDate) {
      formatted.birthDate = formatDateForAPI(formatted.birthDate);
    }

    // Assicurati che il roleType sia valido
    if (!formatted.roleType) {
      formatted.roleType = 'EMPLOYEE'; // Default
    }

    return formatted;
  });
};

/**
 * Utility per la gestione e formattazione delle date nell'importazione persone
 */

import { MONTH_MAP } from './constants';

/**
 * Formatta una data per la visualizzazione (dd/mm/yyyy)
 */
export const formatDateForDisplay = (dateString: string): string => {
  if (!dateString) return '';

  try {
    let date: Date;

    // Se  gi in formato dd/mm/yyyy, restituiscilo cos com'
    if (/^\d{1,2}\/\d{1,2}\/\d{4}$/.test(dateString)) {
      const parts = dateString.split('/');
      const day = parseInt(parts[0]);
      const month = parseInt(parts[1]);
      const year = parseInt(parts[2]);

      // Verifica che sia una data valida
      if (day >= 1 && day <= 31 && month >= 1 && month <= 12 && year >= 1900 && year <= 2100) {
        date = new Date(year, month - 1, day);
        if (date.getDate() === day && date.getMonth() === month - 1 && date.getFullYear() === year) {
          return dateString; //  gi nel formato corretto
        }
      }
    }

    // Se  in formato YYYY-MM-DD, convertilo
    if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
      date = new Date(dateString);
      if (!isNaN(date.getTime())) {
        const day = date.getDate().toString().padStart(2, '0');
        const month = (date.getMonth() + 1).toString().padStart(2, '0');
        const year = date.getFullYear();
        return `${day}/${month}/${year}`;
      }
    }

    // Se  in formato dd-mm-yyyy, convertilo
    if (/^\d{1,2}-\d{1,2}-\d{4}$/.test(dateString)) {
      const parts = dateString.split('-');
      const day = parseInt(parts[0]);
      const month = parseInt(parts[1]);
      const year = parseInt(parts[2]);

      if (day >= 1 && day <= 31 && month >= 1 && month <= 12 && year >= 1900 && year <= 2100) {
        date = new Date(year, month - 1, day);
        if (date.getDate() === day && date.getMonth() === month - 1 && date.getFullYear() === year) {
          const dayStr = day.toString().padStart(2, '0');
          const monthStr = month.toString().padStart(2, '0');
          return `${dayStr}/${monthStr}/${year}`;
        }
      }
    }

    // Fallback: prova a parsare come Date
    date = new Date(dateString);
    if (!isNaN(date.getTime())) {
      const day = date.getDate().toString().padStart(2, '0');
      const month = (date.getMonth() + 1).toString().padStart(2, '0');
      const year = date.getFullYear();
      return `${day}/${month}/${year}`;
    }

    return dateString; // Se non riesce a parsare, restituisce l'originale
  } catch (error) {
    console.warn(`Errore nella formattazione della data: ${dateString}`, error);
    return dateString;
  }
};

/**
 * Valida una data con supporto per pi formati
 */
export const isValidDate = (dateString: string): boolean => {
  if (!dateString) return false;

  try {
    // Se  in formato dd/mm/yyyy
    if (/^\d{1,2}\/\d{1,2}\/\d{4}$/.test(dateString)) {
      const parts = dateString.split('/');
      const day = parseInt(parts[0]);
      const month = parseInt(parts[1]);
      const year = parseInt(parts[2]);

      // Verifica che i valori siano validi
      if (day >= 1 && day <= 31 && month >= 1 && month <= 12 && year >= 1900 && year <= 2100) {
        const date = new Date(year, month - 1, day);
        // Verifica che la data creata corrisponda ai valori inseriti
        return date.getDate() === day && date.getMonth() === month - 1 && date.getFullYear() === year;
      }
      return false;
    }

    // Se  in formato YYYY-MM-DD
    if (/^\d{4}-\d{2}-\d{2}$/.test(dateString)) {
      const date = new Date(dateString);
      return !isNaN(date.getTime());
    }

    // Se  in formato dd-mm-yyyy
    if (/^\d{1,2}-\d{1,2}-\d{4}$/.test(dateString)) {
      const parts = dateString.split('-');
      const day = parseInt(parts[0]);
      const month = parseInt(parts[1]);
      const year = parseInt(parts[2]);

      if (day >= 1 && day <= 31 && month >= 1 && month <= 12 && year >= 1900 && year <= 2100) {
        const date = new Date(year, month - 1, day);
        return date.getDate() === day && date.getMonth() === month - 1 && date.getFullYear() === year;
      }
      return false;
    }

    // Fallback: prova a parsare direttamente
    const date = new Date(dateString);
    return !isNaN(date.getTime());
  } catch (error) {
    return false;
  }
};

/**
 * Formatta una data per l'API (YYYY-MM-DD)
 */
export const formatDateForAPI = (dateString: string): string => {
  if (!dateString) return '';

  // Normalizza la stringa rimuovendo spazi extra
  const cleanDateStr = dateString.toString().trim();

  // Prova diversi formati di data
  const formats = [
    /^\d{4}-\d{2}-\d{2}$/, // YYYY-MM-DD (gi nel formato corretto)
    /^\d{2}\/\d{2}\/\d{4}$/, // DD/MM/YYYY
    /^\d{2}-\d{2}-\d{4}$/, // DD-MM-YYYY
    /^\d{1,2}\/\d{1,2}\/\d{4}$/, // D/M/YYYY o DD/M/YYYY
    /^\d{1,2}-\d{1,2}-\d{4}$/, // D-M-YYYY o DD-M-YYYY
  ];

  // Se  gi nel formato YYYY-MM-DD, verifica che sia valido
  if (formats[0].test(cleanDateStr)) {
    const date = new Date(cleanDateStr);
    if (!isNaN(date.getTime()) && date.getFullYear() >= 1900 && date.getFullYear() <= 2100) {
      return cleanDateStr;
    }
  }

  // Prova gli altri formati
  for (let i = 1; i < formats.length; i++) {
    const format = formats[i];
    if (format.test(cleanDateStr)) {
      let date: Date;

      if (cleanDateStr.includes('/')) {
        const parts = cleanDateStr.split('/');
        if (parts.length === 3) {
          const day = parseInt(parts[0]);
          const month = parseInt(parts[1]);
          const year = parseInt(parts[2]);

          // Verifica che i valori siano validi
          if (day >= 1 && day <= 31 && month >= 1 && month <= 12 && year >= 1900 && year <= 2100) {
            date = new Date(year, month - 1, day);
            // Verifica che la data creata corrisponda ai valori inseriti
            if (date.getDate() === day && date.getMonth() === month - 1 && date.getFullYear() === year) {
              return date.toISOString().split('T')[0]; // Ritorna YYYY-MM-DD
            }
          }
        }
      } else if (cleanDateStr.includes('-')) {
        // DD-MM-YYYY
        const parts = cleanDateStr.split('-');
        if (parts.length === 3) {
          const day = parseInt(parts[0]);
          const month = parseInt(parts[1]);
          const year = parseInt(parts[2]);

          if (day >= 1 && day <= 31 && month >= 1 && month <= 12 && year >= 1900 && year <= 2100) {
            date = new Date(year, month - 1, day);
            if (date.getDate() === day && date.getMonth() === month - 1 && date.getFullYear() === year) {
              return date.toISOString().split('T')[0]; // Ritorna YYYY-MM-DD
            }
          }
        }
      }
    }
  }

  // Fallback: prova a parsare direttamente
  try {
    const date = new Date(cleanDateStr);
    if (!isNaN(date.getTime()) && date.getFullYear() >= 1900 && date.getFullYear() <= 2100) {
      return date.toISOString().split('T')[0]; // Ritorna YYYY-MM-DD
    }
  } catch (error) {
    console.warn(`Impossibile formattare la data: ${cleanDateStr}`);
  }

  return cleanDateStr; // Ritorna il valore originale se non pu essere formattato
};

/**
 * Estrae la data di nascita dal codice fiscale
 */
export const extractBirthDateFromTaxCode = (taxCode: string): string | null => {
  if (!taxCode || taxCode.length !== 16) return null;

  try {
    const yearPart = taxCode.substring(6, 8);
    const monthPart = taxCode.substring(8, 9);
    const dayPart = taxCode.substring(9, 11);

    const month = MONTH_MAP[monthPart];
    if (!month) return null;

    // Determina l'anno (assumendo che anni 00-30 siano 2000-2030, 31-99 siano 1931-1999)
    const currentYear = new Date().getFullYear();
    const currentCentury = Math.floor(currentYear / 100) * 100;
    const year = parseInt(yearPart) <= 30 ? currentCentury + parseInt(yearPart) : currentCentury - 100 + parseInt(yearPart);

    // Per le donne, il giorno  aumentato di 40
    let day = parseInt(dayPart);
    if (day > 31) {
      day -= 40;
    }

    const dayStr = day.toString().padStart(2, '0');

    return `${year}-${month}-${dayStr}`;
  } catch (error) {
    return null;
  }
};

/**
 * Utility per la validazione dei dati delle persone nell'importazione
 */

import { isValidCodiceFiscale } from '../../../lib/utils';
import { isValidDate, extractBirthDateFromTaxCode, formatDateForDisplay } from './dateUtils';
import { VALID_PERSON_STATUSES } from './constants';

/**
 * Valida una singola persona
 */
export const validatePerson = (person: Record<string, any>): string[] => {
  const errors: string[] = [];

  if (!person.firstName?.trim()) {
    errors.push('Nome  obbligatorio');
  }

  if (!person.lastName?.trim()) {
    errors.push('Cognome  obbligatorio');
  }

  if (!person.taxCode?.trim()) {
    errors.push('Codice fiscale  obbligatorio');
  } else if (!isValidCodiceFiscale(person.taxCode)) {
    errors.push('Codice fiscale non valido (formato non corretto)');
  }

  // Email opzionale ma se presente deve essere valida
  if (person.email?.trim() && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(person.email)) {
    errors.push('Email non valida');
  }

  if (person.birthDate && !isValidDate(person.birthDate)) {
    errors.push('Data di nascita non valida');
  }

  return errors;
};

/**
 * Validazione personalizzata che include l'estrazione della data di nascita
 */
export const validatePersons = (persons: Record<string, any>[]): { [rowIdx: number]: string[] } => {
  const errors: { [rowIdx: number]: string[] } = {};

  persons.forEach((person, index) => {
    // Controlla se  un template vuoto (tutti i campi obbligatori sono vuoti)
    const isEmptyTemplate = !person.firstName?.trim() && 
                           !person.lastName?.trim() && 
                           !person.taxCode?.trim();

    // Se  un template vuoto, salta la validazione
    if (isEmptyTemplate) {
      return;
    }

    // Se c' un codice fiscale ma non una data di nascita, prova a estrarla
    if (person.taxCode && !person.birthDate) {
      const extractedDate = extractBirthDateFromTaxCode(person.taxCode);
      if (extractedDate) {
        person.birthDate = extractedDate;
      }
    }

    // Formatta la data di nascita per la visualizzazione
    if (person.birthDate) {
      person.birthDate = formatDateForDisplay(person.birthDate);
    }

    const personErrors = validatePerson(person);
    if (personErrors.length > 0) {
      errors[index] = personErrors;
    }
  });

  return errors;
};

/**
 * Normalizza e valida lo status di una persona
 */
export const normalizePersonStatus = (status: any): string => {
  if (status !== undefined) {
    const statusValue = status?.toString().trim();
    if (!statusValue || statusValue === '') {
      return 'ACTIVE';
    } else {
      // Verifica che il valore sia uno dei valori validi dell'enum PersonStatus
      const upperStatusValue = statusValue.toUpperCase();
      if (VALID_PERSON_STATUSES.includes(upperStatusValue)) {
        return upperStatusValue;
      } else {
        return 'ACTIVE';
      }
    }
  } else {
    // Se il campo status non  presente nel CSV, imposta il default
    return 'ACTIVE';
  }
};

