import React, { useState, useEffect } from 'react';
import { 
  Award,
  Building,
  ChevronDown,
  ChevronRight,
  Crown,
  Edit3,
  Move,
  Plus,
  Save,
  Settings,
  Shield,
  Star,
  Trash2,
  UserCheck,
  Users,
  X
} from 'lucide-react';
import { getRoleHierarchy, getCurrentUserRoleHierarchy } from '../../services/roles';
import { isAuthenticated } from '../../services/auth';
import type { RoleHierarchy as RoleHierarchyType, UserRoleHierarchy } from '../../services/roles';

interface TreeNode {
  id: string;
  name: string;
  description: string;
  level: number;
  roleType: string;
  children: TreeNode[];
  permissions: string[];
  assignableRoles: string[];
  parentId?: string;
}

interface HierarchyTreeViewProps {
  hierarchy?: RoleHierarchyType;
  currentUserHierarchy: UserRoleHierarchy | null;
  onRoleCreate?: (parentId: string | null, roleData: any) => Promise<void>;
  onRoleUpdate?: (roleId: string, roleData: any) => Promise<void>;
  onRoleDelete?: (roleId: string) => Promise<void>;
  onRoleMove?: (roleId: string, newParentId: string | null) => Promise<void>;
}

const HierarchyTreeView: React.FC<HierarchyTreeViewProps> = ({
  hierarchy: externalHierarchy,
  currentUserHierarchy,
  onRoleCreate,
  onRoleUpdate,
  onRoleDelete,
  onRoleMove
}) => {
  const [hierarchy, setHierarchy] = useState<RoleHierarchyType>({});
  const [treeData, setTreeData] = useState<TreeNode[]>([]);
  const [expandedNodes, setExpandedNodes] = useState<Set<string>>(new Set());
  const [editingNode, setEditingNode] = useState<string | null>(null);
  const [creatingChild, setCreatingChild] = useState<string | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [draggedNode, setDraggedNode] = useState<string | null>(null);

  // Form state per editing/creating
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    roleType: '',
    permissions: [] as string[],
    level: 1
  });

  useEffect(() => {
    if (externalHierarchy && Object.keys(externalHierarchy).length > 0) {
      // Usa la gerarchia fornita come prop
      setHierarchy(externalHierarchy);
      const tree = buildTreeStructure(externalHierarchy);
      setTreeData(tree);

      // Espandi i primi livelli per default
      const initialExpanded = new Set<string>();
      tree.forEach(node => {
        if (node.level <= 2) {
          initialExpanded.add(node.id);
          expandChildrenRecursively(node, initialExpanded, 3);
        }
      });
      setExpandedNodes(initialExpanded);
      setLoading(false);
    } else {
      // Carica la gerarchia internamente se non fornita
      loadHierarchyData();
    }
  }, [externalHierarchy]);

  const loadHierarchyData = async () => {
    try {
      setLoading(true);
      setError(null);

      if (!isAuthenticated()) {
        setError('Accesso non autorizzato. Effettua il login per visualizzare la gerarchia dei ruoli.');
        return;
      }

      const hierarchyData = await getRoleHierarchy();

      // Controllo se i dati della gerarchia sono validi
      if (!hierarchyData) {
        setError('Nessun dato di gerarchia disponibile. Verifica la configurazione dei ruoli.');
        return;
      }

      setHierarchy(hierarchyData);

      // Converti i dati della gerarchia in struttura ad albero
      const tree = buildTreeStructure(hierarchyData);
      setTreeData(tree);

      // Espandi i primi livelli per default
      const initialExpanded = new Set<string>();
      tree.forEach(node => {
        if (node.level <= 2) {
          initialExpanded.add(node.id);
          expandChildrenRecursively(node, initialExpanded, 3);
        }
      });
      setExpandedNodes(initialExpanded);

    } catch (err: any) {
      console.error('Error loading hierarchy data:', err);
      setError('Errore nel caricamento della gerarchia dei ruoli. Riprova pi tardi.');
    } finally {
      setLoading(false);
    }
  };

  const expandChildrenRecursively = (node: TreeNode, expanded: Set<string>, maxLevel: number) => {
    if (node.level < maxLevel) {
      node.children.forEach(child => {
        expanded.add(child.id);
        expandChildrenRecursively(child, expanded, maxLevel);
      });
    }
  };

  const buildTreeStructure = (hierarchyData: RoleHierarchyType): TreeNode[] => {
    // Controllo se hierarchyData  valido
    if (!hierarchyData || typeof hierarchyData !== 'object') {
      console.warn('buildTreeStructure: hierarchyData is invalid:', hierarchyData);
      return [];
    }

    const nodes: { [key: string]: TreeNode } = {};
    const rootNodes: TreeNode[] = [];

    // Crea tutti i nodi
    Object.entries(hierarchyData).forEach(([roleType, data]) => {
      const node: TreeNode = {
        id: roleType,
        name: data.name,
        description: data.description,
        level: data.level,
        roleType: roleType,
        children: [],
        permissions: data.permissions || [],
        assignableRoles: data.assignableRoles || [],
        parentId: undefined
      };
      nodes[roleType] = node;
    });

    // Determina le relazioni parent-child basandosi sui livelli
    // Un nodo  figlio del nodo di livello immediatamente superiore pi vicino
    Object.values(nodes).forEach(node => {
      if (node.level === 0) {
        // Livello 0  sempre root
        rootNodes.push(node);
      } else {
        // Trova il parent con il livello immediatamente superiore
        const potentialParents = Object.values(nodes).filter(
          parent => parent.level === node.level - 1
        );

        if (potentialParents.length > 0) {
          // Se ci sono pi potenziali parent dello stesso livello, 
          // usa il primo o implementa una logica pi specifica
          const parent = potentialParents[0];
          node.parentId = parent.id;
          parent.children.push(node);
        } else {
          // Se non trova un parent del livello immediatamente superiore,
          // cerca il parent di livello pi alto disponibile
          const allPotentialParents = Object.values(nodes).filter(
            parent => parent.level < node.level
          );

          if (allPotentialParents.length > 0) {
            // Ordina per livello decrescente e prendi il primo
            allPotentialParents.sort((a, b) => b.level - a.level);
            const parent = allPotentialParents[0];
            node.parentId = parent.id;
            parent.children.push(node);
          } else {
            // Se non trova nessun parent,  un root node
            rootNodes.push(node);
          }
        }
      }
    });

    // Ordina per livello e nome
    const sortNodes = (nodeArray: TreeNode[]) => {
      nodeArray.sort((a, b) => {
        if (a.level !== b.level) return a.level - b.level;
        return a.name.localeCompare(b.name);
      });
      nodeArray.forEach(node => sortNodes(node.children));
    };

    sortNodes(rootNodes);
    return rootNodes;
  };

  const toggleNode = (nodeId: string) => {
    const newExpanded = new Set(expandedNodes);
    if (newExpanded.has(nodeId)) {
      newExpanded.delete(nodeId);
    } else {
      newExpanded.add(nodeId);
    }
    setExpandedNodes(newExpanded);
  };

  const canEditRole = (roleType: string): boolean => {
    if (!currentUserHierarchy) return false;
    // Se l'utente ha ALL_PERMISSIONS o  SUPER_ADMIN, pu modificare tutto
    if (currentUserHierarchy.assignablePermissions?.includes('ALL_PERMISSIONS') ||
        currentUserHierarchy.userRoles?.includes('SUPER_ADMIN')) {
      return true;
    }

    // I ruoli assegnabili potrebbero essere oggetti con propriet type/name
    return currentUserHierarchy.assignableRoles?.some((role: any) => {
      const roleTypeToCheck = typeof role === 'object' ? (role.type || role.name) : role;
      return roleTypeToCheck === roleType;
    }) || false;
  };

  const hasPermission = (permission: string): boolean => {
    console.log(' Checking permission:', permission);
    console.log(' currentUserHierarchy:', currentUserHierarchy);

    if (!currentUserHierarchy) {
      console.log(' No currentUserHierarchy found');
      return false;
    }

    // Se l'utente ha ALL_PERMISSIONS o  SUPER_ADMIN, ha tutti i permessi
    if (currentUserHierarchy.assignablePermissions?.includes('ALL_PERMISSIONS') ||
        currentUserHierarchy.userRoles?.includes('SUPER_ADMIN')) {
      console.log(' User has ALL_PERMISSIONS or SUPER_ADMIN');
      return true;
    }

    const hasPermissionResult = currentUserHierarchy.assignablePermissions?.includes(permission) || false;
    console.log(` Permission ${permission} result:`, hasPermissionResult);
    console.log(' Available permissions:', currentUserHierarchy.assignablePermissions);

    return hasPermissionResult;
  };

  const startEditing = (node: TreeNode) => {
    if (!canEditRole(node.roleType)) return;

    setEditingNode(node.id);
    setFormData({
      name: node.name,
      description: node.description,
      roleType: node.roleType,
      permissions: node.permissions,
      level: node.level
    });
  };

  const startCreating = (parentId: string | null) => {
    if (!hasPermission('CREATE_ROLES')) return;

    const parentNode = parentId ? findNodeById(treeData, parentId) : null;
    const newLevel = parentNode ? parentNode.level + 1 : 1;

    setCreatingChild(parentId);
    setFormData({
      name: '',
      description: '',
      roleType: '',
      permissions: [],
      level: newLevel
    });
  };

  const findNodeById = (nodes: TreeNode[], id: string): TreeNode | null => {
    for (const node of nodes) {
      if (node.id === id) return node;
      const found = findNodeById(node.children, id);
      if (found) return found;
    }
    return null;
  };

  const saveRole = async () => {
    try {
      if (editingNode) {
        // Modifica ruolo esistente
        if (onRoleUpdate) {
          await onRoleUpdate(editingNode, formData);
        }
      } else if (creatingChild !== null) {
        // Crea nuovo ruolo
        if (onRoleCreate) {
          await onRoleCreate(creatingChild, formData);
        }
      }

      // Ricarica i dati
      await loadHierarchyData();
      cancelEditing();
    } catch (error) {
      console.error('Error saving role:', error);
    }
  };

  const deleteRole = async (nodeId: string) => {
    if (!canEditRole(nodeId) || !hasPermission('DELETE_ROLES')) return;

    if (window.confirm('Sei sicuro di voler eliminare questo ruolo? Questa azione non pu essere annullata.')) {
      try {
        if (onRoleDelete) {
          await onRoleDelete(nodeId);
        }
        await loadHierarchyData();
      } catch (error) {
        console.error('Error deleting role:', error);
      }
    }
  };

  const cancelEditing = () => {
    setEditingNode(null);
    setCreatingChild(null);
    setFormData({
      name: '',
      description: '',
      roleType: '',
      permissions: [],
      level: 1
    });
  };

  const handleDragStart = (nodeId: string) => {
    setDraggedNode(nodeId);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const handleDrop = async (e: React.DragEvent, targetNodeId: string | null) => {
    e.preventDefault();

    if (!draggedNode || !hasPermission('EDIT_HIERARCHY')) return;

    try {
      if (onRoleMove) {
        await onRoleMove(draggedNode, targetNodeId);
      }
      await loadHierarchyData();
    } catch (error) {
      console.error('Error moving role:', error);
    } finally {
      setDraggedNode(null);
    }
  };

  const getRoleIcon = (level: number, roleType: string) => {
    if (roleType.includes('SUPER_ADMIN')) return <Crown className="w-4 h-4 text-purple-600" />;
    if (roleType.includes('ADMIN')) return <Star className="w-4 h-4 text-red-600" />;
    if (roleType.includes('MANAGER')) return <Award className="w-4 h-4 text-orange-600" />;
    if (roleType.includes('TRAINER')) return <UserCheck className="w-4 h-4 text-blue-600" />;
    if (level <= 2) return <Building className="w-4 h-4 text-indigo-600" />;
    return <Users className="w-4 h-4 text-green-600" />;
  };

  const renderTreeNode = (node: TreeNode, depth: number = 0): React.ReactNode => {
    const isExpanded = expandedNodes.has(node.id);
    const isEditing = editingNode === node.id;
    const isCreating = creatingChild === node.id;
    const hasChildren = node.children.length > 0;
    const canEdit = canEditRole(node.roleType);
    const canCreate = hasPermission('CREATE_ROLES');
    const canDelete = canEditRole(node.roleType) && hasPermission('DELETE_ROLES');

    // Debug log per capire perch i pulsanti sono disabilitati
    console.log(` Debug pulsanti per nodo ${node.name} (${node.roleType}):`);
    console.log(`  - canEdit: ${canEdit}`);
    console.log(`  - canCreate: ${canCreate}`);
    console.log(`  - canDelete: ${canDelete}`);
    console.log(`  - hasChildren: ${hasChildren}`);
    console.log(`  - currentUserHierarchy:`, currentUserHierarchy);

    return (
      <div key={node.id} className="select-none">
        {/* Nodo principale */}
        <div
          className={`flex items-center py-2 px-3 rounded-lg hover:bg-gray-50 transition-colors ${
            draggedNode === node.id ? 'opacity-50' : ''
          }`}
          style={{ marginLeft: `${depth * 24}px` }}
          draggable={canEdit}
          onDragStart={() => handleDragStart(node.id)}
          onDragOver={handleDragOver}
          onDrop={(e) => handleDrop(e, node.id)}
        >
          {/* Icona espansione */}
          <div className="w-6 h-6 flex items-center justify-center">
            {hasChildren && (
              <button
                onClick={() => toggleNode(node.id)}
                className="p-1 hover:bg-gray-200 rounded"
              >
                {isExpanded ? (
                  <ChevronDown className="w-4 h-4 text-gray-600" />
                ) : (
                  <ChevronRight className="w-4 h-4 text-gray-600" />
                )}
              </button>
            )}
          </div>

          {/* Icona ruolo */}
          <div className="mr-3">
            {getRoleIcon(node.level, node.roleType)}
          </div>

          {/* Contenuto nodo */}
          <div className="flex-1 min-w-0">
            {isEditing ? (
              <div className="space-y-2">
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  className="w-full px-2 py-1 border border-gray-300 rounded text-sm"
                  placeholder="Nome ruolo"
                />
                <input
                  type="text"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  className="w-full px-2 py-1 border border-gray-300 rounded text-sm"
                  placeholder="Descrizione"
                />
              </div>
            ) : (
              <div>
                <div className="font-medium text-gray-900 truncate">{node.name}</div>
                <div className="text-sm text-gray-500 truncate">{node.description}</div>
                <div className="text-xs text-gray-400">
                  Livello {node.level}  {node.permissions.length} permessi
                </div>
              </div>
            )}
          </div>

          {/* Azioni - Stile uguale alla visualizzazione a lista */}
          <div className="flex items-center space-x-0.5 ml-4">
            {isEditing ? (
              <>
                <button
                  onClick={saveRole}
                  className="p-1 text-green-600 hover:bg-green-100 rounded transition-colors"
                  title="Salva"
                >
                  <Save className="w-3 h-3" />
                </button>
                <button
                  onClick={cancelEditing}
                  className="p-1 text-gray-600 hover:bg-gray-100 rounded transition-colors"
                  title="Annulla"
                >
                  <X className="w-3 h-3" />
                </button>
              </>
            ) : (
              <>
                {/* Pulsante Aggiungi sotto-ruolo */}
                <button
                  onClick={canCreate ? () => startCreating(node.id) : undefined}
                  disabled={!canCreate}
                  className={`p-1 transition-colors rounded bg-transparent border-0 shadow-none ${
                    canCreate 
                      ? 'text-green-600 hover:bg-green-100 cursor-pointer' 
                      : 'text-gray-400 opacity-50 cursor-not-allowed'
                  }`}
                  title={canCreate ? "Aggiungi sotto-ruolo" : "Non hai permessi per creare ruoli"}
                >
                  <Plus className="w-3 h-3" />
                </button>

                {/* Pulsante Modifica */}
                <button
                  onClick={canEdit ? () => startEditing(node) : undefined}
                  disabled={!canEdit}
                  className={`p-1 transition-colors rounded bg-transparent border-0 shadow-none ${
                    canEdit 
                      ? 'text-blue-600 hover:bg-blue-100 cursor-pointer' 
                      : 'text-gray-400 opacity-50 cursor-not-allowed'
                  }`}
                  title={canEdit ? "Modifica" : "Non hai permessi per modificare questo ruolo"}
                >
                  <Edit3 className="w-3 h-3" />
                </button>

                {/* Pulsante Elimina */}
                <button
                  onClick={(canDelete && node.children.length === 0) ? () => deleteRole(node.id) : undefined}
                  disabled={!(canDelete && node.children.length === 0)}
                  className={`p-1 transition-colors rounded bg-transparent border-0 shadow-none ${
                    canDelete && node.children.length === 0
                      ? 'text-red-600 hover:bg-red-100 cursor-pointer' 
                      : 'text-gray-400 opacity-50 cursor-not-allowed'
                  }`}
                  title={
                    !canDelete 
                      ? "Non hai permessi per eliminare questo ruolo" 
                      : node.children.length > 0 
                        ? "Non puoi eliminare un ruolo con sotto-ruoli" 
                        : "Elimina"
                  }
                >
                  <Trash2 className="w-3 h-3" />
                </button>

                {/* Pulsante Trascina */}
                <button 
                  disabled={!canEdit}
                  className={`p-1 transition-colors rounded bg-transparent border-0 shadow-none ${
                    canEdit 
                      ? 'text-amber-600 hover:bg-amber-100 cursor-move' 
                      : 'text-gray-400 opacity-50 cursor-not-allowed'
                  }`} 
                  title={canEdit ? "Trascina per riordinare" : "Non hai permessi per riordinare"}
                >
                  <Move className="w-3 h-3" />
                </button>
              </>
            )}
          </div>
        </div>

        {/* Form per creare nuovo figlio */}
        {isCreating && (
          <div
            className="bg-blue-50 border border-blue-200 rounded-lg p-3 mt-2"
            style={{ marginLeft: `${(depth + 1) * 24}px` }}
          >
            <div className="space-y-2">
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                className="w-full px-2 py-1 border border-gray-300 rounded text-sm"
                placeholder="Nome nuovo ruolo"
              />
              <input
                type="text"
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                className="w-full px-2 py-1 border border-gray-300 rounded text-sm"
                placeholder="Descrizione"
              />
              <input
                type="text"
                value={formData.roleType}
                onChange={(e) => setFormData({ ...formData, roleType: e.target.value })}
                className="w-full px-2 py-1 border border-gray-300 rounded text-sm"
                placeholder="Tipo ruolo (es: ADMIN_LAVORO_FORMAZIONE)"
              />
              <div className="flex space-x-2">
                <button
                  onClick={saveRole}
                  className="px-3 py-1 bg-blue-600 text-white rounded text-sm hover:bg-blue-700"
                >
                  Crea
                </button>
                <button
                  onClick={cancelEditing}
                  className="px-3 py-1 bg-gray-300 text-gray-700 rounded text-sm hover:bg-gray-400"
                >
                  Annulla
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Figli */}
        {isExpanded && hasChildren && (
          <div>
            {node.children.map(child => renderTreeNode(child, depth + 1))}
          </div>
        )}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="animate-spin rounded-full h-8 w-8 border-4 border-blue-600 border-t-transparent"></div>
        <span className="ml-3 text-gray-600">Caricamento gerarchia...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <div className="text-red-600 mb-4"></div>
        <h3 className="text-lg font-medium text-gray-900 mb-2">Errore</h3>
        <p className="text-gray-600 mb-4">{error}</p>
        <button
          onClick={loadHierarchyData}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
        >
          Riprova
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Header con azioni globali */}
      <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
        <div className="flex items-center space-x-3">
          <Shield className="w-5 h-5 text-blue-600" />
          <h3 className="font-semibold text-gray-900">Vista ad Albero della Gerarchia</h3>
        </div>
        <div className="flex items-center space-x-2">
          {hasPermission('CREATE_ROLES') && (
            <button
              onClick={() => startCreating(null)}
              className="px-3 py-1.5 bg-blue-600 text-white rounded-full hover:bg-blue-700 flex items-center space-x-1.5 transition-colors text-sm"
            >
              <Plus className="w-3 h-3" />
              <span>Nuovo Ruolo Radice</span>
            </button>
          )}
          <button
            onClick={loadHierarchyData}
            className="px-3 py-1.5 bg-gray-200 text-gray-700 rounded-full hover:bg-gray-300 flex items-center space-x-1.5 transition-colors text-sm"
          >
            <Settings className="w-3 h-3" />
            <span>Aggiorna</span>
          </button>
        </div>
      </div>

      {/* Contenuto principale */}
      <div className="bg-white border border-gray-200 rounded-lg">
        <div className="p-4">
          {/* Form per nuovo ruolo radice */}
          {creatingChild === null && editingNode === null && formData.name !== '' && (
            <div className="bg-green-50 border border-green-200 rounded-lg p-3 mb-4">
              <div className="space-y-2">
                <input
                  type="text"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  className="w-full px-2 py-1 border border-gray-300 rounded text-sm"
                  placeholder="Nome nuovo ruolo radice"
                />
                <input
                  type="text"
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  className="w-full px-2 py-1 border border-gray-300 rounded text-sm"
                  placeholder="Descrizione"
                />
                <input
                  type="text"
                  value={formData.roleType}
                  onChange={(e) => setFormData({ ...formData, roleType: e.target.value })}
                  className="w-full px-2 py-1 border border-gray-300 rounded text-sm"
                  placeholder="Tipo ruolo (es: ADMIN_LAVORO_FORMAZIONE)"
                />
                <div className="flex space-x-2">
                  <button
                    onClick={saveRole}
                    className="px-3 py-1 bg-green-600 text-white rounded text-sm hover:bg-green-700"
                  >
                    Crea Ruolo Radice
                  </button>
                  <button
                    onClick={cancelEditing}
                    className="px-3 py-1 bg-gray-300 text-gray-700 rounded text-sm hover:bg-gray-400"
                  >
                    Annulla
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Albero della gerarchia */}
          <div 
            className="space-y-1"
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, null)}
          >
            {treeData.map(node => renderTreeNode(node))}
          </div>

          {treeData.length === 0 && (
            <div className="text-center py-8 text-gray-500">
              <Shield className="w-12 h-12 mx-auto mb-4 text-gray-300" />
              <p>Nessun ruolo trovato nella gerarchia.</p>
              {hasPermission('CREATE_ROLES') && (
                <button
                  onClick={() => startCreating(null)}
                  className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                >
                  Crea il primo ruolo
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default HierarchyTreeView;

import React, { useState, useEffect } from 'react';
import { Modal } from '../../design-system/molecules/Modal/Modal';
import { Button } from '../../design-system/atoms/Button/Button';
import { Badge } from '../../design-system/atoms/Badge/Badge';
import { Label } from '../../design-system/atoms/Label/Label';
import { Alert, AlertDescription } from '../ui/alert';
import { 
  AlertCircle,
  ChevronDown,
  ChevronRight,
  Loader2s,
  MoveVertical,
  Users
} from 'lucide-react';
import { moveRoleInHierarchy } from '../../services/roles';

interface MoveRoleModalProps {
  isOpen: boolean;
  onClose: () => void;
  onMove: (targetLevel: number, parentRoleType?: string) => Promise<void>;
  role: any;
  hierarchy: Record<string, any>;
  currentLevel: number;
}

const MoveRoleModal: React.FC<MoveRoleModalProps> = ({
  isOpen,
  onClose,
  onMove,
  role,
  hierarchy,
  currentLevel
}) => {
  const [selectedLevel, setSelectedLevel] = useState<number | null>(null);
  const [selectedParentRole, setSelectedParentRole] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [expandedLevels, setExpandedLevels] = useState<Set<number>>(new Set());

  // Reset lo stato quando si apre il modal
  useEffect(() => {
    if (isOpen) {
      setSelectedLevel(null);
      setSelectedParentRole(null);
      setError(null);
      setExpandedLevels(new Set());
    }
  }, [isOpen]);

  const handleMove = async () => {
    if (selectedLevel === null) {
      setError('Seleziona un livello di destinazione');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      // Usa il nuovo servizio per spostare il ruolo
      await moveRoleInHierarchy(role.roleType || role.type, selectedLevel, selectedParentRole || undefined);

      // Chiama anche la callback originale per compatibilit
      await onMove(selectedLevel, selectedParentRole || undefined);
      onClose();
    } catch (error: any) {
      console.error('Error moving role:', error);
      setError(error.message || 'Errore nello spostamento del ruolo');
    } finally {
      setLoading(false);
    }
  };

  // Prepara i livelli disponibili per lo spostamento
  const availableLevels = Object.keys(hierarchy)
    .map(roleType => {
      const roleData = hierarchy[roleType];
      return roleData?.level;
    })
    .filter((level): level is number => typeof level === 'number' && level !== currentLevel)
    .filter((level, index, array) => array.indexOf(level) === index) // Rimuovi duplicati
    .sort((a, b) => a - b);

  // Ottieni i ruoli disponibili come genitori per il livello selezionato
  const getAvailableParentRoles = (targetLevel: number) => {
    return Object.entries(hierarchy)
      .filter(([_, roleData]) => roleData?.level === targetLevel - 1) // Genitori sono al livello precedente
      .map(([roleType, roleData]) => ({ roleType, ...roleData }));
  };

  const toggleLevelExpansion = (level: number) => {
    const newExpanded = new Set(expandedLevels);
    if (newExpanded.has(level)) {
      newExpanded.delete(level);
    } else {
      newExpanded.add(level);
    }
    setExpandedLevels(newExpanded);
  };

  if (!role) return null;

  const modalFooter = (
    <>
      <Button
        variant="outline"
        onClick={onClose}
        disabled={loading}
      >
        Annulla
      </Button>
      <Button
        onClick={handleMove}
        disabled={loading || selectedLevel === null || availableLevels.length === 0}
      >
        {loading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
        <MoveVertical className="mr-2 h-4 w-4" />
        Sposta Ruolo
      </Button>
    </>
  );

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      title="Sposta Ruolo nella Gerarchia"
      size="md"
      className="z-50"
      footer={modalFooter}
    >
      <div className="space-y-6">
        <div className="flex items-center space-x-3 p-4 bg-blue-50 rounded-lg">
          <MoveVertical className="h-6 w-6 text-blue-600 flex-shrink-0" />
          <div>
            <h3 className="font-medium text-blue-900">Modifica Posizione Gerarchica</h3>
            <p className="text-sm text-blue-700 mt-1">
              Seleziona il nuovo livello gerarchico e, opzionalmente, il ruolo genitore per "{role?.name}".
            </p>
          </div>
        </div>

        {role && (
          <div className="space-y-4">
            <div className="p-3 bg-gray-50 rounded-lg">
              <div className="flex items-center justify-between">
                <span className="font-medium">{role.name}</span>
                <Badge variant="outline">Livello {currentLevel}</Badge>
              </div>
              {role.description && (
                <p className="text-sm text-gray-600 mt-1">{role.description}</p>
              )}
            </div>

            {error && (
              <Alert variant="destructive">
                <AlertCircle className="h-4 w-4" />
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}

            <div className="space-y-4">
              <Label className="text-base font-medium">Nuovo Livello Gerarchico</Label>

              <div className="space-y-3 max-h-80 overflow-y-auto border rounded-lg p-4">
                {availableLevels.length > 0 ? (
                  availableLevels.map((level) => {
                    // Trova tutti i ruoli a questo livello
                    const rolesAtLevel = Object.entries(hierarchy)
                      .filter(([_, roleData]) => roleData?.level === level)
                      .map(([roleType, roleData]) => ({ roleType, ...roleData }));

                    const isHigher = level < currentLevel;
                    const isExpanded = expandedLevels.has(level);
                    const availableParents = getAvailableParentRoles(level);

                    return (
                      <div key={level} className="space-y-3 border-b pb-3 last:border-b-0">
                        <div className="flex items-center space-x-3">
                          <input
                            type="radio"
                            name="newLevel"
                            value={level.toString()}
                            checked={selectedLevel === level}
                            onChange={() => {
                              setSelectedLevel(level);
                              setSelectedParentRole(null); // Reset parent selection
                              if (!isExpanded) {
                                toggleLevelExpansion(level);
                              }
                            }}
                            className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300"
                          />
                          <Label className="flex-1 cursor-pointer">
                            <div className="flex items-center justify-between">
                              <span className="font-medium">Livello {level}</span>
                              <div className="flex items-center space-x-2">
                                <Badge variant={isHigher ? "default" : "secondary"}>
                                  {isHigher ? "Superiore" : "Inferiore"}
                                </Badge>
                                <Badge variant="outline" className="flex items-center space-x-1">
                                  <Users className="h-3 w-3" />
                                  <span>{rolesAtLevel.length}</span>
                                </Badge>
                              </div>
                            </div>
                            <p className="text-xs text-gray-500 mt-1">
                              {level < currentLevel ? 'Promozione' : level > currentLevel ? 'Retrocessione' : 'Nessun cambiamento'}
                            </p>
                          </Label>
                          {selectedLevel === level && (
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => toggleLevelExpansion(level)}
                              className="p-1"
                            >
                              {isExpanded ? <ChevronDown className="h-4 w-4" /> : <ChevronRight className="h-4 w-4" />}
                            </Button>
                          )}
                        </div>

                        {selectedLevel === level && isExpanded && (
                          <div className="ml-6 space-y-3">
                            {/* Selezione del genitore */}
                            {availableParents.length > 0 && (
                              <div className="space-y-2">
                                <Label className="text-sm font-medium text-gray-700">
                                  Seleziona Ruolo Genitore (Livello {level - 1})
                                </Label>
                                <div className="space-y-2">
                                  <div className="flex items-center space-x-2">
                                    <input
                                      type="radio"
                                      name="parentRole"
                                      value=""
                                      checked={selectedParentRole === null}
                                      onChange={() => setSelectedParentRole(null)}
                                      className="h-3 w-3 text-blue-600 focus:ring-blue-500 border-gray-300"
                                    />
                                    <Label className="text-sm text-gray-600">Nessun genitore specifico</Label>
                                  </div>
                                  {availableParents.map((parentRole) => (
                                    <div key={parentRole.roleType} className="flex items-center space-x-2">
                                      <input
                                        type="radio"
                                        name="parentRole"
                                        value={parentRole.roleType}
                                        checked={selectedParentRole === parentRole.roleType}
                                        onChange={() => setSelectedParentRole(parentRole.roleType)}
                                        className="h-3 w-3 text-blue-600 focus:ring-blue-500 border-gray-300"
                                      />
                                      <Label className="text-sm">
                                        <Badge variant="outline" className="mr-2">
                                          {parentRole.name || parentRole.roleType}
                                        </Badge>
                                        <span className="text-gray-600">{parentRole.description}</span>
                                      </Label>
                                    </div>
                                  ))}
                                </div>
                              </div>
                            )}

                            {/* Ruoli esistenti a questo livello */}
                            {rolesAtLevel.length > 0 && (
                              <div className="space-y-2">
                                <Label className="text-sm font-medium text-gray-700">
                                  Ruoli esistenti a questo livello:
                                </Label>
                                <div className="flex flex-wrap gap-2">
                                  {rolesAtLevel.map((roleData, index) => (
                                    <Badge 
                                      key={`${level}-${roleData.roleType || index}`} 
                                      variant="outline" 
                                      className="text-xs"
                                    >
                                      {roleData.name || roleData.roleType}
                                    </Badge>
                                  ))}
                                </div>
                              </div>
                            )}
                          </div>
                        )}
                      </div>
                    );
                  })
                ) : (
                  <div className="py-8 text-center text-gray-500">
                    <MoveVertical className="h-8 w-8 mx-auto mb-2 text-gray-400" />
                    <p>Non ci sono altri livelli disponibili nella gerarchia</p>
                  </div>
                )}
              </div>
            </div>

            {selectedLevel !== null && selectedLevel !== currentLevel && (
              <Alert>
                <AlertCircle className="h-4 w-4" />
                <AlertDescription>
                  <strong>Nota:</strong> Spostare il ruolo dal livello {currentLevel} al livello {selectedLevel} 
                  {selectedLevel < currentLevel ? ' aumenter' : ' diminuir'} la sua autorit nella gerarchia.
                  {selectedParentRole && (
                    <span className="block mt-1">
                      Il ruolo sar subordinato a: <strong>{hierarchy[selectedParentRole]?.name || selectedParentRole}</strong>
                    </span>
                  )}
                  <span className="block mt-1 text-sm text-gray-600">
                    Questo potrebbe influire sui permessi e sulle relazioni gerarchiche esistenti.
                  </span>
                </AlertDescription>
              </Alert>
            )}
          </div>
        )}
      </div>
    </Modal>
  );
};

export default MoveRoleModal;

import React, { useState, useEffect } from 'react';
import { 
  Shield, Check, X, AlertCircle, 
  ChevronDown, ChevronUp, Filter, Search 
} from 'lucide-react';
import { getAssignableRolesAndPermissions, assignPermissionsWithHierarchy } from '../../services/roles';
import type { AssignableRolesAndPermissions } from '../../services/roles';

interface PermissionAssignmentProps {
  targetUserId: string;
  targetUserName: string;
  targetUserRole: string;
  onClose: () => void;
  onSuccess?: () => void;
}

const PermissionAssignment: React.FC<PermissionAssignmentProps> = ({
  targetUserId,
  targetUserName,
  targetUserRole,
  onClose,
  onSuccess
}) => {
  const [assignableData, setAssignableData] = useState<AssignableRolesAndPermissions | null>(null);
  const [selectedPermissions, setSelectedPermissions] = useState<Set<string>>(new Set());
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [expandedCategories, setExpandedCategories] = useState<Set<string>>(new Set());
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadAssignableData();
  }, [targetUserRole]);

  const loadAssignableData = async () => {
    try {
      setLoading(true);
      setError(null);

      const data = await getAssignableRolesAndPermissions(targetUserRole);
      setAssignableData(data);

      // Espandi automaticamente le categorie che hanno permessi
      const categories = new Set<string>();
      data.assignablePermissions.forEach(permission => {
        const category = getPermissionCategory(permission);
        categories.add(category);
      });
      setExpandedCategories(categories);

    } catch (err) {
      console.error('Error loading assignable data:', err);
      setError('Errore nel caricamento dei permessi assegnabili');
    } finally {
      setLoading(false);
    }
  };

  const getPermissionCategory = (permission: string): string => {
    if (permission.includes('ROLE')) return 'roles';
    if (permission.includes('USER')) return 'users';
    if (permission.includes('COMPAN')) return 'companies';
    if (permission.includes('COURSE')) return 'courses';
    if (permission.includes('DOCUMENT')) return 'documents';
    if (permission.includes('REPORT')) return 'reports';
    return 'other';
  };

  const getCategoryDisplayName = (category: string): string => {
    const names = {
      roles: 'Gestione Ruoli',
      users: 'Gestione Utenti',
      companies: 'Gestione Aziende',
      courses: 'Gestione Corsi',
      documents: 'Gestione Documenti',
      reports: 'Report e Analytics',
      other: 'Altri Permessi'
    };
    return names[category as keyof typeof names] || category;
  };

  const getPermissionDisplayName = (permission: string): string => {
    const names = {
      'ROLE_MANAGEMENT': 'Gestione Ruoli Completa',
      'VIEW_ROLES': 'Visualizza Ruoli',
      'CREATE_ROLES': 'Crea Ruoli',
      'EDIT_ROLES': 'Modifica Ruoli',
      'DELETE_ROLES': 'Elimina Ruoli',
      'USER_MANAGEMENT': 'Gestione Utenti Completa',
      'VIEW_USERS': 'Visualizza Utenti',
      'CREATE_USERS': 'Crea Utenti',
      'EDIT_USERS': 'Modifica Utenti',
      'DELETE_USERS': 'Elimina Utenti',
      'VIEW_COMPANIES': 'Visualizza Aziende',
      'CREATE_COMPANIES': 'Crea Aziende',
      'EDIT_COMPANIES': 'Modifica Aziende',
      'DELETE_COMPANIES': 'Elimina Aziende',
      'VIEW_COURSES': 'Visualizza Corsi',
      'CREATE_COURSES': 'Crea Corsi',
      'EDIT_COURSES': 'Modifica Corsi',
      'DELETE_COURSES': 'Elimina Corsi',
      'VIEW_DOCUMENTS': 'Visualizza Documenti',
      'CREATE_DOCUMENTS': 'Crea Documenti',
      'EDIT_DOCUMENTS': 'Modifica Documenti',
      'DELETE_DOCUMENTS': 'Elimina Documenti',
      'VIEW_REPORTS': 'Visualizza Report',
      'CREATE_REPORTS': 'Crea Report'
    };
    return names[permission as keyof typeof names] || permission;
  };

  const togglePermission = (permission: string) => {
    const newSelected = new Set(selectedPermissions);
    if (newSelected.has(permission)) {
      newSelected.delete(permission);
    } else {
      newSelected.add(permission);
    }
    setSelectedPermissions(newSelected);
  };

  const toggleCategory = (category: string) => {
    const newExpanded = new Set(expandedCategories);
    if (newExpanded.has(category)) {
      newExpanded.delete(category);
    } else {
      newExpanded.add(category);
    }
    setExpandedCategories(newExpanded);
  };

  const handleAssignPermissions = async () => {
    if (selectedPermissions.size === 0) {
      setError('Seleziona almeno un permesso da assegnare');
      return;
    }

    try {
      setSaving(true);
      setError(null);

      await assignPermissionsWithHierarchy(targetUserId, Array.from(selectedPermissions));

      onSuccess?.();
      onClose();
    } catch (err: any) {
      console.error('Error assigning permissions:', err);
      setError(err.message || 'Errore nell\'assegnazione dei permessi');
    } finally {
      setSaving(false);
    }
  };

  const getFilteredPermissions = () => {
    if (!assignableData) return {};

    const permissionsByCategory: { [category: string]: string[] } = {};

    assignableData.assignablePermissions.forEach(permission => {
      const category = getPermissionCategory(permission);

      // Filtro per categoria
      if (selectedCategory !== 'all' && category !== selectedCategory) return;

      // Filtro per ricerca
      if (searchTerm && !getPermissionDisplayName(permission).toLowerCase().includes(searchTerm.toLowerCase())) {
        return;
      }

      if (!permissionsByCategory[category]) {
        permissionsByCategory[category] = [];
      }
      permissionsByCategory[category].push(permission);
    });

    return permissionsByCategory;
  };

  const categories = assignableData ? 
    Array.from(new Set(assignableData.assignablePermissions.map(getPermissionCategory))) : [];

  if (loading) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4">
          <div className="flex items-center justify-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
            <span className="ml-2">Caricamento permessi...</span>
          </div>
        </div>
      </div>
    );
  }

  if (error && !assignableData) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold text-gray-900">Assegnazione Permessi</h2>
            <button type="button" onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <X className="h-6 w-6" />
            </button>
          </div>
          <div className="text-center py-8">
            <div className="text-red-600 mb-2"> Errore</div>
            <p className="text-gray-600">{error}</p>
            <button
              type="button"
              onClick={loadAssignableData}
              className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
            >
              Riprova
            </button>
          </div>
        </div>
      </div>
    );
  }

  const filteredPermissions = getFilteredPermissions();

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-4xl w-full mx-4 max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center">
            <Shield className="h-6 w-6 text-blue-600 mr-2" />
            <div>
              <h2 className="text-xl font-semibold text-gray-900">Assegnazione Permessi</h2>
              <p className="text-sm text-gray-600">
                Utente: <span className="font-medium">{targetUserName}</span> ({targetUserRole})
              </p>
            </div>
          </div>
          <button type="button" onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X className="h-6 w-6" />
          </button>
        </div>

        {error && (
          <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg">
            <div className="flex items-center">
              <AlertCircle className="h-5 w-5 text-red-600 mr-2" />
              <span className="text-red-800">{error}</span>
            </div>
          </div>
        )}

        {/* Filtri */}
        <div className="mb-6 space-y-4">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <input
                  type="text"
                  placeholder="Cerca permessi..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
            </div>
            <div className="sm:w-48">
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="all">Tutte le categorie</option>
                {categories.map(category => (
                  <option key={category} value={category}>
                    {getCategoryDisplayName(category)}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Informazioni sui permessi assegnabili */}
        {assignableData && (
          <div className="mb-6 p-4 bg-blue-50 rounded-lg border border-blue-200">
            <h3 className="font-medium text-blue-900 mb-2">Permessi disponibili per l'assegnazione</h3>
            <div className="text-sm text-blue-800">
              Puoi assegnare <span className="font-medium">{assignableData.assignablePermissions.length}</span> permessi
              a questo utente in base alla gerarchia dei ruoli.
            </div>
          </div>
        )}

        {/* Lista permessi per categoria */}
        <div className="space-y-4 mb-6">
          {Object.entries(filteredPermissions).map(([category, permissions]) => (
            <div key={category} className="border border-gray-200 rounded-lg">
              <button
                type="button"
                onClick={() => toggleCategory(category)}
                className="w-full p-4 text-left flex items-center justify-between bg-gray-50 hover:bg-gray-100 rounded-t-lg"
              >
                <div className="flex items-center">
                  <Filter className="h-5 w-5 text-gray-600 mr-2" />
                  <span className="font-medium text-gray-900">
                    {getCategoryDisplayName(category)}
                  </span>
                  <span className="ml-2 text-sm text-gray-600">
                    ({permissions.length} permess{permissions.length !== 1 ? 'i' : 'o'})
                  </span>
                </div>
                {expandedCategories.has(category) ? (
                  <ChevronUp className="h-5 w-5 text-gray-600" />
                ) : (
                  <ChevronDown className="h-5 w-5 text-gray-600" />
                )}
              </button>

              {expandedCategories.has(category) && (
                <div className="p-4 border-t border-gray-200">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    {permissions.map(permission => (
                      <div
                        key={permission}
                        className={`p-3 border rounded-lg cursor-pointer transition-colors ${
                          selectedPermissions.has(permission)
                            ? 'border-blue-500 bg-blue-50'
                            : 'border-gray-200 hover:border-gray-300 hover:bg-gray-50'
                        }`}
                        onClick={() => togglePermission(permission)}
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex-1">
                            <div className="flex items-center">
                              <div className={`w-4 h-4 rounded border-2 mr-3 flex items-center justify-center ${
                                selectedPermissions.has(permission)
                                  ? 'border-blue-500 bg-blue-500'
                                  : 'border-gray-300'
                              }`}>
                                {selectedPermissions.has(permission) && (
                                  <Check className="h-3 w-3 text-white" />
                                )}
                              </div>
                              <span className="font-medium text-gray-900">
                                {getPermissionDisplayName(permission)}
                              </span>
                            </div>
                            <p className="text-sm text-gray-600 mt-1 ml-7">
                              {permission}
                            </p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>

        {Object.keys(filteredPermissions).length === 0 && (
          <div className="text-center py-8">
            <div className="text-gray-400 mb-2">
              <Filter className="h-12 w-12 mx-auto" />
            </div>
            <p className="text-gray-600">
              {searchTerm || selectedCategory !== 'all' 
                ? 'Nessun permesso trovato con i filtri applicati'
                : 'Nessun permesso disponibile per l\'assegnazione'
              }
            </p>
          </div>
        )}

        {/* Footer con azioni */}
        <div className="flex items-center justify-between pt-6 border-t border-gray-200">
          <div className="text-sm text-gray-600">
            {selectedPermissions.size > 0 && (
              <span>
                {selectedPermissions.size} permess{selectedPermissions.size !== 1 ? 'i' : 'o'} selezionat{selectedPermissions.size !== 1 ? 'i' : 'o'}
              </span>
            )}
          </div>
          <div className="flex space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200"
              disabled={saving}
            >
              Annulla
            </button>
            <button
              type="button"
              onClick={handleAssignPermissions}
              disabled={selectedPermissions.size === 0 || saving}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
            >
              {saving ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Assegnazione...
                </>
              ) : (
                'Assegna Permessi'
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PermissionAssignment;

import React from 'react';
import { 
  Building2,
  Edit,
  Eye,
  FileText,
  Layers,
  Plus,
  Target,
  Trash2
} from 'lucide-react';
import { Role } from '../../hooks/useRoles';
import { RolePermission } from '../../hooks/usePermissions';
import { Tenant } from '../../hooks/useTenants';

interface PermissionsGridProps {
  selectedRole: Role | null;
  entities: Array<{ key: string; label: string; icon: React.ComponentType<any> }>;
  rolePermissions: Record<string, RolePermission>;
  tenants: Tenant[];
  tenantsLoading: boolean;
  fieldPermissions: Record<string, Array<{ key: string; label: string }>>;
  onPermissionChange: (permissionId: string, granted: boolean, scope?: string) => void;
  onTenantChange: (permissionId: string, tenantId: number, selected: boolean) => void;
  onFieldRestrictionChange: (permissionId: string, field: string, restricted: boolean) => void;
  getPermissionKey: (entity: string, action: string) => string;
  getPermissionsByEntity: (entity: string) => Array<{ id: string; name: string; description: string; category: string; entity: string; action: string }>;
  translatePermissionAction: (action: string) => string;
}

export const PermissionsGrid: React.FC<PermissionsGridProps> = ({
  selectedRole,
  entities,
  rolePermissions,
  tenants,
  tenantsLoading,
  fieldPermissions,
  onPermissionChange,
  onTenantChange,
  onFieldRestrictionChange,
  getPermissionKey,
  getPermissionsByEntity,
  translatePermissionAction
}) => {
  if (!selectedRole) {
    return (
      <div className="flex items-center justify-center h-full bg-gradient-to-br from-gray-50 to-blue-50">
        <div className="text-center p-8">
          <div className="p-6 bg-gradient-to-br from-blue-600 to-purple-600 rounded-2xl shadow-lg mb-6 inline-block">
            <Eye className="w-16 h-16 text-white" />
          </div>
          <h3 className="text-xl font-semibold text-gray-900 mb-2">Seleziona un ruolo</h3>
          <p className="text-gray-600 text-base mb-4">Gestisci permessi e accessi per gli utenti</p>
          <p className="text-gray-500 text-sm">Scegli un ruolo dalla lista a sinistra per iniziare</p>
        </div>
      </div>
    );
  }

  // Icone moderne per le azioni CRUD con colori
  const actionIcons: Record<string, JSX.Element> = {
    view: <Eye className="w-4 h-4 text-blue-600" />,
    create: <Plus className="w-4 h-4 text-green-600" />,
    edit: <Edit className="w-4 h-4 text-amber-600" />,
    delete: <Trash2 className="w-4 h-4 text-red-600" />
  };

  const actionColors: Record<string, string> = {
    view: 'bg-blue-50 border-blue-200',
    create: 'bg-green-50 border-green-200',
    edit: 'bg-amber-50 border-amber-200',
    delete: 'bg-red-50 border-red-200'
  };

  const ringColors: Record<string, string> = {
    view: 'ring-blue-300',
    create: 'ring-green-300',
    edit: 'ring-amber-300',
    delete: 'ring-red-300'
  };

  return (
    <div className="space-y-8">
      {entities.map((entity) => {
        const IconComponent = entity.icon;
        const entityPermissions = getPermissionsByEntity(entity.key);

        return (
          <div key={entity.key} id={`entity-${entity.key}`} className="scroll-mt-20">
            <h4 className="text-lg font-semibold text-gray-900 mb-6 flex items-center space-x-3 sticky top-0 bg-white/95 backdrop-blur-sm py-3 z-10 border-b border-gray-200">
              <div className="p-2 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl shadow-md">
                <IconComponent className="w-5 h-5 text-white" />
              </div>
              <span>{entity.label}</span>
            </h4>

            {/* LAYOUT CRUD 2x2 - OTTIMIZZATO */}
            <div className="grid grid-cols-2 gap-4">
              {entityPermissions.map((permission) => {
                if (!permission.entity || !permission.action) {
                  return null;
                }

                const permissionKey = getPermissionKey(permission.entity, permission.action);
                const rolePermission = rolePermissions[permissionKey];
                const isGranted = rolePermission?.granted || false;
                const scope = rolePermission?.scope || 'all';
                const tenantIds = rolePermission?.tenantIds || [];
                const fieldRestrictions = rolePermission?.fieldRestrictions || [];

                const colorClass = actionColors[permission.action] || 'bg-gray-50 border-gray-200';
                const ringClass = ringColors[permission.action] || 'ring-gray-300';

                return (
                  <div 
                    key={permissionKey} 
                    className={`${colorClass} rounded-xl p-4 border hover:shadow-md transition-all duration-300 ${
                      isGranted ? `ring-2 ring-opacity-50 ${ringClass}` : ''
                    }`}
                  >
                    <div className="flex items-center justify-between mb-4">
                      <div className="flex items-center space-x-3">
                        <input
                          type="checkbox"
                          checked={isGranted}
                          onChange={(e) => onPermissionChange(permissionKey, e.target.checked)}
                          className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded transition-all duration-200"
                        />
                        <div className="flex items-center space-x-2">
                          <div className="p-1.5 bg-white rounded-lg shadow-sm">
                            {actionIcons[permission.action] || <FileText className="w-4 h-4 text-gray-600" />}
                          </div>
                          <span className="font-medium text-gray-900 text-sm">
                            {translatePermissionAction(permission.action)}
                          </span>
                        </div>
                      </div>
                    </div>

                    {isGranted && (
                      <div className="space-y-3 ml-6">
                        {/* Scope selection - DROPDOWN A PILLOLA OTTIMIZZATO */}
                        <div>
                          <label className="block text-xs font-medium text-gray-700 mb-1.5 flex items-center">
                            <Target className="w-3 h-3 mr-1 text-blue-600" />
                            Ambito di applicazione
                          </label>
                          <div className="relative">
                            <select
                              value={scope}
                              onChange={(e) => onPermissionChange(permissionKey, true, e.target.value)}
                              className="w-full px-3 py-1.5 text-xs border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 bg-white appearance-none cursor-pointer hover:border-gray-400 transition-colors"
                              style={{
                                backgroundImage: `url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%236b7280' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='m6 8 4 4 4-4'/%3e%3c/svg%3e")`,
                                backgroundPosition: 'right 0.5rem center',
                                backgroundRepeat: 'no-repeat',
                                backgroundSize: '1.2em 1.2em',
                                paddingRight: '2rem'
                              }}
                            >
                              <option value="all"> Tutti i record</option>
                              <option value="own"> Solo i propri record</option>
                              <option value="tenant"> Per tenant specifici</option>
                            </select>
                          </div>
                        </div>

                        {/* Tenant selection */}
                        {scope === 'tenant' && (
                          <div>
                            <label className="block text-xs font-medium text-gray-700 mb-1.5 flex items-center">
                              <Building2 className="w-3 h-3 mr-1 text-blue-600" />
                              Tenant autorizzati
                            </label>
                            {tenantsLoading ? (
                              <div className="text-xs text-gray-500 p-2 flex items-center">
                                <Layers className="w-3 h-3 mr-1 animate-spin" />
                                Caricamento tenant...
                              </div>
                            ) : (
                              <div className="space-y-1.5 max-h-24 overflow-y-auto bg-white border border-gray-200 rounded-lg p-2">
                                {tenants.map((tenant) => (
                                  <label key={tenant.id} className="flex items-center hover:bg-gray-50 p-1 rounded cursor-pointer">
                                    <input
                                      type="checkbox"
                                      checked={tenantIds.includes(tenant.id)}
                                      onChange={(e) => onTenantChange(permissionKey, tenant.id, e.target.checked)}
                                      className="mr-2 h-3 w-3 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                                    />
                                    <span className="text-xs text-gray-700">{tenant.name}</span>
                                  </label>
                                ))}
                              </div>
                            )}
                          </div>
                        )}

                        {/* Field restrictions */}
                        {fieldPermissions[entity.key] && (
                          <div>
                            <label className="block text-xs font-medium text-gray-700 mb-1.5 flex items-center">
                              <Eye className="w-3 h-3 mr-1 text-blue-600" />
                              Campi visibili
                            </label>
                            <div className="space-y-1.5 max-h-24 overflow-y-auto bg-white border border-gray-200 rounded-lg p-2">
                              {fieldPermissions[entity.key].map((field) => (
                                <label key={field.key} className="flex items-center hover:bg-gray-50 p-1 rounded cursor-pointer">
                                  <input
                                    type="checkbox"
                                    checked={!fieldRestrictions.includes(field.key)}
                                    onChange={(e) => onFieldRestrictionChange(permissionKey, field.key, !e.target.checked)}
                                    className="mr-2 h-3 w-3 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                                  />
                                  <span className="text-xs text-gray-700">{field.label}</span>
                                </label>
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>
        );
      })}
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { Card } from '../../design-system/molecules/Card';
import { Button } from '../../design-system/atoms/Button';
import { Input } from '../../design-system/atoms/Input';
import { Badge } from '../../design-system/atoms/Badge';
import { Alert, AlertDescription } from '../ui/alert';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '../../design-system/molecules/Tabs';
import { Select } from '../../design-system/atoms/Select';
import { FormField } from '../../design-system/molecules/FormField';
import { Separator } from '../ui/separator';
import { useToast } from '../../hooks/useToast';
import { 
  Shield, 
  Search, 
  Filter, 
  ChevronDown,
  Edit,
  Trash2,
  Move,
  AlertCircle,
  Loader2,
  Eye,
  EyeOff,
  UserPlus,
  Crown,
  Award,
  Building,
  TreePine,
  Users,
  Star,
  UserCheck,
  Plus,
  ChevronRight
} from 'lucide-react';
import { rolesService, moveRoleInHierarchy, createRole, deleteRole, updateRole, updateSystemRolePermissions } from '../../services/roles';
import { getRoleHierarchy, getCurrentUserRoleHierarchy } from '../../services/roles';
import { advancedPermissionsService, type EntityPermission } from '../../services/advancedPermissions';
import { isAuthenticated } from '../../services/auth';
import type { RoleHierarchy as RoleHierarchyType, UserRoleHierarchy } from '../../services/roles';
import HierarchyTreeView from './HierarchyTreeView';
import RoleModal from './RoleModal';
import DeleteRoleModal from './DeleteRoleModal';
import MoveRoleModal from './MoveRoleModal';

interface RoleHierarchyProps {
  onRoleAssignment?: (targetUserId: string, roleType: string) => void;
}

const RoleHierarchy: React.FC<RoleHierarchyProps> = ({ onRoleAssignment }) => {
  const { showToast } = useToast();
  const [hierarchy, setHierarchy] = useState<RoleHierarchyType>({});
  const [currentUserHierarchy, setCurrentUserHierarchy] = useState<UserRoleHierarchy | null>(null);
  const [expandedLevels, setExpandedLevels] = useState<Set<number>>(new Set([1, 2, 3, 4, 5]));
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showOnlyAssignable, setShowOnlyAssignable] = useState(false);
  const [selectedRole, setSelectedRole] = useState<string | null>(null);
  const [viewMode, setViewMode] = useState<'list' | 'tree'>('tree');

  // Stati per i modali CRUD
  const [isRoleModalOpen, setIsRoleModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [isMoveModalOpen, setIsMoveModalOpen] = useState(false);
  const [editingRole, setEditingRole] = useState<any>(null);
  const [roleToDelete, setRoleToDelete] = useState<any>(null);
  const [roleToMove, setRoleToMove] = useState<any>(null);

  useEffect(() => {
    loadHierarchyData();
  }, []);

  const loadHierarchyData = async () => {
    try {
      setLoading(true);
      setError(null);

      if (!isAuthenticated()) {
        setError('Accesso non autorizzato. Effettua il login per visualizzare la gerarchia dei ruoli.');
        setHierarchy({});
        setCurrentUserHierarchy(null);
        return;
      }

      const [hierarchyData, userHierarchyData] = await Promise.all([
        getRoleHierarchy(),
        getCurrentUserRoleHierarchy()
      ]);

      // Verifica che hierarchyData sia un oggetto valido
      setHierarchy(hierarchyData && typeof hierarchyData === 'object' ? hierarchyData : {});
      setCurrentUserHierarchy(userHierarchyData || null);
    } catch (err: any) {
      console.error('Error loading hierarchy data:', err);

      // Assicurati che hierarchy rimanga un oggetto vuoto in caso di errore
      setHierarchy({});
      setCurrentUserHierarchy(null);

      if (err?.response?.status === 401 || err?.message?.includes('Authentication required')) {
        setError('Accesso non autorizzato. Effettua il login per visualizzare la gerarchia dei ruoli.');
      } else if (err?.response?.status === 403) {
        setError('Non hai i permessi necessari per visualizzare la gerarchia dei ruoli.');
      } else if (err?.response?.status === 404) {
        setError('Endpoint della gerarchia dei ruoli non trovato. Contatta l\'amministratore di sistema.');
      } else {
        setError('Errore nel caricamento della gerarchia dei ruoli. Riprova pi tardi.');
      }
    } finally {
      setLoading(false);
    }
  };

  const toggleLevel = (level: number) => {
    const newExpanded = new Set(expandedLevels);
    if (newExpanded.has(level)) {
      newExpanded.delete(level);
    } else {
      newExpanded.add(level);
    }
    setExpandedLevels(newExpanded);
  };

  const getRolesByLevel = () => {
    const rolesByLevel: { [level: number]: Array<{ roleType: string; data: any }> } = {};

    // Verifica di sicurezza per hierarchy
    if (!hierarchy || typeof hierarchy !== 'object') {
      return rolesByLevel;
    }

    Object.entries(hierarchy).forEach(([roleType, roleData]) => {
      const level = roleData.level;
      if (!rolesByLevel[level]) {
        rolesByLevel[level] = [];
      }

      // Applica filtri
      const matchesSearch = !searchTerm || 
        roleData.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        roleData.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        roleType.toLowerCase().includes(searchTerm.toLowerCase());

      const matchesAssignable = !showOnlyAssignable || canAssignRole(roleType);

      if (matchesSearch && matchesAssignable) {
        rolesByLevel[level].push({ roleType, data: roleData });
      }
    });

    return rolesByLevel;
  };

  const canAssignRole = (roleType: string): boolean => {
    if (!currentUserHierarchy || !currentUserHierarchy.assignableRoles) return false;
    return currentUserHierarchy.assignableRoles.includes(roleType);
  };

  const isCurrentUserRole = (roleType: string): boolean => {
    if (!currentUserHierarchy || !currentUserHierarchy.userRoles) return false;
    return currentUserHierarchy.userRoles.includes(roleType);
  };

  const getLevelColor = (level: number): string => {
    const colors = {
      0: 'bg-gradient-to-r from-purple-100 to-purple-200 border-purple-400 text-purple-900',
      1: 'bg-gradient-to-r from-red-100 to-red-200 border-red-400 text-red-900',
      2: 'bg-gradient-to-r from-orange-100 to-orange-200 border-orange-400 text-orange-900',
      3: 'bg-gradient-to-r from-yellow-100 to-yellow-200 border-yellow-400 text-yellow-900',
      4: 'bg-gradient-to-r from-blue-100 to-blue-200 border-blue-400 text-blue-900',
      5: 'bg-gradient-to-r from-green-100 to-green-200 border-green-400 text-green-900'
    };
    return colors[level as keyof typeof colors] || 'bg-gradient-to-r from-gray-100 to-gray-200 border-gray-400 text-gray-900';
  };

  const getLevelIcon = (level: number) => {
    const icons = {
      0: <Crown className="w-5 h-5" />,
      1: <Star className="w-5 h-5" />,
      2: <Award className="w-5 h-5" />,
      3: <Building className="w-5 h-5" />,
      4: <UserCheck className="w-5 h-5" />,
      5: <Users className="w-5 h-5" />
    };
    return icons[level as keyof typeof icons] || <Shield className="w-5 h-5" />;
  };

  const getLevelName = (level: number): string => {
    const names = {
      0: 'Super Amministratore',
      1: 'Amministratore',
      2: 'Amministratore Aziendale',
      3: 'Manager',
      4: 'Formatore',
      5: 'Dipendente'
    };
    return names[level as keyof typeof names] || `Livello ${level}`;
  };

  const getRoleIcon = (roleType: string) => {
    if (roleType.includes('SUPER_ADMIN')) return <Crown className="w-4 h-4 text-purple-600" />;
    if (roleType.includes('ADMIN')) return <Star className="w-4 h-4 text-red-600" />;
    if (roleType.includes('MANAGER')) return <Award className="w-4 h-4 text-orange-600" />;
    if (roleType.includes('TRAINER')) return <UserCheck className="w-4 h-4 text-blue-600" />;
    return <Users className="w-4 h-4 text-green-600" />;
  };

  // Funzioni CRUD
  const handleCreateRole = () => {
    setEditingRole(null);
    setIsRoleModalOpen(true);
  };

  const handleEditRole = (roleType: string) => {
    if (!hierarchy || !hierarchy[roleType]) {
      showToast({
        message: "Ruolo non trovato nella gerarchia.",
        type: "error"
      });
      return;
    }
    const roleData = hierarchy[roleType];
    setEditingRole({ roleType, ...roleData });
    setIsRoleModalOpen(true);
  };

  const handleDeleteRole = (roleType: string) => {
    if (!hierarchy || !hierarchy[roleType]) {
      showToast({
        message: "Ruolo non trovato nella gerarchia.",
        type: "error"
      });
      return;
    }
    const roleData = hierarchy[roleType];
    setRoleToDelete({ roleType, ...roleData });
    setIsDeleteModalOpen(true);
  };

  const handleMoveRole = (roleType: string) => {
    if (!hierarchy || !hierarchy[roleType]) {
      showToast({
        message: "Ruolo non trovato nella gerarchia.",
        type: "error"
      });
      return;
    }
    const roleData = hierarchy[roleType];
    setRoleToMove({ roleType, ...roleData });
    setIsMoveModalOpen(true);
  };

  const handleRoleSubmit = async (roleData: any) => {
    try {
      // Converte i permessi dal formato del modal al formato atteso dal backend
      let permissions: string[] = [];
      let fullPermissions: Array<{ permissionId: string; granted: boolean; scope: string; tenantIds: string[]; fieldRestrictions: string[] }> = [];

      console.log(' Original permissions from modal:', roleData.permissions);

      if (roleData.permissions) {
        if (Array.isArray(roleData.permissions)) {
          // Se  gi un array di oggetti con il formato corretto, usalo direttamente
           if (roleData.permissions.length > 0 && typeof roleData.permissions[0] === 'object' && 'permissionId' in roleData.permissions[0]) {
             fullPermissions = roleData.permissions as Array<{ permissionId: string; granted: boolean; scope: string; tenantIds: string[]; fieldRestrictions: string[] }>;
             permissions = fullPermissions.filter((p: any) => p.granted && p.permissionId && p.permissionId.trim() !== '').map((p: any) => p.permissionId.trim());
          } else {
            // Se  un array di stringhe, convertilo
            permissions = roleData.permissions
               .map((perm: any) => typeof perm === 'string' ? perm : perm.permissionId)
               .filter((p: string) => p && typeof p === 'string' && p.trim() !== '')
               .map((p: string) => p.trim());
            fullPermissions = permissions.map(permissionId => ({
              permissionId,
              granted: true,
              scope: 'all',
              tenantIds: [],
              fieldRestrictions: []
            }));
          }
        } else if (typeof roleData.permissions === 'object') {
          // Se  un oggetto Record<string, boolean>, convertilo
          permissions = Object.entries(roleData.permissions)
            .filter(([permissionId, granted]) => granted && permissionId && permissionId.trim() !== '')
            .map(([permissionId]) => permissionId.trim());
          fullPermissions = Object.entries(roleData.permissions)
            .filter(([permissionId]) => permissionId && permissionId.trim() !== '')
            .map(([permissionId, granted]) => ({
              permissionId: permissionId.trim(),
              granted: Boolean(granted),
              scope: 'all',
              tenantIds: [],
              fieldRestrictions: []
            }));
        }
      }

      console.log(' Processed permissions:', permissions);
      console.log(' Permissions count:', permissions.length);
      console.log(' Full permissions:', fullPermissions);

      if (editingRole) {
        // Aggiorna ruolo esistente
        console.log(' Updating role with data:', { ...roleData, permissions });

        // Per i ruoli personalizzati, aggiorna sia i dati base che i permessi
        if (editingRole.roleType.startsWith('CUSTOM_')) {
          // Estrai l'ID del ruolo personalizzato
          const customRoleId = editingRole.roleType.replace('CUSTOM_', '');

          // Aggiorna il ruolo personalizzato con i permessi (usa array di stringhe)
          const updateData = {
            name: roleData.name,
            description: roleData.description,
            permissions: permissions // Array di stringhe per ruoli personalizzati
          };

          await rolesService.updateCustomRole(customRoleId, updateData);
        } else {
          // Per i ruoli di sistema, aggiorna prima i dati base
          await updateRole(editingRole.roleType, {
            name: roleData.name,
            description: roleData.description
          });

          // Prepara il formato corretto per l'endpoint /permissions dei ruoli di sistema
          // Il backend si aspetta un array di oggetti con permissionId, granted, scope, etc.
          const systemRolePermissions = permissions.map(permissionId => ({
            permissionId: permissionId.trim().toUpperCase(), // Normalizza il permissionId
            granted: true,
            scope: 'all',
            tenantIds: [],
            fieldRestrictions: []
          }));

          console.log(' Formatted permissions for backend:', systemRolePermissions);

          // Usa updateSystemRolePermissions che chiama l'endpoint /permissions
          await updateSystemRolePermissions(editingRole.roleType, systemRolePermissions);
        }

        showToast({
          message: "Il ruolo  stato aggiornato con successo.",
          type: "success"
        });
      } else {
        // Crea nuovo custom role (usa array di stringhe)
        const customRoleData = {
          name: roleData.name,
          description: roleData.description,
          level: roleData.level || 1,
          parentRoleType: roleData.parentRoleType || null,
          permissions: permissions
        };

        console.log(' Creating role with data:', customRoleData);
        await createRole(customRoleData);
        showToast({
          message: "Il nuovo ruolo  stato creato con successo.",
          type: "success"
        });
      }

      // Ricarica i dati
      await loadHierarchyData();
      setIsRoleModalOpen(false);
      setEditingRole(null);
    } catch (error: any) {
      console.error(' Error in handleRoleSubmit:', error);
      showToast({
        message: error.message || "Si  verificato un errore durante l'operazione.",
        type: "error"
      });
    }
  };

  const handleRoleDelete = async (roleType: string) => {
    try {
      await deleteRole(roleType);
      showToast({
        message: "Il ruolo  stato eliminato con successo.",
        type: "success"
      });

      // Ricarica i dati
      await loadHierarchyData();
      setIsDeleteModalOpen(false);
      setRoleToDelete(null);
    } catch (error: any) {
      showToast({
        message: error.message || "Si  verificato un errore durante l'eliminazione.",
        type: "error"
      });
    }
  };

  const handleRoleMove = async (roleType: string, newLevel: number, parentRoleType?: string) => {
    try {
      // Usa il nuovo servizio per spostare il ruolo
      await moveRoleInHierarchy(roleType, newLevel, parentRoleType);

      showToast({
        message: `Il ruolo  stato spostato con successo al livello ${newLevel}${parentRoleType ? ` sotto ${parentRoleType}` : ''}.`,
        type: "success"
      });

      // Ricarica i dati
      await loadHierarchyData();
      setIsMoveModalOpen(false);
      setRoleToMove(null);
    } catch (error: any) {
      showToast({
        message: error.message || "Si  verificato un errore durante lo spostamento.",
        type: "error"
      });
    }
  };

  if (loading) {
    return (
      <div className="p-8">
        <div className="flex items-center justify-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-600 border-t-transparent"></div>
          <span className="ml-4 text-lg font-medium text-gray-700">Caricamento gerarchia...</span>
        </div>
      </div>
    );
  }

  if (error) {
    const isAuthError = error.includes('Accesso non autorizzato') || error.includes('login');

    return (
      <div className="p-8">
        <div className="text-center py-12">
          <div className="text-red-600 mb-4 text-4xl"></div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">Errore di Accesso</h3>
          <p className="text-gray-600 mb-6">{error}</p>
          <div className="flex gap-3 justify-center">
            {isAuthError ? (
              <button
                onClick={() => window.location.href = '/login'}
                className="px-6 py-3 bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-xl hover:from-blue-700 hover:to-indigo-700 transition-all duration-300 font-medium shadow-lg hover:shadow-xl transform hover:scale-105"
              >
                Vai al Login
              </button>
            ) : (
              <button
                onClick={loadHierarchyData}
                className="px-6 py-3 bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-xl hover:from-blue-700 hover:to-indigo-700 transition-all duration-300 font-medium shadow-lg hover:shadow-xl transform hover:scale-105"
              >
                Riprova
              </button>
            )}
          </div>
        </div>
      </div>
    );
  }

  const rolesByLevel = getRolesByLevel();
  const sortedLevels = Object.keys(rolesByLevel).map(Number).sort((a, b) => a - b);

  return (
    <div className="space-y-6">
      {/* Header con controlli - Layout compatto */}
      <div className="bg-white border border-gray-200 rounded-lg p-4">
        <div className="flex flex-col space-y-3">
          {/* Prima riga: Tab di navigazione e controlli principali */}
          <div className="flex items-center justify-between">
            {/* Selettore modalit vista compatto */}
            <div className="flex space-x-1 bg-gray-100 p-0.5 rounded-full">
              <button
                onClick={() => setViewMode('tree')}
                className={`px-3 py-1.5 rounded-full text-sm font-medium transition-all duration-200 flex items-center space-x-1.5 ${
                  viewMode === 'tree'
                    ? 'bg-blue-600 text-white shadow-sm'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-200'
                }`}
              >
                <TreePine className="w-3.5 h-3.5" />
                <span>Albero</span>
              </button>
              <button
                onClick={() => setViewMode('list')}
                className={`px-3 py-1.5 rounded-full text-sm font-medium transition-all duration-200 flex items-center space-x-1.5 ${
                  viewMode === 'list'
                    ? 'bg-blue-600 text-white shadow-sm'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-200'
                }`}
              >
                <Shield className="w-3.5 h-3.5" />
                <span>Lista</span>
              </button>
            </div>

            {/* Controlli di destra */}
            <div className="flex items-center space-x-3">
              {/* Statistiche compatte */}
              <div className="flex items-center space-x-4 text-xs text-gray-500">
                <span className="flex items-center">
                  <Users className="w-3 h-3 mr-1" />
                  {Object.keys(hierarchy).length} totali
                </span>
                <span className="flex items-center">
                  <UserCheck className="w-3 h-3 mr-1" />
                  {currentUserHierarchy?.assignableRoles?.length || 0} assegnabili
                </span>
              </div>

              {/* Pulsante Nuovo Ruolo compatto */}
              <button
                onClick={() => {
                  setEditingRole(null);
                  setIsRoleModalOpen(true);
                }}
                className="px-3 py-1.5 bg-green-600 text-white rounded-full hover:bg-green-700 transition-all duration-200 text-sm font-medium flex items-center space-x-1.5"
              >
                <Plus className="w-3.5 h-3.5" />
                <span>Nuovo</span>
              </button>
            </div>
          </div>

          {/* Seconda riga: Controlli specifici per modalit (solo se vista lista) */}
          {viewMode === 'list' && (
            <div className="flex items-center space-x-3 pt-2 border-t border-gray-100">
              {/* Barra di ricerca compatta */}
              <div className="relative flex-1 max-w-sm">
                <Search className="absolute left-2.5 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <input
                  type="text"
                  placeholder="Cerca ruoli..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-8 pr-3 py-1.5 text-sm border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
                />
              </div>

              {/* Filtro ruoli assegnabili compatto */}
              <button
                onClick={() => setShowOnlyAssignable(!showOnlyAssignable)}
                className={`px-3 py-1.5 rounded-lg text-sm font-medium transition-all duration-200 flex items-center space-x-1.5 whitespace-nowrap ${
                  showOnlyAssignable
                    ? 'bg-green-100 text-green-800 border border-green-300'
                    : 'bg-white text-gray-700 border border-gray-300 hover:bg-gray-50'
                }`}
              >
                <Filter className="w-3.5 h-3.5" />
                <span>Solo Assegnabili</span>
              </button>
            </div>
          )}

          {/* Informazioni utente corrente compatte */}
          {currentUserHierarchy && (
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
              <div className="flex items-center space-x-3">
                <UserCheck className="w-4 h-4 text-blue-600 flex-shrink-0" />
                <div className="flex items-center space-x-4 text-xs">
                  <span className="text-blue-800">
                    <strong>Ruolo:</strong> {currentUserHierarchy.highestRole}
                  </span>
                  <span className="text-blue-800">
                    <strong>Livello:</strong> {currentUserHierarchy.userLevel}
                  </span>
                  <span className="text-blue-800">
                    <strong>Assegnabili:</strong> {currentUserHierarchy.assignableRoles?.length || 0}
                  </span>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Contenuto principale */}
      {viewMode === 'tree' ? (
        <HierarchyTreeView
          hierarchy={hierarchy}
          currentUserHierarchy={currentUserHierarchy}
          onRoleCreate={async (parentId, roleData) => {
            try {
              // Prepara i dati per il backend come custom role
              const roleDataForBackend = {
                name: roleData.name,
                description: roleData.description,
                permissions: roleData.permissions || []
              };

              console.log('Creating custom role with data:', roleDataForBackend);
              await createRole(roleDataForBackend);

              showToast({
                message: "Il nuovo ruolo  stato creato con successo.",
                type: "success"
              });

              await loadHierarchyData();
            } catch (error: any) {
              console.error('Error creating role:', error);
              showToast({
                message: error.message || "Si  verificato un errore durante la creazione del ruolo.",
                type: "error"
              });
            }
          }}
          onRoleUpdate={async (roleId, roleData) => {
            try {
              await handleRoleSubmit(roleData);
              await loadHierarchyData();
            } catch (error) {
              console.error('Error updating role:', error);
            }
          }}
          onRoleDelete={async (roleId) => {
            try {
              await handleRoleDelete(roleId);
            } catch (error) {
              console.error('Error deleting role:', error);
            }
          }}
          onRoleMove={async (roleId, newParentId) => {
            try {
              // Trova il nuovo livello basato sul parent
              const newLevel = newParentId && hierarchy[newParentId] ? hierarchy[newParentId].level + 1 : 1;
              await handleRoleMove(roleId, newLevel);
            } catch (error) {
              console.error('Error moving role:', error);
            }
          }}
        />
      ) : (
        /* Vista lista esistente */
        <div className="space-y-3">
          {sortedLevels.map((level) => {
            const isExpanded = expandedLevels.has(level);
            const roles = rolesByLevel[level];

            if (!roles || roles.length === 0) return null;

            return (
              <div key={level} className="bg-white border border-gray-200 rounded-lg shadow-sm overflow-hidden">
                <button
                  onClick={() => toggleLevel(level)}
                  className={`w-full p-3 text-left flex items-center justify-between transition-all duration-300 ${getLevelColor(level)} ${!isExpanded ? 'rounded-lg' : 'rounded-t-lg'}`}
                >
                  <div className="flex items-center">
                    {isExpanded ? (
                      <ChevronDown className="h-4 w-4 mr-2" />
                    ) : (
                      <ChevronRight className="h-4 w-4 mr-2" />
                    )}
                    <div className="mr-2">
                      {React.cloneElement(getLevelIcon(level), { className: "w-4 h-4" })}
                    </div>
                    <div>
                      <span className="font-medium text-sm">
                        Livello {level}: {getLevelName(level)}
                      </span>
                      <div className="text-xs opacity-80 mt-0.5">
                        {roles?.length || 0} ruol{(roles?.length || 0) !== 1 ? 'i' : 'o'} disponibil{(roles?.length || 0) !== 1 ? 'i' : 'e'}
                      </div>
                    </div>
                  </div>
                </button>

                {isExpanded && (
                  <div className="p-4 bg-white border-t border-gray-200">
                    <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-3">
                      {roles.map(({ roleType, data }) => (
                        <div
                          key={roleType}
                          className={`p-3 border-2 rounded-lg transition-all duration-300 cursor-pointer hover:shadow-md transform hover:scale-101 ${
                            isCurrentUserRole(roleType)
                              ? 'border-blue-500 bg-blue-50 shadow-sm'
                              : canAssignRole(roleType)
                              ? 'border-green-400 bg-green-50 hover:border-green-500'
                              : 'border-gray-200 bg-gray-50 hover:border-gray-300'
                          } ${selectedRole === roleType ? 'ring-2 ring-blue-300' : ''}`}
                          onClick={() => setSelectedRole(selectedRole === roleType ? null : roleType)}
                        >
                          <div className="flex items-start justify-between mb-2">
                            <div className="flex items-center">
                              {React.cloneElement(getRoleIcon(roleType), { className: "w-3.5 h-3.5" })}
                              <h4 className="font-medium text-sm text-gray-900 ml-1.5">{data.name}</h4>
                            </div>
                            <div className="flex items-center space-x-1">
                              {isCurrentUserRole(roleType) && (
                                <span className="text-xs bg-blue-500 text-white px-1.5 py-0.5 rounded-full font-medium">
                                  TUO
                                </span>
                              )}

                              {/* Pulsanti azioni CRUD */}
                              <div className="flex space-x-0.5">
                                <button
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    handleEditRole(roleType);
                                  }}
                                  className="p-1 text-blue-600 hover:bg-blue-100 rounded transition-colors"
                                  title="Modifica ruolo"
                                >
                                  <Edit className="w-3 h-3" />
                                </button>
                                <button
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    handleMoveRole(roleType);
                                  }}
                                  className="p-1 text-amber-600 hover:bg-amber-100 rounded transition-colors"
                                  title="Sposta ruolo"
                                >
                                  <Move className="w-3 h-3" />
                                </button>
                                <button
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    handleDeleteRole(roleType);
                                  }}
                                  className="p-1 text-red-600 hover:bg-red-100 rounded transition-colors"
                                  title="Elimina ruolo"
                                >
                                  <Trash2 className="w-3 h-3" />
                                </button>
                              </div>
                            </div>
                          </div>

                          <p className="text-xs text-gray-600 mb-3 line-clamp-2">{data.description}</p>

                          <div className="space-y-2">
                            <div>
                              <span className="text-xs font-medium text-gray-700 block mb-1">Ruoli assegnabili:</span>
                              <div className="text-xs text-gray-600">
                                {data.assignableRoles && data.assignableRoles.length > 0 ? (
                                  <div className="flex flex-wrap gap-1">
                                    {data.assignableRoles.slice(0, 2).map((role: string) => (
                                      <span key={role} className="bg-gray-100 px-1.5 py-0.5 rounded text-xs">
                                        {role}
                                      </span>
                                    ))}
                                    {data.assignableRoles.length > 2 && (
                                      <span className="text-gray-500 px-1.5 py-0.5 text-xs">
                                        +{data.assignableRoles.length - 2}
                                      </span>
                                    )}
                                  </div>
                                ) : (
                                  <span className="text-gray-400 italic text-xs">Nessuno</span>
                                )}
                              </div>
                            </div>

                            <div>
                              <span className="text-xs font-medium text-gray-700 block mb-1">Permessi:</span>
                              <div className="text-xs text-gray-600">
                                {data.permissions?.length > 0 ? (
                                  <span className="bg-blue-100 text-blue-800 px-1.5 py-0.5 rounded text-xs">
                                    {data.permissions.length} permess{data.permissions.length !== 1 ? 'i' : 'o'}
                                  </span>
                                ) : (
                                  <span className="text-gray-400 italic text-xs">Nessuno</span>
                                )}
                              </div>
                            </div>
                          </div>

                          {canAssignRole(roleType) && onRoleAssignment && (
                            <button
                              onClick={(e) => {
                                e.stopPropagation();
                                onRoleAssignment('', roleType);
                              }}
                              className="mt-3 w-full text-xs bg-gradient-to-r from-green-600 to-emerald-600 text-white px-3 py-1.5 rounded hover:from-green-700 hover:to-emerald-700 transition-all duration-300 font-medium shadow-sm hover:shadow-md transform hover:scale-105"
                            >
                              Assegna Ruolo
                            </button>
                          )}
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      )}

      {/* Modali CRUD */}
      <RoleModal
        isOpen={isRoleModalOpen}
        onClose={() => {
          setIsRoleModalOpen(false);
          setEditingRole(null);
        }}
        onSave={handleRoleSubmit}
        role={editingRole}
        mode={editingRole ? 'edit' : 'create'}
        hierarchy={hierarchy}
      />

      <DeleteRoleModal
        isOpen={isDeleteModalOpen}
        onClose={() => {
          setIsDeleteModalOpen(false);
          setRoleToDelete(null);
        }}
        onConfirm={() => roleToDelete && handleRoleDelete(roleToDelete.roleType)}
        role={roleToDelete}
      />

      <MoveRoleModal
        isOpen={isMoveModalOpen}
        onClose={() => {
          setIsMoveModalOpen(false);
          setRoleToMove(null);
        }}
        onMove={(newLevel) => roleToMove && handleRoleMove(roleToMove.roleType, newLevel)}
        role={roleToMove}
        hierarchy={hierarchy}
        currentLevel={roleToMove?.level || 0}
      />
    </div>
  );
};

export default RoleHierarchy;

import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { Modal } from '../../design-system/molecules/Modal';
import { Button } from '../../design-system/atoms/Button';
import { Input } from '../../design-system/atoms/Input';
import { Label } from '../../design-system/atoms/Label';
import { FormField } from '../../design-system/molecules/FormField';
import { Alert, AlertDescription } from '../ui/alert';
import { 
  Loader2, 
  AlertCircle, 
  Shield, 
  Database,
  Building2,
  BookOpen,
  TreePine,
  Building,
  Layers,
  MessageSquare,
  Globe,
  Users,
  Calendar,
  FileText,
  Settings,
  ChevronRight
} from 'lucide-react';
import { rolesService } from '../../services/roles';
import { advancedPermissionsService, EntityDefinition } from '../../services/advancedPermissions';

interface Permission {
  key: string;
  label: string;
  description: string;
}

interface PermissionGroup {
  label: string;
  description: string;
  permissions: Permission[];
}

interface EntityGroup {
  entity: EntityDefinition;
  permissions: Permission[];
}

interface RoleModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSave: (roleData: any) => Promise<void>;
  role?: any;
  mode: 'create' | 'edit';
  hierarchy?: Record<string, any>; // Aggiunto per la selezione del genitore
}

const RoleModal: React.FC<RoleModalProps> = ({
  isOpen,
  onClose,
  onSave,
  role,
  mode,
  hierarchy = {}
}) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    level: '1',
    parentRoleType: '',
    permissions: {} as Record<string, boolean>
  });
  const [availablePermissions, setAvailablePermissions] = useState<Record<string, PermissionGroup>>({});
  const [entities, setEntities] = useState<EntityDefinition[]>([]);
  const [entityGroups, setEntityGroups] = useState<EntityGroup[]>([]);
  const [selectedPermissionGroup, setSelectedPermissionGroup] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [loadingPermissions, setLoadingPermissions] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Mappa delle icone per le entit
  const entityIcons: Record<string, React.ComponentType<any>> = useMemo(() => ({
    persons: Users,
    companies: Building2,
    courses: BookOpen,
    trainings: Calendar,
    roles: Shield,
    hierarchy: TreePine,
    documents: Database,
    sites: Building,
    reparti: Layers,
    form_templates: FileText,
    form_submissions: MessageSquare,
    public_cms: Globe,
    templates: FileText
  }), []);

  // Funzione per ottenere l'icona dell'entit
  const getEntityIcon = useCallback((entityName: string) => {
    return entityIcons[entityName] || Database;
  }, [entityIcons]);

  // Carica i permessi disponibili
  useEffect(() => {
    if (isOpen) {
      loadAvailablePermissions();
    }
  }, [isOpen]);

  // Inizializza il form quando si apre il modal
  useEffect(() => {
    if (isOpen) {
      if (mode === 'edit' && role) {
        setFormData({
          name: role.name || '',
          description: role.description || '',
          level: (role.level || 1).toString(),
          parentRoleType: role.parentRoleType || '',
          permissions: {} // Inizializza vuoto, verr caricato separatamente
        });
        // Carica i permessi del ruolo dal backend
        loadRolePermissions(role.roleType);
      } else {
        setFormData({
          name: '',
          description: '',
          level: '1',
          parentRoleType: '',
          permissions: {}
        });
      }
      setError(null);
    }
  }, [isOpen, mode, role]);

  const loadRolePermissions = async (roleType: string) => {
    try {
      setLoadingPermissions(true);
      console.log(' [RoleModal] Loading permissions for role:', roleType);

      const rolePermissions = await rolesService.getRolePermissions(roleType);
      console.log(' [RoleModal] Received permissions from API:', rolePermissions);
      console.log(' [RoleModal] Permissions type:', typeof rolePermissions);
      console.log(' [RoleModal] Is array:', Array.isArray(rolePermissions));
      console.log(' [RoleModal] Length:', rolePermissions?.length);

      // Converte l'array di permessi in un oggetto Record<string, boolean>
      const permissionsMap: Record<string, boolean> = {};
      if (Array.isArray(rolePermissions)) {
        rolePermissions.forEach((permission: string) => {
          console.log(' [RoleModal] Processing permission:', permission);
          // Il backend restituisce i permessi gi normalizzati (maiuscoli)
          // Li usiamo direttamente come chiavi
          permissionsMap[permission] = true;
        });
      } else {
        console.warn(' [RoleModal] Permissions is not an array:', rolePermissions);
      }

      console.log(' [RoleModal] Final permissions map:', permissionsMap);
      console.log(' [RoleModal] Permissions map keys:', Object.keys(permissionsMap));

      // Aggiorna il form con i permessi caricati
      setFormData(prev => {
        const newFormData = {
          ...prev,
          permissions: permissionsMap
        };
        console.log(' [RoleModal] Updated form data:', newFormData);
        return newFormData;
      });

      console.log(' [RoleModal] Form data updated with permissions successfully');
    } catch (error) {
      console.error(' [RoleModal] Error loading role permissions:', error);
      setError('Errore nel caricamento dei permessi del ruolo');
    } finally {
      setLoadingPermissions(false);
    }
  };

  const loadAvailablePermissions = async () => {
    try {
      setLoadingPermissions(true);

      // Carica sia i permessi che le entit
      const [permissions, entitiesData] = await Promise.all([
        rolesService.getPermissions(),
        advancedPermissionsService.getEntityDefinitions()
      ]);

      console.log(' [RoleModal] Entit caricate:', entitiesData.length);
      console.log(' [RoleModal] Lista entit:', entitiesData.map(e => e.name));

      // Verifica se le entit critiche sono presenti
      const criticalEntities = ['form_templates', 'form_submissions', 'public_cms'];
      const foundCriticalEntities = criticalEntities.filter(entity => 
        entitiesData.some(e => e.name === entity)
      );
      console.log(' [RoleModal] Entit critiche trovate:', foundCriticalEntities);

      // Raggruppa i permessi per categoria (mantenendo la logica esistente)
      const groupedPermissions: Record<string, PermissionGroup> = {};
      permissions.forEach(permission => {
        const category = permission.category || 'general';
        if (!groupedPermissions[category]) {
          // Gestione sicura per evitare errori toUpperCase su undefined
          const categoryLabel = category && typeof category === 'string' 
            ? category.charAt(0).toUpperCase() + category.slice(1)
            : 'General';

          groupedPermissions[category] = {
            label: categoryLabel,
            description: `Permessi per ${category}`,
            permissions: []
          };
        }
        groupedPermissions[category].permissions.push({
          key: permission.id,
          label: permission.name,
          description: permission.description || ''
        });
      });

      // Crea gruppi di entit con permessi CRUD
      const entityGroupsData: EntityGroup[] = entitiesData.map(entity => {
        // Normalizza il nome dell'entit come fa il backend
        const normalizedEntityName = entity.name.trim().toUpperCase();

        const crudPermissions: Permission[] = [
          {
            key: `CREATE_${normalizedEntityName}`,
            label: `Crea ${entity.displayName}`,
            description: `Permesso per creare nuovi record di ${entity.displayName}`
          },
          {
            key: `VIEW_${normalizedEntityName}`,
            label: `Visualizza ${entity.displayName}`,
            description: `Permesso per visualizzare i record di ${entity.displayName}`
          },
          {
            key: `EDIT_${normalizedEntityName}`,
            label: `Modifica ${entity.displayName}`,
            description: `Permesso per modificare i record di ${entity.displayName}`
          },
          {
            key: `DELETE_${normalizedEntityName}`,
            label: `Elimina ${entity.displayName}`,
            description: `Permesso per eliminare i record di ${entity.displayName}`
          }
        ];

        return {
          entity,
          permissions: crudPermissions
        };
      });

      console.log(' [RoleModal] Gruppi entit creati:', entityGroupsData.length);
      console.log(' [RoleModal] Entit nei gruppi:', entityGroupsData.map(g => g.entity.name));

      setAvailablePermissions(groupedPermissions);
      setEntities(entitiesData);
      setEntityGroups(entityGroupsData);
    } catch (error) {
      console.error('Error loading permissions:', error);
      setError('Errore nel caricamento dei permessi');
    } finally {
      setLoadingPermissions(false);
    }
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handlePermissionChange = (permissionKey: string, checked: boolean) => {
    setFormData(prev => ({
      ...prev,
      permissions: {
        ...prev.permissions,
        [permissionKey]: checked
      }
    }));
  };

  const handleSelectAllPermissions = () => {
    const allPermissions: Record<string, boolean> = {};

    // Seleziona tutti i permessi delle categorie esistenti
    Object.values(availablePermissions).forEach(group => {
      group.permissions.forEach(permission => {
        allPermissions[permission.key] = true;
      });
    });

    // Seleziona tutti i permessi delle entit
    entityGroups.forEach(entityGroup => {
      entityGroup.permissions.forEach(permission => {
        allPermissions[permission.key] = true;
      });
    });

    setFormData(prev => ({
      ...prev,
      permissions: allPermissions
    }));
  };

  const handleSelectNoPermissions = () => {
    setFormData(prev => ({
      ...prev,
      permissions: {}
    }));
  };

  const handleSelectGroupPermissions = (groupKey: string, selectAll: boolean) => {
    const newPermissions = { ...formData.permissions };

    if (groupKey.startsWith('entity_')) {
      // Gestione per gruppi di entit
      const entityIndex = parseInt(groupKey.replace('entity_', ''));
      const entityGroup = entityGroups[entityIndex];
      if (entityGroup) {
        entityGroup.permissions.forEach(permission => {
          if (selectAll) {
            newPermissions[permission.key] = true;
          } else {
            delete newPermissions[permission.key];
          }
        });
      }
    } else {
      // Gestione per gruppi di permessi esistenti
      const group = availablePermissions[groupKey];
      if (group) {
        group.permissions.forEach(permission => {
          if (selectAll) {
            newPermissions[permission.key] = true;
          } else {
            delete newPermissions[permission.key];
          }
        });
      }
    }

    setFormData(prev => ({
      ...prev,
      permissions: newPermissions
    }));
  };

  const getSelectedPermissionsCount = (groupPermissions: Permission[]) => {
    return groupPermissions.filter(perm => formData.permissions[perm.key]).length;
  };

  const handleSubmit = async () => {
    try {
      setLoading(true);
      setError(null);

      // Validazione
      if (!formData.name.trim()) {
        setError('Il nome del ruolo  obbligatorio');
        return;
      }

      if (!formData.description.trim()) {
        setError('La descrizione del ruolo  obbligatoria');
        return;
      }

      // Prepara i dati per l'API
      // Il backend si aspetta sempre un array di oggetti con { permissionId, granted, scope, ... }
      const permissions = Object.entries(formData.permissions)
        .map(([permissionId, granted]) => ({
          permissionId: permissionId.trim().toUpperCase(),
          granted: Boolean(granted),
          scope: 'all',
          tenantIds: [],
          fieldRestrictions: []
        }));

      console.log(' RoleModal - Sending permissions:', permissions);

      const roleData = {
        name: formData.name.trim(),
        description: formData.description.trim(),
        permissions: permissions,
        ...(mode === 'create' && {
          level: parseInt(formData.level),
          parentRoleType: formData.parentRoleType || null
        })
      };

      await onSave(roleData);
      onClose();
    } catch (error: any) {
      console.error('Error saving role:', error);
      setError(error.message || 'Errore nel salvataggio del ruolo');
    } finally {
      setLoading(false);
    }
  };

  const totalSelectedPermissions = Object.values(formData.permissions).filter(Boolean).length;

  return (
    <Modal
      isOpen={isOpen}
      onClose={onClose}
      title={mode === 'create' ? 'Crea Nuovo Ruolo' : 'Modifica Ruolo'}
      size="lg"
    >
      <div className="space-y-6">
        {error && (
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}

        {/* Informazioni base */}
        <div className="space-y-4">
          <FormField
            label="Nome Ruolo *"
            name="name"
            type="text"
            value={formData.name}
            onChange={(e) => handleInputChange('name', e.target.value)}
            placeholder="Es. Manager Vendite"
            disabled={loading}
            required
          />

          <FormField
            label="Descrizione *"
            name="description"
            type="textarea"
            value={formData.description}
            onChange={(e) => handleInputChange('description', e.target.value)}
            placeholder="Descrizione del ruolo e delle sue responsabilit"
            rows={3}
            disabled={loading}
            required
          />

          {mode === 'create' && (
            <>
              <div className="space-y-3">
                <Label htmlFor="level" className="text-sm font-medium text-gray-700">
                  Livello Gerarchico *
                </Label>
                <div className="grid grid-cols-6 gap-2">
                  {[1, 2, 3, 4, 5, 6].map((level) => (
                    <button
                      key={level}
                      type="button"
                      onClick={() => handleInputChange('level', level.toString())}
                      className={`
                        relative p-3 rounded-lg border-2 transition-all duration-200 text-center
                        ${parseInt(formData.level) === level
                          ? 'border-blue-500 bg-blue-50 text-blue-700 shadow-md'
                          : 'border-gray-200 bg-white text-gray-600 hover:border-gray-300 hover:bg-gray-50'
                        }
                      `}
                      disabled={loading}
                    >
                      <div className="text-lg font-bold">{level}</div>
                      <div className="text-xs mt-1">
                        {level === 1 ? 'CEO' : 
                         level === 2 ? 'Dir.' : 
                         level === 3 ? 'Mgr' : 
                         level === 4 ? 'Lead' : 
                         level === 5 ? 'Sr.' : 'Jr.'}
                      </div>
                      {parseInt(formData.level) === level && (
                        <div className="absolute -top-1 -right-1 w-3 h-3 bg-blue-500 rounded-full"></div>
                      )}
                    </button>
                  ))}
                </div>
                <p className="text-xs text-gray-500">
                  Seleziona il livello gerarchico (1 = pi alto, 6 = pi basso)
                </p>
              </div>

              <div className="space-y-3">
                <Label htmlFor="parentRoleType" className="text-sm font-medium text-gray-700">
                  Ruolo Genitore
                </Label>

                {parseInt(formData.level) > 1 ? (
                  <div className="space-y-3 max-h-48 overflow-y-auto border rounded-lg p-4">
                    <div className="flex items-center space-x-2 mb-3">
                      <input
                        type="radio"
                        name="parentRole"
                        value=""
                        checked={!formData.parentRoleType}
                        onChange={() => handleInputChange('parentRoleType', '')}
                        className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300"
                        disabled={loading}
                      />
                      <Label className="text-sm text-gray-600 cursor-pointer">
                        Nessun genitore specifico
                      </Label>
                    </div>

                    {Object.entries(hierarchy)
                      .filter(([_, roleData]) => roleData?.level === parseInt(formData.level) - 1)
                      .map(([roleType, roleData]) => (
                        <div key={roleType} className="flex items-start space-x-3 p-3 border rounded-lg hover:bg-gray-50 transition-colors">
                          <input
                            type="radio"
                            name="parentRole"
                            value={roleType}
                            checked={formData.parentRoleType === roleType}
                            onChange={() => handleInputChange('parentRoleType', roleType)}
                            className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 mt-1"
                            disabled={loading}
                          />
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center space-x-2">
                              <Label className="font-medium text-gray-900 cursor-pointer">
                                {roleData?.name || roleType}
                              </Label>
                              <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                                Livello {roleData?.level}
                              </span>
                            </div>
                            {roleData?.description && (
                              <p className="text-sm text-gray-500 mt-1">
                                {roleData.description}
                              </p>
