                {/* Colonna 2: Permessi del gruppo selezionato */}
                <div className="overflow-hidden">
                  {selectedPermissionGroup ? (
                    <div className="h-full overflow-y-auto">
                      {selectedPermissionGroup.startsWith('entity_') ? (
                        // Gestione entit
                        (() => {
