  selectedAction: string | null;
  permissions: EntityPermission[];
  bulkMode: boolean;
  selectedActions: Set<string>;
  onFieldToggle: (entity: string, action: string, fieldId: string, add: boolean) => void;
  onBulkFieldsApply: (fieldIds: string[], add: boolean) => void;
}

const FieldsSection: React.FC<FieldsSectionProps> = ({
  entity,
  selectedAction,
  permissions,
  bulkMode,
  selectedActions,
  onFieldToggle,
  onBulkFieldsApply
}) => {
  // Mostra sempre i campi quando un'entit  selezionata
  const showAllFields = !selectedAction && !bulkMode;

  const getFieldPermissions = (fieldId: string): { [action: string]: boolean } => {
    const result: { [action: string]: boolean } = {};

    if (bulkMode) {
      selectedActions.forEach(action => {
        const permission = getPermission(permissions, entity.name, action);
        result[action] = permission?.fields?.includes(fieldId) || false;
      });
    } else if (selectedAction) {
      const permission = getPermission(permissions, entity.name, selectedAction);
      result[selectedAction] = permission?.fields?.includes(fieldId) || false;
    } else if (showAllFields) {
      // Quando nessuna azione  selezionata, mostra lo stato per tutte le azioni CRUD
      ['create', 'read', 'update', 'delete'].forEach(action => {
        const permission = getPermission(permissions, entity.name, action);
        result[action] = permission?.fields?.includes(fieldId) || false;
      });
    }

    return result;
  };

  const isFieldSelectedInAllActions = (fieldId: string): boolean => {
    if (!bulkMode || selectedActions.size === 0) return false;

    return Array.from(selectedActions).every(action => {
      const permission = getPermission(permissions, entity.name, action);
      return permission?.fields?.includes(fieldId) || false;
    });
  };

  const isFieldSelectedInSomeActions = (fieldId: string): boolean => {
    if (!bulkMode || selectedActions.size === 0) return false;

    return Array.from(selectedActions).some(action => {
      const permission = getPermission(permissions, entity.name, action);
      return permission?.fields?.includes(fieldId) || false;
    });
  };

  return (
    <div className="bg-white h-full flex flex-col">
      {/* Header */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div>
            <h4 className="font-medium text-gray-900">
              {bulkMode 
                ? 'Campi per Azioni Multiple' 
                : selectedAction 
                  ? `Campi per ${selectedAction}` 
                  : 'Campi Disponibili'
              }
            </h4>
            <p className="text-sm text-gray-600 mt-1">
              {entity.fields.length} campi disponibili per {entity.displayName}
              {showAllFields && ' (tutte le azioni)'}
            </p>
          </div>

          {bulkMode && selectedActions.size > 0 && (
            <div className="flex space-x-2">
              <button
                type="button"
                onClick={() => onBulkFieldsApply(entity.fields.map(f => f.name), true)}
                className="text-xs px-2 py-1 bg-green-100 text-green-700 rounded hover:bg-green-200"
              >
                Aggiungi Tutti
              </button>
              <button
                type="button"
                onClick={() => onBulkFieldsApply(entity.fields.map(f => f.name), false)}
                className="text-xs px-2 py-1 bg-red-100 text-red-700 rounded hover:bg-red-200"
              >
                Rimuovi Tutti
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Lista campi */}
      <div className="flex-1 overflow-y-auto p-4">
        <div className="space-y-2">
          {entity.fields.map((field) => {
            const fieldPermissions = getFieldPermissions(field.name);
            const isSelected = bulkMode 
              ? isFieldSelectedInAllActions(field.name)
              : fieldPermissions[selectedAction!] || false;
            const isPartiallySelected = bulkMode && isFieldSelectedInSomeActions(field.name) && !isFieldSelectedInAllActions(field.name);

            return (
              <div
                key={field.name}
                className={`border rounded-lg p-3 transition-colors ${
                  isSelected 
                    ? 'border-blue-300 bg-blue-50' 
                    : isPartiallySelected
                    ? 'border-yellow-300 bg-yellow-50'
                    : 'border-gray-200 hover:border-gray-300'
                }`}
              >
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-3">
                      <div className="flex-shrink-0">
                        {isSelected ? (
                          <div className="w-5 h-5 bg-blue-600 rounded flex items-center justify-center">
                            <Check className="w-3 h-3 text-white" />
                          </div>
                        ) : isPartiallySelected ? (
                          <div className="w-5 h-5 bg-yellow-500 rounded flex items-center justify-center">
                            <div className="w-2 h-2 bg-white rounded" />
                          </div>
                        ) : (
                          <div className="w-5 h-5 border-2 border-gray-300 rounded" />
                        )}
                      </div>

                      <div className="flex-1 min-w-0">
                        <p className="font-medium text-gray-900 truncate">
                          {field.displayName}
                        </p>
                        <p className="text-xs text-gray-500 truncate">
                          {field.name}  {field.type || 'string'}
                        </p>
                      </div>
                    </div>
                  </div>

                  <div className="flex-shrink-0 ml-3">
                    {bulkMode ? (
                      <button
                        type="button"
                        onClick={() => {
                          const shouldAdd = !isFieldSelectedInAllActions(field.name);
                          onBulkFieldsApply([field.name], shouldAdd);
                        }}
                        className={`p-1 rounded transition-colors ${
                          isSelected 
                            ? 'text-blue-600 hover:bg-blue-100' 
                            : isPartiallySelected
                            ? 'text-yellow-600 hover:bg-yellow-100'
                            : 'text-gray-400 hover:bg-gray-100'
                        }`}
                      >
                        {isSelected ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                      </button>
                    ) : (
                      <button
                        type="button"
                        onClick={() => onFieldToggle(entity.name, selectedAction!, field.name, !isSelected)}
                        className={`p-1 rounded transition-colors ${
                          isSelected 
                            ? 'text-blue-600 hover:bg-blue-100' 
                            : 'text-gray-400 hover:bg-gray-100'
                        }`}
                      >
                        {isSelected ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                      </button>
                    )}
                  </div>
                </div>

                {/* Dettagli per modalit bulk */}
                {bulkMode && selectedActions.size > 1 && (
                  <div className="mt-2 pt-2 border-t border-gray-200">
                    <div className="flex flex-wrap gap-1">
                      {Array.from(selectedActions).map(action => {
                        const hasField = fieldPermissions[action];
                        return (
                          <span
                            key={action}
                            className={`text-xs px-2 py-1 rounded ${
                              hasField 
                                ? 'bg-green-100 text-green-700' 
                                : 'bg-gray-100 text-gray-600'
                            }`}
                          >
                            {action}
                          </span>
                        );
                      })}
                    </div>
                  </div>
                )}

                {/* Dettagli per visualizzazione di tutti i campi */}
                {showAllFields && (
                  <div className="mt-2 pt-2 border-t border-gray-200">
                    <div className="flex flex-wrap gap-1">
                      {['create', 'read', 'update', 'delete'].map(action => {
                        const hasField = fieldPermissions[action];
                        return (
                          <span
                            key={action}
                            className={`text-xs px-2 py-1 rounded ${
                              hasField 
                                ? 'bg-green-100 text-green-700' 
                                : 'bg-gray-100 text-gray-600'
                            }`}
                          >
                            {action}
                          </span>
                        );
                      })}
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default FieldsSection;

import React, { useState, useEffect, useCallback } from 'react';
import { toast } from 'react-hot-toast';
import { Role } from '../../../hooks/useRoles';
import { Tenant } from '../../../hooks/useTenants';
import { EntityDefinition, EntityPermission, advancedPermissionsService } from '../../../services/advancedPermissions';
import { 
  updatePermissionInArray, 
  updatePermissionFields, 
  updatePermissionTenants,
  applyBulkPermissions,
  getAllActionNames,
  filterEntities
} from './utils';

// Importa i componenti modulari
import PermissionManagerHeader from './PermissionManagerHeader';
import RoleInfoSection from './RoleInfoSection';
import EntityList from './EntityList';
import PermissionsSection from './PermissionsSection';
import FieldsSection from './FieldsSection';

interface OptimizedPermissionManagerRefactoredProps {
  role: Role;
  tenants: Tenant[];
  onBack: () => void;
}

const OptimizedPermissionManagerRefactored: React.FC<OptimizedPermissionManagerRefactoredProps> = ({
  role,
  tenants,
  onBack
}) => {
  // Stati principali
  const [entities, setEntities] = useState<EntityDefinition[]>([]);
  const [permissions, setPermissions] = useState<EntityPermission[]>([]);
  const [selectedEntity, setSelectedEntity] = useState<EntityDefinition | null>(null);
  const [selectedAction, setSelectedAction] = useState<string | null>(null);

  // Stati per modalit bulk
  const [bulkMode, setBulkMode] = useState(false);
  const [selectedActions, setSelectedActions] = useState<Set<string>>(new Set());

  // Stati di caricamento e errore
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Stato per ricerca entit
  const [searchTerm, setSearchTerm] = useState('');

  // Caricamento iniziale dei dati
  useEffect(() => {
    loadData();
  }, [role.name]);

  const loadData = async () => {
    try {
      setLoading(true);
      setError(null);

      const [entitiesData, permissionsData] = await Promise.all([
        advancedPermissionsService.getEntityDefinitions(),
        advancedPermissionsService.getRolePermissions(role.name)
      ]);

      setEntities(entitiesData);
      setPermissions(permissionsData);
    } catch (err) {
      console.error('Errore nel caricamento dei dati:', err);
      setError('Errore nel caricamento dei dati');
      toast.error('Errore nel caricamento dei dati');
    } finally {
      setLoading(false);
    }
  };

  // Gestione selezione entit
  const handleEntitySelect = useCallback((entity: EntityDefinition) => {
    setSelectedEntity(entity);
    setSelectedAction(null);
    setBulkMode(false);
    setSelectedActions(new Set());
  }, []);

  // Gestione aggiornamento permessi
  const updatePermission = useCallback((
    entity: string, 
    action: string, 
    scope: 'all' | 'tenant' | 'own' | 'none'
  ) => {
    setPermissions(prev => updatePermissionInArray(prev, entity, action, scope));
  }, []);

  // Gestione toggle azioni per modalit bulk
  const toggleActionSelection = useCallback((action: string) => {
    setSelectedActions(prev => {
      const newSet = new Set(prev);
      if (newSet.has(action)) {
        newSet.delete(action);
      } else {
        newSet.add(action);
      }
      return newSet;
    });
  }, []);

  const selectAllActions = useCallback(() => {
    setSelectedActions(getAllActionNames());
  }, []);

  const clearActionSelection = useCallback(() => {
    setSelectedActions(new Set());
  }, []);

  // Gestione operazioni bulk
  const applyBulkScope = useCallback((scope: 'all' | 'tenant' | 'own' | 'none') => {
    if (!selectedEntity || selectedActions.size === 0) return;

    setPermissions(prev => 
      applyBulkPermissions(prev, selectedEntity.name, selectedActions, 'scope', scope)
    );
  }, [selectedEntity, selectedActions]);

  const applyBulkFields = useCallback((fieldIds: string[], add: boolean) => {
    if (!selectedEntity || selectedActions.size === 0) return;

    setPermissions(prev => 
      applyBulkPermissions(prev, selectedEntity.name, selectedActions, 'fields', fieldIds, add)
    );
  }, [selectedEntity, selectedActions]);

  const applyBulkTenants = useCallback((tenantIds: number[], add: boolean) => {
    if (!selectedEntity || selectedActions.size === 0) return;

    setPermissions(prev => 
      applyBulkPermissions(prev, selectedEntity.name, selectedActions, 'tenants', tenantIds, add)
    );
  }, [selectedEntity, selectedActions]);

  // Gestione campi specifici
  const handleFieldToggle = useCallback((entity: string, action: string, fieldId: string, add: boolean) => {
    setPermissions(prev => updatePermissionFields(prev, entity, action, fieldId, add));
  }, []);

  // Gestione tenant
  const handleTenantChange = useCallback((entity: string, action: string, tenantId: number, selected: boolean) => {
    setPermissions(prev => updatePermissionTenants(prev, entity, action, tenantId, selected));
  }, []);

  // Salvataggio permessi
  const savePermissions = async () => {
    try {
      setSaving(true);
      await advancedPermissionsService.updateRolePermissions(role.name, permissions);
      toast.success('Permessi aggiornati con successo');
    } catch (err) {
      console.error('Errore nel salvataggio:', err);
      toast.error('Errore nel salvataggio dei permessi');
    } finally {
      setSaving(false);
    }
  };

  // Filtro entit
  const filteredEntities = filterEntities(entities, searchTerm);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Caricamento permessi...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={loadData}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Riprova
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col bg-gray-50">
      {/* Header principale */}
      <PermissionManagerHeader
        role={role}
        filteredEntitiesCount={filteredEntities.length}
        selectedEntity={selectedEntity}
        bulkMode={bulkMode}
        onBulkModeToggle={() => setBulkMode(!bulkMode)}
      />

      {/* Sezione informazioni ruolo e azioni rapide */}
      <RoleInfoSection
        role={role}
        saving={saving}
        onBack={onBack}
        onSave={savePermissions}
        onReload={loadData}
      />

      {/* Layout principale a 4 colonne */}
      <div className="flex-1 grid grid-cols-4 min-h-0">
        {/* Colonna 1: Lista entit */}
        <EntityList
          entities={filteredEntities}
          selectedEntity={selectedEntity}
          searchTerm={searchTerm}
          onSearchChange={setSearchTerm}
          onEntitySelect={handleEntitySelect}
        />

        {/* Colonna 2: Gestione permessi CRUD */}
        {selectedEntity ? (
          <PermissionsSection
            entity={selectedEntity}
            permissions={permissions}
            tenants={tenants}
            bulkMode={bulkMode}
            selectedActions={selectedActions}
            onPermissionUpdate={updatePermission}
            onTenantChange={handleTenantChange}
            onActionToggle={toggleActionSelection}
            onSelectAllActions={selectAllActions}
            onClearActionSelection={clearActionSelection}
            onBulkScopeApply={applyBulkScope}
            onBulkTenantsApply={applyBulkTenants}
          />
        ) : (
          <div className="bg-white border-r border-gray-200 flex items-center justify-center">
            <p className="text-gray-500">Seleziona un'entit per gestire i permessi</p>
          </div>
        )}

        {/* Colonna 3: Gestione campi specifici */}
        {selectedEntity ? (
          <FieldsSection
            entity={selectedEntity}
            selectedAction={selectedAction}
            permissions={permissions}
            bulkMode={bulkMode}
            selectedActions={selectedActions}
            onFieldToggle={handleFieldToggle}
            onBulkFieldsApply={applyBulkFields}
          />
        ) : (
          <div className="bg-white flex items-center justify-center">
            <p className="text-gray-500">Seleziona un'entit per gestire i campi</p>
          </div>
        )}

        {/* Colonna 4: Informazioni aggiuntive o preview */}
        <div className="bg-white border-l border-gray-200 p-4">
          <div className="text-center text-gray-500">
            <h4 className="font-medium mb-2">Riepilogo Permessi</h4>
            <div className="space-y-2 text-sm">
              <p>Entit totali: {entities.length}</p>
              <p>Permessi attivi: {permissions.length}</p>
              {selectedEntity && (
                <>
                  <p>Entit selezionata: {selectedEntity.displayName}</p>
                  <p>Campi disponibili: {selectedEntity.fields.length}</p>
                </>
              )}
              {bulkMode && (
                <p>Azioni selezionate: {selectedActions.size}</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OptimizedPermissionManagerRefactored;

import React from 'react';
import { 
  Building,
  Check,
  User,
  Users,
  X
} from 'lucide-react';
import { EntityDefinition, EntityPermission } from '../../../services/advancedPermissions';
import { Tenant } from '../../../hooks/useTenants';
import { PERMISSION_ACTIONS, PERMISSION_SCOPES } from './constants';
import { getPermission } from './utils';

interface PermissionsSectionProps {
  entity: EntityDefinition;
  permissions: EntityPermission[];
  tenants: Tenant[];
  bulkMode: boolean;
  selectedActions: Set<string>;
  onPermissionUpdate: (entity: string, action: string, scope: 'all' | 'tenant' | 'own' | 'none') => void;
  onTenantChange: (entity: string, action: string, tenantId: number, selected: boolean) => void;
  onActionToggle: (action: string) => void;
  onSelectAllActions: () => void;
  onClearActionSelection: () => void;
  onBulkScopeApply: (scope: 'all' | 'tenant' | 'own' | 'none') => void;
  onBulkTenantsApply: (tenantIds: number[], add: boolean) => void;
}

const PermissionsSection: React.FC<PermissionsSectionProps> = ({
  entity,
  permissions,
  tenants,
  bulkMode,
  selectedActions,
  onPermissionUpdate,
  onTenantChange,
  onActionToggle,
  onSelectAllActions,
  onClearActionSelection,
  onBulkScopeApply,
  onBulkTenantsApply
}) => {
  const getScopeIcon = (scope: string) => {
    switch (scope) {
      case 'all': return <Users className="w-4 h-4" />;
      case 'tenant': return <Building className="w-4 h-4" />;
      case 'own': return <User className="w-4 h-4" />;
      default: return <X className="w-4 h-4" />;
    }
  };

  const getScopeColor = (scope: string) => {
    switch (scope) {
      case 'all': return 'text-purple-600 bg-purple-50 border-purple-200';
      case 'tenant': return 'text-blue-600 bg-blue-50 border-blue-200';
      case 'own': return 'text-green-600 bg-green-50 border-green-200';
      default: return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  const hasAllTenants = (permission: EntityPermission | undefined): boolean => {
    if (!permission?.tenantIds) return false;
    return tenants.every(tenant => permission.tenantIds!.includes(tenant.id));
  };

  return (
    <div className="bg-white border-r border-gray-200 h-full flex flex-col">
      {/* Header generale con controlli */}
      <div className={`p-4 border-b border-gray-200 ${bulkMode ? 'bg-orange-50' : 'bg-gray-50'}`}>
        <div className="flex items-center justify-between mb-3">
          <h4 className="font-medium text-gray-900">
            {bulkMode ? 'Modalit Multipla' : 'Permessi Azioni'}
          </h4>
          <div className="flex space-x-2">
            <button
              type="button"
              onClick={onSelectAllActions}
              className="text-xs px-2 py-1 bg-blue-100 text-blue-700 rounded hover:bg-blue-200"
            >
              Seleziona Tutto
            </button>
            <button
              type="button"
              onClick={onClearActionSelection}
              className="text-xs px-2 py-1 bg-gray-100 text-gray-700 rounded hover:bg-gray-200"
            >
              Deseleziona
            </button>
          </div>
        </div>

        {/* Controlli specifici per modalit bulk */}
        {bulkMode && (
          <>
            {/* Controlli bulk scope */}
            <div className="space-y-2">
              <p className="text-xs text-gray-600">Applica scope alle azioni selezionate:</p>
              <div className="flex flex-wrap gap-1">
                {PERMISSION_SCOPES.map((scope) => (
                  <button
                    key={scope.name}
                    type="button"
                    onClick={() => onBulkScopeApply(scope.name as any)}
                    disabled={selectedActions.size === 0}
                    className={`flex items-center space-x-1 px-2 py-1 rounded text-xs border transition-colors disabled:opacity-50 ${getScopeColor(scope.name)}`}
                  >
                    {getScopeIcon(scope.name)}
                    <span>{scope.displayName}</span>
                  </button>
                ))}
              </div>
            </div>

            {/* Controlli bulk tenant */}
            {tenants.length > 0 && (
              <div className="mt-3 pt-3 border-t border-orange-200">
                <p className="text-xs text-gray-600 mb-2">Gestione tenant per azioni selezionate:</p>
                <div className="flex space-x-2">
                  <button
                    type="button"
                    onClick={() => onBulkTenantsApply(tenants.map(t => t.id), true)}
                    disabled={selectedActions.size === 0}
                    className="text-xs px-2 py-1 bg-green-100 text-green-700 rounded hover:bg-green-200 disabled:opacity-50"
                  >
                    Aggiungi Tutti
                  </button>
                  <button
                    type="button"
                    onClick={() => onBulkTenantsApply(tenants.map(t => t.id), false)}
                    disabled={selectedActions.size === 0}
                    className="text-xs px-2 py-1 bg-red-100 text-red-700 rounded hover:bg-red-200 disabled:opacity-50"
                  >
                    Rimuovi Tutti
                  </button>
                </div>
              </div>
            )}
          </>
        )}
      </div>

      {/* Lista azioni */}
      <div className="flex-1 overflow-y-auto p-4">
        <div className="space-y-3">
          {PERMISSION_ACTIONS.map((action) => {
            const permission = getPermission(permissions, entity.name, action.name);
            const currentScope = permission?.scope || 'none';
            const isSelected = selectedActions.has(action.name);

            return (
              <div
                key={action.name}
                className={`border rounded-lg p-3 transition-colors ${
                  bulkMode && isSelected 
                    ? 'border-orange-300 bg-orange-50' 
                    : 'border-gray-200 hover:border-gray-300'
                }`}
              >
                <div className="flex items-center justify-between mb-2">
                  <div className="flex items-center space-x-2">
                    {bulkMode && (
                      <input
                        type="checkbox"
                        checked={isSelected}
                        onChange={() => onActionToggle(action.name)}
                        className="rounded border-gray-300 text-orange-600 focus:ring-orange-500"
                      />
                    )}
                    <action.icon className="w-4 h-4 text-gray-600" />
                    <span className="font-medium text-gray-900">{action.displayName}</span>
                  </div>
                  <div className={`flex items-center space-x-1 px-2 py-1 rounded text-xs border ${getScopeColor(currentScope)}`}>
                    {getScopeIcon(currentScope)}
                    <span>{PERMISSION_SCOPES.find(s => s.name === currentScope)?.displayName || 'Nessuno'}</span>
                  </div>
                </div>

                {/* Controlli scope */}
                {!bulkMode && (
                  <div className="flex flex-wrap gap-1 mb-2">
                    {PERMISSION_SCOPES.map((scope) => (
                      <button
                        key={scope.name}
                        type="button"
                        onClick={() => onPermissionUpdate(entity.name, action.name, scope.name as any)}
                        className={`flex items-center space-x-1 px-2 py-1 rounded text-xs border transition-colors ${
                          currentScope === scope.name 
                            ? getScopeColor(scope.name)
                            : 'text-gray-600 bg-white border-gray-200 hover:bg-gray-50'
                        }`}
                      >
                        {getScopeIcon(scope.name)}
                        <span>{scope.displayName}</span>
                      </button>
                    ))}
                  </div>
                )}

                {/* Gestione tenant per scope 'tenant' */}
                {permission?.scope === 'tenant' && tenants.length > 0 && !bulkMode && (
                  <div className="mt-2 pt-2 border-t border-gray-200">
                    <div className="flex items-center justify-between mb-2">
                      <p className="text-xs text-gray-600">Tenant autorizzati:</p>
                      <button
                        type="button"
                        onClick={() => {
                          const allSelected = hasAllTenants(permission);
                          tenants.forEach(tenant => {
                            onTenantChange(entity.name, action.name, tenant.id, !allSelected);
                          });
                        }}
                        className="text-xs text-blue-600 hover:text-blue-800"
                      >
                        {hasAllTenants(permission) ? 'Deseleziona tutti' : 'Seleziona tutti'}
                      </button>
                    </div>
                    <div className="grid grid-cols-2 gap-1">
                      {tenants.map((tenant) => {
                        const isSelected = permission.tenantIds?.includes(tenant.id) || false;
                        return (
                          <label
                            key={tenant.id}
                            className="flex items-center space-x-2 text-xs cursor-pointer hover:bg-gray-50 p-1 rounded"
                          >
                            <input
                              type="checkbox"
                              checked={isSelected}
                              onChange={(e) => onTenantChange(entity.name, action.name, tenant.id, e.target.checked)}
                              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                            />
                            <span className="truncate">{tenant.name}</span>
                          </label>
                        );
                      })}
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default PermissionsSection;

