import { EntityPermission } from '../../../services/advancedPermissions';
import { PERMISSION_ACTIONS } from './constants';

/**
 * Trova un permesso specifico nell'array dei permessi
 */
export const getPermission = (
  permissions: EntityPermission[], 
  entity: string, 
  action: string
): EntityPermission | undefined => {
  return (Array.isArray(permissions) ? permissions : [])
    .find(p => p.entity === entity && p.action === action);
};

/**
 * Aggiorna o crea un permesso nell'array
 */
export const updatePermissionInArray = (
  permissions: EntityPermission[],
  entity: string,
  action: string,
  scope: 'all' | 'tenant' | 'own' | 'none',
  fields?: string[]
): EntityPermission[] => {
  const currentPermissions = Array.isArray(permissions) ? permissions : [];
  const newPermissions = [...currentPermissions];
  const existingIndex = newPermissions.findIndex(p => p.entity === entity && p.action === action);

  if (scope === 'none') {
    if (existingIndex !== -1) {
      newPermissions.splice(existingIndex, 1);
    }
  } else {
    const existingPermission = existingIndex !== -1 ? newPermissions[existingIndex] : null;
    const permission: EntityPermission = {
      entity,
      action: action as 'read' | 'create' | 'update' | 'delete',
      scope,
      fields: fields,
      // Mantieni i tenantIds esistenti se il scope  'tenant'
      tenantIds: scope === 'tenant' ? (existingPermission?.tenantIds || []) : undefined
    };

    if (existingIndex !== -1) {
      newPermissions[existingIndex] = permission;
    } else {
      newPermissions.push(permission);
    }
  }

  return newPermissions;
};

/**
 * Aggiorna i campi di un permesso specifico
 */
export const updatePermissionFields = (
  permissions: EntityPermission[],
  entity: string,
  action: string,
  fieldId: string,
  add: boolean = true
): EntityPermission[] => {
  const currentPermissions = Array.isArray(permissions) ? permissions : [];
  const newPermissions = [...currentPermissions];
  const permissionIndex = newPermissions.findIndex(p => p.entity === entity && p.action === action);

  if (permissionIndex !== -1) {
    const permission = newPermissions[permissionIndex];
    const currentFields = permission.fields || [];

    if (add) {
      if (!currentFields.includes(fieldId)) {
        permission.fields = [...currentFields, fieldId];
      }
    } else {
      permission.fields = currentFields.filter(f => f !== fieldId);
    }
  }

  return newPermissions;
};

/**
 * Aggiorna i tenant di un permesso specifico
 */
export const updatePermissionTenants = (
  permissions: EntityPermission[],
  entity: string,
  action: string,
  tenantId: number,
  selected: boolean
): EntityPermission[] => {
  const currentPermissions = Array.isArray(permissions) ? permissions : [];
  const newPermissions = [...currentPermissions];
  const permissionIndex = newPermissions.findIndex(p => p.entity === entity && p.action === action);

  if (permissionIndex !== -1) {
    const permission = newPermissions[permissionIndex];
    const currentTenantIds = permission.tenantIds || [];

    if (selected) {
      // Aggiungi tenant se non gi presente
      if (!currentTenantIds.includes(tenantId)) {
        permission.tenantIds = [...currentTenantIds, tenantId];
      }
    } else {
      // Rimuovi tenant
      permission.tenantIds = currentTenantIds.filter(id => id !== tenantId);
    }
  }

  return newPermissions;
};

/**
 * Applica operazioni bulk sui permessi
 */
export const applyBulkPermissions = (
  permissions: EntityPermission[],
  entity: string,
  selectedActions: Set<string>,
  operation: 'scope' | 'fields' | 'tenants',
  value: any,
  add: boolean = true
): EntityPermission[] => {
  let newPermissions = [...permissions];

  selectedActions.forEach(action => {
    switch (operation) {
      case 'scope':
        newPermissions = updatePermissionInArray(newPermissions, entity, action, value);
        break;

      case 'fields':
        const fieldIds = Array.isArray(value) ? value : [value];
        fieldIds.forEach(fieldId => {
          newPermissions = updatePermissionFields(newPermissions, entity, action, fieldId, add);
        });
        break;

      case 'tenants':
        const tenantIds = Array.isArray(value) ? value : [value];
        tenantIds.forEach(tenantId => {
          newPermissions = updatePermissionTenants(newPermissions, entity, action, tenantId, add);
        });
        break;
    }
  });

  return newPermissions;
};

/**
 * Seleziona tutte le azioni disponibili
 */
export const getAllActionNames = (): Set<string> => {
  return new Set(PERMISSION_ACTIONS.map(action => action.name));
};

/**
 * Verifica se tutti i tenant sono selezionati per un permesso
 */
export const hasAllTenants = (
  permission: EntityPermission | undefined,
  allTenantIds: number[]
): boolean => {
  if (!permission?.tenantIds) return false;
  return allTenantIds.every(id => permission.tenantIds!.includes(id));
};

/**
 * Filtra le entit in base al termine di ricerca
 */
export const filterEntities = <T extends { name: string; displayName: string }>(
  entities: T[],
  searchTerm: string
): T[] => {
  if (!searchTerm.trim()) return entities;

  const term = searchTerm.toLowerCase();
  return entities.filter(entity =>
    entity.displayName.toLowerCase().includes(term) ||
    entity.name.toLowerCase().includes(term)
  );
};

import { lazy, Suspense } from 'react';
import { LoadingFallback } from '../ui/LoadingFallback';
import { ErrorBoundary } from '../ui/ErrorBoundary';

// Lazy load the heavy ScheduleEventModal component
const ScheduleEventModalComponent = lazy(() => import('./ScheduleEventModal'));

// Props interface (re-export from the original component)
export interface ScheduleEventModalProps {
  trainings: any[];
  trainers: any[];
  companies: any[];
  employees: any[];
  existingEvent?: any;
  onClose: () => void;
  onSuccess: () => void;
  initialDate?: string;
  initialTime?: { start: string; end: string; };
}

// Lazy wrapper component with error boundary and loading fallback
const ScheduleEventModalLazy: React.FC<ScheduleEventModalProps> = (props) => {
  return (
    <ErrorBoundary fallback={<div>Errore nel caricamento del modal</div>}>
      <Suspense fallback={<LoadingFallback message="Caricamento modal..." />}>
        <ScheduleEventModalComponent {...props} />
      </Suspense>
    </ErrorBoundary>
  );
};

export default ScheduleEventModalLazy;

import React, { useState, useEffect, useCallback, useMemo } from "react";
import { Button } from "../../design-system/atoms/Button";
import { create, update } from '../../services/apiClient';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Company } from '../../types';

// Import modular components
import {
  CourseDetailsForm,
  CompanyEmployeeSelector,
  AttendanceManager,
  DocumentManager,
  DateTimeManager
} from './components';

interface Person {
  id: string | number;
  firstName: string;
  lastName: string;
  companyId: string | number;
  company_id?: string | number;
  company?: { id: string | number; name: string };
  email?: string;
  position?: string;
}

interface Trainer {
  id: string | number;
  firstName: string;
  lastName: string;
  certifications?: string[];
}

interface Training {
  id: string | number;
  title: string;
  certifications?: string[];
  duration?: string | number;
}

interface ScheduleEventModalProps {
  trainings: Training[];
  trainers: Trainer[];
  companies: Company[];
  persons: Person[];
  existingEvent?: any;
  onClose: () => void;
  onSuccess: () => void;
  initialDate?: string;
  initialTime?: { start: string; end: string; };
}

type DateEntry = { 
  date: string; 
  start: string; 
  end: string; 
  trainerId: string | number; 
  coTrainerId: string | number 
};

const DELIVERY_MODES = [
  { value: 'in-person', label: 'In presenza' },
  { value: 'online', label: 'Online' },
  { value: 'hybrid', label: 'Ibrido' },
  { value: 'blended', label: 'Blended' }
];

export default function ScheduleEventModal({ 
  trainings, 
  trainers, 
  companies, 
  persons, 
  existingEvent = {}, 
  onClose, 
  onSuccess, 
  initialDate, 
  initialTime 
}: ScheduleEventModalProps) {

  // Core state
  const isEditing = existingEvent && Object.keys(existingEvent).length > 0;
  const [currentStep, setCurrentStep] = useState<number>(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [hasScheduled, setHasScheduled] = useState(isEditing);
  const [scheduleId, setScheduleId] = useState<string | number | null>(existingEvent?.id || null);

  // Form data state
  const initialFormData = useMemo(() => {
    if (isEditing) {
      return {
        training_id: existingEvent.training_id || existingEvent.course?.id || "",
        trainer_id: "",
        co_trainer_id: "",
        dates: (existingEvent.dates || []).map((d: any) => ({
          date: d.date,
          start: d.start,
          end: d.end,
          trainerId: d.trainer_id || d.trainerId,
          coTrainerId: d.co_trainer_id || d.coTrainerId,
        })),
        location: existingEvent.location || "",
        max_participants: existingEvent.max_participants || 20,
        notes: existingEvent.notes || "",
        delivery_mode: existingEvent.delivery_mode || "",
      };
    }

    return {
      training_id: "",
      trainer_id: "",
      co_trainer_id: "",
      dates: [{
        date: initialDate || new Date().toISOString().split("T")[0],
        start: initialTime?.start || "09:00",
        end: initialTime?.end || "13:00",
        trainerId: "",
        coTrainerId: "",
      }],
      location: "",
      max_participants: 20,
      notes: "",
      delivery_mode: "",
    };
  }, [existingEvent, isEditing, initialDate, initialTime]);

  const [formData, setFormData] = useState(initialFormData);

  // Selection state
  const [selectedCompanies, setSelectedCompanies] = useState<(string | number)[]>(
    existingEvent?.company_ids || existingEvent?.companies?.map((c: any) => c.id) || []
  );
  const [selectedPersons, setSelectedPersons] = useState<(string | number)[]>(
    existingEvent?.employee_ids || existingEvent?.employees?.map((e: any) => e.id) || []
  );

  // Attendance state
  const [attendance, setAttendance] = useState<Record<number, (string | number)[]>>({});
  const [status, setStatus] = useState('Preventivo');

  // Search states
  const [courseSearch, setCourseSearch] = useState('');
  const [companySearch, setCompanySearch] = useState('');
  const [personSearch, setPersonSearch] = useState('');
  const [personTab, setPersonTab] = useState<string | number>('');
  const [selectedDayIdx, setSelectedDayIdx] = useState(0);
  const [showStatusMenu, setShowStatusMenu] = useState(false);

  // Computed values
  const selectedCourse = useMemo(() => 
    trainings.find(t => t.id === formData.training_id), 
    [trainings, formData.training_id]
  );

  const requiredCerts = useMemo(() => 
    selectedCourse?.certifications || [], 
    [selectedCourse]
  );

  const filteredTrainers = useMemo(() => 
    trainers.filter(trainer => 
      requiredCerts.length === 0 || 
      requiredCerts.every(cert => trainer.certifications?.includes(cert))
    ), 
    [trainers, requiredCerts]
  );

  const coTrainerOptions = useMemo(() => 
    filteredTrainers.filter(t => t.id !== formData.trainer_id), 
    [filteredTrainers, formData.trainer_id]
  );

  // Step configuration
  const stepItems = useMemo(() => [
    { label: "Dettagli", step: 0, enabled: true },
    { label: "Partecipanti", step: 1, enabled: true },
    { label: "Presenti", step: 2, enabled: hasScheduled || isEditing },
    { label: "Documenti", step: 3, enabled: hasScheduled || isEditing },
  ], [hasScheduled, isEditing]);

  // Utility functions
  const normalizeAttendanceData = useCallback((attendanceData: any): Record<number, (string | number)[]> => {
    if (!attendanceData) return {};

    if (Array.isArray(attendanceData)) {
      return attendanceData.reduce((acc, entry, idx) => {
        acc[idx] = entry.employee_ids || [];
        return acc;
      }, {} as Record<number, (string | number)[]>);
    }

    if (typeof attendanceData === 'object') {
      const result: Record<number, (string | number)[]> = {};
      Object.keys(attendanceData).forEach((key, idx) => {
        const session = attendanceData[key];
        if (session && session.attendees) {
          const presentIds = Object.values(session.attendees)
            .filter((a: any) => a.attended)
            .map((a: any) => a.id);
          result[idx] = presentIds;
        } else if (session && session.employee_ids) {
          result[idx] = session.employee_ids;
        }
      });
      return result;
    }

    return {};
  }, []);

  // Form handlers
  const handleFormDataChange = useCallback((field: string, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  }, []);

  // Utility functions
  const getCompanyName = useCallback((companyId: any) => {
    const company = companies.find(c => c.id === companyId);
    return company?.name || 'Azienda sconosciuta';
  }, [companies]);

  const getPersonIdsForCompany = useCallback((companyId: string) => {
    return persons
      .filter(person => (person.company_id || person.companyId) === companyId)
      .map(person => person.id);
  }, [persons]);

  const formatDate = useCallback((isoDate: string) => {
    return new Date(isoDate).toLocaleDateString('it-IT');
  }, []);

  // Company/Employee handlers
  const handleCompanyToggle = useCallback((companyId: string | number) => {
    setSelectedCompanies(prev => 
      prev.includes(companyId) 
        ? prev.filter(id => id !== companyId)
        : [...prev, companyId]
    );
  }, []);

  const handlePersonToggle = useCallback((personId: string | number) => {
    setSelectedPersons(prev => 
      prev.includes(personId) 
        ? prev.filter(id => id !== personId)
        : [...prev, personId]
    );
  }, []);

  const handleSelectAllPersons = useCallback((companyId: string | number) => {
    const companyPersonIds = getPersonIdsForCompany(String(companyId));
    setSelectedPersons(prev => [...new Set([...prev, ...companyPersonIds])]);
  }, [getPersonIdsForCompany]);

  const handleDeselectAllPersons = useCallback((companyId: string | number) => {
    const companyPersonIds = getPersonIdsForCompany(String(companyId));
    setSelectedPersons(prev => prev.filter(id => !companyPersonIds.includes(id)));
  }, [getPersonIdsForCompany]);

  // Attendance handlers
  const handleAttendanceChange = useCallback((dateIdx: number, personId: string | number, isPresent: boolean) => {
    setAttendance(prev => {
      const newAttendance = { ...prev };
      if (!newAttendance[dateIdx]) {
        newAttendance[dateIdx] = [];
      }

      if (isPresent) {
        if (!newAttendance[dateIdx].includes(personId)) {
          newAttendance[dateIdx] = [...newAttendance[dateIdx], personId];
        }
      } else {
        newAttendance[dateIdx] = newAttendance[dateIdx].filter(id => id !== personId);
      }

      return newAttendance;
    });
  }, []);

  const handleSelectAllForDate = useCallback((dateIdx: number) => {
    setAttendance(prev => ({
      ...prev,
      [dateIdx]: [...selectedPersons]
    }));
  }, [selectedPersons]);

  const handleSelectNoneForDate = useCallback((dateIdx: number) => {
    setAttendance(prev => ({
      ...prev,
      [dateIdx]: []
    }));
  }, []);

  // Validation
  const validateAll = useCallback(() => {
    if (!formData.training_id) {
      setError('Seleziona un corso');
      return false;
    }
    if (!formData.location) {
      setError('Inserisci il luogo del corso');
      return false;
    }
    if (formData.dates.length === 0) {
      setError('Aggiungi almeno una data');
      return false;
    }
    if (selectedCompanies.length === 0) {
      setError('Seleziona almeno un\'azienda');
      return false;
    }
    if (selectedPersons.length === 0) {
      setError('Seleziona almeno un partecipante');
      return false;
    }
    setError(null);
    return true;
  }, [formData, selectedCompanies, selectedPersons]);

  // Navigation handlers
  const handleNext = useCallback(() => {
    setError(null);
    setCurrentStep(s => s + 1);
  }, []);

  const handleBack = useCallback(() => {
    setError(null);
    setCurrentStep(s => s - 1);
  }, []);

  // Build schedule payload
  const buildSchedulePayload = useCallback(() => {
    const dates: string[] = formData.dates.map((d: DateEntry) => d.date);
    const startDateStr = dates.reduce((a, b) => (a < b ? a : b));
    const endDateStr = dates.reduce((a, b) => (a > b ? a : b));

    const firstDate = formData.dates.find((d: DateEntry) => d.date === startDateStr);
    const lastDate = formData.dates.find((d: DateEntry) => d.date === endDateStr);

    const startDateTimeLocal = `${startDateStr}T${firstDate?.start || '09:00'}:00`;
    const endDateTimeLocal = `${endDateStr}T${lastDate?.end || '17:00'}:00`;
    const startDateTimeISO = new Date(startDateTimeLocal).toISOString();
    const endDateTimeISO = new Date(endDateTimeLocal).toISOString();

    const attendanceArr = formData.dates.map((dt: DateEntry, idx: number) => ({
      date: dt.date,
      employee_ids: attendance[idx] || []
    }));

    const validCompanyIds = selectedCompanies.filter(id => id !== null && id !== undefined);
    const validPersonIds = selectedPersons.filter(id => id !== null && id !== undefined);

    const schedulePayload = {
      courseId: formData.training_id,
      start_date: startDateTimeISO,
      end_date: endDateTimeISO,
      location: formData.location,
      max_participants: formData.max_participants,
      notes: formData.notes,
      delivery_mode: formData.delivery_mode,
      dates: formData.dates.map((dt: DateEntry) => ({
        date: dt.date,
        start: dt.start,
        end: dt.end,
        trainer_id: dt.trainerId,
        co_trainer_id: dt.coTrainerId,
      })),
      companies: validCompanyIds.map(id => ({ companyId: String(id) })),
      company_ids: validCompanyIds.map(id => String(id)),
      enrollments: validPersonIds.map(id => ({ employeeId: String(id) })),
      employee_ids: validPersonIds.map(id => String(id)),
      attendance: attendanceArr,
      status,
    };

    if (isEditing && scheduleId) {
      return { ...schedulePayload, id: scheduleId };
    }

    return schedulePayload;
  }, [formData, scheduleId, isEditing, selectedCompanies, selectedPersons, attendance, status]);

  // Main schedule handler
  const handleSchedule = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      if (!validateAll()) return;

      const schedulePayload = buildSchedulePayload();
      let result: { id: string | number };

      if (isEditing && scheduleId) {
        result = await update('schedules', String(scheduleId), schedulePayload) as { id: string | number };
      } else {
        result = await create('schedules', schedulePayload) as { id: string | number };
        setScheduleId(result?.id);
      }

      setHasScheduled(true);
      toast.success('Corso programmato con successo!', { 
        position: 'top-right', 
        autoClose: 2500 
      });

      if (onSuccess) onSuccess();
    } catch (err: any) {
      setError(err.message || 'Errore durante la programmazione del corso');
    } finally {
      setLoading(false);
    }
  }, [validateAll, buildSchedulePayload, isEditing, scheduleId, onSuccess]);

  // Initialize attendance data
  useEffect(() => {
    if (existingEvent && Object.keys(existingEvent).length > 0 && existingEvent.attendance) {
      const normalizedAttendance = normalizeAttendanceData(existingEvent.attendance);
      if (Object.keys(normalizedAttendance).length > 0) {
        setAttendance(normalizedAttendance);
      }
    }
  }, [existingEvent, normalizeAttendanceData]);

  // Prevent body scrolling when modal is open
  useEffect(() => {
    document.body.style.overflow = 'hidden';
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, []);

  // Render step content
  const renderStep = () => {
    switch (currentStep) {
      case 0:
        return (
          <CourseDetailsForm
            trainings={trainings}
            trainers={trainers}
            formData={formData}
            onFormDataChange={handleFormDataChange}
            selectedCourse={selectedCourse}
            filteredTrainers={filteredTrainers}
            coTrainerOptions={coTrainerOptions}
            courseSearch={courseSearch}
            onCourseSearchChange={setCourseSearch}
            DELIVERY_MODES={DELIVERY_MODES}
          />
        );

      case 1:
        return (
          <CompanyEmployeeSelector
            companies={companies}
            persons={persons}
            selectedCompanies={selectedCompanies}
            selectedPersons={selectedPersons}
            onCompanyToggle={handleCompanyToggle}
            onPersonToggle={handlePersonToggle}
            onSelectAllPersons={handleSelectAllPersons}
            onDeselectAllPersons={handleDeselectAllPersons}
            getCompanyName={getCompanyName}
            getPersonIdsForCompany={getPersonIdsForCompany}
            companySearch={companySearch}
            onCompanySearchChange={setCompanySearch}
            personSearch={personSearch}
            onPersonSearchChange={setPersonSearch}
            personTab={personTab}
            onPersonTabChange={setPersonTab}
          />
        );

      case 2:
        return (
          <AttendanceManager
            dates={formData.dates}
            selectedPersons={selectedPersons}
            persons={persons}
            attendance={attendance}
            onAttendanceChange={handleAttendanceChange}
            onSelectAllForDate={handleSelectAllForDate}
            onSelectNoneForDate={handleSelectNoneForDate}
            getCompanyName={getCompanyName}
            formatDate={formatDate}
            selectedDayIdx={selectedDayIdx}
            onSelectedDayChange={setSelectedDayIdx}
          />
        );

      case 3:
        return (
          <DocumentManager
            status={status}
            onStatusChange={setStatus}
            selectedEmployees={selectedPersons}
            selectedCompanies={selectedCompanies}
            attendance={attendance}
            dates={formData.dates}
            showStatusMenu={showStatusMenu}
            onShowStatusMenuChange={setShowStatusMenu}
          />
        );

      default:
        return null;
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50 overflow-hidden">
      <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-2xl relative max-h-[95vh] overflow-hidden flex flex-col">

        {/* Header */}
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-bold">
            {isEditing ? "Modifica Programma Corso" : "Nuovo Programma Corso"}
          </h2>
          <button
            className="text-gray-400 hover:text-gray-600 text-2xl"
            onClick={onClose}
          >
            &times;
          </button>
        </div>

        {/* Navigation */}
        <div className="flex items-center justify-center space-x-2 mb-6">
          {stepItems.map((item, i) => (
            <React.Fragment key={i}>
              <div
                className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium cursor-pointer ${
                  i < currentStep
                    ? 'bg-green-500 text-white'
                    : i === currentStep
                    ? 'bg-blue-500 text-white'
                    : item.enabled
                    ? 'bg-gray-200 text-gray-600 hover:bg-gray-300'
                    : 'bg-gray-100 text-gray-400'
                }`}
                onClick={() => item.enabled && setCurrentStep(i)}
              >
                {i < currentStep ? '' : i + 1}
              </div>
              {i < stepItems.length - 1 && (
                <div
                  className={`w-12 h-1 ${
                    i < currentStep ? 'bg-green-500' : 'bg-gray-200'
                  }`}
                />
              )}
            </React.Fragment>
          ))}
        </div>

        <div className="text-center mb-4">
          <h3 className="text-lg font-semibold text-gray-800">
            {stepItems[currentStep]?.label}
          </h3>
          <p className="text-sm text-gray-600">
            Passo {currentStep + 1} di {stepItems.length}
          </p>
        </div>

        {/* Error Display */}
        {error && (
          <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
            {error}
          </div>
        )}

        {/* Step Content */}
        <div className="border-2 border-blue-300 rounded-lg p-4 mb-6 flex-1 overflow-y-auto">
          {renderStep()}
        </div>

        {/* Footer Actions */}
        <div className="flex justify-end space-x-2">
          <Button onClick={onClose} variant="secondary">
            Annulla
          </Button>

          {currentStep > 0 && (
            <Button onClick={handleBack} variant="secondary">
              Indietro
            </Button>
          )}

          {currentStep < 3 && (
            <Button onClick={handleNext}>
              Avanti
            </Button>
          )}

          <Button onClick={handleSchedule} disabled={loading}>
            {loading ? 'Salvando...' : (isEditing ? "Aggiorna Corso" : "Programma Corso")}
          </Button>
        </div>
      </div>
    </div>
  );
}

import React, { useState } from 'react';
import { X } from 'lucide-react';
import AsyncSelect from 'react-select/async';
import { apiGet } from '../../api/api';
import { Company } from '../../types';

interface Course {
  id: string;
  name: string;
  title: string;
}

interface Trainer {
  id: string;
  firstName: string;
  lastName: string;
}

interface ScheduleFormProps {
  initialData?: {
    id?: string;
    courseId?: string;
    trainerId?: string;
    companyId?: string;
    start_date?: string;
    end_date?: string;
    location?: string;
    max_participants?: number;
    notes?: string;
  };
  courses: Course[];
  trainers: Trainer[];
  companies: Company[];
  onSubmit: (data: any) => Promise<void>;
  onCancel: () => void;
}

const ScheduleForm: React.FC<ScheduleFormProps> = ({
  initialData,
  courses,
  trainers,
  companies,
  onSubmit,
  onCancel
}) => {
  const [formData, setFormData] = useState({
    courseId: initialData?.courseId || '',
    trainerId: initialData?.trainerId || '',
    companyId: initialData?.companyId || '',
    startDate: initialData?.start_date ? new Date(initialData.start_date).toISOString().split('T')[0] : '',
    startTime: initialData?.start_date ? new Date(initialData.start_date).toISOString().split('T')[1].substring(0, 5) : '09:00',
    endDate: initialData?.end_date ? new Date(initialData.end_date).toISOString().split('T')[0] : '',
    endTime: initialData?.end_date ? new Date(initialData.end_date).toISOString().split('T')[1].substring(0, 5) : '17:00',
    location: initialData?.location || '',
    maxParticipants: initialData?.max_participants || 20,
    notes: initialData?.notes || ''
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const data = {
      ...formData,
      startDate: new Date(`${formData.startDate}T${formData.startTime}`).toISOString(),
      endDate: new Date(`${formData.endDate}T${formData.endTime}`).toISOString(),
    };
    await onSubmit(data);
  };

  // Async load courses for react-select
  const loadCourses = async (inputValue: string) => {
    const data = await apiGet(`/courses?search=${encodeURIComponent(inputValue)}`) as any[];
    const options = data.map((course: any) => ({
      value: course.id,
      label: course.title || course.name || String(course.id)
    }));
    console.log('Course options:', options);
    return options;
  };

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
      <div className="relative top-20 mx-auto p-5 border w-full max-w-2xl shadow-lg rounded-lg bg-white">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold text-gray-800">
            {initialData?.id ? 'Modifica Programma Corso' : 'Nuovo Programma Corso'}
          </h2>
          <button
            onClick={onCancel}
            className="text-gray-400 hover:text-gray-500"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700">Corso</label>
            <AsyncSelect
              cacheOptions
              defaultOptions
              loadOptions={loadCourses}
              value={formData.courseId ? { value: formData.courseId, label: courses.find(c => c.id === formData.courseId)?.title || '' } : null}
              onChange={option => setFormData({ ...formData, courseId: option?.value || '' })}
              placeholder="Seleziona un corso"
              isClearable
              styles={{ menu: base => ({ ...base, zIndex: 9999 }) }}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Docente</label>
            <select
              value={formData.trainerId}
              onChange={(e) => setFormData({ ...formData, trainerId: e.target.value })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              required
            >
              <option value="">Seleziona un docente</option>
              {trainers.map((trainer) => (
                <option key={trainer.id} value={trainer.id}>
                  {trainer.firstName} {trainer.lastName}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Azienda</label>
            <select
              value={formData.companyId}
              onChange={(e) => setFormData({ ...formData, companyId: e.target.value })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            >
              <option value="">Seleziona un'azienda</option>
              {companies.map((company) => (
                <option key={company.id} value={company.id}>
                  {company.ragioneSociale}
                </option>
              ))}
            </select>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Data Inizio</label>
              <input
                type="date"
                value={formData.startDate}
                onChange={(e) => setFormData({ ...formData, startDate: e.target.value })}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Ora Inizio</label>
              <input
                type="time"
                value={formData.startTime}
                onChange={(e) => setFormData({ ...formData, startTime: e.target.value })}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Data Fine</label>
              <input
                type="date"
                value={formData.endDate}
                onChange={(e) => setFormData({ ...formData, endDate: e.target.value })}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                required
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Ora Fine</label>
              <input
                type="time"
                value={formData.endTime}
                onChange={(e) => setFormData({ ...formData, endTime: e.target.value })}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                required
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Luogo</label>
            <input
              type="text"
              value={formData.location}
              onChange={(e) => setFormData({ ...formData, location: e.target.value })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              placeholder="Inserisci il luogo"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">
              Numero Massimo Partecipanti
            </label>
            <input
              type="number"
              value={formData.maxParticipants}
              onChange={(e) => setFormData({ ...formData, maxParticipants: parseInt(e.target.value) })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              min="1"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Note</label>
            <textarea
              value={formData.notes}
              onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              rows={3}
              placeholder="Inserisci eventuali note"
            />
          </div>

          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={onCancel}
              className="px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
            >
              Annulla
            </button>
            <button
              type="submit"
              className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700"
            >
              {initialData?.id ? 'Salva Modifiche' : 'Crea Programma'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ScheduleForm;

import React, { useState, useEffect } from 'react';
import AsyncSelect from 'react-select/async';
import { apiGet } from '../../api/api';

const deliveryModes = [
  { value: 'in-person', label: 'In presenza' },
  { value: 'online', label: 'Online' },
  { value: 'hybrid', label: 'Ibrido' },
];

interface CourseOption {
  value: string;
  label: string;
  certifications: string[];
}

interface TrainerOption {
  value: string;
  label: string;
  certifications: string[];
}

const ScheduleTrainingWizard: React.FC = () => {
  const [step, setStep] = useState(1);
  const [form, setForm] = useState({
    course: null as CourseOption | null,
    trainer: null as TrainerOption | null,
    coTrainer: null as TrainerOption | null,
    requiredCerts: [] as string[],
    dates: [{ date: '', start: '', end: '' }],
    location: '',
    deliveryMode: '',
    notes: '',
    companies: [] as string[],
    persons: [] as string[],
  });
  const [companiesList, setCompaniesList] = useState<any[]>([]);
  const [personsList, setPersonsList] = useState<any[]>([]);
  const [personSearch, setPersonSearch] = useState('');

  // Load courses from backend
  const loadCourses = async (inputValue: string) => {
    const data = await apiGet(`/courses?search=${encodeURIComponent(inputValue)}`) as any[];
    return data.map((course: any) => ({
      value: course.id,
      label: course.title,
      certifications: course.certifications ? course.certifications.split(',').map((c: string) => c.trim()) : [],
    })) as CourseOption[];
  };

  // Load trainers from backend, filter by certifications if needed
  const loadTrainers = async (inputValue: string) => {
    const data = await apiGet(`/trainers?search=${encodeURIComponent(inputValue)}`) as any[];
    if (form.course && form.course.certifications && form.course.certifications.length > 0) {
      return data
        .filter((trainer: any) => Array.isArray(trainer.certifications) && form.course!.certifications.every((cert: string) => trainer.certifications.includes(cert)))
        .map((trainer: any) => ({
          value: trainer.id,
          label: `${trainer.firstName} ${trainer.lastName}`,
          certifications: trainer.certifications,
        })) as TrainerOption[];
    }
    return data.map((trainer: any) => ({
      value: trainer.id,
      label: `${trainer.firstName} ${trainer.lastName}`,
      certifications: trainer.certifications,
    })) as TrainerOption[];
  };

  // When course changes, update requiredCerts
  useEffect(() => {
    if (form.course && form.course.certifications) {
      setForm(f => ({ ...f, requiredCerts: form.course!.certifications }));
    } else {
      setForm(f => ({ ...f, requiredCerts: [] }));
    }
    // Reset trainer if course changes
    setForm(f => ({ ...f, trainer: null }));
  }, [form.course]);

  useEffect(() => {
    if (step === 2 && companiesList.length === 0) {
      apiGet('/companies')
        .then((data: any) => setCompaniesList(data))
        .catch(console.error);
    }
  }, [step, companiesList.length]);

  useEffect(() => {
    if (step === 3 && form.companies.length > 0) {
      apiGet(`/api/v1/persons?companyIds=${form.companies.join(',')}&roleType=EMPLOYEE`)
        .then((response: any) => setPersonsList(response.persons || []))
        .catch(console.error);
    }
  }, [step, form.companies]);

  const filteredPersons = personsList.filter((person: any) =>
    personSearch === '' ||
    person.firstName.toLowerCase().includes(personSearch.toLowerCase()) ||
    person.lastName.toLowerCase().includes(personSearch.toLowerCase()) ||
    person.email?.toLowerCase().includes(personSearch.toLowerCase())
  );

  return (
    <div className="max-w-2xl mx-auto bg-white rounded-2xl shadow-lg p-8">
      {/* Stepper */}
      <div className="flex justify-between mb-8">
        {[1, 2, 3].map((s) => (
          <div key={s} className="flex-1 text-center">
            <div className={`mx-auto w-8 h-8 rounded-full flex items-center justify-center font-bold text-lg ${step === s ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-500'}`}>{s}</div>
            <div className={`mt-2 text-sm font-medium ${step === s ? 'text-blue-600' : 'text-gray-500'}`}>{s === 1 ? 'Dettagli Evento' : s === 2 ? 'Aziende' : 'Persone'}</div>
          </div>
        ))}
      </div>

      {/* Step 1: Event Details */}
      {step === 1 && (
        <div className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Tipo di Corso</label>
            <AsyncSelect
              cacheOptions
              defaultOptions
              loadOptions={loadCourses}
              placeholder="Seleziona un corso"
              isClearable
              styles={{ menu: base => ({ ...base, zIndex: 9999 }) }}
              onChange={course => setForm(f => ({ ...f, course }))}
              value={form.course}
            />
          </div>
          {/* Required certifications (placeholder) */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Certificazioni Richieste</label>
            <div className="flex flex-wrap gap-2">
              {/* Render requiredCerts as badges */}
              {form.requiredCerts.length === 0 ? <span className="text-gray-400">Nessuna</span> : form.requiredCerts.map((cert: string) => (
                <span key={cert} className="bg-blue-100 text-blue-700 px-2 py-1 rounded text-xs font-medium">{cert}</span>
              ))}
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Docente</label>
            <AsyncSelect
              cacheOptions
              defaultOptions
              loadOptions={loadTrainers}
              placeholder="Seleziona un docente"
              isClearable
              styles={{ menu: base => ({ ...base, zIndex: 9999 }) }}
              onChange={trainer => setForm(f => ({ ...f, trainer }))}
              value={form.trainer}
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Co-docente</label>
            <AsyncSelect
              cacheOptions
              defaultOptions
              loadOptions={loadTrainers}
              placeholder="Seleziona un co-docente"
              isClearable
              styles={{ menu: base => ({ ...base, zIndex: 9999 }) }}
              onChange={coTrainer => setForm(f => ({ ...f, coTrainer }))}
              value={form.coTrainer}
            />
          </div>
          {/* Multi-date picker (placeholder) */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Date e Orari</label>
            {form.dates.map((d, i) => (
              <div key={i} className="flex gap-2 mb-2">
                <input type="date" className="border rounded px-2 py-1" value={d.date} onChange={e => setForm(f => { const dates = [...f.dates]; dates[i].date = e.target.value; return { ...f, dates }; })} />
                <input type="time" className="border rounded px-2 py-1" value={d.start} onChange={e => setForm(f => { const dates = [...f.dates]; dates[i].start = e.target.value; return { ...f, dates }; })} />
                <input type="time" className="border rounded px-2 py-1" value={d.end} onChange={e => setForm(f => { const dates = [...f.dates]; dates[i].end = e.target.value; return { ...f, dates }; })} />
                <button type="button" className="text-red-500" onClick={() => setForm(f => ({ ...f, dates: f.dates.filter((_, j) => j !== i) }))}></button>
              </div>
            ))}
            <button type="button" className="text-blue-600 hover:underline text-sm" onClick={() => setForm(f => ({ ...f, dates: [...f.dates, { date: '', start: '', end: '' }] }))}>+ Aggiungi data</button>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Luogo</label>
            <input type="text" className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500" value={form.location} onChange={e => setForm(f => ({ ...f, location: e.target.value }))} />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Modalit di erogazione</label>
            <select className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500" value={form.deliveryMode} onChange={e => setForm(f => ({ ...f, deliveryMode: e.target.value }))}>
              <option value="">Seleziona modalit</option>
              {deliveryModes.map(m => <option key={m.value} value={m.value}>{m.label}</option>)}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Note</label>
            <textarea className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500" rows={3} value={form.notes} onChange={e => setForm(f => ({ ...f, notes: e.target.value }))} />
          </div>
        </div>
      )}

      {/* Step 2: Companies */}
      {step === 2 && (
        <div className="space-y-6">
          <h2 className="text-lg font-semibold text-gray-800 mb-2">Seleziona Aziende</h2>
          <div className="bg-gray-50 rounded-lg p-4">
            {companiesList.length === 0 ? (
              <div className="text-gray-400">Nessuna azienda trovata.</div>
            ) : (
              companiesList.map(company => (
                <label key={company.id} className="flex items-center gap-3 py-2 border-b last:border-0 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={form.companies.includes(company.id)}
                    onChange={e => {
                      setForm(f => ({
                        ...f,
                        companies: e.target.checked
                          ? [...f.companies, company.id]
                          : f.companies.filter((id: string) => id !== company.id)
                      }));
                    }}
                  />
                  <div>
                    <div className="font-medium text-gray-800">{company.name}</div>
                    <div className="text-xs text-gray-500">
                      {company.employees_count || 0} persone
                      {company.industry && `  ${company.industry}`}
                    </div>
                  </div>
                </label>
              ))
            )}
          </div>
        </div>
      )}

      {/* Step 3: Persons */}
      {step === 3 && (
        <div className="space-y-6">
          <h2 className="text-lg font-semibold text-gray-800 mb-2">Seleziona Persone</h2>
          <input
            type="text"
            placeholder="Cerca persone..."
            className="mb-3 w-full border rounded px-3 py-2"
            value={personSearch}
            onChange={e => setPersonSearch(e.target.value)}
          />
          <button
            type="button"
            className="mb-2 px-3 py-1 rounded bg-gray-100 text-gray-700 text-sm"
            onClick={() => setForm(f => ({ ...f, persons: filteredPersons.map((person: any) => person.id) }))}
          >
            Seleziona Tutti
          </button>
          <div className="bg-gray-50 rounded-lg p-4 max-h-72 overflow-y-auto">
            {filteredPersons.length === 0 ? (
              <div className="text-gray-400">Nessuna persona trovata.</div>
            ) : (
              filteredPersons.map(person => (
                <label key={person.id} className="flex items-center gap-3 py-2 border-b last:border-0 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={form.persons.includes(person.id)}
                    onChange={e => {
                      setForm(f => ({
                        ...f,
                        persons: e.target.checked
                          ? [...f.persons, person.id]
                          : f.persons.filter((id: string) => id !== person.id)
                      }));
                    }}
                  />
                  <div>
                    <div className="font-medium text-gray-800">{person.firstName} {person.lastName}</div>
                    <div className="text-xs text-gray-500">{person.email}</div>
                    <div className="text-xs text-gray-400">{person.company?.name}</div>
                  </div>
                </label>
              ))
            )}
          </div>
        </div>
      )}

      {/* Step navigation */}
      <div className="flex justify-between mt-8">
        {step > 1 ? <button className="px-4 py-2 rounded border" onClick={() => setStep(s => s - 1)}>Indietro</button> : <span />}
        {step < 3 ? <button className="px-4 py-2 rounded bg-blue-600 text-white" onClick={() => setStep(s => s + 1)}>Avanti</button> : <button className="px-4 py-2 rounded bg-blue-600 text-white">Conferma</button>}
      </div>
    </div>
  );
};

export default ScheduleTrainingWizard;

import React from 'react';
import { Button } from '../../../design-system/atoms/Button';
import { Label } from '../../../design-system/atoms/Label';

interface Person {
  id: string | number;
  firstName: string;
  lastName: string;
  companyId: string | number;
  company_id?: string | number;
  company?: { id: string | number; name: string };
  email?: string;
}

interface DateEntry {
  date: string;
  start: string;
  end: string;
  trainerId: string | number;
  coTrainerId: string | number;
}

interface AttendanceManagerProps {
  dates: DateEntry[];
  selectedPersons: (string | number)[];
  persons: Person[];
  attendance: Record<number, (string | number)[]>;
  onAttendanceChange: (dateIdx: number, personId: string | number, isPresent: boolean) => void;
  onSelectAllForDate: (dateIdx: number) => void;
  onSelectNoneForDate: (dateIdx: number) => void;
  getCompanyName: (companyId: any) => string;
  formatDate: (isoDate: string) => string;
  selectedDayIdx: number;
  onSelectedDayChange: (idx: number) => void;
}

export const AttendanceManager: React.FC<AttendanceManagerProps> = ({
  dates,
  selectedPersons,
  persons,
  attendance,
  onAttendanceChange,
  onSelectAllForDate,
  onSelectNoneForDate,
  getCompanyName,
  formatDate,
  selectedDayIdx,
  onSelectedDayChange
}) => {
  return (
    <div className="space-y-4">
      <h3 className="font-semibold text-gray-700">Registrazione Presenze</h3>

      {/* Date Selector */}
      <div className="flex flex-wrap gap-2">
        {dates.map((dateEntry, idx) => (
          <button
            key={idx}
            type="button"
            onClick={() => onSelectedDayChange(idx)}
            className={`px-3 py-2 text-sm rounded border ${
              selectedDayIdx === idx
                ? 'bg-blue-600 text-white border-blue-600'
                : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-50'
            }`}
          >
            {formatDate(dateEntry.date)}
            <div className="text-xs">
              {dateEntry.start} - {dateEntry.end}
            </div>
          </button>
        ))}
      </div>

      {/* Attendance for Selected Date */}
      {dates[selectedDayIdx] && (
        <div className="border rounded p-4">
          <div className="flex justify-between items-center mb-4">
            <h4 className="font-medium">
              Presenze per {formatDate(dates[selectedDayIdx].date)}
            </h4>
            <div className="space-x-2">
              <Button
                type="button"
                size="sm"
                variant="secondary"
                onClick={() => onSelectAllForDate(selectedDayIdx)}
              >
                Tutti Presenti
              </Button>
              <Button
                type="button"
                size="sm"
                variant="secondary"
                onClick={() => onSelectNoneForDate(selectedDayIdx)}
              >
                Nessuno
              </Button>
            </div>
          </div>

          <div className="max-h-80 overflow-y-auto border rounded">
            {selectedPersons.length > 0 ? (
              persons
                .filter((person: Person) => selectedPersons.includes(person.id))
                .map((person: Person) => (
                  <div key={`${selectedDayIdx}-${person.id}`} className="flex items-center p-3 hover:bg-gray-50 border-b last:border-b-0">
                    <input
                      type="checkbox"
                      id={`attendance-${selectedDayIdx}-${person.id}`}
                      checked={(attendance[selectedDayIdx] || []).includes(person.id)}
                      onChange={(e) => onAttendanceChange(selectedDayIdx, person.id, e.target.checked)}
                      className="mr-3 w-4 h-4 accent-blue-600"
                    />
                    <div className="flex-1">
                      <div className="font-medium">
                        {person.firstName} {person.lastName}
                      </div>
                      <div className="text-sm text-gray-500">
                        {getCompanyName(person.company_id || person.companyId)}
                      </div>
                      {person.email && (
                        <div className="text-xs text-gray-400">{person.email}</div>
                      )}
                    </div>
                  </div>
                ))
            ) : (
              <div className="text-center py-8 text-gray-500">
                Nessun partecipante selezionato
              </div>
            )}
          </div>

          {/* Attendance Summary */}
          <div className="mt-3 text-right text-sm text-gray-600">
            Presenti: <span className="font-medium">{(attendance[selectedDayIdx] || []).length}</span> / {selectedPersons.length}
          </div>
        </div>
      )}
    </div>
  );
};

export default AttendanceManager;

import React, { useCallback } from 'react';
import { Button } from '../../../design-system/atoms/Button';
import { Input } from '../../../design-system/atoms/Input';
import { Label } from '../../../design-system/atoms/Label';
import { Company } from '../../../types';

interface Person {
  id: string | number;
  firstName: string;
  lastName: string;
  companyId: string | number;
  company_id?: string | number;
  company?: { id: string | number; name: string };
  email?: string;
  position?: string;
}

interface CompanyEmployeeSelectorProps {
  companies: Company[];
  persons: Person[];
  selectedCompanies: (string | number)[];
  selectedPersons: (string | number)[];
  onCompanyToggle: (companyId: string | number) => void;
  onPersonToggle: (personId: string | number) => void;
  onSelectAllPersons: (companyId: string | number) => void;
  onDeselectAllPersons: (companyId: string | number) => void;
  getCompanyName: (companyId: any) => string;
  getPersonIdsForCompany: (companyId: string) => (string | number)[];
  companySearch: string;
  onCompanySearchChange: (search: string) => void;
  personSearch: string;
  onPersonSearchChange: (search: string) => void;
  personTab: string | number;
  onPersonTabChange: (tab: string | number) => void;
}

export const CompanyEmployeeSelector: React.FC<CompanyEmployeeSelectorProps> = ({
  companies,
  persons,
  selectedCompanies,
  selectedPersons,
  onCompanyToggle,
  onPersonToggle,
  onSelectAllPersons,
  onDeselectAllPersons,
  getCompanyName,
  getPersonIdsForCompany,
  companySearch,
  onCompanySearchChange,
  personSearch,
  onPersonSearchChange,
  personTab,
  onPersonTabChange
}) => {
  const filteredCompanies = companies.filter(company =>
    company.name?.toLowerCase().includes(companySearch.toLowerCase()) ?? false
  );

  const getFilteredPersonsForCompany = useCallback((companyId: string | number) => {
    return persons.filter((person: Person) => {
      const personCompanyId = person.company_id || person.companyId;
      const matchesCompany = personCompanyId === companyId;
      const matchesSearch = personSearch === '' || 
        `${person.firstName} ${person.lastName}`.toLowerCase().includes(personSearch.toLowerCase());
      return matchesCompany && matchesSearch;
    });
  }, [persons, personSearch]);

  return (
    <div className="space-y-4">
      <h3 className="font-semibold text-gray-700">Seleziona Aziende e Partecipanti</h3>

      {/* Company Search */}
      <div>
        <Label>Cerca Aziende</Label>
        <Input
          type="text"
          placeholder="Cerca per nome azienda..."
          value={companySearch}
          onChange={(e) => onCompanySearchChange(e.target.value)}
        />
      </div>

      {/* Companies List */}
      <div className="border rounded max-h-48 overflow-y-auto">
        {filteredCompanies.map(company => {
          const companyPersons = getPersonIdsForCompany(String(company.id));
          const isSelected = selectedCompanies.includes(company.id);

          return (
            <div key={company.id} className="flex items-center p-2 border-b hover:bg-gray-50">
              <input
                type="checkbox"
                checked={isSelected}
                onChange={() => onCompanyToggle(company.id)}
                className="mr-3"
              />
              <div className="flex-1">
                <div className="font-medium">{company.name}</div>
                <div className="text-sm text-gray-500">
                  {companyPersons.length} dipendenti
                </div>
              </div>
            </div>
          );
        })}
      </div>

      {/* Selected Companies Summary */}
      {selectedCompanies.length > 0 && (
        <div className="bg-blue-50 p-3 rounded">
          <div className="font-medium text-sm mb-2">
            Aziende selezionate: {selectedCompanies.length}
          </div>
          <div className="text-sm text-gray-600">
            {selectedCompanies.map(companyId => getCompanyName(companyId)).join(', ')}
          </div>
        </div>
      )}

      {/* Person Selection */}
      {selectedCompanies.length > 0 && (
        <div className="space-y-3">
          <div>
            <Label>Cerca Dipendenti</Label>
            <Input
              type="text"
              placeholder="Cerca per nome dipendente..."
              value={personSearch}
              onChange={(e) => onPersonSearchChange(e.target.value)}
            />
          </div>

          {/* Company Tabs */}
          <div className="flex flex-wrap gap-2">
            {selectedCompanies.map(companyId => (
              <button
                key={companyId}
                type="button"
                onClick={() => onPersonTabChange(companyId)}
                className={`px-3 py-1 text-sm rounded ${
                  personTab === companyId
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                {getCompanyName(companyId)}
              </button>
            ))}
          </div>

          {/* Person List for Selected Company */}
          {personTab && (
            <div className="border rounded">
              <div className="p-2 bg-gray-50 border-b flex justify-between items-center">
                <span className="font-medium text-sm">
                  Dipendenti - {getCompanyName(personTab)}
                </span>
                <div className="space-x-2">
                  <Button
                    type="button"
                    size="sm"
                    variant="secondary"
                    onClick={() => onSelectAllPersons(personTab)}
                  >
                    Seleziona Tutti
                  </Button>
                  <Button
                    type="button"
                    size="sm"
                    variant="secondary"
                    onClick={() => onDeselectAllPersons(personTab)}
                  >
                    Deseleziona Tutti
                  </Button>
                </div>
              </div>

              <div className="max-h-64 overflow-y-auto">
                {getFilteredPersonsForCompany(personTab).map((person: Person) => (
                  <div key={person.id} className="flex items-center p-2 border-b hover:bg-gray-50">
                    <input
                      type="checkbox"
                      checked={selectedPersons.includes(person.id)}
                      onChange={() => onPersonToggle(person.id)}
                      className="mr-3"
                    />
                    <div className="flex-1">
                      <div className="font-medium text-sm">
                        {person.firstName} {person.lastName}
                      </div>
                      {person.email && (
                        <div className="text-xs text-gray-500">{person.email}</div>
                      )}
                      {person.position && (
                        <div className="text-xs text-gray-500">{person.position}</div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Selected Persons Summary */}
          <div className="bg-green-50 p-3 rounded">
            <div className="font-medium text-sm">
              Partecipanti selezionati: {selectedPersons.length}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CompanyEmployeeSelector;

import React from 'react';
import { Button } from '../../../design-system/atoms/Button';
import { Input } from '../../../design-system/atoms/Input';
import { Label } from '../../../design-system/atoms/Label';
import Select from 'react-select';

interface Training {
  id: string | number;
  title: string;
  certifications?: string[];
  duration?: string | number;
}

interface Trainer {
  id: string | number;
  firstName: string;
  lastName: string;
  certifications?: string[];
}

interface CourseDetailsFormProps {
  trainings: Training[];
  trainers: Trainer[];
  formData: {
    training_id: string | number;
    trainer_id: string | number;
    co_trainer_id: string | number;
    location: string;
    max_participants: number;
    notes: string;
    delivery_mode?: string;
  };
  onFormDataChange: (field: string, value: any) => void;
  selectedCourse: Training | undefined;
  filteredTrainers: Trainer[];
  coTrainerOptions: Trainer[];
  courseSearch: string;
  onCourseSearchChange: (search: string) => void;
  DELIVERY_MODES: Array<{ value: string; label: string }>;
}

export const CourseDetailsForm: React.FC<CourseDetailsFormProps> = ({
  trainings,
  trainers,
  formData,
  onFormDataChange,
  selectedCourse,
  filteredTrainers,
  coTrainerOptions,
  courseSearch,
  onCourseSearchChange,
  DELIVERY_MODES
}) => {
  const courseOptions = trainings.map((t: any) => ({ 
    value: t.id, 
    label: t.title, 
    ...t 
  }));

  const selectedCourseOption = courseOptions.find(opt => opt.value === formData.training_id) || null;

  const getTrainerName = (trainerId: string | number) => {
    const trainer = trainers.find(t => t.id === trainerId);
    return trainer ? `${trainer.firstName} ${trainer.lastName}` : '';
  };

  return (
    <div className="space-y-4">
      <h3 className="font-semibold text-gray-700">Dettagli del Corso</h3>

      {/* Course Selection */}
      <div>
        <Label>Corso *</Label>
        <div className="space-y-2">
          <Input
            type="text"
            placeholder="Cerca corso..."
            value={courseSearch}
            onChange={(e) => onCourseSearchChange(e.target.value)}
          />
          <Select
            value={selectedCourseOption}
            onChange={(option) => onFormDataChange('training_id', option ? option.value : '')}
            options={courseOptions.filter(opt => 
              courseSearch === '' || 
              opt.label.toLowerCase().includes(courseSearch.toLowerCase())
            )}
            placeholder="Seleziona un corso"
            isClearable
            isSearchable
          />
        </div>

        {selectedCourse && (
          <div className="mt-2 p-3 bg-blue-50 rounded border">
            <div className="text-sm">
              <div className="font-medium">{selectedCourse.title}</div>
              {selectedCourse.duration && (
                <div className="text-gray-600">Durata: {selectedCourse.duration} ore</div>
              )}
              {selectedCourse.certifications && selectedCourse.certifications.length > 0 && (
                <div className="text-gray-600">
                  Certificazioni richieste: {selectedCourse.certifications.join(', ')}
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Location */}
      <div>
        <Label>Luogo *</Label>
        <Input
          type="text"
          placeholder="Inserisci il luogo del corso"
          value={formData.location}
          onChange={(e) => onFormDataChange('location', e.target.value)}
        />
      </div>

      {/* Delivery Mode */}
      <div>
        <Label>Modalit di Erogazione</Label>
        <Select
          value={DELIVERY_MODES.find(mode => mode.value === formData.delivery_mode) || null}
          onChange={(option) => onFormDataChange('delivery_mode', option ? option.value : '')}
          options={DELIVERY_MODES}
          placeholder="Seleziona modalit"
          isClearable
        />
      </div>

      {/* Max Participants */}
      <div>
        <Label>Numero Massimo Partecipanti</Label>
        <Input
          type="number"
          min="1"
          max="100"
          value={formData.max_participants}
          onChange={(e) => onFormDataChange('max_participants', parseInt(e.target.value) || 20)}
        />
      </div>

      {/* Notes */}
      <div>
        <Label>Note</Label>
        <textarea
          className="w-full p-2 border rounded resize-none"
          rows={3}
          placeholder="Note aggiuntive sul corso..."
          value={formData.notes}
          onChange={(e) => onFormDataChange('notes', e.target.value)}
        />
      </div>

      {/* Trainer Information */}
      {filteredTrainers.length > 0 && (
        <div className="bg-green-50 p-3 rounded border">
          <div className="text-sm">
            <div className="font-medium text-green-800 mb-1">
              Formatori qualificati disponibili: {filteredTrainers.length}
            </div>
            <div className="text-green-600 text-xs">
              {filteredTrainers.slice(0, 3).map(t => `${t.firstName} ${t.lastName}`).join(', ')}
              {filteredTrainers.length > 3 && ` e altri ${filteredTrainers.length - 3}`}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CourseDetailsForm;

import React, { useCallback } from 'react';
import { Button } from '../../../design-system/atoms/Button';
import { Input } from '../../../design-system/atoms/Input';
import { Label } from '../../../design-system/atoms/Label';
import DatePicker from 'react-datepicker';
import { it } from 'date-fns/locale';
import Select from 'react-select';

interface DateEntry {
  date: string;
  start: string;
  end: string;
  trainerId: string | number;
  coTrainerId: string | number;
}

interface Trainer {
  id: string | number;
  firstName: string;
  lastName: string;
  certifications?: string[];
}

interface DateTimeManagerProps {
  dates: DateEntry[];
  trainers: Trainer[];
  filteredTrainers: Trainer[];
  coTrainerOptions: Trainer[];
  onUpdateDateTime: (idx: number, field: 'date' | 'start' | 'end' | 'trainerId' | 'coTrainerId', value: string) => void;
  onAddDateTime: () => void;
  onRemoveDateTime: (idx: number) => void;
  formatDate: (isoDate: string) => string;
  totalSelectedHours: number;
  courseDuration: number;
  hoursLeft: number;
}

export const DateTimeManager: React.FC<DateTimeManagerProps> = ({
  dates,
  trainers,
  filteredTrainers,
  coTrainerOptions,
  onUpdateDateTime,
  onAddDateTime,
  onRemoveDateTime,
  formatDate,
  totalSelectedHours,
  courseDuration,
  hoursLeft
}) => {
  const getTrainerName = useCallback((trainerId: string | number) => {
    const trainer = trainers.find(t => t.id === trainerId);
    return trainer ? `${trainer.firstName} ${trainer.lastName}` : '';
  }, [trainers]);

  return (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <h3 className="font-semibold text-gray-700">Date e Orari</h3>
        <Button type="button" onClick={onAddDateTime} variant="secondary" size="sm">
          + Aggiungi Data
        </Button>
      </div>

      {/* Hours Summary */}
      <div className="bg-blue-50 p-3 rounded border">
        <div className="text-sm">
          <span className="font-medium">Ore selezionate:</span> {totalSelectedHours}h
          {courseDuration > 0 && (
            <>
              <span className="mx-2">|</span>
              <span className="font-medium">Durata corso:</span> {courseDuration}h
              <span className="mx-2">|</span>
              <span className={`font-medium ${hoursLeft === 0 ? 'text-green-600' : 'text-orange-600'}`}>
                Ore rimanenti: {hoursLeft}h
              </span>
            </>
          )}
        </div>
      </div>

      {dates.map((dateEntry, idx) => (
        <div key={idx} className="border rounded p-4 bg-gray-50">
          <div className="flex justify-between items-center mb-3">
            <h4 className="font-medium">Sessione {idx + 1}</h4>
            {dates.length > 1 && (
              <Button
                type="button"
                onClick={() => onRemoveDateTime(idx)}
                variant="destructive"
                size="sm"
              >
                Rimuovi
              </Button>
            )}
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* Date */}
            <div>
              <Label>Data</Label>
              <DatePicker
                selected={new Date(dateEntry.date)}
                onChange={(date) => {
                  if (date) {
                    onUpdateDateTime(idx, 'date', date.toISOString().split('T')[0]);
                  }
                }}
                dateFormat="dd/MM/yyyy"
                locale={it}
                className="w-full p-2 border rounded"
                placeholderText="Seleziona data"
              />
            </div>

            {/* Time Range */}
            <div className="grid grid-cols-2 gap-2">
              <div>
                <Label>Ora Inizio</Label>
                <Input
                  type="time"
                  value={dateEntry.start}
                  onChange={(e) => onUpdateDateTime(idx, 'start', e.target.value)}
                />
              </div>
              <div>
                <Label>Ora Fine</Label>
                <Input
                  type="time"
                  value={dateEntry.end}
                  onChange={(e) => onUpdateDateTime(idx, 'end', e.target.value)}
                />
              </div>
            </div>

            {/* Main Trainer */}
            <div>
              <Label>Formatore Principale</Label>
              <Select
                value={filteredTrainers.find(t => t.id === dateEntry.trainerId) ? {
                  value: dateEntry.trainerId,
                  label: getTrainerName(dateEntry.trainerId)
                } : null}
                onChange={(option) => {
                  onUpdateDateTime(idx, 'trainerId', option ? String(option.value) : '');
                }}
                options={filteredTrainers.map(t => ({
                  value: t.id,
                  label: `${t.firstName} ${t.lastName}`
                }))}
                placeholder="Seleziona formatore"
                isClearable
              />
            </div>

            {/* Co-Trainer */}
            <div>
              <Label>Co-Formatore (Opzionale)</Label>
              <Select
                value={coTrainerOptions.find(t => t.id === dateEntry.coTrainerId) ? {
                  value: dateEntry.coTrainerId,
                  label: getTrainerName(dateEntry.coTrainerId)
                } : null}
                onChange={(option) => {
                  onUpdateDateTime(idx, 'coTrainerId', option ? String(option.value) : '');
                }}
                options={coTrainerOptions.map(t => ({
                  value: t.id,
                  label: `${t.firstName} ${t.lastName}`
                }))}
                placeholder="Seleziona co-formatore"
                isClearable
              />
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default DateTimeManager;

import React from 'react';
import { Label } from '../../../design-system/atoms/Label';

interface DocumentManagerProps {
  status: string;
  onStatusChange: (status: string) => void;
  selectedEmployees: (string | number)[];
  selectedCompanies: (string | number)[];
  attendance: Record<number, (string | number)[]>;
  dates: any[];
  showStatusMenu: boolean;
  onShowStatusMenuChange: (show: boolean) => void;
}

export const DocumentManager: React.FC<DocumentManagerProps> = ({
  status,
  onStatusChange,
  selectedEmployees,
  selectedCompanies,
  attendance,
  dates,
  showStatusMenu,
  onShowStatusMenuChange
}) => {
  const statusOptions = ['Preventivo', 'Conferma', 'Fattura', 'Pagamento'];

  const hasAttendanceData = dates.every((_, idx) => 
    attendance[idx] && attendance[idx].length > 0
  );

  return (
    <div className="space-y-4">
      <h3 className="font-semibold text-gray-700">Documenti</h3>

      <div className="border rounded-md p-4 bg-gray-50 space-y-4">
        {/* Document Status */}
        <div>
          <Label>Stato Documentazione</Label>
          <div className="relative mt-1">
            <button
              type="button"
              className="w-full p-2 border rounded flex justify-between items-center bg-white hover:bg-gray-50"
              onClick={() => onShowStatusMenuChange(!showStatusMenu)}
            >
              <span>{status}</span>
              <span className="text-gray-400"></span>
            </button>

            {showStatusMenu && (
              <div className="absolute left-0 right-0 mt-1 border rounded bg-white shadow-lg z-10">
                {statusOptions.map(s => (
                  <div
                    key={s}
                    className="p-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => {
                      onStatusChange(s);
                      onShowStatusMenuChange(false);
                    }}
                  >
                    {s}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Participants Summary */}
        <div>
          <Label>Partecipanti confermati</Label>
          <div className="mt-1 p-3 border rounded bg-white">
            <div className="text-sm text-gray-600">
              <span className="font-semibold">{selectedEmployees.length}</span> partecipanti da{' '}
              <span className="font-semibold">{selectedCompanies.length}</span> aziende
            </div>
          </div>
        </div>

        {/* Document Generation Options */}
        <div className="space-y-3">
          <h4 className="font-medium text-sm">Opzioni di generazione</h4>

          <div className="space-y-2">
            <div className="flex items-center gap-3">
              <input 
                type="checkbox" 
                id="preventivo" 
                checked={status === 'Preventivo'} 
                readOnly 
                className="w-4 h-4"
              />
              <label htmlFor="preventivo" className="text-sm">
                Genera Preventivo
              </label>
            </div>

            <div className="flex items-center gap-3">
              <input 
                type="checkbox" 
                id="attestati" 
                disabled={!hasAttendanceData}
                className="w-4 h-4"
              />
              <label 
                htmlFor="attestati" 
                className={`text-sm ${!hasAttendanceData ? "text-gray-400" : ""}`}
              >
                Genera Attestati
              </label>
              {!hasAttendanceData && (
                <span className="text-gray-400 text-xs">
                  (disponibile dopo la registrazione delle presenze)
                </span>
              )}
            </div>

            <div className="flex items-center gap-3">
              <input 
                type="checkbox" 
                id="registro" 
                disabled={!hasAttendanceData}
                className="w-4 h-4"
              />
              <label 
                htmlFor="registro" 
                className={`text-sm ${!hasAttendanceData ? "text-gray-400" : ""}`}
              >
                Genera Registro Presenze
              </label>
            </div>
          </div>
        </div>

        {/* Status Information */}
        <div className="bg-blue-50 p-3 rounded border">
          <div className="text-sm">
            <div className="font-medium text-blue-800 mb-1">Stato attuale: {status}</div>
            <div className="text-blue-600 text-xs">
              {status === 'Preventivo' && 'Il corso  in fase di preventivazione'}
              {status === 'Conferma' && 'Il corso  confermato e pronto per l\'erogazione'}
              {status === 'Fattura' && 'Il corso  stato fatturato'}
              {status === 'Pagamento' && 'Il pagamento  stato ricevuto'}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DocumentManager;

import React from 'react';
import { Label } from '../../../design-system/atoms/Label';
import Select from 'react-select';

interface Trainer {
  id: string | number;
  firstName: string;
  lastName: string;
  certifications?: string[];
}

interface TrainerSelectorProps {
  trainers: Trainer[];
  filteredTrainers: Trainer[];
  coTrainerOptions: Trainer[];
  formData: {
    trainer_id: string | number;
    co_trainer_id: string | number;
  };
  onFormDataChange: (field: string, value: any) => void;
  selectedCourse?: {
    certifications?: string[];
  };
}

export const TrainerSelector: React.FC<TrainerSelectorProps> = ({
  trainers,
  filteredTrainers,
  coTrainerOptions,
  formData,
  onFormDataChange,
  selectedCourse
}) => {
  const getTrainerName = (trainerId: string | number) => {
    const trainer = trainers.find(t => t.id === trainerId);
    return trainer ? `${trainer.firstName} ${trainer.lastName}` : '';
  };

  const getTrainerCertifications = (trainerId: string | number) => {
    const trainer = trainers.find(t => t.id === trainerId);
    return trainer?.certifications || [];
  };

  const isTrainerQualified = (trainer: Trainer) => {
    if (!selectedCourse?.certifications || selectedCourse.certifications.length === 0) {
      return true;
    }

    const trainerCerts = trainer.certifications || [];
    return selectedCourse.certifications.some(cert => 
      trainerCerts.includes(cert)
    );
  };

  const selectedTrainer = trainers.find(t => t.id === formData.trainer_id);
  const selectedCoTrainer = trainers.find(t => t.id === formData.co_trainer_id);

  return (
    <div className="space-y-4">
      <h3 className="font-semibold text-gray-700">Formatori</h3>

      {/* Main Trainer */}
      <div>
        <Label>Formatore Principale *</Label>
        <Select
          value={selectedTrainer ? {
            value: selectedTrainer.id,
            label: `${selectedTrainer.firstName} ${selectedTrainer.lastName}`
          } : null}
          onChange={(option) => {
            onFormDataChange('trainer_id', option ? option.value : '');
          }}
          options={filteredTrainers.map(t => ({
            value: t.id,
            label: `${t.firstName} ${t.lastName}`,
            isDisabled: !isTrainerQualified(t)
          }))}
          placeholder="Seleziona formatore principale"
          isClearable
          isSearchable
          formatOptionLabel={(option: any) => (
            <div className="flex justify-between items-center">
              <span className={!isTrainerQualified(trainers.find(t => t.id === option.value)!) ? 'text-gray-400' : ''}>
                {option.label}
              </span>
              {!isTrainerQualified(trainers.find(t => t.id === option.value)!) && (
                <span className="text-xs text-red-500">Non qualificato</span>
              )}
            </div>
          )}
        />

        {selectedTrainer && (
          <div className="mt-2 p-3 bg-blue-50 rounded border">
            <div className="text-sm">
              <div className="font-medium">{selectedTrainer.firstName} {selectedTrainer.lastName}</div>
              {selectedTrainer.certifications && selectedTrainer.certifications.length > 0 && (
                <div className="text-gray-600">
                  Certificazioni: {selectedTrainer.certifications.join(', ')}
                </div>
              )}
              {selectedCourse?.certifications && (
                <div className={`text-xs mt-1 ${isTrainerQualified(selectedTrainer) ? 'text-green-600' : 'text-red-600'}`}>
                  {isTrainerQualified(selectedTrainer) 
                    ? ' Qualificato per questo corso' 
                    : ' Non ha le certificazioni richieste'
                  }
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Co-Trainer */}
      <div>
        <Label>Co-Formatore (Opzionale)</Label>
        <Select
          value={selectedCoTrainer ? {
            value: selectedCoTrainer.id,
            label: `${selectedCoTrainer.firstName} ${selectedCoTrainer.lastName}`
          } : null}
          onChange={(option) => {
            onFormDataChange('co_trainer_id', option ? option.value : '');
          }}
          options={coTrainerOptions.map(t => ({
            value: t.id,
            label: `${t.firstName} ${t.lastName}`,
            isDisabled: !isTrainerQualified(t)
          }))}
          placeholder="Seleziona co-formatore"
          isClearable
          isSearchable
          formatOptionLabel={(option: any) => (
            <div className="flex justify-between items-center">
              <span className={!isTrainerQualified(trainers.find(t => t.id === option.value)!) ? 'text-gray-400' : ''}>
                {option.label}
              </span>
              {!isTrainerQualified(trainers.find(t => t.id === option.value)!) && (
                <span className="text-xs text-red-500">Non qualificato</span>
              )}
            </div>
          )}
        />

        {selectedCoTrainer && (
          <div className="mt-2 p-3 bg-green-50 rounded border">
            <div className="text-sm">
              <div className="font-medium">{selectedCoTrainer.firstName} {selectedCoTrainer.lastName}</div>
              {selectedCoTrainer.certifications && selectedCoTrainer.certifications.length > 0 && (
                <div className="text-gray-600">
                  Certificazioni: {selectedCoTrainer.certifications.join(', ')}
                </div>
              )}
              {selectedCourse?.certifications && (
                <div className={`text-xs mt-1 ${isTrainerQualified(selectedCoTrainer) ? 'text-green-600' : 'text-red-600'}`}>
                  {isTrainerQualified(selectedCoTrainer) 
                    ? ' Qualificato per questo corso' 
                    : ' Non ha le certificazioni richieste'
                  }
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Trainer Requirements Info */}
      {selectedCourse?.certifications && selectedCourse.certifications.length > 0 && (
        <div className="bg-yellow-50 p-3 rounded border">
          <div className="text-sm">
            <div className="font-medium text-yellow-800 mb-1">
              Certificazioni richieste per questo corso:
            </div>
            <div className="text-yellow-700">
              {selectedCourse.certifications.join(', ')}
            </div>
          </div>
        </div>
      )}

      {/* Available Qualified Trainers Summary */}
      {filteredTrainers.length > 0 && (
        <div className="bg-green-50 p-3 rounded border">
          <div className="text-sm">
            <div className="font-medium text-green-800 mb-1">
              Formatori qualificati disponibili: {filteredTrainers.length}
            </div>
            <div className="text-green-600 text-xs">
              {filteredTrainers.slice(0, 3).map(t => `${t.firstName} ${t.lastName}`).join(', ')}
              {filteredTrainers.length > 3 && ` e altri ${filteredTrainers.length - 3}`}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TrainerSelector;

