const NOTIFICATION_TYPES = {
  system: {
    label: 'Sistema',
    description: 'Notifiche di sistema, errori e manutenzione',
    icon: Bell
  },
  security: {
    label: 'Sicurezza',
    description: 'Accessi, modifiche password e attivit sospette',
    icon: Bell
  },
  updates: {
