import React, { useState, useEffect, useRef, useMemo } from 'react';
import { ArrowUp, ArrowDown, ArrowUpDown, GripHorizontal, ChevronUp, ChevronDown } from 'lucide-react';
import { saveTablePreferences } from '../../services/userPreferences';
import { cn } from '../../design-system/utils';

export interface ResizableTableColumn<T = any> {
  key: string;
  label: string;
  width?: number;
  minWidth?: number;
  sortable?: boolean;
  hidden?: boolean;
  order?: number;
  renderHeader?: (col: ResizableTableColumn<T>) => React.ReactNode;
  renderCell?: (row: T, col: ResizableTableColumn<T>, rowIndex: number) => React.ReactNode;
}

export type SortDirection = 'asc' | 'desc' | null;

interface ResizableTableProps<T = any> {
  columns: ResizableTableColumn<T>[];
  data: T[];
  tableProps?: React.TableHTMLAttributes<HTMLTableElement>;
  tbodyProps?: React.HTMLAttributes<HTMLTableSectionElement>;
  onWidthsChange?: (widths: Record<string, number>) => void;
  initialWidths?: Record<string, number>;
  onRowClick?: (row: T) => void;
  rowClassName?: (row: T, index: number) => string;
  onSort?: (key: string, direction: SortDirection) => void;
  sortKey?: string;
  sortDirection?: SortDirection;
  onColumnVisibilityChange?: (hiddenColumns: string[]) => void;
  hiddenColumns?: string[];
  onColumnOrderChange?: (columnOrder: Record<string, number>) => void;
  columnOrder?: Record<string, number>;
  tableName?: string;
  zebra?: boolean;
}

const ResizableTable = <T extends Record<string, any> = any>({
  columns,
  data,
  tableProps = {},
  tbodyProps = {},
  onWidthsChange,
  initialWidths = {},
  onRowClick,
  rowClassName,
  onSort,
  sortKey,
  sortDirection,
  onColumnVisibilityChange,
  hiddenColumns = [],
  onColumnOrderChange,
  columnOrder = {},
  tableName = 'table',
  zebra = false,
}: ResizableTableProps<T>) => {
  // Attempt to load preferences from local storage first
  const getInitialState = () => {
    if (typeof window === 'undefined') {
      return {
        loadedWidths: {},
        loadedHiddenColumns: [],
        loadedColumnOrder: {},
      };
    }

    try {
      return {
        loadedWidths: JSON.parse(localStorage.getItem(`${tableName}-column-widths`) || '{}'),
        loadedHiddenColumns: JSON.parse(localStorage.getItem(`${tableName}-hidden-columns`) || '[]'),
        loadedColumnOrder: JSON.parse(localStorage.getItem(`${tableName}-column-order`) || '{}'),
      };
    } catch (e) {
      console.error("Error parsing localStorage:", e);
      return {
        loadedWidths: {},
        loadedHiddenColumns: [],
        loadedColumnOrder: {},
      };
    }
  };

  const { loadedWidths, loadedHiddenColumns, loadedColumnOrder } = getInitialState();

  // Set default widths from column definitions
  const defaultWidths = Object.fromEntries(
    columns.map((col) => [col.key, col.width || 120])
  );

  // Initialize with saved data or defaults
  const [colWidths, setColWidths] = useState<Record<string, number>>({ 
    ...defaultWidths, 
    ...loadedWidths,
    ...initialWidths 
  });

  const [localHiddenColumns, setLocalHiddenColumns] = useState<string[]>(
    hiddenColumns.length > 0 ? hiddenColumns : loadedHiddenColumns
  );

  const [effectiveColumnOrder, setEffectiveColumnOrder] = useState(
    Object.keys(columnOrder).length > 0 ? columnOrder : loadedColumnOrder
  );

  // Refs for resize handling
  const resizingCol = useRef<string | null>(null);
  const startX = useRef<number>(0);
  const startWidth = useRef<number>(0);
  const tableContainerRef = useRef<HTMLDivElement>(null);

  // Handle column dragging
  const [draggingCol, setDraggingCol] = useState<string | null>(null);
  const [dropTargetCol, setDropTargetCol] = useState<string | null>(null);

  const minColWidth = 50; // Minimum column width

  // Array of visible columns (not hidden)
  const visibleColumns = useMemo(() => {
    return columns.filter(col => !localHiddenColumns.includes(col.key));
  }, [columns, localHiddenColumns]);

  // Check if column is the last visible one
  const isLastVisibleColumn = (col: ResizableTableColumn<T>) => {
    const lastCol = visibleColumns[visibleColumns.length - 1];
    return lastCol && lastCol.key === col.key;
  };

  // Sort columns by order
  const orderedColumns = [...columns].sort((a, b) => {
    const orderA = effectiveColumnOrder[a.key] ?? columns.findIndex(c => c.key === a.key);
    const orderB = effectiveColumnOrder[b.key] ?? columns.findIndex(c => c.key === b.key);
    return orderA - orderB;
  });

  // Filter out hidden columns
  const sortedColumns = orderedColumns.filter(col => !localHiddenColumns.includes(col.key));

  // Save preferences to localStorage only
  const savePreferences = (type: 'widths' | 'hiddenColumns' | 'order', data: any) => {
    // Only save to localStorage - no API calls
    if (typeof window === 'undefined') return;

    try {
      if (type === 'widths') {
        localStorage.setItem(`${tableName}-column-widths`, JSON.stringify(data));
      } else if (type === 'hiddenColumns') {
        localStorage.setItem(`${tableName}-hidden-columns`, JSON.stringify(data));
      } else if (type === 'order') {
        localStorage.setItem(`${tableName}-column-order`, JSON.stringify(data));
      }
    } catch (error) {
      // Silent error handling
      console.error('Failed to save preferences to localStorage:', error);
    }
  };

  // Handle column resize start with improved event handling
  const handleResizeStart = (colKey: string, e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    // Set the active column being resized
    resizingCol.current = colKey;
    startX.current = e.clientX;
    startWidth.current = colWidths[colKey] || columns.find(c => c.key === colKey)?.width || 120;

    // Setup mouse move and mouse up handlers
    const handleMouseMove = (e: MouseEvent) => {
      if (!resizingCol.current) return;
      e.preventDefault();
      e.stopPropagation();

      // Get the current width and calculate difference
      const diff = e.clientX - startX.current;

      // Calculate new width with a minimum to ensure columns stay visible
      const newWidth = Math.max(startWidth.current + diff, minColWidth);

      // Update the width in state
      setColWidths(prev => ({
        ...prev,
        [resizingCol.current!]: newWidth
      }));
    };

    const handleMouseUp = () => {
      if (!resizingCol.current) return;

      // Create a new widths object with the updated width
      const updatedWidths = {
        ...colWidths,
        [resizingCol.current]: colWidths[resizingCol.current!] || defaultWidths[resizingCol.current!]
      };

      // Save the new width to localStorage
      savePreferences('widths', updatedWidths);

      // Notify parent if needed
      if (onWidthsChange) {
        onWidthsChange(updatedWidths);
      }

      // Reset the resizing column state
      resizingCol.current = null;
      document.body.style.cursor = '';

      // Remove the event listeners
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };

    // Apply cursor to whole document while resizing
    document.body.style.cursor = 'col-resize';

    // Add event listeners
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  // Handle column sort click
  const handleSortClick = (colKey: string) => {
    if (!onSort) return;

    let direction: SortDirection = 'asc';
    if (sortKey === colKey) {
      // Toggle between asc and desc only (eliminating null state)
      direction = sortDirection === 'asc' ? 'desc' : 'asc';
    }

    onSort(colKey, direction);
  };

  // Render column header content
  const renderColumnHeader = (col: ResizableTableColumn<T>) => {
    if (col.renderHeader) {
      return col.renderHeader(col);
    }

    // Default rendering
    return (
      <div className="flex items-center justify-between w-full">
        <span className="font-semibold truncate">{col.label}</span>
      </div>
    );
  };

  // Handle column drag start
  const handleColumnDragStart = (colKey: string, e: React.DragEvent) => {
    e.dataTransfer.effectAllowed = 'move';
    try {
      e.dataTransfer.setData('text/plain', colKey);
    } catch (err) {
      // Fallback for IE
      e.dataTransfer.setData('text', colKey);
    }

    setDraggingCol(colKey);

    // Hide preview image
    const dragIcon = document.createElement('div');
    dragIcon.style.position = 'absolute';
    dragIcon.style.top = '-9999px';
    document.body.appendChild(dragIcon);
    e.dataTransfer.setDragImage(dragIcon, 0, 0);
    setTimeout(() => document.body.removeChild(dragIcon), 0);
  };

  // Handle column drag over
  const handleColumnDragOver = (colKey: string, e: React.DragEvent) => {
    e.preventDefault();
    if (draggingCol === colKey || !draggingCol) return;

    setDropTargetCol(colKey);

    // Set the dropEffect to move
    e.dataTransfer.dropEffect = 'move';
  };

  // Handle column drop
  const handleColumnDrop = (colKey: string, e: React.DragEvent) => {
    e.preventDefault();
    if (draggingCol === colKey || !draggingCol) return;

    // Get the current order values
    const sourceIdx = sortedColumns.findIndex(c => c.key === draggingCol);
    const targetIdx = sortedColumns.findIndex(c => c.key === colKey);

    if (sourceIdx === -1 || targetIdx === -1) return;

    // Create new order with swapped values
    const newOrder = { ...effectiveColumnOrder };

    // Adjust the order of columns between source and target
    if (sourceIdx < targetIdx) {
      // Moving right - all columns between source and target move left
      for (let i = sourceIdx + 1; i <= targetIdx; i++) {
        newOrder[sortedColumns[i].key] = i - 1;
      }
    } else {
      // Moving left - all columns between target and source move right
      for (let i = targetIdx; i < sourceIdx; i++) {
        newOrder[sortedColumns[i].key] = i + 1;
      }
    }

    // Set the dragged column to the target position
    newOrder[draggingCol] = targetIdx;

    setEffectiveColumnOrder(newOrder);
    if (onColumnOrderChange) {
      onColumnOrderChange(newOrder);
    }

    // Save to localStorage
    savePreferences('order', newOrder);

    // Reset drag state
    setDraggingCol(null);
    setDropTargetCol(null);
  };

  // Handle column drag end
  const handleColumnDragEnd = () => {
    setDraggingCol(null);
    setDropTargetCol(null);
  };

  // Handle column visibility change
  const handleColumnVisibilityChange = (newHiddenColumns: string[]) => {
    setLocalHiddenColumns(newHiddenColumns);

    if (onColumnVisibilityChange) {
      onColumnVisibilityChange(newHiddenColumns);
    }

    // Save to localStorage
    savePreferences('hiddenColumns', newHiddenColumns);
  };

  // Apply column visibility changes from external sources
  useEffect(() => {
    if (hiddenColumns.length > 0) {
      setLocalHiddenColumns(hiddenColumns);
    }
  }, [hiddenColumns]);

  // Apply column order changes from external sources
  useEffect(() => {
    if (Object.keys(columnOrder).length > 0) {
      setEffectiveColumnOrder(columnOrder);
    }
  }, [columnOrder]);

  // Apply width changes from external sources
  useEffect(() => {
    if (Object.keys(initialWidths).length > 0) {
      setColWidths(prev => ({
        ...prev,
        ...initialWidths
      }));
    }
  }, [initialWidths]);

  // Render table header cell with sorting and resizing
  const renderHeaderCell = (col: ResizableTableColumn<T>) => {
    // Don't render header cell for hidden columns
    if (col.hidden || localHiddenColumns.includes(col.key)) {
      return null;
    }

    const width = colWidths[col.key] || col.width || 200;
    const isSorted = sortKey === col.key;

    const onSortClick = () => {
      if (!col.sortable) return;
      handleSortClick(col.key);
    };

    return (
      <th 
        key={col.key}
        style={{ 
          width: `${width}px`, 
          minWidth: `${width}px`,
        }}
        className={cn(
          'relative px-4 py-3 select-none border-b border-blue-100',
          'font-medium text-left text-sm',
          isSorted ? 'bg-blue-100' : '',
        )}
      >
        <div 
          className={cn(
            'flex items-center gap-1',
            col.sortable ? 'cursor-pointer group' : ''
          )}
          onClick={col.sortable ? onSortClick : undefined}
        >
          {renderColumnHeader(col)}

          {col.sortable && (
            <div className={cn(
              'flex-shrink-0 h-4 w-4 opacity-0 group-hover:opacity-70',
              isSorted ? 'opacity-100 text-blue-600' : ''
            )}>
              {isSorted ? (
                sortDirection === 'asc' ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />
              ) : null}
            </div>
          )}
        </div>

        {/* Resize handle */}
        {!isLastVisibleColumn(col) && (
          <div
            className={cn(
              "absolute right-0 top-0 bottom-0 w-3 cursor-col-resize z-20 hover:bg-blue-400 hover:opacity-50",
              "flex items-center justify-center group"
            )}
            onMouseDown={(e) => handleResizeStart(col.key, e)}
          >
            <div className="w-px h-4/5 bg-blue-200 group-hover:bg-white"></div>
          </div>
        )}
      </th>
    );
  };

  // Render table cell with proper content
  const renderCell = (row: T, col: ResizableTableColumn<T>, rowIndex: number) => {
    // Don't render cell for hidden columns
    if (col.hidden || localHiddenColumns.includes(col.key)) {
      return null;
    }

    const width = colWidths[col.key] || col.width || 200;
    const isLastColumn = isLastVisibleColumn(col);
    const isFirstColumn = col.key === 'actions';

    const rowBgClass = zebra && rowIndex % 2 === 1 ? 'bg-gray-50' : 'bg-white';

    return (
      <td 
        key={`${rowIndex}-${col.key}`}
        style={{ 
          width: `${width}px`, 
          minWidth: `${width}px`,
          maxWidth: `${width}px`,
        }}
        className={cn(
          'px-4 py-3 overflow-hidden text-sm text-gray-700 align-middle',
          isFirstColumn ? 'sticky left-0 z-20' : '',
          isLastColumn ? 'sticky right-0 z-10' : '', 
          rowBgClass
        )}
      >
        {col.renderCell ? (
          col.renderCell(row, col, rowIndex)
        ) : (
          <div className="truncate">
            {String(row[col.key as keyof T] ?? '')}
          </div>
        )}
      </td>
    );
  };

  // Utility function to reset column widths in localStorage
  const resetColumnWidths = () => {
    const itemsToRemove = [];
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      if (key && key.includes('-column-widths')) {
        itemsToRemove.push(key);
      }
    }

    itemsToRemove.forEach(key => {
      localStorage.removeItem(key);
    });

    // Non ricaricare la pagina per evitare loop infiniti
    // Reimpostare solo lo stato delle larghezze
    setColWidths({...defaultWidths, ...initialWidths});
  };

  // Esegui il reset una sola volta all'avvio per risolvere il problema
  useEffect(() => {
    resetColumnWidths();
    // Importante: Il secondo parametro vuoto [] indica che l'effetto viene eseguito solo una volta
  }, []);

  return (
    <div className="overflow-x-auto rounded-lg border border-gray-200 shadow-sm" ref={tableContainerRef}>
      <table 
        {...tableProps} 
        className={cn(
          'w-full border-collapse border-spacing-0',
          tableProps?.className
        )}
      >
        <thead className="bg-blue-50 text-blue-900 sticky top-0 z-10">
          <tr>
            {sortedColumns.map(col => renderHeaderCell(col))}
          </tr>
        </thead>
        <tbody {...tbodyProps}>
          {data.map((row, rowIndex) => (
            <tr
              key={row.id || rowIndex}
              onClick={onRowClick ? () => onRowClick(row) : undefined}
              className={cn(
                'border-b border-gray-200 last:border-0 whitespace-nowrap',
                zebra && rowIndex % 2 === 1 ? 'bg-gray-50' : 'bg-white',
                rowClassName ? rowClassName(row, rowIndex) : '',
                onRowClick ? 'cursor-pointer hover:bg-gray-50' : ''
              )}
            >
              {sortedColumns.map((col, colIndex) => renderCell(row, col, rowIndex))}
            </tr>
          ))}

          {data.length === 0 && (
            <tr>
              <td 
                colSpan={sortedColumns.length} 
                className="p-6 text-center text-gray-500"
              >
                Nessun dato disponibile
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ResizableTable;

import React from 'react';
import { Link } from 'react-router-dom';
import { MoreVertical } from 'lucide-react';

interface DataItem {
  label: string;
  value: string;
}

interface CardAction {
  icon: React.ReactNode;
  label: string;
  onClick: (e: React.MouseEvent) => void;
}

interface CardDetail {
  icon: React.ReactNode;
  text: string;
}

interface EntityCardProps {
  id?: string;
  title: string;
  subtitle?: React.ReactNode;
  icon?: React.ReactNode;
  extraInfo?: React.ReactNode;
  statusBadge?: React.ReactNode;
  actionButton?: React.ReactNode;
  bgGradient?: string;
  gradientHeight?: number;
  detailUrl?: string;
  onClick?: () => void;
  selected?: boolean;
  onSelect?: () => void;
  selectable?: boolean;
  selectionMode?: boolean;
  onViewDetails?: () => void;
  onEdit?: () => void;
  onDelete?: () => void;
  data?: DataItem[];
  /** Azioni che appaiono nel menu a comparsa */
  actions?: CardAction[];
  /** Righe di dettaglio che appaiono alla base della card */
  details?: CardDetail[];
  /** Colore di sfondo dell'icona */
  iconBgColor?: string;
}

const EntityCard: React.FC<EntityCardProps> = ({
  id,
  title,
  subtitle,
  icon,
  extraInfo,
  statusBadge,
  actionButton,
  bgGradient = 'from-blue-500 to-blue-600',
  gradientHeight = 40,
  detailUrl,
  onClick,
  selected = false,
  onSelect,
  selectionMode = false,
  onViewDetails,
  onEdit,
  onDelete,
  data,
  actions,
  details,
  iconBgColor = 'bg-blue-500',
}) => {
  const [showActions, setShowActions] = React.useState(false);

  const toggleActions = (e: React.MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();
    setShowActions(!showActions);
  };

  const cardContent = (
    <div 
      className={`relative bg-white rounded-xl shadow-sm overflow-hidden transition-all duration-200 hover:shadow-md ${
        selected ? 'ring-2 ring-blue-400 shadow-md' : ''
      }`}
    >
      {/* Top gradient (mostrato solo se gradientHeight > 0) */}
      {gradientHeight > 0 && (
        <div 
          className={`w-full bg-gradient-to-r ${bgGradient}`} 
          style={{ height: `${gradientHeight}px` }} 
        />
      )}

      {/* Selection checkbox */}
      {selectionMode && onSelect && (
        <div 
          className="absolute top-2 right-2 z-10"
          onClick={(e) => {
            e.preventDefault();
            e.stopPropagation();
            onSelect();
          }}
        >
          <input 
            type="checkbox" 
            checked={selected} 
            onChange={() => {}} 
            className="w-4 h-4 accent-blue-600"
          />
        </div>
      )}

      {/* Azioni dropdown */}
      {actions && actions.length > 0 && (
        <div className="absolute top-2 right-2 z-10">
          <button 
            onClick={toggleActions}
            className="p-1 rounded-full bg-white/80 hover:bg-white text-gray-600 hover:text-gray-900 focus:outline-none"
          >
            <MoreVertical className="h-5 w-5" />
          </button>

          {/* Menu azioni */}
          {showActions && (
            <div className="absolute right-0 mt-1 w-48 bg-white rounded-md shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none z-20">
              <div className="py-1" role="menu" aria-orientation="vertical">
                {actions.map((action, index) => (
                  <button
                    key={index}
                    className="flex w-full items-center px-4 py-2 text-sm text-left text-gray-700 hover:bg-gray-100"
                    role="menuitem"
                    onClick={(e) => {
                      e.preventDefault();
                      e.stopPropagation();
                      action.onClick(e);
                      setShowActions(false);
                    }}
                  >
                    {action.icon && <span className="mr-3">{action.icon}</span>}
                    {action.label}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {/* Content */}
      <div className="p-4">
        <div className="flex items-start gap-3">
          {icon && (
            <div className={`shrink-0 flex items-center justify-center p-2 rounded-md ${iconBgColor} text-white h-10 w-10`}>
              {icon}
            </div>
          )}

          <div className="flex-1 min-w-0">
            <h3 className="text-lg font-semibold text-gray-800 truncate">{title}</h3>

            {subtitle && (
              <div className="mt-1 text-gray-600 truncate">
                {subtitle}
              </div>
            )}

            {extraInfo && (
              <div className="mt-2 text-sm text-gray-600">
                {extraInfo}
              </div>
            )}

            {/* Dati con etichette e valori */}
            {data && data.length > 0 && (
              <div className="mt-3 space-y-1.5">
                {data.map((item, index) => (
                  <div key={index} className="flex items-baseline text-sm">
                    <span className="font-medium text-gray-500 w-20">{item.label}:</span>
                    <span className="text-gray-700 truncate">{item.value}</span>
                  </div>
                ))}
              </div>
            )}
          </div>

          {statusBadge && (
            <div className="shrink-0 ml-auto">
              {statusBadge}
            </div>
          )}
        </div>

        {/* Dettagli */}
        {details && details.length > 0 && (
          <div className="mt-3 space-y-1.5 pt-3 border-t border-gray-100">
            {details.map((detail, index) => (
              <div key={index} className="flex items-center text-sm text-gray-600">
                {detail.icon && <span className="mr-2 text-gray-400">{detail.icon}</span>}
                <span className="truncate">{detail.text}</span>
              </div>
            ))}
          </div>
        )}

        {actionButton && (
          <div className="mt-4 flex justify-end">
            {actionButton}
          </div>
        )}
      </div>
    </div>
  );

  if (detailUrl) {
    return (
      <Link 
        to={detailUrl} 
        className="block"
        onClick={(e) => {
          if (selectionMode && onSelect) {
            e.preventDefault();
            onSelect();
          } else if (onClick) {
            e.preventDefault();
            onClick();
          }
        }}
      >
        {cardContent}
      </Link>
    );
  }

  return (
    <div 
      onClick={onClick} 
      className={onClick ? "cursor-pointer" : ""}
    >
      {cardContent}
    </div>
  );
};

export default EntityCard;

import React from 'react';
import { Filter, X } from 'lucide-react';
import { Button } from '../../../design-system/atoms/Button';
import { cn } from '../../../design-system/utils';

interface FilterConfig {
  key: string;
  label: string;
  type: 'text' | 'select' | 'date' | 'number' | 'boolean';
  options?: { value: string; label: string }[];
  placeholder?: string;
}

interface FilterPanelProps {
  filters: FilterConfig[];
  values: Record<string, any>;
  onChange: (values: Record<string, any>) => void;
  className?: string;
}

export const FilterPanel: React.FC<FilterPanelProps> = ({
  filters,
  values,
  onChange,
  className
}) => {
  const handleFilterChange = (key: string, value: any) => {
    onChange({
      ...values,
      [key]: value
    });
  };

  const handleClearFilter = (key: string) => {
    const newValues = { ...values };
    delete newValues[key];
    onChange(newValues);
  };

  const handleClearAll = () => {
    onChange({});
  };

  const hasActiveFilters = Object.keys(values).length > 0;

  const renderFilterInput = (filter: FilterConfig) => {
    const value = values[filter.key] || '';

    switch (filter.type) {
      case 'select':
        return (
          <select
            value={value}
            onChange={(e) => handleFilterChange(filter.key, e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
          >
            <option value="">{filter.placeholder || `Seleziona ${filter.label.toLowerCase()}`}</option>
            {filter.options?.map(option => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        );

      case 'date':
        return (
          <input
            type="date"
            value={value}
            onChange={(e) => handleFilterChange(filter.key, e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
          />
        );

      case 'number':
        return (
          <input
            type="number"
            value={value}
            onChange={(e) => handleFilterChange(filter.key, e.target.value)}
            placeholder={filter.placeholder}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
          />
        );

      case 'boolean':
        return (
          <select
            value={value}
            onChange={(e) => handleFilterChange(filter.key, e.target.value === 'true')}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
          >
            <option value="">{filter.placeholder || `Seleziona ${filter.label.toLowerCase()}`}</option>
            <option value="true">S</option>
            <option value="false">No</option>
          </select>
        );

      default:
        return (
          <input
            type="text"
            value={value}
            onChange={(e) => handleFilterChange(filter.key, e.target.value)}
            placeholder={filter.placeholder}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
          />
        );
    }
  };

  return (
    <div className={cn('bg-gray-50 border border-gray-200 rounded-lg p-4', className)}>
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-2">
          <Filter className="h-4 w-4 text-gray-500" />
          <span className="text-sm font-medium text-gray-700">Filtri</span>
        </div>

        {hasActiveFilters && (
          <Button
            variant="outline"
            size="sm"
            onClick={handleClearAll}
            className="text-xs"
          >
            Cancella tutti
          </Button>
        )}
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        {filters.map(filter => (
          <div key={filter.key} className="space-y-2">
            <div className="flex items-center justify-between">
              <label className="text-sm font-medium text-gray-700">
                {filter.label}
              </label>
              {values[filter.key] && (
                <button
                  onClick={() => handleClearFilter(filter.key)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="h-3 w-3" />
                </button>
              )}
            </div>
            {renderFilterInput(filter)}
          </div>
        ))}
      </div>
    </div>
  );
};

export default FilterPanel;

import React, { useState, useEffect, useRef, ReactNode } from 'react';

type FieldType = 'text' | 'textarea' | 'number' | 'email' | 'password' | 'select' | 'checkbox' | 'radio' | 'date' | 'tel';

interface Option {
  value: string;
  label: string;
}

interface EntityFormFieldProps {
  /** Nome del campo (usato come id e name) */
  name: string;
  /** Label da visualizzare */
  label: string;
  /** Tipo di campo */
  type?: FieldType;
  /** Valore corrente */
  value: string | number | boolean | undefined;
  /** Handler per il cambio valore */
  onChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => void;
  /** Se il campo  obbligatorio */
  required?: boolean;
  /** Placeholder da visualizzare */
  placeholder?: string;
  /** Messaggio di errore */
  error?: string;
  /** Classi CSS aggiuntive */
  className?: string;
  /** Opzioni per select */
  options?: Option[];
  /** Se il campo  disabilitato */
  disabled?: boolean;
  /** Se il campo  in sola lettura */
  readOnly?: boolean;
  /** Numero di righe per textarea */
  rows?: number;
  /** Icona da visualizzare a sinistra dell'input */
  leftIcon?: ReactNode;
  /** Icona da visualizzare a destra dell'input */
  rightIcon?: ReactNode;
  /** Dimensione del campo */
  size?: 'sm' | 'md' | 'lg';
  /** Stile dell'input */
  variant?: 'default' | 'pill';
  /** Testo di aiuto aggiuntivo */
  helpText?: string;
  /** Se il campo select  ricercabile */
  searchable?: boolean;
  /** Se il campo deve essere multilinea (textarea) */
  multiline?: boolean;
  /** Propriet HTML aggiuntive */
  [key: string]: any;
}

/**
 * Campo form standardizzato per l'interfaccia utente
 */
const EntityFormField: React.FC<EntityFormFieldProps> = ({
  name,
  label,
  type = 'text',
  value,
  onChange,
  required = false,
  placeholder,
  error,
  className = '',
  options = [],
  disabled = false,
  readOnly = false,
  rows = 3,
  leftIcon,
  rightIcon,
  size = 'md',
  variant = 'default',
  helpText,
  searchable = false,
  multiline = false,
  ...rest
}) => {
  // Se multiline  true, forza il tipo a textarea
  const fieldType = multiline ? 'textarea' : type;
  // State for select search
  const [searchTerm, setSearchTerm] = useState('');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);
  const selectRef = useRef<HTMLSelectElement>(null);

  // Ensure value is properly formatted for display
  const displayValue = value !== undefined && value !== null ? String(value) : '';

  // Get display label for select options
  const getOptionLabel = () => {
    if (!value || !options.length) return '';
    const option = options.find(opt => opt.value === String(value));
    return option ? option.label : '';
  };

  // Reset search term when value changes or component mounts
  useEffect(() => {
    if (fieldType === 'select' && searchable && value) {
      setSearchTerm('');
    }
  }, [value, fieldType, searchable]);

  // Handle direct input change
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    console.log(`Field ${name} changed:`, e.target.value);
    onChange(e);
  };

  // Handle search input change
  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
    setIsDropdownOpen(true);
  };

  // Handle dropdown option click
  const handleOptionClick = (optionValue: string) => {
    // Create synthetic event for onChange handler
    const syntheticEvent = {
      target: {
        name,
        value: optionValue
      }
    } as React.ChangeEvent<HTMLSelectElement>;

    onChange(syntheticEvent);
    setSearchTerm('');
    setIsDropdownOpen(false);
  };

  // Dimensioni del campo in base alla propriet size
  const sizeClasses = {
    sm: 'h-8 text-xs',
    md: 'h-10 text-sm',
    lg: 'h-12 text-base',
  };

  // Stile dell'input in base alla variante
  const variantClasses = {
    default: 'rounded-md',
    pill: 'rounded-full',
  };

  // Classe base per gli input
  const baseInputClass = `
    block w-full border 
    ${error ? 'border-red-300 focus:border-red-500 focus:ring-red-500' : 'border-gray-300 focus:border-blue-500 focus:ring-blue-500'} 
    ${disabled ? 'bg-gray-100 text-gray-500 cursor-not-allowed' : 'bg-white'} 
    ${readOnly ? 'bg-gray-50' : ''}
    shadow-sm focus:outline-none focus:ring-2 
    transition-colors duration-200
    ${variantClasses[variant]}
    ${fieldType !== 'textarea' ? sizeClasses[size] : ''}
    ${leftIcon ? 'pl-10' : ''}
    ${rightIcon ? 'pr-10' : ''}
  `;

  // Classi per checkbox/radio
  const checkboxClass = `
    h-5 w-5 
    ${error ? 'border-red-300 focus:ring-red-500' : 'border-gray-300 focus:ring-blue-500'} 
    rounded
    ${disabled ? 'bg-gray-100 text-gray-400' : 'text-blue-600'}
    transition-colors duration-200
  `;

  // Renderizza il campo appropriato in base al tipo
  const renderField = () => {
    switch (fieldType) {
      case 'textarea':
        return (
          <textarea
            id={name}
            name={name}
            value={displayValue}
            onChange={handleInputChange}
            className={`${baseInputClass} py-2 px-3 resize-none`}
            rows={rows}
            placeholder={placeholder}
            disabled={disabled}
            readOnly={readOnly}
            required={required}
            {...rest}
          />
        );

      case 'select':
        if (searchable) {
          // Filter options based on search term
          const filteredOptions = options.filter(option => 
            option.label.toLowerCase().includes(searchTerm.toLowerCase())
          );

          return (
            <div className="relative">
              {leftIcon && (
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none text-gray-400">
                  {leftIcon}
                </div>
              )}
              <input
                ref={inputRef}
                type="text"
                className={`${baseInputClass} px-3 cursor-pointer pr-8`}
                placeholder={placeholder || 'Cerca...'}
                onClick={() => setIsDropdownOpen(true)}
                onFocus={() => setIsDropdownOpen(true)}
                onBlur={() => setTimeout(() => setIsDropdownOpen(false), 200)}
                onChange={handleSearchChange}
                value={isDropdownOpen ? searchTerm : getOptionLabel() || searchTerm}
                readOnly={disabled}
                disabled={disabled}
              />
              <div 
                className="absolute inset-y-0 right-0 flex items-center pr-2 cursor-pointer"
                onClick={() => {
                  setIsDropdownOpen(!isDropdownOpen);
                  if (!isDropdownOpen && inputRef.current) {
                    inputRef.current.focus();
                  }
                }}
              >
                <svg className="h-5 w-5 text-gray-400" viewBox="0 0 20 20" fill="none" stroke="currentColor">
                  <path d="M7 7l3-3 3 3m0 6l-3 3-3-3" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
                </svg>
              </div>

              {isDropdownOpen && (
                <div className="absolute z-10 mt-1 w-full bg-white shadow-lg rounded-md py-1 text-base ring-1 ring-black ring-opacity-5 overflow-auto max-h-60">
                  <div className="max-h-60 overflow-y-auto">
                    {filteredOptions.length === 0 ? (
                      <div className="px-3 py-2 text-gray-500">Nessun risultato</div>
                    ) : (
                      filteredOptions.map((option) => (
                        <div
                          key={option.value}
                          className={`px-3 py-2 cursor-pointer hover:bg-blue-50 ${
                            value === option.value ? 'bg-blue-50' : ''
                          }`}
                          onClick={() => handleOptionClick(option.value)}
                        >
                          {option.label}
                        </div>
                      ))
                    )}
                  </div>
                </div>
              )}

              {/* Hidden select for form submission - actually hidden now */}
              <select
                ref={selectRef}
                name={name}
                value={displayValue}
                onChange={handleInputChange}
                className="hidden"
                required={required}
                disabled={disabled}
              >
                <option value="">Seleziona...</option>
                {options.map((option) => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>
          );
        }

        return (
          <div className="relative">
            {leftIcon && (
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none text-gray-400">
                {leftIcon}
              </div>
            )}
            <select
              id={name}
              name={name}
              value={displayValue}
              onChange={handleInputChange}
              className={`${baseInputClass} px-3 pr-8 appearance-none`}
              disabled={disabled}
              required={required}
              {...rest}
            >
              <option value="">{placeholder || 'Seleziona...'}</option>
              {options.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <div className="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
              <svg className="h-5 w-5 text-gray-400" viewBox="0 0 20 20" fill="none" stroke="currentColor">
                <path d="M7 7l3 3 3-3" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
              </svg>
            </div>
          </div>
        );

      case 'checkbox':
        return (
          <div className="flex items-center">
            <input
              id={name}
              name={name}
              type="checkbox"
              checked={Boolean(value)}
              onChange={handleInputChange}
              className={checkboxClass}
              disabled={disabled}
              required={required}
              {...rest}
            />
          </div>
        );

      case 'radio':
        return (
          <div className="flex items-center">
            <input
              id={name}
              name={name}
              type="radio"
              checked={Boolean(value)}
              onChange={handleInputChange}
              className={`${checkboxClass} rounded-full`}
              disabled={disabled}
              required={required}
              {...rest}
            />
          </div>
        );

      default:
        return (
          <div className="relative">
            {leftIcon && (
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none text-gray-400">
                {leftIcon}
              </div>
            )}
            <input
              id={name}
              name={name}
              type={fieldType}
              value={displayValue}
              onChange={handleInputChange}
              className={`${baseInputClass} px-3`}
              placeholder={placeholder}
              disabled={disabled}
              readOnly={readOnly}
              required={required}
              {...rest}
            />
            {rightIcon && (
              <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none text-gray-400">
                {rightIcon}
              </div>
            )}
          </div>
        );
    }
  };

  // Render the complete field with label and error
  return (
    <div className={`mb-4 ${className}`}>
      <div className="flex justify-between items-center mb-1">
        <label htmlFor={name} className="block text-sm font-medium text-gray-700">
          {label} {required && <span className="text-red-500">*</span>}
        </label>
      </div>

      {renderField()}

      {helpText && !error && (
        <p className="mt-1 text-xs text-gray-500">{helpText}</p>
      )}

      {error && (
        <p className="mt-1 text-xs text-red-500">{error}</p>
      )}
    </div>
  );
};

export default EntityFormField;

import React, { useState, useRef, ReactNode } from 'react';
import { Upload, AlertCircle, X, AlertTriangle } from 'lucide-react';
import ImportPreviewTable, { ImportPreviewColumn, ConflictInfo } from '../ImportPreviewTable';
import Button from '../../../design-system/atoms/Button/Button';
import ErrorDisplay from '../ErrorDisplay';
import CSVFormatError from '../CSVFormatError';

export interface ImportModalProps<T> {
  /** Titolo del modale (es. "Importa Aziende") */
  title: string;
  /** Sottotitolo opzionale con istruzioni */
  subtitle?: string;
  /** Chiamata quando l'utente conferma l'importazione */
  onImport: (data: T[], overwriteIds?: string[], selectedRows?: Set<number>) => Promise<void>;
  /** Chiamata quando l'utente chiude il modale */
  onClose: () => void;
  /** Funzione per processare il file caricato e convertirlo in dati strutturati */
  processFile: (file: File) => Promise<T[]>;
  /** Chiave per identificare elementi duplicati */
  uniqueKey: string;
  /** Dati esistenti per confronto e individuazione dei duplicati */
  existingData: any[];
  /** Colonne da visualizzare nella tabella di anteprima */
  previewColumns: ImportPreviewColumn[];
  /** Funzione per validare le righe e rilevare eventuali errori */
  validateRows?: (rows: T[]) => { [rowIdx: number]: string[] };
  /** Componenti aggiuntivi da mostrare sopra la tabella di anteprima */
  extraControls?: ReactNode;
  /** Formati di file supportati */
  supportedFormats?: string[];
  /** Messaggio informativo sui formati supportati */
  formatsMessage?: string;
  /** Mostra o nasconde i pulsanti per selezionare/deselezionare tutti */
  showBulkSelectButtons?: boolean;
  /** Nasconde la tabella di anteprima standard (utile quando si usa un componente personalizzato) */
  hidePreviewTable?: boolean;
  /** Usa un'unica colonna di checkbox per selezione/sovrascrittura */
  useSingleCheckboxColumn?: boolean;
  /** Lista delle aziende disponibili per il menu a pillola */
  availableCompanies?: Array<{id: string, name?: string, ragioneSociale?: string}>;
  /** Funzione per gestire il cambio di azienda per le righe selezionate */
  onCompanyChange?: (selectedIds: string[], companyId: string) => void;
  /** Dati iniziali da mostrare nella preview */
  initialPreviewData?: T[];
  /** Callback per le righe selezionate */
  onOverwriteChange?: (selectedIds: string[]) => void;
  /** Conflitti rilevati per riga */
  conflicts?: { [rowIdx: number]: ConflictInfo };
  /** Callback per aggiornare la risoluzione di un conflitto */
  onConflictResolutionChange?: (rowIdx: number, resolution: Partial<ConflictInfo>) => void;
  /** Set delle righe selezionate per l'importazione (indici) */
  selectedRows?: Set<number>;
  /** Callback per gestire la selezione delle righe */
  onRowSelectionChange?: (selectedRows: Set<number>) => void;
}

/**
 * Componente generico per mostrare un modale di importazione dati
 */
export default function ImportModal<T extends Record<string, any>>({
  title,
  subtitle,
  onImport,
  onClose,
  processFile,
  uniqueKey,
  existingData,
  previewColumns,
  validateRows,
  extraControls,
  supportedFormats = ['.csv'],
  formatsMessage = 'Formati supportati: CSV',
  showBulkSelectButtons = true,
  hidePreviewTable = false,
  useSingleCheckboxColumn = false,
  availableCompanies,
  onCompanyChange,
  initialPreviewData = [],
  onOverwriteChange,
  conflicts,
  onConflictResolutionChange,
  selectedRows,
  onRowSelectionChange
}: ImportModalProps<T>) {
  const [dragActive, setDragActive] = useState(false);
  const [error, setError] = useState<string>('');
  const [importing, setImporting] = useState(false);
  const [preview, setPreview] = useState<T[]>(initialPreviewData);
  const [overwriteIds, setOverwriteIds] = useState<string[]>([]);
  const [rowErrors, setRowErrors] = useState<{ [rowIdx: number]: string[] }>({});
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [expectedHeaders, setExpectedHeaders] = useState<string[]>([]);
  const [foundHeaders, setFoundHeaders] = useState<string[]>([]);

  // Aggiorna la preview quando cambiano i dati iniziali
  React.useEffect(() => {
    if (initialPreviewData && initialPreviewData.length > 0) {
      setPreview(initialPreviewData);
      if (validateRows) {
        const errors = validateRows(initialPreviewData);
        setRowErrors(errors);
      }
    }
  }, [initialPreviewData, validateRows]);

  // Gestisce gli eventi di trascinamento
  const handleDrag = (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  // Gestisce il file lasciato nella zona di trascinamento
  const handleDrop = async (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const file = e.dataTransfer.files?.[0];
    if (file) {
      await handleFileProcess(file);
    }
  };

  // Gestisce la selezione del file tramite finestra di dialogo
  const handleFileInput = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      await handleFileProcess(file);
    }
  };

  // Processa il file selezionato o trascinato
  const handleFileProcess = async (file: File) => {
    setError('');
    setRowErrors({});
    setImporting(true);
    setExpectedHeaders([]);
    setFoundHeaders([]);

    // Verifica estensione
    const fileExt = '.' + (file.name.split('.').pop()?.toLowerCase() || '');
    if (!supportedFormats.includes(fileExt)) {
      setError(`Formato file non supportato. Formati accettati: ${supportedFormats.join(', ')}`);
      setImporting(false);
      return;
    }

    // Verifica dimensione file (max 10MB)
    const maxFileSize = 10 * 1024 * 1024; // 10MB
    if (file.size > maxFileSize) {
      setError(`Il file  troppo grande (${(file.size / (1024 * 1024)).toFixed(2)}MB). Dimensione massima: 10MB`);
      setImporting(false);
      return;
    }

    try {
      // Processa il file
      const processedData = await processFile(file);

      // Verifica che ci siano dati
      if (!processedData || processedData.length === 0) {
        throw new Error('Il file non contiene dati validi. Verifica che il file CSV sia formattato correttamente.');
      }

      // Verifica la qualit dei dati
      const firstRow = processedData[0];
      if (firstRow && Object.keys(firstRow).length <= 1) {
        throw new Error('Il formato del file non sembra corretto. Verifica che il delimitatore CSV sia corretto (es. ";" o ",")');
      }

      setPreview(processedData);

      // Valida i dati se  disponibile una funzione di validazione
      if (validateRows) {
        const errors = validateRows(processedData);
        setRowErrors(errors);

        // Se ci sono errori in tutte le righe, potrebbe indicare un problema di formato
        const rowCount = processedData.length;
        const errorCount = Object.keys(errors).length;

        if (errorCount === rowCount && rowCount > 0) {
          // Recupera il primo errore per mostrarlo come esempio
          const firstErrorRow = Object.keys(errors)[0];
          const firstError = errors[Number(firstErrorRow)]?.[0] || '';

          // Se tutti gli errori sono uguali, potrebbe essere un problema di formato
          if (firstError.includes('obbligator')) {
            setError(`Possibile formato CSV non compatibile: ${firstError}. Verifica che le colonne CSV corrispondano ai campi richiesti.`);
          }
        }
      }
    } catch (err) {
      console.error('Errore durante l\'elaborazione del file:', err);

      // Fornisci messaggi di errore pi dettagliati
      const errorMessage = err instanceof Error ? err.message : 'Errore durante l\'elaborazione del file';

      // Gestisci errori specifici
      if (errorMessage.includes('Intestazioni attese') && errorMessage.includes('Intestazioni trovate')) {
        // Estrai le intestazioni attese e trovate dall'errore
        try {
          const expectedMatch = errorMessage.match(/Intestazioni attese: ([^.]+)/);
          const foundMatch = errorMessage.match(/Intestazioni trovate: ([^.]+)/);

          if (expectedMatch && expectedMatch[1]) {
            setExpectedHeaders(expectedMatch[1].split(', ').map(h => h.trim()));
          }

          if (foundMatch && foundMatch[1]) {
            setFoundHeaders(foundMatch[1].split(', ').map(h => h.trim()));
          }
        } catch (parseErr) {
          console.error('Errore nel parsing delle intestazioni:', parseErr);
        }

        setError(errorMessage);
      } else if (errorMessage.includes('Parse error')) {
        setError('Errore durante l\'analisi del CSV. Verifica che il delimitatore sia corretto (;) e che il file non contenga caratteri speciali non validi.');
      } else if (errorMessage.includes('Il formato del CSV non  compatibile')) {
        setError(errorMessage);
      } else {
        setError(`Errore durante l'elaborazione del file: ${errorMessage}`);
      }

      setPreview([]);
    } finally {
      setImporting(false);
    }
  };

  // Gestore per la conferma dell'importazione
  const handleImport = async () => {
    if (preview.length === 0) return;

    // Se ci sono errori di validazione, non procedere con l'importazione
    if (Object.keys(rowErrors).length > 0) {
      setError('Ci sono errori nei dati. Correggi il file e riprova.');
      return;
    }

    // Verifica che ci siano righe selezionate per l'importazione
    if (selectedRows && selectedRows.size === 0) {
      setError('Nessuna riga selezionata per l\'importazione');
      return;
    }

    setImporting(true);
    setError('');

    try {
      // Passa anche le righe selezionate alla funzione di importazione
      await onImport(preview, overwriteIds, selectedRows);
      // Non chiamiamo onClose qui, lo lasciamo gestire al chiamante in base al risultato
    } catch (err) {
      console.error('Errore durante l\'importazione:', err);
      setError(err instanceof Error ? err.message : 'Errore durante l\'importazione');
      setImporting(false);
    }
  };

  // Determina il testo del pulsante di importazione in base alle selezioni
  const getImportButtonText = () => {
    if (importing) return 'Importazione in corso...';

    if (useSingleCheckboxColumn && preview.length > 0) {
      // Conta quanti elementi sono selezionati (esistenti da sovrascrivere)
      const selectedCount = overwriteIds.length;

      // Se abbiamo selectedRows, usa quello per calcolare il numero di righe selezionate
      if (selectedRows && selectedRows.size !== undefined) {
        // Filtra le righe selezionate per distinguere tra nuove e aggiornamenti
        const selectedIndices = Array.from(selectedRows);
        let newCount = 0;
        let updateCount = 0;

        selectedIndices.forEach(index => {
          if (index < preview.length) {
            const item = preview[index];
            const isExisting = item[uniqueKey] && existingData.some(e => {
              const itemValue = String(item[uniqueKey] || '').trim().toUpperCase();
              const existingValue = String(e[uniqueKey] || '').trim().toUpperCase();
              return itemValue === existingValue;
            });

            if (isExisting) {
              //  un elemento esistente, conta solo se  selezionato per sovrascrittura
              if (item.id && overwriteIds.includes(item.id)) {
                updateCount++;
              }
            } else {
              //  un nuovo elemento
              newCount++;
            }
          }
        });

        const totalToImport = newCount + updateCount;

        if (totalToImport === 0) {
          return 'Nessun elemento da importare';
        } else if (updateCount > 0 && newCount > 0) {
          return `Importa ${totalToImport} elementi (${newCount} nuovi, ${updateCount} aggiornamenti)`;
        } else if (updateCount > 0) {
          return `Aggiorna ${updateCount} elementi`;
        } else if (newCount > 0) {
          return `Importa ${newCount} nuovi elementi`;
        }
      } else {
        // Fallback alla logica precedente se selectedRows non  disponibile
        // Numero di elementi che sono nuovi (non duplicati)
        const nonDuplicateCount = preview.filter(item => 
          !item[uniqueKey] || !existingData.some(e => {
            // Normalizza i valori per il confronto
            const itemValue = String(item[uniqueKey] || '').trim().toUpperCase();
            const existingValue = String(e[uniqueKey] || '').trim().toUpperCase();
            return itemValue === existingValue;
          })
        ).length;

        // Calcola il totale da importare (selezionati + nuovi)
        const totalToImport = selectedCount + nonDuplicateCount;

        if (totalToImport === 0) {
          return 'Nessun elemento da importare';
        } else if (selectedCount > 0 && nonDuplicateCount > 0) {
          return `Importa ${totalToImport} elementi (${nonDuplicateCount} nuovi, ${selectedCount} aggiornamenti)`;
        } else if (selectedCount > 0) {
          return `Aggiorna ${selectedCount} elementi`;
        } else if (nonDuplicateCount > 0) {
          return `Importa ${nonDuplicateCount} nuovi elementi`;
        }
      }
    }

    return `Importa tutti (${preview.length})`;
  };

  return (
    <div className="fixed inset-0 z-[1000] flex items-center justify-center bg-black bg-opacity-30 p-4">
      <div className="bg-white rounded-2xl shadow-xl max-w-6xl w-full mx-auto flex flex-col max-h-[90vh]">
        {/* Header fisso */}
        <div className="flex justify-between items-center p-6 border-b border-gray-200 flex-shrink-0">
          <div>
            <h2 className="text-xl font-semibold text-gray-800">{title}</h2>
            {subtitle && <p className="text-sm text-gray-500 mt-1">{subtitle}</p>}
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-500"
            aria-label="Chiudi"
          >
            <X className="h-5 w-5" />
          </button>
        </div>

        {/* Errori fissi in alto - altezza limitata */}
        {error && (
          <div className="border-b border-gray-200 flex-shrink-0 max-h-32 overflow-y-auto">
            {error.includes('formato CSV') || error.includes('Intestazioni') || error.includes('delimitatore') || error.includes('formato del file') ? (
              <CSVFormatError 
                message={error}
                expectedHeaders={expectedHeaders}
                foundHeaders={foundHeaders}
                onClose={() => {
                  setError('');
                  setPreview([]); // Reset preview to allow new file upload
                }}
                className="m-4"
              />
            ) : (
              <ErrorDisplay 
                type="error"
                message={error}
                onClose={() => setError('')}
                className="m-4"
              />
            )}
          </div>
        )}

        {/* Contenuto scrollabile */}
        <div className="flex-1 overflow-y-auto p-6 min-h-0">

        {!preview.length ? (
          <div
            className={`relative border-2 border-dashed rounded-xl p-8 text-center ${
              dragActive ? 'border-blue-500 bg-blue-50' : 'border-gray-300'
            }`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
          >
            <Upload className="mx-auto h-12 w-12 text-gray-400" />
            <p className="mt-4 text-sm text-gray-600">
              Trascina qui il file, oppure{' '}
              <button
                type="button"
                onClick={() => fileInputRef.current?.click()}
                className="text-blue-600 hover:text-blue-500 font-medium"
              >
                sfoglia
              </button>
            </p>
            <p className="mt-2 text-xs text-gray-500">
              {formatsMessage}
            </p>
            <input
              ref={fileInputRef}
              type="file"
              className="hidden"
              accept={supportedFormats.join(',')}
              onChange={handleFileInput}
            />
          </div>
        ) : (
          <div className="space-y-4">
            {/* Controlli extra (es. selezione azienda) */}
            {extraControls && <div>{extraControls}</div>}

            {/* Tabella di anteprima */}
            {!hidePreviewTable && (
              <ImportPreviewTable
                columns={previewColumns}
                preview={preview}
                existing={existingData}
                uniqueKey={uniqueKey}
                rowErrors={rowErrors}
                onOverwriteChange={setOverwriteIds}
                showBulkSelectButtons={showBulkSelectButtons}
                useSingleCheckboxColumn={useSingleCheckboxColumn}
                availableCompanies={availableCompanies}
                onCompanyChange={onCompanyChange}
                overwriteIds={overwriteIds}
                conflicts={conflicts}
                onConflictResolutionChange={onConflictResolutionChange}
                selectedRows={selectedRows}
                onRowSelectionChange={onRowSelectionChange}
              />
            )}
          </div>
        )}
        </div>

        {/* Footer fisso con pulsanti */}
        <div className="border-t border-gray-200 p-6 flex justify-end space-x-3 flex-shrink-0">
          <Button
            onClick={onClose}
            variant="secondary"
            disabled={importing}
          >
            Annulla
          </Button>
          <Button
            onClick={handleImport}
            variant="primary"
            disabled={preview.length === 0 || importing || Object.keys(rowErrors).length > 0}
          >
            {getImportButtonText()}
          </Button>
        </div>
      </div>
    </div>
  );
}

import React, { useState, useCallback } from 'react';
// import { useTranslation } from 'react-i18next';
import { 
  ChevronLeft,
  ChevronRight
} from 'lucide-react';
import SortableColumn, { SortDirection } from './SortableColumn';
// import Pagination from '../ui/Pagination'; // Componente non disponibile
import { Button } from '../../../design-system/atoms/Button';
import useSorting from '../../../hooks/useSorting';
import usePagination from '../../../hooks/usePagination';
import { FilterCondition } from '../filters/FilterBuilder';

export interface DataTableColumn<T = any> {
  key: string;
  label: string;
  header?: string; // Aggiunto per retrocompatibilit - alcuni componenti usano header invece di label
  width?: number;
  minWidth?: number;
  sortable?: boolean;
  sortKey?: string; // Chiave usata per l'ordinamento (se diversa da 'key')
  renderHeader?: (col: DataTableColumn<T>) => React.ReactNode;
  renderCell?: (row: T, rowIndex: number) => React.ReactNode;
  filterable?: boolean;
  filterType?: 'string' | 'number' | 'date' | 'boolean' | 'select';
  filterOptions?: { value: string; label: string }[]; // For 'select' type filtering
}

interface DataTableProps<T = any> {
  columns: DataTableColumn<T>[];
  data: T[];
  className?: string;
  tableClassName?: string;
  theadClassName?: string;
  tbodyClassName?: string;
  trClassName?: (row: T, index: number) => string;
  onRowClick?: (row: T, index: number) => void;
  keyExtractor?: (row: T, index: number) => string | number;
  pagination?: boolean;
  initialPageSize?: number;
  pageSizeOptions?: number[];
  emptyMessage?: string;
  stickyHeader?: boolean;
  loadingOverlay?: boolean;
  selectionMode?: boolean;
  selectedIds?: string[];
  onSelectRow?: (row: T, selected: boolean) => void;
  idExtractor?: (row: T) => string;
  onFilter?: (filters: FilterCondition[]) => void;
  initialFilters?: FilterCondition[];
  customFilterElement?: React.ReactNode;
  exportOptions?: {
    show?: boolean;
    onExport?: (format: 'csv' | 'excel') => void;
  };

renderActions?: (row: T) => React.ReactNode;
}

const DataTable = <T extends object>({
  columns,
  data,
  className = '',
  tableClassName = '',
  theadClassName = '',
  tbodyClassName = '',
  trClassName = () => '',
  onRowClick,
  keyExtractor = (_, index) => index,
  pagination = true,
  initialPageSize = 10,
  pageSizeOptions = [10, 25, 50, 100],
  emptyMessage,
  stickyHeader = false,
  loadingOverlay = false,
  selectionMode = false,
  selectedIds = [],
  onSelectRow,
  idExtractor = (row: any) => row.id,
  onFilter,
  initialFilters,
  customFilterElement,
  exportOptions,
  renderActions,
}: DataTableProps<T>) => {
  // const { t } = useTranslation();

  // Default empty message
  const defaultEmptyMessage = "Nessun dato disponibile";

  // Use provided or default empty message
  const displayEmptyMessage = emptyMessage || defaultEmptyMessage;

  // Gestione ordinamento
  const { sortedData, sortKey, sortDirection, setSorting } = useSorting<T>({
    data,
  });

  // Gestione paginazione
  const {
    paginatedData,
    totalPages,
    currentPage,
    pageSize,
    totalItems,
    goToPage,
    setPageSize,
  } = usePagination<T>({
    data: sortedData,
    pageSize: initialPageSize,
  });

  // Determina i dati da visualizzare
  const displayData = pagination ? paginatedData : sortedData;

  // Handler per il click sull'header della colonna
  const handleSortColumn = (colKey: string, direction: SortDirection) => {
    setSorting(colKey, direction);
  };

  // Rendering dell'header per una colonna
  const renderColumnHeader = (column: DataTableColumn<T>) => {
    if (column.renderHeader) {
      return column.renderHeader(column);
    }

    // Se la label  vuota, non renderizzare nulla
    if (!column.label && !column.header) {
      return <span></span>;
    }

    const sortableKey = column.sortKey || column.key;

    if (column.sortable) {
      return (
        <SortableColumn
          label={column.label || column.header || ''}
          sortKey={sortableKey}
          currentSortKey={sortKey || undefined}
          currentDirection={sortDirection}
          onSort={handleSortColumn}
        />
      );
    }

    return column.label || column.header || '';
  };

  // Rendering di una cella
  const renderCell = (row: T, column: DataTableColumn<T>, rowIndex: number) => {
    // Special case for actions column
    if (column.key === 'actions' && renderActions) {
      return renderActions(row);
    }

    if (column.renderCell) {
      return column.renderCell(row, rowIndex);
    }

    // Fallback per accedere ai valori delle propriet
    const value = (row as any)[column.key];
    return value !== undefined ? String(value) : '';
  };

  // Handler per il cambio pagina
  const handlePageChange = (page: number) => {
    goToPage(page);
  };

  // Handler per il cambio dimensione pagina
  const handlePageSizeChange = (size: number) => {
    setPageSize(size);
  };

  // Handler for exports
  const handleExport = (format: 'csv' | 'excel') => {
    if (exportOptions?.onExport) {
      exportOptions.onExport(format);
    }
  };

  return (
    <div className={`w-full relative ${className}`}>
      {/* Optional custom filter element */}
      {customFilterElement && (
        <div className="mb-4">
          {customFilterElement}
        </div>
      )}

      {/* Export options */}
      {exportOptions?.show && (
        <div className="flex justify-end mb-4 gap-2">
          <Button 
            variant="outline" 
            size="sm"
            onClick={() => handleExport('csv')}
          >
            Esporta CSV
          </Button>
          <Button 
            variant="outline" 
            size="sm"
            onClick={() => handleExport('excel')}
          >
            Esporta Excel
          </Button>
        </div>
      )}

      {/* Loading overlay */}
      {loadingOverlay && (
        <div className="absolute inset-0 bg-white/50 flex items-center justify-center z-10">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
        </div>
      )}

      {/* Tabella */}
      <div className="w-full overflow-x-auto">
        <table className={`w-full divide-y divide-gray-200 ${tableClassName}`}>
          <thead className={`bg-gray-50 ${stickyHeader ? 'sticky top-0 z-10' : ''} ${theadClassName}`}>
            <tr>
              {columns.map((column) => (
                <th
                  key={column.key}
                  style={{ width: column.width, minWidth: column.minWidth }}
                  className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider whitespace-nowrap"
                >
                  {renderColumnHeader(column)}
                </th>
              ))}
            </tr>
          </thead>
          <tbody className={`bg-white divide-y divide-gray-200 ${tbodyClassName}`}>
            {displayData.length > 0 ? (
              displayData.map((row, rowIndex) => {
                const rowId = idExtractor(row);
                // Assicurati che selectedIds sia un array prima di usare includes
                const isSelected = Array.isArray(selectedIds) && selectedIds.includes(rowId);

                return (
                  <tr
                    key={keyExtractor(row, rowIndex)}
                    className={`hover:bg-gray-50 ${trClassName(row, rowIndex)} ${isSelected ? 'bg-blue-50' : ''}`}
                    onClick={() => onRowClick && onRowClick(row, rowIndex)}
                    style={{ cursor: onRowClick ? 'pointer' : 'default' }}
                  >
                    {columns.map((column) => (
                      <td
                        key={`${keyExtractor(row, rowIndex)}-${column.key}`}
                        className="px-4 py-3 whitespace-nowrap text-sm text-gray-500"
                      >
                        {renderCell(row, column, rowIndex)}
                      </td>
                    ))}
                  </tr>
                );
              })
            ) : (
              <tr>
                <td
                  colSpan={columns.length}
                  className="px-4 py-8 text-center text-sm text-gray-500"
                >
                  {displayEmptyMessage}
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Paginazione */}
      {pagination && totalPages > 0 && (
        <div className="flex items-center justify-between px-4 py-3 bg-white border-t border-gray-200">
          <div className="flex items-center text-sm text-gray-700">
            <span>
              Mostrando {((currentPage - 1) * pageSize) + 1} - {Math.min(currentPage * pageSize, totalItems)} di {totalItems} risultati
            </span>
          </div>

          <div className="flex items-center space-x-2">
            {/* Page size selector */}
            <select
              value={pageSize}
              onChange={(e) => handlePageSizeChange(Number(e.target.value))}
              className="px-2 py-1 border border-gray-300 rounded text-sm"
            >
              {pageSizeOptions.map(size => (
                <option key={size} value={size}>{size} per pagina</option>
              ))}
            </select>

            {/* Previous button */}
            <Button
              variant="outline"
              size="sm"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage <= 1}
            >
              <ChevronLeft className="h-4 w-4" />
            </Button>

            {/* Page numbers */}
            <span className="text-sm text-gray-700">
              Pagina {currentPage} di {totalPages}
            </span>

            {/* Next button */}
            <Button
              variant="outline"
              size="sm"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage >= totalPages}
            >
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        </div>
      )}
    </div>
  );
};

export { DataTableColumn };
export default DataTable;

import React, { useState } from 'react';
import { Button } from '../../../design-system/atoms/Button';
import { 
  Eye,
  FileDown,
  FileText,
  FileType,
  Printer,
  RefreshCw
} from 'lucide-react';
import googleApiClient from '../../../services/googleApiClient';

interface GoogleDocsPreviewProps {
  documentUrl?: string;
  templateId?: string;
  placeholderData?: Record<string, string>;
  documentType: string;
  onGenerationSuccess?: (fileUrl: string, fileName: string) => void;
  className?: string;
}

/**
 * GoogleDocsPreview Component
 * 
 * Displays a preview and control options for Google Docs/Slides templates.
 * Features:
 * - Generate documents with real data
 * - Preview generated PDFs
 * - Download generated documents
 */
const GoogleDocsPreview: React.FC<GoogleDocsPreviewProps> = ({
  documentUrl,
  templateId,
  placeholderData = {},
  documentType,
  onGenerationSuccess,
  className = ''
}) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [generatedFile, setGeneratedFile] = useState<{ url: string; name: string } | null>(null);
  const [showPreview, setShowPreview] = useState(false);

  // Generate document from template
  const handleGenerate = async () => {
    if (!documentUrl && !templateId) {
      setError('Nessun template selezionato');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await googleApiClient.generateDocument(documentType, placeholderData);

      if (response.success && response.fileUrl) {
        setGeneratedFile({
          url: response.fileUrl,
          name: response.fileName || 'documento.pdf'
        });

        if (onGenerationSuccess) {
          onGenerationSuccess(response.fileUrl, response.fileName || 'documento.pdf');
        }

        setShowPreview(true);
      } else {
        setError(response.message || 'Errore durante la generazione del documento');
      }
    } catch (err: any) {
      console.error('Error generating document:', err);
      setError(err.message || 'Impossibile generare il documento');
    } finally {
      setLoading(false);
    }
  };

  // Print the generated document
  const handlePrint = () => {
    if (generatedFile?.url) {
      const printWindow = window.open(generatedFile.url, '_blank');
      if (printWindow) {
        printWindow.addEventListener('load', () => {
          printWindow.print();
        });
      }
    }
  };

  // Download the generated document
  const handleDownload = () => {
    if (generatedFile?.url) {
      const link = document.createElement('a');
      link.href = generatedFile.url;
      link.download = generatedFile.name;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  return (
    <div className={`google-docs-preview ${className}`}>
      <div className="border rounded-xl overflow-hidden">
        <div className="bg-gray-50 p-3 border-b flex justify-between items-center">
          <h3 className="text-gray-700 font-medium">
            Preview Documento {generatedFile ? `- ${generatedFile.name}` : ''}
          </h3>
          <div className="flex gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={handleGenerate}
              disabled={loading || (!documentUrl && !templateId)}
            >
              {loading ? (
                <>
                  <RefreshCw className="h-4 w-4 mr-1 animate-spin" />
                  Generando...
                </>
              ) : (
                <>
                  <FileType className="h-4 w-4 mr-1" />
                  Genera PDF
                </>
              )}
            </Button>

            {generatedFile && (
              <>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handlePrint}
                >
                  <Printer className="h-4 w-4 mr-1" />
                  Stampa
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handleDownload}
                >
                  <FileDown className="h-4 w-4 mr-1" />
                  Scarica
                </Button>
              </>
            )}
          </div>
        </div>

        {error && (
          <div className="bg-red-50 text-red-700 p-3 border-b text-sm">
            {error}
          </div>
        )}

        <div className="p-4 bg-white">
          {!generatedFile && !showPreview ? (
            <div className="border-2 border-dashed rounded-xl p-8 flex flex-col items-center justify-center text-center text-gray-500">
              <FileText className="h-12 w-12 mb-3 text-gray-400" />
              <p className="mb-2">Nessun documento generato</p>
              <p className="text-sm">Clicca su "Genera PDF" per creare un documento usando i dati inseriti</p>
            </div>
          ) : (
            <div className="aspect-[210/297] w-full bg-gray-100 rounded">
              {showPreview && generatedFile && (
                <iframe
                  src={`${generatedFile.url}#toolbar=0&navpanes=0`}
                  className="w-full h-full rounded"
                  title="Document preview"
                />
              )}
            </div>
          )}
        </div>

        <div className="bg-gray-50 p-3 border-t flex justify-between items-center">
          <div className="text-sm text-gray-600">
            {documentType === 'attestato' && 'Anteprima Attestato'}
            {documentType === 'lettera_incarico' && 'Anteprima Lettera di Incarico'}
            {documentType !== 'attestato' && documentType !== 'lettera_incarico' && `Anteprima ${documentType}`}
          </div>

          {generatedFile && (
            <Button
              variant="link"
              size="sm"
              onClick={() => setShowPreview(!showPreview)}
              className="text-blue-600"
            >
              <Eye className="h-4 w-4 mr-1" />
              {showPreview ? 'Nascondi anteprima' : 'Mostra anteprima'}
            </Button>
          )}
        </div>
      </div>
    </div>
  );
};

export default GoogleDocsPreview;

import React, { useState } from 'react';
import { FileEdit, AlertTriangle, Check } from 'lucide-react';
import { Button } from '../../../design-system/atoms/Button';
import googleApiClient from '../../../services/googleApiClient';

interface GoogleTemplateProviderProps {
  documentType: string;
  onTemplateSelected?: (templateUrl: string, templateId: string) => void;
  className?: string;
}

interface GoogleTemplate {
  id: string;
  name: string;
  googleDocsUrl: string;
  type: string;
  isDefault: boolean;
}

interface GoogleTemplateResponse {
  success: boolean;
  template: GoogleTemplate;
  error?: string;
}

interface GenerateDocumentResponse {
  success: boolean;
  message?: string;
  fileName?: string;
  fileUrl?: string;
  error?: string;
  details?: string;
}

/**
 * GoogleTemplateProvider Component
 * 
 * A reusable component that provides Google Docs/Slides template functionality.
 * It allows users to:
 * 1. Connect to Google Docs/Slides templates
 * 2. Generate documents based on templates
 * 3. Get default templates for specific document types
 */
const GoogleTemplateProvider: React.FC<GoogleTemplateProviderProps> = ({
  documentType,
  onTemplateSelected,
  className = '',
}) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [templateUrl, setTemplateUrl] = useState<string>('');
  const [success, setSuccess] = useState(false);

  // Get the default template for this document type
  const getDefaultTemplate = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await googleApiClient.getDefaultTemplate(documentType);

      if (response.success && response.template) {
        setTemplateUrl(response.template.googleDocsUrl);
        setSuccess(true);

        if (onTemplateSelected) {
          onTemplateSelected(response.template.googleDocsUrl, response.template.id);
        }
      } else {
        setError(response.error || 'Nessun template predefinito trovato');
        setSuccess(false);
      }
    } catch (err: any) {
      console.error('Error getting default template:', err);
      setError(err.message || 'Impossibile ottenere il template');
      setSuccess(false);
    } finally {
      setLoading(false);
    }
  };

  // Generate a document using the current template and data
  const generateDocument = async (data: Record<string, string>) => {
    if (!templateUrl) {
      setError('Seleziona prima un template');
      return null;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await googleApiClient.generateDocument(documentType, data);

      if (response.success) {
        setSuccess(true);
        return response;
      } else {
        setError('Errore nella generazione del documento');
        setSuccess(false);
        return null;
      }
    } catch (err: any) {
      console.error('Error generating document:', err);
      setError(err.message || 'Impossibile generare il documento');
      setSuccess(false);
      return null;
    } finally {
      setLoading(false);
    }
  };

  // Handle manual URL input
  const handleUrlChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setTemplateUrl(e.target.value);
    setError(null);

    // Validate URL format
    if (e.target.value && !e.target.value.includes('docs.google.com')) {
      setError('Il link deve puntare a un documento Google Docs o Google Slides');
      setSuccess(false);
    } else if (e.target.value && onTemplateSelected) {
      setSuccess(true);
      onTemplateSelected(e.target.value, '');
    }
  };

  return (
    <div className={`google-template-provider ${className}`}>
      <div className="bg-blue-50 rounded-lg border border-blue-200 p-4">
        <h3 className="text-md font-semibold text-blue-800 flex items-center">
          <FileEdit className="h-5 w-5 mr-2" />
          Template Google Docs/Slides
        </h3>

        <p className="mt-2 text-sm text-blue-700">
          Utilizza un documento Google Docs o Google Slides come template.
          Il sistema sostituir i placeholder nel formato {'{{NOME_PLACEHOLDER}}'} con i valori effettivi.
        </p>

        <div className="mt-4">
          <label className="block text-sm font-medium text-blue-700 mb-1">URL Google Docs/Slides</label>
          <div className="flex gap-2">
            <input
              type="text"
              value={templateUrl}
              onChange={handleUrlChange}
              placeholder="https://docs.google.com/document/d/..."
              className="flex-1 rounded-md border-blue-300 shadow-sm focus:border-blue-500 focus:ring-blue-500 text-sm"
            />
            <Button
              onClick={getDefaultTemplate}
              size="sm"
              disabled={loading}
              variant="outline"
              className="whitespace-nowrap"
            >
              {loading ? 'Caricamento...' : 'Template Predefinito'}
            </Button>
          </div>
          <p className="mt-1 text-xs text-blue-600">
            Formato: https://docs.google.com/document/d/ID_DOCUMENTO o https://docs.google.com/presentation/d/ID_PRESENTAZIONE
          </p>
        </div>

        {error && (
          <div className="mt-3 p-2 bg-red-50 border border-red-200 rounded-md text-red-700 text-sm flex items-start">
            <AlertTriangle className="h-4 w-4 mr-1 flex-shrink-0 mt-0.5" />
            <span>{error}</span>
          </div>
        )}

        {success && !error && templateUrl && (
          <div className="mt-3 p-2 bg-green-50 border border-green-200 rounded-md text-green-700 text-sm flex items-center">
            <Check className="h-4 w-4 mr-1" />
            <span>Template selezionato correttamente</span>
          </div>
        )}

        <div className="mt-4 text-sm text-blue-700">
          <h4 className="font-medium mb-2">Placeholder disponibili:</h4>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-2">
            {[
              { name: 'NOME', desc: 'Nome' },
              { name: 'COGNOME', desc: 'Cognome' },
              { name: 'NOME_COMPLETO', desc: 'Nome completo' },
              { name: 'CORSO_TITOLO', desc: 'Titolo corso' },
              { name: 'DATA_INIZIO', desc: 'Data inizio' },
              { name: 'DATA_FINE', desc: 'Data fine' },
              { name: 'ORE_TOTALI', desc: 'Ore totali' },
              { name: 'AZIENDA_NOME', desc: 'Nome azienda' },
              { name: 'DATA_GENERAZIONE', desc: 'Data generazione' }
            ].map((ph) => (
              <div key={ph.name} className="bg-white rounded p-1 border border-blue-100 text-xs">
                <span className="font-mono text-blue-800">{'{{' + ph.name + '}}'}</span>
                <span className="text-gray-500 ml-1">{ph.desc}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default GoogleTemplateProvider;

import React, { useState, useEffect } from 'react';
