        </Card>
      );
      const card = screen.getByTestId('card');
      expect(card).toHaveClass(
        'bg-white',
        'shadow-md',
        'p-6',
        'hover:shadow-lg',
        'custom-card'
      );
    });
  });

  describe('Accessibility', () => {
    it('supports custom aria attributes', () => {
      render(
        <Card
          aria-label="Custom card"
          aria-describedby="card-description"
          data-testid="card"
        >
          Accessible card
        </Card>
      );
      const card = screen.getByTestId('card');
      expect(card).toHaveAttribute('aria-label', 'Custom card');
      expect(card).toHaveAttribute('aria-describedby', 'card-description');
    });

    it('applies role="button" when clickable', () => {
      render(
        <Card clickable data-testid="card">
          Clickable card
        </Card>
      );
      expect(screen.getByTestId('card')).toHaveAttribute('role', 'button');
    });

    it('does not apply role="button" when not clickable', () => {
      render(
        <Card data-testid="card">
          Normal card
        </Card>
      );
      expect(screen.getByTestId('card')).not.toHaveAttribute('role', 'button');
    });

    it('is focusable when clickable', () => {
      render(
        <Card clickable data-testid="card">
          Focusable card
        </Card>
      );
      const card = screen.getByTestId('card');
      expect(card).toHaveAttribute('tabIndex', '0');

      card.focus();
      expect(card).toHaveFocus();
    });

    it('is not focusable when disabled', () => {
      render(
        <Card clickable disabled data-testid="card">
          Disabled card
        </Card>
      );
      const card = screen.getByTestId('card');
      expect(card).toHaveAttribute('tabIndex', '-1');
    });
  });

  describe('Keyboard navigation', () => {
    it('responds to Enter key when clickable', () => {
      const handleClick = vi.fn();
      render(
        <Card clickable onClick={handleClick} data-testid="card">
          Keyboard accessible card
        </Card>
      );

      const card = screen.getByTestId('card');
      fireEvent.keyDown(card, { key: 'Enter' });

      expect(handleClick).toHaveBeenCalledTimes(1);
    });

    it('responds to Space key when clickable', () => {
      const handleClick = vi.fn();
      render(
        <Card clickable onClick={handleClick} data-testid="card">
          Keyboard accessible card
        </Card>
      );

      const card = screen.getByTestId('card');
      fireEvent.keyDown(card, { key: ' ' });

      expect(handleClick).toHaveBeenCalledTimes(1);
    });

    it('does not respond to keyboard when disabled', () => {
      const handleClick = vi.fn();
      render(
        <Card clickable disabled onClick={handleClick} data-testid="card">
          Disabled card
        </Card>
      );

      const card = screen.getByTestId('card');
      fireEvent.keyDown(card, { key: 'Enter' });
      fireEvent.keyDown(card, { key: ' ' });

      expect(handleClick).not.toHaveBeenCalled();
    });

    it('does not respond to other keys', () => {
      const handleClick = vi.fn();
      render(
        <Card clickable onClick={handleClick} data-testid="card">
          Keyboard accessible card
        </Card>
      );

      const card = screen.getByTestId('card');
      fireEvent.keyDown(card, { key: 'Escape' });
      fireEvent.keyDown(card, { key: 'Tab' });

      expect(handleClick).not.toHaveBeenCalled();
    });
  });

  describe('Complex content', () => {
    it('renders complex nested content correctly', () => {
      render(
        <Card data-testid="card">
          <div>
            <h1>Card Title</h1>
            <p>Card description with <strong>bold text</strong></p>
            <button>Action Button</button>
          </div>
        </Card>
      );

      expect(screen.getByText('Card Title')).toBeInTheDocument();
      expect(screen.getByText('bold text')).toBeInTheDocument();
      expect(screen.getByRole('button', { name: 'Action Button' })).toBeInTheDocument();
    });

    it('handles multiple child elements', () => {
      render(
        <Card data-testid="card">
          <h2>Title</h2>
          <p>Description</p>
          <div>Footer content</div>
        </Card>
      );

      expect(screen.getByText('Title')).toBeInTheDocument();
      expect(screen.getByText('Description')).toBeInTheDocument();
      expect(screen.getByText('Footer content')).toBeInTheDocument();
    });
  });

  describe('Edge cases', () => {
    it('handles empty content gracefully', () => {
      render(<Card data-testid="card"></Card>);
      expect(screen.getByTestId('card')).toBeInTheDocument();
    });

    it('handles null content gracefully', () => {
      render(<Card data-testid="card">{null}</Card>);
      expect(screen.getByTestId('card')).toBeInTheDocument();
    });

    it('handles undefined content gracefully', () => {
      render(<Card data-testid="card">{undefined}</Card>);
      expect(screen.getByTestId('card')).toBeInTheDocument();
    });

    it('handles numeric content', () => {
      render(<Card data-testid="card">{42}</Card>);
      expect(screen.getByText('42')).toBeInTheDocument();
    });

    it('handles boolean content', () => {
      render(<Card data-testid="card">{true}</Card>);
      expect(screen.getByTestId('card')).toBeInTheDocument();
      // Boolean true renders as empty in React
    });

    it('combines all props correctly', () => {
      const handleClick = vi.fn();
      render(
        <Card
          variant="elevated"
          size="lg"
          hoverable
          clickable
          loading
          onClick={handleClick}
          className="custom-card"
          aria-label="Complex card"
          data-testid="card"
        >
          Complex card content
        </Card>
      );

      const card = screen.getByTestId('card');
      expect(card).toHaveClass(
        'bg-white',
        'shadow-md',
        'p-6',
        'hover:shadow-lg',
        'cursor-pointer',
        'animate-pulse',
        'custom-card'
      );
      expect(card).toHaveAttribute('role', 'button');
      expect(card).toHaveAttribute('tabIndex', '0');
      expect(card).toHaveAttribute('aria-label', 'Complex card');

      fireEvent.click(card);
      expect(handleClick).toHaveBeenCalledTimes(1);
    });
  });

  describe('Performance', () => {
    it('does not re-render unnecessarily', () => {
      const renderSpy = vi.fn();
      const TestCard = ({ children, ...props }: { children: React.ReactNode; [key: string]: any }) => {
        renderSpy();
        return <Card {...props}>{children}</Card>;
      };

      const { rerender } = render(<TestCard>Content</TestCard>);
      expect(renderSpy).toHaveBeenCalledTimes(1);

      // Re-render with same props should not cause additional renders
      rerender(<TestCard>Content</TestCard>);
      expect(renderSpy).toHaveBeenCalledTimes(2); // React will still call the function
    });
  });

  describe('Custom HTML attributes', () => {
    it('passes through custom HTML attributes', () => {
      render(
        <Card
          data-testid="card"
          id="custom-card"
          title="Card tooltip"
          style={{ backgroundColor: 'red' }}
        >
          Custom attributes card
        </Card>
      );

      const card = screen.getByTestId('card');
      expect(card).toHaveAttribute('id', 'custom-card');
      expect(card).toHaveAttribute('title', 'Card tooltip');
      expect(card).toHaveAttribute('style', 'background-color: red;');
    });

    it('supports data attributes', () => {
      render(
        <Card
          data-testid="card"
          data-analytics="card-click"
          data-category="product"
        >
          Data attributes card
        </Card>
      );

      const card = screen.getByTestId('card');
      expect(card).toHaveAttribute('data-analytics', 'card-click');
      expect(card).toHaveAttribute('data-category', 'product');
    });
  });
});

/**
 * Design System - Card Component (Molecule)
 * Week 8 Implementation - Component Library
 */

import React from 'react';
import { cn } from '../../utils';

// Card variants and sizes
export type CardVariant = 'default' | 'outlined' | 'elevated' | 'filled';
export type CardSize = 'sm' | 'md' | 'lg' | 'xl';

export interface CardProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Card variant */
  variant?: CardVariant;
  /** Card size */
  size?: CardSize;
  /** Card header content */
  header?: React.ReactNode;
  /** Card footer content */
  footer?: React.ReactNode;
  /** Card title */
  cardTitle?: string;
  /** Card subtitle */
  subtitle?: string;
  /** Card image */
  image?: string;
  /** Image alt text */
  imageAlt?: string;
  /** Card actions */
  actions?: React.ReactNode;
  /** Clickable card */
  clickable?: boolean;
  /** Hoverable card */
  hoverable?: boolean;
  /** Disabled state */
  disabled?: boolean;
  /** Loading state */
  loading?: boolean;
  /** Children content */
  children?: React.ReactNode;
}

// Variant styles
const variantStyles: Record<CardVariant, string> = {
  default: 'bg-white border border-gray-200',
  outlined: 'bg-white border-2 border-gray-300',
  elevated: 'bg-white shadow-md border-0',
  filled: 'bg-gray-50 border-0',
};

// Size styles
const sizeStyles: Record<CardSize, string> = {
  sm: 'p-3',
  md: 'p-4', 
  lg: 'p-6',
  xl: 'p-8',
};

// Base card styles
const baseStyles = `
  rounded-2xl
  transition-all duration-200
  overflow-hidden
`;

// Header styles
const headerStyles = `
  border-b border-gray-200 pb-4 mb-4
`;

// Footer styles
const footerStyles = `
  border-t border-gray-200 pt-4 mt-4
`;

// Title styles
const titleStyles = `
  text-lg font-semibold text-gray-900 mb-1
`;

// Subtitle styles
const subtitleStyles = `
  text-sm text-gray-600 mb-4
`;

// Loading skeleton
const LoadingSkeleton = () => (
  <div className="animate-pulse">
    <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
    <div className="h-3 bg-gray-200 rounded w-1/2 mb-4"></div>
    <div className="space-y-2">
      <div className="h-3 bg-gray-200 rounded"></div>
      <div className="h-3 bg-gray-200 rounded w-5/6"></div>
      <div className="h-3 bg-gray-200 rounded w-4/6"></div>
    </div>
  </div>
);

export const Card: React.FC<CardProps> = ({
  variant = 'default',
  size = 'md',
  header,
  footer,
  cardTitle,
  subtitle,
  image,
  imageAlt,
  actions,
  clickable = false,
  hoverable = false,
  disabled = false,
  loading = false,
  className,
  children,
  onClick,
  ...props
}) => {
  const cardClasses = cn(
    baseStyles,
    variantStyles[variant],
    sizeStyles[size],
    hoverable && 'hover:shadow-lg transition-shadow duration-200',
    clickable && 'cursor-pointer hover:shadow-lg hover:scale-[1.02]',
    disabled && 'opacity-50 cursor-not-allowed',
    loading && 'animate-pulse',
    className
  );

  const contentClasses = cn(
    // Content no longer needs size padding since it's applied to the main card
  );

  const renderImage = () => {
    if (!image) return null;

    return (
      <div className="aspect-video w-full overflow-hidden">
        <img
          src={image}
          alt={imageAlt || 'Card image'}
          className="w-full h-full object-cover"
        />
      </div>
    );
  };

  const renderHeader = () => {
    if (!header && !cardTitle && !subtitle) return null;

    return (
      <div className={headerStyles}>
        {header && header}
        {cardTitle && <h3 className={titleStyles}>{cardTitle}</h3>}
        {subtitle && <p className={subtitleStyles}>{subtitle}</p>}
      </div>
    );
  };

  const renderContent = () => {
    return children;
  };

  const renderActions = () => {
    if (!actions) return null;

    return (
      <div className="flex items-center justify-end space-x-2 mt-4">
        {actions}
      </div>
    );
  };

  const renderFooter = () => {
    if (!footer) return null;

    return (
      <div className={footerStyles}>
        {footer}
      </div>
    );
  };

  const handleClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (disabled) {
      e.preventDefault();
      return;
    }
    if (onClick) {
      onClick(e);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLDivElement>) => {
    if (disabled) {
      return;
    }
    if ((e.key === 'Enter' || e.key === ' ') && onClick) {
      e.preventDefault();
      onClick(e as any); // Cast needed for event type compatibility
    }
  };

  return (
    <div
      className={cardClasses}
      onClick={clickable || onClick ? handleClick : undefined}
      onKeyDown={clickable || onClick ? handleKeyDown : undefined}
      role={clickable ? 'button' : undefined}
      tabIndex={clickable ? (disabled ? -1 : 0) : undefined}
      {...props}
    >
      {renderImage()}

      <div className={contentClasses}>
        {renderHeader()}
        {renderContent()}
        {renderActions()}
      </div>

      {renderFooter()}
    </div>
  );
};

Card.displayName = 'Card';

// Sub-components for flexible card composition
export interface CardHeaderProps extends React.HTMLAttributes<HTMLDivElement> {
  children?: React.ReactNode;
}

export const CardHeader: React.FC<CardHeaderProps> = ({ className, children, ...props }) => {
  return (
    <div className={cn(headerStyles, className)} {...props}>
      {children}
    </div>
  );
};

CardHeader.displayName = 'CardHeader';

export interface CardTitleProps extends React.HTMLAttributes<HTMLHeadingElement> {
  children?: React.ReactNode;
}

export const CardTitle: React.FC<CardTitleProps> = ({ className, children, ...props }) => {
  return (
    <h3 className={cn(titleStyles, className)} {...props}>
      {children}
    </h3>
  );
};

CardTitle.displayName = 'CardTitle';

export interface CardContentProps extends React.HTMLAttributes<HTMLDivElement> {
  children?: React.ReactNode;
}

export const CardContent: React.FC<CardContentProps> = ({ className, children, ...props }) => {
  return (
    <div className={cn('flex-1', className)} {...props}>
      {children}
    </div>
  );
};

CardContent.displayName = 'CardContent';

export interface CardDescriptionProps extends React.HTMLAttributes<HTMLParagraphElement> {
  children?: React.ReactNode;
}

export const CardDescription: React.FC<CardDescriptionProps> = ({ className, children, ...props }) => {
  return (
    <p className={cn('text-sm text-gray-600', className)} {...props}>
      {children}
    </p>
  );
};

CardDescription.displayName = 'CardDescription';

export default Card;

/**
 * Design System - DropdownMenu Component (Molecule)
 * GDPR Entity Page Implementation
 */

import React, { useState, useRef, useEffect } from 'react';
import { createPortal } from 'react-dom';
import { cn } from '../../utils';

export interface DropdownMenuProps {
  /** Dropdown content */
  children: React.ReactNode;
  /** Custom className */
  className?: string;
}

export interface DropdownMenuTriggerProps {
  /** Trigger content */
  children: React.ReactNode;
  /** Custom className */
  className?: string;
  /** As child component */
  asChild?: boolean;
}

export interface DropdownMenuContentProps {
  /** Content */
  children: React.ReactNode;
  /** Alignment */
  align?: 'start' | 'center' | 'end';
  /** Custom className */
  className?: string;
}

export interface DropdownMenuItemProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Item content */
  children: React.ReactNode;
  /** Disabled state */
  disabled?: boolean;
  /** Custom className */
  className?: string;
}

// Context for dropdown state
const DropdownContext = React.createContext<{
  isOpen: boolean;
  setIsOpen: (open: boolean) => void;
  triggerRef: React.RefObject<HTMLElement>;
}>({ 
  isOpen: false, 
  setIsOpen: () => {}, 
  triggerRef: { current: null } 
});

/**
 * Main DropdownMenu container
 */
export const DropdownMenu: React.FC<DropdownMenuProps> = ({
  children,
  className
}) => {
  const [isOpen, setIsOpen] = useState(false);
  const triggerRef = useRef<HTMLElement>(null);

  return (
    <DropdownContext.Provider value={{ isOpen, setIsOpen, triggerRef }}>
      <div className={cn('relative inline-block', className)}>
        {children}
      </div>
    </DropdownContext.Provider>
  );
};

/**
 * Dropdown trigger
 */
export const DropdownMenuTrigger: React.FC<DropdownMenuTriggerProps> = ({
  children,
  className,
  asChild = false
}) => {
  const { setIsOpen, triggerRef } = React.useContext(DropdownContext);

  const handleClick = () => {
    setIsOpen(true);
  };

  if (asChild && React.isValidElement(children)) {
    return React.cloneElement(children, {
      ref: triggerRef,
      onClick: handleClick,
      className: cn(children.props.className, className)
    });
  }

  return (
    <button
      ref={triggerRef as React.RefObject<HTMLButtonElement>}
      onClick={handleClick}
      className={cn('inline-flex items-center justify-center', className)}
    >
      {children}
    </button>
  );
};

/**
 * Dropdown content
 */
export const DropdownMenuContent: React.FC<DropdownMenuContentProps> = ({
  children,
  align = 'start',
  className
}) => {
  const { isOpen, setIsOpen, triggerRef } = React.useContext(DropdownContext);
  const contentRef = useRef<HTMLDivElement>(null);
  const [position, setPosition] = useState<{ top: number; left: number } | null>(null);

  useEffect(() => {
    if (isOpen && triggerRef.current) {
      const triggerRect = triggerRef.current.getBoundingClientRect();
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const scrollLeft = window.pageXOffset || document.documentElement.scrollLeft;

      let left = triggerRect.left + scrollLeft;

      if (align === 'end') {
        left = triggerRect.right + scrollLeft;
      } else if (align === 'center') {
        left = triggerRect.left + scrollLeft + triggerRect.width / 2;
      }

      setPosition({
        top: triggerRect.bottom + scrollTop + 4,
        left
      });
    }
  }, [isOpen, align]);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        contentRef.current &&
        !contentRef.current.contains(event.target as Node) &&
        triggerRef.current &&
        !triggerRef.current.contains(event.target as Node)
      ) {
        setIsOpen(false);
      }
    };

    const handleEscape = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        setIsOpen(false);
      }
    };

    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleEscape);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscape);
    };
  }, [isOpen, setIsOpen]);

  if (!isOpen || !position) {
    return null;
  }

  const alignmentClass = align === 'end' ? '-translate-x-full' : 
                        align === 'center' ? '-translate-x-1/2' : 
                        '';

  return createPortal(
    <div
      ref={contentRef}
      className={cn(
        'absolute z-50 min-w-[8rem] overflow-hidden rounded-md border bg-white p-1 shadow-md',
        'animate-in fade-in-0 zoom-in-95',
        alignmentClass,
        className
      )}
      style={{
        top: position.top,
        left: position.left
      }}
    >
      {children}
    </div>,
    document.body
  );
};

/**
 * Dropdown menu item
 */
export const DropdownMenuItem: React.FC<DropdownMenuItemProps> = ({
  children,
  disabled = false,
  className,
  onClick,
  ...props
}) => {
  const { setIsOpen } = React.useContext(DropdownContext);

  const handleClick = (event: React.MouseEvent<HTMLDivElement>) => {
    if (disabled) return;

    onClick?.(event);
    setIsOpen(false);
  };

  return (
    <div
      className={cn(
        'relative flex cursor-default select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none',
        'transition-colors focus:bg-gray-100 focus:text-gray-900',
        disabled ? 'pointer-events-none opacity-50' : 'hover:bg-gray-100',
        className
      )}
      onClick={handleClick}
      {...props}
    >
      {children}
    </div>
  );
};

export default DropdownMenu;

