/**
 * Design System - SearchBox Component Tests
 * Week 8 Implementation - Component Library
 */

import { describe, it, expect, vi } from 'vitest';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import { SearchBox } from './SearchBox';

describe('SearchBox Component', () => {
  it('renders with default props', () => {
    render(<SearchBox />);

    const input = screen.getByRole('textbox');
    expect(input).toBeInTheDocument();
    expect(input).toHaveAttribute('placeholder', 'Cerca...');
  });

  it('renders with custom placeholder', () => {
    render(<SearchBox placeholder="Custom placeholder" />);

    const input = screen.getByPlaceholderText('Custom placeholder');
    expect(input).toBeInTheDocument();
  });

  it('displays search icon', () => {
    render(<SearchBox />);

    // The search icon should be present (MagnifyingGlassIcon)
    const container = screen.getByRole('textbox').parentElement;
    expect(container).toBeInTheDocument();
  });

  it('handles controlled value', () => {
    const handleChange = vi.fn();
    render(<SearchBox value="test value" onChange={handleChange} />);

    const input = screen.getByRole('textbox');
    expect(input).toHaveValue('test value');
  });

  it('handles uncontrolled value', async () => {
    const user = userEvent.setup();
    render(<SearchBox />);

    const input = screen.getByRole('textbox');
    await user.type(input, 'test input');

    expect(input).toHaveValue('test input');
  });

  it('calls onChange when typing', async () => {
    const user = userEvent.setup();
    const handleChange = vi.fn();
    render(<SearchBox onChange={handleChange} />);

    const input = screen.getByRole('textbox');
    await user.type(input, 'a');

    expect(handleChange).toHaveBeenCalled();
  });

  it('calls onSearch when Enter is pressed', async () => {
    const user = userEvent.setup();
    const handleSearch = vi.fn();
    render(<SearchBox onSearch={handleSearch} />);

    const input = screen.getByRole('textbox');
    await user.type(input, 'search term');
    await user.keyboard('{Enter}');

    expect(handleSearch).toHaveBeenCalledWith('search term');
  });

  it('shows clear button when there is value and clearable is true', async () => {
    const user = userEvent.setup();
    render(<SearchBox clearable />);

    const input = screen.getByRole('textbox');
    await user.type(input, 'some text');

    const clearButton = screen.getByRole('button', { name: /cancella ricerca/i });
    expect(clearButton).toBeInTheDocument();
  });

  it('does not show clear button when clearable is false', async () => {
    const user = userEvent.setup();
    render(<SearchBox clearable={false} />);

    const input = screen.getByRole('textbox');
    await user.type(input, 'some text');

    const clearButton = screen.queryByRole('button', { name: /cancella ricerca/i });
    expect(clearButton).not.toBeInTheDocument();
  });

  it('clears input when clear button is clicked', async () => {
    const user = userEvent.setup();
    const handleChange = vi.fn();
    render(<SearchBox onChange={handleChange} clearable />);

    const input = screen.getByRole('textbox');
    await user.type(input, 'some text');

    const clearButton = screen.getByRole('button', { name: /cancella ricerca/i });
    await user.click(clearButton);

    expect(input).toHaveValue('');
  });

  it('calls onClear when clear button is clicked', async () => {
    const user = userEvent.setup();
    const handleClear = vi.fn();
    render(<SearchBox onClear={handleClear} clearable />);

    const input = screen.getByRole('textbox');
    await user.type(input, 'some text');

    const clearButton = screen.getByRole('button', { name: /cancella ricerca/i });
    await user.click(clearButton);

    expect(handleClear).toHaveBeenCalled();
  });

  it('shows loading spinner when loading is true', () => {
    render(<SearchBox loading />);

    // Check for loading spinner (svg with animate-spin class)
    const spinner = document.querySelector('.animate-spin');
    expect(spinner).toBeInTheDocument();
  });

  it('does not show clear button when loading', async () => {
    const user = userEvent.setup();
    render(<SearchBox loading clearable />);

    const input = screen.getByRole('textbox');
    await user.type(input, 'some text');

    const clearButton = screen.queryByRole('button', { name: /cancella ricerca/i });
    expect(clearButton).not.toBeInTheDocument();
  });

  it('applies correct size classes', () => {
    const { rerender } = render(<SearchBox size="sm" />);
    let container = screen.getByRole('textbox').parentElement;
    expect(container).toHaveClass('h-8');

    rerender(<SearchBox size="md" />);
    container = screen.getByRole('textbox').parentElement;
    expect(container).toHaveClass('h-10');

    rerender(<SearchBox size="lg" />);
    container = screen.getByRole('textbox').parentElement;
    expect(container).toHaveClass('h-12');
  });

  it('is disabled when disabled prop is true', () => {
    render(<SearchBox disabled />);

    const input = screen.getByRole('textbox');
    expect(input).toBeDisabled();
  });

  it('applies custom className', () => {
    render(<SearchBox className="custom-class" />);

    const input = screen.getByRole('textbox');
    expect(input).toHaveClass('custom-class');
  });

  it('applies custom containerClassName', () => {
    render(<SearchBox containerClassName="custom-container" />);

    const container = screen.getByRole('textbox').parentElement;
    expect(container).toHaveClass('custom-container');
  });

  it('forwards ref to input element', () => {
    const ref = vi.fn();
    render(<SearchBox ref={ref} />);

    expect(ref).toHaveBeenCalledWith(expect.any(HTMLInputElement));
  });
});

import React, { ReactNode } from 'react';
import { X, Check, Trash2, CheckSquare, Square } from 'lucide-react';
import { cn } from '../../utils';

export interface SelectionPillAction {
  /** Etichetta dell'azione */
  label: string;
  /** Funzione da eseguire quando l'azione viene selezionata */
  onClick: () => void;
  /** Icona opzionale da mostrare accanto all'etichetta */
  icon?: ReactNode;
  /** Variante di stile */
  variant: 'primary' | 'secondary' | 'danger' | 'default';
}

export interface SelectionPillsProps {
  /** Lista delle azioni mostrate come pill */
  actions: SelectionPillAction[];
  /** Funzione chiamata quando l'utente vuole pulire la selezione */
  onClear?: () => void;
  /** Numero di elementi selezionati */
  count?: number;
  /** Nome dell'entit (es. "righe", "utenti", "aziende") */
  entityName?: string;
  /** Funzione da chiamare quando si vuole cancellare la selezione */
  onClearSelection?: () => void;
  /** Classi CSS aggiuntive */
  className?: string;
}

export const SelectionPills: React.FC<SelectionPillsProps> = ({
  actions,
  onClear,
  count = 0,
  entityName = 'elementi',
  onClearSelection,
  className = '',
}) => {
  // Stili per le diverse varianti
  const getVariantStyle = (variant: SelectionPillAction['variant']) => {
    switch (variant) {
      case 'primary':
        return 'bg-primary-100 text-primary-800 border-primary-200';
      case 'secondary':
        return 'bg-gray-100 text-gray-800 border-gray-200';
      case 'danger':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-700 border-gray-200';
    }
  };

  if (actions.length === 0 && count === 0) {
    return null;
  }

  return (
    <div className={cn('flex flex-wrap items-center gap-2', className)}>
      {/* Pill per il conteggio selezione */}
      {count > 0 && (
        <div className="flex items-center gap-1 px-3 py-1 bg-primary-50 text-primary-700 border border-primary-200 rounded-full text-sm font-medium">
          <Check className="w-4 h-4" />
          <span>
            {count} {entityName} selezionat{count === 1 ? 'o' : 'i'}
          </span>
          {(onClear || onClearSelection) && (
            <button
              onClick={onClear || onClearSelection}
              className="ml-1 p-0.5 hover:bg-primary-100 rounded-full transition-colors"
              aria-label="Cancella selezione"
            >
              <X className="w-3 h-3" />
            </button>
          )}
        </div>
      )}

      {/* Pills per le azioni */}
      {actions.map((action, index) => (
        <button
          key={index}
          onClick={action.onClick}
          className={cn(
            'flex items-center gap-1.5 px-3 py-1 border rounded-full text-sm font-medium',
            'transition-all duration-200 hover:shadow-sm',
            getVariantStyle(action.variant)
          )}
        >
          {action.icon && (
            <span className="w-4 h-4 flex items-center justify-center">
              {action.icon}
            </span>
          )}
          <span>{action.label}</span>
        </button>
      ))}
    </div>
  );
};

// Helper functions for common actions
export const selectAllAction = (onClick: () => void, label: string): SelectionPillAction => ({
  label,
  onClick,
  icon: <CheckSquare className="w-4 h-4" />,
  variant: 'secondary'
});

export const deselectAllAction = (onClick: () => void, label: string): SelectionPillAction => ({
  label,
  onClick,
  icon: <Square className="w-4 h-4" />,
  variant: 'secondary'
});

export const deleteSelectedAction = (onClick: () => void, label: string): SelectionPillAction => ({
  label,
  onClick,
  icon: <Trash2 className="w-4 h-4" />,
  variant: 'danger'
});

