// Helper function to get typography value
export const getTypography = (token: string): any => {
  const keys = token.split('.');
  let value: any = typography;

  for (const key of keys) {
    value = value[key];
    if (value === undefined) {
      console.warn(`Typography token '${token}' not found`);
      return undefined;
    }
  }

  return value;
};

