export function useEmployees() {
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [filteredEmployees, setFilteredEmployees] = useState<Employee[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [filter, setFilter] = useState('');
  const [usingFallback, setUsingFallback] = useState(false);

  useEffect(() => {
    fetchEmployees();
  }, []);

  useEffect(() => {
    if (filter.trim() === '') {
      setFilteredEmployees(employees);
    } else {
      const lowercaseFilter = filter.toLowerCase();
      const filtered = employees.filter(employee => 
        employee.firstName.toLowerCase().includes(lowercaseFilter) ||
        employee.lastName.toLowerCase().includes(lowercaseFilter) ||
        (employee.email && employee.email.toLowerCase().includes(lowercaseFilter)) ||
        (employee.company?.ragioneSociale && employee.company.ragioneSociale.toLowerCase().includes(lowercaseFilter))
      );
      setFilteredEmployees(filtered);
    }
  }, [filter, employees]);

  const fetchEmployees = async () => {
    try {
      setLoading(true);
      setError(null);
      setUsingFallback(false);

      // Use timeout to ensure we don't wait too long for API response
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 8000);

      const response = await getEmployees();

      clearTimeout(timeoutId);

      if (response && Array.isArray(response)) {
        setEmployees(response);
        setFilteredEmployees(response);
      } else {
        throw new Error('Invalid data format received from API');
      }
    } catch (err: unknown) {
      // Use fallback data
      setEmployees(MOCK_EMPLOYEES);
      setFilteredEmployees(MOCK_EMPLOYEES);
      const errorMessage = err instanceof Error ? err.message : 'Failed to fetch employees';
      setError(errorMessage);
      setUsingFallback(true);
    } finally {
      setLoading(false);
    }
  };

  return {
    employees,
    filteredEmployees,
    loading,
    error,
    filter,
    setFilter,
    fetchEmployees,
    usingFallback
  };
}

import { useQuery, useMutation, useQueryClient, UseQueryOptions, UseMutationOptions } from '@tanstack/react-query';
import { useCallback, useMemo } from 'react';
import { useToast } from '../ui/useToast';
import { useErrorHandler } from '../useErrorHandler';

// Tipi base per le API
interface ApiResponse<T> {
  data: T;
  message?: string;
  success: boolean;
}

interface ListResponse<T> {
  data: T[];
  total: number;
  page: number;
  limit: number;
}

interface QueryParams {
  page?: number;
  limit?: number;
  search?: string;
  sort?: string;
  order?: 'asc' | 'desc';
  [key: string]: any;
}

/**
 * Hook ottimizzato per query GET
 */
export const useOptimizedQuery = <T>(
  queryKey: (string | number)[],
  queryFn: () => Promise<T>,
  options?: Omit<UseQueryOptions<T>, 'queryKey' | 'queryFn'>
) => {
  const { error: showError } = useToast();
  const { handleError } = useErrorHandler();

  return useQuery({
    queryKey,
    queryFn,
    ...options,
    onError: (error: any) => {
      handleError(error);
      options?.onError?.(error);
    },
  });
};

/**
 * Hook per liste con paginazione e filtri
 */
export const useListQuery = <T>(
  resource: string,
  params: QueryParams = {},
  fetchFn: (params: QueryParams) => Promise<ListResponse<T>>,
  options?: Omit<UseQueryOptions<ListResponse<T>>, 'queryKey' | 'queryFn'>
) => {
  const queryKey = useMemo(() => [resource, 'list', params], [resource, params]);

  return useOptimizedQuery(
    queryKey,
    () => fetchFn(params),
    {
      keepPreviousData: true, // Mantieni i dati precedenti durante il caricamento
      staleTime: 2 * 60 * 1000, // 2 minuti per le liste
      ...options,
    }
  );
};

/**
 * Hook per dettagli di una risorsa
 */
export const useDetailQuery = <T>(
  resource: string,
  id: string | number,
  fetchFn: (id: string | number) => Promise<T>,
  options?: Omit<UseQueryOptions<T>, 'queryKey' | 'queryFn'>
) => {
  const queryKey = useMemo(() => [resource, 'detail', id], [resource, id]);

  return useOptimizedQuery(
    queryKey,
    () => fetchFn(id),
    {
      enabled: !!id, // Esegui solo se l'ID  presente
      staleTime: 5 * 60 * 1000, // 5 minuti per i dettagli
      ...options,
    }
  );
};

/**
 * Hook ottimizzato per mutazioni
 */
export const useOptimizedMutation = <TData, TVariables>(
  mutationFn: (variables: TVariables) => Promise<TData>,
  options?: UseMutationOptions<TData, Error, TVariables>
) => {
  const queryClient = useQueryClient();
  const { success, error: showError } = useToast();
  const { handleError } = useErrorHandler();

  return useMutation({
    mutationFn,
    ...options,
    onSuccess: (data, variables, context) => {
      options?.onSuccess?.(data, variables, context);
    },
    onError: (error: Error, variables, context) => {
      handleError(error);
      options?.onError?.(error, variables, context);
    },
  });
};

/**
 * Hook per operazioni CRUD ottimizzate
 */
export const useCrudOperations = <T>(
  resource: string,
  api: {
    list: (params: QueryParams) => Promise<ListResponse<T>>;
    get: (id: string | number) => Promise<T>;
    create: (data: Partial<T>) => Promise<T>;
    update: (id: string | number, data: Partial<T>) => Promise<T>;
    delete: (id: string | number) => Promise<void>;
  }
) => {
  const queryClient = useQueryClient();
  const { success, error: showError } = useToast();

  // Funzioni di invalidazione
  const invalidateList = useCallback(() => {
    queryClient.invalidateQueries({ queryKey: [resource, 'list'] });
  }, [queryClient, resource]);

  const invalidateDetail = useCallback((id: string | number) => {
    queryClient.invalidateQueries({ queryKey: [resource, 'detail', id] });
  }, [queryClient, resource]);

  const invalidateAll = useCallback(() => {
    queryClient.invalidateQueries({ queryKey: [resource] });
  }, [queryClient, resource]);

  // Mutazioni ottimizzate
  const createMutation = useOptimizedMutation(
    api.create,
    {
      onSuccess: (data) => {
        invalidateList();
        success(`${resource} creato con successo`);
      },
    }
  );

  const updateMutation = useOptimizedMutation(
    ({ id, data }: { id: string | number; data: Partial<T> }) => api.update(id, data),
    {
      onSuccess: (data, { id }) => {
        invalidateDetail(id);
        invalidateList();
        success(`${resource} aggiornato con successo`);
      },
    }
  );

  const deleteMutation = useOptimizedMutation(
    api.delete,
    {
      onSuccess: (_, id) => {
        queryClient.removeQueries({ queryKey: [resource, 'detail', id] });
        invalidateList();
        success(`${resource} eliminato con successo`);
      },
    }
  );

  // Funzioni di utilit
  const prefetchDetail = useCallback(async (id: string | number) => {
    await queryClient.prefetchQuery({
      queryKey: [resource, 'detail', id],
      queryFn: () => api.get(id),
      staleTime: 5 * 60 * 1000,
    });
  }, [queryClient, resource, api.get]);

  const setDetailData = useCallback((id: string | number, data: T) => {
    queryClient.setQueryData([resource, 'detail', id], data);
  }, [queryClient, resource]);

  const getDetailData = useCallback((id: string | number): T | undefined => {
    return queryClient.getQueryData([resource, 'detail', id]);
  }, [queryClient, resource]);

  return {
    // Mutazioni
    create: createMutation,
    update: updateMutation,
    delete: deleteMutation,

    // Utility
    invalidateList,
    invalidateDetail,
    invalidateAll,
    prefetchDetail,
    setDetailData,
    getDetailData,

    // Stati delle mutazioni
    isCreating: createMutation.isPending,
    isUpdating: updateMutation.isPending,
    isDeleting: deleteMutation.isPending,
    isMutating: createMutation.isPending || updateMutation.isPending || deleteMutation.isPending,
  };
};

export default useOptimizedQuery;

import { useNavigate, useLocation, useParams } from 'react-router-dom';
import { useMemo, useCallback } from 'react';
import { usePermissions } from '../auth/usePermissions';

/**
 * Hook ottimizzato per la navigazione
 * Fornisce utility per navigazione sicura e controllo permessi
 */
export const useNavigation = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const params = useParams();
  const permissions = usePermissions();

  // Funzioni di navigazione memoizzate
  const navigationActions = useMemo(() => ({
    // Navigazione base
    goTo: (path: string, options?: { replace?: boolean; state?: any }) => {
      navigate(path, options);
    },

    goBack: () => navigate(-1),
    goForward: () => navigate(1),

    // Navigazione verso sezioni specifiche
    goToCompanies: () => navigate('/companies'),
    goToCompany: (id: string) => navigate(`/companies/${id}`),
    goToNewCompany: () => navigate('/companies/new'),

    goToCourses: () => navigate('/courses'),
    goToCourse: (id: string) => navigate(`/courses/${id}`),
    goToNewCourse: () => navigate('/courses/new'),

    goToTrainers: () => navigate('/trainers'),
    goToTrainer: (id: string) => navigate(`/trainers/${id}`),
    goToNewTrainer: () => navigate('/trainers/new'),

    goToEmployees: () => navigate('/employees'),
    goToEmployee: (id: string) => navigate(`/employees/${id}`),
    goToNewEmployee: () => navigate('/employees/new'),

    goToSchedules: () => navigate('/schedules'),
    goToSchedule: (id: string) => navigate(`/schedules/${id}`),
    goToNewSchedule: () => navigate('/schedules/new'),

    goToSettings: () => navigate('/settings'),
    goToQuotes: () => navigate('/quotes'),
    goToInvoices: () => navigate('/invoices'),
    goToDocuments: () => navigate('/documents'),

    // Navigazione con controllo permessi
    goToWithPermission: (path: string, requiredPermission: string, action: string = 'read') => {
      if (permissions.hasPermission(requiredPermission, action)) {
        navigate(path);
      } else {
        // Redirect a pagina di errore o mostra messaggio
        navigate('/unauthorized');
      }
    },

    // Navigazione condizionale
    goToIfAllowed: (path: string, condition: boolean, fallbackPath?: string) => {
      if (condition) {
        navigate(path);
      } else if (fallbackPath) {
        navigate(fallbackPath);
      }
    },

  }), [navigate, permissions]);

  // Utility per la location corrente
  const locationUtils = useMemo(() => ({
    // Informazioni sulla route corrente
    currentPath: location.pathname,
    currentSearch: location.search,
    currentHash: location.hash,
    currentState: location.state,

    // Controlli sulla route
    isOnPath: (path: string) => location.pathname === path,
    isOnPathPattern: (pattern: RegExp) => pattern.test(location.pathname),

    // Controlli per sezioni specifiche
    isOnCompanies: () => location.pathname.startsWith('/companies'),
    isOnCourses: () => location.pathname.startsWith('/courses'),
    isOnTrainers: () => location.pathname.startsWith('/trainers'),
    isOnEmployees: () => location.pathname.startsWith('/employees'),
    isOnSchedules: () => location.pathname.startsWith('/schedules'),
    isOnSettings: () => location.pathname.startsWith('/settings'),

    // Utility per query parameters
    getSearchParam: (key: string) => {
      const searchParams = new URLSearchParams(location.search);
      return searchParams.get(key);
    },

    getAllSearchParams: () => {
      const searchParams = new URLSearchParams(location.search);
      const params: Record<string, string> = {};
      searchParams.forEach((value, key) => {
        params[key] = value;
      });
      return params;
    },

    // Utility per costruire URL
    buildUrl: (path: string, searchParams?: Record<string, string>) => {
      const url = new URL(path, window.location.origin);
      if (searchParams) {
        Object.entries(searchParams).forEach(([key, value]) => {
          url.searchParams.set(key, value);
        });
      }
      return url.pathname + url.search;
    },

  }), [location]);

  // Utility per i parametri della route
  const paramsUtils = useMemo(() => ({
    params,

    // Getter tipizzati per parametri comuni
    getId: () => params.id,
    getCompanyId: () => params.companyId,
    getCourseId: () => params.courseId,
    getTrainerId: () => params.trainerId,
    getEmployeeId: () => params.employeeId,
    getScheduleId: () => params.scheduleId,

    // Controlli sui parametri
    hasId: () => !!params.id,
    hasParam: (key: string) => !!params[key],

  }), [params]);

  return {
    ...navigationActions,
    location: locationUtils,
    params: paramsUtils,

    // Funzioni di utilit avanzate
    refresh: useCallback(() => {
      navigate(location.pathname + location.search, { replace: true });
    }, [navigate, location]),

    replaceCurrentUrl: useCallback((newPath: string) => {
      navigate(newPath, { replace: true });
    }, [navigate]),
  };
};

export default useNavigation;

import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { isValidUUID, sanitizeId } from '../../utils/validation';

interface UseValidatedParamsOptions {
  /** Se true, reindirizza automaticamente alla lista se l'ID non  valido */
  redirectOnInvalid?: boolean;
  /** URL di fallback per il redirect (default: '/courses') */
  fallbackUrl?: string;
  /** Se true, mostra un toast di errore quando l'ID non  valido */
  showErrorToast?: boolean;
}

interface UseValidatedParamsResult {
  /** L'ID validato o null se non valido */
  id: string | null;
  /** True se l'ID  valido */
  isValid: boolean;
  /** True se l'ID non  valido */
  isInvalid: boolean;
  /** True se sta ancora validando */
  isValidating: boolean;
  /** Messaggio di errore se l'ID non  valido */
  errorMessage: string | null;
}

/**
 * Hook per validare automaticamente gli ID nei parametri delle route
 * Gestisce la validazione UUID e fornisce stati per la UI
 */
export const useValidatedParams = (
  options: UseValidatedParamsOptions = {}
): UseValidatedParamsResult => {
  const { id: rawId } = useParams<{ id: string }>();
  const navigate = useNavigate();

  const {
    redirectOnInvalid = false,
    fallbackUrl = '/courses',
    showErrorToast = false
  } = options;

  const [isValidating, setIsValidating] = useState(true);
  const [validationResult, setValidationResult] = useState<{
    id: string | null;
    isValid: boolean;
    errorMessage: string | null;
  }>({
    id: null,
    isValid: false,
    errorMessage: null
  });

  useEffect(() => {
    const validateId = () => {
      setIsValidating(true);

      if (!rawId) {
        setValidationResult({
          id: null,
          isValid: false,
          errorMessage: 'ID mancante nell\'URL'
        });

        if (redirectOnInvalid) {
          navigate(fallbackUrl, { replace: true });
        }

        setIsValidating(false);
        return;
      }

      const sanitizedId = sanitizeId(rawId);

      if (!sanitizedId || !isValidUUID(sanitizedId)) {
        setValidationResult({
          id: null,
          isValid: false,
          errorMessage: 'ID non valido. L\'ID deve essere un UUID valido.'
        });

        if (redirectOnInvalid) {
          navigate(fallbackUrl, { replace: true });
        }

        setIsValidating(false);
        return;
      }

      setValidationResult({
        id: sanitizedId,
        isValid: true,
        errorMessage: null
      });

      setIsValidating(false);
    };

    validateId();
  }, [rawId, redirectOnInvalid, fallbackUrl, navigate]);

  return {
    id: validationResult.id,
    isValid: validationResult.isValid,
    isInvalid: !validationResult.isValid,
    isValidating,
    errorMessage: validationResult.errorMessage
  };
};

import { useContext, useMemo, useCallback } from 'react';
import { AppStateContext } from '../../context/AppStateContext';

/**
 * Hook ottimizzato per lo stato globale dell'applicazione
 * Utilizza selettori per evitare re-render inutili
 */
export const useAppState = () => {
  const context = useContext(AppStateContext);

  if (!context) {
    throw new Error('useAppState must be used within an AppStateProvider');
  }

  return context;
};

/**
 * Hook con selettore per ottimizzare le performance
 * Permette di sottoscriversi solo a parti specifiche dello stato
 */
export const useAppStateSelector = <T>(selector: (state: any) => T) => {
  const state = useAppState();

  return useMemo(() => selector(state), [selector, state]);
};

/**
 * Hook specifici per parti dello stato
 */
export const useLanguage = () => {
  return useAppStateSelector(state => ({
    language: state.language,
    setLanguage: state.setLanguage
  }));
};

export const useTheme = () => {
  return useAppStateSelector(state => ({
    theme: state.theme,
    setTheme: state.setTheme,
    isDarkMode: state.theme === 'dark'
  }));
};

export const useSidebar = () => {
  return useAppStateSelector(state => ({
    isExpanded: state.sidebarExpanded,
    setSidebarExpanded: state.setSidebarExpanded,
    toggleSidebar: useCallback(() => {
      state.setSidebarExpanded(!state.sidebarExpanded);
    }, [state.sidebarExpanded, state.setSidebarExpanded])
  }));
};

export const useAttestatiProgress = () => {
  return useAppStateSelector(state => ({
    progress: state.attestatiGenerationProgress,
    setProgress: state.setAttestatiGenerationProgress,
    isGenerating: state.attestatiGenerationProgress > 0 && state.attestatiGenerationProgress < 100
  }));
};

/**
 * Hook per azioni globali dell'applicazione
 */
export const useAppActions = () => {
  const { setLanguage, setTheme, setSidebarExpanded, setAttestatiGenerationProgress } = useAppState();

  return useMemo(() => ({
    // Azioni per il tema
    toggleTheme: () => {
      setTheme(prev => prev === 'light' ? 'dark' : 'light');
    },

    // Azioni per la lingua
    switchToItalian: () => setLanguage('it'),
    switchToEnglish: () => setLanguage('en'),

    // Azioni per la sidebar
    expandSidebar: () => setSidebarExpanded(true),
    collapseSidebar: () => setSidebarExpanded(false),
    toggleSidebar: () => setSidebarExpanded(prev => !prev),

    // Azioni per il progresso
    startAttestatiGeneration: () => setAttestatiGenerationProgress(0),
    updateAttestatiProgress: (progress: number) => setAttestatiGenerationProgress(progress),
    completeAttestatiGeneration: () => setAttestatiGenerationProgress(100),
    resetAttestatiProgress: () => setAttestatiGenerationProgress(0),

  }), [setLanguage, setTheme, setSidebarExpanded, setAttestatiGenerationProgress]);
};

export default useAppState;

import { useContext, useMemo, useCallback } from 'react';
import { ToastContext } from '../../context/ToastContext';
import type { ToastType, ToastOptions } from '../../context/ToastContext';

/**
 * Hook ottimizzato per la gestione dei toast
 * Fornisce API semplificata e funzioni memoizzate
 */
export const useToast = () => {
  const context = useContext(ToastContext);

  if (!context) {
    throw new Error('useToast must be used within a ToastProvider');
  }

  const { toasts, toast, removeToast, clearToasts } = context;

  // Funzioni memoizzate per evitare re-render
  const toastActions = useMemo(() => ({
    // Funzioni base
    add: toast,
    remove: removeToast,
    clear: clearToasts,

    // Funzioni specifiche per tipo
    success: (message: string, options?: Omit<ToastOptions, 'type'>) => {
      toast({ message, ...options, type: 'success' });
    },

    error: (message: string, options?: Omit<ToastOptions, 'type'>) => {
      toast({ message, ...options, type: 'error' });
    },

    warning: (message: string, options?: Omit<ToastOptions, 'type'>) => {
      toast({ message, ...options, type: 'warning' });
    },

    info: (message: string, options?: Omit<ToastOptions, 'type'>) => {
      toast({ message, ...options, type: 'info' });
    },

    // Funzioni per azioni comuni
    saveSuccess: (entity?: string) => {
      const message = entity ? `${entity} salvato con successo` : 'Salvato con successo';
      toast({ message, type: 'success' });
    },

    saveError: (entity?: string, error?: string) => {
      const message = entity 
        ? `Errore nel salvare ${entity}${error ? `: ${error}` : ''}`
        : `Errore nel salvare${error ? `: ${error}` : ''}`;
      toast({ message, type: 'error' });
    },

    deleteSuccess: (entity?: string) => {
      const message = entity ? `${entity} eliminato con successo` : 'Eliminato con successo';
      toast({ message, type: 'success' });
    },

    deleteError: (entity?: string, error?: string) => {
      const message = entity 
        ? `Errore nell'eliminare ${entity}${error ? `: ${error}` : ''}`
        : `Errore nell'eliminare${error ? `: ${error}` : ''}`;
      toast({ message, type: 'error' });
    },

    loadingError: (entity?: string, error?: string) => {
      const message = entity 
        ? `Errore nel caricare ${entity}${error ? `: ${error}` : ''}`
        : `Errore nel caricamento${error ? `: ${error}` : ''}`;
      toast({ message, type: 'error' });
    },

    networkError: () => {
      toast({ 
        message: 'Errore di connessione. Verifica la tua connessione internet.', 
        type: 'error',
        duration: 5000
      });
    },

    validationError: (message?: string) => {
      toast({ message: message || 'Verifica i dati inseriti', type: 'warning' });
    },

    // Funzioni per operazioni asincrone
    promise: async <T>(
      promise: Promise<T>,
      messages: {
        loading?: string;
        success?: string | ((data: T) => string);
        error?: string | ((error: unknown) => string);
      }
    ): Promise<T> => {
      let loadingToastId: string | undefined;

      if (messages.loading) {
        toast({ message: messages.loading, type: 'info', duration: 0 });
      }

      try {
        const result = await promise;

        if (loadingToastId) {
          removeToast(loadingToastId);
        }

        if (messages.success) {
          const successMessage = typeof messages.success === 'function' 
            ? messages.success(result)
            : messages.success;
          toast({ message: successMessage, type: 'success' });
        }

        return result;
      } catch (error) {
        if (loadingToastId) {
          removeToast(loadingToastId);
        }

        if (messages.error) {
          const errorMessage = typeof messages.error === 'function'
            ? messages.error(error)
            : messages.error;
          toast({ message: errorMessage, type: 'error' });
        }

        throw error;
      }
    }

  }), [toast, removeToast, clearToasts]);

  return {
    toasts,
    ...toastActions
  };
};

export default useToast;

import { useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from './auth/useAuth';

/**
 * Hook per gestire il redirect intelligente tra frontend pubblico e privato
 * Gestisce la navigazione basata sullo stato di autenticazione
 */
export const useAuthRedirect = () => {
  const { isAuthenticated, isLoading } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  /**
   * Redirect intelligente per "Area Riservata"
   * Se l'utente  gi autenticato, va alla dashboard
   * Altrimenti va al login
   */
  const handleAreaRiservataClick = () => {
    if (isAuthenticated) {
      navigate('/dashboard');
    } else {
      // Salva la pagina corrente per eventuale redirect post-login
      const currentPath = location.pathname;
      if (currentPath !== '/login') {
        sessionStorage.setItem('redirectAfterLogin', currentPath);
      }
      navigate('/login');
    }
  };

  /**
   * Redirect alla dashboard dopo login se l'utente  autenticato
   * Controlla se c' un redirect salvato o va alla dashboard
   */
  const handlePostLoginRedirect = () => {
    if (isAuthenticated && !isLoading) {
      const savedRedirect = sessionStorage.getItem('redirectAfterLogin');

      // Se siamo nella pagina di login e l'utente  autenticato
      if (location.pathname === '/login') {
        if (savedRedirect && savedRedirect !== '/login') {
          sessionStorage.removeItem('redirectAfterLogin');
          navigate(savedRedirect);
        } else {
          navigate('/dashboard');
        }
      }
    }
  };

  /**
   * Naviga al frontend pubblico dalla dashboard
   */
  const goToPublicFrontend = () => {
    navigate('/');
  };

  /**
   * Controlla se l'utente corrente pu accedere al frontend privato
   */
  const canAccessPrivateArea = () => {
    return isAuthenticated && !isLoading;
  };

  /**
   * Controlla se siamo in una pagina pubblica
   */
  const isPublicPage = () => {
    const publicPaths = ['/', '/corsi', '/servizi', '/contatti', '/lavora-con-noi', '/login'];
    return publicPaths.some(path => 
      path === '/' 
        ? location.pathname === path 
        : location.pathname.startsWith(path)
    );
  };

  /**
   * Controlla se siamo in una pagina privata
   */
  const isPrivatePage = () => {
    const privatePaths = ['/dashboard', '/companies', '/courses', '/persons', '/employees', '/settings'];
    return privatePaths.some(path => location.pathname.startsWith(path));
  };

  // Auto-redirect dopo login
  useEffect(() => {
    handlePostLoginRedirect();
  }, [isAuthenticated, isLoading, location.pathname]);

  return {
    handleAreaRiservataClick,
    handlePostLoginRedirect,
    goToPublicFrontend,
    canAccessPrivateArea,
    isPublicPage: isPublicPage(),
    isPrivatePage: isPrivatePage(),
    isAuthenticated,
    isLoading
  };
};

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { getCompanies, createCompany, updateCompany, deleteCompany } from '../services/companies';
import { Company } from '../types';

export function useCompanies() {
  const [companies, setCompanies] = useState<Company[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const isLoadingRef = useRef(false);
  const requestTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Debounced load function to prevent multiple rapid calls
  const loadCompanies = useCallback(async (forceRefresh = false) => {
    // Skip if we're already loading, unless it's a forced refresh
    if (isLoadingRef.current && !forceRefresh) return;

    // Clear any pending timeout
    if (requestTimeoutRef.current) {
      clearTimeout(requestTimeoutRef.current);
      requestTimeoutRef.current = null;
    }

    try {
      isLoadingRef.current = true;
      setLoading(true);
      setError(null);

      const data = await getCompanies();
      setCompanies(data);
    } catch (err) {
      console.error('Failed to load companies:', err);
      setError(err instanceof Error ? err.message : 'Failed to load companies');
    } finally {
      setLoading(false);
      isLoadingRef.current = false;
    }
  }, []);

  // Initial load
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      loadCompanies();
    }, 100);

    return () => {
      clearTimeout(timeoutId);
      if (requestTimeoutRef.current) {
        clearTimeout(requestTimeoutRef.current);
      }
    };
  }, [loadCompanies]);

  // Add company with optimistic update
  const addCompany = useCallback(async (company: Omit<Company, 'id'> & { id?: string }) => {
    try {
      setError(null);

      // Generate temporary ID for optimistic update
      const tempId = `temp-${Date.now()}`;
      const tempCompany = { ...company, id: tempId } as Company;

      // Optimistic update
      setCompanies((prev) => [...prev, tempCompany]);

      // Actual API call
      const newCompany = await createCompany(company);

      // Update with real data
      setCompanies((prev) => prev.map((c) => c.id === tempId ? newCompany : c));
      return newCompany;
    } catch (err) {
      console.error('Failed to create company:', err);
      // Revert optimistic update on error
      setCompanies((prev) => prev.filter((c) => !c.id.toString().startsWith('temp-')));
      setError(err instanceof Error ? err.message : 'Failed to create company');
      throw err;
    }
  }, []);

  // Edit company with optimistic update
  const editCompany = useCallback(async (id: string, company: Partial<Company>) => {
    // Store original for potential rollback
    const originalCompany = companies.find((c) => c.id === id);

    try {
      setError(null);

      // Optimistic update
      setCompanies((prev) => prev.map((c) => c.id === id ? { ...c, ...company } : c));

      // Actual API call
      const updatedCompany = await updateCompany(id, company);

      // Update with real data
      setCompanies((prev) => prev.map((c) => c.id === id ? updatedCompany : c));
      return updatedCompany;
    } catch (err) {
      console.error('Failed to update company:', err);
      // Rollback on error
      if (originalCompany) {
        setCompanies((prev) => prev.map((c) => c.id === id ? originalCompany : c));
      }
      setError(err instanceof Error ? err.message : 'Failed to update company');
      throw err;
    }
  }, [companies]);

  // Optimistic delete
  const removeCompany = useCallback(async (id: string) => {
    // Store original for potential rollback
    const originalCompany = companies.find((c) => c.id === id);

    try {
      setError(null);

      // Optimistic update
      setCompanies((prev) => prev.filter((c) => c.id !== id));

      // Actual API call
      await deleteCompany(id);
    } catch (err) {
      console.error('Failed to delete company:', err);
      // Rollback on error
      if (originalCompany) {
        setCompanies((prev) => [...prev, originalCompany]);
      }
      setError(err instanceof Error ? err.message : 'Failed to delete company');
      throw err;
    }
  }, [companies]);

  // Force refresh function for manual refresh
  const refresh = useCallback(() => loadCompanies(true), [loadCompanies]);

  return {
    companies,
    loading,
    error,
    addCompany,
    editCompany,
    removeCompany,
    refresh
  };
}

import React, { useState, useEffect } from 'react';
import { getCourses, createCourse, updateCourse, createCourseSchedule, enrollEmployees } from '../services/courses';
import { Course, CourseSchedule } from '../services/courses';

export interface CourseInsert {
  title: string;
  category?: string;
  description?: string;
  duration?: string;
  status?: 'ACTIVE' | 'INACTIVE';
  validityYears?: number;
  code?: string;
  pricePerPerson?: number;
  maxPeople?: number;
  renewalDuration?: string;
  regulation?: string;
  certifications?: string;
  contents?: string;
}

export interface CourseUpdate {
  title?: string;
  category?: string;
  description?: string;
  duration?: string;
  status?: 'ACTIVE' | 'INACTIVE';
  validityYears?: number;
  code?: string;
  pricePerPerson?: number;
  maxPeople?: number;
  renewalDuration?: string;
  regulation?: string;
  certifications?: string;
  contents?: string;
}

export interface CourseScheduleInsert {
  courseId: string;
  startDate: string;
  endDate: string;
  location?: string;
  maxParticipants?: number;
  status?: string;
}

export function useCourses() {
  const [courses, setCourses] = useState<Course[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadCourses();
  }, []);

  async function loadCourses() {
    try {
      setLoading(true);
      setError(null);
      const data = await getCourses();
      setCourses(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load courses');
    } finally {
      setLoading(false);
    }
  }

  async function addCourse(course: CourseInsert) {
    try {
      setError(null);
      const newCourse = await createCourse(course);
      setCourses(prev => [...prev, newCourse]);
      return newCourse;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to create course');
      throw err;
    }
  }

  async function editCourse(id: string, course: CourseUpdate) {
    try {
      setError(null);
      const updatedCourse = await updateCourse(id, course);
      setCourses(prev => prev.map(c => c.id === id ? updatedCourse : c));
      return updatedCourse;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update course');
      throw err;
    }
  }

  async function scheduleCourse(schedule: CourseScheduleInsert) {
    try {
      setError(null);
      const newSchedule = await createCourseSchedule(schedule);
      return newSchedule;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to schedule course');
      throw err;
    }
  }

  async function enrollInCourse(scheduleId: string, employeeIds: string[]) {
    try {
      setError(null);
      const enrollments = await enrollEmployees(scheduleId, employeeIds);
      return enrollments;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to enroll employees');
      throw err;
    }
  }

  return {
    courses,
    loading,
    error,
    addCourse,
    editCourse,
    scheduleCourse,
    enrollInCourse,
    refresh: loadCourses
  };
}

export default useCourses;

/**
 * GDPR Data Export Hook
 * Handles data export requests and downloads
 */

import { useState, useEffect, useCallback } from 'react';
import apiClient from '../services/apiClient';
import {
  DataExportRequest,
  DataExportFormData,
  UseDataExportReturn,
  DataExportResponse,
  GDPRApiResponse
} from '../types/gdpr';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-hot-toast';

export const useDataExport = (): UseDataExportReturn => {
  const [exportRequests, setExportRequests] = useState<DataExportRequest[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();

  /**
   * Fetch user's export requests
   */
  const fetchExportRequests = useCallback(async () => {
    if (!user) return;

    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.get<GDPRApiResponse<{ exports: DataExportRequest[] }>>(
        '/api/gdpr/export/requests'
      );

      if (response.data.success && response.data.data) {
        setExportRequests(response.data.data.exports);
      } else {
        throw new Error(response.data.error || 'Failed to fetch export requests');
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to fetch export requests';
      setError(errorMessage);
      console.error('Error fetching export requests:', err);
      toast.error('Failed to load export requests');
    } finally {
      setLoading(false);
    }
  }, [user]);

  /**
   * Request a new data export
   */
  const requestExport = useCallback(async (data: DataExportFormData) => {
    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.post<DataExportResponse>('/api/gdpr/export', {
        format: data.format,
        includeAuditTrail: data.includeAuditTrail,
        includeConsents: data.includeConsents
      });

      if (response.data.success && response.data.data) {
        const newRequest = response.data.data.exportRequest;

        // Add to local state
        setExportRequests(prev => [newRequest, ...prev]);

        toast.success('Data export requested successfully');

        // Start polling for completion
        pollExportStatus(newRequest.id);
      } else {
        throw new Error(response.data.error || 'Failed to request data export');
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to request data export';
      setError(errorMessage);
      console.error('Error requesting data export:', err);
      toast.error('Failed to request data export');
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Download an export file
   */
  const downloadExport = useCallback(async (requestId: string) => {
    try {
      setLoading(true);

      const exportRequest = exportRequests.find(req => req.id === requestId);
      if (!exportRequest) {
        throw new Error('Export request not found');
      }

      if (exportRequest.status !== 'completed' || !exportRequest.downloadUrl) {
        throw new Error('Export is not ready for download');
      }

      // Check if export has expired
      if (exportRequest.expiryDate && new Date() > new Date(exportRequest.expiryDate)) {
        throw new Error('Export has expired. Please request a new export.');
      }

      const response = await apiClient.get(
        `/api/gdpr/export/download/${requestId}`,
        { responseType: 'blob' }
      );

      // Create download link
      const blob = new Blob([response.data]);
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;

      // Determine file extension based on format
      const extension = exportRequest.format === 'json' ? 'json' : 
                       exportRequest.format === 'csv' ? 'csv' : 'pdf';

      link.download = `gdpr-data-export-${requestId.slice(0, 8)}.${extension}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      toast.success('Export downloaded successfully');
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to download export';
      console.error('Error downloading export:', err);
      toast.error(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  }, [exportRequests]);

  /**
   * Cancel a pending export request
   */
  const cancelExport = useCallback(async (requestId: string) => {
    try {
      setLoading(true);

      const response = await apiClient.delete<GDPRApiResponse>(
        `/api/gdpr/export/${requestId}`
      );

      if (response.data.success) {
        // Remove from local state
        setExportRequests(prev => prev.filter(req => req.id !== requestId));
        toast.success('Export request cancelled');
      } else {
        throw new Error(response.data.error || 'Failed to cancel export');
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to cancel export';
      console.error('Error cancelling export:', err);
      toast.error(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Poll export status until completion
   */
  const pollExportStatus = useCallback(async (requestId: string) => {
    const pollInterval = 5000; // 5 seconds
    const maxPolls = 60; // 5 minutes max
    let pollCount = 0;

    const poll = async () => {
      try {
        const response = await apiClient.get<DataExportResponse>(
          `/api/gdpr/export/status/${requestId}`
        );

        if (response.data.success && response.data.data) {
          const updatedRequest = response.data.data.exportRequest;

          // Update local state
          setExportRequests(prev => 
            prev.map(req => req.id === requestId ? updatedRequest : req)
          );

          // Check if completed or failed
          if (updatedRequest.status === 'completed') {
            toast.success('Data export completed and ready for download');
            return;
          } else if (updatedRequest.status === 'failed') {
            toast.error(`Export failed: ${updatedRequest.error || 'Unknown error'}`);
            return;
          }

          // Continue polling if still processing
          if (updatedRequest.status === 'processing' && pollCount < maxPolls) {
            pollCount++;
            setTimeout(poll, pollInterval);
          } else if (pollCount >= maxPolls) {
            toast.error('Export is taking longer than expected. Please check back later.');
          }
        }
      } catch (err) {
        console.error('Error polling export status:', err);
        // Don't show error toast for polling failures
      }
    };

    // Start polling after a short delay
    setTimeout(poll, 2000);
  }, []);

  /**
   * Refresh export requests
   */
  const refreshRequests = useCallback(async () => {
    await fetchExportRequests();
  }, [fetchExportRequests]);

  /**
   * Get export statistics
   */
  const getExportStats = useCallback(() => {
    const total = exportRequests.length;
    const completed = exportRequests.filter(req => req.status === 'completed').length;
    const pending = exportRequests.filter(req => req.status === 'pending').length;
    const processing = exportRequests.filter(req => req.status === 'processing').length;
    const failed = exportRequests.filter(req => req.status === 'failed').length;
    const expired = exportRequests.filter(req => 
      req.expiryDate && new Date() > new Date(req.expiryDate)
    ).length;

    return {
      total,
      completed,
      pending,
      processing,
      failed,
      expired,
      available: completed - expired
    };
  }, [exportRequests]);

  /**
   * Get the most recent export request
   */
  const getLatestExport = useCallback(() => {
    if (exportRequests.length === 0) return null;

    return exportRequests.reduce((latest, current) => {
      return new Date(current.requestDate) > new Date(latest.requestDate) ? current : latest;
    });
  }, [exportRequests]);

  /**
   * Check if user can request a new export
   */
  const canRequestNewExport = useCallback(() => {
    const pendingOrProcessing = exportRequests.filter(req => 
      req.status === 'pending' || req.status === 'processing'
    ).length;

    // Limit to 3 pending/processing requests at a time
    return pendingOrProcessing < 3;
  }, [exportRequests]);

  // Load export requests on mount and when user changes
  useEffect(() => {
    if (user) {
      fetchExportRequests();
    } else {
      setExportRequests([]);
      setError(null);
    }
  }, [user, fetchExportRequests]);

  // Auto-refresh every 30 seconds to check for status updates
  useEffect(() => {
    if (!user || exportRequests.length === 0) return;

    const hasActiveRequests = exportRequests.some(req => 
      req.status === 'pending' || req.status === 'processing'
    );

    if (!hasActiveRequests) return;

    const interval = setInterval(() => {
      fetchExportRequests();
    }, 30000);

    return () => clearInterval(interval);
  }, [user, exportRequests, fetchExportRequests]);

  return {
    exportRequests,
    loading,
    error,
    requestExport,
    downloadExport,
    cancelExport,
    refreshRequests,
    getExportStats,
    getLatestExport,
    canRequestNewExport
  };
};

export default useDataExport;

/**
 * Hook personalizzato per la gestione centralizzata degli errori nell'applicazione.
 * 
 * Fornisce funzioni per:
 * - Gestire errori API in modo consistente
 * - Mostrare errori all'utente in modo appropriato
 * - Tracciare errori per scopi di logging
 * 
 * @module hooks/useErrorHandler
 */

import React, { useState, useCallback } from 'react';
import { toast } from 'react-hot-toast';
import { sanitizeErrorMessage } from '../utils/errorUtils';

/**
 * Interfaccia per gli errori API
 */
export interface ApiError extends Error {
  statusCode?: number;
  details?: unknown;
  code?: string;
}

/**
 * Opzioni per la gestione degli errori
 */
interface ErrorHandlerOptions {
  /** Se mostrare automaticamente l'errore all'utente */
  showToast?: boolean;
  /** Se registrare l'errore nella console */
  logToConsole?: boolean;
  /** Callback personalizzato per errori specifici */
  onError?: (error: ApiError) => void;
}

/**
 * Risultato del hook useErrorHandler
 */
interface ErrorHandlerResult {
  /** Errore corrente */
  error: ApiError | null;
  /** Se  in corso la gestione di un errore */
  isError: boolean;
  /** Funzione per gestire un errore */
  handleError: (error: unknown, customMessage?: string) => void;
  /** Funzione per resettare lo stato dell'errore */
  clearError: () => void;
  /** Funzione per gestire errori in chiamate API */
  wrapAsync: <T>(promise: Promise<T>, options?: ErrorHandlerOptions) => Promise<T | null>;
}

/**
 * Hook per la gestione centralizzata degli errori nell'applicazione
 * 
 * @example
 * ```tsx
 * const { handleError, wrapAsync } = useErrorHandler();
 * 
 * // Uso con una funzione asincrona
 * const handleSubmit = async () => {
 *   try {
 *     await saveData();
 *   } catch (error) {
 *     handleError(error, "Impossibile salvare i dati");
 *   }
 * };
 * 
 * // Uso con wrapAsync
 * const fetchData = async () => {
 *   const data = await wrapAsync(api.fetchData(), {
 *     showToast: true,
 *     onError: (error) => {
 *       // Logica personalizzata per errori specifici
 *       if (error.statusCode === 404) {
 *         navigate('/not-found');
 *       }
 *     }
 *   });
 *   
 *   if (data) {
 *     // Procedi con i dati
 *   }
 * };
 * ```
 */
export function useErrorHandler(): ErrorHandlerResult {
  const [error, setError] = useState<ApiError | null>(null);

  /**
   * Gestisce un errore, normalizzandolo e eseguendo le azioni appropriate
   * 
   * @param error - Errore da gestire (pu essere qualsiasi tipo)
   * @param customMessage - Messaggio personalizzato da mostrare all'utente
   */
  const handleError = useCallback((error: unknown, customMessage?: string) => {
    // Normalizza l'errore
    const normalizedError: ApiError = error instanceof Error 
      ? error as ApiError
      : new Error(typeof error === 'string' ? error : 'Si  verificato un errore sconosciuto');

    // Imposta lo stato dell'errore
    setError(normalizedError);

    // Logga l'errore nella console
    console.error('Error handled:', normalizedError);

    // Mostra il toast con l'errore sanitizzato
    const message = customMessage || sanitizeErrorMessage(normalizedError, 'Si  verificato un errore');
    toast.error(message);
  }, []);

  /**
   * Resetta lo stato dell'errore
   */
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  /**
   * Avvolge una Promise per gestire automaticamente gli errori
   * 
   * @param promise - Promise da avvolgere
   * @param options - Opzioni per la gestione degli errori
   * @returns Promise che restituisce il risultato o null in caso di errore
   */
  const wrapAsync = useCallback(async <T>(
    promise: Promise<T>, 
    options: ErrorHandlerOptions = {}
  ): Promise<T | null> => {
    const { 
      showToast = true, 
      logToConsole = true,
      onError 
    } = options;

    try {
      return await promise;
    } catch (error) {
      // Normalizza l'errore
      const normalizedError: ApiError = error instanceof Error 
        ? error as ApiError 
        : new Error(typeof error === 'string' ? error : 'Si  verificato un errore sconosciuto');

      // Imposta lo stato dell'errore
      setError(normalizedError);

      // Logga l'errore se richiesto
      if (logToConsole) {
        console.error('API Error:', normalizedError);

        if (normalizedError.details) {
          console.error('Error details:', normalizedError.details);
        }
      }

      // Mostra il toast se richiesto
      if (showToast) {
        toast.error(sanitizeErrorMessage(normalizedError, 'Si  verificato un errore durante l\'operazione'));
      }

      // Chiama il callback personalizzato se fornito
      if (onError) {
        onError(normalizedError);
      }

      return null;
    }
  }, []);

  return {
    error,
    isError: error !== null,
    handleError,
    clearError,
    wrapAsync,
  };
}

export default useErrorHandler;

import React, { useState, useEffect, useRef } from 'react';
import { apiGet } from '../services/api';

interface UseFetchOptions<T> {
  initialData?: T;
  fallbackData?: T;
  timeoutMs?: number;
  onSuccess?: (data: T) => void;
  onError?: (error: string) => void;
}

function useFetch<T = unknown>(
  url: string, 
  options?: Record<string, unknown>, 
  fetchOptions?: UseFetchOptions<T>
) {
  const [data, setData] = useState<T | undefined>(fetchOptions?.initialData);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Track if we're using fallback data
  const [usingFallback, setUsingFallback] = useState(false);

  const fetchData = async (abortController?: AbortController) => {
    if (!url) return;

    setLoading(true);
    setError(null);

    try {
      console.log(`Fetching from: ${url}`);

      const response = await apiGet<T>(url);

      setData(response);
      setUsingFallback(false);
      fetchOptions?.onSuccess?.(response);
    } catch (err: unknown) {
      console.warn(`Fetch error:`, err);

      const errorMessage = err instanceof Error ? err.message : 'An error occurred';

      // Handle fallback data if provided
      if (fetchOptions?.fallbackData !== undefined) {
        console.log(`Using fallback data for ${url}`);
        setData(fetchOptions.fallbackData);
        setUsingFallback(true);
        setError(`Using demo data (${errorMessage})`);
      } else {
        setError(errorMessage);
      }

      fetchOptions?.onError?.(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const abortController = new AbortController();

    if (url) {
      fetchData(abortController);
    } else {
      // If URL is empty, reset everything except for fallback data
      if (fetchOptions?.fallbackData !== undefined) {
        setData(fetchOptions.fallbackData);
        setUsingFallback(true);
        setError('Using demo data (no endpoint provided)');
      } else {
        setData(undefined);
        setError(null);
      }
      setLoading(false);
    }

    return () => {
      abortController.abort();
    };
  }, [url]);

  const refetch = () => fetchData();

  return { data, loading, error, refetch, usingFallback };
}

export default useFetch;

import React, { useState, useCallback, useMemo } from 'react';

interface UseFilterSearchResult<T> {
  searchTerm: string;
  setSearchTerm: (value: string) => void;
  filterActive: boolean;
  toggleFilter: () => void;
  filterItems: (items: T[]) => T[];
  resetFilters: () => void;
}

interface UseFilterSearchOptions<T> {
  searchKeys?: (keyof T)[];
  defaultSearchTerm?: string;
  defaultFilterActive?: boolean;
  customFilter?: (item: T, searchTerm: string) => boolean;
}

/**
 * A custom hook for managing search and filter functionality
 */
function useFilterSearch<T>(options?: UseFilterSearchOptions<T>): UseFilterSearchResult<T> {
  const { 
    searchKeys = [], 
    defaultSearchTerm = '', 
    defaultFilterActive = false,
    customFilter
  } = options || {};

  const [searchTerm, setSearchTerm] = useState(defaultSearchTerm);
  const [filterActive, setFilterActive] = useState(defaultFilterActive);

  const toggleFilter = useCallback(() => {
    setFilterActive(prev => !prev);
  }, []);

  const resetFilters = useCallback(() => {
    setSearchTerm('');
    setFilterActive(false);
  }, []);

  const filterItems = useCallback((items: T[]): T[] => {
    if (!searchTerm.trim() || !filterActive) {
      return items;
    }

    const lowerSearchTerm = searchTerm.toLowerCase();

    return items.filter(item => {
      if (customFilter) {
        return customFilter(item, lowerSearchTerm);
      }

      // Default search implementation, searches across specified keys
      if (searchKeys.length === 0) {
        // If no keys specified, convert the whole item to string and search
        return JSON.stringify(item).toLowerCase().includes(lowerSearchTerm);
      }

      return searchKeys.some(key => {
        const value = item[key];
        if (value === undefined || value === null) return false;
        return String(value).toLowerCase().includes(lowerSearchTerm);
      });
    });
  }, [searchTerm, filterActive, customFilter, searchKeys]);

  return {
    searchTerm,
    setSearchTerm,
    filterActive,
    toggleFilter,
    filterItems,
    resetFilters
  };
}

export default useFilterSearch;

import React, { useState, useCallback, Dispatch, SetStateAction } from 'react';
import { useTranslation } from 'react-i18next';
import { useErrorHandler } from './index';

type ValidationRuleFn = (value: unknown, formData?: Record<string, unknown>) => string | null;

export interface ValidationRule {
  required?: boolean;
  minLength?: number;
  maxLength?: number;
  min?: number;
  max?: number;
  pattern?: RegExp;
  email?: boolean;
  custom?: ValidationRuleFn;
  customMessage?: string;
}

export type ValidationSchema = Record<string, ValidationRule>;

interface UseFormValidationReturn<T extends Record<string, unknown>> {
  formData: T;
  errors: Record<string, string>;
  touched: Record<string, boolean>;
  setFormData: Dispatch<SetStateAction<T>>;
  handleChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => void;
  handleBlur: (e: React.FocusEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => void;
  validateField: (fieldName: string, value?: unknown) => string | null;
  validateForm: () => boolean;
  resetForm: () => void;
  setFieldValue: (fieldName: string, value: unknown) => void;
  getFieldProps: (fieldName: string) => {
    name: string;
    value: unknown;
    onChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => void;
    onBlur: (e: React.FocusEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => void;
    error: string | undefined;
  };
}

/**
 * Hook per gestire la validazione dei form con uno schema definito
 */
export function useFormValidation<T extends Record<string, unknown>>(
  initialValues: T,
  validationSchema: ValidationSchema = {},
  onSubmit?: (values: T) => void
): UseFormValidationReturn<T> {
  const { t } = useTranslation();
  const [formData, setFormData] = useState<T>(initialValues);
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [touched, setTouched] = useState<Record<string, boolean>>({});
  const { handleError } = useErrorHandler();

  /**
   * Validazione di un singolo campo con lo schema
   */
  const validateField = useCallback(
    (fieldName: string, value: unknown = undefined): string | null => {
      const fieldValue = value !== undefined ? value : formData[fieldName];
      const rules = validationSchema[fieldName];

      if (!rules) return null;

      // Validazione del campo richiesto
      if (rules.required && (fieldValue === undefined || fieldValue === null || fieldValue === '')) {
        return t('errors.required');
      }

      // Salta la validazione se il valore  vuoto ma non  richiesto
      if (fieldValue === undefined || fieldValue === null || fieldValue === '') {
        return null;
      }

      // Validazione minLength (per stringhe)
      if (rules.minLength !== undefined && typeof fieldValue === 'string' && fieldValue.length < rules.minLength) {
        return t('validation.minLength', { min: rules.minLength });
      }

      // Validazione maxLength (per stringhe)
      if (rules.maxLength !== undefined && typeof fieldValue === 'string' && fieldValue.length > rules.maxLength) {
        return t('validation.maxLength', { max: rules.maxLength });
      }

      // Validazione min (per numeri)
      if (rules.min !== undefined && typeof fieldValue === 'number' && fieldValue < rules.min) {
        return t('validation.min', { min: rules.min });
      }

      // Validazione max (per numeri)
      if (rules.max !== undefined && typeof fieldValue === 'number' && fieldValue > rules.max) {
        return t('validation.max', { max: rules.max });
      }

      // Validazione pattern (per stringhe)
      if (rules.pattern && typeof fieldValue === 'string' && !rules.pattern.test(fieldValue)) {
        return t('validation.pattern');
      }

      // Validazione email (per stringhe)
      if (
        rules.email &&
        typeof fieldValue === 'string' &&
        !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(fieldValue)
      ) {
        return t('validation.email');
      }

      // Validazione custom
      if (rules.custom) {
        const customError = rules.custom(fieldValue, formData);
        if (customError) {
          return rules.customMessage || customError;
        }
      }

      return null;
    },
    [formData, validationSchema, t]
  );

  /**
   * Validazione dell'intero form
   */
  const validateForm = useCallback((): boolean => {
    const validationErrors: Record<string, string> = {};
    let isValid = true;

    Object.keys(validationSchema).forEach((fieldName) => {
      const errorMessage = validateField(fieldName);
      if (errorMessage) {
        validationErrors[fieldName] = errorMessage;
        isValid = false;
      }
    });

    setErrors(validationErrors);
    return isValid;
  }, [validateField, validationSchema]);

  /**
   * Gestione del cambio valore nei campi
   */
  const handleChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
      const { name, value, type } = e.target as HTMLInputElement;
      let finalValue: unknown = value;

      // Converti i valori in base al tipo di input
      if (type === 'number') {
        finalValue = value === '' ? '' : Number(value);
      } else if (type === 'checkbox') {
        finalValue = (e.target as HTMLInputElement).checked;
      }

      setFormData((prevData) => ({
        ...prevData,
        [name]: finalValue,
      }));

      // Se il campo  gi stato toccato, aggiorna gli errori in tempo reale
      if (touched[name]) {
        const errorMessage = validateField(name, finalValue);
        setErrors((prevErrors) => ({
          ...prevErrors,
          [name]: errorMessage || '',
        }));
      }
    },
    [touched, validateField]
  );

  /**
   * Gestione dell'evento blur (uscita dal campo)
   */
  const handleBlur = useCallback(
    (e: React.FocusEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
      const { name } = e.target;

      // Marca il campo come toccato
      setTouched((prevTouched) => ({
        ...prevTouched,
        [name]: true,
      }));

      // Valida il campo
      const errorMessage = validateField(name);
      setErrors((prevErrors) => ({
        ...prevErrors,
        [name]: errorMessage || '',
      }));
    },
    [validateField]
  );

  /**
   * Imposta direttamente il valore di un campo
   */
  const setFieldValue = useCallback(
    (fieldName: string, value: unknown) => {
      setFormData((prevData) => ({
        ...prevData,
        [fieldName]: value,
      }));

      // Se il campo  gi stato toccato, aggiorna gli errori in tempo reale
      if (touched[fieldName]) {
        const errorMessage = validateField(fieldName, value);
        setErrors((prevErrors) => ({
          ...prevErrors,
          [fieldName]: errorMessage || '',
        }));
      }
    },
    [touched, validateField]
  );

  /**
   * Reset del form ai valori iniziali
   */
  const resetForm = useCallback(() => {
    setFormData(initialValues);
    setErrors({});
    setTouched({});
  }, [initialValues]);

  /**
   * Ottieni tutte le props necessarie per un campo del form
   */
  const getFieldProps = useCallback(
    (fieldName: string) => ({
      name: fieldName,
      value: formData[fieldName],
      onChange: handleChange,
      onBlur: handleBlur,
      error: errors[fieldName],
    }),
    [formData, errors, handleChange, handleBlur]
  );

  return {
    formData,
    errors,
    touched,
    setFormData,
    handleChange,
    handleBlur,
    validateField,
    validateForm,
    resetForm,
    setFieldValue,
    getFieldProps,
  };
}

export default useFormValidation;

/**
 * GDPR Consent Management Hook
 * Handles user consent operations and state management
 */

import { useState, useEffect, useCallback } from 'react';
import apiClient from '../services/apiClient';
import {
  GDPRConsent,
  ConsentFormData,
  ConsentWithdrawalFormData,
  UseGDPRConsentReturn,
  ConsentsListResponse,
  ConsentResponse
} from '../types/gdpr';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-hot-toast';

export const useGDPRConsent = (): UseGDPRConsentReturn => {
  const [consents, setConsents] = useState<GDPRConsent[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();

  /**
   * Fetch user's current consents
   */
  const fetchConsents = useCallback(async () => {
    if (!user) return;

    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.get<ConsentsListResponse>('/api/gdpr/consent');

      if (response.data.success && response.data.data) {
        setConsents(response.data.data.consents);
      } else {
        throw new Error(response.data.error || 'Failed to fetch consents');
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to fetch consents';
      setError(errorMessage);
      console.error('Error fetching consents:', err);
      toast.error('Failed to load consent information');
    } finally {
      setLoading(false);
    }
  }, [user]);

  /**
   * Grant a new consent
   */
  const grantConsent = useCallback(async (data: ConsentFormData) => {
    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.post<ConsentResponse>('/api/gdpr/consent', {
        consentType: data.consentType,
        purpose: data.purpose,
        legalBasis: data.legalBasis || 'consent'
      });

      if (response.data.success && response.data.data) {
        const newConsent = response.data.data.consent;

        // Update local state
        setConsents(prev => {
          const filtered = prev.filter(c => c.consentType !== newConsent.consentType);
          return [...filtered, newConsent];
        });

        toast.success(`Consent granted for ${data.consentType}`);
      } else {
        throw new Error(response.data.error || 'Failed to grant consent');
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to grant consent';
      setError(errorMessage);
      console.error('Error granting consent:', err);
      toast.error('Failed to grant consent');
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Withdraw an existing consent
   */
  const withdrawConsent = useCallback(async (data: ConsentWithdrawalFormData) => {
    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.post<ConsentResponse>('/api/gdpr/consent/withdraw', {
        consentType: data.consentType,
        reason: data.reason
      });

      if (response.data.success && response.data.data) {
        const withdrawnConsent = response.data.data.consent;

        // Update local state
        setConsents(prev => 
          prev.map(consent => 
            consent.consentType === withdrawnConsent.consentType
              ? { ...consent, ...withdrawnConsent }
              : consent
          )
        );

        toast.success(`Consent withdrawn for ${data.consentType}`);
      } else {
        throw new Error(response.data.error || 'Failed to withdraw consent');
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to withdraw consent';
      setError(errorMessage);
      console.error('Error withdrawing consent:', err);
      toast.error('Failed to withdraw consent');
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Refresh consents data
   */
  const refreshConsents = useCallback(async () => {
    await fetchConsents();
  }, [fetchConsents]);

  /**
   * Get consent status for a specific type
   */
  const getConsentStatus = useCallback((consentType: string) => {
    const consent = consents.find(c => c.consentType === consentType);
    return {
      granted: consent?.consentGiven || false,
      date: consent?.consentDate,
      withdrawnAt: consent?.withdrawnAt,
      withdrawalReason: consent?.withdrawalReason
    };
  }, [consents]);

  /**
   * Check if a specific consent is active
   */
  const hasActiveConsent = useCallback((consentType: string) => {
    const consent = consents.find(c => c.consentType === consentType);
    return consent?.consentGiven && !consent?.withdrawnAt;
  }, [consents]);

  /**
   * Get all active consents
   */
  const getActiveConsents = useCallback(() => {
    return consents.filter(consent => consent.consentGiven && !consent.withdrawnAt);
  }, [consents]);

  /**
   * Get consent statistics
   */
  const getConsentStats = useCallback(() => {
    const total = consents.length;
    const active = getActiveConsents().length;
    const withdrawn = consents.filter(c => c.withdrawnAt).length;

    return {
      total,
      active,
      withdrawn,
      percentage: total > 0 ? Math.round((active / total) * 100) : 0
    };
  }, [consents, getActiveConsents]);

  // Load consents on mount and when user changes
  useEffect(() => {
    if (user) {
      fetchConsents();
    } else {
      setConsents([]);
      setError(null);
    }
  }, [user, fetchConsents]);

  return {
    consents,
    loading,
    error,
    grantConsent,
    withdrawConsent,
    refreshConsents,
    // Additional utility functions
    getConsentStatus,
    hasActiveConsent,
    getActiveConsents,
    getConsentStats
  };
};

/**
 * Hook for managing a single consent type
 * Useful for individual consent toggles
 */
export const useSingleConsent = (consentType: string) => {
  const {
    consents,
    loading,
    error,
    grantConsent,
    withdrawConsent,
    hasActiveConsent,
    getConsentStatus
  } = useGDPRConsent();

  const isActive = hasActiveConsent(consentType);
  const status = getConsentStatus(consentType);

  const toggle = useCallback(async (purpose: string, reason?: string) => {
    if (isActive) {
      await withdrawConsent({
        consentType: consentType as any,
        reason: reason || 'User requested withdrawal'
      });
    } else {
      await grantConsent({
        consentType: consentType as any,
        purpose
      });
    }
  }, [isActive, consentType, grantConsent, withdrawConsent]);

  return {
    isActive,
    status,
    loading,
    error,
    toggle
  };
};

export default useGDPRConsent;

import { useState, useEffect } from 'react';
import { apiGet } from '../services/api';
import { getLoadingErrorMessage } from '../utils/errorUtils';

interface UseGDPREntityDataProps {
  apiEndpoint: string;
  entityNamePlural: string;
  entityDisplayNamePlural: string;
  readPermission: string;
}

interface UseGDPREntityDataReturn<T> {
  entities: T[];
  loading: boolean;
  error: string | null;
  refetch: () => Promise<void>;
  setEntities: React.Dispatch<React.SetStateAction<T[]>>;
}

export function useGDPREntityData<T = unknown>({
  apiEndpoint,
  entityNamePlural,
  entityDisplayNamePlural,
  readPermission
}: UseGDPREntityDataProps): UseGDPREntityDataReturn<T> {
  const [entities, setEntities] = useState<T[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const loadEntities = async () => {
    try {
      setLoading(true);
      setError(null);

      // Costruisci i parametri di query per l'endpoint delle persone
      let apiUrl = apiEndpoint;
      if (apiEndpoint === '/api/persons' || apiEndpoint === '/api/v1/persons') {
        // Per l'endpoint delle persone, aggiungi i parametri necessari
        const params = new URLSearchParams();
        params.append('page', '1');
        params.append('limit', '50');
        params.append('sortBy', 'lastLogin');
        params.append('sortOrder', 'desc');

        apiUrl = `${apiEndpoint}?${params.toString()}`;
      }

      const response = await apiGet<{ persons?: T[] } | T[]>(apiUrl);

      // Gestisci la risposta paginata per l'endpoint delle persone
      if ((apiEndpoint === '/api/persons' || apiEndpoint === '/api/v1/persons') && response && typeof response === 'object' && 'persons' in response && response.persons) {
        setEntities(response.persons);
      } else if (Array.isArray(response)) {
        setEntities(response);
      } else {
        setEntities([]);
      }
    } catch (err: unknown) {
      console.error(` Errore caricamento ${entityDisplayNamePlural}:`, err);
      setError(getLoadingErrorMessage(
        (entityNamePlural as keyof typeof import('../utils/errorUtils').errorMessages.loading) || 'generic', 
        err
      ));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadEntities();
  }, [apiEndpoint, entityNamePlural]);

  return {
    entities,
    loading,
    error,
    refetch: loadEntities,
    setEntities
  };
}

import { useState } from 'react';
import { apiDelete } from '../services/api';
import { useToast } from './ui/useToast';
import { sanitizeErrorMessage } from '../utils/errorUtils';

interface UseGDPREntityOperationsProps {
  entityName: string;
  entityNamePlural: string;
  entityDisplayName: string;
  entityDisplayNamePlural: string;
  onDeleteEntity?: (id: string) => Promise<void>;
  refetch: () => Promise<void>;
}

interface UseGDPREntityOperationsReturn {
  selectedIds: string[];
  selectAll: boolean;
  selectionMode: boolean;
  setSelectedIds: React.Dispatch<React.SetStateAction<string[]>>;
  setSelectAll: React.Dispatch<React.SetStateAction<boolean>>;
  setSelectionMode: React.Dispatch<React.SetStateAction<boolean>>;
  handleSelectAll: (entities: Array<{ id: string }>) => void;
  handleSelectEntity: (id: string) => void;
  handleDeleteEntity: (id: string) => Promise<void>;
  handleBatchDelete: () => Promise<void>;
  clearSelection: () => void;
}

export function useGDPREntityOperations({
  entityName,
  entityNamePlural,
  entityDisplayName,
  entityDisplayNamePlural,
  onDeleteEntity,
  refetch
}: UseGDPREntityOperationsProps): UseGDPREntityOperationsReturn {
  const toast = useToast();

  // Stati per selezione e operazioni batch
  const [selectedIds, setSelectedIds] = useState<string[]>([]);
  const [selectAll, setSelectAll] = useState(false);
  const [selectionMode, setSelectionMode] = useState(false);

  const clearSelection = () => {
    setSelectedIds([]);
    setSelectAll(false);
    setSelectionMode(false);
  };

  const handleSelectAll = (entities: Array<{ id: string }>) => {
    // Validazione di sicurezza per assicurarsi che entities sia un array
    if (!Array.isArray(entities)) {
      console.error('handleSelectAll: entities deve essere un array, ricevuto:', typeof entities, entities);
      return;
    }

    if (selectAll) {
      setSelectedIds([]);
      setSelectAll(false);
    } else {
      setSelectedIds(entities.map(entity => entity.id));
      setSelectAll(true);
    }
  };

  const handleSelectEntity = (id: string) => {
    setSelectedIds(prev => {
      if (prev.includes(id)) {
        const newSelection = prev.filter(selectedId => selectedId !== id);
        if (newSelection.length === 0) {
          setSelectionMode(false);
        }
        return newSelection;
      } else {
        if (!selectionMode) {
          setSelectionMode(true);
        }
        return [...prev, id];
      }
    });
  };

  const handleDeleteEntity = async (id: string) => {
    if (!window.confirm(`Sei sicuro di voler eliminare questo ${entityDisplayName.toLowerCase()}?`)) {
      return;
    }

    try {
      if (onDeleteEntity) {
        await onDeleteEntity(id);
      } else {
        await apiDelete(`/api/v1/${entityNamePlural}/${id}`);
      }

      toast.success(`${entityDisplayName} eliminato con successo`);
      await refetch();

      // Rimuovi l'elemento dalla selezione se era selezionato
      setSelectedIds(prev => prev.filter(selectedId => selectedId !== id));
    } catch (error: unknown) {
      console.error(`Errore eliminazione ${entityDisplayName.toLowerCase()}:`, error);
      const errorMessage = error instanceof Error ? error.message : `Errore durante l'eliminazione del ${entityDisplayName.toLowerCase()}`;
      toast.error(sanitizeErrorMessage(errorMessage));
    }
  };

  const handleBatchDelete = async () => {
    if (selectedIds.length === 0) return;

    const confirmMessage = selectedIds.length === 1 
      ? `Sei sicuro di voler eliminare questo ${entityDisplayName.toLowerCase()}?`
      : `Sei sicuro di voler eliminare ${selectedIds.length} ${entityDisplayNamePlural.toLowerCase()}?`;

    if (!window.confirm(confirmMessage)) {
      return;
    }

    try {
      // Elimina tutti gli elementi selezionati in modo sequenziale per evitare rate limiting
      for (const id of selectedIds) {
        if (onDeleteEntity) {
          await onDeleteEntity(id);
        } else {
          await apiDelete(`/api/v1/${entityNamePlural}/${id}`);
        }
        // Piccola pausa tra le richieste per evitare rate limiting
        await new Promise(resolve => setTimeout(resolve, 100));
      }

      const successMessage = selectedIds.length === 1
        ? `${entityDisplayName} eliminato con successo`
        : `${selectedIds.length} ${entityDisplayNamePlural.toLowerCase()} eliminati con successo`;

      toast.success(successMessage);
      await refetch();
      clearSelection();
    } catch (error: unknown) {
      console.error(`Errore eliminazione batch ${entityDisplayNamePlural.toLowerCase()}:`, error);
      const errorMessage = error instanceof Error ? error.message : `Errore durante l'eliminazione dei ${entityDisplayNamePlural.toLowerCase()}`;
      toast.error(sanitizeErrorMessage(errorMessage));
    }
  };

  return {
    selectedIds,
    selectAll,
    selectionMode,
    setSelectedIds,
    setSelectAll,
    setSelectionMode,
    handleSelectAll,
    handleSelectEntity,
    handleDeleteEntity,
    handleBatchDelete,
    clearSelection
  };
}

import React, { useState, useMemo, useCallback } from 'react';

export interface UsePaginationOptions<T> {
  data: T[];
  pageSize?: number;
  initialPage?: number;
}

export interface UsePaginationReturn<T> {
  currentPage: number;
  totalPages: number;
  pageSize: number;
  paginatedData: T[];
  totalItems: number;
  goToPage: (page: number) => void;
  nextPage: () => void;
  prevPage: () => void;
  setPageSize: (size: number) => void;
}

export function usePagination<T>({
  data,
  pageSize: initialPageSize = 10,
  initialPage = 1
}: UsePaginationOptions<T>): UsePaginationReturn<T> {
  const [currentPage, setCurrentPage] = useState(initialPage);
  const [pageSize, setPageSize] = useState(initialPageSize);

  // Resetta la pagina quando cambia dimensione
  const handleSetPageSize = useCallback((newSize: number) => {
    setPageSize(newSize);
    setCurrentPage(1); // Torna alla prima pagina
  }, []);

  // Calcola numero totale di pagine
  const totalPages = useMemo(() => {
    return Math.max(1, Math.ceil(data.length / pageSize));
  }, [data.length, pageSize]);

  // Assicura che currentPage sia sempre valido
  useMemo(() => {
    if (currentPage > totalPages) {
      setCurrentPage(totalPages);
    }
  }, [currentPage, totalPages]);

  // Estrai i dati per la pagina corrente
  const paginatedData = useMemo(() => {
    const startIndex = (currentPage - 1) * pageSize;
    return data.slice(startIndex, startIndex + pageSize);
  }, [data, currentPage, pageSize]);

  // Funzioni di navigazione
  const goToPage = useCallback((page: number) => {
    const validPage = Math.min(Math.max(1, page), totalPages);
    setCurrentPage(validPage);
  }, [totalPages]);

  const nextPage = useCallback(() => {
    if (currentPage < totalPages) {
      setCurrentPage(prev => prev + 1);
    }
  }, [currentPage, totalPages]);

  const prevPage = useCallback(() => {
    if (currentPage > 1) {
      setCurrentPage(prev => prev - 1);
    }
  }, [currentPage]);

  return {
    currentPage,
    totalPages,
    pageSize,
    paginatedData,
    totalItems: data.length,
    goToPage,
    nextPage,
    prevPage,
    setPageSize: handleSetPageSize
  };
}

export default usePagination;

import { useState, useEffect, useMemo } from 'react';
import { apiGet } from '../services/api';
import { 
  Person, 
  FilterConfig, 
  filterPersonsByRoleLevel, 
  filterEmployees, 
  filterTrainers,
  applyCustomFilter 
} from '../services/roleHierarchyService';

export interface UsePersonFiltersOptions {
  filterConfig?: FilterConfig;
  filterType?: 'all' | 'employees' | 'trainers' | 'custom';
  autoFetch?: boolean;
  includeDeleted?: boolean;
}

export interface UsePersonFiltersReturn {
  persons: Person[];
  filteredPersons: Person[];
  loading: boolean;
  error: string | null;
  refetch: () => Promise<void>;
  setPersons: (persons: Person[]) => void;
  totalCount: number;
  filteredCount: number;
}

/**
 * Hook personalizzato per la gestione delle persone con filtri gerarchici
 * Supporta filtri predefiniti per employees/trainers e filtri personalizzati
 */
export const usePersonFilters = ({
  filterConfig,
  filterType = 'all',
  autoFetch = true,
  includeDeleted = false
}: UsePersonFiltersOptions = {}): UsePersonFiltersReturn => {
  const [persons, setPersons] = useState<Person[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Calcola le persone filtrate in base alla configurazione
  const filteredPersons = useMemo(() => {
    if (!persons.length) return [];

    switch (filterType) {
      case 'employees':
        return filterEmployees(persons);

      case 'trainers':
        return filterTrainers(persons);

      case 'custom':
        if (filterConfig) {
          return applyCustomFilter(persons, filterConfig);
        }
        return persons;

      case 'all':
      default:
        return persons;
    }
  }, [persons, filterType, filterConfig]);

  // Fetch delle persone dal backend
  const fetchPersons = async (): Promise<void> => {
    try {
      setLoading(true);
      setError(null);

      // Usa l'endpoint unificato per le persone con parametro includeDeleted
      const params = includeDeleted ? '?includeDeleted=true' : '';
      console.log(` usePersonFilters - Chiamando API: /api/v1/persons${params}`);
      const data = await apiGet<Person[]>(`/api/v1/persons${params}`);
      console.log(` usePersonFilters - Ricevuti ${data?.length || 0} elementi dall'API`);
      console.log(` usePersonFilters - includeDeleted: ${includeDeleted}, filterType: ${filterType}`);
      if (data && data.length > 0) {
        console.log(` usePersonFilters - Primo elemento:`, data[0]);
      }
      setPersons(data || []);
    } catch (err) {
      console.error('Error fetching persons:', err);
      setError(err instanceof Error ? err.message : 'Errore nel caricamento delle persone');
      setPersons([]);
    } finally {
      setLoading(false);
    }
  };

  // Auto-fetch al mount se richiesto
  useEffect(() => {
    if (autoFetch) {
      fetchPersons();
    }
  }, [autoFetch, includeDeleted]);

  return {
    persons,
    filteredPersons,
    loading,
    error,
    refetch: fetchPersons,
    setPersons,
    totalCount: persons.length,
    filteredCount: filteredPersons.length
  };
};

/**
 * Hook semplificato per employees
 */
export const useEmployees = () => {
  return usePersonFilters({ filterType: 'employees' });
};

/**
 * Hook semplificato per trainers
 */
export const useTrainers = () => {
  return usePersonFilters({ filterType: 'trainers' });
};

/**
 * Hook per tutte le persone senza filtri
 */
export const useAllPersons = () => {
  return usePersonFilters({ filterType: 'all' });
};

/**
 * Hook per tutte le persone inclusi i soft-deleted (per importazione)
 */
export const useAllPersonsForImport = () => {
  return usePersonFilters({ filterType: 'all', includeDeleted: true });
};

/**
 * Privacy Settings Hook
 * Manages user privacy preferences and GDPR settings
 */

import { useState, useEffect, useCallback } from 'react';
import apiClient from '../services/apiClient';
import {
  PrivacySettings,
  PrivacySettingsFormData,
  UsePrivacySettingsReturn,
  GDPRApiResponse
} from '../types/gdpr';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-hot-toast';

export const usePrivacySettings = (): UsePrivacySettingsReturn => {
  const [settings, setSettings] = useState<PrivacySettings | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const { user } = useAuth();

  /**
   * Fetch user's privacy settings
   */
  const fetchPrivacySettings = useCallback(async () => {
    if (!user) return;

    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.get<GDPRApiResponse<{ settings: PrivacySettings }>>(
        '/api/gdpr/privacy-settings'
      );

      if (response.data.success && response.data.data) {
        setSettings(response.data.data.settings);
        setHasUnsavedChanges(false);
      } else {
        throw new Error(response.data.error || 'Failed to fetch privacy settings');
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to fetch privacy settings';
      setError(errorMessage);
      console.error('Error fetching privacy settings:', err);
      toast.error('Failed to load privacy settings');
    } finally {
      setLoading(false);
    }
  }, [user]);

  /**
   * Update privacy settings
   */
  const updatePrivacySettings = useCallback(async (data: PrivacySettingsFormData) => {
    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.put<GDPRApiResponse<{ settings: PrivacySettings }>>(
        '/api/gdpr/privacy-settings',
        data
      );

      if (response.data.success && response.data.data) {
        setSettings(response.data.data.settings);
        setHasUnsavedChanges(false);
        toast.success('Privacy settings updated successfully');
      } else {
        throw new Error(response.data.error || 'Failed to update privacy settings');
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to update privacy settings';
      setError(errorMessage);
      console.error('Error updating privacy settings:', err);
      toast.error(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Update a single privacy setting
   */
  const updateSingleSetting = useCallback(async (
    key: keyof PrivacySettingsFormData, 
    value: boolean
  ) => {
    if (!settings) return;

    try {
      const updatedSettings = {
        dataProcessingConsent: settings.dataProcessingConsent,
        marketingConsent: settings.marketingConsent,
        analyticsConsent: settings.analyticsConsent,
        profileVisibility: settings.profileVisibility,
        dataRetentionOptOut: settings.dataRetentionOptOut,
        thirdPartySharing: settings.thirdPartySharing,
        [key]: value
      };

      await updatePrivacySettings(updatedSettings);
    } catch (err) {
      // Error handling is done in updatePrivacySettings
      throw err;
    }
  }, [settings, updatePrivacySettings]);

  /**
   * Reset privacy settings to defaults
   */
  const resetToDefaults = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await apiClient.post<GDPRApiResponse<{ settings: PrivacySettings }>>(
        '/api/gdpr/privacy-settings/reset'
      );

      if (response.data.success && response.data.data) {
        setSettings(response.data.data.settings);
        setHasUnsavedChanges(false);
        toast.success('Privacy settings reset to defaults');
      } else {
        throw new Error(response.data.error || 'Failed to reset privacy settings');
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to reset privacy settings';
      setError(errorMessage);
      console.error('Error resetting privacy settings:', err);
      toast.error(errorMessage);
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Get privacy compliance score
   */
  const getComplianceScore = useCallback(() => {
    if (!settings) return 0;

    const weights = {
      dataProcessingConsent: 30, // Most important
      marketingConsent: 15,
      analyticsConsent: 15,
      profileVisibility: 10,
      dataRetentionOptOut: 15,
      thirdPartySharing: 15
    };

    let score = 0;
    let totalWeight = 0;

    Object.entries(weights).forEach(([key, weight]) => {
      const value = settings[key as keyof PrivacySettings];
      totalWeight += weight;

      // For boolean settings, true = compliant
      if (typeof value === 'boolean') {
        if (key === 'dataRetentionOptOut' || key === 'thirdPartySharing') {
          // These are opt-out settings, so false = more compliant
          score += value ? 0 : weight;
        } else {
          // These are opt-in settings, so true = more compliant
          score += value ? weight : 0;
        }
      }
    });

    return Math.round((score / totalWeight) * 100);
  }, [settings]);

  /**
   * Get compliance recommendations
   */
  const getComplianceRecommendations = useCallback(() => {
    if (!settings) return [];

    const recommendations: string[] = [];

    if (!settings.dataProcessingConsent) {
      recommendations.push('Consider providing explicit consent for data processing to ensure compliance.');
    }

    if (settings.thirdPartySharing) {
      recommendations.push('Review third-party data sharing settings to minimize privacy risks.');
    }

    if (!settings.dataRetentionOptOut && settings.profileVisibility) {
      recommendations.push('Consider opting out of extended data retention for better privacy.');
    }

    if (!settings.analyticsConsent && !settings.marketingConsent) {
      recommendations.push('You have opted out of analytics and marketing. This maximizes privacy but may limit personalized features.');
    }

    if (recommendations.length === 0) {
      recommendations.push('Your privacy settings are well-configured for GDPR compliance.');
    }

    return recommendations;
  }, [settings]);

  /**
   * Check if settings have changed from saved state
   */
  const checkForChanges = useCallback((formData: PrivacySettingsFormData) => {
    if (!settings) return false;

    const hasChanges = Object.keys(formData).some(key => {
      const formKey = key as keyof PrivacySettingsFormData;
      return formData[formKey] !== settings[formKey];
    });

    setHasUnsavedChanges(hasChanges);
    return hasChanges;
  }, [settings]);

  /**
   * Get setting description for UI
   */
  const getSettingDescription = useCallback((key: keyof PrivacySettings) => {
    const descriptions = {
      dataProcessingConsent: 'Allow processing of your personal data for core platform functionality',
      marketingConsent: 'Receive marketing communications and promotional content',
      analyticsConsent: 'Allow collection of usage analytics to improve our services',
      profileVisibility: 'Make your profile visible to other users within your organization',
      dataRetentionOptOut: 'Opt out of extended data retention beyond legal requirements',
      thirdPartySharing: 'Allow sharing of anonymized data with trusted third-party partners'
    };

    return descriptions[key] || '';
  }, []);

  /**
   * Get setting impact level for UI
   */
  const getSettingImpact = useCallback((key: keyof PrivacySettings) => {
    const impacts = {
      dataProcessingConsent: 'high', // Core functionality
      marketingConsent: 'low',
      analyticsConsent: 'medium',
      profileVisibility: 'medium',
      dataRetentionOptOut: 'medium',
      thirdPartySharing: 'low'
    };

    return impacts[key] || 'low';
  }, []);

  /**
   * Export privacy settings
   */
  const exportSettings = useCallback(() => {
    if (!settings) return null;

    const exportData = {
      ...settings,
      exportedAt: new Date().toISOString(),
      complianceScore: getComplianceScore(),
      recommendations: getComplianceRecommendations()
    };

    const blob = new Blob([JSON.stringify(exportData, null, 2)], {
      type: 'application/json'
    });

    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `privacy-settings-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    window.URL.revokeObjectURL(url);

    toast.success('Privacy settings exported successfully');
  }, [settings, getComplianceScore, getComplianceRecommendations]);

  /**
   * Refresh settings
   */
  const refreshSettings = useCallback(async () => {
    await fetchPrivacySettings();
  }, [fetchPrivacySettings]);

  // Load privacy settings on mount and when user changes
  useEffect(() => {
    if (user) {
      fetchPrivacySettings();
    } else {
      setSettings(null);
      setError(null);
      setHasUnsavedChanges(false);
    }
  }, [user, fetchPrivacySettings]);

  // Warn user about unsaved changes before leaving
  useEffect(() => {
    const handleBeforeUnload = (e: BeforeUnloadEvent) => {
      if (hasUnsavedChanges) {
        e.preventDefault();
        e.returnValue = 'You have unsaved changes. Are you sure you want to leave?';
      }
    };

    if (hasUnsavedChanges) {
      window.addEventListener('beforeunload', handleBeforeUnload);
    }

    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, [hasUnsavedChanges]);

  return {
    settings,
    loading,
    error,
    hasUnsavedChanges,
    updatePrivacySettings,
    updateSingleSetting,
    resetToDefaults,
    getComplianceScore,
    getComplianceRecommendations,
    checkForChanges,
    getSettingDescription,
    getSettingImpact,
    exportSettings,
    refreshSettings
  };
};

export default usePrivacySettings;

import React, { useState, useCallback } from 'react';

interface UseSelectionResult<T extends string | number> {
  selectedIds: T[];
  isSelected: (id: T) => boolean;
  toggleSelect: (id: T) => void;
  toggleSelected: (id: T) => void;
  selectAll: (ids: T[]) => void;
  deselectAll: () => void;
  toggleSelectAll: (ids: T[]) => void;
}

/**
 * A custom hook for managing selection in lists and tables
 */
function useSelection<T extends string | number>(): UseSelectionResult<T> {
  const [selectedIds, setSelectedIds] = useState<T[]>([]);

  // Ensure we're working with an array to prevent "includes is not a function" errors
  const isSelectedArray = useCallback((id: T, arr: T[]): boolean => {
    return Array.isArray(arr) ? arr.includes(id) : false;
  }, []);

  const isSelected = useCallback((id: T) => isSelectedArray(id, selectedIds), [selectedIds, isSelectedArray]);

  const toggleSelect = useCallback((id: T) => {
    setSelectedIds(current => 
      isSelectedArray(id, current)
        ? current.filter(itemId => itemId !== id) 
        : [...current, id]
    );
  }, [isSelectedArray]);

  const selectAll = useCallback((ids: T[]) => {
    setSelectedIds(Array.isArray(ids) ? ids : []);
  }, []);

  const deselectAll = useCallback(() => {
    setSelectedIds([]);
  }, []);

  const toggleSelectAll = useCallback((ids: T[]) => {
    setSelectedIds(current => 
      Array.isArray(current) && current.length === ids.length ? [] : (Array.isArray(ids) ? ids : [])
    );
  }, []);

  return {
    selectedIds,
    isSelected,
    toggleSelect,
    toggleSelected: toggleSelect,
    selectAll,
    deselectAll,
    toggleSelectAll,
  };
}

export default useSelection;

import React, { useState, useMemo, useCallback } from 'react';
import { SortDirection } from '../components/shared/tables/SortableColumn';

export interface UseSortingOptions<T> {
  data: T[];
  defaultSortKey?: string | null;
  defaultDirection?: SortDirection;
}

export interface UseSortingReturn<T> {
  sortedData: T[];
  sortKey: string | null;
  sortDirection: SortDirection;
  setSorting: (key: string, direction: SortDirection) => void;
}

type SortValueGetter<T> = (item: T, key: string) => unknown;

export function useSorting<T>({
  data,
  defaultSortKey = null,
  defaultDirection = null,
}: UseSortingOptions<T>): UseSortingReturn<T> {
  const [sortKey, setSortKey] = useState<string | null>(defaultSortKey);
  const [sortDirection, setSortDirection] = useState<SortDirection>(defaultDirection);

  // Helper per ottenere il valore da una chiave (supporta dot notation)
  const getValue: SortValueGetter<T> = useCallback((item: T, key: string) => {
    if (!key) return null;

    // Supporto per dot notation (es. "user.name")
    const keys = key.split('.');
    let current: unknown = item;

    for (const k of keys) {
      if (current && typeof current === 'object' && current !== null) {
        current = (current as Record<string, unknown>)[k];
      } else {
        return null;
      }
    }

    return current;
  }, []);

  // Ordina i dati
  const sortedData = useMemo(() => {
    if (!sortKey || !sortDirection) {
      return [...data];
    }

    return [...data].sort((a, b) => {
      const valueA = getValue(a, sortKey);
      const valueB = getValue(b, sortKey);

      // Gestione NULL/undefined
      if (valueA === null || valueA === undefined) return sortDirection === 'asc' ? -1 : 1;
      if (valueB === null || valueB === undefined) return sortDirection === 'asc' ? 1 : -1;

      // Confronto stringhe (case insensitive)
      if (typeof valueA === 'string' && typeof valueB === 'string') {
        return sortDirection === 'asc'
          ? valueA.localeCompare(valueB, undefined, { sensitivity: 'base' })
          : valueB.localeCompare(valueA, undefined, { sensitivity: 'base' });
      }

      // Confronto numeri
      if (typeof valueA === 'number' && typeof valueB === 'number') {
        return sortDirection === 'asc' ? valueA - valueB : valueB - valueA;
      }

      // Confronto date
      if (valueA instanceof Date && valueB instanceof Date) {
        return sortDirection === 'asc' 
          ? valueA.getTime() - valueB.getTime() 
          : valueB.getTime() - valueA.getTime();
      }

      // Confronto booleani
      if (typeof valueA === 'boolean' && typeof valueB === 'boolean') {
        const numA = valueA ? 1 : 0;
        const numB = valueB ? 1 : 0;
        return sortDirection === 'asc' ? numA - numB : numB - numA;
      }

      // Default: converti a stringa e confronta
      const strA = String(valueA);
      const strB = String(valueB);
      return sortDirection === 'asc' 
        ? strA.localeCompare(strB) 
        : strB.localeCompare(strA);
    });
  }, [data, sortKey, sortDirection, getValue]);

  // Handler per cambiare ordinamento
  const setSorting = useCallback((key: string, direction: SortDirection) => {
    setSortKey(direction === null ? null : key);
    setSortDirection(direction);
  }, []);

  return {
    sortedData,
    sortKey,
    sortDirection,
    setSorting
  };
}

export default useSorting;

import React, { useState, useEffect } from 'react';
import { apiGet, apiDelete } from '../services/api';
import { Template } from '../types/template';

interface UseTemplatesReturn {
  templates: Template[];
  loading: boolean;
  error: string | null;
  success: string | null;
  fetchTemplates: () => Promise<void>;
  setAsDefault: (id: string, type: string) => Promise<void>;
  removeTemplate: (id: string) => Promise<void>;
  createTemplate: (templateData: Partial<Template>) => Promise<void>;
  updateTemplate: (id: string, templateData: Partial<Template>) => Promise<void>;
}

export const useTemplates = (): UseTemplatesReturn => {
  const [templates, setTemplates] = useState<Template[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);

  const fetchTemplates = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await apiGet<Template[]>('/template-links');
      setTemplates(data || []);
    } catch (err) {
      setError('Errore nel recupero dei template');
      console.error('Error fetching templates:', err);
    } finally {
      setLoading(false);
    }
  };

  const setAsDefault = async (id: string, type: string) => {
    try {
      setLoading(true);
      setError(null);
      await apiPut(`/template-links/${id}`, { isDefault: true, type });
      setSuccess('Template impostato come predefinito');

      // Resetta il messaggio di successo dopo 3 secondi
      setTimeout(() => setSuccess(null), 3000);

      // Aggiorna la lista dei template
      await fetchTemplates();
    } catch (err) {
      setError('Errore nell\'impostare il template come predefinito');
      console.error('Error setting template as default:', err);
    } finally {
      setLoading(false);
    }
  };

  const removeTemplate = async (id: string) => {
    if (window.confirm('Sei sicuro di voler eliminare questo template?')) {
      try {
        setLoading(true);
        setError(null);
        await apiDelete(`/template-links/${id}`);
        setSuccess('Template eliminato con successo');

        // Resetta il messaggio di successo dopo 3 secondi
        setTimeout(() => setSuccess(null), 3000);

        // Aggiorna la lista dei template
        await fetchTemplates();
      } catch (err) {
        setError('Errore nell\'eliminazione del template');
        console.error('Error deleting template:', err);
      } finally {
        setLoading(false);
      }
    }
  };

  const createTemplate = async (templateData: Partial<Template>) => {
    try {
      setLoading(true);
      setError(null);
      await apiPost('/template-links', templateData);
      setSuccess('Nuovo template creato con successo');

      // Resetta il messaggio di successo dopo 3 secondi
      setTimeout(() => setSuccess(null), 3000);

      // Aggiorna la lista dei template
      await fetchTemplates();
    } catch (err) {
      setError('Errore nel salvataggio del template');
      console.error('Errore nel salvataggio del template:', err);
    } finally {
      setLoading(false);
    }
  };

  const updateTemplate = async (id: string, templateData: Partial<Template>) => {
    try {
      setLoading(true);
      setError(null);
      await apiPut(`/template-links/${id}`, templateData);
      setSuccess('Template aggiornato con successo');

      // Resetta il messaggio di successo dopo 3 secondi
      setTimeout(() => setSuccess(null), 3000);

      // Aggiorna la lista dei template
      await fetchTemplates();
    } catch (err) {
      setError('Errore nell\'aggiornamento del template');
      console.error('Errore nell\'aggiornamento del template:', err);
    } finally {
      setLoading(false);
    }
  };

  // Carica i template al primo rendering
  useEffect(() => {
    fetchTemplates();
  }, []);

  return {
    templates,
    loading,
    error,
    success,
    fetchTemplates,
    setAsDefault,
    removeTemplate,
    createTemplate,
    updateTemplate
  };
};

import React, { useState } from 'react';
import { X } from 'lucide-react';

export interface Toast {
  id: string;
  title: string;
  description: string;
  status: 'success' | 'error' | 'warning' | 'info';
}

export interface ToastOptions {
  title: string;
  description: string;
  status?: 'success' | 'error' | 'warning' | 'info';
  duration?: number;
  isClosable?: boolean;
}

interface ToastProps {
  title: string;
  description: string;
  status: 'success' | 'error' | 'warning' | 'info';
  onClose: () => void;
}

// Custom Toast notification component
const ToastComponent: React.FC<ToastProps> = ({ title, description, status, onClose }) => {
  const bgColor = status === 'success' ? 'bg-green-100 border-green-500' : 
                 status === 'error' ? 'bg-red-100 border-red-500' : 
                 status === 'warning' ? 'bg-yellow-100 border-yellow-500' : 'bg-blue-100 border-blue-500';

  const textColor = status === 'success' ? 'text-green-800' : 
                   status === 'error' ? 'text-red-800' : 
                   status === 'warning' ? 'text-yellow-800' : 'text-blue-800';

  React.useEffect(() => {
    const timer = setTimeout(() => {
      onClose();
    }, 5000);

    return () => clearTimeout(timer);
  }, [onClose]);

  return (
    <div className={`fixed top-4 right-4 z-50 p-4 rounded shadow-lg border-l-4 ${bgColor} ${textColor} max-w-md`}>
      <div className="flex justify-between items-center">
        <h3 className="font-bold">{title}</h3>
        <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
          <X size={16} />
        </button>
      </div>
      <p className="mt-1">{description}</p>
    </div>
  );
};

export const useToast = () => {
  const [toasts, setToasts] = useState<Toast[]>([]);

  const toast = (options: ToastOptions) => {
    const { title, description, status = 'info', duration = 5000 } = options;
    const id = Math.random().toString(36).substring(2, 11);
    const newToast: Toast = { id, title, description, status };

    setToasts((prev) => [...prev, newToast]);

    setTimeout(() => {
      setToasts((prev) => prev.filter((t) => t.id !== id));
    }, duration);
  };

  const closeToast = (id: string) => {
    setToasts((prev) => prev.filter((t) => t.id !== id));
  };

  const ToastContainer: React.FC = () => (
    <div className="toast-container">
      {toasts.map((t) => (
        <ToastComponent
          key={t.id}
          title={t.title}
          description={t.description}
          status={t.status}
          onClose={() => closeToast(t.id)}
        />
      ))}
    </div>
  );

  return { toast, ToastContainer };
};

import React, { useState, useEffect } from 'react';
import { getTrainers, createTrainer, updateTrainer, Trainer } from '../services/trainers';
import { useQuery } from '@tanstack/react-query';

export interface TrainerInsert {
  firstName: string;
  lastName: string;
  email: string;
  phone?: string;
  specialization?: string;
  specialties?: string[];
  certifications?: string[];
  status?: 'Active' | 'Inactive';
}

export interface TrainerUpdate {
  firstName?: string;
  lastName?: string;
  email?: string;
  phone?: string;
  specialization?: string;
  specialties?: string[];
  certifications?: string[];
  status?: 'Active' | 'Inactive';
}

export function useTrainers() {
  const [trainers, setTrainers] = useState<Trainer[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadTrainers();
  }, []);

  async function loadTrainers() {
    try {
      setLoading(true);
      setError(null);
      const data = await getTrainers();

      const processedData = data.map(trainer => ({
        ...trainer,
        specialties: trainer.specialties || [],
        certifications: trainer.certifications || [],
        status: trainer.status || 'Active'
      }));

      setTrainers(processedData);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to load trainers');
    } finally {
      setLoading(false);
    }
  }

  async function addTrainer(trainer: TrainerInsert) {
    try {
      setError(null);
      const newTrainer = await createTrainer(trainer);
      setTrainers(prev => [...prev, newTrainer]);
      return newTrainer;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to create trainer');
      throw err;
    }
  }

  async function editTrainer(id: string, trainer: TrainerUpdate) {
    try {
      setError(null);
      const updatedTrainer = await updateTrainer(id, trainer);
      setTrainers(prev => prev.map(t => t.id === id ? updatedTrainer : t));
      return updatedTrainer;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update trainer');
      throw err;
    }
  }

  return {
    trainers,
    loading,
    error,
    addTrainer,
    editTrainer,
    refresh: loadTrainers
  };
}

export default useTrainers;

import React, { useState, useEffect } from 'react';

type ViewMode = 'table' | 'grid';

export interface UseViewModeOptions {
  storageKey: string;
  defaultMode?: ViewMode;
}

export interface UseViewModeReturn {
  viewMode: ViewMode;
  setViewMode: (mode: ViewMode) => void;
  toggleViewMode: () => void;
}

export function useViewMode({ 
  storageKey, 
  defaultMode = 'table' 
}: UseViewModeOptions): UseViewModeReturn {
  const [viewMode, setViewMode] = useState<ViewMode>(() => {
    const savedMode = localStorage.getItem(storageKey) as ViewMode;
    return savedMode || defaultMode;
  });

  useEffect(() => {
    localStorage.setItem(storageKey, viewMode);
  }, [viewMode, storageKey]);

  const toggleViewMode = () => {
    setViewMode(current => current === 'table' ? 'grid' : 'table');
  };

  return {
    viewMode,
    setViewMode,
    toggleViewMode
  };
}

export default useViewMode;

