              <li key={item} className="border-b border-gray-100 pb-3 last:border-0 last:pb-0">
                <div className="flex items-start">
                  <span className="flex items-center justify-center bg-blue-100 rounded-full w-8 h-8 mt-1">
                    <TrendingUp className="h-4 w-4 text-blue-600" />
                  </span>
                  <div className="ml-3">
                    <p className="text-sm font-medium text-gray-800">
                      {item % 2 === 0 
                        ? 'New employee added to Acme Corp' 
                        : 'Course completion updated for John Smith'}
                    </p>
                    <p className="text-xs text-gray-500">2 hours ago</p>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
        <div className="bg-white rounded-2xl shadow p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold text-gray-800">Alerts</h2>
            <a href="#" className="text-sm font-medium text-blue-600 hover:text-blue-800">
              View All
            </a>
          </div>
          <ul className="space-y-4">
            {[1, 2, 3].map((item) => (
              <li key={item} className="border-b border-gray-100 pb-3 last:border-0 last:pb-0">
                <div className="flex items-start">
                  <span className="flex items-center justify-center bg-amber-100 rounded-full w-8 h-8 mt-1">
                    <AlertTriangle className="h-4 w-4 text-amber-600" />
                  </span>
                  <div className="ml-3">
                    <p className="text-sm font-medium text-gray-800">
                      {item === 1 
                        ? '5 certifications expiring this month' 
                        : item === 2 
                          ? '3 employees need to complete required training' 
                          : 'Annual health checks due for Tech Solutions Inc.'}
                    </p>
                    <p className="text-xs text-gray-500">
                      {item === 1 ? 'High priority' : 'Medium priority'}
                    </p>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>

      {showForm && (
        <ScheduleEventModalLazy
          trainings={coursesList.map((c: any) => ({ ...c, title: c.title || c.name }))}
          trainers={trainersList}
          companies={companiesList}
          employees={employeesList}
          existingEvent={undefined}
          initialDate={
            selectedSlot
              ? selectedSlot.start.getFullYear() +
                '-' +
                String(selectedSlot.start.getMonth() + 1).padStart(2, '0') +
                '-' +
                String(selectedSlot.start.getDate()).padStart(2, '0')
              : undefined
          }
          initialTime={selectedSlot
            ? selectedSlot.isAllDay
              ? { start: '09:00', end: '13:00' }
              : {
                  start: selectedSlot.start.toTimeString().slice(0, 5),
                  end: selectedSlot.end.toTimeString().slice(0, 5),
                }
            : undefined}
          onClose={() => {
            setShowForm(false);
            setSelectedSlot(null);
          }}
          onSuccess={() => {
            setShowForm(false);
            setSelectedSlot(null);
            // Optionally refresh calendar data here
          }}
        />
      )}
    </div>
  );
};

  export default Dashboard;

import React from 'react';
import { 
  AlertTriangle,
  Calendar
} from 'lucide-react';
import ScheduleCalendar from '../../components/dashboard/ScheduleCalendar';
import ScheduleEventModalLazy from '../../components/schedules/ScheduleEventModal.lazy';
import { DashboardStats, DashboardCharts, RecentActivity, DashboardAlerts } from './components';
import { useDashboardData, useCalendarEvents, useScheduleCreation } from './hooks';

const DashboardRefactored: React.FC = () => {
  // Custom hooks for data and functionality
  const {
    data,
    counters,
    loading,
    error,
    refreshData
  } = useDashboardData();

  const {
    calendarEvents,
    calendarView,
    setCalendarView,
    futureCoursesCount
  } = useCalendarEvents(data.schedules);

  const {
    showForm,
    selectedSlot,
    error: scheduleError,
    handleCreateSchedule,
    handleSelectSlot,
    closeForm
  } = useScheduleCreation(refreshData);

  // Loading state
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Caricamento dashboard...</p>
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-800 mb-2">Errore nel caricamento</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button
            onClick={refreshData}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
          >
            Riprova
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
          <p className="text-gray-600 mt-2">Panoramica generale del sistema di formazione</p>
        </div>

        {/* Error Alert for Schedule Creation */}
        {scheduleError && (
          <div className="mb-6 bg-red-50 border border-red-200 rounded-lg p-4">
            <div className="flex">
              <AlertTriangle className="h-5 w-5 text-red-400 mr-2 mt-0.5" />
              <div>
                <h3 className="text-sm font-medium text-red-800">Errore</h3>
                <p className="text-sm text-red-700 mt-1">{scheduleError}</p>
              </div>
            </div>
          </div>
        )}

        {/* Stats Cards */}
        <div className="mb-8">
          <DashboardStats 
            counters={counters} 
            futureCoursesCount={futureCoursesCount} 
          />
        </div>

        {/* Calendar Section */}
        <div className="mb-8">
          <div className="bg-white rounded-2xl shadow p-6">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-xl font-semibold text-gray-800">Calendario Corsi</h2>
              <div className="flex space-x-2">
                {(['month', 'week', 'day'] as const).map((view) => (
                  <button
                    key={view}
                    onClick={() => setCalendarView(view)}
                    className={`px-3 py-1 rounded-md text-sm font-medium transition-colors ${
                      calendarView === view
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    {view === 'month' ? 'Mese' : view === 'week' ? 'Settimana' : 'Giorno'}
                  </button>
                ))}
              </div>
            </div>

            <ScheduleCalendar
              events={calendarEvents}
              onSelectSlot={handleSelectSlot}
              view={calendarView}
              onViewChange={setCalendarView}
            />
          </div>
        </div>

        {/* Charts Section */}
        <div className="mb-8">
          <DashboardCharts />
        </div>

        {/* Bottom Section: Recent Activity and Alerts */}
        <div className="grid gap-6 grid-cols-1 lg:grid-cols-2">
          <RecentActivity />
          <DashboardAlerts />
        </div>

        {/* Schedule Creation Modal */}
        {showForm && selectedSlot && (
          <ScheduleEventModalLazy
            isOpen={showForm}
            onClose={closeForm}
            onSave={handleCreateSchedule}
            trainings={data.courses.map((c: any) => ({ ...c, title: c.title || c.name }))}
            trainers={data.trainers}
            companies={data.companies}
            employees={data.employees}
            existingEvent={undefined}
            initialDate={
              selectedSlot
                ? selectedSlot.start.getFullYear() +
                  '-' +
                  String(selectedSlot.start.getMonth() + 1).padStart(2, '0') +
                  '-' +
                  String(selectedSlot.start.getDate()).padStart(2, '0')
                : undefined
            }
            initialStartTime={
              selectedSlot && !selectedSlot.isAllDay
                ? String(selectedSlot.start.getHours()).padStart(2, '0') +
                  ':' +
                  String(selectedSlot.start.getMinutes()).padStart(2, '0')
                : undefined
            }
            initialEndTime={
              selectedSlot && !selectedSlot.isAllDay
                ? String(selectedSlot.end.getHours()).padStart(2, '0') +
                  ':' +
                  String(selectedSlot.end.getMinutes()).padStart(2, '0')
                : undefined
            }
          />
        )}
      </div>
    </div>
  );
};

export default DashboardRefactored;

