import { useState, useEffect, useMemo } from 'react';

export interface ScheduleEvent {
  id: string;
  scheduleId: string;
  title: string;
  start: Date;
  end: Date;
  allDay?: boolean;
  resource: any;
  status: string;
  tooltip: string;
  sessioniTooltipHtml: string;
}

export type CalendarView = 'month' | 'week' | 'day';

export const useCalendarEvents = (schedulesData: any[]) => {
  const [calendarView, setCalendarView] = useState<CalendarView>('month');
  const [calendarEvents, setCalendarEvents] = useState<ScheduleEvent[]>([]);

  // Helper function to combine date and time
  const combineDateAndTime = (date: string, time: string): string => {
    const dateOnly = date.split('T')[0];
    return `${dateOnly}T${time}:00`;
  };

  // Helper: group sessions by scheduleId+date for month view
  const groupSessionsByDay = useMemo(() => (schedules: any[]) => {
    const grouped: ScheduleEvent[] = [];
    schedules.forEach((s: any) => {
      if (Array.isArray(s.sessions) && s.sessions.length > 0) {
        // Raggruppa per giorno
        const sessionsByDay: Record<string, any[]> = {};
        s.sessions.forEach((sess: any) => {
          const day = sess.date.split('T')[0];
          if (!sessionsByDay[day]) sessionsByDay[day] = [];
          sessionsByDay[day].push(sess);
        });

        Object.entries(sessionsByDay).forEach(([day, sessions]) => {
          // Tooltip con tutte le sessioni (HTML)
          const allSessions = s.sessions;
          const sessioniTooltipHtml = allSessions.map((ss: any, i: number) => {
            const dateStr = new Date(ss.date).toLocaleDateString('it-IT');
            const orario = `${ss.start || '--:--'} - ${ss.end || '--:--'}`;
            const trainer = ss.trainer ? ` (${ss.trainer.firstName} ${ss.trainer.lastName})` : '';
            return `<span style='color:#2563eb;font-weight:700'>Sessione ${i+1}: ${dateStr}, ${orario}${trainer}</span>`;
          }).join('<br>');

          // Aziende senza duplicati
          const aziende = [...new Set((s.companies || []).map((c: any) => c.company?.ragioneSociale || c.company?.name))].join(', ');

          // Orari del giorno
          const sortedSessions = [...sessions].sort((a, b) => a.start.localeCompare(b.start));
          const start = combineDateAndTime(day, sortedSessions[0].start);
          const end = combineDateAndTime(day, sortedSessions[sessions.length-1].end);

          // Titolo: aggiungi " - Sessione X" solo se ci sono sessioni in giorni diversi
          let title = `${s.course?.title || s.course?.name || 'Corso'}`;
          const allSameDay = allSessions.every((ss: any) => ss.date.split('T')[0] === day);
          if (!allSameDay && allSessions.length > 1) {
            // Trova la sessione corrispondente a questo giorno
            const idx = allSessions.findIndex((ss: any) => ss.date.split('T')[0] === day);
            if (idx !== -1) title += ` - Sessione ${idx + 1}`;
          }

          grouped.push({
            id: s.id + '-' + day,
            scheduleId: s.id,
            title,
            start: new Date(start),
            end: new Date(end),
            resource: s,
            status: s.status,
            tooltip: `Corso: ${s.course?.title || s.course?.name}\nAziende: ${aziende}\nLuogo: ${s.location || '-'}\nData: ${new Date(day).toLocaleDateString('it-IT')}\nStato: ${s.status}`,
            sessioniTooltipHtml
          });
        });
      } else {
        // fallback: usa startDate/endDate della schedule
        const aziende = [...new Set((s.companies || []).map((c: any) => c.company?.ragioneSociale || c.company?.name))].join(', ');
        grouped.push({
          id: s.id,
          scheduleId: s.id,
          title: s.course?.title || s.course?.name || 'Corso',
          start: new Date(s.startDate),
          end: new Date(s.endDate),
          resource: s,
          status: s.status,
          tooltip: `Corso: ${s.course?.title || s.course?.name}\nAziende: ${aziende}\nLuogo: ${s.location || '-'}\nData: ${s.startDate ? new Date(s.startDate).toLocaleDateString('it-IT') : '-'}\nOrario: --:--\nStato: ${s.status}`,
          sessioniTooltipHtml: ''
        });
      }
    });
    return grouped;
  }, [combineDateAndTime]);

  // Helper: eventi separati per sessione (settimana/giorno)
  const mapSessionsIndividually = useMemo(() => (schedules: any[]) => {
    return schedules.flatMap((s: any) => {
      if (Array.isArray(s.sessions) && s.sessions.length > 0) {
        return s.sessions.map((sess: any, idx: number) => {
          const start = new Date(combineDateAndTime(sess.date, sess.start));
          const end = new Date(combineDateAndTime(sess.date, sess.end));
          const allSessions = s.sessions;
          const allSameDay = allSessions.every((ss: any) => ss.date.split('T')[0] === sess.date.split('T')[0]);

          let sessionTitle = s.course?.title || s.course?.name || 'Corso';
          if (!allSameDay && allSessions.length > 1) {
            const sessionNumber = ` - Sessione ${allSessions.findIndex((ss: any) => ss === sess) + 1}`;
            sessionTitle += sessionNumber;
          }

          const sessioniTooltipHtml = allSessions.map((ss: any, i: number) => {
            const dateStr = new Date(ss.date).toLocaleDateString('it-IT');
            const orario = `${ss.start || '--:--'} - ${ss.end || '--:--'}`;
            const trainer = ss.trainer ? ` (${ss.trainer.firstName} ${ss.trainer.lastName})` : '';
            const isCurrent = allSameDay || ss === sess;
            return `<span style='color:${isCurrent ? '#2563eb' : '#1e293b'};font-weight:${isCurrent ? 700 : 400}'>Sessione ${i+1}: ${dateStr}, ${orario}${trainer}</span>`;
          }).join('<br>');

          const aziende = [...new Set((s.companies || []).map((c: any) => c.company?.ragioneSociale || c.company?.name))].join(', ');

          return {
            id: s.id + '-' + (sess.id || idx),
            scheduleId: s.id,
            title: sessionTitle,
            start,
            end,
            allDay: false,
            resource: s,
            status: s.status,
            tooltip: `Corso: ${s.course?.title || s.course?.name}\nAziende: ${aziende}\nLuogo: ${s.location || '-'}\nData: ${new Date(sess.date).toLocaleDateString('it-IT')}\nStato: ${s.status}`,
            sessioniTooltipHtml
          };
        });
      } else {
        const aziende = [...new Set((s.companies || []).map((c: any) => c.company?.ragioneSociale || c.company?.name))].join(', ');
        return [{
          id: s.id,
          scheduleId: s.id,
          title: s.course?.title || s.course?.name || 'Corso',
          start: new Date(s.startDate),
          end: new Date(s.endDate),
          resource: s,
          status: s.status,
          tooltip: `Corso: ${s.course?.title || s.course?.name}\nAziende: ${aziende}\nLuogo: ${s.location || '-'}\nData: ${s.startDate ? new Date(s.startDate).toLocaleDateString('it-IT') : '-'}\nOrario: --:--\nStato: ${s.status}`,
          sessioniTooltipHtml: ''
        }];
      }
    });
  }, [combineDateAndTime]);

  // Rimappa gli eventi ogni volta che cambia la vista o i dati
  useEffect(() => {
    if (!schedulesData.length) {
      setCalendarEvents([]);
      return;
    }

    // Scegli mapping in base alla vista (default: month)
    let events: ScheduleEvent[];
    if (calendarView === 'month') {
      events = groupSessionsByDay(schedulesData);
    } else {
      events = mapSessionsIndividually(schedulesData);
    }

    setCalendarEvents(events);
  }, [calendarView, schedulesData, groupSessionsByDay, mapSessionsIndividually]);

  // Calculate future courses count
  const futureCoursesCount = useMemo(() => {
    const now = new Date();
    // Raggruppa per scheduleId
    const firstSessionBySchedule: Record<string, ScheduleEvent> = {};
    calendarEvents.forEach(e => {
      if (!e.scheduleId) return;
      if (!firstSessionBySchedule[e.scheduleId] || e.start < firstSessionBySchedule[e.scheduleId].start) {
        firstSessionBySchedule[e.scheduleId] = e;
      }
    });
    return Object.values(firstSessionBySchedule).filter((e) => e.start > now).length;
  }, [calendarEvents]);

  return {
    calendarEvents,
    calendarView,
    setCalendarView,
    futureCoursesCount
  };
};

import { useState, useEffect, useCallback, useRef } from 'react';
import { apiGet } from '../../../services/api';
import { useAuth } from '../../../context/AuthContext';
import { logGdprAction, checkConsent } from '../../../utils/gdpr';
import { dummyData } from '../../../data/dummyData';

export interface DashboardCounters {
  companies: number;
  employees: number;
}

export interface DashboardData {
  courses: any[];
  trainers: any[];
  companies: any[];
  employees: any[];
  schedules: any[];
}

interface DashboardTrainer {
  id: string;
  firstName: string;
  lastName: string;
}

interface DashboardCompany {
  id: string;
  name: string;
  employeeCount?: number;
  ragioneSociale: string;
  sector?: string;
}

export const useDashboardData = () => {
  const { tenant } = useTenant();
  const { hasPermission } = useAuth();

  const [courses, setCourses] = useState<any[]>([]);
  const [trainers, setTrainers] = useState<DashboardTrainer[]>([]);
  const [companies, setCompanies] = useState<DashboardCompany[]>([]);
  const [employees, setEmployees] = useState<any[]>([]);
  const [schedules, setSchedules] = useState<any[]>([]);
  const [counters, setCounters] = useState<DashboardCounters>({ companies: 0, employees: 0 });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [dataSource, setDataSource] = useState<'api' | 'fallback'>('api');
  const mountedRef = useRef(true);
  const fetchingRef = useRef(false);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      mountedRef.current = false;
    };
  }, []);

  const loadFallbackData = useCallback(async () => {
    console.log(' Loading fallback data...');

    try {
      const dummyDataTyped = dummyData as any;

      const transformedTrainers = (dummyDataTyped.employees || []).map((e: any) => ({
        id: e.id,
        firstName: e.firstName || '',
        lastName: e.lastName || ''
      }));

      const transformedCompanies = (dummyDataTyped.companies || []).map((c: any) => ({
        ...c,
        ragioneSociale: c.name || '',
        employeeCount: (dummyDataTyped.employees || []).filter((e: any) => 
          e.companyId === c.id
        ).length,
        sector: c.industry || c.sector || c.type || ''
      }));

      if (mountedRef.current) {
        setCourses(dummyDataTyped.courses || []);
        setTrainers(transformedTrainers);
        setCompanies(transformedCompanies);
        setEmployees(dummyDataTyped.employees || []);
        setSchedules(dummyDataTyped.schedules || []);
        setCounters({
          companies: transformedCompanies.length,
          employees: (dummyDataTyped.employees || []).length
        });
        setDataSource('fallback');
      }

      logGdprAction(
        tenant?.id || 'unknown',
        'DASHBOARD_FALLBACK_DATA_LOADED',
        'dashboard',
        'read',
        {
          reason: 'API error or missing GDPR consent'
        }
      );

    } catch (error) {
      console.error('Failed to load fallback data:', error);
    }
  }, [tenant?.id]);

  const fetchData = useCallback(async () => {
    if (fetchingRef.current || !mountedRef.current) return;

    fetchingRef.current = true;
    setLoading(true);
    setError(null);

    try {
      // Check permissions
      const hasDashboardAccess = hasPermission && (
        hasPermission('dashboard', 'read') || 
        hasPermission('dashboard', 'view') || 
        hasPermission('companies', 'read') ||
        hasPermission('administration', 'view')
      );

      if (!hasDashboardAccess) {
        throw new Error('Permessi insufficienti per accedere al dashboard');
      }

      // Fetch counters
      const countersData = await apiGet<DashboardCounters>('/api/counters');

      // Fetch other data
      const [coursesData, trainersData, schedulesData] = await Promise.allSettled([
        apiGet('/courses').catch(() => []),
        apiGet('/trainers').catch(() => []),
        apiGet('/api/v1/schedules').catch(() => [])
      ]);

      const coursesResult = coursesData.status === 'fulfilled' ? (coursesData.value as any[]) : [];
      const trainersResult = trainersData.status === 'fulfilled' ? (trainersData.value as any[]) : [];
      const schedulesResult = schedulesData.status === 'fulfilled' ? (schedulesData.value as any[]) : [];

      // Transform trainers data
      const transformedTrainers = trainersResult.map((trainer: any) => ({
        id: trainer.id,
        firstName: trainer.firstName || '',
        lastName: trainer.lastName || ''
      }));

      if (mountedRef.current) {
        setCourses(coursesResult);
        setTrainers(transformedTrainers);
        setCompanies([]);
        setEmployees([]);
        setSchedules(schedulesResult);
        setCounters(countersData);
        setDataSource('api');
      }

      logGdprAction(
        tenant?.id || 'unknown',
        'DASHBOARD_DATA_LOADED',
        'dashboard',
        'read',
        {
          dataSource: 'api',
          recordCounts: {
            courses: coursesResult.length,
            trainers: transformedTrainers.length,
            schedules: schedulesResult.length
          }
        }
      );

    } catch (error) {
      console.error(' Error loading dashboard data:', error);

      const errorMessage = error instanceof Error ? error.message : 'Errore di connessione al server';

      logGdprAction(
        tenant?.id || 'unknown',
        'DASHBOARD_DATA_ERROR',
        'dashboard',
        'read',
        {
          errorType: error instanceof Error ? error.constructor.name : 'UnknownError'
        },
        false,
        errorMessage
      );

      if (mountedRef.current) {
        setError(errorMessage);
        await loadFallbackData();
      }
    } finally {
      fetchingRef.current = false;
      if (mountedRef.current) {
        setLoading(false);
      }
    }
  }, [tenant?.id, hasPermission, loadFallbackData]);

  // Initial data load
  useEffect(() => {
    if (mountedRef.current) {
      fetchData();
    }
  }, [fetchData]);

  return {
    courses,
    trainers,
    companies,
    employees,
    schedules,
    counters,
    loading,
    error,
    dataSource,
    refetch: fetchData
  };
};

import { useState, useCallback } from 'react';
import { logGdprAction } from '../../../utils/gdpr';

export interface SelectedSlot {
  start: Date;
  end: Date;
  isAllDay: boolean;
}

export const useScheduleCreation = (onSuccess?: () => void) => {
  const { tenant, hasPermission } = useTenant();
  const [showForm, setShowForm] = useState(false);
  const [selectedSlot, setSelectedSlot] = useState<SelectedSlot | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleCreateSchedule = useCallback(async (data: any) => {
    try {
      setError(null);

      // Check permissions
      if (!hasPermission || !hasPermission('schedules', 'create')) {
        throw new Error('Permessi insufficienti per creare pianificazioni');
      }

      logGdprAction(
        tenant?.id || 'unknown',
        'SCHEDULE_CREATE_ATTEMPT',
        'schedule',
        'create',
        {
          courseId: data.courseId,
          companiesCount: data.companies?.length || 0
        }
      );

      const result = await apiPost('/schedules', data);

      logGdprAction(
        tenant?.id || 'unknown',
        'SCHEDULE_CREATE_SUCCESS',
        'schedule',
        'create',
        {
          scheduleId: result.id,
          courseId: data.courseId
        }
      );

      setShowForm(false);
      setSelectedSlot(null);

      // Call success callback
      if (onSuccess) {
        onSuccess();
      }

    } catch (error) {
      console.error('Error creating schedule:', error);

      logGdprAction(
        tenant?.id || 'unknown',
        'SCHEDULE_CREATE_ERROR',
        'schedule',
        'create',
        {
          errorType: error instanceof Error ? error.constructor.name : 'UnknownError'
        },
        false,
        error instanceof Error ? error.message : 'Unknown error'
      );

      const errorMessage = error instanceof Error 
        ? error.message 
        : 'Errore nella creazione della pianificazione';

      setError(errorMessage);
    }
  }, [tenant, hasPermission, onSuccess]);

  const handleSelectSlot = useCallback((slotInfo: any) => {
    const start = slotInfo.start;
    const end = slotInfo.end;
    const isAllDay = start.getHours() === 0 && start.getMinutes() === 0 && 
                     end.getHours() === 0 && end.getMinutes() === 0;

    setSelectedSlot({ start, end, isAllDay });
    setShowForm(true);
  }, []);

  const closeForm = useCallback(() => {
    setShowForm(false);
    setSelectedSlot(null);
    setError(null);
  }, []);

  return {
    showForm,
    selectedSlot,
    error,
    handleCreateSchedule,
    handleSelectSlot,
    closeForm
  };
};

