import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom';
import { 
  ChevronLeft,
  Edit
} from 'lucide-react';
import EmployeeFormNew from '../../components/employees/EmployeeFormNew';
import { useToast } from '../../hooks/useToast';
import { apiGet } from '../../services/api';
import { Company } from '../../types';

const EmployeeEdit: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { showToast } = useToast();
  const [person, setPerson] = useState<any>(null);
  const [companies, setCompanies] = useState<Company[]>([]);
  const [loading, setLoading] = useState(true);
  const [fetchAttempts, setFetchAttempts] = useState(0);
  const isFetchingRef = useRef(false);
  const dataFetchedRef = useRef(false); // Track if we've already fetched data
  const MAX_RETRY_ATTEMPTS = 3;

  // Fetch employee and companies with improved retry logic
  useEffect(() => {
    if (isFetchingRef.current || dataFetchedRef.current) return;

    const fetchData = async () => {
      if (fetchAttempts >= MAX_RETRY_ATTEMPTS) {
        showToast({
          message: `Failed to load data after ${MAX_RETRY_ATTEMPTS} attempts.`,
          type: 'error'
        });
        setLoading(false);
        return;
      }

      isFetchingRef.current = true;

      try {
        // Fetch companies first
        const companiesData = await apiGet<Company[]>('/api/v1/companies');
        setCompanies(companiesData || []);

        // Fetch person data
        const personData = await apiGet(`/api/v1/persons/${id}`) as any;
        // Ensure companyId is correctly set for compatibility with the form
        if (personData.companyId && !personData.companyId) {
          personData.companyId = personData.companyId;
        }
        setPerson(personData);

        // If we get here, both fetches succeeded
        dataFetchedRef.current = true; // Mark that we've successfully fetched data
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);

        // Increment attempt counter
        const nextAttempt = fetchAttempts + 1;
        setFetchAttempts(nextAttempt);

        if (nextAttempt >= MAX_RETRY_ATTEMPTS) {
          showToast({
            message: `Error: ${error instanceof Error ? error.message : 'Failed to load data'}`,
            type: 'error'
          });
          setLoading(false);

          // Only navigate away on person not found
          if (error instanceof Error && error.message.includes('Person not found')) {
            navigate('/employees');
          }
        } else {
          // Try again after delay with exponential backoff
          setTimeout(() => {
            isFetchingRef.current = false;
            // Force re-render to trigger useEffect again
            setLoading(true);
          }, 1000 * Math.pow(2, nextAttempt - 1));
        }
      } finally {
        isFetchingRef.current = false;
      }
    };

    fetchData();
  }, [id, navigate, showToast, fetchAttempts]);

  const handleSuccess = () => {
    navigate(id ? `/employees/${id}` : '/employees');
  };

  const handleClose = () => {
    navigate(id ? `/employees/${id}` : '/employees');
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-full p-8">
        <div className="flex flex-col items-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
          <p className="mt-4 text-gray-600">
            {fetchAttempts > 0 ? `Loading data (attempt ${fetchAttempts + 1}/${MAX_RETRY_ATTEMPTS})...` : 'Loading data...'}
          </p>
        </div>
      </div>
    );
  }

  if (id && !person) {
    return (
      <div className="flex flex-col items-center justify-center h-80">
        <p className="text-red-500 mb-4">Persona non trovata</p>
        <Link 
          to="/employees" 
          className="inline-flex items-center text-sm text-blue-600 hover:text-blue-800"
        >
          <ChevronLeft className="h-4 w-4 mr-1" />
          Torna all'elenco persone
        </Link>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8 max-w-5xl">
      <div className="mb-4">
        <Link 
          to="/employees" 
          className="inline-flex items-center text-sm text-blue-600 hover:text-blue-800"
        >
          <ChevronLeft className="h-4 w-4 mr-1" />
          Torna all'elenco persone
        </Link>
      </div>

      <EmployeeFormNew
        person={person}
        companies={companies}
        onSuccess={handleSuccess}
        onClose={handleClose}
      />
    </div>
  );
};

export default EmployeeEdit;

import React, { useState, useEffect, useRef } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import { Button } from '../../design-system/atoms/Button';
import { SearchBar } from '../../design-system/molecules';
import { SearchBarControls } from '../../design-system/molecules/SearchBarControls';
import ResizableTable from '../../components/shared/ResizableTable';
import { sanitizeErrorMessage } from '../../utils/errorUtils';
import { Company } from '../../types';

interface Invoice {
  id: string;
  number: string;
  date: string;
  company?: Company;
  total: number;
  status: 'draft' | 'issued' | 'paid' | 'overdue';
  payment_date?: string;
  due_date: string;
}

// DropdownMenu component
const DropdownMenu: React.FC<{ children: (close: () => void) => React.ReactNode; trigger: React.ReactNode }> = ({ children, trigger }) => {
  const [open, setOpen] = useState(false);
  const ref = useRef<HTMLDivElement>(null);
  const menuRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleClick = (e: MouseEvent) => {
      if (
        ref.current && !ref.current.contains(e.target as Node) &&
        menuRef.current && !menuRef.current.contains(e.target as Node)
      ) {
        setOpen(false);
      }
    };
    if (open) document.addEventListener('mousedown', handleClick);
    return () => document.removeEventListener('mousedown', handleClick);
  }, [open]);

  // Portal rendering
  const [menuPos, setMenuPos] = useState<{top: number, left: number} | null>(null);
  const triggerRef = useRef<HTMLSpanElement>(null);

  useEffect(() => {
    if (open && triggerRef.current) {
      const rect = triggerRef.current.getBoundingClientRect();
      setMenuPos({
        top: rect.bottom + window.scrollY,
        left: rect.left + window.scrollX
      });
    }
  }, [open]);

  return (
    <div ref={ref} className="relative inline-block text-left" style={{overflow: 'visible'}}>
      <span ref={triggerRef} onClick={() => setOpen(o => !o)}>{trigger}</span>
      {open && menuPos && ReactDOM.createPortal(
        <div ref={menuRef} style={{ position: 'absolute', zIndex: 99999, left: menuPos.left, top: menuPos.top, background: '#fffbe6', minWidth: '8rem', boxShadow: '0 8px 24px rgba(0,0,0,0.18)' }} className="w-32 bg-white border border-gray-200 rounded shadow-lg">
          {children(() => setOpen(false))}
        </div>,
        document.body
      )}
    </div>
  );
};

interface InvoicesProps {
  searchTerm: string;
  setSearchTerm: (term: string) => void;
  activeFilters: Record<string, string>;
  setActiveFilters: (filters: Record<string, string>) => void;
  activeSort: { field: string, direction: 'asc' | 'desc' };
  setActiveSort: (sort: { field: string, direction: 'asc' | 'desc' }) => void;
  selectionMode: boolean;
  setSelectionMode: (mode: boolean) => void;
  selectedIds: string[];
  setSelectedIds: (ids: string[]) => void;
}

const Invoices: React.FC<InvoicesProps> = ({
  searchTerm,
  setSearchTerm,
  activeFilters,
  setActiveFilters,
  activeSort,
  setActiveSort,
  selectionMode,
  setSelectionMode,
  selectedIds,
  setSelectedIds
}) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [invoices, setInvoices] = useState<Invoice[]>([]);

  useEffect(() => {
    fetchInvoices();
  }, []);

  const fetchInvoices = async () => {
    try {
      setLoading(true);
      // In futuro qui verr fatta una chiamata API reale
      // const response = await axios.get<Invoice[]>('/api/invoices');
      // setInvoices(response.data);
      setInvoices([]);
      setError(null);
    } catch (err) {
      setError('Errore nel caricamento delle fatture');
      console.error('Error fetching invoices:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteInvoice = async (id: string, close?: () => void) => {
    try {
      // In futuro qui verr fatta una chiamata API reale
      // const res = await axios.delete('/api/invoices/${id}');
