    name: '',
    description: '',
    type: 'CUSTOM',
    isPublic: false,
    allowAnonymous: false,
    fields: [],
    successMessage: '',
    redirectUrl: ''
  });

  useEffect(() => {
    if (id) {
      loadFormTemplate();
    }
  }, [id]);

