    if (!id) return;

    try {
      setInitialLoading(true);
      const template = await formTemplatesService.getFormTemplate(id);
      setFormData({
        name: template.name,
        description: template.description || '',
        type: 'CUSTOM', // Assumiamo CUSTOM per ora
