  Mail,
  Phone,
  Plus,
  Shield,
  User
} from 'lucide-react';
import { 
  Person, 
  FilterConfig, 
  getRoleDisplayName, 
  getActiveRoles,
  getHighestRole 
} from '../../services/roleHierarchyService';
import { usePersonFilters, useAllPersons, useAllPersonsForImport } from '../../hooks/usePersonFilters';
import { useCompanies } from '../../hooks/useCompanies';
