// Componente per gruppo di campi riutilizzabile
interface FieldGroupProps {
  fields: Array<{
    name: string;
    label: string;
    type: 'text' | 'textarea' | 'email';
    value: string;
    onChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => void;
    disabled?: boolean;
    error?: string;
    rows?: number;
    required?: boolean;
  }>;
}

const FieldGroup: React.FC<FieldGroupProps> = ({ fields }) => (
  <>
    {fields.map((field) => (
      <FormField
        key={field.name}
        name={field.name}
        label={field.label}
        type={field.type}
        value={field.value}
        onChange={field.onChange}
        disabled={field.disabled}
        error={field.error}
        rows={field.rows}
        required={field.required}
      />
    ))}
  </>
);

// Hook per validazione form ottimizzato
const useFormValidation = (data: CMSData) => {
  const [errors, setErrors] = useState<Record<string, string>>({});

  const validateField = (path: string, value: string) => {
    const newErrors = { ...errors };

    if (!value.trim()) {
      newErrors[path] = 'Questo campo  obbligatorio';
    } else {
      delete newErrors[path];
    }

    setErrors(newErrors);
    return !newErrors[path];
  };

  const validateAll = () => {
    const newErrors: Record<string, string> = {};

    // Validazione campi obbligatori
    const requiredFields = [
      'homepage.heroTitle',
      'homepage.heroSubtitle',
      'services.heroTitle',
      'contacts.heroTitle',
      'careers.heroTitle',
      'company.heroTitle'
    ];

    requiredFields.forEach(field => {
      const value = field.split('.').reduce((obj, key) => obj[key], data as any);
      if (!value?.trim()) {
        newErrors[field] = 'Questo campo  obbligatorio';
      }
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  return { errors, validateField, validateAll, setErrors };
};

