import React, { useState, useEffect } from 'react';
import { Shield, CheckCircle, AlertCircle, AlertTriangle, Save } from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import { useRoles, type Role } from '../../hooks/useRoles';
import { useTenants } from '../../hooks/useTenants';
import OptimizedPermissionManager from '../../components/roles/OptimizedPermissionManager';

const RolesTab: React.FC = () => {
  const { hasPermission } = useAuth();
  const { roles, selectedRole, loading: rolesLoading, loadRoles, selectRole, deleteRole, setSelectedRole } = useRoles();
  const { tenants, loadTenants } = useTenants();
  const [message, setMessage] = useState<{ type: 'success' | 'error'; text: string } | null>(null);
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const [isChangingRole, setIsChangingRole] = useState(false);
  const [pendingRoleChange, setPendingRoleChange] = useState<Role | null>(null);
  const [showUnsavedChangesModal, setShowUnsavedChangesModal] = useState(false);
  // Carica ruoli e tenants all'avvio (solo una volta)
  useEffect(() => {
    console.log(' RolesTab: Initial load');
    loadRoles();
    loadTenants();
  }, []); // Rimuovo le dipendenze per evitare loop

  // Ricarica i ruoli quando la tab diventa visibile (con debouncing)
  useEffect(() => {
    let timeoutId: NodeJS.Timeout;

    const handleVisibilityChange = () => {
      if (!document.hidden && roles.length > 0) {
        // Debounce per evitare troppe richieste
        clearTimeout(timeoutId);
        timeoutId = setTimeout(() => {
          console.log(' RolesTab: Tab became visible, refreshing data');
          loadRoles(true); // Force refresh
        }, 500);
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
      clearTimeout(timeoutId);
    };
  }, [roles.length]); // Solo roles.length come dipendenza

  // Mostra messaggio temporaneo
  const showMessage = (type: 'success' | 'error', text: string) => {
    setMessage({ type, text });
    setTimeout(() => setMessage(null), 5000);
  };



  // Gestione cambio ruolo con controllo modifiche non salvate
  const handleRoleChange = async (role: Role) => {
    if (hasUnsavedChanges && selectedRole) {
      setPendingRoleChange(role);
      setShowUnsavedChangesModal(true);
      return;
    }

    await performRoleChange(role);
  };

  // Esegue il cambio ruolo
  const performRoleChange = async (role: Role | null) => {
    if (!role) return;

    setIsChangingRole(true);
    try {
      await selectRole(role);
      setHasUnsavedChanges(false);
      // Rimosso il messaggio di successo per la selezione del ruolo
    } catch (error) {
      showMessage('error', 'Errore nel cambio ruolo');
    } finally {
      setIsChangingRole(false);
    }
  };

  // Salva e cambia ruolo
  const saveAndChangeRole = async () => {
    // Per ora procediamo direttamente al cambio ruolo
    // In futuro si potr implementare il salvataggio automatico
    if (pendingRoleChange) {
      await performRoleChange(pendingRoleChange);
      setPendingRoleChange(null);
    }
    setShowUnsavedChangesModal(false);
  };

  // Cambia ruolo senza salvare
  const changeRoleWithoutSaving = async () => {
    if (pendingRoleChange) {
      await performRoleChange(pendingRoleChange);
      setPendingRoleChange(null);
    }
    setShowUnsavedChangesModal(false);
  };

  // Annulla cambio ruolo
  const cancelRoleChange = () => {
    setPendingRoleChange(null);
    setShowUnsavedChangesModal(false);
  };

  // Torna alla selezione dei ruoli
  const handleBackToRoleSelection = () => {
    setSelectedRole(null);
    setHasUnsavedChanges(false);
  };

  // Converte il ruolo da useRoles al formato richiesto da OptimizedPermissionManager
  const convertRole = (role: Role | null) => {
    if (!role) return null;
    return {
      ...role,
      description: role.description || '',
      userCount: role.userCount || 0,
      isActive: true
    };
  };

  // Verifica permessi di lettura ruoli
  if (!hasPermission('roles', 'read')) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="text-center p-8">
          <Shield className="w-16 h-16 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-gray-900 mb-2">Accesso negato</h3>
          <p className="text-gray-600">Non hai i permessi necessari per visualizzare i ruoli.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full flex flex-col bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 px-6 py-4 shadow-sm">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <div className="p-3 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl shadow-lg">
              <Shield className="w-6 h-6 text-white" />
            </div>
            <div>
              <div className="flex items-center space-x-3">
                <h1 className="text-2xl font-bold text-gray-900">Gestione Ruoli e Permessi</h1>
                {hasUnsavedChanges && (
                  <div className="flex items-center space-x-1 px-2 py-1 bg-amber-100 border border-amber-300 rounded-lg">
                    <AlertTriangle className="w-4 h-4 text-amber-600" />
                    <span className="text-xs font-medium text-amber-700">Modifiche non salvate</span>
                  </div>
                )}
                {isChangingRole && (
                  <div className="flex items-center space-x-1 px-2 py-1 bg-blue-100 border border-blue-300 rounded-lg">
                    <div className="w-3 h-3 border-2 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
                    <span className="text-xs font-medium text-blue-700">Cambio ruolo...</span>
                  </div>
                )}
              </div>
              <p className="text-sm text-gray-600">Configura ruoli, permessi CRUD, scope tenant e campi specifici</p>
            </div>
          </div>

          {/* Statistiche rapide */}
          <div className="flex items-center space-x-6">
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-600">{roles.length}</div>
              <div className="text-xs text-gray-500">Ruoli</div>
            </div>
            {selectedRole && (
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600">{selectedRole.userCount || 0}</div>
                <div className="text-xs text-gray-500">Utenti</div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Messaggi di stato */}
      {message && (
        <div className={`mx-6 mt-4 p-4 rounded-lg border shadow-sm ${
          message.type === 'success' 
            ? 'bg-green-50 border-green-200 text-green-800' 
            : 'bg-red-50 border-red-200 text-red-800'
        }`}>
          <div className="flex items-center">
            {message.type === 'success' ? (
              <CheckCircle className="w-5 h-5 mr-2" />
            ) : (
              <AlertCircle className="w-5 h-5 mr-2" />
            )}
            <span>{message.text}</span>
          </div>
        </div>
      )}

      {/* Selezione ruolo se nessuno  selezionato */}
      {!selectedRole && !rolesLoading && (
        <div className="mx-6 mt-4 p-6 bg-blue-50 border border-blue-200 rounded-lg">
          <div className="flex items-center space-x-3">
            <Shield className="w-6 h-6 text-blue-600" />
            <div>
              <h3 className="font-semibold text-blue-900">Seleziona un ruolo</h3>
              <p className="text-sm text-blue-700">
                Scegli un ruolo dalla lista per configurare i permessi CRUD, scope tenant e campi specifici
              </p>
            </div>
          </div>

          {/* Lista ruoli rapida */}
          <div className="mt-4 grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3">
            {roles.map((role, index) => (
              <button
                key={role?.type || `role-${index}`}
                onClick={() => role && handleRoleChange(role)}
                disabled={isChangingRole || !role}
                className={`p-3 bg-white border border-blue-200 rounded-lg hover:bg-blue-50 transition-colors text-left relative ${
                  isChangingRole || !role ? 'opacity-50 cursor-not-allowed' : ''
                } ${
                  selectedRole?.type === role?.type ? 'ring-2 ring-blue-500 bg-blue-50' : ''
                }`}
              >
                <div className="font-medium text-blue-900 text-sm">{role?.name || 'Ruolo sconosciuto'}</div>
                <div className="text-xs text-blue-600 mt-1">{role?.userCount || 0} utenti</div>
                {selectedRole?.type === role?.type && (
                  <div className="absolute top-2 right-2">
                    <CheckCircle className="w-4 h-4 text-blue-600" />
                  </div>
                )}
              </button>
            ))}
          </div>
        </div>
      )}

      {/* Gestore permessi unificato */}
      {selectedRole && (
        <div className="flex-1 flex flex-col overflow-hidden">
          {/* Header sezione permessi */}
          <div className="bg-white border-b border-gray-200 px-6 py-4">
            <div className="flex items-center space-x-2">
              <Shield className="w-5 h-5 text-blue-600" />
              <h3 className="text-lg font-semibold text-gray-900">
                Gestione Permessi - {selectedRole.name}
              </h3>
            </div>
            <p className="text-sm text-gray-600 mt-1">
              Configura i permessi per tutte le entit del sistema, incluse quelle virtuali (Dipendenti e Formatori)
            </p>
          </div>

          {/* Contenuto principale */}
          <div className="flex-1 overflow-hidden">
            <div className="h-full p-6 overflow-hidden">
              <OptimizedPermissionManager
                role={convertRole(selectedRole)!}
                tenants={tenants}
                onBack={handleBackToRoleSelection}
              />
            </div>
          </div>
        </div>
      )}

      {/* Loading state */}
      {rolesLoading && (
        <div className="flex items-center justify-center h-64">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600">Caricamento ruoli...</p>
          </div>
        </div>
      )}

      {/* Modale conferma cambio ruolo */}
      {showUnsavedChangesModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-md w-full mx-4">
            <div className="p-6">
              <div className="flex items-center space-x-3 mb-4">
                <div className="p-2 bg-amber-100 rounded-full">
                  <AlertTriangle className="w-6 h-6 text-amber-600" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-gray-900">Modifiche non salvate</h3>
                  <p className="text-sm text-gray-600">
                    Hai modifiche non salvate per il ruolo "{selectedRole?.name}"
                  </p>
                </div>
              </div>

              <p className="text-gray-700 mb-6">
                Vuoi salvare le modifiche prima di cambiare ruolo o procedere senza salvare?
              </p>

              <div className="flex space-x-3">
                <button
                  onClick={saveAndChangeRole}
                  className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center space-x-2"
                >
                  <Save className="w-4 h-4" />
                  <span>Salva e cambia</span>
                </button>
                <button
                  onClick={changeRoleWithoutSaving}
                  className="flex-1 bg-gray-600 text-white px-4 py-2 rounded-lg hover:bg-gray-700 transition-colors"
                >
                  Non salvare
                </button>
                <button
                  onClick={cancelRoleChange}
                  className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
                >
                  Annulla
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RolesTab;

import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import TemplatesSettingsPage from './Templates';
import UsersTab from './UsersTab';
import RolesTab from './RolesTab';
import HierarchyTab from './HierarchyTab';
import ActivityLogsTab from './ActivityLogsTab';
import UserPreferences from './UserPreferences';
import AdminSettings from '../../components/settings/AdminSettings';
import PermissionsTab from './PermissionsTab';
import PublicCMSPage from './PublicCMSPage';
import { useAuth } from '../../context/AuthContext';
import { Settings as SettingsIcon } from 'lucide-react';
import { TabNavigation } from '../../components/shared';

const Settings: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { hasPermission } = useAuth();

  // Determina quale tab  attivo dalla URL o usa il default
  const getActiveTab = () => {
    if (location.pathname.endsWith('/users')) return 'users';
    if (location.pathname.endsWith('/roles')) return 'roles';
    if (location.pathname.endsWith('/hierarchy')) return 'hierarchy';
    if (location.pathname.endsWith('/logs')) return 'logs';
    if (location.pathname.endsWith('/templates')) return 'templates';
    if (location.pathname.endsWith('/permissions')) return 'permissions';
    if (location.pathname.endsWith('/cms')) return 'cms';
    return 'general';
  };

  const [activeTab, setActiveTab] = useState(getActiveTab());

  const changeTab = (tabId: string) => {
    setActiveTab(tabId);
    navigate(`/settings${tabId !== 'general' ? `/${tabId}` : ''}`);
  };

  const canViewAdminSettings = hasPermission('admin_settings', 'read');
  const canViewPermissions = hasPermission('permissions', 'read');
  const canViewPublicCMS = hasPermission('public_cms', 'read');

  // Crea array di tab basato sui permessi
  const tabs = [
    { id: 'general', label: 'Generali' },
    { id: 'templates', label: 'Templates' },
  ];

  // Aggiungi i tab solo se l'utente ha i permessi
  if (hasPermission('users', 'read')) {
    tabs.push({ id: 'users', label: 'Utenti' });
  }

  if (hasPermission('roles', 'read') || hasPermission('ROLE_MANAGEMENT', 'read')) {
    tabs.push({ id: 'roles', label: 'Ruoli' });
  }

  // Aggiungi tab Gerarchia se l'utente pu gestire i ruoli
  if (hasPermission('roles', 'read') || hasPermission('ROLE_MANAGEMENT', 'read')) {
    tabs.push({ id: 'hierarchy', label: 'Gerarchia' });
  }

  if (hasPermission('system', 'admin')) {
    tabs.push({ id: 'permissions', label: 'Permessi' });
  }

  // Aggiungi tab CMS se l'utente pu gestire il contenuto pubblico
  if (hasPermission('PUBLIC_CMS', 'read') || hasPermission('PUBLIC_CMS', 'update')) {
    tabs.push({ id: 'cms', label: 'Frontend Pubblico' });
  }

  if (hasPermission('logs', 'read')) {
    tabs.push({ id: 'logs', label: 'Log Attivit' });
  }

  return (
    <div className="container px-4 mx-auto py-6">
      <h1 className="text-2xl font-bold mb-6 flex items-center">
        <SettingsIcon className="mr-2 h-6 w-6" /> 
        Impostazioni
      </h1>

      <div className="bg-white rounded-2xl shadow-sm">
        {/* Tabs - usando TabNavigation */}
        <div className="border-b border-gray-200 p-4">
          <TabNavigation 
            tabs={tabs}
            activeTabId={activeTab}
            onTabChange={changeTab}
          />
        </div>

        {/* Tab content */}
        <div className="p-6">
          {activeTab === 'general' && <UserPreferences />}

          {activeTab === 'templates' && <TemplatesSettingsPage />}

          {activeTab === 'users' && hasPermission('users', 'read') && <UsersTab />}

          {activeTab === 'roles' && (hasPermission('roles', 'read') || hasPermission('ROLE_MANAGEMENT', 'read')) && <RolesTab />}

          {activeTab === 'hierarchy' && (hasPermission('roles', 'read') || hasPermission('ROLE_MANAGEMENT', 'read')) && <HierarchyTab />}

          {activeTab === 'permissions' && hasPermission('system', 'admin') && <PermissionsTab />}

          {activeTab === 'cms' && (hasPermission('PUBLIC_CMS', 'read') || hasPermission('PUBLIC_CMS', 'update')) && <PublicCMSPage />}

          {activeTab === 'logs' && hasPermission('logs', 'read') && <ActivityLogsTab />}
        </div>
      </div>
    </div>
  );
};

export default Settings;

import React, { useState, useEffect } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import { apiGet, apiPost, apiPut } from '../../services/api';
import { ChevronLeft, Save, Download, Layout, Image, Eye, FileEdit } from 'lucide-react';
import { Button } from '../../design-system/atoms/Button';
import { PlaceholderDemo, GoogleTemplateProvider, GoogleDocsPreview } from '../../components/shared/template';
import PageHeader from '../../components/layouts/PageHeader';

// Template interface
interface Template {
  id: string;
  name: string;
  url: string;
  type: string;
  content?: string;
  header?: string;
  footer?: string;
  isDefault?: boolean;
  fileFormat?: string;
  logoImage?: string;
  logoPosition?: string;
  googleDocsUrl?: string;
}

// Common placeholders for templates
const TEMPLATE_PLACEHOLDERS = [
  { name: 'NOME_FORMATORE', description: 'Nome del formatore' },
  { name: 'COGNOME_FORMATORE', description: 'Cognome del formatore' },
  { name: 'DATA_GENERAZIONE', description: 'Data di generazione documento' },
  { name: 'NUMERO_PROGRESSIVO', description: 'Numero progressivo documento' },
  { name: 'CORSO_TITOLO', description: 'Titolo del corso' },
  { name: 'AZIENDA_RAGIONE_SOCIALE', description: 'Ragione sociale azienda' },
  { name: 'PRIMA_DATA', description: 'Data prima sessione' },
  { name: 'ULTIMA_DATA', description: 'Data ultima sessione' },
  { name: 'ORE_TOTALI', description: 'Ore totali corso' },
  { name: 'TARIFFA_ORARIA', description: 'Tariffa oraria' },
  { name: 'COMPENSO_TOTALE', description: 'Compenso totale' },
];

const TemplateEditor: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const location = useLocation();
  const navigate = useNavigate();
  const [template, setTemplate] = useState<Template | null>(null);
  const [templateName, setTemplateName] = useState<string>('');
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [content, setContent] = useState<string>('');
  const [header, setHeader] = useState<string>('');
  const [footer, setFooter] = useState<string>('');
  const [googleDocsUrl, setGoogleDocsUrl] = useState<string>('');
  const [showHeaderFooter, setShowHeaderFooter] = useState(false);
  const [logoImage, setLogoImage] = useState<string | null>(null);
  const [logoPosition, setLogoPosition] = useState<string>('top-center');
  const [isDefault, setIsDefault] = useState<boolean>(false);
  const [expandLogoPanel, setExpandLogoPanel] = useState<boolean>(false);
  const [showPreview, setShowPreview] = useState(false);
  const [previewContent, setPreviewContent] = useState<string>('');
  const [editingName, setEditingName] = useState(false);
  const [templateType, setTemplateType] = useState<string>('');
  const templateFormat = new URLSearchParams(location.search).get('format') || 'text';

  // Template types available for selection
  const templateTypes = [
    { value: 'lettera_incarico', label: 'Lettera di Incarico' },
    { value: 'attestati', label: 'Attestati' },
    { value: 'fattura', label: 'Fattura' },
    { value: 'programma_corso', label: 'Programma Corso' },
    { value: 'registro_presenze', label: 'Registro Presenze' },
  ];

  // Load template based on ID from URL params
  useEffect(() => {
    const fetchTemplate = async () => {
      const initialType = new URLSearchParams(location.search).get('type') || 'lettera_incarico';
      setTemplateType(initialType);

      if (id) {
        try {
          console.log('Fetching template with ID:', id);
          const templateData = await apiGet<Template>(`/template-links/${id}`);

          if (!templateData) {
            throw new Error(`Template with ID ${id} not found`);
          }

          console.log('Template loaded:', templateData);
          setTemplate(templateData);
          setTemplateName(templateData.name);
          setIsDefault(templateData.isDefault || false);
          setTemplateType(templateData.type);

          // Set header, footer, and logo if available
          if (templateData.header) setHeader(templateData.header);
          if (templateData.footer) setFooter(templateData.footer);
          if (templateData.logoImage) setLogoImage(templateData.logoImage);
          if (templateData.logoPosition) setLogoPosition(templateData.logoPosition);
          if (templateData.googleDocsUrl) setGoogleDocsUrl(templateData.googleDocsUrl);

          // Load content
          if (templateData.content) {
            setContent(templateData.content);
          } else if (templateData.url && !templateData.url.includes('placeholder')) {
            try {
              const contentData = await apiGet<string>(`${templateData.url}`);
              if (contentData) {
                setContent(typeof contentData === 'string' 
                  ? contentData 
                  : JSON.stringify(contentData));
              }
            } catch (err) {
              console.error('Could not load template content:', err);
              setContent('<p>Inserisci il tuo contenuto qui...</p>');
              setError('Non  stato possibile caricare il contenuto del template');
            }
          } else {
            setContent('<p>Inserisci il tuo contenuto qui...</p>');
          }

          // Update URL to reflect the template format
          if (templateData.fileFormat) {
            const url = new URL(window.location.href);
            url.searchParams.set('format', templateData.fileFormat);
            window.history.replaceState({}, '', url.toString());
          }
        } catch (err) {
          console.error('Failed to load template:', err);
          setError('Impossibile caricare il template');
          setContent('<p>Inserisci il tuo contenuto qui...</p>');
        }
      } else {
        // Set default content for new template
        setTemplateName(`Nuovo Template ${initialType}`);
        setContent('<p>Inserisci il tuo contenuto qui...</p>');
        setIsDefault(false);
      }
      setLoading(false);
    };

    fetchTemplate();
  }, [id, location.search]);

  // Handle save action
  const handleSave = async () => {
    if (!templateName || templateName.trim() === '') {
      setError('Impossibile salvare: inserisci un nome per il template.');
      return;
    }

    // If using Google Docs, ensure URL is provided 
    if (googleDocsUrl && !googleDocsUrl.trim().startsWith('https://docs.google.com/')) {
      setError('Il link a Google Docs/Slides non  valido. Deve iniziare con "https://docs.google.com/"');
      return;
    }

    setSaving(true);
    setError(null);

    try {
      // Generate unique filename for the template
      const timestamp = Date.now();
      const filename = `template_${templateType}_${timestamp}.html`;
      const templateUrl = `/uploads/templates/${filename}`;

      // Prepare template data
      const templateData = {
        name: templateName.trim(),
        url: googleDocsUrl ? '' : templateUrl,
        type: templateType,
        content: googleDocsUrl ? '' : content,
        header: header,
        footer: footer,
        logoPosition: logoPosition,
        logoImage: logoImage,
        isDefault: isDefault,
        fileFormat: templateFormat,
        googleDocsUrl: googleDocsUrl.trim() || null
      };

      console.log('Saving template data:', templateData);

      // Save template (create or update)
      if (id) {
        await apiPut(`/template-links/${id}`, templateData);
        console.log(`Template with ID ${id} updated`);
      } else {
        const newTemplate = await apiPost<Template>('/template-links', templateData);
        console.log('New template created:', newTemplate);

        // If this template is set as default, update other templates of the same type
        if (isDefault) {
          const allTemplates = await apiGet<Template[]>('/template-links');
          const templatesOfSameType = allTemplates.filter(
            (t: Template) => t.type === templateType && t.id !== (newTemplate.id || id)
          );

          // Update other templates to not be default
          for (const template of templatesOfSameType) {
            if (template.isDefault) {
              await apiPut(`/template-links/${template.id}`, {
                ...template,
                isDefault: false
              });
            }
          }
        }
      }

      // Navigate back to templates list
      navigate('/settings/templates');
    } catch (err: any) {
      console.error('Error saving template:', err);
      let errorMessage = 'Errore durante il salvataggio del template';
      if (err.response?.data?.error) {
        errorMessage += `: ${err.response.data.error}`;
      }
      setError(errorMessage);
    } finally {
      setSaving(false);
    }
  };

  // Show loading state
  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen bg-slate-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-slate-600 font-medium">Caricamento editor...</p>
        </div>
      </div>
    );
  }

  // Render template editor
  return (
    <div className="container mx-auto py-8">
      <PageHeader 
        title="Editor Template" 
        subtitle="Crea e modifica template"
        viewMode="table" 
        onViewModeChange={() => {}}
        selectionMode={false}
        onToggleSelectionMode={() => {}}
        searchValue=""
        onSearchChange={() => {}}
        description="Crea e modifica template per documenti con supporto per placeholder"
      />

      {/* Template type selector */}
      <div className="mt-6">
        <label className="block text-sm font-medium text-gray-700 mb-2">Tipo di Template</label>
        <select
          value={templateType}
          onChange={(e) => setTemplateType(e.target.value)}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
        >
          {templateTypes.map((type) => (
            <option key={type.value} value={type.value}>{type.label}</option>
          ))}
        </select>
      </div>

      {/* Template name */}
      <div className="mt-6">
        <label className="block text-sm font-medium text-gray-700 mb-2">Nome Template</label>
        <input
          type="text"
          value={templateName}
