}

/**
 * Attestati service for managing course certificates
 */
const attestatiService = {
  /**
   * Get all attestati documents
   */
  async getAllAttestati() {
    try {
      return await apiGet(`/api${API_ENDPOINTS.ATTESTATI}`);
    } catch (error) {
      console.error('Error fetching attestati:', error);
      throw error;
    }
  },

  /**
   * Check if attestato exists for a specific employee and course
   */
  async checkExistingAttestato(scheduledCourseId: string, employeeId: string): Promise<CheckExistingAttesatoResult> {
    try {
      return await apiGet<CheckExistingAttesatoResult>(
        `/api/attestati/check-existing?scheduledCourseId=${scheduledCourseId}&employeeId=${employeeId}`
      );
    } catch (error) {
      console.error('Error checking for existing attestato:', error);
      return { exists: false };
    }
  },

  /**
   * Generate attestati for a scheduled course
   */
  async generateAttestati(scheduledCourseId: string, options: { 
    templateId?: string, 
    templateUrl?: string,
    overwriteExisting?: boolean,
    employeeIds?: string[]
  } = {}) {
    try {
      console.log("generateAttestati called with options:", {
        scheduledCourseId,
        ...options
      });

      // Get default template if none specified
      let templateUrl = options.templateUrl;
      let templateId = options.templateId;

      if (!templateUrl && !templateId) {
        // Fetch available templates
        const templates = await apiGet<Template[]>('/api/template-links');
        const attestatoTemplate = templates.find((tpl) => tpl.type === 'attestato' && tpl.isDefault);

        if (!attestatoTemplate) {
          throw new Error('Nessun template attestato configurato nelle impostazioni.');
        }

        templateUrl = attestatoTemplate.url;
        templateId = attestatoTemplate.id;
      }

      // Build request data
      const requestData: Record<string, any> = {
        scheduledCourseId,
        overwriteExisting: options.overwriteExisting || false
      };

      // Add template info
      if (templateUrl) {
        requestData.templateUrl = templateUrl;
      }

      if (templateId) {
        requestData.templateId = templateId;
      }

      // Add specific employees if provided - IMPORTANT: backend expects participantIds, not employeeIds
      if (options.employeeIds && options.employeeIds.length > 0) {
        requestData.participantIds = options.employeeIds; // Fixed parameter name
        console.log(`Converting employeeIds to participantIds for ${options.employeeIds.length} employees`);
      } else {
        console.warn("No employeeIds provided in options - server will reject request");
      }

      // Make API request
      console.log("Sending attestati generation request:", requestData);

      // Make API request using centralized service
      console.log("Sending attestati generation request:", requestData);

      try {
        const response = await apiPost<GenerateAttestatiResponse>(
          '/api/attestati/genera',
          requestData
        );

        console.log("Server responded with:", {
          success: response.success,
          message: response.message,
          attestatiCount: response.attestati?.length || 0
        });

        return response;
      } catch (error: any) {
        console.error('Error generating attestati:', error);
        throw error;
      }
    } catch (error) {
      console.error('Error generating attestati:', error);
      throw error;
    }
  },

  /**
   * Delete an attestato
   */
  async deleteAttestato(id: string) {
    if (!id) {
      throw new Error('ID attestato non valido');
    }

    try {
      console.log(`Attempting to delete attestato ${id}`);
      const result = await apiDelete(`/api/attestati/${id}`);
      console.log(`Successfully deleted attestato ${id}`);
      return result;
    } catch (error: any) {
      console.error('Error deleting attestato:', error);
      throw new Error(`Errore durante l'eliminazione dell'attestato: ${error.message || error}`);
    }
  },

  /**
   * Delete multiple attestati at once
   */
  async deleteMultipleAttestati(ids: string[]) {
    if (!ids.length) {
      throw new Error('Nessun attestato selezionato');
    }

    try {
      console.log(`Deleting ${ids.length} attestati`);

      // Define possible endpoints - using Vite proxy
      const endpoints = [
        `/api/attestati/delete-multiple`,
        `/attestati/delete-multiple`
      ];

      // Define different formats for the request payload that servers might expect
      const payloadFormats = [
        { ids },
        { attestatoIds: ids },
        { id: ids }
      ];

      let lastError = null;

      // Try each endpoint with each payload format
      for (const endpoint of endpoints) {
        for (const payload of payloadFormats) {
          try {
            console.log(`Trying to delete multiple attestati with endpoint: ${endpoint} and payload:`, payload);
            const response = await apiPost(endpoint, payload);

            console.log(`Successfully deleted attestati with endpoint: ${endpoint}`);
            return response;
          } catch (error: any) {
            lastError = error;
            console.warn(`Error with endpoint ${endpoint}:`, error.message || error);
          }
        }
      }

      // If all batch endpoints failed, try deleting one by one
      console.log('All batch deletion endpoints failed, trying to delete attestati one by one');
      const results = await Promise.allSettled(ids.map(id => this.deleteAttestato(id)));

      const succeeded = results.filter(r => r.status === 'fulfilled').length;
      const failed = results.filter(r => r.status === 'rejected').length;

      if (succeeded > 0) {
        console.log(`Successfully deleted ${succeeded}/${ids.length} attestati individually`);
        return { 
          success: true,
          message: `Deleted ${succeeded}/${ids.length} attestati`,
          failedCount: failed
        };
      }

      // Se arriviamo qui, tutti gli endpoint hanno fallito
      console.error('Error deleting multiple attestati:', lastError);
      throw lastError || new Error('Failed to delete attestati with all available endpoints');
    } catch (error) {
      console.error('Error in deleteMultipleAttestati:', error);
      throw error;
    }
  }
};

export default attestatiService;

import { apiGet, apiPost } from './api';
import { AuthResponse, AuthVerifyResponse, LoginRequest } from '../types';

// Types for permissions
export interface UserPermissions {
  role: string;
  permissions: Array<{
    resource: string;
    action: string;
    scope?: string;
  }>;
}

export const login = async (identifier: string, password: string): Promise<AuthResponse> => {
  return await apiPost<AuthResponse>('/api/v1/auth/login', {
    identifier,
    password,
  });
};

export const verifyToken = async (): Promise<AuthVerifyResponse> => {
  return await apiGet<AuthVerifyResponse>('/api/v1/auth/verify');
};

export const forgotPassword = async (email: string): Promise<{ message: string }> => {
  return await apiPost<{ message: string }>('/api/v1/auth/forgot-password', { email });
};

export const resetPassword = async (token: string, password: string): Promise<{ message: string }> => {
  return await apiPost<{ message: string }>('/api/v1/auth/reset-password', { token, password });
};

export const saveToken = (token: string): void => {
  localStorage.setItem('authToken', token);
};

export const getToken = (): string | null => {
  return localStorage.getItem('authToken');
};

export const removeToken = (): void => {
  localStorage.removeItem('authToken');
};

export const isAuthenticated = (): boolean => {
  return !!getToken();
};

export const getUserPermissions = async (personId: string): Promise<UserPermissions> => {
  try {
    console.log(' getUserPermissions: Calling API for personId:', personId);
    console.log(' getUserPermissions: Current token:', getToken() ? getToken()?.substring(0, 20) + '...' : 'NO TOKEN');
    const response = await apiGet<{ success: boolean; data: { personId: string; role: string; permissions: Record<string, boolean> } }>(`/api/v1/auth/permissions/${personId}`);

    console.log(' getUserPermissions: Raw API response:', response);

    // Convert backend response format to frontend expected format
    const permissionsArray = Object.entries(response.data.permissions || {})
      .filter(([key, value]) => value === true) // Only include permissions that are granted
      .map(([key, value]) => {
        // Handle both formats: 'resource.action' and 'resource:action'
        const [resource, action] = key.includes('.') ? key.split('.') : key.split(':');
        return {
          resource: resource || 'unknown',
          action: action || 'unknown',
          scope: undefined
        };
      })
      .filter(p => p.resource !== 'unknown' && p.action !== 'unknown');

    console.log(' getUserPermissions: Converted permissions:', {
      backendPermissions: response.data.permissions,
      convertedPermissions: permissionsArray,
      role: response.data.role
    });

    return {
      role: response.data.role,
      permissions: permissionsArray
    };
  } catch (error: any) {
    console.error(' getUserPermissions: Error fetching user permissions:', {
      error: error.message,
      status: error.response?.status,
      statusText: error.response?.statusText,
      data: error.response?.data,
      personId,
      hasToken: !!getToken(),
      tokenPreview: getToken() ? getToken()?.substring(0, 20) + '...' : 'NO TOKEN',
      fullError: error
    });

    // Return default EMPLOYEE role if there's an error
    console.warn(' getUserPermissions: Returning default EMPLOYEE role due to error');
    return {
      role: 'EMPLOYEE',
      permissions: []
    };
  }
};

export default {
  login,
  verifyToken,
  forgotPassword,
  resetPassword,
  saveToken,
  getToken,
  removeToken,
  isAuthenticated
};

/**
 * Contact Submissions Service
 * Gestisce l'invio di contact submissions dal frontend pubblico
 */

import { apiGet } from './api';

export interface ContactSubmissionData {
  name: string;
  email: string;
  phone?: string;
  subject: string;
  message: string;
  privacyAccepted: boolean;
  marketingAccepted?: boolean;
}

export interface ContactSubmissionResponse {
  id: string;
  status: 'pending' | 'processed' | 'archived';
  submittedAt: string;
  message: string;
}

/**
 * Invia una contact submission dal frontend pubblico
 */
export const submitContactForm = async (data: ContactSubmissionData): Promise<ContactSubmissionResponse> => {
  try {
    console.log(' Invio contact form:', data);

    const response = await apiPost<ContactSubmissionResponse>('/api/v1/submissions', data);

    console.log(' Contact form inviato con successo:', response);
    return response;
  } catch (error: any) {
    console.error(' Errore invio contact form:', error);

    // Gestione specifica per rate limiting
    if (error.response?.status === 429) {
      throw new Error('Troppe richieste. Riprova tra qualche minuto.');
    }

    // Gestione per errori di validazione
    if (error.response?.status === 400) {
      const errorMessage = error.response?.data?.message || 'Dati non validi';
      throw new Error(errorMessage);
    }

    // Gestione per errori del server
    if (error.response?.status >= 500) {
      throw new Error('Errore del server. Riprova pi tardi.');
    }

    // Errore generico
    throw new Error('Errore nell\'invio del messaggio. Riprova pi tardi.');
  }
};

