import { ConflictInfo, PersonData, CompanyOption } from '../../types/import/personImportTypes';

/**
 * Servizio per la rilevazione e gestione dei conflitti durante l'importazione
 */

/**
 * Rileva duplicati basati sul codice fiscale
 */
export const detectDuplicates = (
  persons: PersonData[],
  existingPersons: any[] = []
): ConflictInfo[] => {
  const conflicts: ConflictInfo[] = [];
  const fiscalCodes = new Set();

  // Controlla duplicati negli esistenti
  existingPersons.forEach(existing => {
    if (existing.fiscalCode) {
      fiscalCodes.add(existing.fiscalCode.toLowerCase());
    }
  });

  persons.forEach((person, index) => {
    if (person.taxCode) {
      const fiscalCode = person.taxCode.toLowerCase();

      if (fiscalCodes.has(fiscalCode)) {
        const existingPerson = existingPersons.find(
          p => p.fiscalCode?.toLowerCase() === fiscalCode
        );

        conflicts.push({
          type: 'duplicate',
          rowIndex: index,
          personData: person,
          message: `Persona con codice fiscale ${person.taxCode} gi esistente nel sistema`,
          existingPerson,
          resolution: 'skip' // Default: salta i duplicati
        });
      } else {
        fiscalCodes.add(fiscalCode);
      }
    }
  });

  return conflicts;
};

/**
 * Rileva aziende non valide e suggerisce alternative
 */
export const detectInvalidCompanies = (
  persons: PersonData[],
  companies: CompanyOption[]
): ConflictInfo[] => {
  const conflicts: ConflictInfo[] = [];
  const companyNames = new Set(companies.map(c => c.label.toLowerCase()));

  persons.forEach((person, index) => {
    if (person.companyName && !companyNames.has(person.companyName.toLowerCase())) {
      // Trova aziende simili
      const suggestedCompanies = findSimilarCompanies(person.companyName, companies);

      conflicts.push({
        type: 'invalid_company',
        rowIndex: index,
        personData: person,
        message: `Azienda "${person.companyName}" non trovata nel sistema`,
        suggestedCompanies: suggestedCompanies.map(c => ({ id: c.value, name: c.label })),
        suggestedCompanyId: suggestedCompanies.length > 0 ? suggestedCompanies[0].value : undefined,
        resolution: suggestedCompanies.length > 0 ? 'company' : 'skip'
      });
    }
  });

  return conflicts;
};

/**
 * Trova aziende simili basate sul nome
 */
export const findSimilarCompanies = (
  companyName: string,
  companies: CompanyOption[]
): CompanyOption[] => {
  const searchTerm = companyName.toLowerCase();

  return companies
    .filter(company => {
      const companyNameLower = company.label.toLowerCase();
      return companyNameLower.includes(searchTerm) || 
             searchTerm.includes(companyNameLower) ||
             calculateSimilarity(searchTerm, companyNameLower) > 0.6;
    })
    .sort((a, b) => {
      const similarityA = calculateSimilarity(searchTerm, a.label.toLowerCase());
      const similarityB = calculateSimilarity(searchTerm, b.label.toLowerCase());
      return similarityB - similarityA;
    })
    .slice(0, 5);
};

/**
 * Calcola la similarit tra due stringhe (algoritmo semplificato)
 */
const calculateSimilarity = (str1: string, str2: string): number => {
  const longer = str1.length > str2.length ? str1 : str2;
  const shorter = str1.length > str2.length ? str2 : str1;

  if (longer.length === 0) return 1.0;

  const editDistance = levenshteinDistance(longer, shorter);
  return (longer.length - editDistance) / longer.length;
};

/**
 * Calcola la distanza di Levenshtein tra due stringhe
 */
const levenshteinDistance = (str1: string, str2: string): number => {
  const matrix = [];

  for (let i = 0; i <= str2.length; i++) {
    matrix[i] = [i];
  }

  for (let j = 0; j <= str1.length; j++) {
    matrix[0][j] = j;
  }

  for (let i = 1; i <= str2.length; i++) {
    for (let j = 1; j <= str1.length; j++) {
      if (str2.charAt(i - 1) === str1.charAt(j - 1)) {
        matrix[i][j] = matrix[i - 1][j - 1];
      } else {
        matrix[i][j] = Math.min(
          matrix[i - 1][j - 1] + 1,
          matrix[i][j - 1] + 1,
          matrix[i - 1][j] + 1
        );
      }
    }
  }

  return matrix[str2.length][str1.length];
};

/**
 * Aggiorna la risoluzione di un conflitto
 */
export const updateConflictResolution = (
  conflicts: ConflictInfo[],
  index: number,
  resolution: 'skip' | 'overwrite' | 'company',
  companyId?: string
): ConflictInfo[] => {
  return conflicts.map((conflict, i) => {
    if (i === index) {
      return {
        ...conflict,
        resolution,
        resolvedCompanyId: resolution === 'company' ? companyId : undefined
      };
    }
    return conflict;
  });
};

/**
 * Risolve i conflitti preparando i dati per l'importazione
 */
export const resolveConflicts = (
  persons: PersonData[],
  conflicts: ConflictInfo[]
): PersonData[] => {
  const resolvedPersons: PersonData[] = [];

  persons.forEach((person, index) => {
    const conflict = conflicts.find(c => c.rowIndex === index);

    if (!conflict) {
      // Nessun conflitto, aggiungi la persona
      resolvedPersons.push(person);
    } else if (conflict.resolution === 'overwrite') {
      // Sovrascrivi la persona esistente
      resolvedPersons.push(person);
    } else if (conflict.resolution === 'company' && conflict.resolvedCompanyId) {
      // Assegna l'azienda risolta
      resolvedPersons.push({
        ...person,
        companyId: conflict.resolvedCompanyId
      });
    }
    // Se resolution  'skip', non aggiungere la persona
  });

  return resolvedPersons;
};

/**
 * Ottiene un riassunto dei conflitti
 */
export const getConflictSummary = (conflicts: ConflictInfo[]) => {
  const duplicates = conflicts.filter(c => c.type === 'duplicate').length;
  const invalidCompanies = conflicts.filter(c => c.type === 'invalid_company').length;
  const resolved = conflicts.filter(c => c.resolution).length;

  return {
    total: conflicts.length,
    duplicates,
    invalidCompanies,
    resolved,
    unresolved: conflicts.length - resolved
  };
};

/**
 * Verifica se tutti i conflitti sono stati risolti
 */
export const areAllConflictsResolved = (conflicts: ConflictInfo[]): boolean => {
  return conflicts.every(conflict => conflict.resolution !== undefined);
};

/**
 * Ottiene gli indici delle righe con conflitti non risolti
 */
export const getUnresolvedConflictIndices = (conflicts: ConflictInfo[]): number[] => {
  return conflicts
    .filter(conflict => !conflict.resolution)
    .map(conflict => conflict.rowIndex);
};

/**
 * Deseleziona automaticamente le righe con conflitti di duplicato
 */
export const autoDeselectDuplicateConflicts = (
  selectedRows: boolean[],
  conflicts: ConflictInfo[]
): boolean[] => {
  const newSelectedRows = [...selectedRows];

  conflicts.forEach(conflict => {
    if (conflict.type === 'duplicate' && conflict.resolution === 'skip') {
      newSelectedRows[conflict.rowIndex] = false;
    }
  });

  return newSelectedRows;
};

import { CSV_HEADER_MAPPINGS, TITLE_CASE_FIELDS } from '../../types/import/personImportTypes';
import { applyTitleCaseToFields } from '../../utils/textFormatters';

/**
 * Servizio per la mappatura e normalizzazione dei dati CSV
 */
export class CsvMappingService {

  /**
   * Ottiene la mappatura degli header CSV
   */
  static getHeaderMappings(): Record<string, string> {
    return CSV_HEADER_MAPPINGS;
  }

  /**
   * Ottiene i campi da formattare in Title Case
   */
  static getTitleCaseFields(): readonly string[] {
    return TITLE_CASE_FIELDS;
  }

  /**
   * Applica la formattazione Title Case ai campi specificati
   */
  static applyTitleCaseFormatting(person: any): any {
    return applyTitleCaseToFields(person, [...TITLE_CASE_FIELDS]);
  }

  /**
   * Normalizza il codice fiscale
   */
  static normalizeTaxCode(taxCode: string): string {
    if (!taxCode) return '';
    return taxCode.toUpperCase().trim();
  }

  /**
   * Normalizza il nome di un'azienda per il confronto
   */
  static normalizeCompanyName(companyName: string): string {
    if (!companyName) return '';
    return companyName.toLowerCase().trim();
  }

  /**
   * Trova un'azienda corrispondente nella lista esistente
   */
  static findMatchingCompany(companyName: string, existingCompanies: any[]): any | null {
    if (!companyName || !existingCompanies.length) return null;

    const normalizedName = this.normalizeCompanyName(companyName);

    return existingCompanies.find(company => {
      const ragioneSociale = company.ragioneSociale ? this.normalizeCompanyName(company.ragioneSociale) : '';
      const name = company.name ? this.normalizeCompanyName(company.name) : '';

      return ragioneSociale === normalizedName || name === normalizedName;
    }) || null;
  }

  /**
   * Trova aziende simili per suggerimenti
   */
  static findSimilarCompanies(companyName: string, existingCompanies: any[]): any[] {
    if (!companyName || !existingCompanies.length) return [];

    const normalizedName = this.normalizeCompanyName(companyName);

    return existingCompanies.filter(company => {
      const ragioneSociale = company.ragioneSociale ? this.normalizeCompanyName(company.ragioneSociale) : '';
      const name = company.name ? this.normalizeCompanyName(company.name) : '';

      return ragioneSociale.includes(normalizedName) || 
             normalizedName.includes(ragioneSociale) ||
             name.includes(normalizedName) || 
             normalizedName.includes(name);
    });
  }

  /**
   * Risolve l'ID dell'azienda basandosi sul nome
   */
  static resolveCompanyId(person: any, existingCompanies: any[]): any {
    if (!person.companyName || typeof person.companyName !== 'string') {
      return person;
    }

    const matchingCompany = this.findMatchingCompany(person.companyName, existingCompanies);

    if (matchingCompany) {
      return {
        ...person,
        companyId: matchingCompany.id,
        companyName: matchingCompany.ragioneSociale || matchingCompany.name
      };
    }

    // Se non trova corrispondenza, rimuove companyId per attivare il conflict detection
    return {
      ...person,
      companyId: undefined
    };
  }

  /**
   * Pulisce e normalizza i dati di una persona
   */
  static cleanPersonData(person: any): any {
    const cleaned = { ...person };

    // Normalizza il codice fiscale
    if (cleaned.taxCode) {
      cleaned.taxCode = this.normalizeTaxCode(cleaned.taxCode);
    }

    // Rimuove spazi extra da tutti i campi stringa
    Object.keys(cleaned).forEach(key => {
      if (typeof cleaned[key] === 'string') {
        cleaned[key] = cleaned[key].trim();
        // Rimuove campi vuoti
        if (cleaned[key] === '') {
          cleaned[key] = undefined;
        }
      }
    });

    return cleaned;
  }

  /**
   * Valida se una riga  un template vuoto
   */
  static isEmptyTemplate(person: any): boolean {
    return !person.firstName?.trim() && 
           !person.lastName?.trim() && 
           !person.taxCode?.trim();
  }

  /**
   * Prepara i dati per l'API rimuovendo campi non necessari
   */
  static prepareForAPI(person: any): any {
    const apiData: any = {};

    // Campi da includere nell'API
    const apiFields = [
      'firstName', 'lastName', 'email', 'phone', 'taxCode', 'birthDate',
      'residenceAddress', 'city', 'province', 'postalCode', 'title', 'companyId', 
      'username', 'notes', 'roleType'
    ];

    apiFields.forEach(field => {
      if (person[field] !== undefined && person[field] !== null && person[field] !== '') {
        apiData[field] = person[field];
      }
    });

    // Assicura che il roleType sia valido
    if (!apiData.roleType) {
      apiData.roleType = 'EMPLOYEE'; // Default
    }

    return apiData;
  }

  /**
   * Converte le opzioni azienda per il componente SearchableSelect
   */
  static convertCompaniesToOptions(companies: any[]): Array<{ value: string; label: string }> {
    return companies.map(company => ({
      value: company.id,
      label: company.ragioneSociale || company.name || 'Azienda senza nome'
    }));
  }

  /**
   * Estrae i nomi delle aziende uniche dai dati importati
   */
  static extractUniqueCompanyNames(persons: any[]): string[] {
    const companyNames = new Set<string>();

    persons.forEach(person => {
      if (person.companyName && typeof person.companyName === 'string') {
        companyNames.add(person.companyName.trim());
      }
    });

    return Array.from(companyNames).filter(name => name.length > 0);
  }

  /**
   * Valida la struttura dei dati importati
   */
  static validateImportStructure(data: any[]): { isValid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (!Array.isArray(data)) {
      errors.push('I dati importati devono essere un array');
      return { isValid: false, errors };
    }

    if (data.length === 0) {
      errors.push('Il file non contiene dati da importare');
      return { isValid: false, errors };
    }

    // Verifica che almeno una riga abbia dati validi
    const hasValidData = data.some(person => !this.isEmptyTemplate(person));

    if (!hasValidData) {
      errors.push('Il file non contiene righe con dati validi');
      return { isValid: false, errors };
    }

    return { isValid: true, errors };
  }
}

import { DATE_FORMATS, DateFormatConfig } from '../../types/import/personImportTypes';

/**
 * Servizio per la gestione e formattazione delle date nell'importazione
 */
export class DateFormattingService {

  /**
   * Formatta una data per la visualizzazione (dd/mm/yyyy)
   */
  static formatDateForDisplay(dateString: string): string {
    if (!dateString) return '';

    try {
      const cleanDateStr = dateString.toString().trim();

      // Se  gi in formato dd/mm/yyyy, verifica che sia valido
      if (/^\d{1,2}\/\d{1,2}\/\d{4}$/.test(cleanDateStr)) {
        const parts = cleanDateStr.split('/');
        const day = parseInt(parts[0]);
        const month = parseInt(parts[1]);
        const year = parseInt(parts[2]);

        // Verifica che sia una data valida
        if (day >= 1 && day <= 31 && month >= 1 && month <= 12 && year >= 1900 && year <= 2100) {
          const date = new Date(year, month - 1, day);
          if (date.getDate() === day && date.getMonth() === month - 1 && date.getFullYear() === year) {
            return cleanDateStr; //  gi nel formato corretto
          }
        }
      }

      // Prova i diversi formati supportati
      for (const format of DATE_FORMATS) {
        if (format.inputFormat.test(cleanDateStr)) {
          const date = format.parser(cleanDateStr);
          if (date && !isNaN(date.getTime())) {
            const day = date.getDate().toString().padStart(2, '0');
            const month = (date.getMonth() + 1).toString().padStart(2, '0');
            const year = date.getFullYear();
            return `${day}/${month}/${year}`;
          }
        }
      }

      // Fallback: prova a parsare come Date
      const date = new Date(cleanDateStr);
      if (!isNaN(date.getTime())) {
        const day = date.getDate().toString().padStart(2, '0');
        const month = (date.getMonth() + 1).toString().padStart(2, '0');
        const year = date.getFullYear();
        return `${day}/${month}/${year}`;
      }

      return cleanDateStr; // Se non riesce a parsare, restituisce l'originale
    } catch (error) {
      console.warn(`Errore nella formattazione della data: ${dateString}`, error);
      return dateString;
    }
  }

  /**
   * Valida una data con supporto per pi formati
   */
  static isValidDate(dateString: string): boolean {
    if (!dateString) return false;

    try {
      const cleanDateStr = dateString.toString().trim();

      // Prova tutti i formati supportati
      for (const format of DATE_FORMATS) {
        if (format.inputFormat.test(cleanDateStr) && format.validator(cleanDateStr)) {
          return true;
        }
      }

      // Fallback: prova a parsare direttamente
      const date = new Date(cleanDateStr);
      return !isNaN(date.getTime()) && date.getFullYear() >= 1900 && date.getFullYear() <= 2100;
    } catch (error) {
      return false;
    }
  }

  /**
   * Formatta una data per l'API (YYYY-MM-DD)
   */
  static formatDateForAPI(dateString: string): string {
    if (!dateString) return '';

    const cleanDateStr = dateString.toString().trim();

    // Se  gi nel formato YYYY-MM-DD, verifica che sia valido
    if (/^\d{4}-\d{2}-\d{2}$/.test(cleanDateStr)) {
      const date = new Date(cleanDateStr);
      if (!isNaN(date.getTime()) && date.getFullYear() >= 1900 && date.getFullYear() <= 2100) {
        return cleanDateStr;
      }
    }

    // Prova i diversi formati supportati
    for (const format of DATE_FORMATS) {
      if (format.inputFormat.test(cleanDateStr) && format.validator(cleanDateStr)) {
        const date = format.parser(cleanDateStr);
        if (date && !isNaN(date.getTime())) {
          return date.toISOString().split('T')[0]; // Ritorna YYYY-MM-DD
        }
      }
    }

    // Fallback: prova a parsare direttamente
    try {
      const date = new Date(cleanDateStr);
      if (!isNaN(date.getTime()) && date.getFullYear() >= 1900 && date.getFullYear() <= 2100) {
        return date.toISOString().split('T')[0];
      }
    } catch (error) {
      console.warn(`Impossibile formattare la data per l'API: ${dateString}`, error);
    }

    return cleanDateStr; // Se non riesce a convertire, restituisce l'originale
  }

  /**
   * Estrae la data di nascita dal codice fiscale italiano
   */
  static extractBirthDateFromTaxCode(taxCode: string): string | null {
    if (!taxCode || taxCode.length !== 16) {
      return null;
    }

    try {
      const normalizedTaxCode = taxCode.toUpperCase().trim();

      // Estrai anno, mese e giorno dal codice fiscale
      const yearPart = normalizedTaxCode.substring(6, 8);
      const monthChar = normalizedTaxCode.substring(8, 9);
      const dayPart = normalizedTaxCode.substring(9, 11);

      // Mappa dei mesi
      const monthMap: { [key: string]: number } = {
        'A': 1, 'B': 2, 'C': 3, 'D': 4, 'E': 5, 'H': 6,
        'L': 7, 'M': 8, 'P': 9, 'R': 10, 'S': 11, 'T': 12
      };

      const month = monthMap[monthChar];
      if (!month) {
        return null;
      }

      // Calcola l'anno (assumendo che le persone siano nate tra 1920 e 2020)
      let year = parseInt(yearPart);
      const currentYear = new Date().getFullYear();
      const currentYearLastTwoDigits = currentYear % 100;

      if (year <= currentYearLastTwoDigits) {
        year += 2000;
      } else {
        year += 1900;
      }

      // Calcola il giorno (per le donne si aggiunge 40)
      let day = parseInt(dayPart);
      if (day > 31) {
        day -= 40; //  una donna
      }

      // Verifica che la data sia valida
      if (day < 1 || day > 31 || month < 1 || month > 12 || year < 1900 || year > currentYear) {
        return null;
      }

      const date = new Date(year, month - 1, day);
      if (date.getDate() !== day || date.getMonth() !== month - 1 || date.getFullYear() !== year) {
        return null;
      }

      // Ritorna nel formato dd/mm/yyyy
      const dayStr = day.toString().padStart(2, '0');
      const monthStr = month.toString().padStart(2, '0');
      return `${dayStr}/${monthStr}/${year}`;
    } catch (error) {
      console.warn(`Errore nell'estrazione della data dal codice fiscale: ${taxCode}`, error);
      return null;
    }
  }

  /**
   * Normalizza una data in un formato standard
   */
  static normalizeDate(dateString: string): string {
    const formatted = this.formatDateForDisplay(dateString);
    return formatted || dateString;
  }

  /**
   * Verifica se due date sono uguali (indipendentemente dal formato)
   */
  static areDatesEqual(date1: string, date2: string): boolean {
    if (!date1 || !date2) return false;

    const normalized1 = this.formatDateForAPI(date1);
    const normalized2 = this.formatDateForAPI(date2);

    return normalized1 === normalized2;
  }
}

import { PersonData, ValidationError, ImportValidationResult } from '../../types/import/personImportTypes';
import { DateFormattingService } from './dateFormattingService';
import { CsvMappingService } from './csvMappingService';
import { isValidCodiceFiscale } from '../../lib/utils';

/**
 * Servizio per la validazione dei dati delle persone durante l'importazione
 */
export class ValidationService {

  /**
   * Valida una singola persona
   */
  static validatePerson(person: PersonData): string[] {
    const errors: string[] = [];

    // Controlla se  un template vuoto
    if (CsvMappingService.isEmptyTemplate(person)) {
      return errors; // Non validare template vuoti
    }

    // Validazione campi obbligatori
    if (!person.firstName?.trim()) {
      errors.push('Nome  obbligatorio');
    }

    if (!person.lastName?.trim()) {
      errors.push('Cognome  obbligatorio');
    }

    if (!person.taxCode?.trim()) {
      errors.push('Codice fiscale  obbligatorio');
    } else if (!isValidCodiceFiscale(person.taxCode)) {
      errors.push('Formato codice fiscale non valido');
    }

    // Validazione email se presente
    if (person.email && !this.isValidEmail(person.email)) {
      errors.push('Formato email non valido');
    }

    // Validazione data di nascita se presente
    if (person.birthDate && !DateFormattingService.isValidDate(person.birthDate)) {
      errors.push('Formato data di nascita non valido');
    }

    // Validazione telefono se presente
    if (person.phone && !this.isValidPhone(person.phone)) {
      errors.push('Formato telefono non valido');
    }

    // Validazione CAP se presente
    if (person.postalCode && !this.isValidPostalCode(person.postalCode)) {
      errors.push('Formato CAP non valido');
    }

    return errors;
  }

  /**
   * Valida un array di persone
   */
  static validatePersons(persons: PersonData[]): ImportValidationResult {
    const errors: ImportValidationResult = {};

    persons.forEach((person, index) => {
      // Estrae la data di nascita dal codice fiscale se non presente
      if (person.taxCode && !person.birthDate) {
        const extractedDate = DateFormattingService.extractBirthDateFromTaxCode(person.taxCode);
        if (extractedDate) {
          person.birthDate = extractedDate;
        }
      }

      // Formatta la data di nascita per la visualizzazione
      if (person.birthDate) {
        person.birthDate = DateFormattingService.formatDateForDisplay(person.birthDate);
      }

      const personErrors = this.validatePerson(person);
      if (personErrors.length > 0) {
        errors[index] = personErrors;
      }
    });

    return errors;
  }



  /**
   * Valida il formato email
   */
  static isValidEmail(email: string): boolean {
    if (!email) return false;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email.trim());
  }

  /**
   * Valida il formato telefono
   */
  static isValidPhone(phone: string): boolean {
    if (!phone) return false;
    // Accetta numeri con o senza prefisso internazionale, spazi, trattini, parentesi
    const phoneRegex = /^[\+]?[0-9\s\-\(\)]{6,20}$/;
    return phoneRegex.test(phone.trim());
  }

  /**
   * Valida il formato CAP italiano
   */
  static isValidPostalCode(postalCode: string): boolean {
    if (!postalCode) return false;
    const capRegex = /^[0-9]{5}$/;
    return capRegex.test(postalCode.trim());
  }

  /**
   * Verifica se ci sono errori critici che impediscono l'importazione
   */
  static hasCriticalErrors(validationResult: ImportValidationResult): boolean {
    return Object.keys(validationResult).length > 0;
  }

  /**
   * Ottiene un riassunto degli errori di validazione
   */
  static getValidationSummary(validationResult: ImportValidationResult): {
    totalErrors: number;
    affectedRows: number;
    errorTypes: { [errorType: string]: number };
  } {
    const totalErrors = Object.values(validationResult).reduce((sum, errors) => sum + errors.length, 0);
    const affectedRows = Object.keys(validationResult).length;
    const errorTypes: { [errorType: string]: number } = {};

    Object.values(validationResult).forEach(errors => {
      errors.forEach((error: string) => {
        errorTypes[error] = (errorTypes[error] || 0) + 1;
      });
    });

    return {
      totalErrors,
      affectedRows,
      errorTypes
    };
  }

  /**
   * Filtra le righe valide da quelle con errori
   */
  static filterValidRows(persons: PersonData[], validationResult: ImportValidationResult): {
    validRows: PersonData[];
    invalidRows: PersonData[];
    validIndices: number[];
    invalidIndices: number[];
  } {
    const validRows: PersonData[] = [];
    const invalidRows: PersonData[] = [];
    const validIndices: number[] = [];
    const invalidIndices: number[] = [];

    persons.forEach((person, index) => {
      if (validationResult[index]) {
        invalidRows.push(person);
        invalidIndices.push(index);
      } else {
        validRows.push(person);
        validIndices.push(index);
      }
    });

    return {
      validRows,
      invalidRows,
      validIndices,
      invalidIndices
    };
  }
}

/**
 * Servizio di throttling per gestire le richieste API
 * Previene ERR_INSUFFICIENT_RESOURCES limitando le richieste simultanee
 */

interface PendingRequest {
  url: string;
  resolve: () => Promise<void>;
  reject: (error: any) => void;
  timestamp: number;
  priority: number;
}

class RequestThrottler {
  private maxConcurrentRequests = 3; // Massimo 3 richieste simultanee
  private activeRequests = new Set<string>();
  private pendingQueue: PendingRequest[] = [];
  private requestCounts = new Map<string, number>();
  private lastRequestTime = new Map<string, number>();
  private readonly minInterval = 100; // Minimo 100ms tra richieste dello stesso tipo

  /**
   * Aggiunge una richiesta alla coda con throttling
   */
  async throttleRequest<T>(
    url: string, 
    requestFn: () => Promise<T>,
    priority: number = 1
  ): Promise<T> {
    const requestKey = this.getRequestKey(url);

    // Le richieste di autenticazione hanno priorit massima e non vengono mai throttled
    if (requestKey.startsWith('auth-')) {
      console.log(` RequestThrottler: Auth request detected for ${url}, executing immediately`);
      return this.executeRequest(url, requestFn);
    }

    // Le richieste di permessi e ruoli sono critiche e non devono essere throttled
    if (requestKey.startsWith('roles-') || requestKey.startsWith('permissions-') || requestKey.includes('permissions')) {
      console.log(` RequestThrottler: Critical permissions request detected for ${url}, executing immediately`);
      return this.executeRequest(url, requestFn);
    }

    // Controlla se c' gi una richiesta identica in corso
    if (this.activeRequests.has(requestKey)) {
      console.log(` RequestThrottler: Duplicate request detected for ${url}, waiting...`);
      await this.waitForRequest(requestKey);
    }

    // Controlla rate limiting per tipo di richiesta
    if (this.shouldThrottle(requestKey)) {
      console.log(` RequestThrottler: Rate limiting ${url}, queuing...`);
      return this.queueRequest(url, requestFn, priority);
    }

    return this.executeRequest(url, requestFn);
  }

  /**
   * Esegue una richiesta immediatamente
   */
  private async executeRequest<T>(url: string, requestFn: () => Promise<T>): Promise<T> {
    const requestKey = this.getRequestKey(url);

    try {
      this.activeRequests.add(requestKey);
      this.updateRequestStats(requestKey);

      console.log(` RequestThrottler: Executing ${url} (active: ${this.activeRequests.size})`);

      const result = await requestFn();

      console.log(` RequestThrottler: Completed ${url}`);
      return result;
    } catch (error: any) {
      console.error(` RequestThrottler: Failed ${url}:`, error.message);

      // Se  un errore di risorse insufficienti, aumenta il throttling
      if (error.code === 'ERR_INSUFFICIENT_RESOURCES') {
        this.handleResourceError(requestKey);
      }

      throw error;
    } finally {
      this.activeRequests.delete(requestKey);
      this.processQueue();
    }
  }

  /**
   * Aggiunge una richiesta alla coda
   */
  private queueRequest<T>(
    url: string, 
    requestFn: () => Promise<T>,
    priority: number
  ): Promise<T> {
    return new Promise((resolve, reject) => {
      const request: PendingRequest = {
        url,
        resolve: async () => {
          try {
            const result = await this.executeRequest(url, requestFn);
            resolve(result);
          } catch (error) {
            reject(error);
          }
        },
        reject,
        timestamp: Date.now(),
        priority
      };

      // Inserisce nella coda ordinata per priorit
      const insertIndex = this.pendingQueue.findIndex(r => r.priority < priority);
      if (insertIndex === -1) {
        this.pendingQueue.push(request);
      } else {
        this.pendingQueue.splice(insertIndex, 0, request);
      }

      console.log(` RequestThrottler: Queued ${url} (queue size: ${this.pendingQueue.length})`);
    });
  }

  /**
   * Processa la coda delle richieste in attesa
   */
  private processQueue(): void {
    if (this.pendingQueue.length === 0) return;
    if (this.activeRequests.size >= this.maxConcurrentRequests) return;

    const nextRequest = this.pendingQueue.shift();
    if (nextRequest) {
      // Controlla se la richiesta non  scaduta (timeout di 30 secondi)
      if (Date.now() - nextRequest.timestamp > 30000) {
        nextRequest.reject(new Error('Request timeout'));
        this.processQueue();
        return;
      }

      const requestKey = this.getRequestKey(nextRequest.url);
      if (!this.shouldThrottle(requestKey)) {
        // CORREZIONE CRITICA: Esegui la richiesta chiamando la funzione resolve asincrona
        setTimeout(async () => {
          await nextRequest.resolve();
        }, 0);
      } else {
        // Rimette in coda se ancora throttled
        this.pendingQueue.unshift(nextRequest);
        setTimeout(() => this.processQueue(), this.minInterval);
      }
    }
  }

  /**
   * Determina se una richiesta deve essere throttled
   */
  private shouldThrottle(requestKey: string): boolean {
    // Le richieste di autenticazione non devono mai essere throttled
    if (requestKey.startsWith('auth-')) {
      return false;
    }

    // Le richieste di permessi e ruoli non devono mai essere throttled
    if (requestKey.startsWith('roles-') || requestKey.startsWith('permissions-') || requestKey.includes('permissions')) {
      return false;
    }

    const lastTime = this.lastRequestTime.get(requestKey) || 0;
    const timeSinceLastRequest = Date.now() - lastTime;

    return timeSinceLastRequest < this.minInterval;
  }

  /**
   * Aggiorna le statistiche delle richieste
   */
  private updateRequestStats(requestKey: string): void {
    this.lastRequestTime.set(requestKey, Date.now());
    const count = this.requestCounts.get(requestKey) || 0;
    this.requestCounts.set(requestKey, count + 1);
  }

  /**
   * Gestisce errori di risorse insufficienti
   */
  private handleResourceError(requestKey: string): void {
    // Riduce temporaneamente il numero di richieste simultanee
    this.maxConcurrentRequests = Math.max(1, this.maxConcurrentRequests - 1);

    console.warn(` RequestThrottler: Resource error, reducing concurrent requests to ${this.maxConcurrentRequests}`);

    // Ripristina dopo 10 secondi
    setTimeout(() => {
      this.maxConcurrentRequests = Math.min(3, this.maxConcurrentRequests + 1);
      console.log(` RequestThrottler: Restored concurrent requests to ${this.maxConcurrentRequests}`);
    }, 10000);
  }

  /**
   * Aspetta che una richiesta specifica sia completata
   */
  private async waitForRequest(requestKey: string): Promise<void> {
    return new Promise((resolve) => {
      const checkInterval = setInterval(() => {
        if (!this.activeRequests.has(requestKey)) {
          clearInterval(checkInterval);
          resolve();
        }
      }, 50);

      // Timeout dopo 5 secondi
      setTimeout(() => {
        clearInterval(checkInterval);
        resolve();
      }, 5000);
    });
  }

  /**
   * Genera una chiave per identificare il tipo di richiesta
   */
  private getRequestKey(url: string): string {
    // Normalizza l'URL per raggruppare richieste simili
    const cleanUrl = url.split('?')[0]; // Rimuove query parameters

    // Raggruppa per tipo di endpoint
    // PRIORIT ALTA: Richieste di autenticazione (non devono essere throttled)
    if (cleanUrl.includes('/auth/login')) return 'auth-login';
    if (cleanUrl.includes('/auth/logout')) return 'auth-logout';
    if (cleanUrl.includes('/auth/refresh')) return 'auth-refresh';
    if (cleanUrl.includes('/auth/verify')) return 'auth-verify';
    if (cleanUrl.includes('/auth/')) return 'auth-other';

    // PRIORIT ALTA: Richieste di permessi e ruoli (critiche per il funzionamento)
    if (cleanUrl.includes('/roles/') && cleanUrl.includes('/permissions')) return 'permissions-role-specific';
    if (cleanUrl.includes('/advanced-permissions')) return 'permissions-advanced';
    if (cleanUrl.includes('/permissions')) return 'permissions-general';
    if (cleanUrl.includes('/roles/hierarchy')) return 'roles-hierarchy';
    if (cleanUrl.includes('/roles')) return 'roles-general';

    // Altri endpoint
    if (cleanUrl.includes('/tenants')) return 'tenants';
    if (cleanUrl.includes('/users')) return 'users';

    return cleanUrl;
  }

  /**
   * Ottiene statistiche del throttler
   */
  getStats() {
    return {
      activeRequests: this.activeRequests.size,
      queuedRequests: this.pendingQueue.length,
      maxConcurrentRequests: this.maxConcurrentRequests,
      requestCounts: Object.fromEntries(this.requestCounts),
      lastRequestTimes: Object.fromEntries(this.lastRequestTime)
    };
  }

  /**
   * Resetta il throttler
   */
  reset(): void {
    this.activeRequests.clear();
    this.pendingQueue.length = 0;
    this.requestCounts.clear();
    this.lastRequestTime.clear();
    this.maxConcurrentRequests = 3;
    console.log(' RequestThrottler: Reset completed');
  }
}

// Istanza singleton
export const requestThrottler = new RequestThrottler();

/**
 * Wrapper per le chiamate API con throttling automatico
 */
export async function throttledApiCall<T>(
  url: string,
  requestFn: () => Promise<T>,
  priority: number = 1
): Promise<T> {
  return requestThrottler.throttleRequest(url, requestFn, priority);
}

export default requestThrottler;

/**
 * Servizio per la gestione della gerarchia dei ruoli
 * Implementa i filtri per employees e trainers secondo le regole del progetto
 */

export interface PersonRole {
  id: string;
  roleType: string;
  isActive: boolean;
  company?: {
    id: string;
    ragioneSociale: string;
  };
  assignedAt: string;
}

export interface Person {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  phone?: string;
  taxCode?: string;
  roles: PersonRole[];
  company?: {
    id: string;
    ragioneSociale: string;
  };
  status: 'ACTIVE' | 'INACTIVE' | 'PENDING';
  deletedAt?: string;
  createdAt: string;
  updatedAt: string;
}

export interface FilterConfig {
  minRoleLevel: number;
  maxRoleLevel: number;
  roleTypes?: string[];
}

/**
 * Gerarchia dei ruoli secondo il planning dettagliato
 * Livelli pi bassi = maggiore autorit
 */
export const ROLE_HIERARCHY: Record<string, number> = {
  'SUPER_ADMIN': 0,
  'ADMIN': 1,
  'COMPANY_ADMIN': 2,        // "Responsabile Aziendale"
  'HR_MANAGER': 3,
  'MANAGER': 3,
  'TRAINER_COORDINATOR': 4,  // "Coordinatore Formatori"
  'SENIOR_TRAINER': 5,
  'TRAINER': 6,
  'EXTERNAL_TRAINER': 6,
  'EMPLOYEE': 8
};

/**
 * Configurazioni predefinite per i filtri
 */
export const FILTER_CONFIGS = {
  // Employees: COMPANY_ADMIN (2) o inferiore (livelli pi alti)
  employees: {
    minRoleLevel: 2,
    maxRoleLevel: 8,
    roleTypes: ['COMPANY_ADMIN', 'HR_MANAGER', 'MANAGER', 'TRAINER_COORDINATOR', 'SENIOR_TRAINER', 'TRAINER', 'EMPLOYEE'] as string[]
  } as FilterConfig,

  // Trainers: TRAINER_COORDINATOR (4) o inferiore (livelli pi alti)
  trainers: {
    minRoleLevel: 4,
    maxRoleLevel: 6,
    roleTypes: ['TRAINER_COORDINATOR', 'SENIOR_TRAINER', 'TRAINER', 'EXTERNAL_TRAINER'] as string[]
  } as FilterConfig
};

/**
 * Filtra le persone in base alla gerarchia dei ruoli
 * Una persona appare nel filtro se ha ALMENO UN ruolo attivo nel range specificato
 */
export const filterPersonsByRoleLevel = (
  persons: Person[], 
  minLevel: number, 
  maxLevel: number
): Person[] => {
  return persons.filter(person => 
    person.roles.some(role => {
      if (!role.isActive) return false;

      const level = ROLE_HIERARCHY[role.roleType];
      return level !== undefined && level >= minLevel && level <= maxLevel;
    })
  );
};

/**
 * Filtra le persone per il ramo "Employees"
 * Include persone con ruoli da COMPANY_ADMIN in gi
 */
export const filterEmployees = (persons: Person[]): Person[] => {
  const config = FILTER_CONFIGS.employees;
  return filterPersonsByRoleLevel(persons, config.minRoleLevel, config.maxRoleLevel);
};

/**
 * Filtra le persone per il ramo "Trainers"
 * Include persone con ruoli da TRAINER_COORDINATOR in gi
 */
export const filterTrainers = (persons: Person[]): Person[] => {
  const config = FILTER_CONFIGS.trainers;
  return filterPersonsByRoleLevel(persons, config.minRoleLevel, config.maxRoleLevel);
};

/**
 * Ottiene il nome visualizzato per un ruolo
 */
export const getRoleDisplayName = (roleType: string): string => {
  const roleNames: Record<string, string> = {
    'SUPER_ADMIN': 'Super Admin',
    'ADMIN': 'Amministratore',
    'COMPANY_ADMIN': 'Responsabile Aziendale',
    'HR_MANAGER': 'Manager HR',
    'MANAGER': 'Manager',
    'TRAINER_COORDINATOR': 'Coordinatore Formatori',
    'SENIOR_TRAINER': 'Formatore Senior',
    'TRAINER': 'Formatore',
    'EXTERNAL_TRAINER': 'Formatore Esterno',
    'EMPLOYEE': 'Dipendente'
  };

  return roleNames[roleType] || roleType;
};

/**
 * Ottiene il roleType dal nome visualizzato (mappatura inversa)
 */
export const getRoleTypeFromDisplayName = (displayName: string): string => {
  const displayNameToRoleType: Record<string, string> = {
    'Super Admin': 'SUPER_ADMIN',
    'Amministratore': 'ADMIN',
    'Responsabile Aziendale': 'COMPANY_ADMIN',
    'Manager HR': 'HR_MANAGER',
    'Manager': 'MANAGER',
    'Coordinatore Formatori': 'TRAINER_COORDINATOR',
    'Formatore Senior': 'SENIOR_TRAINER',
    'Formatore': 'TRAINER',
    'Formatore Esterno': 'EXTERNAL_TRAINER',
    'Dipendente': 'EMPLOYEE'
  };

  return displayNameToRoleType[displayName] || displayName;
};

/**
 * Ottiene il livello di un ruolo
 */
export const getRoleLevel = (roleType: string): number => {
  return ROLE_HIERARCHY[roleType] ?? 999;
};

/**
 * Verifica se una persona ha un ruolo specifico attivo
 */
export const hasActiveRole = (person: Person, roleType: string): boolean => {
  return person.roles.some(role => role.roleType === roleType && role.isActive);
};

/**
 * Ottiene tutti i ruoli attivi di una persona
 */
export const getActiveRoles = (person: Person): PersonRole[] => {
  if (!person.roles || !Array.isArray(person.roles)) {
    return [];
  }
  return person.roles.filter(role => role.isActive);
};

/**
 * Ottiene il ruolo con il livello pi alto (numero pi basso) di una persona
 */
export const getHighestRole = (person: Person): PersonRole | null => {
  const activeRoles = getActiveRoles(person);
  if (activeRoles.length === 0) return null;

  return activeRoles.reduce((highest, current) => {
    const currentLevel = getRoleLevel(current.roleType);
    const highestLevel = getRoleLevel(highest.roleType);
    return currentLevel < highestLevel ? current : highest;
  });
};

/**
 * Verifica se una persona rientra in entrambi i rami (employees e trainers)
 */
export function isInBothBranches(person: Person): boolean {
  const employeeRoleTypes = FILTER_CONFIGS.employees?.roleTypes;
  const trainerRoleTypes = FILTER_CONFIGS.trainers?.roleTypes;

  if (!employeeRoleTypes || !trainerRoleTypes) {
    return false;
  }

  const activeRoles = getActiveRoles(person);
  const hasEmployeeRole = activeRoles.some(role => 
    employeeRoleTypes.includes(role.roleType as any)
  );
  const hasTrainerRole = activeRoles.some(role => 
    trainerRoleTypes.includes(role.roleType as any)
  );

  return hasEmployeeRole && hasTrainerRole;
}

/**
 * Applica un filtro personalizzato alle persone
 */
export const applyCustomFilter = (persons: Person[], filterConfig: FilterConfig): Person[] => {
  return filterPersonsByRoleLevel(persons, filterConfig.minRoleLevel, filterConfig.maxRoleLevel);
};

import { apiGet, apiDelete } from './api';

interface Role {
  type: string;
  name: string;
  description: string;
  userCount: number;
  persons?: Person[];
  permissions?: string[];
}

interface Person {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
}

interface Permission {
  id: string;
  name: string;
  category: string;
  description: string;
  resource?: string;
  action?: string;
  entity?: string; // Aggiungo il campo entity
  scope?: 'all' | 'own';
}

interface RolePermissionUpdate {
  roleType: string;
  permissions: {
    [entity: string]: {
      [action: string]: {
        granted: boolean;
        scope?: 'all' | 'own';
        fields?: string[];
      }
    }
  };
}

// Nuove interfacce per la gerarchia dei ruoli
interface RoleHierarchyLevel {
  level: number;
