import React, { useState } from 'react';
import { Button } from '../../../design-system/atoms/Button';
import { Dropdown, DropdownAction } from '../../../design-system/molecules/Dropdown';
import { Modal } from '../../../design-system/molecules/Modal';
import { ChevronDown, Trash2, Download, Archive } from 'lucide-react';
import { toast } from 'react-hot-toast';
import { GDPRPermissions } from '../hooks/useGDPRPermissions';

export interface BatchOperation {
  key: string;
  label: string;
  icon?: React.ReactNode;
  variant?: 'default' | 'danger' | 'secondary' | 'primary';
  requiresConfirmation?: boolean;
  confirmationTitle?: string;
  confirmationDescription?: string;
  permission?: string;
  action: (selectedIds: string[]) => Promise<void>;
}

export interface BatchOperationsProps {
  selectedEntities: Set<string>;
  permissions: GDPRPermissions;
  onClearSelection: () => void;
  entityName: string;
  entityNamePlural: string;
  customOperations?: BatchOperation[];
}

/**
 * Componente per le operazioni batch sulle entit GDPR
 * Fornisce azioni comuni come eliminazione, esportazione, archiviazione
 */
export function BatchOperations({
  selectedEntities,
  permissions,
  onClearSelection,
  entityName,
  entityNamePlural,
  customOperations = []
}: BatchOperationsProps) {
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [currentOperation, setCurrentOperation] = useState<BatchOperation | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);

  const selectedCount = selectedEntities.size;
  const selectedIds = Array.from(selectedEntities);

  // Operazioni predefinite
  const defaultOperations: BatchOperation[] = [
    {
      key: 'export',
      label: `Esporta ${selectedCount} ${selectedCount === 1 ? entityName : entityNamePlural}`,
      icon: <Download className="h-4 w-4" />,
      variant: 'secondary',
      permission: 'export',
      action: async (ids: string[]) => {
        try {
          const response = await fetch('/api/export/batch', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ ids, type: entityName })
          });

          if (!response.ok) throw new Error('Errore durante l\'esportazione');

          const blob = await response.blob();
          const url = window.URL.createObjectURL(blob);
          const a = document.createElement('a');
          a.href = url;
          a.download = `${entityNamePlural}_${new Date().toISOString().split('T')[0]}.csv`;
          document.body.appendChild(a);
          a.click();
          window.URL.revokeObjectURL(url);
          document.body.removeChild(a);

          toast.success(`${ids.length} ${ids.length === 1 ? entityName : entityNamePlural} esportati con successo`);
        } catch (error) {
          toast.error('Errore durante l\'esportazione');
          throw error;
        }
      }
    },
    {
      key: 'archive',
      label: `Archivia ${selectedCount} ${selectedCount === 1 ? entityName : entityNamePlural}`,
      icon: <Archive className="h-4 w-4" />,
      variant: 'secondary',
      requiresConfirmation: true,
      confirmationTitle: 'Conferma archiviazione',
      confirmationDescription: `Sei sicuro di voler archiviare ${selectedCount} ${selectedCount === 1 ? entityName : entityNamePlural}? Questa azione pu essere annullata.`,
      permission: 'update',
      action: async (ids: string[]) => {
        try {
          const response = await fetch('/api/batch/archive', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ ids, type: entityName })
          });

          if (!response.ok) throw new Error('Errore durante l\'archiviazione');

          toast.success(`${ids.length} ${ids.length === 1 ? entityName : entityNamePlural} archiviati con successo`);
        } catch (error) {
          toast.error('Errore durante l\'archiviazione');
          throw error;
        }
      }
    },
    {
      key: 'delete',
      label: `Elimina ${selectedCount} ${selectedCount === 1 ? entityName : entityNamePlural}`,
      icon: <Trash2 className="h-4 w-4" />,
      variant: 'danger',
      requiresConfirmation: true,
      confirmationTitle: 'Conferma eliminazione',
      confirmationDescription: `Sei sicuro di voler eliminare definitivamente ${selectedCount} ${selectedCount === 1 ? entityName : entityNamePlural}? Questa azione non pu essere annullata.`,
      permission: 'delete',
      action: async (ids: string[]) => {
        try {
          const response = await fetch('/api/batch/delete', {
            method: 'DELETE',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ ids, type: entityName })
          });

          if (!response.ok) throw new Error('Errore durante l\'eliminazione');

          toast.success(`${ids.length} ${ids.length === 1 ? entityName : entityNamePlural} eliminati con successo`);
        } catch (error) {
          toast.error('Errore durante l\'eliminazione');
          throw error;
        }
      }
    }
  ];

  // Combina operazioni predefinite e personalizzate
  const allOperations = [...defaultOperations, ...customOperations];

  // Filtra operazioni in base ai permessi
  const availableOperations = allOperations.filter(operation => {
    if (!operation.permission) return true;

    switch (operation.permission) {
      case 'export':
        return permissions.canExport;
      case 'delete':
        return permissions.canDelete;
      case 'update':
        return permissions.canUpdate;
      default:
        return permissions.checkPermission(operation.permission);
    }
  });

  // Converte le operazioni in azioni per il dropdown
  const dropdownActions: DropdownAction[] = availableOperations.map(operation => ({
    label: operation.label,
    icon: operation.icon,
    variant: operation.variant,
    onClick: () => handleOperation(operation)
  }));

  // Gestione esecuzione operazione
  const handleOperation = async (operation: BatchOperation) => {
    if (operation.requiresConfirmation) {
      setCurrentOperation(operation);
      setShowConfirmModal(true);
      return;
    }

    await executeOperation(operation);
  };

  // Esecuzione effettiva dell'operazione
  const executeOperation = async (operation: BatchOperation) => {
    setIsProcessing(true);

    try {
      await operation.action(selectedIds);
      onClearSelection();
    } catch (error) {
      console.error(`Errore nell'operazione ${operation.key}:`, error);
    } finally {
      setIsProcessing(false);
      setShowConfirmModal(false);
      setCurrentOperation(null);
    }
  };

  // Gestione conferma modal
  const handleConfirm = () => {
    if (currentOperation) {
      executeOperation(currentOperation);
    }
  };

  const handleCancel = () => {
    setShowConfirmModal(false);
    setCurrentOperation(null);
  };

  if (selectedCount === 0) {
    return null;
  }

  return (
    <>
      <div className="flex items-center gap-2 p-4 bg-blue-50 border border-blue-200 rounded-lg">
        <span className="text-sm font-medium text-blue-900">
          {selectedCount} {selectedCount === 1 ? entityName : entityNamePlural} selezionati
        </span>

        <div className="flex items-center gap-2 ml-auto">
          <Button
            variant="outline"
            size="sm"
            onClick={onClearSelection}
          >
            Deseleziona tutto
          </Button>

          {availableOperations.length > 0 && (
            <Dropdown
              label="Azioni batch"
              variant="primary"
              actions={dropdownActions}
              showArrow={true}
              pill={true}
            />
          )}
        </div>
      </div>

      {/* Modal di conferma */}
      <Modal
        isOpen={showConfirmModal}
        onClose={handleCancel}
        title={currentOperation?.confirmationTitle || 'Conferma operazione'}
        size="md"
        footer={
          <div className="flex justify-end gap-2">
            <Button
              variant="outline"
              onClick={handleCancel}
              disabled={isProcessing}
            >
              Annulla
            </Button>
            <Button
               variant={currentOperation?.variant === 'danger' ? 'destructive' : 'primary'}
               onClick={handleConfirm}
               disabled={isProcessing}
             >
              {isProcessing ? 'Elaborazione...' : 'Conferma'}
            </Button>
          </div>
        }
      >
        <p className="text-gray-600">
          {currentOperation?.confirmationDescription || 
           `Sei sicuro di voler eseguire questa operazione su ${selectedCount} ${selectedCount === 1 ? entityName : entityNamePlural}?`}
        </p>
      </Modal>
    </>
  );
}

import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../../design-system/molecules/Card';
import { Badge } from '../../../design-system/atoms/Badge';
import { Button } from '../../../design-system/atoms/Button';
import { Eye, Edit, Trash2, Download } from 'lucide-react';

/**
 * Componente per il rendering delle card nella vista griglia
 * Gestisce la visualizzazione delle entit in formato card con azioni
 */
export interface CardConfig<T> {
  title: string | ((entity: T) => string);
  subtitle?: string | ((entity: T) => string);
  description?: string | ((entity: T) => string);
  badge?: {
    field: string | ((entity: T) => string);
    variant?: 'default' | 'secondary' | 'destructive' | 'outline';
  };
  icon?: React.ComponentType<{ className?: string }>;
}

export interface EntityCardProps<T> {
  entity: T;
  config: CardConfig<T>;
  isSelected: boolean;
  onSelect: (id: string) => void;
  onView?: (entity: T) => void;
  onEdit?: (entity: T) => void;
  onDelete?: (id: string) => void;
  onExport?: (entity: T) => void;
  showActions: {
    view: boolean;
    edit: boolean;
    delete: boolean;
    export: boolean;
  };
  selectionMode: boolean;
}

export const EntityCard = <T extends Record<string, any>>({
  entity,
  config,
  isSelected,
  onSelect,
  onView,
  onEdit,
  onDelete,
  onExport,
  showActions,
  selectionMode
}: EntityCardProps<T>) => {
  // Helper per ottenere il valore da una configurazione
  const getValue = (value: string | ((entity: T) => string)): string => {
    return typeof value === 'function' ? value(entity) : entity[value] || '';
  };

  // Gestione click sulla card
  const handleCardClick = () => {
    if (selectionMode) {
      onSelect(entity.id);
    } else if (onView) {
      onView(entity);
    }
  };

  return (
    <Card 
      className={`cursor-pointer transition-all hover:shadow-md ${
        isSelected ? 'ring-2 ring-blue-500' : ''
      }`}
      onClick={handleCardClick}
    >
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <CardTitle className="text-lg font-semibold">
              {getValue(config.title)}
            </CardTitle>
            {config.subtitle && (
              <p className="text-sm text-muted-foreground mt-1">
                {getValue(config.subtitle)}
              </p>
            )}
          </div>

          {/* Icona e badge */}
          <div className="flex items-center gap-2">
            {config.icon && (
              <config.icon className="h-5 w-5 text-muted-foreground" />
            )}
            {config.badge && (
              <Badge variant={config.badge.variant || 'default'}>
                {getValue(config.badge.field)}
              </Badge>
            )}
          </div>
        </div>
      </CardHeader>

      <CardContent className="pt-0">
        {/* Descrizione */}
        {config.description && (
          <p className="text-sm text-muted-foreground mb-4">
            {getValue(config.description)}
          </p>
        )}

        {/* Azioni */}
        <div className="flex items-center justify-between">
          {/* Checkbox per selezione */}
          {selectionMode && (
            <input
              type="checkbox"
              checked={isSelected}
              onChange={() => onSelect(entity.id)}
              onClick={(e: React.MouseEvent) => e.stopPropagation()}
              className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
            />
          )}

          {/* Pulsanti azioni */}
          <div className="flex gap-1 ml-auto">
            {showActions.view && onView && (
              <Button
                variant="ghost"
                size="sm"
                onClick={(e: React.MouseEvent) => {
                  e.stopPropagation();
                  onView(entity);
                }}
                title="Visualizza"
              >
                <Eye className="h-4 w-4" />
              </Button>
            )}

            {showActions.edit && onEdit && (
              <Button
                variant="ghost"
                size="sm"
                onClick={(e: React.MouseEvent) => {
                  e.stopPropagation();
                  onEdit(entity);
                }}
                title="Modifica"
              >
                <Edit className="h-4 w-4" />
              </Button>
            )}

            {showActions.delete && onDelete && (
              <Button
                variant="ghost"
                size="sm"
                onClick={(e: React.MouseEvent) => {
                  e.stopPropagation();
                  onDelete(entity.id);
                }}
                title="Elimina"
                className="text-destructive hover:text-destructive"
              >
                <Trash2 className="h-4 w-4" />
              </Button>
            )}

            {showActions.export && onExport && (
              <Button
                variant="ghost"
                size="sm"
                onClick={(e: React.MouseEvent) => {
                  e.stopPropagation();
                  onExport(entity);
                }}
                title="Esporta"
              >
                <Download className="h-4 w-4" />
              </Button>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
};

import React from 'react';
import { Button } from '../../../design-system/atoms/Button';
import { Badge } from '../../../design-system/atoms/Badge';
import { Search, Filter, X, ChevronDown } from 'lucide-react';

/**
 * Componente per la barra di ricerca e filtri avanzati
 * Gestisce ricerca testuale e filtri multipli
 */
export interface FilterOption {
  key: string;
  label: string;
  options: Array<{ label: string; value: string }>;
}

export interface EntitySearchBarProps {
  searchQuery: string;
  onSearchChange: (query: string) => void;
  activeFilters: Record<string, string>;
  onFilterChange: (filters: Record<string, string>) => void;
  filterOptions: FilterOption[];
  onClearFilters: () => void;
  hasActiveFilters: boolean;
  placeholder?: string;
}

export const EntitySearchBar: React.FC<EntitySearchBarProps> = ({
  searchQuery,
  onSearchChange,
  activeFilters,
  onFilterChange,
  filterOptions,
  onClearFilters,
  hasActiveFilters,
  placeholder = "Cerca..."
}) => {
  // Gestione cambio filtro
  const handleFilterChange = (filterKey: string, value: string) => {
    onFilterChange({
      ...activeFilters,
      [filterKey]: value
    });
  };

  // Conta filtri attivi
  const activeFilterCount = Object.values(activeFilters).filter(value => value !== '').length;

  return (
    <div className="space-y-4">
      {/* Barra di ricerca principale */}
      <div className="relative">
        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
        <input
          type="text"
          placeholder={placeholder}
          value={searchQuery}
          onChange={(e) => onSearchChange(e.target.value)}
          className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        />
        {searchQuery && (
          <button
            onClick={() => onSearchChange('')}
            className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
          >
            <X className="h-4 w-4" />
          </button>
        )}
      </div>

      {/* Filtri avanzati */}
      {filterOptions.length > 0 && (
        <div className="flex flex-wrap gap-2 items-center">
          <div className="flex items-center gap-1 text-sm text-gray-600">
            <Filter className="h-4 w-4" />
            <span>Filtri:</span>
          </div>

          {filterOptions.map((filter) => (
            <div key={filter.key} className="relative">
              <select
                value={activeFilters[filter.key] || ''}
                onChange={(e) => handleFilterChange(filter.key, e.target.value)}
                className="appearance-none bg-white border border-gray-300 rounded px-3 py-1 pr-8 text-sm focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="">{filter.label}</option>
                {filter.options.map((option) => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
              <ChevronDown className="absolute right-2 top-1/2 transform -translate-y-1/2 h-3 w-3 text-gray-400 pointer-events-none" />
            </div>
          ))}

          {/* Badge contatore filtri attivi */}
          {activeFilterCount > 0 && (
            <Badge variant="secondary" className="text-xs">
              {activeFilterCount} filtri attivi
            </Badge>
          )}

          {/* Pulsante pulisci filtri */}
          {hasActiveFilters && (
            <Button
              variant="ghost"
              size="sm"
              onClick={onClearFilters}
              className="text-xs"
            >
              <X className="h-3 w-3 mr-1" />
              Pulisci tutto
            </Button>
          )}
        </div>
      )}

      {/* Indicatori filtri attivi */}
      {activeFilterCount > 0 && (
        <div className="flex flex-wrap gap-1">
          {Object.entries(activeFilters)
            .filter(([_, value]) => value !== '')
            .map(([key, value]) => {
              const filter = filterOptions.find(f => f.key === key);
              const option = filter?.options.find(o => o.value === value);

              return (
                <Badge
                  key={key}
                  variant="outline"
                  className="text-xs flex items-center gap-1"
                >
                  <span>{filter?.label}: {option?.label || value}</span>
                  <button
                    onClick={() => handleFilterChange(key, '')}
                    className="ml-1 hover:text-red-600"
                  >
                    <X className="h-3 w-3" />
                  </button>
                </Badge>
              );
            })}
        </div>
      )}
    </div>
  );
};

import React from 'react';
import { Button } from '../../../design-system/atoms/Button';
import { 
  Table, 
  TableHeader, 
  TableBody, 
  TableRow, 
  TableHead, 
  TableCell 
} from '../../../design-system/molecules/Table';
import { Card, CardContent } from '../../../design-system/molecules/Card';
import { Grid, List, Eye, Edit, Trash2, Download } from 'lucide-react';
import { cn } from '../../../design-system/utils';
import { ColumnConfig } from '../hooks/useTableColumns';
import { GDPRPermissions } from '../hooks/useGDPRPermissions';

export type ViewMode = 'table' | 'grid';

export interface EntityViewProps<T> {
  entities: T[];
  viewMode: ViewMode;
  onViewModeChange: (mode: ViewMode) => void;
  columns: ColumnConfig[];
  permissions: GDPRPermissions;
  selectedEntities: Set<string>;
  onEntitySelect: (id: string) => void;
  onSelectAll: () => void;
  onClearSelection: () => void;
  onEntityAction: (action: string, entity: T) => void;
  loading?: boolean;
  error?: string | null;
  emptyMessage?: string;
  className?: string;
}

/**
 * Componente per la visualizzazione delle entit GDPR
 * Supporta modalit tabella e griglia con azioni personalizzabili
 */
export function EntityView<T extends { id: string; [key: string]: any }>({
  entities,
  viewMode,
  onViewModeChange,
  columns,
  permissions,
  selectedEntities,
  onEntitySelect,
  onSelectAll,
  onClearSelection,
  onEntityAction,
  loading = false,
  error = null,
  emptyMessage = 'Nessuna entit trovata',
  className
}: EntityViewProps<T>) {

  // Rendering della modalit tabella
  const renderTableView = () => {
    return (
      <Table className="w-full">
        <TableHeader>
          <TableRow>
            {/* Colonna di selezione */}
            {(permissions.canUpdate || permissions.canDelete) && (
              <TableHead className="w-12">
                <input
                  type="checkbox"
                  checked={entities.length > 0 && selectedEntities.size === entities.length}
                  onChange={() => {
                    if (selectedEntities.size === entities.length) {
                      onClearSelection();
                    } else {
                      onSelectAll();
                    }
                  }}
                  className="rounded border-gray-300"
                />
              </TableHead>
            )}

            {/* Colonne dati */}
            {columns.map((col) => (
              <TableHead key={col.key} style={{ width: col.width }}>
                {col.label}
              </TableHead>
            ))}

            {/* Colonna azioni */}
            <TableHead className="w-32">Azioni</TableHead>
          </TableRow>
        </TableHeader>

        <TableBody>
          {entities.map((entity) => (
            <TableRow key={entity.id}>
              {/* Checkbox di selezione */}
              {(permissions.canUpdate || permissions.canDelete) && (
                <TableCell>
                  <input
                    type="checkbox"
                    checked={selectedEntities.has(entity.id)}
                    onChange={() => onEntitySelect(entity.id)}
                    className="rounded border-gray-300"
                  />
                </TableCell>
              )}

              {/* Celle dati */}
              {columns.map((col) => {
                const value = entity[col.key];
                const displayValue = col.render 
                  ? col.render(value, entity)
                  : value?.toString() || '-';

                return (
                  <TableCell key={col.key}>
                    {displayValue}
                  </TableCell>
                );
              })}

              {/* Celle azioni */}
              <TableCell>
                <div className="flex items-center gap-1">
                  {permissions.canRead && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => onEntityAction('view', entity)}
                      title="Visualizza"
                    >
                      <Eye className="h-4 w-4" />
                    </Button>
                  )}
                  {permissions.canUpdate && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => onEntityAction('edit', entity)}
                      title="Modifica"
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                  )}
                  {permissions.canDelete && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => onEntityAction('delete', entity)}
                      title="Elimina"
                      className="text-red-600 hover:text-red-700"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  )}
                  {permissions.canExport && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => onEntityAction('export', entity)}
                      title="Esporta"
                    >
                      <Download className="h-4 w-4" />
                    </Button>
                  )}
                </div>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    );
  };

  // Rendering della modalit griglia
  const renderGridView = () => {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        {entities.map((entity) => (
          <Card
            key={entity.id}
            className={cn(
              'relative hover:shadow-md transition-shadow',
              selectedEntities.has(entity.id) && 'ring-2 ring-blue-500'
            )}
          >
            <CardContent className="p-4">
              {/* Checkbox di selezione */}
              {(permissions.canUpdate || permissions.canDelete) && (
                <div className="absolute top-2 left-2">
                  <input
                    type="checkbox"
                    checked={selectedEntities.has(entity.id)}
                    onChange={() => onEntitySelect(entity.id)}
                    className="rounded border-gray-300"
                  />
                </div>
              )}

              {/* Contenuto della card */}
              <div className="space-y-2">
                {columns
                  .filter(col => col.key !== 'actions' && col.key !== 'select')
                  .slice(0, 4) // Mostra solo i primi 4 campi
                  .map((col) => {
                    const value = entity[col.key];
                    const displayValue = col.render 
                      ? col.render(value, entity)
                      : value?.toString() || '-';

                    return (
                      <div key={col.key} className="text-sm">
                        <span className="font-medium text-gray-600">{col.label}:</span>
                        <span className="ml-2 text-gray-900">{displayValue}</span>
                      </div>
                    );
                  })}
              </div>

              {/* Azioni della card */}
              <div className="flex justify-end gap-1 mt-4 pt-2 border-t border-gray-200">
                {permissions.canRead && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEntityAction('view', entity)}
                    title="Visualizza"
                  >
                    <Eye className="h-4 w-4" />
                  </Button>
                )}
                {permissions.canUpdate && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEntityAction('edit', entity)}
                    title="Modifica"
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                )}
                {permissions.canDelete && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEntityAction('delete', entity)}
                    title="Elimina"
                    className="text-red-600 hover:text-red-700"
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                )}
                {permissions.canExport && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEntityAction('export', entity)}
                    title="Esporta"
                  >
                    <Download className="h-4 w-4" />
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  };

  // Rendering degli stati di caricamento ed errore
  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        <span className="ml-2 text-gray-600">Caricamento...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="text-red-600 text-lg font-medium mb-2">Errore</div>
          <div className="text-gray-600">{error}</div>
        </div>
      </div>
    );
  }

  if (entities.length === 0) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="text-gray-400 text-lg font-medium mb-2">Nessun risultato</div>
          <div className="text-gray-500">{emptyMessage}</div>
        </div>
      </div>
    );
  }

  return (
    <div className={cn('space-y-4', className)}>
      {/* Toggle modalit visualizzazione */}
      <div className="flex justify-end">
        <div className="flex rounded-lg border border-gray-300 p-1">
          <Button
            variant={viewMode === 'table' ? 'primary' : 'ghost'}
            size="sm"
            onClick={() => onViewModeChange('table')}
            className="rounded-md"
          >
            <List className="h-4 w-4" />
            <span className="ml-1 hidden sm:inline">Tabella</span>
          </Button>
          <Button
            variant={viewMode === 'grid' ? 'primary' : 'ghost'}
            size="sm"
            onClick={() => onViewModeChange('grid')}
            className="rounded-md"
          >
            <Grid className="h-4 w-4" />
            <span className="ml-1 hidden sm:inline">Griglia</span>
          </Button>
        </div>
      </div>

      {/* Contenuto principale */}
      {viewMode === 'table' ? renderTableView() : renderGridView()}
    </div>
  );
}

/**
 * GDPRAuditPanel - Pannello per visualizzare e gestire l'audit trail GDPR
 * 
 * Componente che mostra la cronologia delle azioni GDPR per un'entit,
 * permettendo di visualizzare, filtrare e esportare i log di audit.
 * 
 * @version 1.0
 * @date 30 Gennaio 2025
 */

import React, { useState, useEffect } from 'react';
import { 
  Calendar,
  Download,
  Eye,
  Filter,
  Search
} from 'lucide-react';
import { GDPRAuditLogEntry, GDPRAuditAction } from '../types/gdpr.types';
import { Button } from '../../../design-system/atoms/Button';
import { Card } from '../../../design-system/molecules/Card';
import { SearchBar } from '../../../design-system/molecules/SearchBar';
import { cn } from '../../../design-system/utils';

export interface GDPRAuditPanelProps {
  /** ID dell'entit per cui mostrare l'audit trail */
  entityId: string;

  /** Tipo di entit */
  entityType: string;

  /** Log di audit da visualizzare */
  auditLogs?: GDPRAuditLogEntry[];

  /** Callback per caricare pi log */
  onLoadMore?: () => void;

  /** Callback per esportare i log */
  onExport?: (logs: GDPRAuditLogEntry[]) => void;

  /** Stato di caricamento */
  loading?: boolean;

  /** Classi CSS personalizzate */
  className?: string;
}

export const GDPRAuditPanel: React.FC<GDPRAuditPanelProps> = ({
  entityId,
  entityType,
  auditLogs,
  onLoadMore,
  onExport,
  loading = false,
  className = ''
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedAction, setSelectedAction] = useState<GDPRAuditAction | 'all'>('all');
  const [expandedLog, setExpandedLog] = useState<string | null>(null);

  // Filtra i log in base ai criteri di ricerca
  const filteredLogs = (auditLogs || []).filter(log => {
    const matchesSearch = searchTerm === '' || 
      log.action.toLowerCase().includes(searchTerm.toLowerCase()) ||
      log.ipAddress?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      log.userAgent?.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesAction = selectedAction === 'all' || log.action === selectedAction;

    return matchesSearch && matchesAction;
  });

  // Formatta la data per la visualizzazione
  const formatDate = (date: Date) => {
    return new Intl.DateTimeFormat('it-IT', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    }).format(new Date(date));
  };

  // Ottieni il colore per il tipo di azione
  const getActionColor = (action: GDPRAuditAction) => {
    switch (action) {
      case 'VIEW':
        return 'bg-blue-100 text-blue-800';
      case 'CREATE':
        return 'bg-green-100 text-green-800';
      case 'UPDATE':
        return 'bg-yellow-100 text-yellow-800';
      case 'DELETE':
        return 'bg-red-100 text-red-800';
      case 'EXPORT':
        return 'bg-purple-100 text-purple-800';
      case 'CONSENT_GIVEN':
        return 'bg-emerald-100 text-emerald-800';
      case 'CONSENT_REVOKED':
        return 'bg-orange-100 text-orange-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Gestisce l'espansione/contrazione dei dettagli del log
  const toggleLogExpansion = (logId: string) => {
    setExpandedLog(expandedLog === logId ? null : logId);
  };

  return (
    <Card className={cn('p-6', className)}>
      {/* Header del pannello */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-2">
          <Calendar className="h-5 w-5 text-gray-500" />
          <h3 className="text-lg font-semibold text-gray-900">
            Audit Trail GDPR
          </h3>
          <span className="text-sm text-gray-500">
            ({filteredLogs.length} di {(auditLogs || []).length} log)
          </span>
        </div>

        {onExport && (
          <Button
            variant="outline"
            size="sm"
            onClick={() => onExport(filteredLogs)}
            disabled={filteredLogs.length === 0}
          >
            <Download className="h-4 w-4 mr-2" />
            Esporta
          </Button>
        )}
      </div>

      {/* Controlli di filtro */}
      <div className="flex flex-col sm:flex-row gap-4 mb-6">
        <div className="flex-1">
          <SearchBar
            value={searchTerm}
            onChange={setSearchTerm}
            placeholder="Cerca nei log di audit..."
            className="w-full"
          />
        </div>

        <div className="flex items-center space-x-2">
          <Filter className="h-4 w-4 text-gray-500" />
          <select
            value={selectedAction}
            onChange={(e) => setSelectedAction(e.target.value as GDPRAuditAction | 'all')}
            className="px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="all">Tutte le azioni</option>
            <option value="VIEW">Visualizzazione</option>
            <option value="CREATE">Creazione</option>
            <option value="UPDATE">Modifica</option>
            <option value="DELETE">Eliminazione</option>
            <option value="EXPORT">Esportazione</option>
            <option value="CONSENT_GIVEN">Consenso dato</option>
            <option value="CONSENT_REVOKED">Consenso revocato</option>
          </select>
        </div>
      </div>

      {/* Lista dei log */}
      <div className="space-y-3">
        {loading ? (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
            <p className="text-gray-500 mt-2">Caricamento log...</p>
          </div>
        ) : filteredLogs.length === 0 ? (
          <div className="text-center py-8">
            <Calendar className="h-12 w-12 text-gray-300 mx-auto mb-4" />
            <p className="text-gray-500">
              {(auditLogs || []).length === 0 
                ? 'Nessun log di audit disponibile'
                : 'Nessun log corrisponde ai criteri di ricerca'
              }
            </p>
          </div>
        ) : (
          filteredLogs.map((log) => (
            <div
              key={log.id}
              className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50 transition-colors"
            >
              {/* Riga principale del log */}
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <span className={cn(
                    'px-2 py-1 rounded-full text-xs font-medium',
                    getActionColor(log.action)
                  )}>
                    {log.action}
                  </span>

                  <div className="text-sm">
                    <span className="font-medium text-gray-900">
                      {formatDate(log.timestamp)}
                    </span>
                    {log.ipAddress && (
                      <span className="text-gray-500 ml-2">
                        da {log.ipAddress}
                      </span>
                    )}
                  </div>
                </div>

                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => toggleLogExpansion(log.id)}
                >
                  <Eye className="h-4 w-4" />
                </Button>
              </div>

              {/* Dettagli espansi */}
              {expandedLog === log.id && (
                <div className="mt-4 pt-4 border-t border-gray-100">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                    <div>
                      <span className="font-medium text-gray-700">ID Sessione:</span>
                      <span className="ml-2 text-gray-600">{log.sessionId || 'N/A'}</span>
                    </div>

                    <div>
                      <span className="font-medium text-gray-700">User Agent:</span>
                      <span className="ml-2 text-gray-600 truncate" title={log.userAgent}>
                        {log.userAgent || 'N/A'}
                      </span>
                    </div>

                    {log.oldData && (
                      <div className="md:col-span-2">
                        <span className="font-medium text-gray-700">Dati precedenti:</span>
                        <pre className="mt-1 p-2 bg-gray-100 rounded text-xs overflow-x-auto">
                          {JSON.stringify(log.oldData, null, 2)}
                        </pre>
                      </div>
                    )}

                    {log.newData && (
                      <div className="md:col-span-2">
                        <span className="font-medium text-gray-700">Nuovi dati:</span>
                        <pre className="mt-1 p-2 bg-gray-100 rounded text-xs overflow-x-auto">
                          {JSON.stringify(log.newData, null, 2)}
                        </pre>
                      </div>
                    )}

                    {log.metadata && (
                      <div className="md:col-span-2">
                        <span className="font-medium text-gray-700">Metadati:</span>
                        <pre className="mt-1 p-2 bg-gray-100 rounded text-xs overflow-x-auto">
                          {JSON.stringify(log.metadata, null, 2)}
                        </pre>
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          ))
        )}
      </div>

      {/* Pulsante per caricare pi log */}
      {onLoadMore && !loading && filteredLogs.length > 0 && (
        <div className="text-center mt-6">
          <Button
            variant="outline"
            onClick={onLoadMore}
          >
            Carica altri log
          </Button>
        </div>
      )}
    </Card>
  );
};

