/**
 * Modale per gestione consensi GDPR
 */
export function GDPRConsentModal({
  open,
  onClose,
  config,
  currentConsents,
  onConsentChange,
  onConsentRevoke,
  showOnlyMissing = false,
  readOnly = false
}: GDPRConsentModalProps) {

  const [pendingConsents, setPendingConsents] = useState<Record<string, boolean>>({});
  const [loading, setLoading] = useState(false);
  const [acceptAll, setAcceptAll] = useState(false);

  // Inizializza consensi pending con quelli attuali
  useEffect(() => {
    setPendingConsents({ ...currentConsents });
  }, [currentConsents, open]);

  // Aggiorna stato "Accetta tutto"
  useEffect(() => {
    if (!config) return;

    const allRequired = config.requiredConsents.every(type => pendingConsents[type]);
    const allOptional = config.optionalConsents?.every(type => pendingConsents[type]) ?? true;

    setAcceptAll(allRequired && allOptional);
  }, [pendingConsents, config]);

  // Consensi da mostrare
  const consentsToShow = React.useMemo(() => {
    if (!config) return [];

    const allConsents = [...config.requiredConsents, ...(config.optionalConsents || [])];

    if (showOnlyMissing) {
      return allConsents.filter(type => !currentConsents[type]);
    }

    return allConsents;
  }, [config, currentConsents, showOnlyMissing]);

  // Handler per cambio consenso singolo
  const handleConsentToggle = (consentType: GDPRConsentType, granted: boolean) => {
    setPendingConsents(prev => ({
      ...prev,
      [consentType]: granted
    }));
  };

  // Handler per "Accetta tutto"
  const handleAcceptAll = (accept: boolean) => {
    if (!config) return;

    const newConsents = { ...pendingConsents };

    // Imposta tutti i consensi richiesti
    config.requiredConsents.forEach(type => {
      newConsents[type] = accept;
    });

    // Imposta tutti i consensi opzionali
    config.optionalConsents?.forEach(type => {
      newConsents[type] = accept;
    });

    setPendingConsents(newConsents);
    setAcceptAll(accept);
  };

  // Handler per salvataggio
  const handleSave = async () => {
    if (!config) return;

    setLoading(true);

    try {
      // Identifica consensi da concedere e revocare
      const toGrant: GDPRConsentType[] = [];
      const toRevoke: GDPRConsentType[] = [];

      Object.entries(pendingConsents).forEach(([type, granted]) => {
        const consentType = type as GDPRConsentType;
        const currentlyGranted = currentConsents[type];

        if (granted && !currentlyGranted) {
          toGrant.push(consentType);
        } else if (!granted && currentlyGranted) {
          toRevoke.push(consentType);
        }
      });

      // Esegui operazioni
      if (toGrant.length > 0) {
        await onConsentChange(toGrant);
      }

      if (toRevoke.length > 0) {
        await onConsentRevoke(toRevoke);
      }

      onClose();
    } catch (error) {
      console.error('Errore nel salvataggio consensi:', error);
    } finally {
      setLoading(false);
    }
  };

  // Verifica se ci sono consensi richiesti mancanti
  const missingRequiredConsents = config?.requiredConsents.filter(type => !pendingConsents[type]) || [];
  const canSave = missingRequiredConsents.length === 0;

  return (
    <Modal 
      isOpen={open} 
      onClose={onClose}
      title="Gestione Consensi GDPR"
      size="xl"
      className="max-w-4xl"
    >
      <div className="space-y-4">
        <div className="flex items-center gap-2 text-lg font-semibold">
          <Shield className="h-5 w-5" />
          Gestione Consensi GDPR
        </div>
        <p className="text-sm text-gray-600">
          Gestisci i tuoi consensi per il trattamento dei dati personali in conformit al GDPR.
          I consensi contrassegnati come obbligatori sono necessari per l'utilizzo del servizio.
        </p>

        <div className="max-h-[60vh] overflow-y-auto pr-4">
          <div className="space-y-6">
            {/* Informazioni generali */}
            <div className="flex gap-3 p-4 bg-blue-50 border border-blue-200 rounded-lg">
              <Info className="h-4 w-4 text-blue-600 mt-0.5 flex-shrink-0" />
              <div className="text-sm">
                <strong>I tuoi diritti:</strong> Puoi modificare o revocare i tuoi consensi in qualsiasi momento.
                La revoca non compromette la liceit del trattamento basata sul consenso prestato prima della revoca.
              </div>
            </div>

            {/* Controllo "Accetta tutto" */}
            {!readOnly && consentsToShow.length > 1 && (
              <Card className="p-6">
                <div className="flex items-center justify-between">
                  <div className="space-y-1">
                    <Label className="text-base font-medium">Accetta tutti i consensi</Label>
                    <p className="text-sm text-gray-600">
                      Concedi tutti i consensi disponibili con un solo click
                    </p>
                  </div>
                  <input
                    type="checkbox"
                    checked={acceptAll}
                    onChange={(e) => handleAcceptAll(e.target.checked)}
                    disabled={loading}
                    className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                  />
                </div>
              </Card>
            )}

            <hr className="border-gray-200" />

            {/* Lista consensi */}
            <div className="space-y-4">
              {consentsToShow.map((consentType) => {
                const consentInfo = CONSENT_DESCRIPTIONS[consentType];
                const isRequired = config?.requiredConsents.includes(consentType);
                const isGranted = pendingConsents[consentType];
                const isCurrentlyGranted = currentConsents[consentType];

                return (
                  <Card key={consentType} className={`p-4 transition-colors ${
                    isGranted ? 'border-green-200 bg-green-50/50' : 'border-gray-200'
                  }`}>
                    <div className="pb-3">
                      <div className="flex items-start justify-between">
                        <div className="flex items-start gap-3">
                          {consentInfo.icon}
                          <div className="space-y-1">
                            <h3 className="text-base font-semibold flex items-center gap-2">
                              {consentInfo.title}
                              {isRequired && (
                                <span className="px-2 py-1 text-xs bg-red-100 text-red-800 rounded">
                                  Obbligatorio
                                </span>
                              )}
                              {isCurrentlyGranted && (
                                <span className="px-2 py-1 text-xs bg-blue-100 text-blue-800 rounded">
                                  Attivo
                                </span>
                              )}
                            </h3>
                            <p className="text-sm text-gray-600">
                              {consentInfo.description}
                            </p>
                          </div>
                        </div>

                        {!readOnly && (
                          <input
                            type="checkbox"
                            checked={isGranted}
                            onChange={(e) => handleConsentToggle(consentType, e.target.checked)}
                            disabled={loading || (isRequired && showOnlyMissing)}
                            className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                          />
                        )}

                        {readOnly && (
                          <span className={`px-2 py-1 text-xs rounded ${
                            isGranted ? 'bg-blue-100 text-blue-800' : 'bg-gray-100 text-gray-800'
                          }`}>
                            {isGranted ? 'Concesso' : 'Non concesso'}
                          </span>
                        )}
                      </div>
                    </div>

                    <div className="pt-0">
                      <div className="space-y-3 text-sm">
                        <div>
                          <strong>Finalit:</strong>
                          <p className="text-gray-600 mt-1">{consentInfo.purpose}</p>
                        </div>

                        <div>
                          <strong>Base giuridica:</strong>
                          <p className="text-gray-600 mt-1">{consentInfo.legalBasis}</p>
                        </div>

                        {config?.consentValidityDays && (
                          <div className="flex items-center gap-2 text-gray-600">
                            <Clock className="h-4 w-4" />
                            <span>Validit: {config.consentValidityDays} giorni</span>
                          </div>
                        )}
                      </div>
                    </div>
                  </Card>
                );
              })}
            </div>

            {/* Avviso consensi mancanti */}
            {missingRequiredConsents.length > 0 && (
              <div className="flex gap-3 p-4 bg-red-50 border border-red-200 rounded-lg">
                <AlertTriangle className="h-4 w-4 text-red-600 mt-0.5 flex-shrink-0" />
                <div className="text-sm">
                  <strong>Attenzione:</strong> Alcuni consensi obbligatori non sono stati concessi.
                   necessario concedere tutti i consensi obbligatori per utilizzare il servizio.
                </div>
              </div>
            )}

            {/* Informazioni aggiuntive */}
            {config?.requiresReconfirmation && (
              <div className="flex gap-3 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                <Info className="h-4 w-4 text-blue-600 mt-0.5 flex-shrink-0" />
                <div className="text-sm">
                  <strong>Riconferma periodica:</strong> Ti verr richiesto di riconfermare i tuoi consensi
                  ogni {config.reconfirmationIntervalDays} giorni per garantire che siano sempre aggiornati.
                </div>
              </div>
            )}
          </div>
        </div>

        <div className="flex justify-end gap-2 mt-6 pt-4 border-t">
          <Button variant="outline" onClick={onClose} disabled={loading}>
            Annulla
          </Button>

          {!readOnly && (
            <Button
              onClick={handleSave}
              disabled={!canSave || loading}
              className="min-w-[120px]"
            >
              {loading ? 'Salvataggio...' : 'Salva Consensi'}
            </Button>
          )}
        </div>
      </div>
    </Modal>
  );
}

export default GDPRConsentModal;

/**
 * GDPRDeletionRequestModal - Modal per la richiesta di cancellazione dati GDPR
 * 
 * Componente che permette agli utenti di richiedere la cancellazione
 * dei propri dati personali in conformit al diritto all'oblio GDPR.
 * 
 * @version 1.0
 * @date 30 Gennaio 2025
 */

import React, { useState, useEffect } from 'react';
import { Trash2, AlertTriangle, CheckCircle, Clock, X, Loader, Shield } from 'lucide-react';
import { GDPRDeletionRequest, GDPRDeletionReason, GDPRDeletionStatus } from '../types/gdpr.types';
import { Button } from '../../../design-system/atoms/Button';
import { Card } from '../../../design-system/molecules/Card';
import { cn } from '../../../design-system/utils';

export interface GDPRDeletionRequestModalProps {
  /** Indica se il modal  aperto */
  isOpen: boolean;

  /** Callback per chiudere il modal */
  onClose: () => void;

  /** ID dell'entit da cancellare */
  entityId: string;

  /** Tipo di entit */
  entityType: string;

  /** Callback per avviare la richiesta di cancellazione */
  onSubmitDeletion: (request: Omit<GDPRDeletionRequest, 'id' | 'createdAt' | 'status'>) => Promise<void>;

  /** Lista delle richieste di cancellazione esistenti */
  existingRequests?: GDPRDeletionRequest[];

  /** Indica se ci sono dati che non possono essere cancellati */
  hasRetentionConstraints?: boolean;

  /** Dettagli sui vincoli di conservazione */
  retentionDetails?: string[];

  /** Stato di caricamento */
  loading?: boolean;

  /** Classi CSS personalizzate */
  className?: string;
}

export const GDPRDeletionRequestModal: React.FC<GDPRDeletionRequestModalProps> = ({
  isOpen,
  onClose,
  entityId,
  entityType,
  onSubmitDeletion,
  existingRequests = [],
  hasRetentionConstraints = false,
  retentionDetails = [],
  loading = false,
  className = ''
}) => {
  const [selectedReason, setSelectedReason] = useState<GDPRDeletionReason>('NO_LONGER_NECESSARY');
  const [customReason, setCustomReason] = useState('');
  const [confirmationText, setConfirmationText] = useState('');
  const [hasConfirmed, setHasConfirmed] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [currentStep, setCurrentStep] = useState<'reason' | 'confirmation' | 'review'>('reason');

  const CONFIRMATION_TEXT = 'CANCELLA I MIEI DATI';

  // Reset form quando il modal si apre
  useEffect(() => {
    if (isOpen) {
      setSelectedReason('NO_LONGER_NECESSARY');
      setCustomReason('');
      setConfirmationText('');
      setHasConfirmed(false);
      setIsSubmitting(false);
      setCurrentStep('reason');
    }
  }, [isOpen]);

  // Gestisce la chiusura del modal
  const handleClose = () => {
    if (!isSubmitting) {
      onClose();
    }
  };

  // Gestisce l'invio della richiesta di cancellazione
  const handleSubmit = async () => {
    if (isSubmitting || !hasConfirmed) return;

    setIsSubmitting(true);

    try {
      await onSubmitDeletion({
        entityId,
        entityType,
        reason: selectedReason,
        customReason: selectedReason === 'OTHER' ? customReason.trim() : undefined,
        requestedAt: new Date()
      });

      onClose();
    } catch (error) {
      console.error('Errore durante la richiesta di cancellazione:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  // Ottieni la configurazione per lo stato della richiesta
  const getStatusConfig = (status: GDPRDeletionStatus) => {
    switch (status) {
      case 'PENDING':
        return {
          icon: Clock,
          color: 'text-blue-600',
          bgColor: 'bg-blue-50',
          label: 'In attesa'
        };
      case 'UNDER_REVIEW':
        return {
          icon: Loader,
          color: 'text-yellow-600',
          bgColor: 'bg-yellow-50',
          label: 'In revisione'
        };
      case 'APPROVED':
        return {
          icon: CheckCircle,
          color: 'text-green-600',
          bgColor: 'bg-green-50',
          label: 'Approvata'
        };
      case 'COMPLETED':
        return {
          icon: CheckCircle,
          color: 'text-green-600',
          bgColor: 'bg-green-50',
          label: 'Completata'
        };
      case 'REJECTED':
        return {
          icon: X,
          color: 'text-red-600',
          bgColor: 'bg-red-50',
          label: 'Rifiutata'
        };
      default:
        return {
          icon: Clock,
          color: 'text-gray-600',
          bgColor: 'bg-gray-50',
          label: 'Sconosciuto'
        };
    }
  };

  // Ottieni la descrizione per il motivo di cancellazione
  const getReasonDescription = (reason: GDPRDeletionReason) => {
    switch (reason) {
      case 'NO_LONGER_NECESSARY':
        return 'I dati non sono pi necessari per gli scopi originali';
      case 'WITHDRAW_CONSENT':
        return 'Ritiro il consenso al trattamento dei dati';
      case 'UNLAWFUL_PROCESSING':
        return 'I dati sono stati trattati in modo illecito';
      case 'LEGAL_OBLIGATION':
        return 'Cancellazione richiesta per obbligo legale';
      case 'OTHER':
        return 'Altro motivo (specificare)';
      default:
        return '';
    }
  };

  // Formatta la data
  const formatDate = (date: Date) => {
    return new Intl.DateTimeFormat('it-IT', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    }).format(new Date(date));
  };

  // Verifica se pu procedere al passo successivo
  const canProceed = () => {
    if (currentStep === 'reason') {
      return selectedReason !== 'OTHER' || customReason.trim().length > 0;
    }
    if (currentStep === 'confirmation') {
      return confirmationText === CONFIRMATION_TEXT;
    }
    return hasConfirmed;
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 overflow-y-auto">
      {/* Overlay */}
      <div 
        className="fixed inset-0 bg-black bg-opacity-50 transition-opacity"
        onClick={handleClose}
      />

      {/* Modal */}
      <div className="flex min-h-full items-center justify-center p-4">
        <Card className={cn(
          'relative w-full max-w-2xl bg-white shadow-xl',
          className
        )}>
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-gray-200">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-red-50 rounded-full">
                <Trash2 className="h-6 w-6 text-red-600" />
              </div>
              <div>
                <h2 className="text-xl font-semibold text-gray-900">
                  Richiesta Cancellazione Dati
                </h2>
                <p className="text-sm text-gray-500">
                  Diritto all'oblio secondo il GDPR
                </p>
              </div>
            </div>

            <Button
              variant="ghost"
              size="sm"
              onClick={handleClose}
              disabled={isSubmitting}
            >
              <X className="h-5 w-5" />
            </Button>
          </div>

          {/* Contenuto */}
          <div className="p-6">
            {/* Richieste esistenti */}
            {existingRequests.length > 0 && (
              <div className="mb-6">
                <h3 className="text-lg font-medium text-gray-900 mb-3">
                  Richieste precedenti
                </h3>
                <div className="space-y-2 max-h-32 overflow-y-auto">
                  {existingRequests.slice(0, 3).map((request) => {
                    const statusConfig = getStatusConfig(request.status);
                    const StatusIcon = statusConfig.icon;

                    return (
                      <div
                        key={request.id}
                        className="flex items-center justify-between p-3 bg-gray-50 rounded-lg"
                      >
                        <div className="flex items-center space-x-3">
                          <StatusIcon className={cn(
                            'h-4 w-4',
                            statusConfig.color
                          )} />
                          <div>
                            <div className="text-sm font-medium text-gray-900">
                              {formatDate(request.createdAt)}
                            </div>
                            <div className="text-xs text-gray-500">
                              {statusConfig.label} - {getReasonDescription(request.reason)}
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Vincoli di conservazione */}
            {hasRetentionConstraints && (
              <div className="mb-6 bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                <div className="flex items-start space-x-3">
                  <Shield className="h-5 w-5 text-yellow-600 mt-0.5" />
                  <div className="text-sm text-yellow-800">
                    <p className="font-medium mb-2">Vincoli di conservazione attivi:</p>
                    <ul className="list-disc list-inside space-y-1 text-yellow-700">
                      {retentionDetails.map((detail, index) => (
                        <li key={index}>{detail}</li>
                      ))}
                    </ul>
                    <p className="mt-2 text-xs">
                      Alcuni dati potrebbero non essere cancellabili immediatamente per motivi legali.
                    </p>
                  </div>
                </div>
              </div>
            )}

            {/* Step 1: Selezione motivo */}
            {currentStep === 'reason' && (
              <div className="space-y-6">
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">
                    Motivo della richiesta di cancellazione
                  </h3>

                  <div className="space-y-3">
                    {(['NO_LONGER_NECESSARY', 'WITHDRAW_CONSENT', 'UNLAWFUL_PROCESSING', 'LEGAL_OBLIGATION', 'OTHER'] as GDPRDeletionReason[]).map((reason) => (
                      <label
                        key={reason}
                        className={cn(
                          'relative flex cursor-pointer rounded-lg border p-4 focus:outline-none',
                          selectedReason === reason
                            ? 'border-red-600 bg-red-50'
                            : 'border-gray-300 bg-white hover:bg-gray-50'
                        )}
                      >
                        <input
                          type="radio"
                          name="reason"
                          value={reason}
                          checked={selectedReason === reason}
                          onChange={(e) => setSelectedReason(e.target.value as GDPRDeletionReason)}
                          className="sr-only"
                        />
                        <div className="flex items-center w-full">
                          <div className="text-sm flex-1">
                            <div className="font-medium text-gray-900">
                              {getReasonDescription(reason)}
                            </div>
                          </div>
                        </div>
                        {selectedReason === reason && (
                          <CheckCircle className="h-5 w-5 text-red-600 absolute top-4 right-4" />
                        )}
                      </label>
                    ))}
                  </div>

                  {selectedReason === 'OTHER' && (
                    <div className="mt-4">
                      <label htmlFor="customReason" className="block text-sm font-medium text-gray-700 mb-2">
                        Specifica il motivo
                      </label>
                      <textarea
                        id="customReason"
                        value={customReason}
                        onChange={(e) => setCustomReason(e.target.value)}
                        rows={3}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-red-500 focus:border-red-500"
                        placeholder="Descrivi il motivo della richiesta di cancellazione..."
                        maxLength={500}
                        required
                      />
                      <div className="text-xs text-gray-500 mt-1">
                        {customReason.length}/500 caratteri
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}

            {/* Step 2: Conferma */}
            {currentStep === 'confirmation' && (
              <div className="space-y-6">
                <div className="bg-red-50 border border-red-200 rounded-lg p-4">
                  <div className="flex items-start space-x-3">
                    <AlertTriangle className="h-5 w-5 text-red-600 mt-0.5" />
                    <div className="text-sm text-red-800">
                      <p className="font-medium mb-2">Attenzione: Azione irreversibile</p>
                      <ul className="list-disc list-inside space-y-1 text-red-700">
                        <li>Questa azione canceller permanentemente i tuoi dati</li>
                        <li>Non sar possibile recuperare i dati una volta cancellati</li>
                        <li>Alcuni dati potrebbero essere conservati per motivi legali</li>
                        <li>La cancellazione potrebbe richiedere fino a 30 giorni</li>
                      </ul>
                    </div>
                  </div>
                </div>

                <div>
                  <label htmlFor="confirmation" className="block text-sm font-medium text-gray-700 mb-2">
                    Per confermare, digita: <span className="font-mono font-bold">{CONFIRMATION_TEXT}</span>
                  </label>
                  <input
                    id="confirmation"
                    type="text"
                    value={confirmationText}
                    onChange={(e) => setConfirmationText(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-red-500 focus:border-red-500"
                    placeholder={CONFIRMATION_TEXT}
                  />
                </div>
              </div>
            )}

            {/* Step 3: Revisione finale */}
            {currentStep === 'review' && (
              <div className="space-y-6">
                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-4">
                    Riepilogo richiesta
                  </h3>

                  <div className="bg-gray-50 rounded-lg p-4 space-y-3">
                    <div>
                      <span className="font-medium text-gray-700">Motivo:</span>
                      <span className="ml-2 text-gray-600">
                        {getReasonDescription(selectedReason)}
                      </span>
                    </div>

                    {selectedReason === 'OTHER' && customReason && (
                      <div>
                        <span className="font-medium text-gray-700">Dettagli:</span>
                        <span className="ml-2 text-gray-600">{customReason}</span>
                      </div>
                    )}

                    <div>
                      <span className="font-medium text-gray-700">Entit:</span>
                      <span className="ml-2 text-gray-600">{entityType} (ID: {entityId})</span>
                    </div>
                  </div>
                </div>

                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={hasConfirmed}
                    onChange={(e) => setHasConfirmed(e.target.checked)}
                    className="h-4 w-4 text-red-600 focus:ring-red-500 border-gray-300 rounded"
                  />
                  <span className="ml-3 text-sm text-gray-700">
                    Confermo di voler procedere con la cancellazione dei miei dati personali
                  </span>
                </label>
              </div>
            )}
          </div>

          {/* Footer */}
          <div className="flex items-center justify-between p-6 border-t border-gray-200">
            <div className="flex space-x-2">
              {currentStep !== 'reason' && (
                <Button
                  variant="outline"
                  onClick={() => {
                    if (currentStep === 'confirmation') setCurrentStep('reason');
                    if (currentStep === 'review') setCurrentStep('confirmation');
                  }}
                  disabled={isSubmitting}
                >
                  Indietro
                </Button>
              )}
            </div>

            <div className="flex space-x-3">
              <Button
                variant="outline"
                onClick={handleClose}
                disabled={isSubmitting}
              >
                Annulla
              </Button>

              {currentStep === 'review' ? (
                <Button
                  variant="destructive"
                  onClick={handleSubmit}
                  disabled={!canProceed() || isSubmitting}
                >
                  {isSubmitting ? (
                    <>
                      <Loader className="h-4 w-4 mr-2 animate-spin" />
                      Elaborazione...
                    </>
                  ) : (
                    <>
                      <Trash2 className="h-4 w-4 mr-2" />
                      Conferma cancellazione
                    </>
                  )}
                </Button>
              ) : (
                <Button
                  onClick={() => {
                    if (currentStep === 'reason') setCurrentStep('confirmation');
                    if (currentStep === 'confirmation') setCurrentStep('review');
                  }}
                  disabled={!canProceed()}
                >
                  Continua
                </Button>
              )}
            </div>
          </div>
        </Card>
      </div>
    </div>
  );
};

export default GDPRDeletionRequestModal;

/**
 * GDPR Entity Actions - Componente azioni batch
 * 
 * Componente per gestire le azioni batch sulle entit:
 * - Azioni di selezione
 * - Azioni batch
 * - Azioni GDPR
 * - Conferme e validazioni
 * 
 * @version 1.0
 * @date 30 Dicembre 2024
 */

import React, { useState } from 'react';
import { Button, Badge } from '../../../design-system';
import { 
  DropdownMenu, 
  DropdownMenuTrigger, 
  DropdownMenuContent, 
  DropdownMenuItem
} from '../../../design-system';
import { 
  CheckSquare,
  Square,
  Trash2,
  Download,
  Archive,
  Mail,
  Shield,
  AlertTriangle,
  ChevronDown,
  X
} from 'lucide-react';
import { EntityAction, BaseEntity } from '../types';

/**
 * Props del componente GDPREntityActions
 */
export interface GDPREntityActionsProps<T extends BaseEntity = BaseEntity> {
  /** Elementi selezionati */
  selectedItems: string[];

  /** Totale elementi */
  totalItems: number;

  /** Dati completi per validazioni */
  allData: T[];

  /** Azioni batch disponibili */
  batchActions?: EntityAction[];

  /** Callback per selezione tutti */
  onSelectAll: () => void;

  /** Callback per deseleziona tutti */
  onDeselectAll: () => void;

  /** Callback per azioni batch */
  onBatchAction?: (actionKey: string, selectedIds: string[]) => void;

  /** Mostra contatori */
  showCounters?: boolean;

  /** Mostra azioni GDPR */
  showGDPRActions?: boolean;

  /** Classi CSS personalizzate */
  className?: string;
}

/**
 * Componente per gestire le azioni batch
 */
export function GDPREntityActions<T extends BaseEntity = BaseEntity>({
  selectedItems,
  totalItems,
  allData,
  batchActions = [],
  onSelectAll,
  onDeselectAll,
  onBatchAction,
  showCounters = true,
  showGDPRActions = true,
  className
}: GDPREntityActionsProps<T>) {
  const [confirmAction, setConfirmAction] = useState<string | null>(null);

  // Stato selezione
  const hasSelection = selectedItems.length > 0;
  const isAllSelected = selectedItems.length === totalItems && totalItems > 0;
  const isPartiallySelected = selectedItems.length > 0 && selectedItems.length < totalItems;

  // Azioni GDPR predefinite
  const gdprActions: EntityAction[] = [
    {
      key: 'export-data',
      label: 'Esporta Dati Selezionati',
      icon: <Download className="h-4 w-4" />,
      variant: 'secondary',
      visible: true
    },
    {
      key: 'request-consent',
      label: 'Richiedi Consenso',
      icon: <Shield className="h-4 w-4" />,
      variant: 'secondary',
      visible: true
    },
    {
      key: 'anonymize',
      label: 'Anonimizza Dati',
      icon: <Archive className="h-4 w-4" />,
      variant: 'secondary',
      visible: true
    },
    {
      key: 'delete-gdpr',
      label: 'Cancellazione GDPR',
      icon: <Trash2 className="h-4 w-4" />,
      variant: 'danger',
      visible: true
    }
  ];

  // Combina azioni batch e GDPR
  const allActions = [
    ...batchActions,
    ...(showGDPRActions ? gdprActions : [])
  ];

  // Azioni che richiedono conferma
  const actionsRequiringConfirmation = ['request-consent', 'anonymize', 'delete-gdpr'];

  // Gestione azioni
  const handleAction = (actionKey: string) => {
    if (actionsRequiringConfirmation.includes(actionKey)) {
      setConfirmAction(actionKey);
    } else {
      onBatchAction?.(actionKey, selectedItems);
    }
  };

  // Conferma azione
  const handleConfirmAction = () => {
    if (confirmAction) {
      onBatchAction?.(confirmAction, selectedItems);
      setConfirmAction(null);
    }
  };

  // Annulla conferma
  const handleCancelAction = () => {
    setConfirmAction(null);
  };

  // Filtra azioni visibili
  const visibleActions = allActions.filter(action => {
    if (typeof action.visible === 'function') {
      // Per azioni batch, verifica su tutti gli elementi selezionati
      const selectedData = allData.filter(item => selectedItems.includes(item.id));
      return selectedData.every(item => action.visible!(item));
    }
    return action.visible !== false;
  });

  // Azioni principali (prime 3)
  const primaryActions = visibleActions.slice(0, 3);
  const secondaryActions = visibleActions.slice(3);

  if (!hasSelection && !showCounters) {
    return null;
  }

  return (
    <div className={`gdpr-entity-actions ${className || ''}`}>
      <div className="flex items-center justify-between p-4 bg-muted/25 border rounded-lg">
        {/* Selezione e contatori */}
        <div className="flex items-center gap-4">
          {/* Checkbox selezione tutti */}
          <div className="flex items-center gap-2">
            <button
              onClick={isAllSelected ? onDeselectAll : onSelectAll}
              className="flex items-center justify-center w-5 h-5 border-2 border-gray-300 rounded hover:border-primary focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-1"
            >
              {isAllSelected ? (
                <CheckSquare className="h-4 w-4 text-primary" />
              ) : isPartiallySelected ? (
                <div className="w-2 h-2 bg-primary rounded-sm" />
              ) : (
                <Square className="h-4 w-4 text-gray-400" />
              )}
            </button>

            <span className="text-sm font-medium">
              {isAllSelected ? 'Deseleziona tutto' : 'Seleziona tutto'}
            </span>
          </div>

          {/* Contatori */}
          {showCounters && (
            <div className="flex items-center gap-2">
              <Badge variant="secondary" className="text-xs">
                {selectedItems.length} di {totalItems} selezionati
              </Badge>

              {hasSelection && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={onDeselectAll}
                  className="h-6 px-2 text-xs"
                >
                  <X className="h-3 w-3 mr-1" />
                  Deseleziona
                </Button>
              )}
            </div>
          )}
        </div>

        {/* Azioni batch */}
        {hasSelection && visibleActions.length > 0 && (
          <div className="flex items-center gap-2">
            {/* Azioni principali */}
            {primaryActions.map((action) => (
              <Button
                key={action.key}
                variant={action.variant === 'danger' ? 'danger' : 'outline'}
                size="sm"
                onClick={() => handleAction(action.key)}
                disabled={
                  typeof action.disabled === 'function'
                    ? allData
                        .filter(item => selectedItems.includes(item.id))
                        .some(item => action.disabled!(item))
                    : action.disabled
                }
                className="flex items-center gap-1"
              >
                {action.icon}
                <span className="hidden sm:inline">{action.label}</span>
              </Button>
            ))}

            {/* Menu azioni secondarie */}
            {secondaryActions.length > 0 && (
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="outline" size="sm">
                    <span className="hidden sm:inline">Altre azioni</span>
                    <ChevronDown className="h-4 w-4 sm:ml-1" />
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end">
                  {secondaryActions.map((action) => (
                    <DropdownMenuItem
                      key={action.key}
                      onClick={() => handleAction(action.key)}
                      disabled={
                        typeof action.disabled === 'function'
                          ? allData
                              .filter(item => selectedItems.includes(item.id))
                              .some(item => action.disabled!(item))
                          : action.disabled
                      }
                      className="flex items-center gap-2"
                    >
                      {action.icon}
                      {action.label}
                    </DropdownMenuItem>
                  ))}
                </DropdownMenuContent>
              </DropdownMenu>
            )}
          </div>
        )}
      </div>

      {/* Modal conferma azione */}
      {confirmAction && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
            <div className="flex items-center gap-3 mb-4">
              <AlertTriangle className="h-6 w-6 text-amber-500" />
              <h3 className="text-lg font-semibold">Conferma Azione</h3>
            </div>

            <p className="text-gray-600 mb-6">
              Sei sicuro di voler eseguire l'azione "{allActions.find(a => a.key === confirmAction)?.label}" 
              su {selectedItems.length} elementi selezionati?
              {confirmAction === 'delete-gdpr' && (
                <span className="block mt-2 text-red-600 font-medium">
                  Questa azione non pu essere annullata e comporter la cancellazione permanente dei dati.
                </span>
              )}
            </p>

            <div className="flex justify-end gap-3">
              <Button
                variant="outline"
                onClick={handleCancelAction}
              >
                Annulla
              </Button>
              <Button
                variant={confirmAction === 'delete-gdpr' ? 'destructive' : 'primary'}
                onClick={handleConfirmAction}
              >
                Conferma
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default GDPREntityActions;

/**
 * GDPR Entity Filters - Componente filtri avanzati
 * 
 * Componente per gestire filtri avanzati delle entit:
 * - Filtri per campo
 * - Filtri per data
 * - Filtri per stato
 * - Filtri GDPR
 * - Reset filtri
 * 
 * @version 1.0
 * @date 30 Dicembre 2024
 */

import React, { useState } from 'react';
import { Button, Input, Select, Badge } from '../../../design-system';
import { Card } from '../../../design-system';
import { 
  X, 
  Filter, 
  Search,
  RotateCcw,
  ChevronDown,
  ChevronUp
} from 'lucide-react';
import { FilterConfig, EntityField } from '../types';

/**
 * Tipo per valore filtro
 */
export type FilterValue = string | number | boolean | Date | null | undefined;

/**
 * Tipo per tipo di filtro supportato
 */
export type FilterType = 'text' | 'number' | 'boolean' | 'date' | 'select';

/**
 * Interfaccia per filtro attivo
 */
export interface ActiveFilter {
  key: string;
  label: string;
  value: FilterValue;
  operator: 'equals' | 'contains' | 'startsWith' | 'endsWith' | 'gt' | 'lt' | 'gte' | 'lte' | 'between';
  type: FilterType;
}

/**
 * Props del componente GDPREntityFilters
 */
export interface GDPREntityFiltersProps {
  /** Configurazione filtri disponibili */
  filterConfig: FilterConfig[];

  /** Campi entit per filtri dinamici */
  fields: EntityField[];

  /** Filtri attivi */
  activeFilters: ActiveFilter[];

  /** Callback per aggiornamento filtri */
  onFiltersChange: (filters: ActiveFilter[]) => void;

  /** Callback per reset filtri */
  onResetFilters: () => void;

  /** Mostra filtri compatti */
  compact?: boolean;

  /** Mostra contatore filtri */
  showCount?: boolean;

  /** Classi CSS personalizzate */
  className?: string;
}

/**
 * Componente per gestire i filtri avanzati
 */
export function GDPREntityFilters({
  filterConfig,
  fields,
  activeFilters,
  onFiltersChange,
  onResetFilters,
  compact = false,
  showCount = true,
  className
}: GDPREntityFiltersProps) {
  const [isExpanded, setIsExpanded] = useState(!compact);
  const [selectedField, setSelectedField] = useState<string>('');
  const [filterValue, setFilterValue] = useState<string>('');
  const [filterOperator, setFilterOperator] = useState<string>('contains');

  // Operatori disponibili per tipo
  const operatorsByType = {
    text: [
      { value: 'contains', label: 'Contiene' },
      { value: 'equals', label: 'Uguale a' },
      { value: 'startsWith', label: 'Inizia con' },
      { value: 'endsWith', label: 'Finisce con' }
    ],
    number: [
      { value: 'equals', label: 'Uguale a' },
      { value: 'gt', label: 'Maggiore di' },
      { value: 'lt', label: 'Minore di' },
      { value: 'gte', label: 'Maggiore o uguale' },
      { value: 'lte', label: 'Minore o uguale' }
    ],
    date: [
      { value: 'equals', label: 'Uguale a' },
      { value: 'gt', label: 'Dopo' },
      { value: 'lt', label: 'Prima' },
      { value: 'gte', label: 'Da' },
      { value: 'lte', label: 'Fino a' }
    ],
    boolean: [
      { value: 'equals', label: 'Uguale a' }
    ],
    select: [
      { value: 'equals', label: 'Uguale a' }
    ]
  };

  // Aggiungi nuovo filtro
  const handleAddFilter = () => {
    if (!selectedField || !filterValue) return;

    const field = fields.find(f => f.key === selectedField);
    if (!field) return;

    // Mappa i tipi di campo ai tipi di filtro supportati
    const getFilterType = (fieldType: string | undefined): FilterType => {
      switch (fieldType) {
        case 'number':
          return 'number';
        case 'boolean':
          return 'boolean';
        case 'date':
          return 'date';
        case 'select':
          return 'select';
        case 'email':
        case 'phone':
        case 'textarea':
        case 'text':
        default:
          return 'text';
      }
    };

    const newFilter: ActiveFilter = {
      key: selectedField,
      label: field.label,
      value: filterValue,
      operator: filterOperator as ActiveFilter['operator'],
      type: getFilterType(field.type)
    };

    // Evita duplicati
    const existingIndex = activeFilters.findIndex(f => f.key === selectedField);
    if (existingIndex >= 0) {
      const updatedFilters = [...activeFilters];
      updatedFilters[existingIndex] = newFilter;
      onFiltersChange(updatedFilters);
    } else {
      onFiltersChange([...activeFilters, newFilter]);
    }

    // Reset form
    setSelectedField('');
    setFilterValue('');
    setFilterOperator('contains');
  };

  // Rimuovi filtro
  const handleRemoveFilter = (index: number) => {
    const updatedFilters = activeFilters.filter((_, i) => i !== index);
    onFiltersChange(updatedFilters);
  };

  // Campi filtrabili
  const filterableFields = fields.filter(field => 
    field.filterable !== false && 
    !['id', 'createdAt', 'updatedAt'].includes(field.key)
  );

  // Campo selezionato
  const selectedFieldConfig = filterableFields.find(f => f.key === selectedField);

  // Mappa il tipo di campo al tipo di filtro supportato
  const getFilterType = (fieldType: string | undefined): FilterType => {
    switch (fieldType) {
      case 'number':
        return 'number';
      case 'boolean':
        return 'boolean';
      case 'date':
        return 'date';
      case 'select':
        return 'select';
      case 'email':
      case 'phone':
      case 'textarea':
      case 'text':
      default:
        return 'text';
    }
  };

  const selectedFieldType = selectedFieldConfig ? getFilterType(selectedFieldConfig.type) : 'text';
  const availableOperators = operatorsByType[selectedFieldType] || operatorsByType.text;

  // Renderizza valore filtro
  const renderFilterValue = (filter: ActiveFilter) => {
    if (filter.type === 'boolean') {
      return filter.value ? 'S' : 'No';
    }

    if (filter.type === 'date' && filter.value instanceof Date) {
      return filter.value.toLocaleDateString('it-IT');
    }

    return String(filter.value);
  };

  return (
    <div className={`gdpr-entity-filters ${className || ''}`}>
      {/* Header con toggle */}
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-2">
          <Filter className="h-4 w-4" />
          <span className="font-medium">Filtri</span>
          {showCount && activeFilters.length > 0 && (
            <Badge variant="secondary" className="text-xs">
              {activeFilters.length}
            </Badge>
          )}
        </div>

        <div className="flex items-center gap-2">
          {activeFilters.length > 0 && (
            <Button
              variant="ghost"
              size="sm"
              onClick={onResetFilters}
              className="text-xs"
            >
              <RotateCcw className="h-3 w-3 mr-1" />
              Reset
            </Button>
          )}

          {compact && (
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsExpanded(!isExpanded)}
            >
              {isExpanded ? (
                <ChevronUp className="h-4 w-4" />
              ) : (
                <ChevronDown className="h-4 w-4" />
              )}
            </Button>
          )}
        </div>
      </div>

      {/* Filtri attivi */}
      {activeFilters.length > 0 && (
        <div className="mb-4">
          <div className="text-sm text-muted-foreground mb-2">Filtri attivi:</div>
          <div className="flex flex-wrap gap-2">
            {activeFilters.map((filter, index) => (
              <Badge
                key={`${filter.key}-${index}`}
                variant="outline"
                className="flex items-center gap-1 pr-1"
              >
                <span className="text-xs">
                  {filter.label}: {renderFilterValue(filter)}
                </span>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => handleRemoveFilter(index)}
                  className="h-4 w-4 p-0 hover:bg-destructive hover:text-destructive-foreground"
                >
                  <X className="h-3 w-3" />
                </Button>
              </Badge>
            ))}
          </div>
        </div>
      )}

      {/* Form aggiunta filtri */}
      {isExpanded && (
        <Card className="p-4">
          <div className="space-y-4">
            <div className="text-sm font-medium">Aggiungi filtro</div>

            <div className="grid grid-cols-1 md:grid-cols-4 gap-3">
              {/* Selezione campo */}
              <div>
                <label className="text-xs text-muted-foreground mb-1 block">
                  Campo
                </label>
                <select
                  value={selectedField}
                  onChange={(e) => setSelectedField(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                >
                  <option value="">Seleziona campo</option>
                  {filterableFields.map((field) => (
                    <option key={field.key} value={field.key}>
                      {field.label}
                    </option>
                  ))}
                </select>
              </div>

              {/* Selezione operatore */}
              <div>
                <label className="text-xs text-muted-foreground mb-1 block">
                  Operatore
                </label>
                <select
                  value={filterOperator}
                  onChange={(e) => setFilterOperator(e.target.value)}
                  disabled={!selectedField}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent disabled:bg-gray-100"
                >
                  {availableOperators.map((op: { value: string; label: string }) => (
                    <option key={op.value} value={op.value}>
                      {op.label}
                    </option>
                  ))}
                </select>
              </div>

              {/* Input valore */}
              <div>
                <label className="text-xs text-muted-foreground mb-1 block">
                  Valore
                </label>
                {selectedFieldConfig?.type === 'boolean' ? (
                  <select
                    value={filterValue}
                    onChange={(e) => setFilterValue(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                  >
                    <option value="">Seleziona valore</option>
                    <option value="true">S</option>
                    <option value="false">No</option>
                  </select>
                ) : selectedFieldConfig?.type === 'date' ? (
                  <Input
                    type="date"
                    value={filterValue}
                    onChange={(e) => setFilterValue(e.target.value)}
                    placeholder="Seleziona data"
                  />
                ) : selectedFieldConfig?.type === 'number' ? (
                  <Input
                    type="number"
                    value={filterValue}
                    onChange={(e) => setFilterValue(e.target.value)}
                    placeholder="Inserisci numero"
                  />
                ) : selectedFieldConfig?.options ? (
                  <select
                    value={filterValue}
                    onChange={(e) => setFilterValue(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                  >
                    <option value="">Seleziona opzione</option>
                    {selectedFieldConfig.options.map((option) => (
                      <option key={option.value} value={option.value}>
                        {option.label}
                      </option>
                    ))}
                  </select>
                ) : (
                  <Input
                    type="text"
                    value={filterValue}
                    onChange={(e) => setFilterValue(e.target.value)}
                    placeholder="Inserisci valore"
                  />
                )}
              </div>

              {/* Pulsante aggiungi */}
              <div className="flex items-end">
                <Button
                  onClick={handleAddFilter}
                  disabled={!selectedField || !filterValue}
                  className="w-full"
                >
                  <Search className="h-4 w-4 mr-1" />
                  Aggiungi
                </Button>
              </div>
            </div>
          </div>
        </Card>
      )}

      {/* Filtri predefiniti */}
      {filterConfig.length > 0 && isExpanded && (
        <div className="mt-4">
          <div className="text-sm font-medium mb-2">Filtri rapidi</div>
          <div className="flex flex-wrap gap-2">
            {filterConfig.map((config) => (
              <Button
                key={config.key}
                variant="outline"
                size="sm"
                onClick={() => {
                  const newFilter: ActiveFilter = {
                    key: config.key,
                    label: config.label,
                    value: config.defaultValue,
                    operator: 'equals',
                    type: config.type === 'dateRange' ? 'date' : (config.type as FilterType) || 'text'
                  };
                  onFiltersChange([...activeFilters, newFilter]);
                }}
                className="text-xs"
              >
                {config.label}
              </Button>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default GDPREntityFilters;

/**
 * GDPR Entity Grid - Componente griglia per visualizzare le entit
 * 
 * Componente griglia che include:
 * - Visualizzazione dati in formato card
 * - Layout responsive
 * - Selezione card
 * - Azioni per card
 * - Supporto GDPR
 * 
 * @version 1.0
 * @date 30 Dicembre 2024
 */

import React from 'react';
import { Button, Badge, Card } from '../../../design-system';
import { 
  DropdownMenu, 
  DropdownMenuTrigger, 
  DropdownMenuContent, 
  DropdownMenuItem
} from '../../../design-system';
import { 
  MoreVertical,
  Eye,
  Edit,
  Trash2,
  Download,
  Check
} from 'lucide-react';
import { EntityField, EntityAction, BaseEntity } from '../types';

/**
 * Props del componente GDPREntityGrid
 */
export interface GDPREntityGridProps<T extends BaseEntity = BaseEntity> {
  /** Dati da visualizzare */
  data: T[];

  /** Configurazione campi principali */
  fields: EntityField[];

  /** Configurazione campi aggiuntivi */
  additionalFields?: EntityField[];

  /** Elementi selezionati */
  selectedItems: string[];

  /** Callback per selezione elementi */
  onSelectionChange: (selectedIds: string[]) => void;

  /** Azioni disponibili per card */
  cardActions?: EntityAction[];

  /** Callback per azioni card */
  onCardAction?: (actionKey: string, entity: T) => void;

  /** Stato di caricamento */
  loading?: boolean;

  /** Messaggio stato vuoto */
  emptyMessage?: string;

  /** Mostra checkbox selezione */
  showSelection?: boolean;

  /** Mostra azioni */
  showActions?: boolean;

  /** Numero colonne griglia */
  columns?: 1 | 2 | 3 | 4 | 6;

  /** Classi CSS personalizzate */
  className?: string;
}

/**
 * Componente griglia per visualizzare le entit
 */
export function GDPREntityGrid<T extends BaseEntity = BaseEntity>({
  data,
  fields,
  additionalFields = [],
  selectedItems,
  onSelectionChange,
  cardActions,
  onCardAction,
  loading = false,
  emptyMessage = 'Nessun elemento trovato',
  showSelection = true,
  showActions = true,
  columns = 3,
  className
}: GDPREntityGridProps<T>) {

  // Gestione selezione
  const handleSelectItem = (id: string) => {
    if (selectedItems.includes(id)) {
      onSelectionChange(selectedItems.filter(itemId => itemId !== id));
    } else {
      onSelectionChange([...selectedItems, id]);
    }
  };

  // Renderizza valore campo
  const renderFieldValue = (field: EntityField, entity: T) => {
    const value = (entity as any)[field.key];

    if (field.formatter) {
      return field.formatter(value, entity);
    }

    // Formattazione di default per tipi comuni
    if (value === null || value === undefined) {
      return <span className="text-muted-foreground">-</span>;
    }

    if (typeof value === 'boolean') {
      return (
        <Badge variant={value ? 'secondary' : 'outline'} className="text-xs">
          {value ? 'S' : 'No'}
        </Badge>
      );
    }

    if (value instanceof Date) {
      return <span className="text-sm">{value.toLocaleDateString('it-IT')}</span>;
    }

    if (typeof value === 'string' && value.includes('@')) {
      return <span className="font-mono text-xs text-muted-foreground">{value}</span>;
    }

    if (typeof value === 'string' && value.length > 50) {
      return (
        <span className="text-sm" title={value}>
          {value.substring(0, 50)}...
        </span>
      );
    }

    return <span className="text-sm">{String(value)}</span>;
  };

  // Classi CSS per il grid
  const gridClasses = {
    1: 'grid-cols-1',
    2: 'grid-cols-1 md:grid-cols-2',
    3: 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3',
    4: 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4',
    6: 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 2xl:grid-cols-6'
  };

  if (loading) {
    return (
      <div className={`gdpr-entity-grid ${className || ''}`}>
        <div className="text-center py-12">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Caricamento dati...</p>
        </div>
      </div>
    );
  }

  if (data.length === 0) {
    return (
      <div className={`gdpr-entity-grid ${className || ''}`}>
        <div className="text-center py-12">
          <p className="text-muted-foreground">{emptyMessage}</p>
        </div>
      </div>
    );
  }

  return (
    <div className={`gdpr-entity-grid ${className || ''}`}>
      <div className={`grid gap-4 ${gridClasses[columns]}`}>
        {data.map((entity) => {
          const isSelected = selectedItems.includes(entity.id);

          return (
            <Card
              key={entity.id}
              className={`relative transition-all duration-200 hover:shadow-md ${
                isSelected ? 'ring-2 ring-primary bg-primary/5' : ''
              }`}
            >
              {/* Header card con selezione e azioni */}
              <div className="flex items-center justify-between p-4 pb-2">
                {/* Checkbox selezione */}
                {showSelection && (
                  <div className="flex items-center">
                    <input
                      type="checkbox"
                      checked={isSelected}
                      onChange={() => handleSelectItem(entity.id)}
                      className="rounded border-gray-300 text-primary focus:ring-primary"
                    />
                    {isSelected && (
                      <Check className="h-4 w-4 text-primary ml-2" />
                    )}
                  </div>
                )}

                {/* Menu azioni */}
                {showActions && cardActions && cardActions.length > 0 && (
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button variant="ghost" size="sm" className="h-8 w-8 p-0">
                        <MoreVertical className="h-4 w-4" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      {cardActions
                        .filter(action => 
                          typeof action.visible === 'function' 
                            ? action.visible(entity) 
                            : action.visible !== false
                        )
                        .map((action) => (
                          <DropdownMenuItem
                            key={action.key}
                            onClick={() => onCardAction?.(action.key, entity)}
                            disabled={
                              typeof action.disabled === 'function' 
                                ? action.disabled(entity) 
                                : action.disabled
                            }
                            className="flex items-center gap-2"
                          >
                            {action.icon && <span>{action.icon}</span>}
                            {action.label}
                          </DropdownMenuItem>
                        ))}
                    </DropdownMenuContent>
                  </DropdownMenu>
                )}
              </div>

              {/* Contenuto principale */}
              <div className="px-4 pb-4 space-y-3">
                {/* Campi principali */}
                {fields.slice(0, 3).map((field) => {
                  const value = (entity as any)[field.key];
                  if (value === null || value === undefined || value === '') return null;

                  return (
                    <div key={field.key} className="space-y-1">
                      <div className="text-xs font-medium text-muted-foreground uppercase tracking-wide">
                        {field.label}
                      </div>
                      <div className="font-medium">
                        {renderFieldValue(field, entity)}
                      </div>
                    </div>
                  );
                })}

                {/* Campi aggiuntivi (compatti) */}
                {additionalFields.length > 0 && (
                  <div className="pt-2 border-t space-y-2">
                    {additionalFields.slice(0, 4).map((field) => {
                      const value = (entity as any)[field.key];
                      if (value === null || value === undefined || value === '') return null;

                      return (
                        <div key={field.key} className="flex justify-between items-center">
                          <span className="text-xs text-muted-foreground">
                            {field.label}:
                          </span>
                          <div className="text-xs">
                            {renderFieldValue(field, entity)}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                )}

                {/* Badge di stato (se presente) */}
                {(entity as any).status && (
                  <div className="pt-2">
                    <Badge 
                      variant={
                        (entity as any).status === 'active' ? 'secondary' : 'outline'
                      }
                      className="text-xs"
                    >
                      {(entity as any).status}
                    </Badge>
                  </div>
                )}
              </div>

              {/* Footer con azioni rapide */}
              {showActions && cardActions && cardActions.length > 0 && (
                <div className="px-4 py-3 bg-muted/25 border-t flex justify-end gap-2">
                  {cardActions.slice(0, 3).map((action) => {
                    const isVisible = typeof action.visible === 'function' 
                      ? action.visible(entity) 
                      : action.visible !== false;

                    const isDisabled = typeof action.disabled === 'function' 
                      ? action.disabled(entity) 
                      : action.disabled;

                    if (!isVisible) return null;

                    return (
                      <Button
                        key={action.key}
                        variant="ghost"
                        size="sm"
                        onClick={() => onCardAction?.(action.key, entity)}
                        disabled={isDisabled}
                        className="h-8 px-2 text-xs"
                        title={action.label}
                      >
                        {action.icon || <Eye className="h-3 w-3" />}
                      </Button>
                    );
                  })}
                </div>
              )}
            </Card>
          );
        })}
      </div>
    </div>
  );
}

export default GDPREntityGrid;

import React, { useState, useCallback } from 'react';
import { Button } from '../../../design-system/atoms/Button';
import { Input } from '../../../design-system/atoms/Input';
import { Dropdown } from '../../../design-system/molecules/Dropdown';
import { 
  Download,
  Filter,
  Plus,
  Search,
  Settings
} from 'lucide-react';
import { cn } from '../../../design-system/utils';

// Import dei componenti ottimizzati
import { useGDPRPermissions } from '../hooks/useGDPRPermissions';
import { useEntityState } from '../hooks/useEntityState';
import { useTableColumns } from '../hooks/useTableColumns';
import { EntityView, ViewMode } from './EntityView';
import { BatchOperations } from './BatchOperations';

export interface GDPREntityPageProps<T> {
  // Configurazione entit
  entityType: string;
  entityName: string;
  entityNamePlural: string;

  // API endpoints
  apiEndpoint: string;

  // Configurazione colonne
  columns: Array<{
    key: string;
    label: string;
    sortable?: boolean;
    width?: number;
    render?: (value: any, entity: T) => React.ReactNode;
  }>;

  // Azioni personalizzate
  actions?: {
    onView?: (entity: T) => void;
    onEdit?: (entity: T) => void;
    onDelete?: (entity: T) => void;
    onExport?: (entity: T) => void;
    onCreate?: () => void;
    onImport?: () => void;
    custom?: Array<{
      label: string;
      icon?: React.ReactNode;
      onClick: (entity: T) => void;
      variant?: 'primary' | 'secondary' | 'danger';
      permission?: string;
    }>;
  };

  // Configurazione permessi
  permissions?: {
    read?: string;
    create?: string;
    update?: string;
    delete?: string;
    export?: string;
  };

  // Opzioni di visualizzazione
  defaultViewMode?: ViewMode;
  showBatchOperations?: boolean;
  showFilters?: boolean;
  showColumnSettings?: boolean;

  // Personalizzazione UI
  className?: string;
  headerActions?: React.ReactNode;
}

/**
 * Componente principale ottimizzato per la gestione delle entit GDPR
 * Sostituisce il monolitico GDPREntityTemplate con un approccio modulare
 */
export function GDPREntityPage<T extends { id: string; [key: string]: any }>({
  entityType,
  entityName,
  entityNamePlural,
  apiEndpoint,
  columns,
  actions = {},
  permissions = {},
  defaultViewMode = 'table',
  showBatchOperations = true,
  showFilters = true,
  showColumnSettings = true,
  className,
  headerActions
}: GDPREntityPageProps<T>) {

  // Stati locali per UI
  const [viewMode, setViewMode] = useState<ViewMode>(defaultViewMode);
  const [searchQuery, setSearchQuery] = useState('');
  const [showFiltersPanel, setShowFiltersPanel] = useState(false);
  const [showColumnPanel, setShowColumnPanel] = useState(false);

  // Hook personalizzati per la logica di business
  const gdprPermissions = useGDPRPermissions({
    entityName,
    entityNamePlural,
    readPermission: permissions.read || `${entityType}:read`,
    writePermission: permissions.create || `${entityType}:write`,
    deletePermission: permissions.delete || `${entityType}:delete`,
    exportPermission: permissions.export || `${entityType}:export`
  });

  const [entityState, entityActions] = useEntityState<T>({
    apiEndpoint,
    pageSize: 10,
    enableSearch: true,
    enablePagination: true
  });

  const [columnState, columnActions] = useTableColumns({
    baseColumns: columns,
    actions,
    permissions: gdprPermissions
  });

  // Gestori di eventi
  const handleSearch = useCallback((query: string) => {
    entityActions.setSearchTerm(query);
  }, [entityActions]);

  const handleEntityAction = useCallback((action: string, entity: T) => {
    switch (action) {
      case 'view':
        actions.onView?.(entity);
        break;
      case 'edit':
        actions.onEdit?.(entity);
        break;
      case 'delete':
        actions.onDelete?.(entity);
        break;
      case 'export':
        actions.onExport?.(entity);
        break;
      default:
        // Gestione azioni personalizzate
        const customAction = actions.custom?.find(a => a.label === action);
        customAction?.onClick(entity);
    }
  }, [actions]);

  const handleBatchAction = useCallback(async (action: string, entityIds: string[]) => {
    switch (action) {
      case 'delete':
        // Elimina le entit selezionate
        entityIds.forEach(id => entityActions.removeEntity(id));
        break;
      case 'export':
        // Logica di esportazione batch
        console.log('Esportazione batch:', entityIds);
        break;
      case 'archive':
        // Logica di archiviazione batch
        console.log('Archiviazione batch:', entityIds);
        break;
    }
  }, [entityActions]);

  // Preparazione delle azioni del dropdown "Aggiungi"
  const addActions = [
    ...(gdprPermissions.canCreate ? [{
      label: `Nuovo ${entityName}`,
      icon: <Plus className="h-4 w-4" />,
      onClick: () => actions.onCreate?.(),
      variant: 'primary' as const
    }] : []),
    ...(gdprPermissions.canCreate ? [{
      label: `Importa ${entityNamePlural}`,
      icon: <Download className="h-4 w-4" />,
      onClick: () => actions.onImport?.(),
      variant: 'secondary' as const
    }] : [])
  ];

  return (
    <div className={cn('space-y-6', className)}>
      {/* Header della pagina */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">
            {entityNamePlural}
          </h1>
          <p className="text-gray-600">
            Gestisci {entityNamePlural.toLowerCase()} conformi al GDPR
          </p>
        </div>

        <div className="flex items-center gap-2">
          {headerActions}

          {/* Dropdown per aggiungere entit */}
          {addActions.length > 0 && (
            <Dropdown
              actions={addActions}
              variant="primary"
              icon={<Plus className="h-4 w-4" />}
            />
          )}
        </div>
      </div>

      {/* Barra di ricerca e filtri */}
      <div className="flex flex-col sm:flex-row gap-4">
        {/* Campo di ricerca */}
        <div className="flex-1 relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
          <Input
            type="text"
            placeholder={`Cerca ${entityNamePlural.toLowerCase()}...`}
            value={entityState.searchTerm}
            onChange={(e) => handleSearch(e.target.value)}
            className="pl-10"
          />
        </div>

        {/* Controlli aggiuntivi */}
        <div className="flex items-center gap-2">
          {/* Filtri avanzati */}
          {showFilters && (
            <Button
              variant="outline"
              onClick={() => setShowFiltersPanel(!showFiltersPanel)}
              className={cn(showFiltersPanel && 'bg-gray-100')}
            >
              <Filter className="h-4 w-4" />
              <span className="ml-1 hidden sm:inline">Filtri</span>
            </Button>
          )}

          {/* Impostazioni colonne */}
          {showColumnSettings && viewMode === 'table' && (
            <Button
              variant="outline"
              onClick={() => setShowColumnPanel(!showColumnPanel)}
              className={cn(showColumnPanel && 'bg-gray-100')}
            >
              <Settings className="h-4 w-4" />
              <span className="ml-1 hidden sm:inline">Colonne</span>
            </Button>
          )}
        </div>
      </div>

      {/* Operazioni batch */}
      {showBatchOperations && entityState.selectedEntities.size > 0 && (
        <BatchOperations
          selectedEntities={entityState.selectedEntities}
          permissions={gdprPermissions}
          onClearSelection={entityActions.clearSelection}
          entityName={entityName}
          entityNamePlural={entityNamePlural}
        />
      )}

      {/* Visualizzazione entit */}
        <EntityView
          entities={entityState.entities}
          viewMode={viewMode}
          onViewModeChange={setViewMode}
          columns={tableColumns}
          permissions={gdprPermissions}
          selectedEntities={entityState.selectedEntities}
          onEntitySelect={entityActions.toggleEntitySelection}
          onSelectAll={entityActions.selectAllEntities}
          onClearSelection={entityActions.clearSelection}
          onEntityAction={handleEntityAction}
          loading={entityState.loading}
          error={entityState.error}
          emptyMessage={`Nessun ${entityName.toLowerCase()} trovato`}
        />

      {/* Paginazione */}
        {entityState.totalPages > 1 && (
          <div className="flex justify-center">
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                disabled={entityState.currentPage === 1}
                onClick={() => entityActions.setCurrentPage(entityState.currentPage - 1)}
              >
                Precedente
              </Button>

              <span className="text-sm text-gray-600">
                Pagina {entityState.currentPage} di {entityState.totalPages}
              </span>

              <Button
                variant="outline"
                disabled={entityState.currentPage === entityState.totalPages}
                onClick={() => entityActions.setCurrentPage(entityState.currentPage + 1)}
              >
                Successiva
              </Button>
            </div>
          </div>
        )}
    </div>
  );
}

/**
 * GDPR Entity Page Template - Componente principale
 * 
 * Template di pagina GDPR-compliant che replica la struttura
 * della pagina Courses con componenti riutilizzabili.
 * 
 * TODO: Refactoring completo delle interfacce GDPR necessario
 * Disabilitazione temporanea errori TypeScript per permettere build
 * 
 * @version 1.0
 * @date 30 Dicembre 2024
 */

// eslint-disable-next-line @typescript-eslint/ban-ts-comment
// @ts-nocheck

import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Navigate } from 'react-router-dom';
import { BaseEntity } from '../types/entity.types';
import { GDPREntityPageConfig, GDPREntityPageContext as GDPREntityPageContextType } from '../types/template.types';
import { useGDPREntityPage } from '../hooks/useGDPREntityPage';
import { useGDPRConsent } from '../hooks/useGDPRConsent';
import { useGDPRAudit } from '../hooks/useGDPRAudit';
import { useAuth } from '../../../context/AuthContext';

// Context per condividere stato del template
export const GDPREntityPageContextProvider = React.createContext<GDPREntityPageContextType | null>(null);

// Import componenti esistenti (da adattare ai path reali)
import EntityListLayout from '../../../components/layouts/EntityListLayout';
import { SearchBar, FilterPanel, Button, Card } from '../../../design-system';
import DataTable, { DataTableColumn } from '../../../components/shared/tables/DataTable';
// Note: Badge, Separator, Alert components need to be replaced with simple HTML or alternative components

// Import componenti GDPR specifici
import { GDPREntityHeader } from './GDPREntityHeader';
import { GDPRConsentModal } from './GDPRConsentModal';
import { GDPRAuditPanel } from './GDPRAuditPanel';
import { GDPRComplianceIndicator } from './GDPRComplianceIndicator';
import { GDPRDataExportModal } from './GDPRDataExportModal';
import { GDPRDeletionRequestModal } from './GDPRDeletionRequestModal';

/**
 * Props del template principale
 */
export interface GDPREntityPageTemplateProps<T extends BaseEntity = BaseEntity> {
  /** Configurazione del template */
  config: GDPREntityPageConfig<T>;

  /** Dati iniziali (opzionale) */
  initialData?: T[];

  /** Callback personalizzate */
  onEntityCreate?: (entity: Omit<T, 'id'>) => Promise<T>;
  onEntityUpdate?: (id: string, entity: Partial<T>) => Promise<T>;
  onEntityDelete?: (id: string) => Promise<void>;
  onBulkDelete?: (ids: string[]) => Promise<void>;
  onDataExport?: (format: string, filters?: any) => Promise<Blob>;
  onDataImport?: (file: File) => Promise<void>;

  /** Componenti personalizzati */
  customComponents?: {
    header?: React.ComponentType<any>;
    toolbar?: React.ComponentType<any>;
    table?: React.ComponentType<any>;
    sidebar?: React.ComponentType<any>;
  };

  /** Classi CSS personalizzate */
  className?: string;

  /** Stili inline personalizzati */
  style?: React.CSSProperties;
}

/**
 * Template principale per pagine entit GDPR-compliant
 */
export function GDPREntityPageTemplate<T extends BaseEntity = BaseEntity>({
  config,
  initialData,
  onEntityCreate,
  onEntityUpdate,
  onEntityDelete,
  onBulkDelete,
  onDataExport,
  onDataImport,
  customComponents,
  className,
  style
}: GDPREntityPageTemplateProps<T>) {
  // Hook per autenticazione e autorizzazione con gestione sicura
  let authData;
  let hasPermissionFunc;

  try {
    authData = useAuth();
    hasPermissionFunc = authData.hasPermission;
  } catch (error) {
    console.warn(' GDPREntityPageTemplate: AuthContext not yet initialized:', error.message);
    return (
      <div className="h-64 flex justify-center items-center">
        <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  const { isAuthenticated, isLoading: authLoading, user } = authData;

  // Verifica autorizzazione
  if (authLoading) {
    return (
      <div className="h-64 flex justify-center items-center">
        <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  // Verifica permessi specifici per la risorsa
  const resourceName = config.entity.name.toLowerCase();

  // Debug logging per troubleshooting
  console.log(' GDPREntityPageTemplate - Debug permessi:', {
    resourceName,
    entityName: config.entity.name,
    user: user ? { id: user.id, email: user.email, roles: user.roles } : null,
    isAuthenticated,
    authLoading
  });

  const hasReadPermission = hasPermissionFunc(resourceName, 'read');
  console.log(' hasPermission result:', {
    resource: resourceName,
    action: 'read',
    result: hasReadPermission
  });

  if (!hasReadPermission) {
    console.log(' Accesso negato per:', resourceName);
    return (
      <div className="h-64 flex flex-col justify-center items-center text-center px-4">
        <h1 className="text-2xl font-bold mb-4">Accesso negato</h1>
        <p className="text-gray-600 mb-6">
          Non hai i permessi necessari per accedere a questa sezione.
        </p>
        <p className="text-sm text-gray-500">
          Risorsa richiesta: {resourceName} (lettura)
        </p>
        <div className="mt-4 text-xs text-gray-400">
          <p>Debug info:</p>
          <p>User: {user?.email || 'N/A'}</p>
          <p>Roles: {user?.roles?.join(', ') || 'N/A'}</p>
          <p>Resource: {resourceName}</p>
        </div>
      </div>
    );
  }

  console.log(' Accesso consentito per:', resourceName);

  // Hooks principali
  const {
    state,
    actions,
    loading,
    error
  } = useGDPREntityPage<T>({
    config,
    initialData,
    onEntityCreate,
    onEntityUpdate,
    onEntityDelete,
    onBulkDelete
  });

  const {
    consents,
    requestConsent,
    revokeConsent,
    checkConsent
  } = useGDPRConsent({
    config: config.gdpr.consentConfig,
    personId: user?.id || 'anonymous'
  });

  const {
    auditLog,
    logAction,
    getAuditHistory
  } = useGDPRAudit({
    config: config.gdpr.auditConfig,
    entityType: config.entity.name
  });

  // Stati locali
  const [searchQuery, setSearchQuery] = useState('');
  const [activeFilters, setActiveFilters] = useState<Record<string, any>>({});
  const [selectedEntities, setSelectedEntities] = useState<T[]>([]);
  const [showGDPRConsent, setShowGDPRConsent] = useState(false);
  const [showAuditPanel, setShowAuditPanel] = useState(false);
  const [showDataExport, setShowDataExport] = useState(false);
  const [showDeletionRequest, setShowDeletionRequest] = useState(false);

  // Verifica consensi GDPR all'avvio
  useEffect(() => {
    if (config.gdpr.requiresConsent) {
      checkRequiredConsents();
    }
  }, [config.gdpr.requiresConsent]);

  // Funzione per verificare consensi richiesti
  const checkRequiredConsents = useCallback(async () => {
    if (!config.gdpr.consentConfig) return;

    const verification = await checkConsent(config.gdpr.consentConfig.requiredConsents);

    if (!verification.hasConsent && verification.missingConsents.length > 0) {
      setShowGDPRConsent(true);
    }
  }, [config.gdpr.consentConfig, checkConsent]);

  // Dati filtrati e ordinati
  const filteredEntities = useMemo(() => {
    let filtered = state.entities;

    // Applica ricerca
    if (searchQuery) {
      filtered = filtered.filter(entity => {
        return config.entity.columns.some(column => {
          const value = entity[column.key as keyof T];
          return value?.toString().toLowerCase().includes(searchQuery.toLowerCase());
        });
      });
    }

    // Applica filtri
    Object.entries(activeFilters).forEach(([key, value]) => {
      if (value !== undefined && value !== null && value !== '') {
        filtered = filtered.filter(entity => {
          const entityValue = entity[key as keyof T];
          return entityValue === value;
        });
      }
    });

    return filtered;
  }, [state.entities, searchQuery, activeFilters, config.entity.columns]);

  // Handlers per azioni entit
  const handleEntityCreate = useCallback(async (entityData: Omit<T, 'id'>) => {
    await logAction('CREATE', undefined, entityData);
    await actions.createEntity(entityData);
  }, [actions, logAction]);

  const handleEntityUpdate = useCallback(async (id: string, entityData: Partial<T>) => {
    await logAction('UPDATE', id, entityData);
    await actions.updateEntity(id, entityData);
  }, [actions, logAction]);

  const handleEntityDelete = useCallback(async (id: string) => {
    await logAction('DELETE', id);
    await actions.deleteEntity(id);
  }, [actions, logAction]);

  const handleBulkDelete = useCallback(async (ids: string[]) => {
    await logAction('BULK_DELETE', undefined, { ids });
    await actions.bulkDelete(ids);
    setSelectedEntities([]);
  }, [actions, logAction]);

  // Handler per export dati
  const handleDataExport = useCallback(async (format: string) => {
    await logAction('EXPORT', undefined, { format, filters: activeFilters });

    if (onDataExport) {
      const blob = await onDataExport(format, activeFilters);
      // Trigger download
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${config.entity.namePlural}-export.${format}`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    }

    setShowDataExport(false);
  }, [onDataExport, activeFilters, config.entity.namePlural, logAction]);

  // Configurazione colonne tabella con GDPR
  const tableColumns = useMemo(() => {
    return config.entity.columns.map(column => ({
      ...column,
      // Applica mascheramento per campi sensibili se necessario
      render: (value: any, entity: T) => {
        if (config.gdpr.sensitiveFields.includes(column.key)) {
          // Verifica permessi per visualizzare dati sensibili
          const userRole = user?.role || 'EMPLOYEE';
          const canViewSensitive = config.gdpr.dataMinimizationConfig?.sensitiveDataRoles.includes(userRole);

          if (!canViewSensitive) {
            const maskingConfig = config.gdpr.dataMinimizationConfig?.fieldMaskingConfig[column.key];
            if (maskingConfig) {
              return maskValue(value, maskingConfig);
            }
            return '***';
          }
        }

        return column.render ? column.render(value, entity) : value;
      }
    }));
  }, [config.entity.columns, config.gdpr.sensitiveFields, config.gdpr.dataMinimizationConfig, user]);

  // Funzione per mascherare valori
  const maskValue = (value: any, maskingConfig: any) => {
    if (!value) return value;

    const str = value.toString();
    const { pattern, showFirst = 0, showLast = 0, replacement = '*' } = maskingConfig;

    if (pattern) {
      return str.replace(new RegExp(pattern, 'g'), replacement);
    }

    if (showFirst || showLast) {
      const start = str.substring(0, showFirst);
      const end = str.substring(str.length - showLast);
      const middle = replacement.repeat(Math.max(0, str.length - showFirst - showLast));
      return start + middle + end;
    }

    return replacement.repeat(str.length);
  };

  // Render del template
  return (
    <GDPREntityPageContextProvider.Provider value={{
      config,
      state,
      actions,
      gdpr: {
        consents,
        auditLog,
        dataRetention: {
          daysRemaining: config.gdpr.dataRetentionDays,
          nextCleanup: new Date(Date.now() + config.gdpr.dataRetentionDays * 24 * 60 * 60 * 1000)
        },
        compliance: {
          score: 85, // Da calcolare dinamicamente
          issues: [],
          recommendations: []
        }
      }
    }}>
      <div className={`gdpr-entity-page ${className || ''}`} style={style}>
        {/* Alert errori */}
        {error && (
          <div className="mb-4 p-4 border border-red-300 bg-red-50 rounded-md">
            <p className="text-red-800 text-sm">{error}</p>
          </div>
        )}

        {/* Header della pagina */}
        {customComponents?.header ? (
          <customComponents.header
            config={config}
            entityCount={filteredEntities.length}
            selectedCount={selectedEntities.length}
          />
        ) : (
          <GDPREntityHeader
            title={config.ui.header.title}
            subtitle={config.ui.header.subtitle}
            entityCount={filteredEntities.length}
            selectedCount={selectedEntities.length}
            showGDPRIndicators={config.ui.header.showGDPRIndicators}
            breadcrumb={config.ui.header.breadcrumb}
            customActions={config.ui.header.customActions}
            onAuditClick={() => setShowAuditPanel(true)}
            onExportClick={() => setShowDataExport(true)}
            onConsentClick={() => setShowGDPRConsent(true)}
          />
        )}

        <hr className="my-4 border-gray-200" />

        {/* Layout principale */}
        <EntityListLayout
          sidebar={config.ui.layout.showSidebar ? (
            customComponents?.sidebar ? (
              <customComponents.sidebar config={config} />
            ) : (
              <Card className="p-4">
                <GDPRComplianceIndicator
                  score={85}
                  issues={[]}
                  recommendations={[]}
                />

                {config.ui.header.showGDPRIndicators && (
                  <div className="mt-4 space-y-2">
                    <span className="inline-block px-2 py-1 text-xs font-medium bg-green-100 text-green-800 rounded border border-green-200">GDPR Compliant</span>
                    <span className="inline-block px-2 py-1 text-xs font-medium bg-gray-100 text-gray-800 rounded border border-gray-200">Audit Enabled</span>
                    <span className="inline-block px-2 py-1 text-xs font-medium bg-blue-100 text-blue-800 rounded border border-blue-200">Consent Managed</span>
                  </div>
                )}
              </Card>
            )
          ) : undefined}
          sidebarWidth={config.ui.layout.sidebar?.width}
          sidebarCollapsed={state.ui.sidebarCollapsed}
          onSidebarToggle={() => actions.toggleSidebar?.()}
        >
          {/* Toolbar */}
          {customComponents?.toolbar ? (
            <customComponents.toolbar
              config={config}
              searchQuery={searchQuery}
              onSearchChange={setSearchQuery}
              filters={activeFilters}
              onFiltersChange={setActiveFilters}
              selectedEntities={selectedEntities}
              onBulkDelete={() => handleBulkDelete(selectedEntities.map(e => e.id))}
            />
          ) : (
            <div className="flex flex-col gap-4 mb-6">
              {/* Barra di ricerca e azioni principali */}
              <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
                <div className="flex-1 max-w-md">
                  {config.ui.toolbar.showSearch && (
                    <SearchBar
                      value={searchQuery}
                      onChange={setSearchQuery}
                      placeholder={config.ui.toolbar.searchPlaceholder || `Cerca ${config.entity.namePlural.toLowerCase()}...`}
                    />
                  )}
                </div>

                <div className="flex gap-2">
                  {/* Pulsante Crea */}
                  <Button
                    onClick={() => actions.openModal('create')}
                    disabled={loading}
                  >
                    Crea {config.entity.name}
                  </Button>

                  {/* Azioni personalizzate toolbar */}
                  {config.ui.toolbar.customActions?.map(action => (
                    <Button
                      key={action.id}
                      variant={action.variant || 'outline'}
                      onClick={action.onClick}
                      disabled={action.disabled || loading}
                      title={action.tooltip}
                    >
                      {action.icon && <span className="mr-2">{action.icon}</span>}
                      {action.label}
                    </Button>
                  ))}

                  {/* Azioni bulk se ci sono selezioni */}
                  {selectedEntities.length > 0 && (
                    <>
                      <Button
                        variant="destructive"
                        onClick={() => handleBulkDelete(selectedEntities.map(e => e.id))}
                        disabled={loading}
                      >
                        Elimina Selezionati ({selectedEntities.length})
                      </Button>

                      <Button
                        variant="outline"
                        onClick={() => setShowDeletionRequest(true)}
                        disabled={loading}
                      >
                        Richiesta Cancellazione GDPR
                      </Button>
                    </>
                  )}
                </div>
              </div>

              {/* Pannello filtri */}
              {config.ui.toolbar.showFilters && config.entity.filters.length > 0 && (
                <FilterPanel
                  filters={config.entity.filters}
                  values={activeFilters}
                  onChange={setActiveFilters}
                />
              )}
            </div>
          )}

          {/* Tabella dati */}
          {customComponents?.table ? (
            <customComponents.table
              config={config}
              data={filteredEntities}
              columns={tableColumns}
              loading={loading}
              selectedEntities={selectedEntities}
              onSelectionChange={setSelectedEntities}
              onEntityEdit={(entity) => actions.openModal('edit', entity)}
              onEntityDelete={handleEntityDelete}
            />
          ) : (
            <DataTable
              data={filteredEntities}
              columns={tableColumns}
              loading={loading}
              pagination={{
                page: state.pagination.page,
                pageSize: state.pagination.pageSize,
                total: state.pagination.total,
                onPageChange: actions.setPage,
                onPageSizeChange: actions.setPageSize
              }}
              sorting={{
                field: state.sorting?.field,
                direction: state.sorting?.direction,
                onSortChange: actions.setSorting
              }}
              selection={{
                selectedItems: selectedEntities,
                onSelectionChange: setSelectedEntities,
                showCheckboxes: config.ui.table.showSelectionCheckboxes
              }}
              density={state.ui.density}
              onDensityChange={actions.setDensity}
              enableColumnResizing={config.ui.table.enableColumnResizing}
              enableColumnReordering={config.ui.table.enableColumnReordering}
              showRowNumbers={config.ui.table.showRowNumbers}
              virtualization={config.ui.table.virtualization}
            />
          )}
        </EntityListLayout>

        {/* Modali GDPR */}
        <GDPRConsentModal
          open={showGDPRConsent}
          onClose={() => setShowGDPRConsent(false)}
          config={config.gdpr.consentConfig}
          currentConsents={consents}
          onConsentChange={requestConsent}
          onConsentRevoke={revokeConsent}
        />

        {showAuditPanel && (
          <GDPRAuditPanel
            entityId="current-entity"
            entityType={config.entity.name}
            auditLogs={auditLog}
            onExport={() => handleDataExport('audit-log')}
            loading={loading}
          />
        )}

        <GDPRDataExportModal
          open={showDataExport}
          onClose={() => setShowDataExport(false)}
          config={config.gdpr.dataPortabilityConfig}
          onExport={handleDataExport}
          entityType={config.entity.name}
        />

        <GDPRDeletionRequestModal
          open={showDeletionRequest}
          onClose={() => setShowDeletionRequest(false)}
          selectedEntities={selectedEntities}
          config={config.gdpr.rightToBeForgottenConfig}
          onSubmitRequest={(reason) => {
            // Implementa logica richiesta cancellazione
            console.log('Richiesta cancellazione GDPR:', { entities: selectedEntities, reason });
            setShowDeletionRequest(false);
          }}
        />
      </div>
    </GDPREntityPageContextProvider.Provider>
  );
}

// Hook per utilizzare il context
export const useGDPREntityPageContext = () => {
  const context = React.useContext(GDPREntityPageContextProvider);
  if (!context) {
    throw new Error('useGDPREntityPageContext must be used within GDPREntityPageTemplate');
  }
  return context;
};

export default GDPREntityPageTemplate;

/**
 * GDPR Entity Table - Componente tabella per visualizzare le entit
 * 
 * Componente tabella che include:
 * - Visualizzazione dati in formato tabella
 * - Ordinamento colonne
 * - Selezione righe
 * - Azioni per riga
 * - Supporto GDPR
 * 
 * @version 1.0
 * @date 30 Dicembre 2024
 */

import React from 'react';
import { Button, Badge } from '../../../design-system';
import { 
  DropdownMenu, 
  DropdownMenuTrigger, 
  DropdownMenuContent, 
  DropdownMenuItem
} from '../../../design-system';
import { 
  ChevronUp, 
  ChevronDown, 
  MoreVertical,
  Eye,
  Edit,
  Trash2,
  Download
} from 'lucide-react';
import { ColumnConfig, EntityAction, BaseEntity } from '../types';

/**
 * Props del componente GDPREntityTable
 */
export interface GDPREntityTableProps<T extends BaseEntity = BaseEntity> {
  /** Dati da visualizzare */
  data: T[];

  /** Configurazione colonne */
  columns: ColumnConfig[];

  /** Colonne visibili */
  visibleColumns: string[];

  /** Configurazione ordinamento */
  sortConfig?: {
    field: string;
    direction: 'asc' | 'desc';
  };

  /** Callback per cambio ordinamento */
  onSort?: (field: string, direction: 'asc' | 'desc') => void;

  /** Elementi selezionati */
  selectedItems: string[];

  /** Callback per selezione elementi */
  onSelectionChange: (selectedIds: string[]) => void;

  /** Azioni disponibili per riga */
  rowActions?: EntityAction[];

  /** Callback per azioni riga */
  onRowAction?: (actionKey: string, entity: T) => void;

  /** Stato di caricamento */
  loading?: boolean;

  /** Messaggio stato vuoto */
  emptyMessage?: string;

  /** Mostra checkbox selezione */
  showSelection?: boolean;

  /** Mostra colonna azioni */
  showActions?: boolean;

  /** Classi CSS personalizzate */
  className?: string;
}

/**
 * Componente tabella per visualizzare le entit
 */
export function GDPREntityTable<T extends BaseEntity = BaseEntity>({
  data,
  columns,
  visibleColumns,
  sortConfig,
  onSort,
  selectedItems,
  onSelectionChange,
  rowActions,
  onRowAction,
  loading = false,
  emptyMessage = 'Nessun elemento trovato',
  showSelection = true,
  showActions = true,
  className
}: GDPREntityTableProps<T>) {

  // Filtra colonne visibili
  const displayColumns = columns.filter(col => visibleColumns.includes(col.key));

  // Gestione selezione
  const isAllSelected = data.length > 0 && selectedItems.length === data.length;
  const isPartiallySelected = selectedItems.length > 0 && selectedItems.length < data.length;

  const handleSelectAll = () => {
    if (isAllSelected) {
      onSelectionChange([]);
    } else {
      onSelectionChange(data.map(item => item.id));
    }
  };

  const handleSelectItem = (id: string) => {
    if (selectedItems.includes(id)) {
      onSelectionChange(selectedItems.filter(itemId => itemId !== id));
    } else {
      onSelectionChange([...selectedItems, id]);
    }
  };

  // Gestione ordinamento
  const handleSort = (field: string) => {
    if (!onSort) return;

    let direction: 'asc' | 'desc' = 'asc';
    if (sortConfig?.field === field && sortConfig.direction === 'asc') {
      direction = 'desc';
    }

    onSort(field, direction);
  };

  // Renderizza icona ordinamento
  const renderSortIcon = (field: string) => {
    if (sortConfig?.field !== field) return null;

    return sortConfig.direction === 'asc' ? (
      <ChevronUp className="h-4 w-4" />
    ) : (
      <ChevronDown className="h-4 w-4" />
    );
  };

  // Renderizza valore cella
  const renderCellValue = (column: ColumnConfig, entity: T) => {
    const value = (entity as any)[column.key];

    if (column.formatter) {
      return column.formatter(value, entity);
    }

    // Formattazione di default per tipi comuni
    if (value === null || value === undefined) {
      return <span className="text-muted-foreground">-</span>;
    }

    if (typeof value === 'boolean') {
      return (
        <Badge variant={value ? 'secondary' : 'outline'}>
          {value ? 'S' : 'No'}
        </Badge>
      );
    }

    if (value instanceof Date) {
      return value.toLocaleDateString('it-IT');
    }

    if (typeof value === 'string' && value.includes('@')) {
      return <span className="font-mono text-sm">{value}</span>;
    }

    return String(value);
  };

  if (loading) {
    return (
      <div className={`gdpr-entity-table ${className || ''}`}>
        <div className="border rounded-lg">
          <div className="p-8 text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-muted-foreground">Caricamento dati...</p>
          </div>
        </div>
      </div>
    );
  }

  if (data.length === 0) {
    return (
      <div className={`gdpr-entity-table ${className || ''}`}>
        <div className="border rounded-lg">
          <div className="p-8 text-center">
            <p className="text-muted-foreground">{emptyMessage}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`gdpr-entity-table ${className || ''}`}>
      <div className="border rounded-lg overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            {/* Header */}
            <thead className="bg-muted/50">
              <tr>
                {/* Checkbox selezione */}
                {showSelection && (
                  <th className="w-12 p-4">
                    <input
                      type="checkbox"
                      checked={isAllSelected}
                      ref={(input) => {
                        if (input) input.indeterminate = isPartiallySelected;
                      }}
                      onChange={handleSelectAll}
                      className="rounded border-gray-300 text-primary focus:ring-primary"
                    />
                  </th>
                )}

                {/* Colonne dati */}
                {displayColumns.map((column) => (
                  <th
                    key={column.key}
                    className={`p-4 text-left font-medium text-sm ${
                      column.sortable ? 'cursor-pointer hover:bg-muted' : ''
                    } ${column.headerClassName || ''}`}
                    onClick={() => column.sortable && handleSort(column.key)}
                    style={{
                      width: column.width,
                      minWidth: column.minWidth,
                      maxWidth: column.maxWidth
                    }}
                  >
                    <div className="flex items-center gap-2">
                      <span>{column.label}</span>
                      {column.sortable && renderSortIcon(column.key)}
                    </div>
                  </th>
                ))}

                {/* Colonna azioni */}
                {showActions && rowActions && rowActions.length > 0 && (
                  <th className="w-16 p-4 text-center">
                    <span className="text-sm font-medium">Azioni</span>
                  </th>
                )}
              </tr>
            </thead>

            {/* Body */}
            <tbody>
              {data.map((entity, index) => (
                <tr
                  key={entity.id}
                  className={`border-t hover:bg-muted/25 ${
                    selectedItems.includes(entity.id) ? 'bg-muted/50' : ''
                  }`}
                >
                  {/* Checkbox selezione */}
                  {showSelection && (
                    <td className="p-4">
                      <input
                        type="checkbox"
                        checked={selectedItems.includes(entity.id)}
                        onChange={() => handleSelectItem(entity.id)}
                        className="rounded border-gray-300 text-primary focus:ring-primary"
                      />
                    </td>
                  )}

                  {/* Celle dati */}
                  {displayColumns.map((column) => (
                    <td
                      key={column.key}
                      className={`p-4 ${column.cellClassName || ''}`}
                      style={{
                        textAlign: column.align || 'left'
                      }}
                    >
                      {renderCellValue(column, entity)}
                    </td>
                  ))}

                  {/* Cella azioni */}
                  {showActions && rowActions && rowActions.length > 0 && (
                    <td className="p-4 text-center">
                      {rowActions.length === 1 ? (
                        // Singola azione - pulsante diretto
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => onRowAction?.(rowActions[0].key, entity)}
                          disabled={
                            typeof rowActions[0].disabled === 'function' 
                              ? rowActions[0].disabled(entity) 
                              : rowActions[0].disabled
                          }
                          className="h-8 w-8 p-0"
                        >
                          {rowActions[0].icon || <Eye className="h-4 w-4" />}
                        </Button>
                      ) : (
                        // Multiple azioni - dropdown menu
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" size="sm" className="h-8 w-8 p-0">
                              <MoreVertical className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            {rowActions
                              .filter(action => 
                                typeof action.visible === 'function' 
                                  ? action.visible(entity) 
                                  : action.visible !== false
                              )
                              .map((action) => (
                                <DropdownMenuItem
                                  key={action.key}
                                  onClick={() => onRowAction?.(action.key, entity)}
                                  disabled={
                                    typeof action.disabled === 'function' 
                                      ? action.disabled(entity) 
                                      : action.disabled
                                  }
                                  className="flex items-center gap-2"
                                >
                                  {action.icon && <span>{action.icon}</span>}
                                  {action.label}
                                </DropdownMenuItem>
                              ))}
                          </DropdownMenuContent>
                        </DropdownMenu>
                      )}
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default GDPREntityTable;

/**
 * GDPR Entity Toolbar - Toolbar con controlli di ricerca, filtri e azioni
 * 
 * Componente toolbar che include:
 * - Barra di ricerca
 * - Toggle vista (tabella/griglia)
 * - Pulsanti azioni batch
 * - Dropdown per aggiungere entit
 * 
 * @version 1.0
 * @date 30 Dicembre 2024
 */

import React from 'react';
import { Button, Input, Badge } from '../../../design-system';
import { 
  DropdownMenu, 
  DropdownMenuTrigger, 
  DropdownMenuContent, 
  DropdownMenuItem
} from '../../../design-system';
import { 
  ChevronDown,
  Columns,
  Download,
  Filter,
  Grid,
  Plus,
  Search,
  Settings,
  Table,
  X
} from 'lucide-react';
import { FilterState, ColumnConfig, EntityAction } from '../types';

export type ViewMode = 'table' | 'grid';

/**
 * Props del componente GDPREntityToolbar
 */
export interface GDPREntityToolbarProps {
  /** Valore corrente della ricerca */
  searchValue: string;

  /** Callback per cambio ricerca */
  onSearchChange: (value: string) => void;

  /** Stato corrente dei filtri */
  filters: FilterState;

  /** Callback per cambio filtri */
  onFiltersChange: (filters: FilterState) => void;

  /** Configurazione colonne disponibili */
  columns: ColumnConfig[];

  /** Colonne visibili */
  visibleColumns: string[];

  /** Callback per cambio visibilit colonne */
  onColumnsChange: (columns: string[]) => void;

  /** Modalit vista corrente */
  viewMode: ViewMode;

  /** Callback per cambio modalit vista */
  onViewModeChange: (mode: ViewMode) => void;

  /** Numero di elementi selezionati */
  selectedCount: number;

  /** Azioni disponibili per aggiungere entit */
  addActions?: EntityAction[];

  /** Azioni batch disponibili */
  batchActions?: EntityAction[];

  /** Callback per azioni batch */
  onBatchAction?: (actionKey: string) => void;

  /** Mostra controlli GDPR */
  showGDPRControls?: boolean;

  /** Callback per export GDPR */
  onGDPRExport?: () => void;

  /** Callback per audit */
  onAuditAction?: () => void;

  /** Classi CSS personalizzate */
  className?: string;
}

/**
 * Toolbar con controlli per la gestione delle entit
 */
export function GDPREntityToolbar({
  searchValue,
  onSearchChange,
  filters,
  onFiltersChange,
  columns,
  visibleColumns,
  onColumnsChange,
  viewMode,
  onViewModeChange,
  selectedCount,
  addActions,
  batchActions,
  onBatchAction,
  showGDPRControls = true,
  onGDPRExport,
  onAuditAction,
  className
}: GDPREntityToolbarProps) {

  // Conta filtri attivi
  const activeFiltersCount = Object.values(filters).filter(value => 
    value !== null && value !== undefined && value !== '' && 
    (Array.isArray(value) ? value.length > 0 : true)
  ).length;

  return (
    <div className={`gdpr-entity-toolbar space-y-4 ${className || ''}`}>
      {/* Prima riga: Ricerca e azioni principali */}
      <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
        {/* Sezione sinistra: Ricerca */}
        <div className="flex-1 max-w-md">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Cerca entit..."
              value={searchValue}
              onChange={(e) => onSearchChange(e.target.value)}
              className="pl-10"
            />
            {searchValue && (
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onSearchChange('')}
                className="absolute right-1 top-1/2 transform -translate-y-1/2 h-6 w-6 p-0"
              >
                <X className="h-3 w-3" />
              </Button>
            )}
          </div>
        </div>

        {/* Sezione destra: Azioni principali */}
        <div className="flex items-center gap-2">
          {/* Dropdown Aggiungi Entit */}
          {addActions && addActions.length > 0 && (
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button className="flex items-center gap-2">
                  <Plus className="h-4 w-4" />
                  Aggiungi
                  <ChevronDown className="h-3 w-3" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                {addActions.map((action) => (
                  <DropdownMenuItem
                    key={action.key}
                    onClick={() => action.onClick({})}
                    disabled={typeof action.disabled === 'function' ? action.disabled({}) : action.disabled}
                    className="flex items-center gap-2"
                  >
                    {action.icon && <span>{action.icon}</span>}
                    {action.label}
                  </DropdownMenuItem>
                ))}
              </DropdownMenuContent>
            </DropdownMenu>
          )}

          {/* Controlli GDPR */}
          {showGDPRControls && (
            <>
              {onGDPRExport && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={onGDPRExport}
                  className="flex items-center gap-2"
                >
                  <Download className="h-4 w-4" />
                  Export GDPR
                </Button>
              )}

              {onAuditAction && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={onAuditAction}
                  className="flex items-center gap-2"
                >
                  <Settings className="h-4 w-4" />
                  Audit
                </Button>
              )}
            </>
          )}
        </div>
      </div>

      {/* Seconda riga: Filtri e controlli vista */}
      <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between">
        {/* Sezione sinistra: Filtri */}
        <div className="flex items-center gap-2 flex-wrap">
          {/* Pulsante Filtri */}
          <Button variant="outline" size="sm" className="flex items-center gap-2">
            <Filter className="h-4 w-4" />
            Filtri
            {activeFiltersCount > 0 && (
              <Badge variant="secondary" className="ml-1 h-5 w-5 p-0 text-xs">
                {activeFiltersCount}
              </Badge>
            )}
          </Button>

          {/* Badge filtri attivi */}
          {activeFiltersCount > 0 && (
            <div className="flex items-center gap-1 flex-wrap">
              {Object.entries(filters).map(([key, value]) => {
                if (!value || (Array.isArray(value) && value.length === 0)) return null;

                return (
                  <Badge
                    key={key}
                    variant="secondary"
                    className="flex items-center gap-1 text-xs"
                  >
                    {key}: {Array.isArray(value) ? value.join(', ') : String(value)}
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => onFiltersChange({ ...filters, [key]: null })}
                      className="h-3 w-3 p-0 ml-1"
                    >
                      <X className="h-2 w-2" />
                    </Button>
                  </Badge>
                );
              })}
            </div>
          )}
        </div>

        {/* Sezione destra: Controlli vista */}
        <div className="flex items-center gap-2">
          {/* Selettore Colonne */}
          <Button variant="outline" size="sm" className="flex items-center gap-2">
            <Columns className="h-4 w-4" />
            Colonne
          </Button>

          {/* Toggle Vista */}
          <div className="flex items-center border rounded-md">
            <Button
              variant={viewMode === 'table' ? 'primary' : 'ghost'}
              size="sm"
              onClick={() => onViewModeChange('table')}
              className="rounded-r-none border-r"
            >
              <Table className="h-4 w-4" />
            </Button>
            <Button
              variant={viewMode === 'grid' ? 'primary' : 'ghost'}
              size="sm"
              onClick={() => onViewModeChange('grid')}
              className="rounded-l-none"
            >
              <Grid className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </div>

      {/* Terza riga: Azioni batch (visibile solo se ci sono elementi selezionati) */}
      {selectedCount > 0 && batchActions && batchActions.length > 0 && (
        <div className="flex items-center justify-between p-3 bg-muted rounded-lg">
          <div className="flex items-center gap-2">
            <Badge variant="secondary">
              {selectedCount} {selectedCount === 1 ? 'elemento selezionato' : 'elementi selezionati'}
            </Badge>
          </div>

          <div className="flex items-center gap-2">
            {batchActions.map((action) => {
              // Mappa i variant non supportati a quelli supportati
              let buttonVariant: 'primary' | 'secondary' | 'outline' | 'ghost' | 'destructive' = 'outline';
              if (action.variant === 'danger') {
                buttonVariant = 'destructive';
              } else if (action.variant && ['primary', 'secondary', 'outline'].includes(action.variant)) {
                buttonVariant = action.variant as 'primary' | 'secondary' | 'outline';
              }

              return (
                <Button
                  key={action.key}
                  variant={buttonVariant}
                  size="sm"
                  onClick={() => onBatchAction?.(action.key)}
                  disabled={typeof action.disabled === 'function' ? action.disabled({}) : action.disabled}
                  className="flex items-center gap-2"
                >
                  {action.icon && <span>{action.icon}</span>}
                  {action.label}
                </Button>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
}

export default GDPREntityToolbar;

