/**
 * Factory per creare configurazione personalizzata
 */
export class ConfigFactory {

  /**
   * Crea configurazione base per un tipo di entit
   */
  static createBaseConfig(
    entityType: string,
    title: string,
    overrides: Partial<GDPREntityPageConfig> = {}
  ): GDPREntityPageConfig {
    return {
      ...DEFAULT_TEMPLATE_CONFIG,
      entity: {
        ...DEFAULT_TEMPLATE_CONFIG.entity,
        name: entityType,
        namePlural: title,
        description: `Gestione ${title.toLowerCase()}`
      },
      ...overrides
    };
  }

  /**
   * Crea configurazione per entit semplice (senza GDPR complesso)
   */
  static createSimpleConfig(
    entityType: string,
    title: string,
    overrides: Partial<GDPREntityPageConfig> = {}
  ): GDPREntityPageConfig {
    const simpleGDPR: GDPRConfig = {
      ...DEFAULT_GDPR_CONFIG,
      consent: {
        ...DEFAULT_GDPR_CONFIG.consent!,
        requiredConsents: ['data_processing'],
        optionalConsents: []
      },
      audit: {
        ...DEFAULT_GDPR_CONFIG.audit!,
        level: 'minimal'
      },
      dataMinimization: {
        ...DEFAULT_GDPR_CONFIG.dataMinimization!,
        enabled: false
      }
    };

    return this.createBaseConfig(entityType, title, {
      gdpr: simpleGDPR,
      ...overrides
    });
  }

  /**
   * Crea configurazione per entit con dati sensibili
   */
  static createSensitiveDataConfig(
    entityType: string,
    title: string,
    overrides: Partial<GDPREntityPageConfig> = {}
  ): GDPREntityPageConfig {
    const sensitiveGDPR: GDPRConfig = {
      ...DEFAULT_GDPR_CONFIG,
      audit: {
        ...DEFAULT_GDPR_CONFIG.audit!,
        level: 'comprehensive',
        encryptSensitiveData: true
      },
      dataMinimization: {
        ...DEFAULT_GDPR_CONFIG.dataMinimization!,
        autoApply: true
      },
      privacyImpact: {
        ...DEFAULT_GDPR_CONFIG.privacyImpact!,
        riskAssessmentRequired: true,
        manualReviewRequired: true
      }
    };

    return this.createBaseConfig(entityType, title, {
      gdpr: sensitiveGDPR,
      ...overrides
    });
  }

  /**
   * Crea configurazione per entit di sola lettura
   */
  static createReadOnlyConfig(
    entityType: string,
    title: string,
    overrides: Partial<GDPREntityPageConfig> = {}
  ): GDPREntityPageConfig {
    const readOnlyPermissions: PermissionsTemplateConfig = {
      required: {
        create: false,
        read: true,
        update: false,
        delete: false,
        export: true,
        import: false
      },
      optional: {
        bulkEdit: false,
        bulkDelete: false,
        advancedSearch: true,
        auditView: true,
        gdprManagement: false
      }
    };

    const readOnlyUI: UITemplateConfig = {
      ...DEFAULT_UI_CONFIG,
      toolbar: {
        ...DEFAULT_UI_CONFIG.toolbar,
        showImport: false,
        showBulkActions: false
      },
      modals: {
        ...DEFAULT_UI_CONFIG.modals,
        showCreateModal: false,
        showEditModal: false,
        showDeleteConfirmation: false,
        showBulkDeleteConfirmation: false
      }
    };

    return this.createBaseConfig(entityType, title, {
      permissions: readOnlyPermissions,
      ui: readOnlyUI,
      ...overrides
    });
  }
}

export default {
  DEFAULT_GDPR_CONFIG,
  DEFAULT_PERMISSIONS,
  DEFAULT_UI_CONFIG,
  DEFAULT_API_CONFIG,
  DEFAULT_VALIDATION_CONFIG,
  DEFAULT_DATA_EXCHANGE_CONFIG,
  DEFAULT_ADVANCED_CONFIG,
  DEFAULT_ENTITY_COLUMNS,
  DEFAULT_ENTITY_FILTERS,
  DEFAULT_ENTITY_ACTIONS,
  DEFAULT_TEMPLATE_CONFIG,
  ConfigFactory
};

