import { useState, useEffect, useCallback } from 'react';
import { apiGet } from '../../../services/api';
import { getLoadingErrorMessage } from '../../../utils/errorUtils';
import { useToast } from '../../../hooks/useToast';

/**
 * Hook per la gestione dei dati delle entit
 * Centralizza caricamento, errori e refresh dei dati
 */
export interface UseEntityDataProps {
  apiEndpoint: string;
  entityNamePlural: string;
  entityDisplayNamePlural: string;
}

export interface UseEntityDataReturn<T> {
  entities: T[];
  loading: boolean;
  error: string | null;
  refreshData: () => Promise<void>;
  setEntities: React.Dispatch<React.SetStateAction<T[]>>;
}

export const useEntityData = <T extends Record<string, any>>({
  apiEndpoint,
  entityNamePlural,
  entityDisplayNamePlural
}: UseEntityDataProps): UseEntityDataReturn<T> => {
  const [entities, setEntities] = useState<T[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { showToast } = useToast();

  // Costruisce l'URL dell'API con parametri specifici per le persone
  const buildApiUrl = useCallback((endpoint: string): string => {
    if (endpoint === '/api/persons' || endpoint === '/api/v1/persons') {
      const params = new URLSearchParams();
      params.append('page', '1');
      params.append('limit', '50');
      params.append('sortBy', 'lastLogin');
      params.append('sortOrder', 'desc');
      return `${endpoint}?${params.toString()}`;
    }
    return endpoint;
  }, []);

  // Processa la risposta dell'API
  const processApiResponse = useCallback((response: any): T[] => {
    // Gestisci la risposta paginata per l'endpoint delle persone
    if ((apiEndpoint === '/api/persons' || apiEndpoint === '/api/v1/persons') && 
        response && typeof response === 'object' && response.persons) {
      return response.persons;
    } else if (Array.isArray(response)) {
      return response;
    } else {
      console.warn(` Risposta API non  un array:`, response);
      return [];
    }
  }, [apiEndpoint]);

  // Caricamento dati
  const loadEntities = useCallback(async (): Promise<void> => {
    try {
      setLoading(true);
      setError(null);

      console.log(` Caricamento ${entityDisplayNamePlural}...`);

      const apiUrl = buildApiUrl(apiEndpoint);
      console.log(` Chiamata API: ${apiUrl}`);

      const response = await apiGet<any>(apiUrl);
      console.log(` Risposta API ${entityNamePlural}:`, response);

      const processedEntities = processApiResponse(response);
      setEntities(processedEntities);
      console.log(` ${entityDisplayNamePlural} caricate:`, processedEntities.length);

    } catch (err: any) {
      console.error(` Errore caricamento ${entityDisplayNamePlural}:`, err);
      const errorMessage = getLoadingErrorMessage(
        (entityNamePlural as keyof typeof import('../../../utils/errorUtils').errorMessages.loading) || 'generic', 
        err
      );
      setError(errorMessage);
      setEntities([]);

      showToast({
        message: `Errore durante il caricamento dei ${entityDisplayNamePlural.toLowerCase()}: ${err instanceof Error ? err.message : 'Errore sconosciuto'}`,
        type: 'error'
      });
    } finally {
      setLoading(false);
    }
  }, [apiEndpoint, entityNamePlural, entityDisplayNamePlural, buildApiUrl, processApiResponse, showToast]);

  // Refresh dati (alias per loadEntities)
  const refreshData = useCallback(async (): Promise<void> => {
    await loadEntities();
  }, [loadEntities]);

  // Caricamento iniziale
  useEffect(() => {
    loadEntities();
  }, [loadEntities]);

  return {
    entities,
    loading,
    error,
    refreshData,
    setEntities
  };
};

import { useState, useMemo, useCallback } from 'react';

/**
 * Hook per la gestione di filtri, ricerca e ordinamento delle entit
 * Centralizza tutta la logica di filtraggio e ordinamento
 */
export interface FilterOption {
  key: string;
  label: string;
  options: Array<{ label: string; value: string }>;
}

export interface SortOption {
  key: string;
  label: string;
}

export interface ActiveSort {
  field: string;
  direction: 'asc' | 'desc';
}

export interface UseEntityFiltersProps<T> {
  entities: T[];
  searchFields: (keyof T)[];
  filterOptions?: FilterOption[];
  sortOptions?: SortOption[];
}

export interface UseEntityFiltersReturn<T> {
  filteredEntities: T[];
  searchQuery: string;
  setSearchQuery: (query: string) => void;
  activeFilters: Record<string, string>;
  setActiveFilters: (filters: Record<string, string>) => void;
  activeSort: ActiveSort | undefined;
  setActiveSort: (sort: ActiveSort | undefined) => void;
  clearAllFilters: () => void;
  hasActiveFilters: boolean;
}

export const useEntityFilters = <T extends Record<string, any>>({
  entities,
  searchFields,
  filterOptions = [],
  sortOptions = []
}: UseEntityFiltersProps<T>): UseEntityFiltersReturn<T> => {
  const [searchQuery, setSearchQuery] = useState('');
  const [activeFilters, setActiveFilters] = useState<Record<string, string>>({});
  const [activeSort, setActiveSort] = useState<ActiveSort | undefined>(undefined);

  // Verifica se ci sono filtri attivi
  const hasActiveFilters = useMemo(() => {
    return searchQuery.trim() !== '' || 
           Object.values(activeFilters).some(value => value !== '') ||
           activeSort !== undefined;
  }, [searchQuery, activeFilters, activeSort]);

  // Applica filtri
  const applyFilters = useCallback((data: T[]): T[] => {
    let filtered = data;

    // Applica filtri attivi
    Object.entries(activeFilters).forEach(([key, value]) => {
      if (value) {
        filtered = filtered.filter(entity => {
          const entityValue = entity[key];
          return entityValue === value || String(entityValue).toLowerCase().includes(value.toLowerCase());
        });
      }
    });

    return filtered;
  }, [activeFilters]);

  // Applica ricerca
  const applySearch = useCallback((data: T[]): T[] => {
    if (!searchQuery) return data;

    const searchLower = searchQuery.toLowerCase();
    return data.filter(entity => {
      return searchFields.some(field => {
        const value = entity[field];
        return value && String(value).toLowerCase().includes(searchLower);
      });
    });
  }, [searchQuery, searchFields]);

  // Applica ordinamento
  const applySort = useCallback((data: T[]): T[] => {
    if (!activeSort) return data;

    return [...data].sort((a, b) => {
      const valueA = a[activeSort.field];
      const valueB = b[activeSort.field];

      if (valueA == null && valueB == null) return 0;
      if (valueA == null) return activeSort.direction === 'asc' ? -1 : 1;
      if (valueB == null) return activeSort.direction === 'asc' ? 1 : -1;

      const compareValueA = typeof valueA === 'string' ? valueA.toLowerCase() : valueA;
      const compareValueB = typeof valueB === 'string' ? valueB.toLowerCase() : valueB;

      if (compareValueA < compareValueB) return activeSort.direction === 'asc' ? -1 : 1;
      if (compareValueA > compareValueB) return activeSort.direction === 'asc' ? 1 : -1;
      return 0;
    });
  }, [activeSort]);

  // Calcola entit filtrate
  const filteredEntities = useMemo(() => {
    let result = entities;

    // Applica filtri in sequenza
    result = applyFilters(result);
    result = applySearch(result);
    result = applySort(result);

    return result;
  }, [entities, applyFilters, applySearch, applySort]);

  // Pulisce tutti i filtri
  const clearAllFilters = useCallback(() => {
    setSearchQuery('');
    setActiveFilters({});
    setActiveSort(undefined);
  }, []);

  return {
    filteredEntities,
    searchQuery,
    setSearchQuery,
    activeFilters,
    setActiveFilters,
    activeSort,
    setActiveSort,
    clearAllFilters,
    hasActiveFilters
  };
};

import { useCallback } from 'react';
import { apiDelete } from '../../../services/api';
import { useToast } from '../../../hooks/useToast';

/**
 * Hook per la gestione delle operazioni CRUD sulle entit
 * Centralizza le operazioni di eliminazione, creazione e modifica
 */
export interface UseEntityOperationsProps {
  entityNameSingular: string;
  entityDisplayNameSingular: string;
  entityDisplayNamePlural: string;
  apiEndpoint: string;
  onDeleteEntity?: (id: string) => Promise<void>;
  refreshData: () => Promise<void>;
}

export interface UseEntityOperationsReturn {
  handleDelete: (id: string) => Promise<void>;
  handleDeleteSelected: (selectedIds: string[]) => Promise<void>;
  confirmDelete: (id: string, entityName?: string) => Promise<boolean>;
  confirmDeleteMultiple: (count: number) => Promise<boolean>;
}

export const useEntityOperations = ({
  entityNameSingular,
  entityDisplayNameSingular,
  entityDisplayNamePlural,
  apiEndpoint,
  onDeleteEntity,
  refreshData
}: UseEntityOperationsProps): UseEntityOperationsReturn => {
  const { showToast } = useToast();

  // Conferma eliminazione singola
  const confirmDelete = useCallback(async (id: string, entityName?: string): Promise<boolean> => {
    const displayName = entityName || `${entityDisplayNameSingular} con ID ${id}`;
    return window.confirm(`Sei sicuro di voler eliminare ${displayName}?`);
  }, [entityDisplayNameSingular]);

  // Conferma eliminazione multipla
  const confirmDeleteMultiple = useCallback(async (count: number): Promise<boolean> => {
    return window.confirm(`Sei sicuro di voler eliminare ${count} ${entityDisplayNamePlural.toLowerCase()}?`);
  }, [entityDisplayNamePlural]);

  // Eliminazione singola
  const handleDelete = useCallback(async (id: string): Promise<void> => {
    try {
      console.log(` Eliminazione ${entityDisplayNameSingular} con ID:`, id);

      // Conferma eliminazione
      const confirmed = await confirmDelete(id);
      if (!confirmed) return;

      // Usa il callback personalizzato se fornito
      if (onDeleteEntity) {
        await onDeleteEntity(id);
      } else {
        // Altrimenti usa l'API standard
        await apiDelete(`${apiEndpoint}/${id}`);
      }

      showToast({
        message: `${entityDisplayNameSingular} eliminato con successo`,
        type: 'success'
      });

      // Ricarica i dati
      await refreshData();

    } catch (error: any) {
      console.error(` Errore eliminazione ${entityDisplayNameSingular}:`, error);
      showToast({
        message: `Errore durante l'eliminazione: ${error instanceof Error ? error.message : 'Errore sconosciuto'}`,
        type: 'error'
      });
    }
  }, [
    entityDisplayNameSingular, 
    confirmDelete, 
    onDeleteEntity, 
    apiEndpoint, 
    showToast, 
    refreshData
  ]);

  // Eliminazione multipla
  const handleDeleteSelected = useCallback(async (selectedIds: string[]): Promise<void> => {
    if (selectedIds.length === 0) return;

    try {
      console.log(` Eliminazione multipla ${entityDisplayNamePlural}:`, selectedIds);

      // Conferma eliminazione
      const confirmed = await confirmDeleteMultiple(selectedIds.length);
      if (!confirmed) return;

      // Elimina in batch
      const deletePromises = selectedIds.map(async (id) => {
        if (onDeleteEntity) {
          await onDeleteEntity(id);
        } else {
          await apiDelete(`${apiEndpoint}/${id}`);
        }
      });

      await Promise.all(deletePromises);

      showToast({
        message: `${selectedIds.length} ${entityDisplayNamePlural.toLowerCase()} eliminati con successo`,
        type: 'success'
      });

      // Ricarica i dati
      await refreshData();

    } catch (error: any) {
      console.error(` Errore eliminazione multipla ${entityDisplayNamePlural}:`, error);
      showToast({
        message: `Errore durante l'eliminazione multipla: ${error instanceof Error ? error.message : 'Errore sconosciuto'}`,
        type: 'error'
      });
    }
  }, [
    entityDisplayNamePlural, 
    confirmDeleteMultiple, 
    onDeleteEntity, 
    apiEndpoint, 
    showToast, 
    refreshData
  ]);

  return {
    handleDelete,
    handleDeleteSelected,
    confirmDelete,
    confirmDeleteMultiple
  };
};

import { useState, useCallback } from 'react';
import { useAuth } from '../../../context/AuthContext';
import { usePermissions } from '../../../hooks/auth/usePermissions';

/**
 * Hook per la gestione dei permessi delle entit GDPR
 * Centralizza tutta la logica di verifica permessi con supporto per formati multipli
 */
export interface EntityPermissions {
  canCreate: boolean;
  canUpdate: boolean;
  canDelete: boolean;
  canExport: boolean;
  canRead: boolean;
}

export interface UseEntityPermissionsProps {
  entityName: string;
  entityNamePlural: string;
  readPermission: string;
  writePermission: string;
  deletePermission: string;
  exportPermission?: string;
}

export const useEntityPermissions = ({
  entityName,
  entityNamePlural,
  readPermission,
  writePermission,
  deletePermission,
  exportPermission
}: UseEntityPermissionsProps): EntityPermissions => {
  const { hasPermission, user } = useAuth();
  const { canCreate: canCreateHook, canUpdate: canUpdateHook, canDelete: canDeleteHook } = usePermissions();

  // Helper function per dividere i permessi in resource e action
  const checkPermission = useCallback((permission: string): boolean => {
    if (!permission) return false;

    // Se il permesso contiene ":", dividilo in resource e action
    if (permission.includes(':')) {
      const [resource, action] = permission.split(':');
      return hasPermission(resource, action);
    }

    // Altrimenti usa il permesso come resource con action vuota
    return hasPermission(permission, '');
  }, [hasPermission]);

  // Helper per verificare se l'utente  admin
  const isAdmin = useCallback((): boolean => {
    return user?.role === 'Admin' || user?.role === 'Administrator';
  }, [user?.role]);

  // Verifica permessi di lettura
  const canRead = useCallback((): boolean => {
    if (isAdmin()) return true;
    return checkPermission(readPermission) || 
           hasPermission(entityName, 'read') || 
           hasPermission(entityNamePlural, 'read');
  }, [isAdmin, checkPermission, readPermission, hasPermission, entityName, entityNamePlural]);

  // Verifica permessi di creazione
  const canCreate = useCallback((): boolean => {
    if (isAdmin()) return true;

    return canCreateHook(entityName) || 
           canCreateHook(entityNamePlural) ||
           checkPermission(writePermission) ||
           hasPermission(entityName, 'create') ||
           hasPermission(entityNamePlural, 'create');
  }, [isAdmin, canCreateHook, entityName, entityNamePlural, checkPermission, writePermission, hasPermission]);

  // Verifica permessi di modifica
  const canUpdate = useCallback((): boolean => {
    if (isAdmin()) return true;

    return canUpdateHook(entityName) || 
           canUpdateHook(entityNamePlural) ||
           checkPermission(writePermission) ||
           hasPermission(entityName, 'update') ||
           hasPermission(entityNamePlural, 'update') ||
           hasPermission(entityName, 'write') ||
           hasPermission(entityNamePlural, 'write');
  }, [isAdmin, canUpdateHook, entityName, entityNamePlural, checkPermission, writePermission, hasPermission]);

  // Verifica permessi di eliminazione
  const canDelete = useCallback((): boolean => {
    if (isAdmin()) return true;

    return canDeleteHook(entityName) || 
           canDeleteHook(entityNamePlural) ||
           checkPermission(deletePermission) ||
           hasPermission(entityName, 'delete') ||
           hasPermission(entityNamePlural, 'delete');
  }, [isAdmin, canDeleteHook, entityName, entityNamePlural, checkPermission, deletePermission, hasPermission]);

  // Verifica permessi di export
  const canExport = useCallback((): boolean => {
    if (isAdmin()) return true;

    return hasPermission('export', entityName) || 
           hasPermission('export', entityNamePlural) ||
           hasPermission(entityName, 'export') ||
           hasPermission(entityNamePlural, 'export') ||
           (exportPermission ? checkPermission(exportPermission) : true);
  }, [isAdmin, hasPermission, entityName, entityNamePlural, exportPermission, checkPermission]);

  return {
    canRead: canRead(),
    canCreate: canCreate(),
    canUpdate: canUpdate(),
    canDelete: canDelete(),
    canExport: canExport()
  };
};

import { useState, useCallback, useEffect } from 'react';

/**
 * Hook per la gestione della selezione multipla delle entit
 * Centralizza la logica di selezione, modalit batch e operazioni multiple
 */
export interface UseEntitySelectionProps<T> {
  entities: T[];
  entityNamePlural: string;
}

export interface UseEntitySelectionReturn {
  selectedIds: string[];
  selectAll: boolean;
  selectionMode: boolean;
  setSelectionMode: (mode: boolean) => void;
  handleSelect: (id: string) => void;
  handleSelectAll: () => void;
  clearSelection: () => void;
  isSelected: (id: string) => boolean;
  selectedCount: number;
  hasSelection: boolean;
}

export const useEntitySelection = <T extends Record<string, any>>({
  entities,
  entityNamePlural
}: UseEntitySelectionProps<T>): UseEntitySelectionReturn => {
  const [selectedIds, setSelectedIds] = useState<string[]>([]);
  const [selectAll, setSelectAll] = useState(false);
  const [selectionMode, setSelectionMode] = useState(false);

  // Gestione selezione singola
  const handleSelect = useCallback((id: string) => {
    setSelectedIds(prev => 
      prev.includes(id) ? prev.filter(sid => sid !== id) : [...prev, id]
    );
  }, []);

  // Gestione selezione tutti
  const handleSelectAll = useCallback(() => {
    // Validazione di sicurezza per assicurarsi che entities sia un array
    if (!Array.isArray(entities)) {
      console.error('useEntitySelection: entities deve essere un array, ricevuto:', typeof entities, entities);
      return;
    }

    if (selectAll) {
      setSelectedIds([]);
      setSelectAll(false);
    } else {
      setSelectedIds(entities.map(e => e.id));
      setSelectAll(true);
    }
  }, [selectAll, entities]);

  // Pulisce la selezione
  const clearSelection = useCallback(() => {
    setSelectedIds([]);
    setSelectAll(false);
    setSelectionMode(false);
  }, []);

  // Verifica se un elemento  selezionato
  const isSelected = useCallback((id: string): boolean => {
    return selectedIds.includes(id);
  }, [selectedIds]);

  // Aggiorna selectAll quando cambiano le selezioni
  useEffect(() => {
    // Validazione di sicurezza per assicurarsi che entities sia un array
    if (!Array.isArray(entities)) {
      console.error('useEntitySelection: entities deve essere un array nel useEffect, ricevuto:', typeof entities, entities);
      setSelectAll(false);
      return;
    }

    if (entities.length === 0) {
      setSelectAll(false);
      return;
    }

    const allSelected = entities.every(entity => selectedIds.includes(entity.id));
    setSelectAll(allSelected);
  }, [selectedIds, entities]);

  // Pulisce la selezione quando si esce dalla modalit selezione
  useEffect(() => {
    if (!selectionMode) {
      setSelectedIds([]);
      setSelectAll(false);
    }
  }, [selectionMode]);

  // Salva le preferenze di selezione nel localStorage
  useEffect(() => {
    if (selectedIds.length > 0) {
      localStorage.setItem(`${entityNamePlural}-selected-ids`, JSON.stringify(selectedIds));
    } else {
      localStorage.removeItem(`${entityNamePlural}-selected-ids`);
    }
  }, [selectedIds, entityNamePlural]);

  return {
    selectedIds,
    selectAll,
    selectionMode,
    setSelectionMode,
    handleSelect,
    handleSelectAll,
    clearSelection,
    isSelected,
    selectedCount: selectedIds.length,
    hasSelection: selectedIds.length > 0
  };
};

/**
 * useGDPRAudit Hook - Hook per gestione audit GDPR
 * 
 * Hook che gestisce il logging delle azioni e l'audit trail
 * per la conformit GDPR.
 * 
 * @version 1.0
 * @date 30 Dicembre 2024
 */

import { useState, useEffect, useCallback, useRef } from 'react';
import {
  GDPRAuditConfig,
  GDPRAuditAction,
  GDPRAuditLogEntry,
  GDPRAuditLevel,
  GDPRAwareOperation,
  GDPROperationResult
} from '../types/gdpr.types';
import { apiService } from '../../../services/api';

/**
 * Configurazione hook audit
 */
export interface UseGDPRAuditConfig {
  /** Configurazione audit GDPR */
  config?: GDPRAuditConfig;

  /** Tipo di entit */
  entityType: string;

  /** ID della persona (utente corrente) */
  personId?: string;

  /** Livello di audit override */
  auditLevel?: GDPRAuditLevel;

  /** Abilita audit automatico */
  autoAudit?: boolean;

  /** Buffer size per batch logging */
  bufferSize?: number;

  /** Intervallo flush buffer (ms) */
  flushInterval?: number;
}

/**
 * Stato audit
 */
interface AuditState {
  auditLog: GDPRAuditLogEntry[];
  loading: boolean;
  error: string | null;
  lastSync: Date | null;
  pendingEntries: GDPRAuditLogEntry[];
  stats: {
    totalEntries: number;
    successfulOperations: number;
    failedOperations: number;
    lastWeekEntries: number;
  };
}

/**
 * Metadati browser per audit
 */
interface BrowserMetadata {
  userAgent: string;
  ipAddress?: string;
  sessionId?: string;
  timestamp: Date;
  timezone: string;
  language: string;
  screen: {
    width: number;
    height: number;
    colorDepth: number;
  };
}

/**
 * Hook per gestione audit GDPR
 */
export function useGDPRAudit({
  config,
  entityType,
  personId = 'current-user', // Da implementare con context utente reale
  auditLevel,
  autoAudit = true,
  bufferSize = 10,
  flushInterval = 30000 // 30 secondi
}: UseGDPRAuditConfig) {

  const [state, setState] = useState<AuditState>({
    auditLog: [],
    loading: false,
    error: null,
    lastSync: null,
    pendingEntries: [],
    stats: {
      totalEntries: 0,
      successfulOperations: 0,
      failedOperations: 0,
      lastWeekEntries: 0
    }
  });

  const bufferRef = useRef<GDPRAuditLogEntry[]>([]);
  const flushTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const sessionIdRef = useRef<string>(generateSessionId());

  // Genera session ID univoco
  function generateSessionId(): string {
    return `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
  }

  // Ottieni metadati browser
  const getBrowserMetadata = useCallback((): BrowserMetadata => {
    return {
      userAgent: navigator.userAgent,
      sessionId: sessionIdRef.current,
      timestamp: new Date(),
      timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
      language: navigator.language,
      screen: {
        width: screen.width,
        height: screen.height,
        colorDepth: screen.colorDepth
      }
    };
  }, []);

  // Determina se un'azione deve essere loggata
  const shouldLogAction = useCallback((action: GDPRAuditAction): boolean => {
    if (!config || !autoAudit) return false;

    const level = auditLevel || 'standard';

    switch (level) {
      case 'minimal':
        return config.minimalActions.includes(action);
      case 'standard':
        return config.standardActions.includes(action);
      case 'comprehensive':
        return config.comprehensiveActions.includes(action);
      default:
        return false;
    }
  }, [config, auditLevel, autoAudit]);

  // Crea entry di audit
  const createAuditEntry = useCallback((
    action: GDPRAuditAction,
    entityId?: string,
    data?: any,
    oldData?: any,
    success: boolean = true,
    errorMessage?: string,
    reason?: string
  ): GDPRAuditLogEntry => {
    const metadata = getBrowserMetadata();
    const id = `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    // Calcola changes se disponibili oldData e newData
    let changes: Record<string, { from: any; to: any }> | undefined;
    if (oldData && data && typeof data === 'object' && typeof oldData === 'object') {
      changes = {};
      Object.keys({ ...oldData, ...data }).forEach(key => {
        if (oldData[key] !== data[key]) {
          changes![key] = {
            from: oldData[key],
            to: data[key]
          };
        }
      });
    }

    const entry: GDPRAuditLogEntry = {
      id,
      personId,
      action,
      entityType,
      entityId,
      timestamp: metadata.timestamp,
      success,
      oldData,
      newData: data,
      changes,
      reason,
      errorMessage,
      metadata: {
        sessionId: metadata.sessionId,
        timezone: metadata.timezone,
        language: metadata.language,
        screen: metadata.screen
      }
    };

    // Aggiungi metadati opzionali se configurati
    if (config?.includeUserAgent) {
      entry.userAgent = metadata.userAgent;
    }

    if (config?.includeIpAddress) {
      // L'IP address dovrebbe essere aggiunto dal backend
      entry.ipAddress = 'to-be-set-by-backend';
    }

    if (config?.includeSessionId) {
      entry.sessionId = metadata.sessionId;
    }

    return entry;
  }, [config, entityType, personId, getBrowserMetadata]);

  // Flush buffer al server
  const flushBuffer = useCallback(async () => {
    if (bufferRef.current.length === 0) return;

    const entriesToSend = [...bufferRef.current];
    bufferRef.current = [];

    try {
      await apiService.post('/api/gdpr/audit/batch', {
        entries: entriesToSend
      });

      setState(prev => ({
        ...prev,
        lastSync: new Date(),
        stats: {
          ...prev.stats,
          totalEntries: prev.stats.totalEntries + entriesToSend.length,
          successfulOperations: prev.stats.successfulOperations + entriesToSend.filter(e => e.success).length,
          failedOperations: prev.stats.failedOperations + entriesToSend.filter(e => !e.success).length
        }
      }));

    } catch (error) {
      console.error('Errore nel flush audit buffer:', error);

      // Rimetti le entries nel buffer
      bufferRef.current = [...entriesToSend, ...bufferRef.current];

      setState(prev => ({
        ...prev,
        error: 'Errore nella sincronizzazione audit log'
      }));
    }
  }, []);

  // Aggiungi entry al buffer
  const addToBuffer = useCallback((entry: GDPRAuditLogEntry) => {
    bufferRef.current.push(entry);

    // Aggiungi anche al log locale
    setState(prev => ({
      ...prev,
      auditLog: [entry, ...prev.auditLog].slice(0, 100), // Mantieni solo le ultime 100 entries localmente
      pendingEntries: [...prev.pendingEntries, entry]
    }));

    // Flush se buffer  pieno
    if (bufferRef.current.length >= bufferSize) {
      flushBuffer();
    }

    // Reset timeout flush
    if (flushTimeoutRef.current) {
      clearTimeout(flushTimeoutRef.current);
    }

    flushTimeoutRef.current = setTimeout(flushBuffer, flushInterval);
  }, [bufferSize, flushInterval, flushBuffer]);

  // Log azione principale
  const logAction = useCallback(async (
    action: GDPRAuditAction,
    entityId?: string,
    data?: any,
    oldData?: any,
    options?: {
      reason?: string;
      success?: boolean;
      errorMessage?: string;
      metadata?: Record<string, any>;
      immediate?: boolean;
    }
  ) => {
    if (!shouldLogAction(action)) return;

    const entry = createAuditEntry(
      action,
      entityId,
      data,
      oldData,
      options?.success ?? true,
      options?.errorMessage,
      options?.reason
    );

    // Aggiungi metadati personalizzati
    if (options?.metadata) {
      entry.metadata = { ...entry.metadata, ...options.metadata };
    }

    if (options?.immediate) {
      // Invio immediato
      try {
        await apiService.post('/api/gdpr/audit', entry);

        setState(prev => ({
          ...prev,
          auditLog: [entry, ...prev.auditLog].slice(0, 100),
          lastSync: new Date(),
          stats: {
            ...prev.stats,
            totalEntries: prev.stats.totalEntries + 1,
            successfulOperations: entry.success ? prev.stats.successfulOperations + 1 : prev.stats.successfulOperations,
            failedOperations: !entry.success ? prev.stats.failedOperations + 1 : prev.stats.failedOperations
          }
        }));

      } catch (error) {
        console.error('Errore nel logging immediato:', error);
        // Fallback al buffer
        addToBuffer(entry);
      }
    } else {
      // Aggiungi al buffer
      addToBuffer(entry);
    }
  }, [shouldLogAction, createAuditEntry, addToBuffer]);

  // Log operazione GDPR-aware
  const logGDPROperation = useCallback(async <T = any>(
    operation: GDPRAwareOperation<T>
  ): Promise<GDPROperationResult<T>> => {
    const startTime = Date.now();

    try {
      // Log inizio operazione
      await logAction(
        operation.operation,
        operation.entityId,
        operation.data,
        undefined,
        {
          reason: operation.reason,
          metadata: {
            ...operation.metadata,
            riskLevel: operation.riskLevel,
            startTime
          }
        }
      );

      // Qui andrebbe eseguita l'operazione reale
      // Per ora simuliamo il successo
      const result: GDPROperationResult<T> = {
        success: true,
        data: operation.data,
        auditLogId: `audit-${Date.now()}`,
        metadata: {
          duration: Date.now() - startTime,
          timestamp: new Date().toISOString()
        }
      };

      // Log completamento operazione
      await logAction(
        operation.operation,
        operation.entityId,
        result.data,
        operation.data,
        {
          success: true,
          metadata: {
            ...operation.metadata,
            duration: result.metadata?.duration,
            auditLogId: result.auditLogId
          }
        }
      );

      return result;

    } catch (error: any) {
      // Log errore operazione
      await logAction(
        operation.operation,
        operation.entityId,
        operation.data,
        undefined,
        {
          success: false,
          errorMessage: error.message,
          metadata: {
            ...operation.metadata,
            duration: Date.now() - startTime,
            stackTrace: config?.includeStackTrace ? error.stack : undefined
          }
        }
      );

      return {
        success: false,
        errors: [error.message],
        metadata: {
          duration: Date.now() - startTime,
          timestamp: new Date().toISOString()
        }
      };
    }
  }, [logAction, config]);

  // Carica storico audit
  const loadAuditHistory = useCallback(async (
    filters?: {
      startDate?: Date;
      endDate?: Date;
      actions?: GDPRAuditAction[];
      entityId?: string;
      success?: boolean;
      limit?: number;
      offset?: number;
    }
  ) => {
    setState(prev => ({ ...prev, loading: true, error: null }));

    try {
      const params = new URLSearchParams();

      if (filters?.startDate) {
        params.append('startDate', filters.startDate.toISOString());
      }

      if (filters?.endDate) {
        params.append('endDate', filters.endDate.toISOString());
      }

      if (filters?.actions) {
        filters.actions.forEach(action => params.append('actions', action));
      }

      if (filters?.entityId) {
        params.append('entityId', filters.entityId);
      }

      if (filters?.success !== undefined) {
        params.append('success', filters.success.toString());
      }

      params.append('entityType', entityType);
      params.append('personId', personId);
      params.append('limit', (filters?.limit || 50).toString());
      params.append('offset', (filters?.offset || 0).toString());

      const response = await apiService.get(`/api/gdpr/audit?${params.toString()}`);

      setState(prev => ({
        ...prev,
        auditLog: (response as any).data || [],
        loading: false,
        stats: {
          ...prev.stats,
          totalEntries: (response as any).total || 0,
          lastWeekEntries: (response as any).lastWeekCount || 0
        }
      }));

    } catch (error) {
      console.error('Errore nel caricamento audit history:', error);
      setState(prev => ({
        ...prev,
        loading: false,
        error: 'Errore nel caricamento dello storico audit'
      }));
    }
  }, [entityType, personId]);

  // Export audit log
  const exportAuditLog = useCallback(async (
    format: 'json' | 'csv' | 'xlsx' = 'json',
    filters?: Parameters<typeof loadAuditHistory>[0]
  ): Promise<Blob> => {
    const params = new URLSearchParams();
    params.append('format', format);
    params.append('entityType', entityType);
    params.append('personId', personId);

    if (filters) {
      Object.entries(filters).forEach(([key, value]) => {
        if (value !== undefined) {
          if (Array.isArray(value)) {
            value.forEach(v => params.append(key, v.toString()));
          } else {
            params.append(key, value.toString());
          }
        }
      });
    }

    const response = await apiService.get(
      `/api/gdpr/audit/export?${params.toString()}`,
      { responseType: 'blob' }
    );

    return (response as any).data;
  }, [entityType, personId]);

  // Cleanup e flush finale
  useEffect(() => {
    return () => {
      if (flushTimeoutRef.current) {
        clearTimeout(flushTimeoutRef.current);
      }

      // Flush finale
      if (bufferRef.current.length > 0) {
        flushBuffer();
      }
    };
  }, [flushBuffer]);

  // Carica statistiche iniziali
  useEffect(() => {
    if (autoAudit) {
      loadAuditHistory({ limit: 10 });
    }
  }, [autoAudit, loadAuditHistory]);

  return {
    // Stato
    auditLog: state.auditLog,
    loading: state.loading,
    error: state.error,
    lastSync: state.lastSync,
    pendingEntries: state.pendingEntries,
    stats: state.stats,

    // Azioni
    logAction,
    logGDPROperation,
    loadAuditHistory,
    exportAuditLog,
    flushBuffer,

    // Utility
    getAuditHistory: () => state.auditLog,
    getPendingCount: () => bufferRef.current.length,
    clearError: () => setState(prev => ({ ...prev, error: null })),

    // Configurazione
    isAuditEnabled: autoAudit && !!config,
    currentAuditLevel: auditLevel || 'standard',
    sessionId: sessionIdRef.current
  };
}

export default useGDPRAudit;

import { useState, useCallback, useEffect } from 'react';

export interface ColumnConfig {
  key: string;
  label: string;
  sortable?: boolean;
  filterable?: boolean;
  width?: number;
  minWidth?: number;
  maxWidth?: number;
  resizable?: boolean;
  visible?: boolean;
  order?: number;
  formatter?: (value: any, row: any) => React.ReactNode;
  className?: string;
}

export interface ColumnState {
  visible: boolean;
  width?: number;
  order: number;
}

export interface UseGDPRColumnsConfig {
  columns: ColumnConfig[];
  storageKey?: string;
  defaultVisibleColumns?: string[];
  enableReordering?: boolean;
  enableResizing?: boolean;
}

/**
 * Hook per gestire la configurazione, visibilit e ordinamento delle colonne
 * Salva le preferenze utente nel localStorage
 */
export function useGDPRColumns({
  columns,
  storageKey = 'gdpr-columns-config',
  defaultVisibleColumns,
  enableReordering = true,
  enableResizing = true
}: UseGDPRColumnsConfig) {

  // Stato delle colonne con configurazione salvata
  const [columnStates, setColumnStates] = useState<Record<string, ColumnState>>(() => {
    try {
      const saved = localStorage.getItem(storageKey);
      if (saved) {
        const parsed = JSON.parse(saved);
        // Verifica che le colonne salvate siano ancora valide
        const validColumns = columns.reduce((acc, col) => {
          acc[col.key] = {
            visible: parsed[col.key]?.visible ?? col.visible ?? true,
            width: parsed[col.key]?.width ?? col.width,
            order: parsed[col.key]?.order ?? col.order ?? 0
          };
          return acc;
        }, {} as Record<string, ColumnState>);
        return validColumns;
      }
    } catch (error) {
      console.warn('Errore nel caricamento configurazione colonne:', error);
    }

    // Configurazione di default
    return columns.reduce((acc, col, index) => {
      const isVisible = defaultVisibleColumns 
        ? defaultVisibleColumns.includes(col.key)
        : col.visible ?? true;

      acc[col.key] = {
        visible: isVisible,
        width: col.width,
        order: col.order ?? index
      };
      return acc;
    }, {} as Record<string, ColumnState>);
  });

  // Salva la configurazione nel localStorage
  const saveConfiguration = useCallback(() => {
    try {
      localStorage.setItem(storageKey, JSON.stringify(columnStates));
    } catch (error) {
      console.warn('Errore nel salvataggio configurazione colonne:', error);
    }
  }, [storageKey, columnStates]);

  // Effetto per salvare automaticamente le modifiche
  useEffect(() => {
    saveConfiguration();
  }, [saveConfiguration]);

  // Mostra/nasconde una colonna
  const toggleColumnVisibility = useCallback((columnKey: string) => {
    setColumnStates(prev => ({
      ...prev,
      [columnKey]: {
        ...prev[columnKey],
        visible: !prev[columnKey]?.visible
      }
    }));
  }, []);

  // Imposta la visibilit di una colonna
  const setColumnVisibility = useCallback((columnKey: string, visible: boolean) => {
    setColumnStates(prev => ({
      ...prev,
      [columnKey]: {
        ...prev[columnKey],
        visible
      }
    }));
  }, []);

  // Ridimensiona una colonna
  const resizeColumn = useCallback((columnKey: string, width: number) => {
    if (!enableResizing) return;

    setColumnStates(prev => ({
      ...prev,
      [columnKey]: {
        ...prev[columnKey],
        width: Math.max(50, width) // Larghezza minima 50px
      }
    }));
  }, [enableResizing]);

  // Riordina le colonne
  const reorderColumns = useCallback((newOrder: string[]) => {
    if (!enableReordering) return;

    setColumnStates(prev => {
      const updated = { ...prev };
      newOrder.forEach((columnKey, index) => {
        if (updated[columnKey]) {
          updated[columnKey] = {
            ...updated[columnKey],
            order: index
          };
        }
      });
      return updated;
    });
  }, [enableReordering]);

  // Resetta la configurazione ai valori di default
  const resetConfiguration = useCallback(() => {
    const defaultStates = columns.reduce((acc, col, index) => {
      const isVisible = defaultVisibleColumns 
        ? defaultVisibleColumns.includes(col.key)
        : col.visible ?? true;

      acc[col.key] = {
        visible: isVisible,
        width: col.width,
        order: col.order ?? index
      };
      return acc;
    }, {} as Record<string, ColumnState>);

    setColumnStates(defaultStates);
  }, [columns, defaultVisibleColumns]);

  // Mostra tutte le colonne
  const showAllColumns = useCallback(() => {
    setColumnStates(prev => {
      const updated = { ...prev };
      Object.keys(updated).forEach(key => {
        updated[key] = { ...updated[key], visible: true };
      });
      return updated;
    });
  }, []);

  // Nasconde tutte le colonne (tranne quelle obbligatorie)
  const hideAllColumns = useCallback(() => {
    setColumnStates(prev => {
      const updated = { ...prev };
      columns.forEach(col => {
        // Non nascondere colonne obbligatorie (es. azioni, selezione)
        if (!col.key.includes('actions') && !col.key.includes('select')) {
          updated[col.key] = { ...updated[col.key], visible: false };
        }
      });
      return updated;
    });
  }, [columns]);

  // Calcola le colonne visibili ordinate
  const visibleColumns = columns
    .filter(col => columnStates[col.key]?.visible !== false)
    .map(col => ({
      ...col,
      width: columnStates[col.key]?.width ?? col.width,
      order: columnStates[col.key]?.order ?? col.order ?? 0
    }))
    .sort((a, b) => (a.order || 0) - (b.order || 0));

  // Calcola le colonne nascoste
  const hiddenColumns = columns.filter(col => columnStates[col.key]?.visible === false);

  // Statistiche sulle colonne
  const columnStats = {
    total: columns.length,
    visible: visibleColumns.length,
    hidden: hiddenColumns.length,
    hasCustomOrder: visibleColumns.some((col, index) => col.order !== index),
    hasCustomWidths: visibleColumns.some(col => 
      col.width !== undefined && col.width !== columns.find(c => c.key === col.key)?.width
    )
  };

  return {
    // Configurazione colonne
    columns,
    visibleColumns,
    hiddenColumns,
    columnStates,
    columnStats,

    // Azioni
    toggleColumnVisibility,
    setColumnVisibility,
    resizeColumn,
    reorderColumns,
    resetConfiguration,
    showAllColumns,
    hideAllColumns,
    saveConfiguration,

    // Configurazione
    enableReordering,
    enableResizing,
    storageKey
  };
}

/**
 * useGDPRConsent Hook - Hook per gestione consensi GDPR
 * 
 * Hook che gestisce i consensi GDPR dell'utente con verifica
 * automatica e gestione della scadenza.
 * 
 * @version 1.0
 * @date 30 Dicembre 2024
 */

import { useState, useEffect, useCallback, useRef } from 'react';
import {
  GDPRConsentConfig,
  GDPRConsentType,
  GDPRConsent,
  GDPRConsentRequest,
  GDPRConsentVerification
} from '../types/gdpr.types';
import { apiService } from '../../../services/api';

/**
 * Configurazione hook consensi
 */
export interface UseGDPRConsentConfig {
  /** Configurazione consensi GDPR */
  config?: GDPRConsentConfig;

  /** ID della persona */
  personId: string;

  /** Abilita verifica automatica */
  autoCheck?: boolean;

  /** Intervallo verifica automatica (ms) */
  checkInterval?: number;

  /** Callback per consensi scaduti */
  onConsentsExpired?: (expiredConsents: GDPRConsentType[]) => void;

  /** Callback per consensi mancanti */
  onConsentsMissing?: (missingConsents: GDPRConsentType[]) => void;
}

/**
 * Stato consensi
 */
interface ConsentState {
  consents: Record<string, boolean>;
  consentDetails: Record<string, GDPRConsent>;
  loading: boolean;
  error: string | null;
  lastCheck: Date | null;
  verification: GDPRConsentVerification | null;
}

/**
 * Hook per gestione consensi GDPR
 */
export function useGDPRConsent({
  config,
  personId,
  autoCheck = true,
  checkInterval = 60000, // 1 minuto
  onConsentsExpired,
  onConsentsMissing
}: UseGDPRConsentConfig) {

  const [state, setState] = useState<ConsentState>({
    consents: {},
    consentDetails: {},
    loading: false,
    error: null,
    lastCheck: null,
    verification: null
  });

  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  const abortControllerRef = useRef<AbortController | null>(null);

  // Carica consensi dal server
  const loadConsents = useCallback(async () => {
    if (!personId) return;

    // Cancella richiesta precedente se in corso
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }

    abortControllerRef.current = new AbortController();

    setState(prev => ({ ...prev, loading: true, error: null }));

    try {
      const response = await apiService.get(
        `/api/gdpr/consents/${personId}`,
        { signal: abortControllerRef.current.signal }
      );

      const consentList: GDPRConsent[] = (response as any).data || [];

      // Converte in formato pi utilizzabile
      const consents: Record<string, boolean> = {};
      const consentDetails: Record<string, GDPRConsent> = {};

      consentList.forEach(consent => {
        consents[consent.consentType] = consent.isActive && consent.granted;
        consentDetails[consent.consentType] = consent;
      });

      setState(prev => ({
        ...prev,
        consents,
        consentDetails,
        loading: false,
        lastCheck: new Date()
      }));

    } catch (error: any) {
      if (error.name !== 'AbortError') {
        console.error('Errore nel caricamento consensi:', error);
        setState(prev => ({
          ...prev,
          loading: false,
          error: 'Errore nel caricamento dei consensi'
        }));
      }
    }
  }, [personId]);

  // Verifica consensi
  const checkConsent = useCallback(async (
    requiredConsents: GDPRConsentType[]
  ): Promise<GDPRConsentVerification> => {
    const now = new Date();

    const hasConsent = requiredConsents.every(type => state.consents[type]);
    const missingConsents = requiredConsents.filter(type => !state.consents[type]);

    const expiredConsents: GDPRConsentType[] = [];
    const validConsents: GDPRConsentType[] = [];
    const requiresReconfirmation: GDPRConsentType[] = [];

    requiredConsents.forEach(type => {
      const consentDetail = state.consentDetails[type];

      if (consentDetail && state.consents[type]) {
        // Verifica scadenza
        if (consentDetail.expiresAt && new Date(consentDetail.expiresAt) < now) {
          expiredConsents.push(type);
        } else {
          validConsents.push(type);

          // Verifica se richiede riconferma
          if (config?.requiresReconfirmation && config.reconfirmationIntervalDays) {
            const daysSinceGrant = Math.floor(
              (now.getTime() - new Date(consentDetail.grantedAt || 0).getTime()) / (1000 * 60 * 60 * 24)
            );

            if (daysSinceGrant >= config.reconfirmationIntervalDays) {
              requiresReconfirmation.push(type);
            }
          }
        }
      }
    });

    const verification: GDPRConsentVerification = {
      hasConsent: hasConsent && expiredConsents.length === 0,
      missingConsents,
      expiredConsents,
      validConsents,
      requiresReconfirmation
    };

    setState(prev => ({ ...prev, verification }));

    // Trigger callbacks
    if (expiredConsents.length > 0 && onConsentsExpired) {
      onConsentsExpired(expiredConsents);
    }

    if (missingConsents.length > 0 && onConsentsMissing) {
      onConsentsMissing(missingConsents);
    }

    return verification;
  }, [state.consents, state.consentDetails, config, onConsentsExpired, onConsentsMissing]);

  // Richiedi consenso
  const requestConsent = useCallback(async (
    consentTypes: GDPRConsentType[],
    options?: {
      purpose?: string;
      legalBasis?: string;
      expirationDays?: number;
      metadata?: Record<string, any>;
    }
  ) => {
    if (!personId) throw new Error('Person ID richiesto');

    setState(prev => ({ ...prev, loading: true, error: null }));

    try {
      const request: GDPRConsentRequest = {
        personId,
        consentTypes,
        purpose: options?.purpose || 'Utilizzo del servizio',
        legalBasis: options?.legalBasis || 'Art. 6(1)(a) GDPR - Consenso dell\'interessato',
        expirationDays: options?.expirationDays || config?.consentValidityDays,
        metadata: options?.metadata
      };

      await apiService.post('/api/gdpr/consents/grant', request);

      // Ricarica consensi
      await loadConsents();

    } catch (error) {
      console.error('Errore nella richiesta consenso:', error);
      setState(prev => ({
        ...prev,
        loading: false,
        error: 'Errore nella richiesta del consenso'
      }));
      throw error;
    }
  }, [personId, config, loadConsents]);

  // Revoca consenso
  const revokeConsent = useCallback(async (
    consentTypes: GDPRConsentType[],
    reason?: string
  ) => {
    if (!personId) throw new Error('Person ID richiesto');

    setState(prev => ({ ...prev, loading: true, error: null }));

    try {
      await apiService.post('/api/gdpr/consents/revoke', {
        personId,
        consentTypes,
        reason
      });

      // Ricarica consensi
      await loadConsents();

    } catch (error) {
      console.error('Errore nella revoca consenso:', error);
      setState(prev => ({
        ...prev,
        loading: false,
        error: 'Errore nella revoca del consenso'
      }));
      throw error;
    }
  }, [personId, loadConsents]);

  // Aggiorna consenso esistente
  const updateConsent = useCallback(async (
    consentType: GDPRConsentType,
    granted: boolean,
    options?: {
      reason?: string;
      metadata?: Record<string, any>;
    }
  ) => {
    if (granted) {
      await requestConsent([consentType], {
        metadata: options?.metadata
      });
    } else {
      await revokeConsent([consentType], options?.reason);
    }
  }, [requestConsent, revokeConsent]);

  // Verifica se un consenso specifico  valido
  const isConsentValid = useCallback((consentType: GDPRConsentType): boolean => {
    const consent = state.consentDetails[consentType];

    if (!consent || !consent.isActive || !consent.granted) {
      return false;
    }

    // Verifica scadenza
    if (consent.expiresAt && new Date(consent.expiresAt) < new Date()) {
      return false;
    }

    return true;
  }, [state.consentDetails]);

  // Ottieni dettagli consenso
  const getConsentDetails = useCallback((consentType: GDPRConsentType): GDPRConsent | null => {
    return state.consentDetails[consentType] || null;
  }, [state.consentDetails]);

  // Ottieni consensi in scadenza
  const getExpiringConsents = useCallback((daysThreshold = 7): GDPRConsentType[] => {
    const threshold = new Date();
    threshold.setDate(threshold.getDate() + daysThreshold);

    return Object.entries(state.consentDetails)
      .filter(([_, consent]) => {
        return consent.isActive &&
               consent.granted &&
               consent.expiresAt &&
               new Date(consent.expiresAt) <= threshold &&
               new Date(consent.expiresAt) > new Date();
      })
      .map(([type, _]) => type as GDPRConsentType);
  }, [state.consentDetails]);

  // Verifica automatica periodica
  useEffect(() => {
    if (autoCheck && config) {
      const performCheck = async () => {
        const allRequiredConsents = [
          ...config.requiredConsents,
          ...(config.optionalConsents || [])
        ];

        if (allRequiredConsents.length > 0) {
          await checkConsent(config.requiredConsents);
        }
      };

      // Verifica immediata
      performCheck();

      // Imposta intervallo
      intervalRef.current = setInterval(performCheck, checkInterval);

      return () => {
        if (intervalRef.current) {
          clearInterval(intervalRef.current);
        }
      };
    }
  }, [autoCheck, config, checkInterval, checkConsent]);

  // Carica consensi all'avvio
  useEffect(() => {
    loadConsents();

    return () => {
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, [loadConsents]);

  // Cleanup
  useEffect(() => {
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
      if (abortControllerRef.current) {
        abortControllerRef.current.abort();
      }
    };
  }, []);

  return {
    // Stato
    consents: state.consents,
    consentDetails: state.consentDetails,
    loading: state.loading,
    error: state.error,
    lastCheck: state.lastCheck,
    verification: state.verification,

    // Azioni
    loadConsents,
    requestConsent,
    revokeConsent,
    updateConsent,
    checkConsent,

    // Utility
    isConsentValid,
    getConsentDetails,
    getExpiringConsents,

    // Stato derivato
    hasRequiredConsents: config ? config.requiredConsents.every(type => state.consents[type]) : true,
    missingRequiredConsents: config ? config.requiredConsents.filter(type => !state.consents[type]) : [],
    expiringConsents: getExpiringConsents(),

    // Controllo errori
    clearError: () => setState(prev => ({ ...prev, error: null }))
  };
}

export default useGDPRConsent;

import { useState, useEffect } from 'react';
import { apiGet } from '../../../services/api';
import { getLoadingErrorMessage } from '../../../utils/errorUtils';
import { useToast } from '../../../hooks/useToast';

export interface GDPREntityDataConfig {
  apiEndpoint: string;
  entityNamePlural: string;
  entityDisplayNamePlural: string;
}

export interface GDPREntityDataState<T> {
  entities: T[];
  loading: boolean;
  error: string | null;
  refetch: () => Promise<void>;
}

/**
 * Hook per gestire il caricamento e la gestione dei dati delle entit
 * Centralizza la logica di fetch, loading e error handling
 */
export function useGDPREntityData<T extends Record<string, any>>({
  apiEndpoint,
  entityNamePlural,
  entityDisplayNamePlural
}: GDPREntityDataConfig): GDPREntityDataState<T> {
  const [entities, setEntities] = useState<T[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { showToast } = useToast();

  const loadEntities = async () => {
    try {
      setLoading(true);
      setError(null);

      console.log(` Caricamento ${entityDisplayNamePlural}...`);

      // Costruisci i parametri di query per l'endpoint delle persone
      let apiUrl = apiEndpoint;
      if (apiEndpoint === '/api/persons' || apiEndpoint === '/api/v1/persons') {
        // Per l'endpoint delle persone, aggiungi i parametri necessari
        const params = new URLSearchParams();
        // Non forzare roleType per mostrare tutti gli utenti
        params.append('page', '1');
        params.append('limit', '50');
        params.append('sortBy', 'lastLogin');
        params.append('sortOrder', 'desc');

        apiUrl = `${apiEndpoint}?${params.toString()}`;
      }

      console.log(` Chiamata API: ${apiUrl}`);
      const response = await apiGet<any>(apiUrl);
      console.log(` Risposta API ${entityNamePlural}:`, response);

      // Gestisci la risposta paginata per l'endpoint delle persone
      if ((apiEndpoint === '/api/persons' || apiEndpoint === '/api/v1/persons') && response && typeof response === 'object' && response.persons) {
        setEntities(response.persons);
        console.log(` ${entityDisplayNamePlural} caricate:`, response.persons.length);
      } else if (Array.isArray(response)) {
        setEntities(response);
        console.log(` ${entityDisplayNamePlural} caricate:`, response.length);
      } else {
        console.warn(` Risposta API non  un array:`, response);
        setEntities([]);
      }
    } catch (err: any) {
      console.error(` Errore caricamento ${entityDisplayNamePlural}:`, err);
      setError(getLoadingErrorMessage(
        (entityNamePlural as keyof typeof import('../../../utils/errorUtils').errorMessages.loading) || 'generic', 
        err
      ));
      setEntities([]);
      showToast({
        message: `Errore durante il caricamento dei ${entityDisplayNamePlural.toLowerCase()}: ${err instanceof Error ? err.message : 'Errore sconosciuto'}`,
        type: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  // Caricamento iniziale
  useEffect(() => {
    loadEntities();
  }, [apiEndpoint, entityNamePlural, entityDisplayNamePlural]);

  return {
    entities,
    loading,
    error,
    refetch: loadEntities
  };
}

/**
 * useGDPREntityOperations Hook - Hook per operazioni CRUD con supporto GDPR
 * 
 * Hook che gestisce le operazioni CRUD (Create, Read, Update, Delete)
 * con controlli di consenso GDPR e audit logging.
 * 
 * @version 1.0
 * @date 30 Dicembre 2024
 */

import { useState, useCallback, useRef } from 'react';
import {
  BaseEntity,
  EntityOperationResult,
  EntityAPIConfig,
  EntityPermissions
} from '../types/entity.types';
import {
  GDPRConfig,
  GDPRConsentType
} from '../types/gdpr.types';
import { useGDPRConsent } from './useGDPRConsent';
import { useGDPRAudit } from './useGDPRAudit';
import { apiService } from '../../../services/api';

/**
 * Configurazione hook operazioni
 */
export interface UseGDPREntityOperationsConfig<T extends BaseEntity> {
  /** Configurazione API */
  apiConfig: EntityAPIConfig;

  /** Configurazione GDPR */
  gdprConfig?: GDPRConfig;

  /** Permessi richiesti */
  permissions: EntityPermissions;

  /** Tipo di entit */
  entityType: string;

  /** Validatore personalizzato */
  validator?: (entity: Partial<T>) => Promise<string[]>;

  /** Trasformatore dati pre-salvataggio */
  dataTransformer?: (entity: Partial<T>) => Partial<T>;

  /** Callback post-operazione */
  onOperationComplete?: (operation: string, result: EntityOperationResult<T>) => void;

  /** Abilita cache locale */
  enableCache?: boolean;

  /** TTL cache (ms) */
  cacheTTL?: number;
}

/**
 * Stato operazioni
 */
interface OperationsState<T extends BaseEntity> {
  loading: boolean;
  saving: boolean;
  deleting: boolean;
  error: string | null;
  lastOperation: string | null;
  cache: Map<string, { data: T; timestamp: number }>;
  optimisticUpdates: Map<string, Partial<T>>;
}

/**
 * Opzioni operazione
 */
interface OperationOptions {
  /** Richiedi consenso esplicito */
  requireConsent?: boolean;

  /** Tipi di consenso richiesti */
  consentTypes?: GDPRConsentType[];

  /** Motivo dell'operazione */
  reason?: string;

  /** Metadati aggiuntivi */
  metadata?: Record<string, any>;

  /** Bypass cache */
  bypassCache?: boolean;

  /** Update ottimistico */
  optimistic?: boolean;

  /** Validazione personalizzata */
  skipValidation?: boolean;
}

/**
 * Hook per operazioni CRUD con supporto GDPR
 */
export function useGDPREntityOperations<T extends BaseEntity>({
  apiConfig,
  gdprConfig,
  permissions,
  entityType,
  validator,
  dataTransformer,
  onOperationComplete,
  enableCache = true,
  cacheTTL = 300000 // 5 minuti
}: UseGDPREntityOperationsConfig<T>) {

  const [state, setState] = useState<OperationsState<T>>({
    loading: false,
    saving: false,
    deleting: false,
    error: null,
    lastOperation: null,
    cache: new Map(),
    optimisticUpdates: new Map()
  });

  const abortControllerRef = useRef<AbortController | null>(null);

  // Hook GDPR
  const { checkConsent, requestConsent } = useGDPRConsent({
    config: gdprConfig?.consentConfig,
    personId: 'current-user' // TODO: Ottenere dall'auth context
  });

  const { logGDPROperation } = useGDPRAudit({
    config: gdprConfig?.auditConfig,
    entityType
  });

  // Verifica permessi
  const checkPermissions = useCallback((operation: string): boolean => {
    switch (operation) {
      case 'create':
        return permissions.create.length > 0;
      case 'read':
        return permissions.read.length > 0;
      case 'update':
        return permissions.update.length > 0;
      case 'delete':
        return permissions.delete.length > 0;
      case 'export':
        return permissions.export?.length > 0 || false;
      case 'import':
        return permissions.import?.length > 0 || false;
      default:
        return false;
    }
  }, [permissions]);

  // Verifica consensi GDPR
  const checkGDPRConsents = useCallback(async (
    operation: string,
    consentTypes?: GDPRConsentType[]
  ): Promise<boolean> => {
    if (!gdprConfig?.consentConfig || !consentTypes?.length) return true;

    // Verifica tutti i consensi richiesti
    const verification = await checkConsent(consentTypes);

    // Se ci sono consensi mancanti o scaduti, richiedili
    const missingOrExpired = [...verification.missingConsents, ...verification.expiredConsents];

    if (missingOrExpired.length > 0) {
      // Richiedi consensi mancanti/scaduti
      await requestConsent(missingOrExpired, {
        purpose: `Operazione ${operation} su ${entityType}`,
        legalBasis: 'legitimate_interest'
      });
    }

    return verification.hasConsent;
  }, [gdprConfig, checkConsent, requestConsent, entityType]);

  // Valida entit
  const validateEntity = useCallback(async (entity: Partial<T>): Promise<string[]> => {
    const errors: string[] = [];

    // Validazione base
    if (!entity) {
      errors.push('Entit non valida');
      return errors;
    }

    // Validazione personalizzata
    if (validator) {
      const customErrors = await validator(entity);
      errors.push(...customErrors);
    }

    return errors;
  }, [validator]);

  // Gestione cache
  const getCachedEntity = useCallback((id: string): T | null => {
    if (!enableCache) return null;

    const cached = state.cache.get(id);
    if (!cached) return null;

    // Verifica TTL
    if (Date.now() - cached.timestamp > cacheTTL) {
      state.cache.delete(id);
      return null;
    }

    return cached.data;
  }, [enableCache, cacheTTL, state.cache]);

  const setCachedEntity = useCallback((entity: T) => {
    if (!enableCache || !entity.id) return;

    setState(prev => {
      const newCache = new Map(prev.cache);
      newCache.set(entity.id!, {
        data: entity,
        timestamp: Date.now()
      });

      return {
        ...prev,
        cache: newCache
      };
    });
  }, [enableCache]);

  // Applica update ottimistico
  const applyOptimisticUpdate = useCallback((id: string, updates: Partial<T>) => {
    setState(prev => {
      const newOptimistic = new Map(prev.optimisticUpdates);
      newOptimistic.set(id, { ...newOptimistic.get(id), ...updates });

      return {
        ...prev,
        optimisticUpdates: newOptimistic
      };
    });
  }, []);

  // Rimuovi update ottimistico
  const removeOptimisticUpdate = useCallback((id: string) => {
    setState(prev => {
      const newOptimistic = new Map(prev.optimisticUpdates);
      newOptimistic.delete(id);

      return {
        ...prev,
        optimisticUpdates: newOptimistic
      };
    });
  }, []);

  // Cancella operazione in corso
  const cancelOperation = useCallback(() => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
      abortControllerRef.current = null;
    }

    setState(prev => ({
      ...prev,
      loading: false,
      saving: false,
      deleting: false
    }));
  }, []);

  // CREATE - Crea nuova entit
  const createEntity = useCallback(async (
    entityData: Omit<T, 'id' | 'createdAt' | 'updatedAt'>,
    options: OperationOptions = {}
  ): Promise<EntityOperationResult<T>> => {
    try {
      // Verifica permessi
      if (!checkPermissions('create')) {
        throw new Error('Permessi insufficienti per creare l\'entit');
      }

      // Verifica consensi GDPR
      await checkGDPRConsents('create', options.consentTypes);

      setState(prev => ({ ...prev, saving: true, error: null, lastOperation: 'create' }));

      // Validazione
      if (!options.skipValidation) {
        const errors = await validateEntity(entityData as Partial<T>);
        if (errors.length > 0) {
          throw new Error(`Errori di validazione: ${errors.join(', ')}`);
        }
      }

      // Trasformazione dati
      let processedData = entityData;
      if (dataTransformer) {
        processedData = dataTransformer(entityData as Partial<T>) as Omit<T, 'id' | 'createdAt' | 'updatedAt'>;
      }

      // Abort controller
      abortControllerRef.current = new AbortController();

      // Log operazione GDPR
      const gdprResult = await logGDPROperation({
        operation: 'CREATE',
        entityType,
        data: processedData,
        reason: options.reason || 'Creazione nuova entit',
        riskLevel: 'MEDIUM',
        metadata: options.metadata
      });

      if (!gdprResult.success) {
        throw new Error('Errore nel logging GDPR');
      }

      // Chiamata API
      const response = await apiService.post(
        apiConfig.endpoints.create,
        processedData,
        {
          signal: abortControllerRef.current.signal,
          ...apiConfig.defaultOptions
        }
      );

      const createdEntity = (response as any).data as T;

      // Aggiorna cache
      setCachedEntity(createdEntity);

      const result: EntityOperationResult<T> = {
        success: true,
        data: createdEntity,
        operation: 'create'
      };

      setState(prev => ({ ...prev, saving: false }));

      // Callback
      onOperationComplete?.('create', result);

      return result;

    } catch (error: any) {
      setState(prev => ({
        ...prev,
        saving: false,
        error: error.message
      }));

      const result: EntityOperationResult<T> = {
        success: false,
        errors: [error.message],
        operation: 'create'
      };

      onOperationComplete?.('create', result);

      return result;
    } finally {
      abortControllerRef.current = null;
    }
  }, [
    checkPermissions,
    checkGDPRConsents,
    validateEntity,
    dataTransformer,
    logGDPROperation,
    apiConfig,
    entityType,
    setCachedEntity,
    onOperationComplete
  ]);

  // READ - Leggi entit
  const readEntity = useCallback(async (
    id: string,
    options: OperationOptions = {}
  ): Promise<EntityOperationResult<T>> => {
    try {
      // Verifica permessi
      if (!checkPermissions('read')) {
        throw new Error('Permessi insufficienti per leggere l\'entit');
      }

      // Verifica cache
      if (!options.bypassCache) {
        const cached = getCachedEntity(id);
        if (cached) {
          return {
            success: true,
            data: cached,
            operation: 'read'
          };
        }
      }

      setState(prev => ({ ...prev, loading: true, error: null, lastOperation: 'read' }));

      // Abort controller
      abortControllerRef.current = new AbortController();

      // Chiamata API
      const response = await apiService.get(
        `${apiConfig.endpoints.list}/${id}`,
        {
          signal: abortControllerRef.current.signal,
          ...apiConfig.defaultOptions
        }
      );

      const entity = (response as any).data as T;

      // Aggiorna cache
      setCachedEntity(entity);

      const result: EntityOperationResult<T> = {
        success: true,
        data: entity,
        operation: 'read'
      };

      setState(prev => ({ ...prev, loading: false }));

      onOperationComplete?.('read', result);

      return result;

    } catch (error: any) {
      setState(prev => ({
        ...prev,
        loading: false,
        error: error.message
      }));

      const result: EntityOperationResult<T> = {
        success: false,
        errors: [error.message],
        operation: 'read'
      };

      onOperationComplete?.('read', result);

      return result;
    } finally {
      abortControllerRef.current = null;
    }
  }, [
    checkPermissions,
    getCachedEntity,
    apiConfig,
    setCachedEntity,
    onOperationComplete
  ]);

  // UPDATE - Aggiorna entit
  const updateEntity = useCallback(async (
    id: string,
    updates: Partial<T>,
    options: OperationOptions = {}
  ): Promise<EntityOperationResult<T>> => {
    try {
      // Verifica permessi
      if (!checkPermissions('update')) {
        throw new Error('Permessi insufficienti per aggiornare l\'entit');
      }

      // Verifica consensi GDPR
      await checkGDPRConsents('update', options.consentTypes);

      setState(prev => ({ ...prev, saving: true, error: null, lastOperation: 'update' }));

      // Ottieni dati correnti per audit
      const currentResult = await readEntity(id, { bypassCache: true });
      if (!currentResult.success) {
        throw new Error('Impossibile ottenere i dati correnti dell\'entit');
      }

      const currentData = currentResult.data!;

      // Update ottimistico
      if (options.optimistic) {
        applyOptimisticUpdate(id, updates);
      }

      // Validazione
      if (!options.skipValidation) {
        const mergedData = { ...currentData, ...updates };
        const errors = await validateEntity(mergedData);
        if (errors.length > 0) {
          if (options.optimistic) {
            removeOptimisticUpdate(id);
          }
          throw new Error(`Errori di validazione: ${errors.join(', ')}`);
        }
      }

      // Trasformazione dati
      let processedUpdates = updates;
      if (dataTransformer) {
        processedUpdates = dataTransformer(updates);
      }

      // Abort controller
      abortControllerRef.current = new AbortController();

      // Log operazione GDPR
      const gdprResult = await logGDPROperation({
        operation: 'UPDATE',
        entityType,
        entityId: id,
        data: processedUpdates,
        reason: options.reason || 'Aggiornamento entit',
        riskLevel: 'MEDIUM',
        metadata: {
          ...options.metadata,
          oldData: currentData
        }
      });

      if (!gdprResult.success) {
        if (options.optimistic) {
          removeOptimisticUpdate(id);
        }
        throw new Error('Errore nel logging GDPR');
      }

      // Chiamata API
      const response = await apiService.put(
        `${apiConfig.endpoints.update}/${id}`,
        processedUpdates
      );

      const updatedEntity = (response as any).data as T;

      // Rimuovi update ottimistico e aggiorna cache
      if (options.optimistic) {
        removeOptimisticUpdate(id);
      }
      setCachedEntity(updatedEntity);

      const result: EntityOperationResult<T> = {
        success: true,
        data: updatedEntity,
        operation: 'update'
      };

      setState(prev => ({ ...prev, saving: false }));

      onOperationComplete?.('update', result);

      return result;

    } catch (error: any) {
      // Rimuovi update ottimistico in caso di errore
      if (options.optimistic) {
        removeOptimisticUpdate(id);
      }

      setState(prev => ({
        ...prev,
        saving: false,
        error: error.message
      }));

      const result: EntityOperationResult<T> = {
        success: false,
        errors: [error.message],
        operation: 'update'
      };

      onOperationComplete?.('update', result);

      return result;
    } finally {
      abortControllerRef.current = null;
    }
  }, [
    checkPermissions,
    checkGDPRConsents,
    readEntity,
    applyOptimisticUpdate,
    removeOptimisticUpdate,
    validateEntity,
    dataTransformer,
    logGDPROperation,
    apiConfig,
    entityType,
    setCachedEntity,
    onOperationComplete
  ]);

  // DELETE - Elimina entit
  const deleteEntity = useCallback(async (
    id: string,
    options: OperationOptions = {}
  ): Promise<EntityOperationResult<void>> => {
    try {
      // Verifica permessi
      if (!checkPermissions('delete')) {
        throw new Error('Permessi insufficienti per eliminare l\'entit');
      }

      // Verifica consensi GDPR (diritto all'oblio)
      await checkGDPRConsents('delete', options.consentTypes || ['DATA_PROCESSING']);

      setState(prev => ({ ...prev, deleting: true, error: null, lastOperation: 'delete' }));

      // Ottieni dati correnti per audit
      const currentResult = await readEntity(id, { bypassCache: true });
      const currentData = currentResult.success ? currentResult.data : null;

      // Abort controller
      abortControllerRef.current = new AbortController();

      // Log operazione GDPR
      const gdprResult = await logGDPROperation({
        operation: 'DELETE',
        entityType,
        entityId: id,
        data: currentData,
        reason: options.reason || 'Eliminazione entit',
        riskLevel: 'HIGH',
        metadata: options.metadata
      });

      if (!gdprResult.success) {
        throw new Error('Errore nel logging GDPR');
      }

      // Chiamata API
      await apiService.delete(`${apiConfig.endpoints.delete}/${id}`);

      // Rimuovi da cache
      setState(prev => {
        const newCache = new Map(prev.cache);
        newCache.delete(id);

        const newOptimistic = new Map(prev.optimisticUpdates);
        newOptimistic.delete(id);

        return {
          ...prev,
          cache: newCache,
          optimisticUpdates: newOptimistic,
          deleting: false
        };
      });

      const result: EntityOperationResult<void> = {
        success: true,
        operation: 'delete'
      };

      onOperationComplete?.('delete', result as EntityOperationResult<T>);

      return result;

    } catch (error: any) {
      setState(prev => ({
        ...prev,
        deleting: false,
        error: error.message
      }));

      const result: EntityOperationResult<void> = {
        success: false,
        errors: [error.message],
        operation: 'delete'
      };

      onOperationComplete?.('delete', result as EntityOperationResult<T>);

      return result;
    } finally {
      abortControllerRef.current = null;
    }
  }, [
    checkPermissions,
    checkGDPRConsents,
    readEntity,
    logGDPROperation,
    apiConfig,
    entityType,
    onOperationComplete
  ]);

  // Bulk operations
  const bulkDelete = useCallback(async (
    ids: string[],
    options: OperationOptions = {}
  ): Promise<EntityOperationResult<{ deleted: string[]; failed: string[] }>> => {
    const deleted: string[] = [];
    const failed: string[] = [];

    for (const id of ids) {
      try {
        const result = await deleteEntity(id, options);
        if (result.success) {
          deleted.push(id);
        } else {
          failed.push(id);
        }
      } catch (error) {
        failed.push(id);
      }
    }

    return {
      success: failed.length === 0,
      data: { deleted, failed },
      operation: 'bulk_delete',
      errors: failed.length > 0 ? [`Fallite ${failed.length} eliminazioni`] : undefined
    };
  }, [deleteEntity]);

  // Clear cache
  const clearCache = useCallback(() => {
    setState(prev => ({
      ...prev,
      cache: new Map(),
      optimisticUpdates: new Map()
    }));
  }, []);

  // Clear error
  const clearError = useCallback(() => {
    setState(prev => ({ ...prev, error: null }));
  }, []);

  return {
    // Stato
    loading: state.loading,
    saving: state.saving,
    deleting: state.deleting,
    error: state.error,
    lastOperation: state.lastOperation,

    // Operazioni CRUD
    createEntity,
    readEntity,
    updateEntity,
    deleteEntity,
    bulkDelete,

    // Utility
    cancelOperation,
    clearCache,
    clearError,
    getCachedEntity,

    // Stato cache
    cacheSize: state.cache.size,
    optimisticUpdatesCount: state.optimisticUpdates.size,

    // Configurazione
    permissions,
    entityType,
    isGDPREnabled: !!gdprConfig
  };
}

export default useGDPREntityOperations;

/**
 * useGDPREntityPage Hook - Hook principale per gestione template
 * 
 * Hook che gestisce lo stato e le azioni principali del template
 * GDPR-compliant per pagine entit.
 * 
 * @version 1.0
 * @date 30 Dicembre 2024
 */

import { useState, useEffect, useCallback, useReducer } from 'react';
import { BaseEntity } from '../types/entity.types';
import { GDPREntityPageConfig, TemplateState, TemplateActions } from '../types/template.types';
import { GDPRAuditAction } from '../types/gdpr.types';
import { useGDPRAudit } from './useGDPRAudit';
import { useGDPRConsent } from './useGDPRConsent';
import { apiService } from '../../../services/api';

/**
 * Configurazione hook
 */
export interface UseGDPREntityPageConfig<T extends BaseEntity = BaseEntity> {
  /** Configurazione template */
  config: GDPREntityPageConfig<T>;

  /** Dati iniziali */
  initialData?: T[];

  /** Callback personalizzate */
  onEntityCreate?: (entity: Omit<T, 'id'>) => Promise<T>;
  onEntityUpdate?: (id: string, entity: Partial<T>) => Promise<T>;
  onEntityDelete?: (id: string) => Promise<void>;
  onBulkDelete?: (ids: string[]) => Promise<void>;

  /** Configurazione cache */
  enableCache?: boolean;
  cacheTTL?: number;
}

/**
 * Azioni del reducer
 */
type EntityPageAction<T extends BaseEntity> = 
  | { type: 'SET_LOADING'; payload: boolean }
  | { type: 'SET_ERROR'; payload: string | null }
  | { type: 'SET_ENTITIES'; payload: T[] }
  | { type: 'ADD_ENTITY'; payload: T }
  | { type: 'UPDATE_ENTITY'; payload: { id: string; entity: Partial<T> } }
  | { type: 'REMOVE_ENTITY'; payload: string }
  | { type: 'REMOVE_ENTITIES'; payload: string[] }
  | { type: 'SET_FILTERS'; payload: Record<string, any> }
  | { type: 'SET_SORTING'; payload: { field: keyof T; direction: 'asc' | 'desc' } | null }
  | { type: 'SET_PAGINATION'; payload: { page?: number; pageSize?: number; total?: number } }
  | { type: 'SET_SELECTED_ENTITIES'; payload: T[] }
  | { type: 'SET_UI_STATE'; payload: Partial<TemplateState<T>['ui']> }
  | { type: 'RESET_STATE' };

/**
 * Reducer per gestione stato
 */
function entityPageReducer<T extends BaseEntity>(
  state: TemplateState<T>,
  action: EntityPageAction<T>
): TemplateState<T> {
  switch (action.type) {
    case 'SET_LOADING':
      return { ...state, loading: action.payload };

    case 'SET_ERROR':
      return { ...state, error: action.payload, loading: false };

    case 'SET_ENTITIES':
      return {
        ...state,
        entities: action.payload,
        loading: false,
        error: null,
        pagination: {
          ...state.pagination,
          total: action.payload.length
        }
      };

    case 'ADD_ENTITY':
      return {
        ...state,
        entities: [...state.entities, action.payload],
        pagination: {
          ...state.pagination,
          total: state.pagination.total + 1
        }
      };

    case 'UPDATE_ENTITY':
      return {
        ...state,
        entities: state.entities.map(entity =>
          entity.id === action.payload.id
            ? { ...entity, ...action.payload.entity }
            : entity
        )
      };

    case 'REMOVE_ENTITY':
      return {
        ...state,
        entities: state.entities.filter(entity => entity.id !== action.payload),
        selectedEntities: state.selectedEntities.filter(entity => entity.id !== action.payload),
        pagination: {
          ...state.pagination,
          total: Math.max(0, state.pagination.total - 1)
        }
      };

    case 'REMOVE_ENTITIES':
      return {
        ...state,
        entities: state.entities.filter(entity => !action.payload.includes(entity.id)),
        selectedEntities: state.selectedEntities.filter(entity => !action.payload.includes(entity.id)),
        pagination: {
          ...state.pagination,
          total: Math.max(0, state.pagination.total - action.payload.length)
        }
      };

    case 'SET_FILTERS':
      return {
        ...state,
        filters: action.payload,
        pagination: { ...state.pagination, page: 1 }
      };

    case 'SET_SORTING':
      return { ...state, sorting: action.payload };

    case 'SET_PAGINATION':
      return {
        ...state,
        pagination: { ...state.pagination, ...action.payload }
      };

    case 'SET_SELECTED_ENTITIES':
      return { ...state, selectedEntities: action.payload };

    case 'SET_UI_STATE':
      return {
        ...state,
        ui: { ...state.ui, ...action.payload }
      };

    case 'RESET_STATE':
      return {
        entities: [],
        loading: false,
        error: null,
        selectedEntities: [],
        filters: {},
        sorting: null,
        pagination: { page: 1, pageSize: 10, total: 0 },
        ui: {
          density: 'standard',
          sidebarCollapsed: false,
          activeModal: null
        }
      };

    default:
      return state;
  }
}

/**
 * Hook principale per gestione template GDPR
 */
export function useGDPREntityPage<T extends BaseEntity = BaseEntity>({
  config,
  initialData,
  onEntityCreate,
  onEntityUpdate,
  onEntityDelete,
  onBulkDelete,
  enableCache = true,
  cacheTTL = 300000 // 5 minuti
}: UseGDPREntityPageConfig<T>) {

  // Stato iniziale
  const initialState: TemplateState<T> = {
    entities: initialData || [],
    loading: false,
    error: null,
    selectedEntities: [],
    filters: {},
    sorting: null,
    pagination: {
      page: 1,
      pageSize: config.ui.table.defaultPageSize,
      total: initialData?.length || 0
    },
    ui: {
      density: config.ui.table.defaultDensity,
      sidebarCollapsed: config.ui.layout.sidebar?.defaultCollapsed || false,
      activeModal: null
    }
  };

  const [state, dispatch] = useReducer(entityPageReducer<T>, initialState);

  // Hooks GDPR
  const { logAction } = useGDPRAudit({
    config: config.gdpr.auditConfig,
    entityType: config.entity.name
  });

  const { checkConsent } = useGDPRConsent({
    config: config.gdpr.consentConfig,
    personId: 'current-user' // Da implementare con context utente reale
  });

  // Cache per entit
  const [cache, setCache] = useState<Map<string, { data: T[]; timestamp: number }>>(new Map());

  // Carica entit dal server
  const loadEntities = useCallback(async (forceRefresh = false) => {
    const cacheKey = `${config.api.baseEndpoint}_${JSON.stringify(state.filters)}_${JSON.stringify(state.sorting)}`;

    // Verifica cache se abilitata
    if (enableCache && !forceRefresh) {
      const cached = cache.get(cacheKey);
      if (cached && Date.now() - cached.timestamp < cacheTTL) {
        dispatch({ type: 'SET_ENTITIES', payload: cached.data });
        return;
      }
    }

    dispatch({ type: 'SET_LOADING', payload: true });

    try {
      // Log azione
      await logAction('READ' as GDPRAuditAction, undefined, {
        filters: state.filters,
        sorting: state.sorting,
        pagination: state.pagination
      });

      // Costruisci parametri query
      const params = new URLSearchParams();

      // Aggiungi filtri
      Object.entries(state.filters).forEach(([key, value]) => {
        if (value !== undefined && value !== null && value !== '') {
          params.append(key, value.toString());
        }
      });

      // Aggiungi ordinamento
      if (state.sorting) {
        params.append('sortBy', state.sorting.field.toString());
        params.append('sortOrder', state.sorting.direction);
      }

      // Aggiungi paginazione
      params.append('page', state.pagination.page.toString());
      params.append('limit', state.pagination.pageSize.toString());

      // Chiamata API
      const response = await apiService.get(`${config.api.endpoints.list}?${params.toString()}`);

      const responseData = response as { data?: T[]; total?: number };
      const entities = responseData.data || [];
      const total = responseData.total || entities.length;

      // Aggiorna cache
      if (enableCache) {
        setCache(prev => new Map(prev.set(cacheKey, {
          data: entities,
          timestamp: Date.now()
        })));
      }

      dispatch({ type: 'SET_ENTITIES', payload: entities });
      dispatch({ type: 'SET_PAGINATION', payload: { total } });

    } catch (error) {
      console.error('Errore nel caricamento entit:', error);
      dispatch({ type: 'SET_ERROR', payload: 'Errore nel caricamento dei dati' });
    }
  }, [config, state.filters, state.sorting, state.pagination, enableCache, cacheTTL, cache, logAction]);

  // Crea nuova entit
  const createEntity = useCallback(async (entityData: Omit<T, 'id'>) => {
    dispatch({ type: 'SET_LOADING', payload: true });

    try {
      // Verifica consensi se richiesto
      if (config.gdpr.requiresConsent && config.gdpr.consentConfig) {
        const verification = await checkConsent(config.gdpr.consentConfig.requiredConsents);
        if (!verification.hasConsent) {
          throw new Error('Consensi GDPR mancanti per questa operazione');
        }
      }

      let newEntity: T;

      if (onEntityCreate) {
        newEntity = await onEntityCreate(entityData);
      } else {
        const response = await apiService.post(config.api.endpoints.create, entityData);
        const responseData = response as { data: T };
        newEntity = responseData.data;
      }

      dispatch({ type: 'ADD_ENTITY', payload: newEntity });

      // Invalida cache
      if (enableCache) {
        setCache(new Map());
      }

      return newEntity;

    } catch (error) {
      console.error('Errore nella creazione entit:', error);
      dispatch({ type: 'SET_ERROR', payload: 'Errore nella creazione dell\'elemento' });
      throw error;
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  }, [config, onEntityCreate, checkConsent, enableCache]);

  // Aggiorna entit esistente
  const updateEntity = useCallback(async (id: string, entityData: Partial<T>) => {
    dispatch({ type: 'SET_LOADING', payload: true });

    try {
      // Verifica consensi se richiesto
      if (config.gdpr.requiresConsent && config.gdpr.consentConfig) {
        const verification = await checkConsent(config.gdpr.consentConfig.requiredConsents);
        if (!verification.hasConsent) {
          throw new Error('Consensi GDPR mancanti per questa operazione');
        }
      }

      let updatedEntity: T;

      if (onEntityUpdate) {
        updatedEntity = await onEntityUpdate(id, entityData);
      } else {
        const response = await apiService.put(config.api.endpoints.update.replace(':id', id), entityData);
        const responseData = response as { data: T };
        updatedEntity = responseData.data;
      }

      dispatch({ type: 'UPDATE_ENTITY', payload: { id, entity: entityData } });

      // Invalida cache
      if (enableCache) {
        setCache(new Map());
      }

      return updatedEntity;

    } catch (error) {
      console.error('Errore nell\'aggiornamento entit:', error);
      dispatch({ type: 'SET_ERROR', payload: 'Errore nell\'aggiornamento dell\'elemento' });
      throw error;
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  }, [config, onEntityUpdate, checkConsent, enableCache]);

  // Elimina entit
  const deleteEntity = useCallback(async (id: string) => {
    dispatch({ type: 'SET_LOADING', payload: true });

    try {
      if (onEntityDelete) {
        await onEntityDelete(id);
      } else {
        await apiService.delete(config.api.endpoints.delete.replace(':id', id));
      }

      dispatch({ type: 'REMOVE_ENTITY', payload: id });

      // Invalida cache
      if (enableCache) {
        setCache(new Map());
      }

    } catch (error) {
      console.error('Errore nell\'eliminazione entit:', error);
      dispatch({ type: 'SET_ERROR', payload: 'Errore nell\'eliminazione dell\'elemento' });
      throw error;
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  }, [config, onEntityDelete, enableCache]);

  // Elimina entit multiple
  const bulkDelete = useCallback(async (ids: string[]) => {
    dispatch({ type: 'SET_LOADING', payload: true });

    try {
      if (onBulkDelete) {
        await onBulkDelete(ids);
      } else if (config.api.endpoints.bulkDelete) {
        await apiService.post(config.api.endpoints.bulkDelete, { ids });
      } else {
        // Fallback: elimina uno per uno
        await Promise.all(ids.map(id => 
          apiService.delete(config.api.endpoints.delete.replace(':id', id))
        ));
      }

      dispatch({ type: 'REMOVE_ENTITIES', payload: ids });

      // Invalida cache
      if (enableCache) {
        setCache(new Map());
      }

    } catch (error) {
      console.error('Errore nell\'eliminazione multipla:', error);
      dispatch({ type: 'SET_ERROR', payload: 'Errore nell\'eliminazione degli elementi' });
      throw error;
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  }, [config, onBulkDelete, enableCache]);

  // Azioni template
  const actions: TemplateActions<T> = {
    loadEntities,
    createEntity,
    updateEntity,
    deleteEntity,
    bulkDelete,

    setFilters: (filters) => dispatch({ type: 'SET_FILTERS', payload: filters }),
    setSorting: (field, direction) => dispatch({ type: 'SET_SORTING', payload: { field, direction } }),
    setPage: (page) => dispatch({ type: 'SET_PAGINATION', payload: { page } }),
    setPageSize: (pageSize) => dispatch({ type: 'SET_PAGINATION', payload: { pageSize, page: 1 } }),
    setSelectedEntities: (entities) => dispatch({ type: 'SET_SELECTED_ENTITIES', payload: entities }),

    openModal: (modalId, data) => dispatch({ type: 'SET_UI_STATE', payload: { activeModal: modalId } }),
    closeModal: () => dispatch({ type: 'SET_UI_STATE', payload: { activeModal: null } }),

    requestConsent: async (consentTypes) => {
      // Implementa richiesta consenso
      console.log('Richiesta consenso:', consentTypes);
    },
    revokeConsent: async (consentTypes) => {
      // Implementa revoca consenso
      console.log('Revoca consenso:', consentTypes);
    },
    exportData: async (format) => {
      // Implementa export dati
      console.log('Export dati:', format);
    },
    requestDeletion: async (reason) => {
      // Implementa richiesta cancellazione
      console.log('Richiesta cancellazione:', reason);
    }
  };

  // Carica dati iniziali
  useEffect(() => {
    if (!initialData) {
      loadEntities();
    }
  }, []);

  // Ricarica quando cambiano filtri, ordinamento o paginazione
  useEffect(() => {
    if (!initialData) {
      loadEntities();
    }
  }, [state.filters, state.sorting, state.pagination.page, state.pagination.pageSize]);

  return {
    state,
    actions,
    loading: state.loading,
    error: state.error,

    // Utility functions
    clearError: () => dispatch({ type: 'SET_ERROR', payload: null }),
    resetState: () => dispatch({ type: 'RESET_STATE' }),
    refreshData: () => loadEntities(true),

    // Cache utilities
    clearCache: () => setCache(new Map()),
    getCacheSize: () => cache.size
  };
}

export default useGDPREntityPage;

import { useState, useMemo, useCallback } from 'react';

export interface FilterConfig {
  key: string;
  label: string;
  type: 'text' | 'select' | 'date' | 'boolean' | 'number';
  options?: { value: string; label: string }[];
  placeholder?: string;
}

export interface SortConfig {
  field: string;
  direction: 'asc' | 'desc';
}

export interface GDPRFiltersState {
  searchTerm: string;
  filters: Record<string, any>;
  sortConfig: SortConfig;
  showAdvancedFilters: boolean;
}

export interface GDPRFiltersActions {
  setSearchTerm: (term: string) => void;
  setFilter: (key: string, value: any) => void;
  clearFilter: (key: string) => void;
  clearAllFilters: () => void;
  setSortConfig: (config: SortConfig) => void;
  toggleAdvancedFilters: () => void;
  resetFilters: () => void;
}

export interface UseGDPRFiltersConfig {
  defaultSort?: SortConfig;
  filterConfigs?: FilterConfig[];
  enableAdvancedFilters?: boolean;
}

/**
 * Hook per gestire filtri, ricerca e ordinamento delle entit
 * Centralizza la logica di filtraggio con supporto per filtri avanzati
 */
export function useGDPRFilters<T extends Record<string, any>>({
  defaultSort = { field: 'createdAt', direction: 'desc' },
  filterConfigs = [],
  enableAdvancedFilters = true
}: UseGDPRFiltersConfig = {}) {

  const [state, setState] = useState<GDPRFiltersState>({
    searchTerm: '',
    filters: {},
    sortConfig: defaultSort,
    showAdvancedFilters: false
  });

  // Azioni per gestire i filtri
  const setSearchTerm = useCallback((term: string) => {
    setState(prev => ({ ...prev, searchTerm: term }));
  }, []);

  const setFilter = useCallback((key: string, value: any) => {
    setState(prev => ({
      ...prev,
      filters: { ...prev.filters, [key]: value }
    }));
  }, []);

  const clearFilter = useCallback((key: string) => {
    setState(prev => {
      const newFilters = { ...prev.filters };
      delete newFilters[key];
      return { ...prev, filters: newFilters };
    });
  }, []);

  const clearAllFilters = useCallback(() => {
    setState(prev => ({
      ...prev,
      searchTerm: '',
      filters: {}
    }));
  }, []);

  const setSortConfig = useCallback((config: SortConfig) => {
    setState(prev => ({ ...prev, sortConfig: config }));
  }, []);

  const toggleAdvancedFilters = useCallback(() => {
    setState(prev => ({ ...prev, showAdvancedFilters: !prev.showAdvancedFilters }));
  }, []);

  const resetFilters = useCallback(() => {
    setState({
      searchTerm: '',
      filters: {},
      sortConfig: defaultSort,
      showAdvancedFilters: false
    });
  }, [defaultSort]);

  // Funzione per applicare i filtri ai dati
  const applyFilters = useCallback((data: T[]): T[] => {
    if (!data || data.length === 0) return [];

    let filteredData = [...data];

    // Applica ricerca testuale
    if (state.searchTerm) {
      const searchLower = state.searchTerm.toLowerCase();
      filteredData = filteredData.filter(item => {
        return Object.values(item).some(value => {
          if (value === null || value === undefined) return false;
          return String(value).toLowerCase().includes(searchLower);
        });
      });
    }

    // Applica filtri specifici
    Object.entries(state.filters).forEach(([key, value]) => {
      if (value === null || value === undefined || value === '') return;

      filteredData = filteredData.filter(item => {
        const itemValue = item[key];

        if (typeof value === 'boolean') {
          return Boolean(itemValue) === value;
        }

        if (typeof value === 'string') {
          if (itemValue === null || itemValue === undefined) return false;
          return String(itemValue).toLowerCase().includes(value.toLowerCase());
        }

        if (typeof value === 'number') {
          return Number(itemValue) === value;
        }

        return itemValue === value;
      });
    });

    // Applica ordinamento
    if (state.sortConfig.field) {
      filteredData.sort((a, b) => {
        const aValue = a[state.sortConfig.field];
        const bValue = b[state.sortConfig.field];

        // Gestione valori null/undefined
        if (aValue === null || aValue === undefined) return 1;
        if (bValue === null || bValue === undefined) return -1;

        // Ordinamento per date
        if (aValue instanceof Date || bValue instanceof Date) {
          const aDate = new Date(aValue);
          const bDate = new Date(bValue);
          return state.sortConfig.direction === 'asc' 
            ? aDate.getTime() - bDate.getTime()
            : bDate.getTime() - aDate.getTime();
        }

        // Ordinamento per stringhe
        if (typeof aValue === 'string' && typeof bValue === 'string') {
          return state.sortConfig.direction === 'asc'
            ? aValue.localeCompare(bValue)
            : bValue.localeCompare(aValue);
        }

        // Ordinamento per numeri
        if (typeof aValue === 'number' && typeof bValue === 'number') {
          return state.sortConfig.direction === 'asc'
            ? aValue - bValue
            : bValue - aValue;
        }

        // Fallback per altri tipi
        const aStr = String(aValue);
        const bStr = String(bValue);
        return state.sortConfig.direction === 'asc'
          ? aStr.localeCompare(bStr)
          : bStr.localeCompare(aStr);
      });
    }

    return filteredData;
  }, [state.searchTerm, state.filters, state.sortConfig]);

  // Statistiche sui filtri
  const filterStats = useMemo(() => {
    const activeFiltersCount = Object.keys(state.filters).filter(
      key => state.filters[key] !== null && state.filters[key] !== undefined && state.filters[key] !== ''
    ).length;

    return {
      hasSearchTerm: Boolean(state.searchTerm),
      activeFiltersCount,
      hasActiveFilters: activeFiltersCount > 0 || Boolean(state.searchTerm),
      isDefaultSort: state.sortConfig.field === defaultSort.field && 
                     state.sortConfig.direction === defaultSort.direction
    };
  }, [state.searchTerm, state.filters, state.sortConfig, defaultSort]);

  const actions: GDPRFiltersActions = {
    setSearchTerm,
    setFilter,
    clearFilter,
    clearAllFilters,
    setSortConfig,
    toggleAdvancedFilters,
    resetFilters
  };

  return {
    // Stato
    searchTerm: state.searchTerm,
    filters: state.filters,
    sortConfig: state.sortConfig,
    showAdvancedFilters: state.showAdvancedFilters,

    // Azioni
    ...actions,

    // Funzioni di utilit
    applyFilters,
    filterStats,

    // Configurazione
    filterConfigs,
    enableAdvancedFilters
  };
}

import { useState, useCallback, useMemo } from 'react';
import { Button } from '../../../components/ui/button';
import { Eye, Edit, Trash2, Download } from 'lucide-react';
