  /** Fattori di rischio */
  riskFactors: {
    largescaleProcessing: number;
    sensitiveData: number;
    publiclyAccessible: number;
    vulnerableSubjects: number;
    innovativeTechnology: number;
    preventRightsExercise: number;
  };
}

